(window.webpackJsonp=window.webpackJsonp||[]).push([[1224],{1577:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2074-te-đan-tac-dung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2074-te-đan-tac-dung"}},[n._v("#")]),n._v(" Chương 2074: Tế đàn tác dụng")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Trương Huyền cau mày: “Linh hồn khí tức hoàn toàn tương đồng, đối võ kỹ lý giải đạt đến Thiên đạo cấp bậc, cùng cấp bậc chiến đấu vô địch...”"),t("br"),t("br"),n._v("Từ nội tâm chỗ sâu, hắn cũng không tin vị này chính là chỗ sùng bái vạn thế chi sư."),t("br"),t("br"),n._v("Nhưng vô luận từ chỗ nào một điểm, đều nhìn không ra sơ hở!"),t("br"),t("br"),n._v("Linh hồn khí tức tương đồng, có lẽ có thể ngụy trang, nhưng đối võ kỹ lý giải, cùng cấp bậc sức chiến đấu, tuyệt đối ngụy trang không được!"),t("br"),t("br"),n._v("Vừa rồi nếu không phải phân thân đột phá Ngụy Thần, lại thêm nhiều như vậy Ngụy Thần Tiên thú, vũ khí trợ giúp, căn bản không có khả năng chiến thắng!"),t("br"),t("br"),n._v("Nói cách khác, đối phương cùng cấp bậc sức chiến đấu, so với tu luyện Thiên đạo công pháp hắn, đều chỉ mạnh không yếu."),t("br"),t("br"),n._v("Không phải hắn... Ai có thể làm được?"),t("br"),t("br"),n._v("Huống chi, Thiên đạo thư viện dò xét không ra đối phương chuẩn xác thân phận, loại trừ vị này, thu được Thiên đạo hữu tự siêu cấp cường giả, lại có gì người có thể tại thư viện chiếu xuống, ẩn nấp đi?"),t("br"),t("br"),n._v("“Ta cả đời cùng Khổng sư giao thủ qua vài lần, hắn là hạng người gì, biết rất rõ ràng, trước mắt vị này, cứ việc cùng Khổng sư tương đồng hình dáng, tương đồng linh hồn khí tức, nhưng... Tuyệt không phải hắn! Hơn nữa, nếu như là hắn, ngươi cảm thấy, chỉ bằng phân thân cùng những này Tiên thú, thật có thể đem đánh bại?”"),t("br"),t("br"),n._v("Ngoan Nhân nói."),t("br"),t("br"),n._v("Trương Huyền nói không ra lời."),t("br"),t("br"),n._v("Dựa theo Khổng sư trước kia trải qua, bách chiến bách thắng, không gì không đánh được, lại đến mười mấy đầu Ngụy Thần, chỉ sợ thật khó mà thành công!"),t("br"),t("br"),n._v("“Năm đó, bắt hắn một cái đệ tử, hoàn toàn có thể giả bộ như không biết, có thể biết rõ nguy hiểm, lại việc nghĩa chẳng từ nan... Biết hẳn phải chết, vì đệ tử, lại hào hiệp đi chi, mặc dù là ta cả đời đối thủ, lại dị thường khâm phục, chắc chắn sẽ không làm ra người trước mắt này động tác!”"),t("br"),t("br"),n._v("Ngoan Nhân nói tiếp: “Thiên đạo có thiếu là rất cường đại, nhưng Thiên đạo hữu tự, cũng không hề yếu! Nếu như ngay cả điểm ấy đạo tâm đều không có, làm sao hưởng tiếng tăm vạn thế mà bất diệt.”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Có thể để cho Khổng sư thành tựu như vậy lớn, Thiên đạo hữu tự, không thể bỏ qua công lao."),t("br"),t("br"),n._v("Đã có Thiên đạo một bộ phận, chạy tới cướp bản thân, làm không cẩn thận sẽ bị Thiên đạo đồng hóa, tại chỗ chết... Thật sự là Khổng sư lời nói, làm sao không biết!"),t("br"),t("br"),n._v("Ngay cả mình đều có loại này định lực, sẽ không hâm mộ người khác, đối phương khẳng định cũng có."),t("br"),t("br"),n._v("Có thể... Nếu không phải Khổng sư, thì là ai?"),t("br"),t("br"),n._v("“Muốn nhiều như vậy vô dụng, đem Đỗ Thanh Diên cứu ra, nàng có lẽ có thể biết nhiều bí mật hơn...”"),t("br"),t("br"),n._v("Vừa rồi tế đàn, đã bị vị kia “Khổng sư” mang đi, nơi này cũng mất cái gì có thể lưu luyến đồ vật, Trương Huyền tìm tới không gian gấp cửa ra vào, rời khỏi cái gọi là Thông Thần điện tổng bộ, lúc này mới phát hiện, vậy mà liền tại khoảng cách Tiếp Thiên thạch không đủ một trăm cây số địa phương!"),t("br"),t("br"),n._v("Khoảng cách gần như thế, sáu đại tông môn một mực không có người phát hiện, ẩn tàng quả thực đủ sâu."),t("br"),t("br"),n._v("Dọc theo Tiếp Thiên thạch, đi vào đỉnh, quả nhiên thấy Thông Thiên kiều đã biến mất không thấy gì nữa, xa xôi Thần điện, lần nữa biến mất ở trong trời đêm, tìm không thấy tung tích."),t("br"),t("br"),n._v("“Tông chủ... Ngươi không có việc gì?”"),t("br"),t("br"),n._v("Thấy hắn xuất hiện, Khôi Hiểu, Hàn Kiếm Thu đám người tất cả đều khó tin."),t("br"),t("br"),n._v("Thông Thiên kiều lùi về, đã tấn cấp Ngụy Thần Hồng Vũ trưởng lão đám người trở về, có thể thủy chung không có thấy Trương Huyền, còn tưởng rằng, gặp phải nguy hiểm, triệt để về không được, không nghĩ tới, hắn không tại Thần điện phương hướng, ngược lại từ Tiếp Thiên thạch phía dưới bay tới."),t("br"),t("br"),n._v("“Nói rất dài dòng, Bạch Huyền Sinh cùng Cốc Truy Vân hai vị tông chủ đâu?”"),t("br"),t("br"),n._v("Nhìn quanh một vòng, Trương Huyền nói."),t("br"),t("br"),n._v("Hai cái này đầu nhập vào Thông Thần điện tông chủ, giờ phút này vậy mà mất đi tung tích, không biết đi nơi nào."),t("br"),t("br"),n._v("“Các ngươi đi vào Thông Thiên kiều không lâu, bọn họ liền rời đi, đi cực kỳ vội vàng, cụ thể đi đâu, cũng không rõ ràng...” Hàn Kiếm Thu nói."),t("br"),t("br"),n._v("“Không lâu?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Xem ra, đối phương hẳn là thông qua cái gì phương pháp đặc thù, biết mình chém giết bọn họ thiên tài, trước thời hạn trượt!"),t("br"),t("br"),n._v("Đã qua hơn mười giờ, còn muốn tìm, hẳn là không tìm thấy."),t("br"),t("br"),n._v("“Các ngươi có biết, loại trừ Thông Thiên kiều, như thế nào mới có thể đi vào Thần điện?” Trương Huyền hỏi."),t("br"),t("br"),n._v("Mấy người đồng thời lắc đầu."),t("br"),t("br"),n._v("Nếu là biết, cũng không đến mức, trăm năm chờ đợi một lần."),t("br"),t("br"),n._v("“Khẳng định loại trừ Thông Thiên kiều, còn có mặt khác thông đạo...”"),t("br"),t("br"),n._v("Trương Huyền suy nghĩ."),t("br"),t("br"),n._v("Đã Khổng sư có thể đem Thần điện người đồ diệt, toàn bộ đổi thành Thông Thần điện người, giải thích rõ khẳng định có phương pháp của mình đi vào Thần điện, mà cũng không phải là thông qua cầu nối."),t("br"),t("br"),n._v("Nếu không, mỗi lần cầu nối chỉ xuất hiện một cái trời, về thời gian không kịp ah!"),t("br"),t("br"),n._v("Thông Thiên kiều biến mất, tiếp tục đợi ở chỗ này cũng không có cái gì ý nghĩa, Trương Huyền đám người từ Tiếp Thiên thạch bay xuống, còn không có trở lại mặt biển, Ngột Thần tiến lên đón."),t("br"),t("br"),n._v("Vì không bày ra thực lực, hắn cũng không cùng đám người trèo lên đến đài đỉnh, mà là một mực chờ đợi ở đây."),t("br"),t("br"),n._v("“Đỗ cung chủ, có thể cùng ngươi đề cập qua, cách khác đi vào Thần điện sự tình?”"),t("br"),t("br"),n._v("Trong lòng hơi động, Trương Huyền tiếp tục hỏi."),t("br"),t("br"),n._v("Hàn Kiếm Thu đám người không biết, có lẽ Đỗ Thanh Diên biết, Ngột Thần đi theo đối phương sau lưng rất lâu, hẳn là có thể biết một ít."),t("br"),t("br"),n._v("“Không có đề cập qua...” Ngột Thần lắc đầu: “Loại trừ Thông Thiên kiều, nàng hẳn là cũng không biết, loại phương pháp nào mới có thể đi vào Thần điện đi!”"),t("br"),t("br"),n._v("Trương Huyền cười khổ."),t("br"),t("br"),n._v("Nhìn tới vẫn là hi vọng quá lớn, quả thực, đừng nói Ngột Thần, Đỗ Thanh Diên khẳng định cũng không rõ."),t("br"),t("br"),n._v("Nếu không, Trục Tinh cung, đã sớm trở thành sáu đại tông môn đứng đầu, cũng không đến mức, bị Thông Thần điện tấn công, mà không có lực phản kháng chút nào."),t("br"),t("br"),n._v("“Cái kia... Loại trừ Phiêu Miễu ngoa, không biết còn có cái gì phương pháp, có thể trực tiếp xuyên toa không gian?”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền nhìn về phía Hàn Kiếm Thu đám người."),t("br"),t("br"),n._v("Giả Khổng sư từ Thần điện đem hắn mang ra, mượn Phiêu Miễu ngoa, xuyên toa không gian, đến Thông Thần điện tổng bộ, nếu như có thể tìm tới tương tự đồ vật, có lẽ cũng có thể tại Tiếp Thiên thạch bên trên xuyên qua Thần Linh lưu lại bình chướng, lần nữa đi vào Thần điện!"),t("br"),n._v(" “Xuyên toa không gian?”"),t("br"),t("br"),n._v("“Nghe nói chỉ có đi đến Thần Linh cảnh giới, mới có thể làm đến...”"),t("br"),t("br"),n._v("Hàn Kiếm Thu đám người nói."),t("br"),t("br"),n._v("“Thần Linh?” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Thượng Thương có thể xuất hiện chân chính Thần Linh, đoán chừng vị này “Khổng sư” đã sớm làm được, không có khả năng còn ở nơi này cướp đoạt bản thân Thiên đạo thư viện."),t("br"),t("br"),n._v("Đã trải qua Thông Thiên kiều cùng danh sư đại lục, hắn biết, cũng không phải là thiên phú vấn đề, hẳn là, thế giới này, đồng dạng thiếu hụt một loại có thể để người ta tấn cấp Thần Linh đặc thù vật chất!"),t("br"),t("br"),n._v("Không có thứ này, cho dù đi đến Ngụy Thần lại đỉnh phong, thiên phú mạnh hơn, cũng không có khả năng thành công."),t("br"),t("br"),n._v("Cho nên... Thành tựu Thần Linh, mới có thể xuyên toa không gian, đáp án này, đối với hắn mà nói, tương đương với vô dụng."),t("br"),t("br"),n._v("Liền hắn tràn đầy bất đắc dĩ, không biết như thế nào mới có thể tìm tới Thần điện, cứu trở về Đỗ Thanh Diên thời điểm, một giọng nói vang lên: “Ta biết một cái phương pháp, ngược lại là có thể làm được...”"),t("br"),t("br"),n._v("Nói chuyện là Ngột Thần."),t("br"),t("br"),n._v("“Nói nghe một chút?” Trương Huyền vội vàng nhìn tới."),t("br"),t("br"),n._v("Ngột Thần nói: “Xuyên toa không gian, dưới tình huống bình thường, quả thực không có cách nào làm được, nhưng... Mượn tế tự lực lượng lời nói, có thể hoàn thành, lúc trước... Tiểu thư chính là mượn loại lực lượng này, liên tục xuyên qua hai đại thế giới hàng rào!”"),t("br"),t("br"),n._v("Trương Huyền vỗ trán một cái."),t("br"),t("br"),n._v("Làm sao không nghĩ tới điểm ấy."),t("br"),t("br"),n._v("Bàn về xuyên toa không gian, cái gì có thể so ra mà vượt Dị Linh tộc nhân đặc thù tế tự?"),t("br"),t("br"),n._v("Thân là Linh Thần Lạc Nhược Hi, đều dùng loại phương pháp này, xuyên qua hai thế giới hàng rào, chỉ ở cùng một cái thế giới lời nói, khẳng định vô cùng đơn giản."),t("br"),t("br"),n._v("Ngột Thần tiếp tục nói: “Có điều, muốn sử dụng loại này xuyên qua, cần hai địa phương đều có tế đàn, một bên thông qua tế tự, có thể đem người, đưa đến mặt khác một bên...”"),t("br"),t("br"),n._v("Tại danh sư đại lục, bắt mặt nạ đồng xanh thời điểm, hắn liền sử dụng qua loại này tế đàn, nhẹ nhõm từ một chỗ, đem người truyền tống đến mặt khác một chỗ."),t("br"),t("br"),n._v("“Nếu như là như vậy, có lẽ... Thật có thể, không dùng Thông Thiên kiều, cũng có thể nhẹ nhõm đi vào Thần điện!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Mặc dù không biết vị kia “Khổng sư”, chạy tới chỗ nào, nhưng vì an toàn lời nói, tám chín phần mười, sẽ lần nữa trở lại Thần điện."),t("br"),t("br"),n._v("Chỉ cần hắn trở về, tế đàn cũng liền đến Thần điện, hoàn toàn có thể sử dụng loại này truyền tống phương pháp, đem bản thân tế tự đến trong thần điện."),t("br"),t("br"),n._v("“Về Trục Tinh cung!”"),t("br"),t("br"),n._v("Không chần chờ nữa, cấp tốc trở lại Trục Tinh cung."),t("br"),t("br"),n._v("Tìm tới Giang Dao trưởng lão."),t("br"),t("br"),n._v("“Đây là trước đó ta ngụy trang thành cung chủ, sử dụng tế đàn...”"),t("br"),t("br"),n._v("Đem tế đàn lấy ra ngoài, Giang Dao trưởng lão lấy ra đủ loại tế tự cần có bảo vật."),t("br"),t("br"),n._v("Tuy là nàng chỉ là phổ thông trưởng lão, nhưng đối tế tự biết rất nhiều, thời gian không dài, bảo vật thiêu đốt lên, một cỗ đặc thù lực lượng lan ra mà ra."),t("br"),t("br"),n._v("Trương Huyền đi vào tế đàn phía trên, một lát sau, bảo vật thiêu đốt hầu như không còn, mà hắn cũng không bị truyền tống rời khỏi, vẫn như cũ đợi tại nguyên chỗ."),t("br"),t("br"),n._v("“Xem ra, chỉ có cái này trấn tông chi bảo, mới có thể đi vào đi truyền tống, mặt khác tế đàn, tiến hành một ít tiểu nhân tế tự còn có thể, lớn không làm được...”"),t("br"),t("br"),n._v("Lại thí nghiệm một lần, Giang Dao trưởng lão bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Bị giả Khổng sư mang đi cái kia tế đàn, là Trục Tinh cung trấn cung chi bảo, có năng lực đặc thù."),t("br"),t("br"),n._v("Trong môn phái còn lại tế đàn, tiến hành một ít nhỏ tế tự còn có thể, muốn đem một cái Tiên Quân cường giả đỉnh phong, truyền tống đến không biết tên địa phương, hơn nữa thông qua Thần Linh lưu lại bình chướng, liền khẳng định không làm được!"),t("br"),t("br"),n._v("“Cái kia tế đàn vô cùng có khả năng tại Thần điện...”"),t("br"),t("br"),n._v("Trương Huyền bất đắc dĩ."),t("br"),t("br"),n._v("Nếu có thể tìm tới, cũng không đến mức bị động như thế."),t("br"),t("br"),n._v("Có thể những này phổ thông tế đàn không làm được... Nhìn tới, tế đàn truyền tống phương pháp, cũng vô ích."),t("br"),t("br"),n._v("“Thiếu gia... Phổ thông tế đàn là không thể, nhưng... Nếu như có thể trở về danh sư đại lục, cầm tới Linh tộc cái kia tế đàn, hẳn là có thể làm được dễ dàng!”"),t("br"),t("br"),n._v("Ngột Thần xen vào nói."),t("br"),t("br"),n._v("“Danh sư đại lục Linh tộc tế đàn?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Đúng a."),t("br"),t("br"),n._v("Cái tế đàn này, có thể để cho Lạc Nhược Hi từ Thượng Thương đi vào danh sư đại lục, cấp bậc khả năng không bằng Trục Tinh cung cái kia, nhưng công hiệu không chút nào không kém!"),t("br"),t("br"),n._v("Nếu là có thể lấy đến trong tay, thông qua tế tự, hoàn toàn có thể câu thông đối diện tế đàn, đem bản thân truyền tống đến Thần điện."),t("br"),t("br"),n._v("Chỉ là..."),t("br"),t("br"),n._v("Cái tế đàn này, vẫn còn danh sư đại lục, như thế nào mới có thể trở về?"),t("br"),t("br"),n._v("Lúc trước Lạc Nhược Hi tiến vào bên trong, là để đối diện tiến hành tế tự, mới lấy thành công, dù vậy, đi vào danh sư đại lục về sau, tu vi từng một lần rơi xuống tới Thánh vực sơ kỳ... Mình bây giờ chỉ là Tiên Quân đỉnh phong, coi như biết, đối diện tế đàn có thể sử dụng, cũng không cách nào với tay cầm đi!"),t("br"),t("br"),n._v("“Thật ra thì... Muốn trở về cũng không có phức tạp như vậy!” Biết ý nghĩ của hắn, Ngột Thần nói."),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Ngột Thần suy nghĩ phút chốc, nói: “Linh Thần tới thời điểm, Thượng Thương đến danh sư đại lục thông đạo là phủ kín ở, trên cơ bản không cách nào thông qua, nhưng Xuân Thu đại điển sau khi xuất hiện, thời gian không gian đều xuất hiện thay đổi, thông đạo đã xuất hiện vết rách... Thiếu gia tới thời điểm, đã quán thông, chỉ cần có thể tìm tới ngươi lúc đến cái lối đi kia, trở về không khó...”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2087"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2089"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);