(window.webpackJsonp=window.webpackJsonp||[]).push([[1225],{1578:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2075-trieu-nha-đam-nguoi-toi-thuong-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2075-trieu-nha-đam-nguoi-toi-thuong-thuong"}},[n._v("#")]),n._v(" Chương 2075: Triệu Nhã đám người tới Thượng Thương")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Đối phương nói không sai."),t("br"),t("br"),n._v("Trước đó kết nối danh sư đại lục cùng Thượng Thương không gian thông đạo, bị Khổng sư dùng bản thân học sinh thi thể phủ kín, hơn nữa sắp đặt thành đại trận, tình huống bình thường, là không cách nào thông qua."),t("br"),t("br"),n._v("Nhưng... Xuân Thu đại điển sau khi xuất hiện, thời không đều xuất hiện rối loạn, trận pháp có sơ hở, cho nên, hắn có thể mượn cơ hội, đi vào Thượng Thương, đi vào thế giới này!"),t("br"),t("br"),n._v("Chỉ cần nói với hắn đồng dạng, tìm tới cửa vào, quả thực có thể lần nữa trở lại danh sư đại lục."),t("br"),t("br"),n._v("Cổ Thánh tứ trọng không chịu nổi không gian phong bạo, đối với hắn giờ phút này tới nói, đã không coi vào đâu."),t("br"),t("br"),n._v("“Cái này cửa vào, chỉ sợ không dễ dàng như vậy tìm tới đi!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Trước đó hỏi qua vị kia “Khổng sư”, bị hắn người chăm sóc, nhất định ẩn giấu ở một cái nào đó không gian đặc thù phía trong, Di Khí đại lục như vậy lớn, muốn tìm được, nào có dễ dàng như vậy."),t("br"),t("br"),n._v("“Tại Thượng Thương vị trí, là không dễ dàng tìm kiếm, nhưng... Tại danh sư đại lục vị trí, thiếu gia hẳn phải biết đi!”"),t("br"),t("br"),n._v("Ngột Thần cười cười."),t("br"),t("br"),n._v("“Đương nhiên!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Ngay tại Chư Tử bách gia vị trí Côn Hư cảnh phía trong."),t("br"),t("br"),n._v("Ngột Thần nói: “Đã như vậy, chúng ta hoàn toàn có thể, nghĩ biện pháp câu thông đương thời Dung Hoàng, chỉ cần hắn có thể tại đây cái vị trí tế tự, bên này người, liền có thể có thể cảm nhận được triệu hoán lực lượng, từ đó tìm tới cửa vào, lần nữa trở về!”"),t("br"),t("br"),n._v("“Không sai!” Trương Huyền mắt sáng lên."),t("br"),t("br"),n._v("Không hổ là Thần Dung Hoàng, Dị Linh tộc nhân vật thiên tài nhất một trong, đây chính là biện pháp tốt nhất."),t("br"),t("br"),n._v("“Còn làm sao câu thông hạ giới Dung Hoàng, cũng không khó khăn, Trục Tinh cung hẳn là có đặc thù con đường!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu, đem Giang Dao trưởng lão cùng Triệu Nguyệt đại trưởng lão cùng một chỗ tìm tới."),t("br"),t("br"),n._v("“Cho hạ giới truyền lại tin tức phương pháp chúng ta có, không cần tế đàn, nhưng cũng chỉ có thể truyền lại tin tức, mặt khác không làm được...”"),t("br"),t("br"),n._v("Nghe rõ tông chủ mục đích, Triệu Nguyệt đại trưởng lão nói."),t("br"),t("br"),n._v("“Đây là ta muốn truyền lại tin tức, ngươi nghĩ biện pháp truyền lại cho đương đại Dung Hoàng, để hắn một tháng sau, tiến hành tế tự!” Ngón tay một điểm, một đạo ý niệm truyền lại quá khứ."),t("br"),t("br"),n._v("Triệu Nguyệt đại trưởng lão đáp ứng: “Ta vậy thì đi làm!”"),t("br"),t("br"),n._v("Trục Tinh cung cùng danh sư đại lục Dị Linh tộc, thường xuyên có tin tức truyền lại, nguyên nhân chính là như vậy, mới có thể đồng bộ tế tự, để Lạc Nhược Hi thành công xuyên qua."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("An bài xong đối phương, Trương Huyền giáng xuống Ngột Thần."),t("br"),t("br"),n._v("“Đi đâu?” Ngột Thần sững sờ."),t("br"),t("br"),n._v("Trương Huyền nói: “Ta xuất hiện thời điểm, tại Lăng Vân kiếm các phạm vi, chắc hẳn thông đạo vị trí, khoảng cách không xa, một khi Dung Hoàng tế tự, chúng ta cũng có thể nhanh chút chạy trở về!”"),t("br"),t("br"),n._v("Mặc dù không xác định thông đạo vị trí cụ thể, nhưng hắn đi vào Thượng Thương về sau, tại Lăng Vân kiếm các phạm vi bên trong Huyền Giang thành, đoán không sai, thông đạo hẳn là khoảng cách không xa."),t("br"),t("br"),n._v("Nếu không, cũng không có khả năng bị Thiện Hiểu Thiên cứu."),t("br"),t("br"),n._v("Đem giao long cùng Cửu Đầu Hỏa Phượng thú mang theo trên người, mặt khác rất nhiều Tiên thú, đều lưu tại Trục Tinh cung, ngộ nhỡ giả Khổng sư tới, những người này phối hợp tông môn trận pháp cũng có thể ngăn cản."),t("br"),t("br"),n._v("Làm xong những này, Trương Huyền thì cùng Ngột Thần, ngồi giao long hai thú, cấp tốc hướng Lăng Vân kiếm các phạm vi bay lượn mà đi."),t("br"),t("br"),n._v("Giao long cùng Cửu Đầu Hỏa Phượng thú, đều đạt đến Ngụy Thần cấp bậc, vốn là am hiểu tốc độ, toàn lực phía dưới, so với chính mình phi hành, nhanh hơn nhiều."),t("br"),t("br"),n._v("Chỉ dùng ba ngày, là đến Lăng Vân kiếm các phạm vi."),t("br"),t("br"),n._v("Lúc trước, Trương Huyền từ nơi này đi tới Trục Tinh cung, tiếp cận hai mươi ngày, giờ phút này chỉ dùng ba ngày, Ngụy Thần cảnh Tiên thú tốc độ, quả nhiên đáng sợ."),t("br"),t("br"),n._v("“Đây là Huyền Giang thành...”"),t("br"),t("br"),n._v("Đem giao long các loại thú thu về thú sủng túi, Trương Huyền cùng Ngột Thần, che lại tu vi, rơi vào Huyền Giang thành trên đường phố rộng rãi."),t("br"),t("br"),n._v("Cùng một tháng trước, không có biến hoá quá lớn, tất cả như trước, Lăng Vân kiếm các phái mới thành chủ, trước đó Tiết Cầm đám người, cũng nhận nên được trừng phạt."),t("br"),t("br"),n._v("“Nghe nói không? Thiện Hiểu Thiên vị kia cùng hắn cùng một chỗ đi vào tông môn bằng hữu, thật giống liền kêu Trương Huyền!”"),t("br"),t("br"),n._v("“Ngươi ý tứ... Hắn vị bằng hữu này, thành Lăng Vân kiếm các, Trục Tinh cung, Vạn Thú môn, Thất Tinh lâu bốn đại tông môn tông chủ?”"),t("br"),t("br"),n._v("“Đúng vậy a, ta em vợ dì hai tam biểu tỷ tiểu thúc tử, tại Lăng Vân kiếm các làm tạp dịch đệ tử, hôm qua ta tại Thông Thần điện nhìn thấy hắn, chính miệng nói... Bọn họ tông chủ, chính là vị này!”"),t("br"),t("br"),n._v("“Chừng hai mươi tuổi, liền thành bốn tông tông chủ? Thiện Hiểu Thiên vận khí làm sao tốt như vậy?”"),t("br"),t("br"),n._v("“Còn không phải sao... Nghe nói, đã bái sư! Tuỳ ý cứu người, liền thành bốn tông tông chủ, vận khí tốt, ta đều muốn đi ra ngoài cứu người!”"),t("br"),t("br"),n._v("“Nói đến cứu người, ta bên cạnh Trương viên ngoại, thật đúng là cứu mấy cái, cũng đều không đến hai mươi, bốn cái nữ hài tử, đẹp đặc biệt, còn có năm cái thanh niên, từng cái oai hùng bất phàm... Cứu thời điểm, cũng lâm vào hôn mê!”"),t("br"),t("br"),n._v("“Thật là có người hôn mê? Tu vi có cao hay không?”"),t("br"),t("br"),n._v("“Vậy thì không biết, nghe nói thương thế rất nặng, không có gì tu vi đi... Cái này đã mấy ngày, cụ thể ta cũng không biết!”"),t("br"),t("br"),n._v("“Tuy là biết rõ, những người này, không có khả năng có Trương Huyền tông chủ thiên tư cùng năng lực, nhưng ít ra cũng coi như một cơ hội, nhìn tới ta cũng muốn ra ngoài đi một chút, không thể một mực đợi ở nhà!”"),t("br"),t("br"),n._v("“Đúng vậy a, chết mập trạch là không có tiền đồ!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Xung quanh vang lên thảo luận âm thanh."),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Bản thân vừa lên làm bốn tông tông chủ, không đến mười ngày, vậy mà đã truyền khắp toàn bộ đại lục, liền như vậy chỗ thật xa, đều biết."),t("br"),t("br"),n._v("“Thiếu gia, chúng ta đi đâu tìm?”"),t("br"),t("br"),n._v("Không để ý tới những nghị luận này, Ngột Thần hỏi."),t("br"),t("br"),n._v("Trước mắt vị này, dường như vô luận đi đến nơi nào, đều có thể trở thành tất cả mọi người chủ đề trung tâm."),t("br"),t("br"),n._v("Danh sư đại lục như vậy, Thượng Thương, cũng tương đồng."),t("br"),t("br"),n._v("“Trước đừng có gấp, ta để Triệu Nguyệt đại trưởng lão đưa tin, một tháng sau tế tự, thời gian hẳn là không sai biệt lắm, chúng ta trước tìm một chỗ an tĩnh, sắp đặt trận pháp, cảm ứng tế đàn, có lẽ liền có thể tìm tới!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),n._v(" Danh sư đại lục cùng Thượng Thương có gấp mười lần chênh lệch thời gian, làm cho đối phương một tháng sau tế tự, ở đây chẳng qua ba ngày trái phải, thời gian vừa vặn."),t("br"),t("br"),n._v("“Liền đi bên kia trên núi đi!”"),t("br"),t("br"),n._v("Nhìn quanh một vòng, Ngột Thần chỉ hướng một bên."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Huyền Giang thành, có sông lớn chảy qua, cũng có sơn mạch đứng sừng sững."),t("br"),t("br"),n._v("Rời khỏi thành thị, đi vào phía trên ngọn núi, Trương Huyền vẫy bàn tay lớn một cái, vô số trận kỳ bay ra, rơi vào mặt đất."),t("br"),t("br"),n._v("Loại cảm ứng này trận pháp, là hắn trên đường tới, chuyên môn thiết kế ra được, cùng kiếp trước tín hiệu tiếp thu tháp giống nhau, có thể tiếp nhận tế tự sinh ra lực lượng, hơn nữa xác nhận phương hướng, so với chính mình đám người mù quáng tìm kiếm muốn dễ dàng nhiều."),t("br"),t("br"),n._v("Bố trí tốt trận pháp về sau, lấy ra một cái trường mâu cắm ở chính giữa, đồng thời ở phía trên buộc lại một cái dây đỏ."),t("br"),t("br"),n._v("Bàn chân một bước, trận pháp vận chuyển."),t("br"),t("br"),n._v("Dây đỏ tiu nghỉu xuống, không có bất cứ động tĩnh gì."),t("br"),t("br"),n._v("“Chờ lấy a, tế tự hẳn là còn chưa bắt đầu...”"),t("br"),t("br"),n._v("Liếc mắt nhìn, Trương Huyền nói."),t("br"),t("br"),n._v("Tế tự, không có khả năng duy trì liên tục quá lâu, cho dù danh sư đại lục, duy trì liên tục ba ngày, Thượng Thương cũng không đến bốn canh giờ mà thôi."),t("br"),t("br"),n._v("Chỉ có vừa đúng, mới có thể vừa vặn bắt giữ lực lượng của đối phương, từ đó tìm tới thông đạo chuẩn xác vị trí."),t("br"),t("br"),n._v("Yên tĩnh ngồi tại nguyên chỗ, mỗi người tu luyện, Trương Huyền tiếp tục cân nhắc, đột phá Ngụy Thần công pháp."),t("br"),t("br"),n._v("Tới đồng thời, Huyền Giang thành phía trong, một cái rộng lớn trong phủ đệ, một cái dáng vẻ uyển chuyển nữ tử, từ từ mở mắt."),t("br"),t("br"),n._v("“Vị cô nương này, ngươi đã tỉnh...”"),t("br"),t("br"),n._v("Một cái người hầu đi vào trước mặt."),t("br"),t("br"),n._v("“Đây là địa phương nào? Ta ở đâu?”"),t("br"),t("br"),n._v("Nữ tử lông mày nhăn lại, vùng vẫy một hồi, hướng bốn phía nhìn lại."),t("br"),t("br"),n._v("Trước mắt sân nhỏ, vô cùng lạ lẫm, cùng trước kia thấy qua hoàn toàn khác biệt."),t("br"),t("br"),n._v("“Đây là Huyền Giang thành, là nhà chúng ta lão gia, Trương viên ngoại đem bọn ngươi cứu lại...”"),t("br"),t("br"),n._v("Người hầu vội nói."),t("br"),t("br"),n._v("“Các ngươi?” Nữ tử sửng sốt một chút, ngay sau đó ánh mắt sáng lên: “Cùng ta cùng nhau những người khác, các ngươi gặp, ở đâu?”"),t("br"),t("br"),n._v("“Cùng ngươi cùng nhau có tám người, tất cả đều hôn mê, đều an bài tại đây cái trong sân...” Người hầu gật đầu."),t("br"),t("br"),n._v("“Tám cái? Quá tốt rồi...” Nữ tử thở phào nhẹ nhõm, giẫy giụa đứng dậy: “Dẫn ta đi gặp bọn họ...”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy nàng kiên trì, người hầu chần chờ một chút, vẫn gật đầu, đi đầu dẫn đường, vừa đi ra gian phòng, ngay sau đó nhìn thấy trọng thương hôn mê tám người, tất cả đều tỉnh lại, cũng đều đến trong sân."),t("br"),t("br"),n._v("“Sư tỷ...”"),t("br"),t("br"),n._v("Nhìn thấy nữ tử, đám người đồng thời ôm quyền."),t("br"),t("br"),n._v("“Ừm, ngồi xuống trước đã!”"),t("br"),t("br"),n._v("Nữ tử gật gật đầu, nói một tiếng, mọi người tại trong viện trong lương đình ngồi xuống."),t("br"),t("br"),n._v("“Đều vô sự liền tốt, có điều, thương thế có chút nghiêm trọng, vừa rồi ta dò xét thoáng cái, trong cơ thể có không gian xé rách dấu vết, không có mấy năm công phu, rất khó khôi phục...” Cô bé nói."),t("br"),t("br"),n._v("“Ta cũng vậy!”"),t("br"),t("br"),n._v("“Chúng ta cũng như nhau...”"),t("br"),t("br"),n._v("Còn lại tám người, cũng mỗi người kiểm tra một phen, đồng thời nói."),t("br"),t("br"),n._v("Thương thế lần này, so trong tưởng tượng nghiêm trọng nhiều, cho dù nơi này linh khí đầy đủ, không có mấy năm công phu, muốn triệt để khôi phục, cũng không thể nào làm được."),t("br"),t("br"),n._v("“Một năm trước, lão sư lẻ loi một mình đi vào Thượng Thương, đoán chừng cũng bị đồng dạng thương, cũng không biết hiện tại xong chưa, nên đi chỗ nào tìm hắn...”"),t("br"),t("br"),n._v("Biết thân thể tình huống, một lát sau, nữ tử ánh mắt lộ ra lo lắng."),t("br"),t("br"),n._v("“Lão sư người hiền tự có thiên tướng, chắc chắn sẽ không xảy ra vấn đề!” Trong đám người một cái khác nữ tử, nói."),t("br"),t("br"),n._v("“Đúng vậy a, lão sư cát nhân thiên tướng...” Đi đầu nữ tử, đồng dạng gật gật đầu."),t("br"),t("br"),n._v("“Không đúng, thế giới này tốc độ thời gian trôi qua, thật giống cùng danh sư đại lục khác biệt...”"),t("br"),t("br"),n._v("Đột nhiên, trong đám người, một thanh niên nhíu nhíu mày mở miệng nói."),t("br"),t("br"),n._v("“Tốc độ thời gian trôi qua khác biệt?”"),t("br"),t("br"),n._v("Đám người tất cả đều sững sờ."),t("br"),t("br"),n._v("Trước mắt vị này, huyết mạch lực lượng chính là thời gian, đối thời gian lưu tốc có tự nhiên mẫn cảm."),t("br"),t("br"),n._v("“Ừm, chờ một lát, ta tính toán một chút...”"),t("br"),t("br"),n._v("Thanh niên chậm rãi nhắm mắt lại, qua chắc là ba phút mở hai mắt ra, trong ánh mắt mang theo hưng phấn: “Cùng danh sư đại lục, chênh lệch gấp mười lần, cũng sẽ nói, nơi này một ngày, danh sư đại lục mười ngày!”"),t("br"),t("br"),n._v("Nếu như Trương Huyền ở đây, khẳng định có thể nhận ra chín người này, đúng là hắn tại danh sư đại lục dưới tay chín vị chân truyền."),t("br"),t("br"),n._v("Triệu Nhã, Lưu Dương, Vương Dĩnh, Trịnh Dương, Viên Đào, Lộ Xung, Ngụy Như Yên, Trương Cửu Tiêu cùng Khổng Thi Dao!"),t("br"),t("br"),n._v("Trương Huyền đi vào Thượng Thương, mới hơn một tháng thời gian, mà danh sư đại lục, đã qua hơn một năm."),t("br"),t("br"),n._v("Thời gian lâu như vậy phía trong, đám người tất cả đều tu luyện đến Cổ Thánh tứ trọng đỉnh phong, không cách nào đột phá, lâu như vậy, lão sư tin tức hoàn toàn không có, rơi vào đường cùng, tập trung đến Côn Hư cảnh, tìm tới Khổng Thi Dao, cùng một chỗ đi vào thông đạo, đến nơi này!"),t("br"),t("br"),n._v("Được xưng sư tỷ nữ hài, là Triệu Nhã, phát giác Thượng Thương cùng danh sư đại lục có thời gian kém, là Trương Cửu Tiêu."),t("br"),t("br"),n._v("“Gấp mười lần chênh lệch? Ý của ngươi là nói... Lão sư mới đi đến nơi này, hơn một tháng?”"),t("br"),t("br"),n._v("Triệu Nhã ánh mắt sáng lên: “Thời gian ngắn như vậy, lão sư có lẽ cùng chúng ta đồng dạng, thương thế không có tốt, thậm chí, cũng có thể cũng tại đây cái Huyền Giang thành phía trong!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2088"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2090"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);