(window.webpackJsonp=window.webpackJsonp||[]).push([[1237],{1592:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-12-long-te-huyet-mach"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-12-long-te-huyet-mach"}},[n._v("#")]),n._v(" Chương 12: Long Tê huyết mạch")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Sau nửa canh giờ, Trương Huyền từ sau cần nơi đi ra, trong mắt tinh quang lấp loé, tràn đầy hưng phấn."),t("br"),t("br"),n._v(" Đổi lại tiền thân, hôm nay thiệt thòi nhất định phải ăn, có thể có Thiên Đạo Thư Viện, nhân sinh liền hoàn toàn khác biệt, đã đi lên một hướng khác."),t("br"),t("br"),n._v(" “Hay là, xuyên việt tới, là đúng! Ta hi vọng chính là này loại xán lạn nhân sinh!” "),t("br"),t("br"),n._v(" Nắm đấm xiết chặt, Trương Huyền thở ra một hơi."),t("br"),t("br"),n._v(" Đời trước của hắn, chỉ là cái sách báo nhân viên quản lý, trải qua hai điểm tạo thành một đường thẳng tháng ngày, bình thường phổ thông, bình thường đơn giản, tiếp tục làm tiếp, cũng chỉ có thể cầm chết tiền lương, tầm thường vô vi xuống, đến nơi này không đồng dạng, có Thiên Đạo Thư Viện cái này xuyên qua gói quà lớn, sau đó hay là thật có thể càng chạy càng xa, càng chạy càng mạnh, đi ra một cái hoàn toàn mới, xán lạn yêu kiều nhân sinh!"),t("br"),t("br"),n._v(" Thời khắc này, Trương Huyền rốt cục cùng thế giới này triệt để hòa làm một thể! Lại không còn tha hương nơi đất khách quê người nội tâm giãy dụa."),t("br"),t("br"),n._v(" “Đừng kéo ta, để cho ta chết, ta muốn chết...” "),t("br"),t("br"),n._v(" Chính đang âm thầm cao hứng, liền nghe đến cách đó không xa một tiếng gào khóc thảm thiết kêu gào, âm thanh khác nào phát rồ trâu hoang, được kêu là một cái tan nát cõi lòng."),t("br"),t("br"),n._v(" Quay đầu nhìn lại, chỉ thấy một tên béo, gào thét muốn hướng về trong học viện hồ nhân tạo xông, dự định nhảy hồ tự sát."),t("br"),t("br"),n._v(" Tên béo phía sau, cũng không có người ngăn cản, hắn cũng không xông về phía trước, mà là liên tục gào thét, đột nhiên, quay người lại, kéo bên người cái học viên bàn tay, thả trên người mình, làm ra kéo hắn hình, lần thứ hai rống lên."),t("br"),t("br"),n._v(" “Đừng kéo ta, để cho ta chết, ta không sống được...” "),t("br"),t("br"),n._v(" “...” "),t("br"),t("br"),n._v(" Tất cả mọi người không còn gì để nói."),t("br"),t("br"),n._v(" “Quá vô sỉ đi!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v(" Mập mạp này, rõ ràng không muốn chết, còn càng muốn làm ra người khác không cho hắn chết giả tạo, thật là vô sỉ."),t("br"),t("br"),n._v(" Biết cái tên này khẳng định không chết được, chẳng muốn tiếp tục quan tâm, hướng mình lớp học phương hướng đi đến, còn chưa đi xa, liền nghe đến tiếng gào thét càng ngày càng gần, mặt đất như địa chấn run rẩy, tiếp theo một đôi thô to cánh tay, ôm lấy bắp đùi của chính mình."),t("br"),t("br"),n._v(" “Lão sư, van cầu ngươi thu ta làm đồ đệ đi, bọn họ đều chê ta quá béo, không quan tâm ta...” "),t("br"),t("br"),n._v(" Tên béo oa oa khóc lớn."),t("br"),t("br"),n._v(" “Ngươi buông ra!” "),t("br"),t("br"),n._v(" Trương Huyền không còn gì để nói."),t("br"),t("br"),n._v(" Cái tên này cũng quá kỳ hoa đi, nhìn thấy tự mình là lão sư, liền xông lại, để cho mình thu hắn làm đồ, người như thế, còn chưa từng gặp."),t("br"),t("br"),n._v(" “Lão sư đáp ứng thu ta, ta mới thả ra!” Tên béo nước mắt một cái nước mũi một cái, này thanh âm gọi một cái thê thảm, kẻ thấy được thương tâm, nghe rơi lệ: “Ta hôm nay lạy mười cái lão sư, đều không có người muốn, vị lão sư này, ngươi nhìn ta như thế đáng thương, liền nhận lấy ta đi!” "),t("br"),t("br"),n._v(" Học sinh ở giữa sát hạch, cũng sẽ ảnh hưởng lão sư đánh giá, mập như vậy gia hỏa, chiến đấu, nhạy bén đều có vấn đề rất lớn, bình thường có tiếng lão sư, cũng không muốn thu."),t("br"),t("br"),n._v(" “Muốn cho ta thu, cũng ít nhất phải ta nhìn ngươi một chút bản lĩnh đi, ôm bắp đùi tính là gì sự tình?” Trương Huyền hừ nói."),t("br"),t("br"),n._v(" Hắn nắm giữ Thiên Đạo Thư Viện, chiêu thu học sinh đã không vấn đề quá lớn, nếu như quá kém, cũng không thể thu làm môn hạ."),t("br"),t("br"),n._v(" “Lão sư ngươi có thể nhất định phải thu ta làm đệ tử a, ta vẫn là rất có bản lãnh...” Tên béo chần chờ ngẩng đầu nhìn một chút, chậm rãi buông tay ra cánh tay."),t("br"),t("br"),n._v(" “Có bản lãnh hay không, nhìn mới biết, nói nhiều như vậy vô dụng!” Thấy hắn còn có chút “Lưu luyến”, Trương Huyền ghét bỏ một cước đá văng."),t("br"),t("br"),n._v(" Chuyện gì, nếu như cái học viên nữ chạy tới ôm bắp đùi ngược lại cũng thôi, một người nam, còn là một tên béo... Ngẫm lại đều không rét mà run."),t("br"),t("br"),n._v(" “Hảo! Xem ta!” Tên béo không chút nào bị ghét bỏ giác ngộ, đứng dậy, nhìn một vòng, từ nơi không xa ôm đến mấy khối dời gạch, tiện tay cầm lấy một cái, đối đầu của chính mình liền đập tới."),t("br"),t("br"),n._v(" Oành!"),t("br"),t("br"),n._v(" Dời gạch nát tan thành vụn."),t("br"),t("br"),n._v(" Tiếp theo lại từng người cầm lấy mấy khối gạch ở cánh tay, trên đùi phân biệt đập phá mấy lần, đều không ngoại lệ, dời gạch đều vỡ vụn ra."),t("br"),t("br"),n._v(" Cái tên này xem ra rất béo, lại một thân khổ luyện công phu."),t("br"),t("br"),n._v(" Không để ý tới của hắn khổ luyện, Trương Huyền đang xem trong đầu tự động hình thành thư tịch."),t("br"),t("br"),n._v(" Khổ luyện công phu cũng là võ kỹ một loại,"),t("br"),t("br"),n._v(" Tên béo vừa triển khai, thư viện lập tức tạo thành liên quan tới hắn nội dung."),t("br"),t("br"),n._v(" “Viên Đào, Địa Hoàng Thành tán tu, võ giả vọt một cái Tụ Tức cảnh nơi kỳ!” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" “Khuyết điểm: Mười tám nơi, một, trong cơ thể Thượng Cổ Long tê huyết mạch, chưa kích hoạt! Thứ hai, cơ sở quá kém, tu luyện võ kỹ...” "),t("br"),n._v(" “Long Tê huyết mạch?” "),t("br"),t("br"),n._v(" Nhìn thấy phía trên ghi chép, Trương Huyền sửng sốt."),t("br"),t("br"),n._v(" Kết hợp tiền thân ký ức, hắn biết thế giới này, huyết mạch, Tiên Thiên chi thể phi thường trọng yếu, nắm giữ hai thứ này một người trong đó bất luận một vị nào, chỉ cần vận dụng đúng phương pháp, sau đó tu luyện, đều sẽ tăng nhanh như gió, càng ngày càng mạnh."),t("br"),t("br"),n._v(" Tiên Thiên chi thể chia làm nhiều loại, Thuần Âm thân thể, Thuần Dương thân thể, Vô Cấu thân thể, Kim thân thân thể..."),t("br"),t("br"),n._v(" Huyết mạch cũng chia làm nhiều loại, cổ mạch, mới mạch, truyền thừa, biến dị..."),t("br"),t("br"),n._v(" Mỗi một dạng, một khi bị phát hiện, đều sẽ trở thành vô số lão sư tranh đoạt đối tượng."),t("br"),t("br"),n._v(" Mập mạp cái này Long Tê huyết mạch, chính là cổ mạch một loại, thuộc về thượng cổ mới có huyết mạch, truyền thuyết tu luyện tới đại thành, đao thương bất nhập, không gì có thể phá, là trong phòng ngự, đứng đầu vô địch huyết mạch một trong."),t("br"),t("br"),n._v(" Cái này xem ra không đáng chú ý, lại mập lại vô liêm sỉ gia hỏa, lại có cổ mạch?"),t("br"),t("br"),n._v(" “Cổ mạch không kích hoạt, nhìn dáng dấp liền ngay cả cái tên này bản thân cũng không biết!” "),t("br"),t("br"),n._v(" Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v(" Trong huyết mạch, cổ mạch xem như là cực kỳ mạnh mẽ, mập mạp này bái phỏng mười mấy vị lão sư, đều không có nhân thu, không hề là mọi người không biết hàng, mà là hắn mạch máu trong người không kích hoạt, cùng thường nhân không khác, đừng nói những người khác không thấy được, liền ngay cả chính hắn, e sợ cũng không biết!"),t("br"),t("br"),n._v(" Bất quá, coi như không biết, cũng có phương diện này tiềm chất, sức phòng ngự so với người bình thường mạnh hơn rất nhiều, lúc này mới học xong cái này bị đánh võ kỹ."),t("br"),t("br"),n._v(" “Cái tên này nhất định phải thu được môn hạ!” "),t("br"),t("br"),n._v(" Ánh mắt sáng lên."),t("br"),t("br"),n._v(" Nắm giữ cổ mạch học viên, toàn bộ học viện, không biết bao nhiêu năm đều không thấy được đồng loạt, tốt như vậy mầm đặt tại trước mặt, làm sao có khả năng thả hắn rời đi."),t("br"),t("br"),n._v(" “Không sai, ta hiện tại thu ngươi làm học viên, bái sư đi!” "),t("br"),t("br"),n._v(" Nghĩ tới đây, Trương Huyền đè nén hưng phấn trong lòng, một mặt lạnh nhạt ném ra đại biểu thân phận ngọc bài."),t("br"),t("br"),n._v(" “Lão sư, ngươi thật sự thu ta? Quá tốt rồi...” "),t("br"),t("br"),n._v(" Tên béo xem ra cũng bị đả kích tàn nhẫn, nghe được lại có lão sư thu hắn, hưng phấn không có quá nhiều do dự, trực tiếp cắt ra ngón tay, đem máu tươi đưa tới."),t("br"),t("br"),n._v(" “Dạng gì lão sư thu dạng gì học sinh, thật là có đạo lý, lão sư rác rưởi, học sinh cũng rác rưởi!” "),t("br"),t("br"),n._v(" Nhưng vào lúc này, một cái hừ lạnh vang lên."),t("br"),t("br"),n._v(" Quay đầu nhìn lại, chỉ thấy một người thanh niên lãnh ngạo đi tới."),t("br"),t("br"),n._v(" Thanh niên bên người, là cái xinh đẹp bóng người, mái tóc đen nhánh choàng tại trên vai, trắng nõn da dẻ uyển như mỡ đông, đen thui hai con mắt nhìn sang, cho người ta một loại kinh diễm cảm giác."),t("br"),t("br"),n._v(" “Thượng Bân? Trầm Bích Như?” "),t("br"),t("br"),n._v(" Nhìn thấy hai người trong đầu lập tức hiện ra hai cái tên."),t("br"),t("br"),n._v(" Nói tới Trầm Bích Như, toàn bộ học viện, e sợ không ai không biết, không người không hay!"),t("br"),t("br"),n._v(" Cũng không phải thân phận nàng đa đặc thù, mà là nàng là tất cả mọi người công nhận cô giáo xinh đẹp, trong học viện hoa khôi của trường cùng với so sánh, đều ảm đạm phai mờ."),t("br"),t("br"),n._v(" Chỉ là như vậy ngược lại cũng thôi, then chốt khóa dạy cũng tốt, đến học viện không đủ một năm, đã trở thành toàn bộ học viện nổi danh nhất lão sư một trong."),t("br"),t("br"),n._v(" Cao quý trang nhã, mỹ lệ có học vấn, hấp dẫn không ít lão sư điên cuồng theo đuổi."),t("br"),t("br"),n._v(" Tiền thân chính là người theo đuổi một trong."),t("br"),t("br"),n._v(" Chỉ có điều, trước Trương Huyền quá khuyết điểm bại, tu vi thấp không nói, mọi thứ sát hạch đều là kém cỏi nhất, bởi vì tự ti, tuy rằng có lòng này, nhưng cũng chưa bao giờ cùng trong lòng nữ thần nói chuyện nhiều, chớ nói chi là theo đuổi."),t("br"),t("br"),n._v(" Người thanh niên này gọi Thượng Bân, là học viện Thượng Thần trưởng lão cháu, cũng là người theo đuổi một trong, dựa dẫm thân phận, chèn ép những người theo đuổi khác, không biết từ nơi nào nghe nói Trương Huyền cũng yêu thích Trầm Bích Như, mỗi lần gặp mặt đều chê cười, thậm chí còn từng động thủ."),t("br"),t("br"),n._v(" Bất quá, Trầm Bích Như tựa hồ đối với hắn cũng không chú ý, vẫn luôn là lạnh như băng, không dính một hạt bụi dáng vẻ, để Thượng Bân lại sốt ruột vừa bất đắc dĩ."),t("br"),t("br"),n._v(" “Rác rưởi ngươi nói người nào?” Nghe được Thượng Bân không giữ lại chút nào trào phúng, Trương Huyền cũng không tức giận, quay đầu nhìn sang."),t("br"),t("br"),n._v(" “Rác rưởi nói ngươi!” Thượng Bân một mặt cười gằn."),t("br"),t("br"),n._v(" “Há, quả nhiên là ‘Rác rưởi’ nói ta, thối quá, thối quá!” Trương Huyền khoát tay áo một cái, làm ra buồn nôn vẻ mặt."),t("br"),t("br"),n._v(" “Ngươi...” Thượng Bân này mới phản ứng được, bị học viện cái này thứ nhất đếm ngược gia hỏa xuyến, tức giận đến sắc mặt ửng hồng."),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-20"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-22"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);