(window.webpackJsonp=window.webpackJsonp||[]).push([[1240],{1594:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-2087-lai-thay-đo-cung-chu"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2087-lai-thay-đo-cung-chu"}},[n._v("#")]),n._v(" Chương 2087: Lại thấy Đỗ cung chủ")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("Khó trách cái tên này, chạy trốn sau đó, liền không có ra tay, vốn cho rằng là nghĩ biện pháp khôi phục lực lượng, không nghĩ tới, là đang chờ mình tới, mục đích là... Những thần linh này chi khí!"),g("br"),g("br"),n._v("Thậm chí có khả năng, đối phương lần đầu tiên, bắt bản thân rút ra Thiên đạo thư viện, liền biết không có khả năng thành công, cố ý bố cục, chờ lấy bản thân tới."),g("br"),g("br"),n._v("Còn đối phương vì sao không bản thân xông trước mắt cái này cung điện... Khẳng định là có đặc thù nguyên nhân, không thể xông hoặc là... Xông không qua!"),g("br"),g("br"),n._v("Vị này là giả Khổng sư lời nói, cung điện phía trong đạo kia ý niệm là thật Khổng sư lưu lại, người sau nhất định có thủ đoạn gì, không cách nào để cho đối phương đi vào, liền giống như... Phân thân vào không được cái này cung điện đồng dạng."),g("br"),g("br"),n._v("“Chẳng lẽ...”"),g("br"),g("br"),n._v("Một cái ý nghĩ xông ra, Trương Huyền trong lòng hơi động, liếc mắt nhìn cách đó không xa phân thân, thấy hắn một mặt kéo kéo, mảy may đều không có bị Thần điện từ chối sau khó chịu cùng giác ngộ, chỉ vì không có trang bức mà cảm thấy tiếc nuối, lúc này mới niêm phong lại đạo ý niệm này, thở phào nhẹ nhõm."),g("br"),g("br"),n._v("“Bây giờ không phải là lúc cân nhắc những thứ này, không thể để cho cái tên này chạy, một khi thật muốn luyện hóa Thần Linh chi khí, lại không cách nào đối kháng...”"),g("br"),g("br"),n._v("Đem những này suy nghĩ áp chế lại, Trương Huyền lộ ra lo lắng."),g("br"),g("br"),n._v("Vị này Khổng sư, bản thân sức chiến đấu liền so với mình không kém, một khi đột phá, trở thành chân chính Thần Linh, đừng nói bản thân không ngăn được, tựu tính nhiều hơn nữa Ngụy Thần, tại phía trước cũng không đáng chú ý!"),g("br"),g("br"),n._v("Trước đó, Thượng Thương thiếu hụt có thể đột phá lực lượng, hiện tại có đạo này kim hoàng thể khí, đột phá đã thành kết cục đã định."),g("br"),g("br"),n._v("Nhất định phải nghĩ phương pháp ngăn cản!"),g("br"),g("br"),n._v("Chỉ là... Đối phương xé rách không gian chạy trốn, trước tiên không nói đi nơi nào, chỉ nói bản thân, cũng phải có phương pháp, rời đi nơi này mới được!"),g("br"),g("br"),n._v("Tế đàn bị kích phá, mất đi trở về thông đạo, vốn đạt được màu vàng kim thể khí, chỉ cần đột phá Thần Linh, đồng dạng có thể nhẹ nhõm phá vỡ không gian, mà bây giờ... Con đường này triệt để phá hỏng!"),g("br"),g("br"),n._v("“Vị này Khổng sư có thể xuyên toa không gian, dùng hẳn là Huyền Kính môn Thái Huyền kính cùng với Phiêu Miễu tiên tông Phiêu Miễu ngoa!”"),g("br"),g("br"),n._v("Nhớ lại vừa rồi đối phương chạy trốn phương pháp."),g("br"),g("br"),n._v("Phiêu Miễu ngoa, cứ việc không phải Ngụy Thần cảnh pháp bảo, lại có được năng lực đặc thù, có thể để người ta đạp phá không gian, tiến hành xuyên qua, phối hợp thêm Thái Huyền kính, thế mà ngay cả mình đều không ngăn cản nổi!"),g("br"),g("br"),n._v("Đối phương có thứ này, bản thân không có, không nhanh chóng nghĩ biện pháp, làm không cẩn thận thật sự sinh linh đồ thán."),g("br"),g("br"),n._v("Mặc dù mau, nhưng trong lòng không hoảng hốt, đem những ngày này trải qua sự tình, từ từ cân nhắc."),g("br"),g("br"),n._v("“Khổng sư ở đây, giành lấy qua Thần chữ, Lăng Vân kiếm các khai phái tổ sư cũng ở nơi đây giành lấy qua nửa chữ, chứng minh nơi này, mới thật sự là Thần điện... Trước đó, tiến vào cái kia, có thể là giả!”"),g("br"),g("br"),n._v("Chưa đi đến đập vào mắt trước cái này cung điện, có thể sẽ cho rằng, trước đó bị mười tên Ngụy Thần cảnh cường giả vây công địa phương, là chân chính Thần điện, hiện tại xem ra, nơi này mới là!"),g("br"),g("br"),n._v("Cái kia... Có lẽ chỉ là vị kia Khổng sư, cố ý mời mình vào cuộc địa phương, thậm chí, là chân chính Thông Thần điện tổng bộ!"),g("br"),g("br"),n._v("Nếu không, làm sao có thể tuỳ tiện đi vào không gian gấp?"),g("br"),g("br"),n._v("Mà đối phương nói tới Thông Thần điện tổng bộ, lại thế nào khả năng một bóng người đều không có? Càng trước thời hạn bố trí xong, có thể bùng nổ trận pháp... Rõ ràng không phù hợp đạo lý!"),g("br"),g("br"),n._v("Nếu như đây là Thần điện, như vậy... Vài ngàn năm trước, Thông Thiên kiều chỗ giáp nhau, là nơi này, nguyên nhân chính là như vậy, Khổng sư cùng kiếm các khai phái tổ sư mới có thể thuận lợi tiến vào bên trong!"),g("br"),g("br"),n._v("Nếu không, hai người này, không có tế đàn, thông đạo là phá hỏng, làm sao cướp đoạt Thần chữ?"),g("br"),g("br"),n._v("Làm sao lần nữa trở lại Thượng Thương, khai tông lập phái!"),g("br"),g("br"),n._v("“Đây là Thần điện lời nói, Thông Thiên kiều khẳng định tồn tại...”"),g("br"),g("br"),n._v("Cẩn thận quan sát."),g("br"),g("br"),n._v("Tế đàn nổ nát địa phương, trống không, thật giống đứng sừng sững hư không bên trong, bốn phía tràn đầy hắc ám, không có nửa điểm sinh cơ."),g("br"),g("br"),n._v("Minh Lý Chi Nhãn, đem chung quanh toàn bộ quét một lần, cũng không phát hiện liên quan tới Thông Thiên kiều bất cứ dấu vết gì."),g("br"),g("br"),n._v("Chẳng lẽ đoán sai?"),g("br"),g("br"),n._v("Lại dạo qua một vòng, vẫn như cũ không có phát hiện bất kỳ thông đạo, liền giống như trước mắt cái cung điện này, cô lập lơ lửng trong hư không đồng dạng, Trương Huyền đầy mặt bất đắc dĩ, đang không biết như thế nào cho phải, đột nhiên con mắt rơi vào cung điện hai cái cắm vào hư không trên cây cột."),g("br"),g("br"),n._v("Cái này hai cái cây cột, vừa đến chỉ thấy."),g("br"),g("br"),n._v("Đâm vào hư không, không nhìn thấy cuối cùng, quan trọng còn không biết làm bằng vật liệu gì luyện chế mà thành, ít nhất đều đạt đến Ngụy Thần cấp bậc, dù sao lấy thực lực của hắn, không cách nào phá hư."),g("br"),g("br"),n._v("“Có thể hay không... Đây là cái gọi là Thông Thiên kiều?”"),g("br"),g("br"),n._v("Địa phương khác không có thông đạo, đã kiểm tra xong mấy lần, có lẽ cái này hai cái không nhìn thấy đầu cây cột, mới là quan trọng!"),g("br"),g("br"),n._v("“Đi lên xem một chút!”"),g("br"),g("br"),n._v("Đem phân thân thu vào trữ vật giới chỉ, thân thể vụt qua, dán chặt cây cột, bò lên."),g("br"),g("br"),n._v("Cây cột không biết cao bao nhiêu, Trương Huyền không ngừng hướng lên trên, ngay sau đó nhìn thấy cung điện càng ngày càng nhỏ, cho đến biến thành một điểm tròn."),g("br"),g("br"),n._v("Trọn bò lên hai canh giờ, đột phá một tầng cách ngăn, đen kịt hư không xuất hiện một tia sáng, ngay sau đó Trương Huyền cảm thấy không gian thoáng cái nghịch chuyển tới, vốn leo lên trên đi, trong chốc lát, biến thành hướng phía dưới, cả người đang ôm cây cột, bất cứ lúc nào cũng sẽ từ không trung rơi xuống."),g("br"),g("br"),n._v("Loại tình huống này, Tiếp Thiên thạch đi vào Thông Thiên kiều lúc liền trải qua, thời không khác biệt, cho người cảm giác cũng không giống nhau."),g("br"),g("br"),n._v("Tại Thần điện cảm giác là leo lên trên đi, lúc này nhìn tới, là tại hướng phía dưới, Thần điện tại thực sự phía trên."),g("br"),g("br"),n._v("Quay ngược lại thân thể, nhìn xuống dưới."),g("br"),g("br"),n._v("Một tòa hùng vũ cung điện, xuất hiện tại tầm mắt."),g("br"),g("br"),n._v("“Đây là... Trước đó tiến vào ngôi thần điện kia?”"),g("br"),g("br"),n._v("Liếc mắt nhìn, liền nhận ra được."),g("br"),g("br"),n._v("Chính là quãng thời gian trước, thông qua Thông Thiên kiều, đi tới toà kia, gặp rất nhiều Ngụy Thần cảnh cường giả, hơn nữa tới đại chiến, bị vị kia Khổng sư cứu đi."),g("br"),g("br"),n._v("“Nguyên lai cái này hai tòa cung điện, là liên tiếp...”"),g("br"),g("br"),n._v("Lúc này mới phát hiện, cái này hai cái cây cột, vừa vặn kết nối tại cung điện đỉnh, trước đó cho rằng chỉ là trang trí tác dụng, náo loạn hồi lâu, cái này cung điện, cũng không phải là cuối cùng."),g("br"),n._v(" Chỉ có dọc theo cây cột bò sát, mới có thể đi vào chân chính Thần điện."),g("br"),g("br"),n._v("“Vị kia Khổng sư, hẳn là trở lại nơi này...”"),g("br"),g("br"),n._v("Ánh mắt sáng lên, đem khí tức của mình áp chế lại, Trương Huyền đi vào cung điện phía trên."),g("br"),g("br"),n._v("Vị kia giả Khổng sư, chính là ở đây đem bản thân cứu đi, nếu như hắn nói không sai, trước đó những người kia đều là thuộc hạ của hắn... Tòa cung điện này, chính là đối phương sào huyệt!"),g("br"),g("br"),n._v("Đạt được màu vàng kim thể khí, thật muốn đột phá, đoán chừng cũng sẽ ở đây!"),g("br"),g("br"),n._v("Nghĩ biện pháp, lại đem thể khí cướp đi, chỉ cần mình đột phá... Đối phương ngay tại không đáng để lo."),g("br"),g("br"),n._v("Dọc theo cung điện bức tường lặng lẽ tuột xuống, hướng cung điện phía trong đi vào."),g("br"),g("br"),n._v("Cũng không biết là bởi vì biết không có người có thể đi vào, vẫn cảm thấy không người dám xông, trong điện không có an bài hộ vệ, Trương Huyền lặng lẽ tiến vào bên trong."),g("br"),g("br"),n._v("Đại điện mênh mông như dã, dán chặt lấy bức tường, Trương Huyền đi vào trong, đang muốn tìm kiếm vị kia Khổng sư ở nơi nào, một hồi tiếng bước chân vang lên."),g("br"),g("br"),n._v("“Tông chủ có lệnh, có thể đem vị kia xử tử!”"),g("br"),g("br"),n._v("Hai bóng người đi tới, vừa đi vừa trò chuyện."),g("br"),g("br"),n._v("Nơi này cơ bản không người, bọn họ cũng không có gì giấu diếm cùng lo lắng do dự."),g("br"),g("br"),n._v("“Xử tử?”"),g("br"),g("br"),n._v("“Đúng vậy a, trước đó dùng nàng, là bởi vì muốn mượn lực lượng của nàng, niệm động chú ngữ, kích hoạt tế đàn, hiện tại tông chủ chỉ cần đột phá Thần Linh, không cần tế đàn, cũng có năng lực, làm được bất cứ chuyện gì! Nàng sống sót vẫn là tử vong, đã ý nghĩa không lớn...”"),g("br"),g("br"),n._v("“Này ngược lại là, tông chủ một khi thành Thần, chúng ta khẳng định chỗ tốt không ít, ngoan ngoãn nghe lời, có lẽ cũng có cơ hội!”"),g("br"),g("br"),n._v("“Không phải là, nhanh lên một chút động thủ đi...”"),g("br"),g("br"),n._v("Thảo luận âm thanh càng ngày càng xa."),g("br"),g("br"),n._v("“Đọc chú ngữ, kích hoạt tế đàn? Chẳng lẽ là... Đỗ cung chủ?” Trương Huyền lông mày giương lên."),g("br"),g("br"),n._v("Đỗ cung chủ bị bắt, lần trước nhìn thấy thời điểm, dường như ngay tại cái này cung điện, nhìn tới vẫn tại cái này."),g("br"),g("br"),n._v("Hơn nữa, nghe đối phương giọng nói, cũng định xuống tay với nàng."),g("br"),g("br"),n._v("“Trước tiên đem người cứu lại nói...”"),g("br"),g("br"),n._v("Híp mắt lại, chẳng muốn phí lời, nhẹ nhàng vụt qua đã đi tới hai người sau lưng, Đồng Thường kiếm vung vẩy."),g("br"),g("br"),n._v("Kiếm mang di tán mà ra."),g("br"),g("br"),n._v("Phốc! Phốc!"),g("br"),g("br"),n._v("Hai vị này Ngụy Thần cảnh cường giả, liền phản ứng cũng không kịp, đầu đồng loạt rơi trên mặt đất, đồng thời thi thể bị thu vào trữ vật giới chỉ."),g("br"),g("br"),n._v("Tiên Quân đỉnh phong thời điểm, phổ thông Ngụy Thần, một kiếm liền có thể tuỳ tiện đánh giết, giờ phút này không những đột phá, càng là sáng chế ra vượt qua Thiên đạo kiếm thuật, đánh lén giết người, dễ như trở bàn tay, không có làm ra một tia động tĩnh, thậm chí liền sóng linh khí, đều không có chấn động tới."),g("br"),g("br"),n._v("Đem hai cái này chém giết, đã biến hóa thành một người trong đó hình dáng, bàn tay một trảo, trong giới chỉ đối phương y phục mặc ở trên người, bất luận nhìn thế nào, đều giống nhau như đúc, tìm không thấy bất kỳ sơ hở."),g("br"),g("br"),n._v("Ngụy trang xong xuôi, Trương Huyền nhấc chân tiếp tục đi đến phía trước."),g("br"),g("br"),n._v("Cái cung điện này tuy là rộng lớn, cung điện lại không nhiều, đối phương đi phương hướng lại chỉ một phần mười, tựu tính không thẩm vấn, cũng biết Đỗ cung chủ bị giam giữ ở nơi nào."),g("br"),g("br"),n._v("Thẳng tắp tiến lên, quả nhiên thấy một cái phong bế gian phòng xuất hiện tại phía trước, cổ tay khẽ đảo, trên thân hai người chìa khoá xuất hiện tại lòng bàn tay, nhẹ nhàng mở ra, đẩy cửa đi vào."),g("br"),g("br"),n._v("Ngay sau đó nhìn thấy một cái trung niên phụ nhân, trên tay chân đều mang xích sắt, ngồi trong phòng, đầu tóc rối bời, tinh thần uể oải suy sụp, khí tức cũng hết sức yếu ớt, bị thương rất nặng."),g("br"),g("br"),n._v("Thời khắc này Đỗ Thanh Diên, đã tỉnh táo lại, cùng trước đó nhìn thấy thần chí mơ hồ, hoàn toàn khác biệt."),g("br"),g("br"),n._v("“Hắn muốn giết ta sao?”"),g("br"),g("br"),n._v("Thấy Trương Huyền đi vào, còn tưởng rằng là đối phương Ngụy Thần, Đỗ Thanh Diên trên mặt không lộ vẻ gì, dường như đã biết kết quả."),g("br"),g("br"),n._v("Trương Huyền không có trả lời, trở tay đóng cửa lại."),g("br"),g("br"),n._v("“Động thủ đi, tế đàn bị hủy, ta liền biết, đã không còn giá trị, khẳng định chỉ có một con đường chết...”"),g("br"),g("br"),n._v("Đỗ Thanh Diên hừ lạnh."),g("br"),g("br"),n._v("Tế đàn là nàng luyện hóa bảo vật, phá đi, tự nhiên có thể cảm ứng được."),g("br"),g("br"),n._v("Đối phương bắt được mục đích của mình là vì tốt hơn sử dụng tế đàn, hiện tại liền món bảo vật này đều hỏng, không cần nghĩ, giờ chết sắp tới, lại không sống tiếp lý do."),g("br"),g("br"),n._v("“Đã đều phải chết, ta hi vọng, có thể cho cái thể diện...”"),g("br"),g("br"),n._v("Đỗ Thanh Diên vẻ mặt có phần cô đơn."),g("br"),g("br"),n._v("Không nghĩ tới tung hoành thiên hạ cả đời, cuối cùng chết tại nhỏ như vậy trong phòng."),g("br"),g("br"),n._v("Bàn tay nâng lên, đem trâm cài tóc mở ra, dùng tay vuốt vuốt mái tóc đen nhánh, hơi sửa sang lại quần áo một chút, chậm rãi đứng lên, nhìn về phía trước mắt Trương Huyền: “Động thủ đi!”"),g("br"),g("br"),n._v("Nói xong, hai tay chắp sau lưng, nhắm mắt đợi chết, ngay tại nàng cảm thấy, khẳng định không cách nào may mắn thoát khỏi thời điểm, bên tai truyền đến thanh âm nhàn nhạt."),g("br"),g("br"),n._v("“Đỗ cung chủ ngươi tốt, chúng ta lại gặp mặt!”"),g("br"),g("br"),n._v("Vội vàng mở mắt ra, lập tức nhìn thấy trước mắt Ngụy Thần, biến hóa hình dáng, thành một cái chừng hai mươi tuổi thanh niên."),g("br"),g("br"),n._v("“Trương Huyền?”"),g("br"),g("br"),n._v("Đỗ Thanh Diên thân thể cứng đờ, phút chốc kích động run rẩy lên."),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-2100"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-2102"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);