(window.webpackJsonp=window.webpackJsonp||[]).push([[125],{479:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1090-đoc-đien-tong-bo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1090-đoc-đien-tong-bo"}},[n._v("#")]),n._v(" Chương 1090: Độc điện tổng bộ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1090: Độc điện tổng bộ"),t("br"),t("br"),n._v("Suy nghĩ một chút vừa rồi quét rác vị kia học viên công kích, hai đại phó viện trưởng liền cảm thấy lệ rơi đầy mặt."),t("br"),t("br"),n._v("Quả thực quá mạnh, thật giống như có thể xem thấu bọn họ chiêu số bên trong thiếu hụt đồng dạng, vô luận như thế nào tránh né, đều có thể đánh vào trên mặt, không lệch không dời..."),t("br"),t("br"),n._v("Nếu không phải thực lực của bọn hắn đều đạt đến Nguyên Thần cảnh, thân thể cường hãn, chỉ sợ hiện tại cũng đã không bò dậy nổi."),t("br"),t("br"),n._v("Vốn cho rằng cái này quét rác học viên chỉ là ví dụ, một chiêu bại trận về sau, tại Huyền Huyền hội lại tùy tiện tuyển một cái... Kết quả, thua hơn!"),t("br"),t("br"),n._v("Cùng cấp bậc bọn họ, căn bản cũng không phải là những tên kia đối thủ!"),t("br"),t("br"),n._v("“Huấn luyện mấy tiết khóa?”"),t("br"),t("br"),n._v("Nhìn thấy hai đại phó viện trưởng hình, Chương Dẫn Khâu nhớ tới thanh niên đã nói với hắn lời nói, khóe miệng nhịn không được run rẩy... Vị này Trương sư đến cùng huấn luyện cái gì? Để cái này học viên có như thế biến hóa nghiêng trời lệch đất?"),t("br"),t("br"),n._v("Thực sự nhịn không được, nhìn về phía dưới thân Tử Dương thú, hỏi lên."),t("br"),t("br"),n._v("“Trương viện trưởng, là cho những học sinh này có chui lên lớp, bất quá... Tình huống cụ thể ta không rõ ràng!” Tử Dương thú lắc đầu."),t("br"),t("br"),n._v("Nó phần lớn thời gian đều tại Thiên Nghĩ Phong sào bên trong tu luyện, đối học viện sự tình, biết đến không nhiều."),t("br"),t("br"),n._v("“Đi tìm Mi Trúc bọn họ đi!”"),t("br"),t("br"),n._v("Thấy nó không rõ ràng, ba người không hỏi thêm nữa, vội vã hướng trưởng lão viện chạy tới, thời gian không dài, thấy được Triệu Bính Tuất, Mi Trúc đám người, lần nữa gặp mặt nhịn không được hàn huyên, khách sáo xong xuôi, đem nghi vấn trong lòng nhịn không được hỏi lên."),t("br"),t("br"),n._v("“A, Chiến sư đường liên tục tới hai nhóm người, đều bị đánh đánh mất lòng tin, trở về, khóc gọi là một cái, thậm chí nghe nói muốn khiêu chiến, sợ tới mức trong đêm trốn, mặt đều không có dám thấy...”"),t("br"),t("br"),n._v("Mi Trúc đem trước chuyện nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Ngươi xác định, không phải chúng ta học viên đánh mất lòng tin?”"),t("br"),t("br"),n._v("Ba đại viện trưởng đồng thời phát điên."),t("br"),t("br"),n._v("Bọn họ liền bị giam tại di tích bên trong thời gian hai năm, danh sư học viện đến cùng đã trải qua thứ gì..."),t("br"),t("br"),n._v("“Còn không chỉ có như vậy, Trương viện trưởng, còn đem Vân Vụ lĩnh tất cả Thánh thú, linh thú đều thuần phục, hơn nữa phân cho thuần thú học viện tất cả học viên, hiện tại chúng ta học viện chỉ riêng Quy Nhất cảnh cấp bậc trở lên linh thú, liền có hai vạn đầu trở lên... Mặt khác, viện trưởng công khai giảng bài, làm cho cả học viện sức chiến đấu chợt tăng không chỉ một lần...”"),t("br"),t("br"),n._v("Không để ý tới ba người kinh ngạc, Mi Trúc đem học viện biến hóa, tiếp tục nói."),t("br"),t("br"),n._v("Nói càng nhiều, chỉ thấy trước mắt ba vị viện trưởng, thân thể càng cứng ngắc, còn không có hoàn toàn nói hết, chỉ thấy bọn họ đã hoàn toàn hóa đá."),t("br"),t("br"),n._v("Trước đó còn lo lắng vị này Trương viện trưởng tuổi trẻ, quản lý không được học viện, bây giờ mới biết... Bọn họ quản lý trình độ so sánh cùng nhau, vỗ ngựa cũng không đuổi kịp..."),t("br"),t("br"),n._v("Bọn họ còn nghi ngờ đối phương, thật sự là buồn cười..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mười ngày sau."),t("br"),t("br"),n._v("Một cái cực lớn thuyền lên, Trương Huyền đứng tại boong thuyền, nhìn về phía trước, trong khoang thuyền, mấy vạn vị Huyền Huyền hội thành viên, ngồi ngay ngắn trong đó, nguyên một đám tràn đầy hưng phấn."),t("br"),t("br"),n._v("Chuẩn bị ổn thoả, Trương Huyền trong mười ngày, lại cho mọi người nói năm tiết khóa, lúc này mới dẫn đầu mọi người, hướng Thanh Nguyên nhất đẳng đế quốc bay đi."),t("br"),t("br"),n._v("Lúc này, danh sư học viện phi chu cũng rốt cục kiến tạo được rồi, cái đầu cực lớn, tìm mấy chục con Thánh thú mới nâng giơ lên."),t("br"),t("br"),n._v("Lần này đi tới Chiến sư đường trao đổi, Chương Dẫn Khâu, Kê Nham đám người liền lưu lại, tại Hồng Viễn học viện chủ trì sự vật, Trương Huyền chỉ đem Mi trưởng lão cùng Mạc Cao Viễn đường chủ mang tại bên cạnh."),t("br"),t("br"),n._v("Hắn cũng định được rồi, đi Thanh Nguyên đế quốc, về Hồng Viễn học viện thời gian khẳng định cực ít, thậm chí cũng có thể không về nữa, liền đem viện trưởng vị trí, đưa cho Chương Dẫn Khâu."),t("br"),t("br"),n._v("Nhưng kẻ sau, bất luận thế nào đều không đồng ý, dù sao hắn hiện tại còn thuộc về nửa chết nửa sống trạng thái, đường đường học viện viện trưởng, là hắn bộ dáng này, truyền đi có sai lầm danh sư tôn nghiêm."),t("br"),t("br"),n._v("Hắn không đồng ý, đành phải tiếp tục trên danh nghĩa viện trưởng, còn chuyện, lần nữa làm vung tay chưởng quỹ, toàn quyền giao cho đối phương xử lý."),t("br"),t("br"),n._v("Thời gian mười ngày, nghiên cứu không ít liên quan tới luyện khí sách, nhìn một chút có thể hay không đem Kim Nguyên đỉnh khôi phục, đáng tiếc, Hồng Viễn thành luyện khí trình độ quá thấp, cưỡng ép xuất thủ, là có thể để hắn lần nữa sinh ra linh trí, mà muốn cho ngủ say linh trí khôi phục, liền không làm được."),t("br"),t("br"),n._v("Kim Nguyên đỉnh vì cứu bọn họ mới biến thành như vậy, tự nhiên không thể biến mất, suy đi nghĩ lại, Trương Huyền quyết định đi trước Thanh Nguyên đế quốc, học tập cao hơn thuật luyện khí về sau, lại nghĩ biện pháp giúp nó khôi phục."),t("br"),t("br"),n._v("Nhìn trước mắt phất phới mây trắng, cảm thụ bốn phía tiếng gió gào thét, Trương Huyền thở dài một tiếng."),t("br"),t("br"),n._v("Hôm qua, cũng chính là khởi hành trước một ngày, Lạc Nhược Hi tìm tới, nói cho hắn biết, mình còn có sự tình, muốn rời khỏi Hồng Viễn đế quốc, không đi Thanh Nguyên đế quốc, mặc dù lòng tràn đầy không muốn, hắn vẫn là gật đầu đồng ý."),t("br"),t("br"),n._v("Dù sao biết nàng thân phận, không lo đằng sau không thấy được, không cần thiết nhi nữ tình trường."),t("br"),t("br"),n._v("Ngô sư đám người nói rất đúng, thực lực của hắn bây giờ, đối mặt chống lại Thánh Nhân quý tộc lời nói, còn kém rất nhiều, muốn chân chính cùng một chỗ, cần càng thêm cố gắng mới được."),t("br"),n._v("“Trương sư... Đây là cha ta cùng Hoài Vương gia đưa cho ngươi quà tặng, chỉ sợ ngươi không thu, muốn ta rời khỏi Hồng Viễn thành sau lại giao cho ngươi!”"),t("br"),t("br"),n._v("Một cái thanh âm thanh thúy vang lên, Ngọc Phi Nhi đi tới trước mặt."),t("br"),t("br"),n._v("Lần này đi tới Thanh Nguyên đế quốc, không riêng gì nàng, Lạc Thất Thất cùng Hồ Yêu Yêu cũng theo sau, mấy người các nàng hiện tại cũng đều là Huyền Huyền hội thành viên, xem như có tư cách đi gặp việc đời."),t("br"),t("br"),n._v("“Quà tặng?” Không nghĩ tới Ngọc Thần Thanh bệ hạ khách khí như vậy, Trương Huyền tiện tay nhận lấy."),t("br"),t("br"),n._v("ngantruyen.com/ để  đọc truyện"),t("br"),n._v("“Biết Trương sư tại luyện khí, luyện đan bên trên tinh thông, trong giới chỉ đều là chút dược liệu cùng khoáng thạch, mặc dù không phải đặc biệt trân quý, nhưng thắng ở số lượng không ít, về sau Trương sư có lẽ có thể cần dùng đến!”"),t("br"),t("br"),n._v("Ngọc Phi Nhi cười cười."),t("br"),t("br"),n._v("Trương Huyền thân là danh sư học viện viện trưởng, về sau tiền đồ vô lượng, Ngọc Thần Thanh đám người, đương nhiên sẽ không buông tha cây to này."),t("br"),t("br"),n._v("“Thật ra thì, ta còn muốn cảm ơn phụ thân ngươi!” Biết đối phương chân tâm thật ý, không cách nào cự tuyệt, Trương Huyền đành phải nhận lấy, cười khổ một tiếng."),t("br"),t("br"),n._v("Nói thật, nếu không phải Ngọc Thần Thanh đem Bồ Đề thụ hạt giống cho hắn, trồng ra Thánh vực Bồ Đề thụ, chỉ sợ rất khó thông qua Hắc Yên Trần Sa!"),t("br"),t("br"),n._v("Có thể nói trong chuyện này, hắn còn không hảo hảo cảm giác Tạ Nhất bên dưới vị này Ngọc Thần Thanh bệ hạ."),t("br"),t("br"),n._v("“Trương sư, ta tu vi bên trên xuất hiện vấn đề, có thể hay không chỉ cho ta điểm thoáng cái...” Không tại cái đề tài này bên trên tiếp tục xoắn xuýt, Ngọc Phi Nhi tràn đầy khát vọng nhìn qua."),t("br"),t("br"),n._v("“Đương nhiên có thể...” Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Chỉ điểm một hồi, Lạc Thất Thất, Hồ Yêu Yêu mấy người cũng đi tới trước mặt, Trương Huyền dứt khoát liền đem gần nhất tu luyện tâm đắc nói một lần."),t("br"),t("br"),n._v("Nghe qua Khổng sư lời khuyên, hắn những ngày này không có gấp tu luyện, mà là không ngừng suy tư."),t("br"),t("br"),n._v("Mặc dù không sáng chế công pháp lợi hại gì, võ kỹ, lại làm cho tu vi của hắn trở nên càng thêm củng cố, càng thêm thuận buồm xuôi gió."),t("br"),t("br"),n._v("Giờ phút này tùy tiện giải thích vài câu, đối tu luyện giả tới nói, đều là lời vàng ngọc, được ích lợi không nhỏ."),t("br"),t("br"),n._v("Một tháng thời gian, thoáng qua liền qua."),t("br"),t("br"),n._v("Ba vị nữ hài có cực lớn tiến bộ."),t("br"),t("br"),n._v("Ngọc Phi Nhi trước đó là Tằm Phong cảnh đỉnh phong, đi qua cái này tiếp cận ba tháng tu luyện, chẳng những đột phá Bán Thánh, càng là đạt đến đỉnh phong! Hồ Yêu Yêu càng là đạt đến Tòng Thánh đỉnh phong cấp bậc!"),t("br"),t("br"),n._v("Đương nhiên, muốn nói tiến bộ nhanh nhất, không phải hai người này, mà là Lạc Thất Thất, hơn hai tháng trước, cùng Ngọc Phi Nhi đồng dạng, đều là Tằm Phong cảnh đỉnh phong, bây giờ lại cùng Trương Huyền đồng dạng, đạt đến Thánh vực nhất trọng đỉnh phong!"),t("br"),t("br"),n._v("Như thế nhanh chóng tiến bộ, liền xem như Trương Huyền, đều thất kinh, có thể nói so với hắn đều muốn nhanh"),t("br"),t("br"),n._v("Nhìn tới thật có thiên phú tuyệt cao người."),t("br"),t("br"),n._v("Không chỉ tu vi bạo tăng, thuật luyện đan lên, cũng đột nhiên tăng mạnh, nếu không phải tu vi không đủ, chỉ sợ cấp bảy sơ kỳ đan dược, đều có thể nhẹ nhõm luyện chế."),t("br"),t("br"),n._v("Ba người này tiến bộ thần tốc, Vương Dĩnh, Lưu Dương hai vị thân truyền, càng không nhàn rỗi, Vương Dĩnh theo hơn hai tháng trước Tằm Phong cảnh sơ kỳ, một lần đột phá đến Tòng Thánh đỉnh phong, Lưu Dương cũng đạt tới Bán Thánh đỉnh phong!"),t("br"),t("br"),n._v("Đây là bởi vì tu luyện chính là bản rút gọn Phong Thánh giải nguyên nhân, nếu là tu luyện bình thường công pháp, chỉ sợ cũng sớm đã đột phá đến cảnh giới cao hơn."),t("br"),t("br"),n._v("Tôn Cường vẫn như cũ là tiến bộ chậm chạp, cứ việc tu luyện không như vậy tận tâm, nhưng gặp Trương Huyền, có thể tính nhắm vào chỉ điểm, lại thêm đủ loại vật tư, nhiều vô số kể, phú khả địch quốc, cùng Lạc Thất Thất, Vương Dĩnh đám người so ra chậm, cùng cái khác học sinh so, cũng nhanh như ngồi chung hỏa tiễn."),t("br"),t("br"),n._v("Giờ phút này đạt đến Đạp Hư cảnh đỉnh phong, khoảng cách Tằm Phong cảnh cũng chỉ có cách xa một bước."),t("br"),t("br"),n._v("Đi qua một tháng này chìm xuống đáy, Trương Huyền đã hoàn toàn củng cố Thánh vực nhất trọng đỉnh phong thực lực, tu vi càng thêm hùng hậu, cứ việc không đột phá, nhưng thực lực chân chính, đã là Nguyên Thần cảnh bên dưới vô địch."),t("br"),t("br"),n._v("Mấu chốt nhất là, trên tâm cảnh tu dưỡng, trước đó đi qua sư chữ luyện tâm, đã đạt đến 2 1.1, có thể so với đồng dạng thất tinh danh sư, mà bây giờ, đi qua phong thánh kiếp cùng trong khoảng thời gian này tích lũy, lần nữa tăng lên 2. 0, đạt đến 23. 1."),t("br"),t("br"),n._v("Tâm cảnh khắc độ, cùng tu vi đồng dạng, càng cao càng khó, theo tâm cảnh 1. 0 đến 3.0, mười phần đơn giản, nhưng theo 2 1.1 đến 23. 1, muốn khó khăn bên trên mấy chục lần."),t("br"),t("br"),n._v("Bất quá, nếu không phải phong thánh kiếp bên trong tâm kiếp, rất dễ dàng đối phó, không một điểm tính khiêu chiến, khẳng định tăng trưởng không chỉ điểm này."),t("br"),t("br"),n._v("Một đường tu luyện, không có quá nhiều lời nói, phi chu ròng rã phi hành một tháng thời gian, mọi người lúc này mới nhìn thấy một cái thành phố khổng lồ, chậm rãi xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Thanh Nguyên đế quốc Đế đô, Thanh Nguyên thành, đến!"),t("br"),t("br"),n._v("Bên này, Trương Huyền đám người đi tới Thanh Nguyên Đế đô, Bạch Đình độc sư cùng Hải Minh Kim độc sư, cũng mang theo Ngụy Như Yên, rốt cục chạy tới Độc điện tổng bộ."),t("br"),t("br"),n._v("Hoành Tảo Thiên Nhai nói"),t("br"),t("br"),n._v("Hết sốt, viết một chương. Thuận tiện giải thích một chút a, nhìn tất cả mọi người nói Trương Huyền là Trương gia người, cùng Lạc Nhược Hi đính hôn, cũ đường. Ha ha, lão nhai sách, nếu như tốt như vậy đoán, cũng không cần lăn lộn! Yên tâm đi, tất cả bình luận sách ta đều nhìn, hiện nay mỗi một cái đoán đúng, hắc hắc!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1098"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1100"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);