(window.webpackJsonp=window.webpackJsonp||[]).push([[1258],{1613:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2104-nhu-vay-than-gioi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2104-nhu-vay-than-gioi"}},[n._v("#")]),n._v(" Chương 2104: Như vậy Thần giới")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Thần giới."),t("br"),t("br"),n._v("Tựa như tuyên cổ tồn tại, không biết kéo dài đã bao nhiêu năm, vô số cổ xưa Thần Linh, từ khi có trí nhớ bắt đầu, vẫn sừng sững chư thiên vạn giới chi đỉnh, vĩnh cửu không suy."),t("br"),t("br"),n._v("Ánh tà dương đỏ quạch như máu, gió mạnh như đao."),t("br"),t("br"),n._v("Một bóng người, một mình đi tại hoang tàn vắng vẻ sơn cốc, đi thong thả tiến lên."),t("br"),t("br"),n._v("Đột nhiên, ngừng lại, trốn ở một cái nham thạch đằng sau, vừa giấu kỹ thân hình, một cái bóng xám nhảy cà tưng đi vào cách đó không xa."),t("br"),t("br"),n._v("Là cái màu xám thỏ rừng."),t("br"),t("br"),n._v("Bóng người đập mạnh mang theo khô quắt bờ môi, tiện tay nhặt lên trên đất một cái gậy gỗ, bỗng nhiên nhảy lên, cây gậy phía trên, lập tức bắn ra một đạo kiếm mang, diễn biến ra tựa như lưới đánh cá đồng dạng kiếm khí."),t("br"),t("br"),n._v("Ba!"),t("br"),t("br"),n._v("Lan ra không đến một mét, tiêu tán trên không trung."),t("br"),t("br"),n._v("Thấy kiếm khí đủ không đến đối phương, bóng người quyết định thật nhanh, bàn tay buông lỏng, cây gậy thẳng tắp hướng về phía thỏ rừng bay đi, tựa như tên bắn ra mũi tên."),t("br"),t("br"),n._v("Vèo!"),t("br"),t("br"),n._v("Đã có đề phòng, thỏ rừng chân sau giẫm một cái, nhảy ra năm, sáu gạo khoảng cách, gậy gỗ cắm trên mặt đất, không ngừng lay động."),t("br"),t("br"),n._v("Quay đầu liếc mắt nhìn đánh lén bóng người của nó, tựa như mang theo chê cười, liên tục vài cái nhảy lên, thỏ rừng biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("“Đáng tiếc...” Bất đắc dĩ lắc đầu, bóng người cười khổ."),t("br"),t("br"),n._v("Chính là đi vào Thần giới Trương Huyền."),t("br"),t("br"),n._v("Danh sư đại lục vạn thế chi sư, Thượng Thương bốn tông tông chủ, thiên hạ đệ nhất nhân, đến nơi này sau đó, thậm chí ngay cả con thỏ đều bắt không được, thậm chí bị đối phương miệt thị, truyền đi, tuyệt đối sẽ để người cười đến rụng răng."),t("br"),t("br"),n._v("Có thể sự thật chính là như vậy, không có những biện pháp khác."),t("br"),t("br"),n._v("“Xem ra hôm nay lại chỉ có thể ăn rau dại...” Biết loại này đi săn cơ hội, có thể ngộ nhưng không thể cầu, lần này chưa bắt được, còn muốn tìm, gần như không có khả năng, Trương Huyền bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Đã đi tới Thần giới hơn nửa tháng."),t("br"),t("br"),n._v("Xuyên qua cái kia đoạn không gian loạn lưu về sau, là đến nơi này."),t("br"),t("br"),n._v("Cùng đi vào Thượng Thương khác biệt, cũng không hôn mê, có điều, suy nghĩ kỹ một chút, còn không bằng hôn mê càng tốt hơn."),t("br"),t("br"),n._v("Hắn “Thiên thiên kết” kiếm võng, là rất cường đại, nhưng Thần giới không gian lực lượng thật là đáng sợ, vừa tiến vào trong, lập tức bị trọng thương, kém chút bị xé thành mảnh nhỏ."),t("br"),t("br"),n._v("Nếu không phải Lạc Nhược Hi cho mặt dây chuyền hộ thân, chỉ sợ đã chết."),t("br"),t("br"),n._v("Đi vào Thần giới, đi qua ngắn ngủi chóng mặt sau đó, phát hiện xuất hiện ở mảnh này trên núi hoang."),t("br"),t("br"),n._v("Nơi này, cả ngày cuồng phong tàn phá bừa bãi, thỉnh thoảng có thể đập chết Ngụy Thần mưa rào tầm tã, còn có hung mãnh thần thú ẩn hiện... Trọng yếu nhất chính là, hầu như không có bất kỳ cái gì linh khí!"),t("br"),t("br"),n._v("Điều dưỡng trọn nửa tháng, mới đưa thương thế áp chế xuống, còn triệt để khôi phục, còn không biết năm nào tháng nào."),t("br"),t("br"),n._v("Trước đó, từ Thượng Thương mang tới nguyên lực đan cùng với rất nhiều bảo vật, ở đây cơ bản không còn hiệu quả, tiểu hoàng kê nước tắm, dường như hiệu quả cũng không lớn."),t("br"),t("br"),n._v("Bản thân bị trọng thương, không cách nào đi săn, chỉ có thể ăn chút cỏ dại, may mắn những thực vật này tuy là khó ăn, chí ít còn ẩn chứa nhất định linh lực, đủ mỗi ngày tiêu hao."),t("br"),t("br"),n._v("Đương nhiên, là cùng trước mắt tình huống so sánh, những này đầy đất thoạt nhìn không đáng giá cỏ dại, nếu như có thể đưa đến Thượng Thương, tuyệt đối là vô số cường giả tha thiết ước mơ linh đan diệu dược, tuyệt phẩm nguyên lực đan dùng tới, đều tính lãng phí!"),t("br"),t("br"),n._v("Hai thế giới chênh lệch quá lớn, không thể so sánh nổi."),t("br"),t("br"),n._v("Cũng nguyên nhân chính là như vậy, Thượng Thương đệ nhất nhân, đến nơi này, cùng người bình thường không có khác nhau quá nhiều."),t("br"),t("br"),n._v("Vô cùng có khả năng, nơi này một con kiến đi vào Thượng Thương, đều có thể nhẹ nhõm diệt đi một cái môn phái, Hàn Kiếm Thu loại này cường giả cũng chưa chắc có thể chống lại."),t("br"),t("br"),n._v("Cái này... Chính là Thần giới!"),t("br"),t("br"),n._v("Thời gian tốc độ chảy so sánh với thương chậm gấp trăm lần, không gian cũng vững chắc gấp trăm lần!"),t("br"),t("br"),n._v("“Không chỉ không thể phi hành, đi bộ đều sẽ trở nên quá mệt, hơn nữa... Cũng cảm thấy đói bụng!”"),t("br"),t("br"),n._v("Nhớ tới cái này hơn nửa tháng trải qua, Trương Huyền liền nói không ra mỏi mệt."),t("br"),t("br"),n._v("Vốn cho rằng đột phá đến Thần Linh, cho dù đến nơi này, cũng là một phương hào hùng, có thể rong ruổi bát phương, nằm mơ đều không có nghĩ đến, trong nháy mắt khôi phục cuộc sống của người bình thường."),t("br"),t("br"),n._v("Đi bộ sẽ mệt, không ăn sẽ đói, chạy nhanh sẽ thở, tỉnh lâu sẽ buồn ngủ..."),t("br"),t("br"),n._v("Trước đó, vừa xuyên qua tới, trở thành cái kia vô dụng lão sư, có vẻ như cũng không có chật vật như thế qua."),t("br"),t("br"),n._v("Thẳng đến lúc này mới hiểu được... Thần Linh cảnh giới ở cái thế giới này, xem như kém nhất tu vi, nói trắng ra là... Liền con thỏ cũng chưa chắc có thể đánh thắng được!"),t("br"),t("br"),n._v("Tựa như hôm qua gặp phải gà rừng, chiến đấu hồi lâu, bị cào nát bàn tay không nói, cuối cùng vẫn để nó chạy trốn."),t("br"),t("br"),n._v("Cái này thỏ cũng giống như vậy."),t("br"),t("br"),n._v("May mắn chỉ là chạy trốn không có xông lại, nếu không, khả năng lại muốn nằm lên hồi lâu."),t("br"),t("br"),n._v("Cô cô cô!"),t("br"),t("br"),n._v("Đang miên man suy nghĩ, phát hiện nghĩ đến nhiều, tiêu hao lớn, cái bụng đói hơn."),t("br"),t("br"),n._v("“Tiếp tục tìm kiếm a, ngộ nhỡ có cái gì ngốc thỏ, bản thân đụng vào trên cây, có lẽ liền có thể mỹ vị một trận...” Liếm môi một cái, cố nén mệt mỏi, tiếp tục tiến lên, vòng qua một cái khe núi, con mắt đột nhiên sáng lên."),t("br"),t("br"),n._v("“Lại có viên trái cây cây!”"),t("br"),t("br"),n._v("Cách đó không xa nham thạch hẹp chỗ khe, nghiêng cắm một cái trái cây cây, phía trên mọc ra bảy, tám cái phát xanh trái cây."),t("br"),t("br"),n._v("Chỉ có to bằng nắm đấm trẻ con, hơn nữa không có chín, nhưng đối với một mực ăn rau dại người mà nói, tuyệt đối tính được là đồ tốt."),t("br"),t("br"),n._v("Vội vàng lấy xuống, hút vào một ngụm, thơm mát xông vào mũi, toàn thân lỗ chân lông đều tựa như mở ra."),t("br"),n._v(" Toàn bộ lấy xuống, lấy ra một cái, Trương Huyền vừa định cắn nát, cuối cùng vẫn lắc đầu."),t("br"),t("br"),n._v("“Quên đi, bọn họ tu vi thấp, càng không chịu nổi!”"),t("br"),t("br"),n._v("Nuốt ngụm nước miếng, đem trái cây dùng hàng mã bao lấy, Trương Huyền xoay người quay trở về."),t("br"),t("br"),n._v("Hắn đã đột phá thần linh, đều đói nhanh như vậy, thân thể có chút không kiên trì nổi, dẫn tới Triệu Nhã đám người, chỉ là Ngụy Thần, càng chịu không được."),t("br"),t("br"),n._v("Mấy ngày nay ăn rau dại, tất cả mọi người nhanh gầy thành mầm đậu xanh, đã thật vất vả gặp được chút linh khí đầy đủ trái cây, đương nhiên là lưu cho bọn hắn."),t("br"),t("br"),n._v("“Bạo loạn khí lưu, mỏng manh linh khí, ta khả năng đi vào một cái giả Thần giới...”"),t("br"),t("br"),n._v("Một bên hướng về đi, một bên lắc đầu."),t("br"),t("br"),n._v("Ở trên thương thời điểm, tưởng tượng Thần giới, đâu đâu cũng có Thần Linh chi khí, tùy ý rút ra, tất cả mọi người có thể đột phá, tiếp đó thầy trò liên hợp cùng một chỗ, tung hoành ngang dọc, vô địch thiên hạ..."),t("br"),t("br"),n._v("Hiện tại xem ra... Cả nghĩ quá rồi."),t("br"),t("br"),n._v("Nhìn bộ dáng bây giờ, đoán chừng còn chưa kịp ngang dọc... Trước hết chết đói."),t("br"),t("br"),n._v("Đi vào Thần giới ngày thứ bảy, bị thương thân thể, mới coi như khôi phục một chút, lúc này mới câu thông tiểu hoàng kê, đem mọi người phóng ra."),t("br"),t("br"),n._v("Còn không gian gấp, đám người sau khi ra ngoài, Thần giới không gian áp lực dưới, trực tiếp rách nát, bên trong tất cả vật phẩm, tất cả đều biến thành tro bụi."),t("br"),t("br"),n._v("Liên tục bảy ngày chưa ăn cơm, ở trên thương không tính là gì, ở tại thần giới, tất cả mọi người đói đã thoi thóp."),t("br"),t("br"),n._v("Tìm chút rau dại ăn xong, mới khôi phục tới."),t("br"),t("br"),n._v("Còn tiểu hoàng kê, cũng không biết là lần trước bị thương không có triệt để khôi phục, mãi cho tới Thần giới không thích ứng, tiếp tục trong đan điền ngủ say, chí ít hiện nay không có tỉnh dậy dấu hiệu."),t("br"),t("br"),n._v("Còn muốn lấy, để con gà này, ra ngoài đứng đài, xúc động chút thần thú, nghĩ biện pháp săn giết, tìm một ít thức ăn, hiện tại xem ra, đã không trông cậy được vào."),t("br"),t("br"),n._v("Thời gian không dài, trở lại tạm thời cư trú sơn động."),t("br"),t("br"),n._v("“Lão sư...” Triệu Nhã đám người đi tới trước mặt: “Chúng ta không bị tổn thương, có thể ra ngoài đi săn hoặc là tìm kiếm ăn...”"),t("br"),t("br"),n._v("Bọn họ một mực trốn ở trữ vật giới chỉ không gian gấp phía trong, gặp được gió lốc thời điểm, càng là tại tiểu hoàng kê trong bụng, cũng không bị thương."),t("br"),t("br"),n._v("Tuy là Thần giới không gian áp chế quá lớn, để hắn cảm giác hài đồng giống như nhỏ yếu, nhưng tìm kiếm cái rau dại, hoặc là nghĩ biện pháp đánh lén cái nhỏ yếu thần thú, có lẽ còn là có thể làm được."),t("br"),t("br"),n._v("“Không được! Bên ngoài tuỳ ý một đầu dã thú chính là Thần Linh, các ngươi hơi không cẩn thận liền có thể bị giết! Vẫn là trước đợi ở chỗ này, nghĩ biện pháp đột phá đến cùng ta tương đồng tu vi lại nói!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Thế nhưng là...” Triệu Nhã đầy mặt sốt ruột."),t("br"),t("br"),n._v("Các nàng là học sinh, để lão sư ngày ngày bất chấp nguy hiểm đi tìm đồ ăn đồ vật, tại tình không thông, tại lễ không hợp."),t("br"),t("br"),n._v("“Tại ta chỗ này không có thế nhưng là! Làm sao, liền lão sư lời nói, đều không nghe?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Học sinh không dám...” Triệu Nhã vội vàng cúi đầu."),t("br"),t("br"),n._v("“Được rồi, xem ta cho các ngươi mang món gì ăn ngon tới...” Mỉm cười, Trương Huyền cầm quần áo ôm lấy trái cây để xuống, nhất thời toàn bộ sơn động, mùi trái cây bốn phía."),t("br"),t("br"),n._v("“Đây là... Trái cây?” Con mắt sáng lên, Viên Đào cùng Tôn Cường nước bọt chảy ròng."),t("br"),t("br"),n._v("Liên tục hơn mười ngày trời, một mực ăn rau dại, sớm đã có chút a-xít dạ dày, giờ phút này nhìn thấy thứ này, sao có thể an chịu được."),t("br"),t("br"),n._v("“Ăn đi!” Trương Huyền cười cười: “Có điều, số lượng có chút ít, mọi người phân một cái, ta một lát nữa lại đi ra tìm xem, nhìn có thể hay không lại tìm càng nhiều hơn một chút! Tôn Cường, ngươi tới chấp hành!”"),t("br"),t("br"),n._v("“Tốt!” Tôn Cường đi vào trước mặt."),t("br"),t("br"),n._v("Trừ bỏ Trương Huyền, ở đây hết thảy mười bốn người. (Mười một vị thân truyền cùng Hưng Mộng kiếm thánh cùng Tôn Cường)"),t("br"),t("br"),n._v("“Tám cái trái cây, thiếu gia, ngươi ăn một cái... Còn lại chúng ta một người một nửa!” Đếm, Tôn Cường nói."),t("br"),t("br"),n._v("“Ta vừa rồi tại bên ngoài nếm qua, các ngươi ăn đi!” Trương Huyền khoát tay áo: “Đem cái kia để lại cho tiểu hoàng kê, nó tuy là vẫn còn ngủ say, tỉnh lại, khẳng định muốn ăn. Không lưu, đoán chừng lại có không ít ý kiến.”"),t("br"),t("br"),n._v("Không nói thêm lời, Tôn Cường mỗi người nửa cái, đem trái cây phân phối xong."),t("br"),t("br"),n._v("Mặc dù xanh chát, nhưng trong đó ẩn chứa linh khí so cỏ dại nhiều hơn nhiều, Viên Đào, Tôn Cường đám người ăn, lập tức cảm thấy một ngày mỏi mệt quét sạch sành sanh, lại không có trước đó như vậy suy yếu."),t("br"),t("br"),n._v("“Lão sư... Ta cái này nửa cái ngươi ăn đi...” Thấy Trương Huyền đứng tại cửa động, Vương Dĩnh đi vào trước mặt."),t("br"),t("br"),n._v("“Vẫn là ngươi ăn đi, mau chóng tăng thực lực lên, mới là vương đạo!” Trương Huyền khoát tay áo, đang muốn tiếp tục nói chuyện, đột nhiên lông mày nhíu một cái: “Yên tĩnh đợi ở chỗ này, ta không khai vù vù, phát sinh bất cứ chuyện gì đều không được đi ra!”"),t("br"),t("br"),n._v("Nói xong, thân thể vụt qua, thẳng tắp hướng ra phía ngoài vọt ra ngoài, chỉ chốc lát liền biến mất ở trong sơn cốc."),t("br"),t("br"),n._v("Thấy hắn rời khỏi, Vương Dĩnh đem ngoài động dây leo lần nữa chải vuốt tốt, để người ngoài không nhìn thấy bọn họ, lúc này mới xoay người lại, nhìn về phía đám người, con mắt có chút hồng nhuận."),t("br"),t("br"),n._v("“Thế nào?” Thấy nàng biểu lộ không đúng, Triệu Nhã đi vào trước mặt."),t("br"),t("br"),n._v("“Không có gì, lão sư để chúng ta cẩn thận một chút, tuyệt đối đừng chỗ đi, hắn hẳn là lại đi ra ngoài tìm kiếm thức ăn!”"),t("br"),t("br"),n._v("Cúi đầu nhìn về phía trong lòng bàn tay nửa cái trái cây, Vương Dĩnh sợ người khác nhìn ra, nước mắt không cầm được nhỏ xuống."),t("br"),t("br"),n._v("Người khác không nhìn ra, nàng tâm tư tỉ mỉ, nhìn rõ ràng, những này trái cây, lão sư tuyệt đối mở miệng cũng chưa ăn."),t("br"),t("br"),n._v("Thần giới loại này tình huống đặc biệt, ở bên ngoài đi săn một ngày, vốn là mỏi mệt kinh khủng, còn đem đồ ăn ngon để lại cho bọn họ, bản thân cố nén đói khát, vừa nghĩ đến điểm này, làm học sinh, trong lòng liền nói không ra cảm thụ."),t("br"),t("br"),n._v("Có ngươi vi sư, sao mà vinh hạnh!"),t("br"),t("br"),n._v("Phảng phất trong nháy mắt, rất khổ Thần giới, cũng không có khổ như vậy, ngược lại có chút ngọt."),t("br"),t("br"),n._v("(Mới cuốn một cái, chương mới. Mọi người có phiếu đề cử, nguyệt phiếu, gửi hai tờ. Khà khà.)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2117"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2119"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);