(window.webpackJsonp=window.webpackJsonp||[]).push([[1262],{1617:function(n,h,g){"use strict";g.r(h);var i=g(44),t=Object(i.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-2107-ngut-troi-đai-đe-khong-su"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2107-ngut-troi-đai-đe-khong-su"}},[n._v("#")]),n._v(" Chương 2107: Ngút trời Đại Đế, Khổng sư?")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("“Thần Linh, Thiên Thần, Thần Vương, phong hào Thần Vương, Đế Quân...”"),g("br"),g("br"),n._v("Trương Huyền trong lòng âm thầm ghi nhớ, đồng thời nghi hoặc: “Lạc Nhược Hi... Là cấp bậc gì?”"),g("br"),g("br"),n._v("Có thể nhẹ nhõm từ Thượng Thương, không thông qua Thần điện, trực tiếp chém thủng hư không đi vào Thần giới, hơn nữa cưỡng ép phá vỡ không gian bích chướng, lại chỉ chịu một chút tổn thương... Khẳng định không phải Thần Linh cấp bậc có thể hoàn thành, thậm chí, Thiên Thần cũng chưa chắc có thể làm được."),g("br"),g("br"),n._v("Chẳng lẽ... Nàng là Thần Vương?"),g("br"),g("br"),n._v("Còn Đế Quân, thật đúng là không dám nghĩ, Cửu Thiên chỉ có chín vị, mỗi một cái đều chân chính đứng ở vũ trụ vạn giới chi đỉnh, loại này siêu tuyệt cường giả, lại thế nào vừa ý một cái bên dưới giới tiểu nhân vật!"),g("br"),g("br"),n._v("Dù là bản thân nắm giữ Thiên đạo thư viện, loại này cấp độ nghịch thiên thần khí."),g("br"),g("br"),n._v("Trong lòng nghĩ như vậy, vẫn là không nhịn được mở miệng, nói: “Mặc Vân lão sư có biết... Cửu Đế xưng hào hoặc là họ tên?”"),g("br"),g("br"),n._v("Thấy hắn hỏi thăm, Mặc Vân lão sư ánh mắt lộ ra nghi ngờ biểu lộ, bất quá vẫn là gật gật đầu, nói: “Cửu Thiên Cửu Đế, cỡ nào tôn quý, bọn họ tôn húy, há lại là chúng ta nhỏ thăng đấu tiểu dân có thể ngôn luận? Ta cũng chỉ biết chúng ta U Hồn thiên Đế Quân vì Càn Khôn Ma Đế, cũng được xưng là Càn Khôn Đế Quân, trấn thủ Thần Vực đông nam, khống chế tứ phương bát động, Lục Thể Luân Hồi. Đối lập đông nam Liệt Dương thiên, chưởng khống giả Chước Dương Đại Đế, quản lý không trung mặt trời, vạn vật sinh trưởng, thiên địa luân thường. Tây nam Linh Lung thiên, Linh Lung tiên tử, mỹ lệ vô song... Còn mặt khác, biết liền không nhiều lắm.”"),g("br"),g("br"),n._v("Trương Huyền gật gật đầu."),g("br"),g("br"),n._v("Cửu Thiên Cửu Đế loại nào thân phận, cùng kiếp trước hoàng đế đồng dạng, cũng không phải cái gì người đều có thể thảo luận. Đối phương chẳng qua là cái học viện lão sư, có thể biết ba vị, hơn nữa đem xưng hào nói ra, đã rất không dễ dàng."),g("br"),g("br"),n._v("“Nếu như Trương huynh thật muốn biết, ngược lại là có thể hỏi thăm sư phụ ngươi, tiền bối loại thực lực này, đối với Cửu Thiên tình huống, hẳn là so ta tìm hiểu càng nhiều đi!” Mặc Vân lão sư nói."),g("br"),g("br"),n._v("Cửu Thiên Cửu Đế người bình thường không dám thảo luận, nhưng biết vẫn như cũ không ít, dù sao, lại không cho phép thảo luận, cũng không có khả năng không biết, hoàng đế đương triều là ai đi! Cố ý như vậy chất vấn, trên thực tế là trong lòng có nghi ngờ."),g("br"),g("br"),n._v("“Chúng ta chỉ là được lão sư cứu người bình thường, thấy không chỗ nương tựa mới thu làm học sinh, bình thường gặp mặt cũng khó khăn, việc này, như thế nào lại cùng chúng ta nói nhiều! Không phải như thế lời nói, cũng sẽ không cần làm phiền Mặc Vân lão sư, đem chúng ta mang ra sơn mạch này...”"),g("br"),g("br"),n._v("Biết ý nghĩ của đối phương, Trương Huyền cười khổ."),g("br"),g("br"),n._v("“Cái này...” Mặc Vân lão sư sửng sốt một chút, trong lòng nghi hoặc tiêu tán."),g("br"),g("br"),n._v("Thần giới là có loại tình huống này, một ít khổ tu lão sư, đối đãi học sinh vô cùng nghiêm khắc, mỗi lần gặp mặt, đều là sắp đặt nhiệm vụ, cũng không có quá nhiều phí lời."),g("br"),g("br"),n._v("Cửu Thiên Cửu Đế loại chuyện này, không nói cũng liền bình thường."),g("br"),g("br"),n._v("“Không chỉ có như vậy, ngươi xem chúng ta, cơ bản đều không có đi đến Thần Linh, làm sao đột phá, lão sư cũng không nói, chỉ nói thời cơ không thành thục, cần cố gắng tu luyện, chỉ có tích lũy hùng hậu, sau khi đột phá, mới có thể phát huy ra lực chiến đấu mạnh hơn... Nhưng nơi này linh khí cũng thực sự quá mỏng manh, tu luyện... Quá khó khăn!” Trương Huyền lắc đầu."),g("br"),g("br"),n._v("“Dương Huyền lão sư, thật đúng là cái nghiêm khắc lão sư! Đều theo kịp hai năm này xuất hiện danh sư, có thể gọi là Dương sư.” Mặc Vân lão sư cảm xúc."),g("br"),g("br"),n._v("“Dương sư? Danh sư?” Trương Huyền chấn động trong lòng."),g("br"),g("br"),n._v("Cái nghề nghiệp này, vì Khổng sư sáng tạo, chỉ ở danh sư đại lục mới có, Thượng Thương cũng không nghe qua, không nghĩ tới ở đây, nghe được tên."),g("br"),g("br"),n._v("“Là mấy chục năm qua, Thần giới đột nhiên nhiều ra tới, tự xưng danh sư, xây dựng Danh Sư đường, phụ trách truyền đạo thụ nghiệp giải thích nghi hoặc, đối đãi học sinh, có đặc thù giáo sư phương pháp cùng hệ thống, hiệu quả vô cùng tốt, có điều, học sinh muốn so người bình thường càng thêm khổ cực một ít!” Thấy hắn không biết, Mặc Vân lão sư nói."),g("br"),g("br"),n._v("Trương Huyền rung một cái."),g("br"),g("br"),n._v("Có danh sư cùng Danh Sư đường, khẳng định cùng Khổng sư có liên quan rồi."),g("br"),g("br"),n._v("Nhìn tới, Thượng Thương được bản thân trấn áp cái kia, quả thực là phân thân, mà bản tôn cũng giống như mình, đã đi tới nơi này."),g("br"),g("br"),n._v("Danh sư đại lục, đã trải qua vài vạn năm, Thượng Thương thời gian tốc độ chảy là một phần mười, đã trải qua mấy ngàn năm, mà ở trong đó... Hắn tuy là thực lực quá thấp, cảm ứng không ra thời gian cụ thể tốc độ chảy, cũng không xảy ra ngoài ý muốn cùng Thần điện, hẳn là không kém bao nhiêu."),g("br"),g("br"),n._v("Nói cách khác, là Thượng Thương một phần trăm, danh sư đại lục một phần ngàn."),g("br"),g("br"),n._v("Danh sư đại lục trải qua vài vạn năm, nơi này chẳng qua mấy chục năm mà thôi."),g("br"),g("br"),n._v("Không tốt trực tiếp hỏi, Trương Huyền hữu ý vô ý nói bóng nói gió: “Đột nhiên xây dựng Danh Sư đường, sẽ ảnh hưởng Cửu Thiên Cửu Đế thống trị a, mặt khác phổ thông học viện hẳn là cũng lại nhận xung kích, các ngươi chẳng lẽ mặc kệ?”"),g("br"),g("br"),n._v("Danh sư đại lục, sở dĩ Danh Sư đường san sát, đó là bởi vì, đã tạo thành đặc biệt hệ thống, vô số người, thậm chí vương quốc, đế quốc, cũng đều lấy danh sư làm vinh, mà ở trong đó, Cửu Thiên Cửu Đế vi tôn, mỗi một cái thế lực, đều có chỗ tôn sùng Đế Quân, liền cùng Thượng Thương sáu đại tông môn đồng dạng."),g("br"),g("br"),n._v("Lấy một thí dụ, ngươi chạy đến Lăng Vân kiếm các tuyên dương Thất Tinh lầu công pháp, truyền thụ học sinh, không phải là cướp nhiều kiếm các ưu tú truyền nhân, không bị dạy dỗ mới là lạ."),g("br"),g("br"),n._v("Càng quan trọng hơn là... Những này phổ thông học viện làm sao có thể chịu đựng đối phương làm như vậy?"),g("br"),g("br"),n._v("Danh sư mạnh mẽ, hắn tự mình trải qua, giảng bài so với người bình thường lão sư mạnh mẽ không biết gấp bao nhiêu lần, một khi mở ra, những này phổ thông học viện... Còn có thể chiêu đến học sinh?"),g("br"),g("br"),n._v("“Bọn họ những người này chủ trương hữu giáo vô loại, giáo sư học sinh đều là chúng ta rất nhiều học viện, tuyển chọn còn lại, cơ bản không có thiên phú gì...” Nói đến đây Mặc Vân lão sư xấu hổ cười một tiếng: “Trương huynh, ta không phải nói các ngươi, đừng thấy lạ...”"),g("br"),n._v(" Vừa nói vị kia Dương lão sư, có danh sư đường tác phong, đảo mắt liền nói, bọn họ chỉ thu nhận học viện chọn còn lại phế vật... Rõ ràng có chửi chó mắng mèo chi ngại."),g("br"),g("br"),n._v("“Không sao cả!” Trương Huyền xua tay: “Thiên phú của chúng ta quả thực không tốt, không đuổi kịp chư vị cao túc, nếu không, cũng không có khả năng liền Thần Linh cảnh giới cũng không đột phá!”"),g("br"),g("br"),n._v("Mặc Vân lão sư không tại vấn đề này tiếp tục rầu rĩ, nói: “Thần sơn Thần Linh chi khí có hạn, hàng năm thu nhận học sinh thời điểm, đều là học viện ưu tiên chiêu thu đệ tử, đem có thiên phú toàn bộ tụ lại xong, bọn họ lại thu nhận còn lại, cho nên, đối với chúng ta ảnh hưởng không lớn! Chẳng qua...”"),g("br"),g("br"),n._v("Dừng lại một chút, Mặc Vân lão sư cảm xúc: “Không thể không nói, đám này danh sư quả thực có chút bản lĩnh, những cái kia tại chúng ta nhìn tới, không có khả năng đột phá người, tại bọn họ giáo sư bên dưới, vậy mà cũng đều bước ra một bước cuối cùng, thậm chí có chút học sinh, so với chúng ta đệ tử đều muốn tiến bộ nhanh! Ở loại tình huống này đuổi theo bên dưới, chúng ta cũng muốn liều mạng tu luyện, nếu không, ta cũng không có khả năng mang theo một đám chỉ có mười bảy, tám tuổi hài tử, chạy đến nơi đây thí luyện rồi!”"),g("br"),g("br"),n._v("Đều là những cái kia gây nên Danh Sư đường ép, nếu không, an toàn giảng bài dạy học thật tốt, mười mấy tuổi liền chạy tới núi hoang thí luyện... Nếu không liền đuổi theo chẳng qua những người kia, tuy là bất đắc dĩ, nhưng lại không thể không nói, cũng có rất lớn động lực."),g("br"),g("br"),n._v("“Còn Cửu Thiên Cửu Đế, tự nhiên không cho phép hắn làm như thế, cho nên... Vị kia ngút trời Đại Đế, mới khiêu chiến rất nhiều Đế Quân. Mười mấy năm trước, cùng Càn Khôn Ma quân đóng cửa tỷ thí, kết quả làm sao, chúng ta không được biết, nhưng... Chúng ta U Hồn thiên, lại không hề phản đối Danh Sư đường, lưu đày tự do!”"),g("br"),g("br"),n._v("Mặc Vân lão sư lắc đầu."),g("br"),g("br"),n._v("Mặc dù không biết giao đấu kết quả, nhưng nhìn thấy cục diện này, nhưng đã biết, khẳng định là Càn Khôn Ma quân thua."),g("br"),g("br"),n._v("Nếu không, không có khả năng như vậy."),g("br"),g("br"),n._v("“Ngút trời Đại Đế? Ngươi nói vị kia vừa mới thăng cấp Đại Đế?” Trương Huyền hỏi."),g("br"),g("br"),n._v("“Chính là hắn!” Mặc Vân lão sư gật đầu."),g("br"),g("br"),n._v("“Khó trách...” Trương Huyền giật mình."),g("br"),g("br"),n._v("Vừa rồi nghe đối phương nói Cửu Thiên Cửu Đế, tuyên cổ tồn tại, liền cảm thấy kỳ lạ, tại sao lại đột nhiên toát ra một vị, có thể cùng tranh tài tồn tại, hiện tại xem ra, hẳn là chính là Khổng sư."),g("br"),g("br"),n._v("Thần giới mấy chục năm, danh sư đại lục mấy vạn năm, thời gian cũng đối ứng bên trên... Mấu chốt nhất là, Khổng sư tại danh sư đại lục, đã từng được không ít người xưng hô ngút trời Đại Thánh!"),g("br"),g("br"),n._v("Nơi này trở thành ngút trời Đại Đế hoặc là ngút trời Đế Quân, cũng đúng lúc ăn khớp."),g("br"),g("br"),n._v("Ngắn ngủi mấy chục năm, ngang dọc đại lục, sáng tạo Danh Sư đường, đơn đấu tám ngày tám Đế, ép bọn họ thừa nhận thân phận, hơn nữa đồng ý mở Danh Sư đường..."),g("br"),g("br"),n._v("Không hổ là Khổng sư, cho dù tại cường giả vô số Thần giới, đồng dạng nghịch thiên đến làm người tuyệt vọng!"),g("br"),g("br"),n._v("Đương nhiên, bất kể có phải hay không là Khổng sư, hắn đều đã không có Thượng Thương thời điểm, như vậy muốn đi bái kiến."),g("br"),g("br"),n._v("Đã trải qua giả Khổng sư sự kiện, không có năng lực tự vệ phía trước, chạy tới tìm đối phương, ngộ nhỡ muốn cướp đoạt bản thân Thiên đạo thư viện, thật có có thể sẽ được đánh giết."),g("br"),g("br"),n._v("Không phải đối Khổng sư nhân phẩm sinh ra nghi ngờ, mà là thất phu vô tội mang ngọc có tội, không có cùng đối phương ngang hàng thực lực phía trước, tốt nhất vẫn là bớt tiếp xúc."),g("br"),g("br"),n._v("Giả Khổng sư tuy là làm nhiều việc ác, nhưng ít ra có chuyện nói hắn vô cùng tán đồng... Đó chính là, bản thân cùng Khổng sư đều nắm trong tay Thiên đạo một bộ phận, cả hai hợp nhất, nhất định có thể để cho bên trong một người, thực lực tăng nhiều, thậm chí... Đi đến không cách nào lường được tình trạng."),g("br"),g("br"),n._v("Loại này lợi ích, Thánh Nhân cũng chưa chắc có thể chống cự ở."),g("br"),g("br"),n._v("Đem khiếp sợ trong lòng áp chế xuống, Trương Huyền tiếp tục hỏi: “Mặc Vân lão sư mới vừa nói... Thần sơn Thần Linh chi khí có hạn, là ý gì?”"),g("br"),g("br"),n._v("Vừa rồi đối phương nói, thần sơn Thần Linh chi khí, để hắn tràn đầy nghi hoặc."),g("br"),g("br"),n._v("“Nhìn tới Dương sư thật đúng là không có nói với các ngươi qua... Cũng đúng, các ngươi còn không có tôi luyện đến cực hạn, không biết rất bình thường!” Thấy hắn không hiểu, Mặc Vân lão sư không có trực tiếp trả lời, mà là nhìn lại, hỏi ngược lại: “Trương huynh, lấy ánh mắt của ngươi đến xem, toà này Linh Cơ sơn bên trên nồng độ linh khí làm sao?”"),g("br"),g("br"),n._v("“Linh Cơ sơn?” Trương Huyền rốt cuộc biết tên của ngọn núi này, ghi vào trong óc, trên mặt lại không biểu hiện ra ngoài, nhịn không được lắc đầu: “Ngọn núi này, hầu như không có bất kỳ cái gì linh khí, làm cho không người nào có thể bình thường tu luyện!”"),g("br"),g("br"),n._v("Vừa đến Thần giới hắn liền phát hiện, nơi này cuồng phong tàn phá bừa bãi ngược lại cũng thôi, mấu chốt nhất là, linh khí mỏng manh hầu như tương đương không có, căn bản là không có cách rút ra, thay đổi thành lực lượng trong cơ thể, nếu không, đám người cũng không đến mức dựa vào ăn rau dại mà sống."),g("br"),g("br"),n._v("May mắn những này rau dại, còn có chút linh khí, nếu không, đường đường vạn thế chi sư, Thượng Thương đệ nhất nhân, đi vào Thần giới kết quả... Bị chết đói, nha đều có thể được người cười đi."),g("br"),g("br"),n._v("“Đúng vậy a, không chỉ có nơi này như vậy, toàn bộ Thần giới chỉ cần không có thành trì địa phương đều cùng nơi này đồng dạng, linh khí suy yếu! Thậm chí không ít vị trí, đều đã hoang tàn vắng vẻ!”"),g("br"),g("br"),n._v("Mặc Vân lão sư lắc đầu."),g("br"),g("br"),n._v("“Linh khí suy yếu? Hoang tàn vắng vẻ?” Sững sờ tại nguyên chỗ, Trương Huyền có chút không dám tin tưởng."),g("br"),g("br"),n._v("Đây chính là chư thiên vạn giới đỉnh phong nhất ah... Vậy mà loại trừ thành thị, linh khí suy yếu... Làm sao đều làm người không dám tưởng tượng."),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-2120"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-2122"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);