(window.webpackJsonp=window.webpackJsonp||[]).push([[1263],{1618:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-2108-suy-yeu-than-gioi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2108-suy-yeu-than-gioi"}},[n._v("#")]),n._v(" Chương 2108: Suy yếu Thần giới")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Tại Trương Huyền trong tưởng tượng, Thần giới hẳn là cường giả vô số, cao thủ nhiều như mây, khắp nơi linh khí, dị thường đầy đủ, tuỳ ý có thể để người ta đột phá, tiện tay nhặt lên cành cây, đều so tuyệt phẩm Tiên khí mạnh mẽ, thậm chí vượt qua Ngụy Thần khí;"),i("br"),i("br"),n._v("Nhìn khắp nơi đến cỏ dại, đều là chữa thương cùng đột phá tiên dược, vượt qua tuyệt phẩm nguyên lực đan..."),i("br"),i("br"),n._v("Khắp nơi là bảo, đâu đâu cũng có của cải..."),i("br"),i("br"),n._v("Có thể nghe đối phương, linh khí suy yếu, vạn vật suy yếu, cảm giác cùng phế tích đồng dạng..."),i("br"),i("br"),n._v("Tựa như toà này Linh Cơ sơn, mặc dù có chút thực vật, có thể kèm theo thời gian lâu dài không cách nào hấp thu linh khí, liền cùng không có ánh nắng đồng dạng, cuối cùng trở nên khô héo, chậm chạp biến mất."),i("br"),i("br"),n._v("Cũng nguyên nhân chính là như vậy, Trương Huyền mỗi lần tìm kiếm rau dại, đều cơ hồ muốn đi lên hơn nửa ngày, thậm chí một ngày, lấy được số lượng cũng rất có hạn."),i("br"),i("br"),n._v("Cái này... Thật sự là Thần giới?"),i("br"),i("br"),n._v("Đến nơi này, ngay cả cọng cỏ đều không có gì để ăn? Làm sao cảm giác cùng Dị Linh tộc cổ chiến trường có chút giống nhau?"),i("br"),i("br"),n._v("Suy yếu, suy yếu, tựa như phế tích."),i("br"),i("br"),n._v("“Trên núi còn có không ít rau dại cùng trái cây, ẩn chứa linh khí, loại này suy yếu... Hẳn là kéo dài thời gian cũng không lâu đi!” Nhịn không được hỏi."),i("br"),i("br"),n._v("Nếu như thời gian lâu dài, một mực không có linh khí, cái này chỉ sợ sớm đã biến thành một tòa không núi, bất kỳ cái gì thảm thực vật cũng sẽ không tồn tại, lúc này còn có thể tìm tới cây cối cùng rau dại, thậm chí trái cây, thần thú... Chứng minh, suy yếu thời gian, sẽ không quá lớn."),i("br"),i("br"),n._v("Thấy hắn hỏi như vậy, Mặc Vân lão sư càng thêm nghi ngờ nhìn qua: “Ngươi sẽ không phải là mới đi đến Thần giới a?”"),i("br"),i("br"),n._v("“Đương nhiên sẽ không... Chúng ta bản sinh hoạt tại một cái sơn thôn nhỏ, hoàn toàn tách biệt với thế gian, đối với ngoại giới sự tình biết không nhiều... Về sau một hồi ôn dịch, chỉ còn lại có chúng ta những người này, được lão sư cứu, cho nên... Rất nhiều chuyện, đều không biết!”"),i("br"),i("br"),n._v("Trương Huyền xấu hổ cười nói."),i("br"),i("br"),n._v("Không biết Thần giới đối phi thăng giả làm pháp là cái gì, có điều, vẫn là muốn cẩn thận một chút, nếu không, được người bán đi cũng không biết."),i("br"),i("br"),n._v("“Ừm!” Thấy hắn nghĩ một đằng nói một nẻo, khả năng không quá muốn nói, Mặc Vân lão sư cũng không truy cứu nữa, nói: “Loại này linh khí tán loạn, lần đầu tiên xuất hiện, có lẽ là hơn bốn mươi năm, không cao hơn năm mươi năm đi! Lúc ấy trời đất sụp đổ, toàn bộ trời đều giống như sập nửa bên, hồng thủy lan ra đại địa, truyền thuyết trấn thủ đông, nam, tây, bắc tứ thiên Đế Quân, tự thân ra tay, chém xuống  bốn chân, làm kình thiên trụ, lúc này mới ổn định thế giới.”"),i("br"),i("br"),n._v("Nói đến đây, Mặc Vân lão sư cười cười: “Đương nhiên, đây chỉ là truyền thuyết, không phải thật! Cũng có người nói, là trung tâm Tự Tại thiên vị kia Đế Quân, luyện hóa đặc thù lực lượng, đem đổ sụp không trung bổ sung... Tóm lại, các loại thuyết pháp đều có, nhưng từ khi đó bắt đầu, các nơi linh khí ngay lập tức tán loạn, loại trừ thành trấn bên ngoài, cơ bản đều cùng nơi này đồng dạng, rất khó cư trú sinh mệnh!”"),i("br"),i("br"),n._v("“Cái này...” Trương Huyền trong lòng chấn động kịch liệt."),i("br"),i("br"),n._v("Hơn bốn mươi năm trước, tiếp cận năm mươi năm... Đổi lại Thượng Thương thời gian, chính là hơn bốn ngàn năm trước, danh sư đại lục thời gian thì là... Hơn bốn vạn năm trước!"),i("br"),i("br"),n._v("Chính là Khổng sư đột nhiên xuất hiện, Thượng Thương sáu đại tông môn xây dựng thời điểm."),i("br"),i("br"),n._v("Thần giới xuất hiện vận rủi, thiên địa sụp đổ, Khổng sư khống chế Thiên đạo hữu tự... Chẳng lẽ, giữa hai bên có cái gì đặc biệt liên hệ?"),i("br"),i("br"),n._v("Nếu có liên hệ lời nói, như vậy... Bản thân thư viện, Thiên đạo có thiếu, có thể hay không cũng cùng chi liên quan đến?"),i("br"),i("br"),n._v("Dù sao, Thiên đạo, không có khả năng đột nhiên xuất hiện, hắn thư viện, ở tại thần giới đều có thể sử dụng, chứng minh Thiên đạo cấp bậc, không thể so Thần giới Thiên đạo thấp..."),i("br"),i("br"),n._v("Làm cho lòng người bên trong không thể không sinh ra sự hoài nghi này."),i("br"),i("br"),n._v("“Linh khí suy yếu, cho nên, muốn đột phá Thần Linh, đã không giống lúc trước dễ dàng như vậy! Hơn nữa, đột phá càng nhiều, đối tài nguyên tiêu hao càng lớn, vô cùng có khả năng, dẫn tới càng lớn biến cố, cho nên, Cửu Thiên Cửu Đế, chỉ có thể khống chế Thần Linh số lượng, tự thân ra tay, xây dựng chín tòa thần sơn. Muốn đột phá đến Thần Linh, nhất định phải đi thần sơn, tìm kiếm Thần Linh chi khí, người thành công đột phá, không thành công người, cũng chỉ có thể ngoan ngoãn chấp nhận.”"),i("br"),i("br"),n._v("Mặc Vân lão sư tiếp tục nói."),i("br"),i("br"),n._v("Trương Huyền bừng tỉnh hiểu ra."),i("br"),i("br"),n._v("Loại tình huống này cùng danh sư đại lục Côn Hư cảnh có chút giống nhau, tài nguyên nhất định, không có thiên phú, không có tư cách, liền triệt để mất đi tấn cấp tư cách."),i("br"),i("br"),n._v("Đối với tu luyện giả mặc dù rất tàn khốc, nhưng lại có thể để cho một cái thế giới, càng kéo dài phát triển, không đến mức sụp đổ."),i("br"),i("br"),n._v("“Dương sư như vậy rèn luyện các ngươi, rõ ràng là cho các ngươi tại Ngụy Thần thời điểm, sức chiến đấu mạnh hơn, hơn nữa trong núi hoang thí luyện, càng có thể thích ứng thần sơn hoàn cảnh, tìm tới Thần Linh chi khí, từ đó thuận lợi đột phá!”"),i("br"),i("br"),n._v("Mặc Vân lão sư cảm xúc: “Không hổ là Danh Sư đường người, thật sự là dụng tâm lương khổ! Khó trách bọn hắn cho dù thu nhận học sinh, thiên phú đồng dạng, thành tựu cuối cùng lại không thể so những thiên tài kia muốn thấp... A, xấu hổ, ta cũng không phải là nói ngươi thiên phú thấp, cũng không có bất kỳ ý khinh thường...”"),i("br"),i("br"),n._v("Trương Huyền nháy con mắt, không biết trả lời như thế nào."),i("br"),i("br"),n._v("Triệu Nhã đám người kém chút bật cười."),i("br"),i("br"),n._v("Dám nói lão sư của các nàng thiên phú thấp... Người trước mắt này, lá gan thật là đủ lớn."),i("br"),i("br"),n._v("Lão sư tại danh sư đại lục tu luyện không đủ hai năm, tại Thượng Thương không đủ hai tháng, toàn bộ chuyển đổi thành Thần giới thời gian..."),i("br"),i("br"),n._v("đọc ngantruyen.com/"),i("br"),n._v("Danh sư đại lục là 0. 7 3 ngày, Thượng Thương 0.6 ngày, tổng cộng 1.3 3 ngày... Không sai biệt lắm, Thần giới thời gian 16 canh giờ. Đối với ngươi mà nói. Ngủ một giấc công phu, lão sư từ người bình thường, tấn cấp thành Thần linh cảnh giới, một đường bật hack, lớn nhỏ cảnh giới, mấy chục nhiều... Ngươi dám nói hắn thiên phú kém?"),i("br"),i("br"),n._v("Nói đùa cái gì!"),i("br"),i("br"),n._v("“Khụ khụ, ta không để tâm...” Trương Huyền lắc đầu."),i("br"),n._v("Lại hỏi thăm một ít chuyện, vị này Mặc Vân lão sư ngược lại là biết gì nói nấy biết gì nói nấy, rất nhanh, Trương Huyền đối Thần giới xem như có cái trực quan quen biết."),i("br"),i("br"),n._v("Không hổ là chư thiên vạn giới đỉnh phong nhất tồn tại, quả thực mạnh lấy vô số, nhưng mà mạnh mẽ cũng có mạnh mẽ chỗ xấu, đó chính là... Không có danh sư đại lục như vậy chú ý quy củ."),i("br"),i("br"),n._v("Thực lực chân chính vi tôn, cường giả có thể tung hoành thiên hạ, ngao du tứ phương; Kẻ yếu chỉ có thể đợi trong thành không dám ra ngoài, nếu không gặp được giặc cướp, đều vô cùng có khả năng cả người cả của đều không còn, chết tha hương tha hương."),i("br"),i("br"),n._v("Đương nhiên đây đều là ở ngoài thành, nội thành lời nói nhất định phải tuân thủ nội thành quy củ. Mỗi một cái thành thành chủ, đều có tuyệt đối quyền lợi cùng thực lực."),i("br"),i("br"),n._v("Tỷ như bọn họ vị trí Dạ Minh thành, thành chủ Ngô Phương Thanh chính là một vị Thiên Thần cảnh cường giả, đầu đồng thiết tí, đao thương bất nhập, từng một đôi tay không huyết tẩy mười tám chỗ sơn trại, thần quỷ chớ ngăn cản."),i("br"),i("br"),n._v("Nội thành nhất định phải tuân thủ hắn quyết định quy củ, nếu không, sẽ nhận tuyệt đối ngăn cấm."),i("br"),i("br"),n._v("Cường giả lập ra quy củ, kẻ yếu tuân thủ."),i("br"),i("br"),n._v("Nếu không, liền có thể được giết, bị rút lấy Thần Linh chi khí..."),i("br"),i("br"),n._v("Đây là Thần giới, trần trụi luật rừng."),i("br"),i("br"),n._v("“Mặc Vân lão sư, có thể hay không nói cho ta một chút, vị kia Thiên Túng Đại Đế chuyện xưa?” Vương Dĩnh dường như cũng cảm giác được không đúng, tò mò hỏi."),i("br"),i("br"),n._v("“Thiên Túng Đại Đế, là gần mấy chục năm đột nhiên xuất hiện siêu cấp cường giả, nổi danh là hơn bốn mươi năm trước, hắn một cái thượng phẩm Thần Linh, một mình đối chiến tám vị hạ phẩm Thiên Thần, thành công đem hắn đánh giết, đứng hàng Thiên Thần tiềm lực bảng số một!”"),i("br"),i("br"),n._v("Mặc Vân lão sư ánh mắt lộ ra tôn sùng, xem ra đối với vị này đột nhiên xuất hiện Đại Đế, hắn từ nội tâm chỗ sâu cảm thấy khâm phục."),i("br"),i("br"),n._v("“Thiên Thần tiềm lực bảng?”"),i("br"),i("br"),n._v("“Cửu Thiên chín nơi thế lực, mỗi một chỗ, đều sẽ bình chọn thuộc về mình Thiên Thần tiềm lực bảng, có thể đi vào bảng danh sách mười vị trí đầu, hầu như đều không ngoại lệ đều có thể tấn cấp đến thượng phẩm Thiên Thần! Thậm chí trước ba, đều có cơ hội thành tựu Thần Vương! Nguyên nhân chính là như vậy, bảng danh sách này, là vô số tu luyện giả truy đuổi đối tượng, chỉ cần có thể tiến vào bên trong, chẳng khác nào bước vào vô số tài nguyên cung cấp đại môn, về sau tiền đồ vô lượng!”"),i("br"),i("br"),n._v("Mặc Vân lão sư trong mắt tràn đầy hâm mộ."),i("br"),i("br"),n._v("Thần giới tính toán tài nguyên, không có tuyệt đối thiên phú, là không thể nào đạt được càng tốt bồi dưỡng cơ hội, cho nên... Sẽ xuất hiện các loại bảng danh sách, để tu luyện giả đi tranh đoạt."),i("br"),i("br"),n._v("Đạt được tốt hơn thứ tự, tài nguyên đủ, mới có thể thu được bồi dưỡng tư cách, tấn cấp cao hơn tu vi."),i("br"),i("br"),n._v("Điểm ấy, cùng Côn Hư cảnh gần như giống nhau."),i("br"),i("br"),n._v("“Đạt được cái này đệ nhất về sau, đã xảy ra là không thể ngăn cản, không đến thời gian một năm, liền thành công đột phá đi đến Thần Vương cảnh giới, tiếp đó linh khí thuỷ triều bên trong, một người độc chiếm vị trí đầu, được bảy đại Đế Quân đồng thời ban cho phong hào Thần Vương vinh dự.”"),i("br"),i("br"),n._v("“Lại qua không đến một năm, bắt đầu khiêu chiến Cửu Thiên Cửu Đế, vừa mới bắt đầu, tất cả mọi người cảm thấy, người này quả thực điên rồi, ai biết, trận đầu liền chiến thắng... Về sau mấy chục năm, đối chín vị Đế Quân, từng cái ước chiến, nghe nói hiện tại chỉ còn lại có trung tâm Tự Tại thiên vị kia Đế Quân không có tỷ thí qua, mặt khác đều đã công nhận thân phận của hắn... Tuy là kết quả không có người biết, đã công nhận thân phận, vô cùng có khả năng, mặt khác tám Đế... Đều thua!”"),i("br"),i("br"),n._v("Mặc Vân lão sư thấp giọng."),i("br"),i("br"),n._v("Dính dáng Cửu Thiên Cửu Đế, tuy là ngoại giới tin tức truyền nói chắc như đinh đóng cột, vẫn là không muốn truyền đi cho thỏa đáng."),i("br"),i("br"),n._v("“Liên tục khiêu chiến tám vị Đế Quân...”"),i("br"),i("br"),n._v("Trương Huyền nghe nhiệt huyết sôi trào."),i("br"),i("br"),n._v("Không hổ là Khổng sư, bất luận ở thế giới nào, đều là cuối cùng đứng tại đỉnh phong nhất siêu cấp nhân vật."),i("br"),i("br"),n._v("“Không biết Tự Tại thiên vị kia Đế Quân... Tên gì? Lại vì sao không cùng hắn tỷ thí?” Tràn đầy tò mò, Trương Huyền hỏi."),i("br"),i("br"),n._v("Khiêu chiến Đế Quân, đến cùng thắng hay thua, người bình thường không biết, nhưng đều là Đế Quân siêu cấp cường giả, khẳng định biết."),i("br"),i("br"),n._v("Có thể hay không vị này Tự Tại thiên Đế Quân, biết những người khác thua, không dám cùng so đấu thử?"),i("br"),i("br"),n._v("“Cái này ta cũng không biết! Nói đến Tự Tại thiên vị này Đế Quân, khống chế trong thần giới, vì Cửu Đế chi tôn, là Thần giới lợi hại nhất Đế Quân, cũng là thần bí nhất một vị... Ngoại giới, không có một chút liên quan tới hắn tin đồn, cụ thể là nam hay là nữ, là già hay trẻ, đều không rõ ràng, thậm chí liền họ tên cũng không biết! Nhưng trung tâm Tự Tại thiên vị trí, không người dám động, Thần giới sừng sững không biết bao lâu, đều không có người dám đánh nơi này tính toán...”"),i("br"),i("br"),n._v("Nói đến đây, Mặc Vân lão sư đột nhiên giật mình, tự biết nói quá nhiều, xấu hổ cười một tiếng: “Được rồi, ngày hôm nay cũng không muốn nói nhiều, chúng ta chỉ là tiểu nhân vật, Cửu Thiên Cửu Đế sự tình, mặc kệ chuyện của chúng ta, cũng tham dự không đến, không cần buồn lo vô cớ...”"),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Biết hỏi lại không ra cái gì, Trương Huyền không nói thêm lời, tất cả mọi người một ngày mệt nhọc, tràn đầy mệt mỏi, Mặc Vân lão sư rất nhiều học sinh, nắm đấm nghiêng dựa vào trên vách đá, ngủ thật say."),i("br"),i("br"),n._v("Ngay cả vị lão sư này, cũng nhắm mắt lại, không biết ngủ không ngủ."),i("br"),i("br"),n._v("Chỉ có Trương Huyền cùng với rất nhiều học sinh, toàn bộ khoanh chân ngồi dưới đất, nhắm mắt lại tu luyện."),i("br"),i("br"),n._v("Mặc dù nơi này linh khí, vô cùng mỏng manh, nhập không đủ xuất, tu luyện vô cùng khó khăn, nhưng các nàng vẫn như cũ không dám trễ nãi một phân một hào thời gian."),i("br"),i("br"),n._v("“Ừm?” Tu luyện tới sau nửa đêm, Trương Huyền đang định nghỉ ngơi, đột nhiên ngừng lại, giống như là cảm ứng được cái gì, nhìn về phía một cái phương hướng, lông mày không khỏi nhăn lại."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-2121"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-2123"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);