(window.webpackJsonp=window.webpackJsonp||[]).push([[1264],{1619:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2109-thanh-thu-co-thao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2109-thanh-thu-co-thao"}},[n._v("#")]),n._v(" Chương 2109: Thanh Thú Cổ Thảo")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Một bên Mặc Vân lão sư cũng mở mắt."),t("br"),t("br"),n._v("Bảo vệ học sinh đi ra, không có khả năng ngủ được rất an tâm, đồng dạng cảm ứng được cái gì, hướng một bên liếc mắt nhìn, con mắt nhịn không được sáng lên."),t("br"),t("br"),n._v("Thấy hắn biểu lộ, dường như rõ ràng cái gì, Trương Huyền nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Không có gì bất ngờ xảy ra, hẳn là chúng ta lần luyện tập này muốn tìm đồ vật, Tật Phong Sa Ly thú!” Mặc Vân lão sư đứng dậy."),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Đối với Thần giới, hắn hoàn toàn không biết gì cả, đây là loại động vật gì, hoàn toàn không rõ ràng."),t("br"),t("br"),n._v("Thấy hắn không hiểu, Mặc Vân lão sư nói: “Là một loại hạ phẩm Thần Linh cảnh giới thần thú, tấn công cùng năng lực phòng ngự không mạnh, nhưng am hiểu tốc độ, trong cơ thể huyết dịch cùng tinh hạch, ẩn chứa phong thuộc tính lực lượng, sau khi phục dụng, có thể để người ta tốc độ trở nên cực nhanh! Cũng không nguy hiểm, lại không dễ dàng như vậy bắt, đối với học sinh thí luyện tới nói, thích hợp nhất. Căn cứ chúng ta điều tra, sào huyệt của nó, hẳn là cách chúng ta còn có hồi lâu lộ trình, không nghĩ tới, vậy mà xuất hiện!”"),t("br"),t("br"),n._v("Lần luyện tập này chính là vì bắt thứ này, vốn muốn, trước thời hạn trở về, thí luyện muốn thất bại, không nghĩ tới, ngủ đến nửa đêm, bản thân chạy tới."),t("br"),t("br"),n._v("“Thẩm Thành, Ngô Tiêu Tiêu, nhanh đứng dậy!”"),t("br"),t("br"),n._v("Giải thích xong, Mặc Vân lão sư cười nhạt một tiếng, hạ giọng hô một tiếng."),t("br"),t("br"),n._v("Những học sinh này mặc dù đã ngủ chìm, dù sao cũng là Thần Linh cấp bậc cường giả, nghe được tiếng la toàn bộ bò dậy, thời gian nháy mắt điều chỉnh trạng thái tốt nhất, từng cái ánh mắt lấp lánh nhìn qua."),t("br"),t("br"),n._v("“Lão sư, làm sao vậy?”"),t("br"),t("br"),n._v("Gọi là Thẩm Thành thanh niên áo trắng, mang theo nghi hoặc."),t("br"),t("br"),n._v("“Chính ngươi nghe...” Mặc Vân lão sư cũng không nói chuyện, mà là hướng vừa rồi nhìn lại phương hướng một chỉ."),t("br"),t("br"),n._v("Thẩm Thành lắng nghe một cái, con mắt trong nháy mắt trở nên hừng hực: “Là Tật Phong Sa Ly thú?”"),t("br"),t("br"),n._v("“Không tệ!” Mặc Vân lão sư gật đầu."),t("br"),t("br"),n._v("“Quá tốt rồi, xem ra ngày hôm nay có thể hoàn thành nhiệm vụ...” Tràn đầy hưng phấn mà, Thẩm Thành nhảy lên, giáng xuống rất nhiều đồng học: “Tất cả mọi người chuẩn bị kỹ càng, dựa theo chúng ta trước đó thiết lập phương án, hành động!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Đám người tất cả đều hạ giọng, thân thể lay động, đồng loạt vọt lên phía trước ra ngoài."),t("br"),t("br"),n._v("Thấy bọn họ rời đi động tác chỉnh tề như một, không có bất kỳ cái gì dư thừa hoặc là lãng phí, cũng không có dẫn tới mảy may tiếng gió cùng động tĩnh, Trương Huyền nhịn không được gật đầu."),t("br"),t("br"),n._v("Không hổ là học viện cao túc, thoạt nhìn quả thực không đơn giản."),t("br"),t("br"),n._v("Mặc dù chỉ là phổ thông Thần Linh, những người này sức chiến đấu, lại từng cái không tệ."),t("br"),t("br"),n._v("“Trương Huyền huynh đệ, các ngươi ở đây tiếp tục nghỉ ngơi, ta đi qua nhìn lấy bọn hắn...” Mặc Vân lão sư cười ha ha, cũng đứng dậy, theo sát tới."),t("br"),t("br"),n._v("“Triệu Nhã, ngươi phụ trách cảnh giới, chăm sóc tốt sư đệ, sư muội, cùng với sư công bọn họ, ta đi một chút liền đến!” Bàn giao một câu, Trương Huyền biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("Tật Phong Sa Ly thú, còn không có gặp qua, vừa vặn nhìn một chút, những này học viện đệ tử, làm sao phối hợp bắt, cũng có thể đối Thần giới, có càng nhiều tìm hiểu."),t("br"),t("br"),n._v("Đám người tốc độ rất nhanh, Trương Huyền thương thế không có hoàn toàn khôi phục, miễn cưỡng đuổi theo, vừa tới đến một chỗ nham thạch đằng sau, liền thấy Thẩm Thành rất nhiều học viện đệ tử, đã đem một cái thần thú vây quanh ở trung tâm."),t("br"),t("br"),n._v("Là cái hồ ly bộ dáng thần thú, hình thể không lớn, tại mọi người đang bao vây, tả hữu xung đột, lực lượng không mạnh, cùng Mặc Vân lão sư nói đồng dạng, tốc độ xác thực cực nhanh."),t("br"),t("br"),n._v("“Đám học sinh này không tệ...”"),t("br"),t("br"),n._v("Liếc mắt nhìn, đám người vây công con đường, Trương Huyền nhịn không được gật đầu."),t("br"),t("br"),n._v("Còn tưởng rằng là một đám ngu ngốc học viên, giờ phút này nhìn thấy vây công thủ pháp, nhất thời bỏ đi ý nghĩ này."),t("br"),t("br"),n._v("Đám người nhất cử nhất động, đều phù hợp trận pháp quy luật, đem ở giữa Tật Phong Sa Ly thú tất cả đường đi toàn bộ đóng kín, cái tên này cho dù tốc độ rất nhanh, nhưng thủy chung không cách nào chạy trốn."),t("br"),t("br"),n._v("Khó trách vị này Mặc Vân lão sư vẫn cảm thấy hơn người một bậc, xem thường danh sư thu nhận học sinh, những người này xác thực không tầm thường."),t("br"),t("br"),n._v("Lực lượng, ý thức, động tác... Đều là nhân tuyển tốt nhất, vượt xa người bình thường."),t("br"),t("br"),n._v("“Nhìn tới đầu này Tật Phong Sa Ly thú, trốn không thoát...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đầu này thần thú tốc độ tuy là rất nhanh, nhưng đám học sinh này năng lực từng cái không yếu, bắt được đối phương đã thành kết cục đã định, nhìn cùng không nhìn, đều không quan trọng."),t("br"),t("br"),n._v("Đang muốn trở về, tiếp tục tu luyện khôi phục thương thế, trong lòng đột nhiên động một cái."),t("br"),t("br"),n._v("Căn cứ hắn đối Thánh thú, linh thú loại hình lý giải, những động vật này, mỗi một cái đều có thuộc về mình phạm vi thế lực cùng hoạt động khu vực, dưới tình huống bình thường là rất ít vi phạm."),t("br"),t("br"),n._v("Dựa theo Mặc Vân lão sư nói, đối phương sinh hoạt hoàn cảnh, cách nơi này ít nhất còn có nửa giờ, làm sao lại hơn nửa đêm xuất hiện?"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn nhìn sang."),t("br"),t("br"),n._v("Rắc rối phức tạp hành động dấu vết xuất hiện ở trước mắt, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Đầu này Tật Phong Sa Ly thú tiến lên phương hướng, rõ ràng có một đạo dấu vết mờ mờ, hiển nhiên thứ này đang đuổi bắt cái gì."),t("br"),t("br"),n._v("Dọc theo dấu vết hướng về phía trước lan ra, biến mất ở sau lưng mọi người trong đêm tối, nhẹ nhàng đi tới."),t("br"),t("br"),n._v("Có thể để cho đầu này thần thú, hơn nửa đêm bất chấp nguy hiểm rời khỏi sào huyệt của mình, muốn truy nã đồ vật nhất định không đơn giản."),t("br"),t("br"),n._v("Đi về phía trước không bao xa, ngừng lại."),t("br"),t("br"),n._v("Dấu vết tại một mảnh dày đặc rừng cây biến mất không thấy gì nữa."),t("br"),t("br"),n._v("Nhíu nhíu mày, Trương Huyền thân thể vụt qua, đi vào trong rừng rậm."),t("br"),t("br"),n._v("Không trung trăng tàn, không tính quá sáng, rừng rậm cành lá rậm rạp, che chắn bốn phía đen kịt một màu, Trương Huyền Minh Lý Chi Nhãn cũng nhận hạn chế, nhìn không rõ lắm."),t("br"),t("br"),n._v("Ngừng lại."),t("br"),t("br"),n._v("Dấu vết ở đây biến mất không thấy hình bóng."),t("br"),t("br"),n._v("Cúi đầu tìm một vòng, Trương Huyền nhíu mày."),t("br"),n._v(" Dấu vết biến mất địa phương, chỉ có một ít phổ thông thực vật, không nhìn ra bất kỳ đặc thù."),t("br"),t("br"),n._v("Chẳng lẽ, Tật Phong Sa Ly thú truy nã đồ vật, hư không tiêu thất?"),t("br"),t("br"),n._v("Hắn chỉ là tò mò, có thể để cho thứ này, bất chấp nguy hiểm đi ra bắt đồ vật, đến cùng là cái gì, lại là vật gì, hiện tại thứ này vậy mà rõ ràng có dấu vết dưới tình huống, hư không tiêu thất, lập tức sinh ra nồng đậm hứng thú."),t("br"),t("br"),n._v("Đi vào Thần giới hơn nửa tháng đến, thư viện không thể dùng, Thần giới không ít thảm thực vật, cũng cùng Thượng Thương, thanh danh đại lục khác biệt, làm sao phân biệt có hay không độc tính, có thể hay không ăn vào, dựa vào là Minh Lý Chi Nhãn."),t("br"),t("br"),n._v("Chỉ cần có mặt khác phổ thông sinh mệnh thường xuyên ăn vào, hẳn là liền không có vấn đề."),t("br"),t("br"),n._v("Đương nhiên, cho dù có độc cũng không quan trọng, trong bọn họ thế nhưng là có một vị Tiên Thiên độc thể, cùng lắm thì nàng ăn trước, cảm thấy không ngon miệng, khẳng định chính là không độc..."),t("br"),t("br"),n._v("Gần nhất một mực sử dụng, cho nên đối với Minh Lý Chi Nhãn ở tại thần giới năng lực, biết rất rõ ràng."),t("br"),t("br"),n._v("Đã dấu vết ở đây biến mất, như vậy... Đối phương muốn truy nã đồ vật, nhất định vẫn còn lân cận!"),t("br"),t("br"),n._v("Lần nữa tìm một vòng, vẫn như cũ không thu được gì."),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Dấu vết tại, trừ phi phi thiên độn địa, nếu không tuyệt không có khả năng giấu giếm được bản thân."),t("br"),t("br"),n._v("“Có thể phi thiên, Sa Ly thú khẳng định không đuổi kịp... Chẳng lẽ là độn địa?” Mặt đất bùn đất khô quắt, cũng không có bị vượt qua dấu vết, Trương Huyền ngay tại nghi hoặc, đột nhiên con mắt rơi vào một cái nồng lục cỏ dại phía trên."),t("br"),t("br"),n._v("Bụi cỏ này, dung hợp tại bốn phía không tính quá mức bất ngờ, nhưng... Chung quanh đây, vì tìm kiếm rau dại, chuyển qua không dưới mười mấy lần, có thể bảo đảm, tuyệt đối không nhìn thấy qua như vậy một gốc cỏ dại."),t("br"),t("br"),n._v("Lúc trước nếu là nhìn thấy, đoán chừng trực tiếp liền lấy tới ăn, không có khả năng lưu đến bây giờ."),t("br"),t("br"),n._v("Bàn tay ở phía trên nhẹ nhàng sờ một cái."),t("br"),t("br"),n._v("Vù!"),t("br"),t("br"),n._v("Một quyển sách xuất hiện tại trong óc."),t("br"),t("br"),n._v("“Thanh Thú Cổ Thảo, có thể tại thú cùng thảo ở giữa, tự do chuyển đổi, đối linh hồn có cực lớn ôn dưỡng tác dụng, phối hợp , phối chế dược vật, có thể để cho phổ thông Thần Linh linh hồn, tấn cấp đến Thiên Thần cấp bậc. Thiếu hụt một, nắm giữ cực lớn độc tính, với thân thể người có hại, không có cách nào giải độc, linh hồn tiến bộ, nhưng thân thể sẽ nhận tổn thương. Thứ hai số lượng thưa thớt, rất khó tìm...”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Nếu không phải thư viện có thể sử dụng, khả năng thật bị đối phương giấu diếm đi qua."),t("br"),t("br"),n._v("Thứ này lớn lên quả thực cùng cỏ xanh giống nhau như đúc, nhìn không ra mảy may khác biệt."),t("br"),t("br"),n._v("Chính là thú, lại là thảo, không nói danh sư đại lục cùng Thượng Thương, kiếp trước đều tồn tại, đông mà vì sâu, hạ mà vì thảo đông trùng hạ thảo đã là như thế, dược dụng giá trị cực lớn."),t("br"),t("br"),n._v("Không nghĩ tới trước mắt cái này cũng là như vậy, hơn nữa đối linh hồn có hiệu quả."),t("br"),t("br"),n._v("Trực tiếp để Thần Linh tấn cấp đến Thiên Thần cấp bậc linh hồn, có thể xưng kinh khủng."),t("br"),t("br"),n._v("Khó trách Sa Ly thú, vì đối phương, không tiếc từ sào huyệt xông tới, bất chấp nguy hiểm."),t("br"),t("br"),n._v("Còn độc tính, đối với nắm giữ Thiên đạo công pháp hắn tới nói, không quan trọng."),t("br"),t("br"),n._v("Rõ ràng chuyện gì xảy ra, sao có thể để trước mắt loại bảo vật này chạy trốn, Trương Huyền ngừng thở, bỗng nhiên bắt tới."),t("br"),t("br"),n._v("Dường như cảm nhận được trong lòng của hắn sinh ra bắt chi ý, trước mắt cỏ xanh nhẹ nhàng vụt qua, bỗng nhiên từ trong đất bùn nhảy ra ngoài, thẳng tắp hướng về phía trước xông tới."),t("br"),t("br"),n._v("Tốc độ nhanh chóng, tựa như một đạo gió táp."),t("br"),t("br"),n._v("“Trốn chỗ nào...”"),t("br"),t("br"),n._v("Trước đó bắt thỏ, là bởi vì thương thế quá nặng, đêm nay ăn một bữa cơm no, thể lực khôi phục không ít, liền thảo đều bắt không được, thật sự mất mặt."),t("br"),t("br"),n._v("Vừa sải bước ra, nhảy ra bảy, tám mét, lăng không một trảo."),t("br"),t("br"),n._v("Thần lực từ năm cái đầu ngón tay thả ra, hình thành một cái lưới đánh cá."),t("br"),t("br"),n._v("Tâm Tự Song Ti Võng, Trung Hữu Thiên Thiên Kết!"),t("br"),t("br"),n._v("Vượt qua Thiên đạo kiếm pháp, bao phủ xuống, lập tức đem bụi cỏ này bao vây ở bên trong."),t("br"),t("br"),n._v("Nhẹ nhàng bóp một cái, cầm ở lòng bàn tay, tinh thần khẽ động, hướng trong đó lan ra, quả nhiên phát hiện, một cỗ để cho người ta linh hồn ấm áp lực lượng, không ngừng chảy xuôi, ấm lòng người tỳ."),t("br"),t("br"),n._v("Ngón tay ngoắc ngoắc chuyển một cái, lưới đánh cá kiếm khí, triệt để bao phủ ở bên trong, để hắn lại không cách nào chạy trốn, Trương Huyền lúc này mới bỏ vào trong ngực, dùng quần áo đảm bảo tốt."),t("br"),t("br"),n._v("Hắn hiện tại linh hồn cấp bậc, chỉ là hạ phẩm Thần Linh tình trạng, nếu như có thể tấn cấp đến Thiên Thần cấp bậc, coi như gặp được chân chính Thiên Thần cường giả cũng không cần sợ hãi!"),t("br"),t("br"),n._v("Đem dược liệu sắp xếp gọn, trở lại chỗ ở, phát hiện Mặc Vân lão sư mang theo rất nhiều học sinh cũng quay về rồi, đầu kia Tật Phong Sa Ly thú, ngay tại trong tay đối phương, không ngừng giãy dụa, lại không thể làm gì."),t("br"),t("br"),n._v("“Chúc mừng!”"),t("br"),t("br"),n._v("Thấy bọn họ hoàn thành thí luyện, bắt được đầu này thần thú, Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Vừa rồi liền cảm thấy thứ này không thể chạy trốn, hiện tại xem ra, quả nhiên."),t("br"),t("br"),n._v("“Trương huynh đệ đi nơi nào?”"),t("br"),t("br"),n._v("Thấy hắn cũng không cùng mọi người tại cùng một chỗ, Mặc Vân lão sư nhíu nhíu mày, lộ ra nghi hoặc."),t("br"),t("br"),n._v("“Thấy các ngươi đi bắt thú, ta liền đi qua nhìn một chút, còn không có tìm tới các ngươi, liền trở lại, cho nên đi ngõ khác...” Trương Huyền nói."),t("br"),t("br"),n._v("“Liền điểm ấy khoảng cách, đều có thể đi đường khác, thật đúng là ngốc nghếch!”"),t("br"),t("br"),n._v("Trước đó vị kia hơi có tư sắc nữ học sinh Ngô Tiêu Tiêu khẽ nói, lần nữa nhìn về phía Trương Huyền ánh mắt lộ ra xem thường."),t("br"),t("br"),n._v("Đám người kia, kém chút làm hại bọn họ thí luyện không thành công, thực lực mạnh ngược lại cũng thôi, điểm ấy lộ đều có thể đi nhánh... Thật không phải bình thường ngốc."),t("br"),t("br"),n._v("Mấu chốt là... Trước đó một mực ánh mắt ở lại ở trên người nàng Thẩm Thành sư huynh, từ khi cái kia mấy cái nữ hài xuất hiện, nhìn cũng không nhìn nàng một cái, vừa nghĩ đến đây, liền nói không ra nổi giận."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2122"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2124"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);