(window.webpackJsonp=window.webpackJsonp||[]).push([[1265],{1620:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2110-mot-tac-bui"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2110-mot-tac-bui"}},[n._v("#")]),n._v(" Chương 2110: Một Tấc Bụi")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Ngươi nói người nào ngốc?” Lông mày giương lên, Triệu Nhã đứng dậy."),t("br"),t("br"),n._v("Làm nhục nàng có thể, làm nhục lão sư, tuyệt đối không được."),t("br"),t("br"),n._v("“Nói người nào người đó trong lòng rõ ràng!” Ngô Tiêu Tiêu nhếch miệng: “Không có thực lực, về sau ít chạy loạn, nếu không, gặp được nguy hiểm, lại muốn phiền phức người khác đưa trở về...”"),t("br"),t("br"),n._v("“Được rồi!” Một bên Mặc Vân lão sư quát tháo."),t("br"),t("br"),n._v("“Lão sư...” Ngô Tiêu Tiêu ngẩng đầu."),t("br"),t("br"),n._v("“Bớt tranh cãi!” Mặc Vân lão sư nhíu nhíu mày, nhìn lại: “Tiêu Tiêu nàng tuổi tác còn nhỏ, không hiểu lễ nghi, trong ngôn ngữ có chỗ đắc tội, xin hãy tha lỗi...”"),t("br"),t("br"),n._v("“Không sao cả!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Ngang dọc danh sư đại lục, Thượng Thương, tâm cảnh sớm đã trầm ổn như sắt, còn không đến mức cùng một cái tiểu nữ hài suy tính."),t("br"),t("br"),n._v("Tóm lấy Tật Phong Sa Ly thú, Mặc Vân lão sư dường như dùng phương pháp đặc thù tra hỏi một hồi, tiếp đó lặng lẽ rời khỏi đám người, tìm tiếp cận một canh giờ, cuối cùng cau mày đi trở về."),t("br"),t("br"),n._v("Hiển nhiên, cũng phát hiện không đúng, chỉ là không tìm được Thanh Thú Cổ Thảo."),t("br"),t("br"),n._v("Còn Trương Huyền, đổ không có quá nhiều nghi ngờ."),t("br"),t("br"),n._v("Sa Ly thú loại tốc độ này, đều không có bắt được, trước mắt vị này hầu như cái gì cũng đều không hiểu phổ thông Thần Linh, coi như nhìn thấy, cũng chưa chắc quen biết, chớ nói chi là bắt được."),t("br"),t("br"),n._v("Một đêm không có chuyện gì xảy ra, bình minh đám người xuất phát."),t("br"),t("br"),n._v("Nhiệm vụ hoàn thành, lại có thể chấp hành vị tiền bối kia nhắc nhở, Mặc Vân lão sư thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Đoàn người hướng Linh Cơ sơn đi ra ngoài."),t("br"),t("br"),n._v("Đi qua một chuyến, Mặc Vân lão sư cùng rất nhiều học sinh xe nhẹ đường quen, lại thêm, kèm theo linh khí suy yếu, trong núi hung mãnh thần thú, cũng đã chạy không sai biệt lắm, đổ không có gặp được quá lớn nguy hiểm."),t("br"),t("br"),n._v("Liên tục đi ba ngày."),t("br"),t("br"),n._v("Trong khoảng thời gian này, có Mặc Vân lão sư vị này trung phẩm Thần Linh ra tay, đi săn loại hình, không thành vấn đề, thức ăn bên trên, không cần tiếp tục lo lắng, có năng lượng bổ sung, Thiên đạo chi khí, dần dần khôi phục, Trương Huyền thương thế bên trong cơ thể hoàn hảo không chút tổn hại."),t("br"),t("br"),n._v("Tuy là khoảng cách tu vi đột phá, còn không biết năm nào tháng nào, nhưng ít ra không có trước đó như vậy suy yếu."),t("br"),t("br"),n._v("  “Ngày hôm nay không sai biệt lắm liền có thể rời khỏi sơn mạch!” Mặc Vân lão sư nói: “Có điều, mọi người không được để lỏng cảnh giác, Linh Cơ sơn bên ngoài, có không ít giặc cướp sơn tặc, những người này, nhưng mà cái gì đều không quan tâm!”"),t("br"),t("br"),n._v("Linh khí suy yếu, vì sinh tồn, không ít tu luyện giả, vào rừng làm cướp, cướp đoạt thành trấn ở giữa thương khách đoàn đội hoặc là qua lại lữ khách, tuy là phủ thành chủ, thường xuyên vây quét, nhưng hiệu quả quá mức bé nhỏ."),t("br"),t("br"),n._v("Lửa rừng đốt không hết, gió xuân thổi lại mọc."),t("br"),t("br"),n._v("Không giải quyết triệt để linh khí vấn đề, loại tình huống này, liền sẽ một mực tồn tại, không có người có thể tránh khỏi."),t("br"),t("br"),n._v("Nghe được giặc cướp sơn tặc, đám người đồng thời vẻ mặt nghiêm túc không ít."),t("br"),t("br"),n._v("Lần luyện tập này, trên núi nguy hiểm, ngược lại không như núi lâm đến Dạ Minh thành đoạn này khoảng cách lớn."),t("br"),t("br"),n._v("“Ngươi nghe được, trên đường tuyệt đối không nên lại chạy loạn, mặt khác, gặp được sơn tặc, chúng ta có thể bảo vệ không bảo vệ được các ngươi!” Ngô Tiêu Tiêu nhìn về phía Trương Huyền đám người, hừ lạnh nói."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Mặc kệ gặp được cái gì, hắn chưa hề hi vọng qua người khác, tất cả dựa vào chính mình."),t("br"),t("br"),n._v("“Triệu Nhã, nếu quả thật gặp được sơn tặc, ngươi đi theo ta đằng sau liền tốt, ta bảo vệ ngươi an toàn...” Thẩm Thành đi vào trước mặt."),t("br"),t("br"),n._v("“Không cần!” Không thèm để ý, Triệu Nhã đi vào lão sư sau lưng."),t("br"),t("br"),n._v("“Cái này...” Bị cự tuyệt, Thẩm Thành có chút xấu hổ."),t("br"),t("br"),n._v("Nhìn Ngô Tiêu Tiêu lần nữa nghiến răng."),t("br"),t("br"),n._v("Vì phòng ngừa ngoài ý muốn, đám người đi đường suốt đêm."),t("br"),t("br"),n._v("“Lão sư, nghỉ ngơi một hồi a, không sai biệt lắm lại quá hai canh giờ, liền có thể trở về thành, không cần thiết một mực đi đường, không được, chúng ta bình minh lại đi cũng không muộn...” Ngô Tiêu Tiêu nói."),t("br"),t("br"),n._v("Đi hơn phân nửa đêm, tất cả mọi người cảm thấy thân thể có chút chịu không nổi."),t("br"),t("br"),n._v("Ngược lại tu vi yếu Triệu Nhã đám người, không nói một lời, cũng không nhìn ra bất kỳ mệt mỏi."),t("br"),t("br"),n._v("“Cũng tốt!” Thấy khoảng cách thành thị không xa, Mặc Vân lão sư cũng thở phào nhẹ nhõm, gật gật đầu."),t("br"),t("br"),n._v("Đám người tìm cái chỗ trũng chỗ, đốt lên lửa trại, ngồi xuống."),t("br"),t("br"),n._v("Đang chuẩn bị nghỉ ngơi, đột nhiên nơi xa truyền đến móng ngựa gấp rút âm thanh, ngay sau đó mặt đất chấn động, dường như có một đám người lao đến."),t("br"),t("br"),n._v("“Nguy rồi!”"),t("br"),t("br"),n._v("Không nghĩ tới ra khỏi thành không xa, sẽ có người đêm khuya tới, Mặc Vân lão sư biến sắc, vung tay lên, đem hỏa diễm dập tắt, vội vàng đứng dậy hướng âm thanh nguồn gốc nhìn sang."),t("br"),t("br"),n._v("Luật luật..."),t("br"),t("br"),n._v("Móng ngựa ngừng lại, hơn mười bóng người, cưỡi ngựa cao to, đi vào trước mặt, đem mọi người bao bọc vây quanh."),t("br"),t("br"),n._v("Cầm đầu trung niên nhân, cầm trong tay yển nguyệt đao, tóc đen nhánh bên trên, một đạo màu xám Lưu Hải, đáp lên trên mặt, cùng trắng noãn khuôn mặt, hình thành sự chênh lệch rõ ràng."),t("br"),t("br"),n._v("“Một Tấc Bụi?” Đồng tử co rụt lại, Mặc Vân lão sư thân thể run rẩy."),t("br"),t("br"),n._v("Dạ Minh thành xung quanh, có rất nhiều sơn tặc, thực lực cao thấp không đều, nhưng... Muốn nói danh khí lớn nhất, mạnh nhất, chính là cái này Một Tấc Bụi!"),t("br"),t("br"),n._v("Vị này sơn tặc, tên thật tên gì đã không có người biết, bởi vì tóc có một túm, màu xám trắng, được người xưng là “Một Tấc Bụi”, không chỉ hung ác, thực lực càng là so với chính mình đều mạnh, đã đạt đến trung phẩm Thần Linh đỉnh phong."),t("br"),t("br"),n._v("“Nhìn tới tên tuổi của ta cũng không tệ lắm!” Ghìm chặt dưới thân tuấn mã, Một Tấc Bụi cười ha ha."),t("br"),t("br"),n._v("“Lão đại tên tuổi, đương nhiên không tệ!”"),t("br"),t("br"),n._v("“Nếu biết là lão đại tự thân tới, cũng đừng phí lời, chúng ta chỉ cướp tiền, không cần thiết sai lầm, tổn thương tính mạng!”"),t("br"),t("br"),n._v("Bên cạnh mấy vị sơn tặc, đồng thời cười ha ha nói."),t("br"),t("br"),n._v("Tiến về phía trước một bước, Mặc Vân lão sư nghiến răng, nói: “Tại hạ là Dạ Minh học viện lão sư, Mặc Vân! Lần này mang học sinh chỉ là đi ra thí luyện, trên người không có gì tiền bạc, mong rằng Một Tấc Bụi đương gia, thả chúng ta một ngựa...”"),t("br"),n._v("“Học sinh? Đã như vậy, đem bọn ngươi binh khí trong tay, cùng đầu kia Sa Ly thú, cùng một chỗ cho ta!” Nhíu nhíu mày, Một Tấc Bụi nhìn một vòng, nói."),t("br"),t("br"),n._v("Mặc Vân lão sư chần chừ."),t("br"),t("br"),n._v("“Chúng ta thật vất vả bắt được Sa Ly thú, vì sao phải cho ngươi?” Ngô Tiêu Tiêu hướng về phía trước: “Lập tức tránh ra, để chúng ta về thành, nếu không... Có tin ta hay không để cho ta cha, đem bọn ngươi đều diệt?”"),t("br"),t("br"),n._v("“Cha ngươi?”"),t("br"),t("br"),n._v("“Không sai, cha ta chính là Dạ Minh thành thành chủ, Ngô Phương Thanh! Thiên Thần cảnh cường giả, ta khuyên ngươi thức thời một ít, ngay cả ta cũng dám ăn cướp, hắn khẳng định sẽ đem các ngươi hết thảy bắt được, giết!”"),t("br"),t("br"),n._v("Thân thể mềm mại ưỡn một cái, Ngô Tiêu Tiêu mang theo ngạo khí."),t("br"),t("br"),n._v("Nàng đường đường thành chủ nữ nhi, không sánh bằng mấy cái Ngụy Thần nữ hài ngược lại cũng thôi, mấy tên sơn tặc, lại còn muốn đánh Sa Ly thú chủ ý, quả thực không muốn sống nữa."),t("br"),t("br"),n._v("“Thành chủ nữ nhi?” Một Tấc Bụi sững sờ, chợt cười ha ha, nhìn về phía sau lưng đám người: “Chúng tiểu nhân, nói cho nàng, chúng ta sợ qua vị thành chủ kia chưa?”"),t("br"),t("br"),n._v("“Sợ? Sợ sẽ không làm cái này trên mũi đao kiếm ăn công việc!”"),t("br"),t("br"),n._v("“Đến hay lắm, đem tiểu nương bì này trói lại, để cha nàng đưa tiền tới chuộc người!”"),t("br"),t("br"),n._v("“Vị thành chủ này, những năm này đuổi chúng ta đuổi thật là điên rồi, bắt được nữ nhân này tốt nhất, để hắn cũng biết kịch liệt...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đám người tất cả xôn xao."),t("br"),t("br"),n._v("“Các ngươi muốn làm gì?”"),t("br"),t("br"),n._v("Vốn cho rằng báo ra thân phận của mình, một đám nho nhỏ mâu tặc liền sẽ nghe hơi mà chạy, không dám tìm phiền phức, không nghĩ tới hoàn toàn ngược lại, Ngô Tiêu Tiêu đồng tử co rụt lại."),t("br"),t("br"),n._v("Cha hắn thế nhưng là Thiên Thần, toàn bộ Dạ Minh thành, chỉ cần báo ra tên, ai dám ngỗ nghịch, đám sơn tặc này, vậy mà không thèm quan tâm... Thật không sợ chết ư?"),t("br"),t("br"),n._v("“Làm gì?” Một Tấc Bụi cười cười: “Vốn cho rằng thật sự là một đám học sinh nghèo, không nghĩ tới, còn có như vậy một con cá lớn! Chúng tiểu nhân, đem người lưu lại, một cái đều đừng để chạy, bắt bọn hắn lại, chúng ta liền có thể chân chính ăn mặn...”"),t("br"),t("br"),n._v("“Tốt!” Rất nhiều sơn tặc cười ha ha, mỗi người từ tuấn mã bên trên nhảy xuống tới, đem mọi người bao bọc vây quanh."),t("br"),t("br"),n._v("“Thẩm Thành, ngươi nghĩ biện pháp mang theo đám người phá vây...”"),t("br"),t("br"),n._v("Không nghĩ tới sẽ diễn biến thành như vậy, Mặc Vân lão sư sắc mặt tái xanh, giờ phút này nói cái gì cũng không kịp, hạ giọng, thân thể vụt qua, đi đầu hướng về phía trước Một Tấc Bụi vọt tới."),t("br"),t("br"),n._v("Kế trước mắt, chỉ có nghĩ biện pháp đánh bại vị này, dùng cái này uy hiếp, mới có liền sẽ chạy trốn."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Kiếm khí liền tản mát ra, cho người ta một loại nồng đậm áp bức."),t("br"),t("br"),n._v("Trung phẩm Thần Linh, mặc dù ở tại thần giới, vẫn như cũ thuộc về tầng dưới chót, nhưng lực lượng cùng độ nhạy, đều vượt xa hạ phẩm Thần Linh, lại thêm Mặc Vân thân là lão sư, chiêu số tinh diệu, đột nhiên tấn công, uy hiếp cực lớn."),t("br"),t("br"),n._v("“Không biết sống chết!” Dường như đã sớm đoán ra hắn sẽ động thủ, Một Tấc Bụi trong tay yển nguyệt đao dựng thẳng lên, không có bất kỳ cái gì động tác, trực tiếp bổ xuống."),t("br"),t("br"),n._v("Thoạt nhìn đơn giản, lại mang theo lực lượng khổng lồ, để cho người ta không thể không phòng."),t("br"),t("br"),n._v("Mặc Vân lão sư vội vàng thu kiếm ngăn cản."),t("br"),t("br"),n._v("Tạch tạch!"),t("br"),t("br"),n._v("Lực lượng quán dũng mà đến, trường kiếm không ngừng lay động, Mặc Vân lão sư chỉ cảm thấy gan bàn tay run lên, máu tươi từ lòng bàn tay chảy xuôi, cả người bị lực lượng khổng lồ nghiền ép, đầu gối mềm nhũn, quỳ rạp xuống đất."),t("br"),t("br"),n._v("Đối phương mượn yển nguyệt đao lực lượng, cùng ngồi tại ngựa bên trên ở trên cao nhìn xuống ưu thế, một chiêu liền đem vị lão sư này tất cả chiêu số đánh tan."),t("br"),t("br"),n._v("“Nhất lực hàng thập hội...” Trương Huyền cau mày."),t("br"),t("br"),n._v("Vừa rồi vị kia Ngô Tiêu Tiêu, báo ra gia môn, là hắn biết không ổn, thành chủ là rất lợi hại, nhưng nơi này là ngoài thành, hơn nữa đối phương vẫn là sơn tặc, nói như vậy không phải muốn chết sao?"),t("br"),t("br"),n._v("Nhìn tới cô bé này, không chỉ nuông chiều đã quen, càng quan trọng hơn là, không có một chút, dã ngoại trải qua cùng kinh nghiệm."),t("br"),t("br"),n._v("Nếu không, cũng sẽ không như vậy lỗ mãng."),t("br"),t("br"),n._v("Đương nhiên... Thật ra thì vị này không nói như vậy, cũng không cho rằng trước mắt đám sơn tặc này sẽ bỏ qua nhóm người mình."),t("br"),t("br"),n._v("Có thể đi vào Dạ Minh học viện, đều là nội thành thiên tài, từng cái gia thế không ít, đem đồ vật cướp đi, người lại thả... Không phải lưu lại cho mình tai hoạ ngầm ư?"),t("br"),t("br"),n._v("Có thể ngang dọc Dạ Minh thành không thiếu niên, danh khí càng lúc càng lớn, hắn cũng không cho rằng đối phương lại bởi vì học sinh mà lòng dạ mềm yếu."),t("br"),t("br"),n._v("Sở dĩ nói, chỉ cướp đoạt trường kiếm cùng Sa Ly thú, tê liệt mọi người mà thôi."),t("br"),t("br"),n._v("Nguyên nhân chính là đoán được cái này, Trương Huyền cùng Mặc Vân lão sư, biết rõ vị này Ngô Tiêu Tiêu đùa nghịch đại tiểu thư tính tình, sẽ dẫn tới tai hoạ, vẫn không có ngăn cản."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Trong lòng suy tư, Một Tấc Bụi yển nguyệt đao, nằm ngang đánh tới, rơi vào Mặc Vân lão sư trên người, chỉ thoáng cái, người sau liền bay ngang ra ngoài, thân thể cứng ngắc, lại khó động đậy."),t("br"),t("br"),n._v("“Cho ta trói lại!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Hai cái thuộc hạ đến đến trước mặt, lấy ra dây thừng, rất mau đem Mặc Vân lão sư trói thật chặt."),t("br"),t("br"),n._v("“Thế nào? Còn muốn hay không phản kháng?”"),t("br"),t("br"),n._v("Hai chiêu liền đem thực lực mạnh nhất Mặc Vân lão sư đánh thành trọng thương, Một Tấc Bụi cười nhạt một tiếng, nhìn quanh một tuần, trên mặt lộ ra ngạo khí."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thẩm Thành, Ngô Tiêu Tiêu đám người, tất cả đều sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Vốn cho rằng, lão sư có thể ngăn cản đối phương, coi như thắng không nổi, chí ít có thể kéo dài một đoạn thời gian, để hắn thuận lợi chạy trốn, không nghĩ tới, hai chiêu liền thua..."),t("br"),t("br"),n._v("Lão sư trung phẩm Thần Linh, cũng đỡ không nổi hai chiêu, nhóm người mình đi lên, chẳng phải một chiêu đều không kiên trì nổi?"),t("br"),t("br"),n._v("Đang không biết như thế nào cho phải, chỉ thấy vị kia gọi Trương Huyền thanh niên, một mặt đần độn đi tới."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2123"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2125"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);