(window.webpackJsonp=window.webpackJsonp||[]).push([[1271],{1634:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-204-luc-tam-giam-hoa-trinh-đo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-204-luc-tam-giam-hoa-trinh-đo"}},[n._v("#")]),n._v(" Chương 204: Lục Tầm giám họa trình độ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Vốn là Lưu Lăng còn muốn nhắc nhở một câu Điền lão, nhớ tới hắn vừa nãy đánh gãy lời của mình, sợ nói nhiều rồi chọc giận hắn không cao hứng, cũng sẽ không tại nhiều lời."),t("br"),t("br"),n._v(" “Các ngươi đã hai cái cũng không có vấn đề gì, Điền Cương, đi đem bức họa kia đem ra.” Nói xong Điền lão khoát tay chặn lại."),t("br"),t("br"),n._v(' "Vâng!'),t("br"),t("br"),n._v(" Thời gian không lâu, Điền Cương ôm một cái dài nhỏ hộp đi tới."),t("br"),t("br"),n._v(" “Đây là Lưu sư tiễn đưa thư họa của ta, có thể nói hoàn mỹ, mỗi lần quan sát, đều khen không dứt miệng, ngày hôm nay cũng coi như các ngươi có phúc, có thể chứng kiến loại này cấp bậc tác phẩm!” "),t("br"),t("br"),n._v(" Điền lão cười đem tranh vẽ theo trong hộp lấy đi ra."),t("br"),t("br"),n._v(" “Thật hy vọng sinh thời có thể nhìn thấy như vậy thư họa đại sư, tận mắt chứng kiến hắn phi phàm cao siêu tài nghệ.” "),t("br"),t("br"),n._v(" Xoa xoa bức họa trong tay, Điền lão ánh mắt mang theo thành kính."),t("br"),t("br"),n._v(" Trà đạo cùng thư họa, có không ít điểm giống nhau, chính vì như thế, hắn đối với thư họa, cực kỳ yêu tha thiết, tuy rằng không có đạt đến đại sư cấp bậc, nhưng cũng nghiên cứu khá sâu."),t("br"),t("br"),n._v(" “Không uổng lời nói, hiện tại liền cho các ngươi nhìn, Trang Hiền, Trịnh Phi, các ngươi cũng tinh thông thư họa, thuận tiện chỉ điểm một, hai.” "),t("br"),t("br"),n._v(" Cẩn thận từng li từng tí một đem thư họa quyển sách mở ra, một bộ thủy mặc đồ lập tức xuất hiện ở trước mắt mọi người."),t("br"),t("br"),n._v(" Là một mảnh bãi cỏ, bên trên đứng yên một cái dã lộc, đang cảnh giác ăn cỏ, xung quanh tràn đầy hoa dại, làm cho người ta một loại núi hoa sáng lạn, hương vị nức mũi cảm giác."),t("br"),t("br"),n._v(" Tuy rằng chỉ là một bộ mặt bằng tranh vẽ, để người nhìn lại cảm giác dã lộc sẽ bất cứ lúc nào chạy đến, vọt tới trước mặt."),t("br"),t("br"),n._v(" “Đây là... Ngũ cảnh thư họa?” "),t("br"),t("br"),n._v(" Chỉ liếc mắt nhìn, Lục Tầm lập tức con ngươi co rụt lại, hô hấp dồn dập."),t("br"),t("br"),n._v(" Thân là thư họa thế gia đi ra người, đối với thư họa hiểu rõ rất nhiều, liếc mắt là đã nhìn ra bức họa này tinh diệu chỗ."),t("br"),t("br"),n._v(" Hiển nhiên vượt qua kinh hồng, đạt đến càng cao thâm cảnh giới."),t("br"),t("br"),n._v(" Loại này cấp bậc thư họa, coi như Thiên Huyền Vương quốc đều là trăm năm khó gặp, Điền lão có thể có được một bộ, đồng thời cầm đưa cho bọn hắn giám thưởng, tuyệt đối kiếm bộn rồi."),t("br"),t("br"),n._v(" Coi như phụ thân hắn loại này cấp bậc đại sư, cũng chưa từng gặp."),t("br"),t("br"),n._v(" “Không sai, chính là một bức ngũ cảnh thư họa, chỉ tiếc, tác giả không có lưu lại tên của chính mình cùng thư họa tên gọi, bằng không, ta nhất định tự mình đến nhà bái phỏng!” "),t("br"),t("br"),n._v(" Điền lão lắc đầu một cái, tràn đầy cảm khái: “Được rồi, nếu muốn giám thưởng, liền bắt đầu đi, quan sát bức họa này, sau đó làm ra bình luận, ai bình luận chuẩn xác nhất, thích hợp nhất thực tế, ai liền thắng lợi, do ta cùng ba sư làm phán xét, chắc là không cần hoài nghi công chính, lần này... Do Trương lão sư mở miệng trước.” "),t("br"),t("br"),n._v(" Vừa nãy là Lục Tầm trước tiên nói, vì công bằng, lần này để Trương Huyền mở miệng."),t("br"),t("br"),n._v(" Điền lão nói xong, thấy không ai trả lời, không nhịn được nhìn về phía cách đó không xa Trương Huyền, đã thấy hắn nhìn trước mắt tranh vẽ, trợn mắt ngoác mồm, đầy mặt mất tập trung."),t("br"),t("br"),n._v(" Thấy hắn bộ này dáng vẻ, Điền lão nhíu nhíu mày."),t("br"),t("br"),n._v(" Ngũ cảnh thư họa, quá mức cao siêu, một ít đối với thư họa không hiểu người thường, rất dễ dàng lạc lối trong đó, không nhận rõ hiện thực vẫn là thư họa, hiển nhiên, vị này Trương lão sư chính là như vậy."),t("br"),t("br"),n._v(" Liền thư họa cùng hiện thực đều không nhận rõ gia hỏa, làm sao có khả năng hiểu tranh?"),t("br"),t("br"),n._v(" Xem ra tỷ thí thư họa tuyển đúng rồi, vị này Trương lão sư, chắc là còn lâu mới là đối thủ của Lục Tầm."),t("br"),t("br"),n._v(" “Hừ, để ngươi trang bức, liền trình độ loại này, còn thư họa đại sư? Đùa gì thế!” "),t("br"),t("br"),n._v(" Một bên Điền Long nhìn thấy hắn thất thố, cũng cười lạnh."),t("br"),t("br"),n._v(" Nếu như nói trước bởi vì Nguyên Ngữ xưng hô để hắn lòng sinh hoài nghi mà nói, nhìn thấy cái tên này giờ khắc này dáng vẻ, nhất thời yên ổn."),t("br"),t("br"),n._v(" Chân chính thư họa đại sư, làm sao có khả năng nhìn thấy ngũ cảnh thư họa biến thành dáng vẻ ấy?"),t("br"),t("br"),n._v(" Nhất định là quá mức chấn động, bản thân bị lạc lối."),t("br"),t("br"),n._v(" “Trương lão sư!” Điền lão nói."),t("br"),t("br"),n._v(" “Ừm!” Trương Huyền lúc này mới khôi phục như cũ, khuôn mặt quái lạ: “Không cần, vẫn để cho Lục lão sư trước tiên nói đi, ta... Nghe là được!” "),t("br"),t("br"),n._v(" “Tốt lắm, ta liền không khách khí, bất quá, hi vọng một lúc nữa thua, không muốn lại kiếm cớ!” "),t("br"),t("br"),n._v(" Thấy đối phương liền mở miệng trước tiên nói dũng khí đều không có, Lục Tầm đầy mặt xem thường."),t("br"),t("br"),n._v(" Cái tên này khẳng định là thấy trước mắt thư họa quá mức cao thâm, không biết làm sao giám thưởng, mới để cho mình trước tiên nói, đến thời điểm chỉ cần trông mèo vẽ hổ liền có thể."),t("br"),t("br"),n._v(" Bất quá, chiêu này vừa nãy có thể dùng, lần này có phòng bị, lại làm sao có khả năng để ngươi thành công!"),t("br"),t("br"),n._v(" Vung tay áo một cái, đi đến đại sảnh ở giữa, Lục Tầm trên mặt lộ ra nồng đậm tự tin."),t("br"),t("br"),n._v(" “Ngũ cảnh thư họa, chính là thư họa Tông Sư lưu lại, ta không dám quá nhiều xen vào, nếu như một lúc nữa có chút không đúng, còn hi vọng chư vị góp ý, coi như giao lưu!” "),t("br"),t("br"),n._v(" Đi tới thư họa trước mặt, Lục Tầm hai mắt tỏa ánh sáng, mang theo đại sư đặc hữu phong thái: “Giám thưởng loại này cấp bậc cao tranh, cần từ xa đến gần, từ nông đến sâu, đã như vậy, ta liền trước tiên nói một chút về, bức họa này toàn thể bố cục.” "),t("br"),t("br"),n._v(" Vừa nãy trà đạo thời điểm, hắn chỉ nói tên gọi cùng trà hương mấy phẩm, làm cho đối phương có cơ hội để lợi dụng được, lần này hắn có thể chuẩn bị kỹ càng, dự định đem biết đến trọn vẹn nói ra."),t("br"),n._v("  Chỉ cần đem tất cả mọi thứ nhiều lời xong, xem ngươi một lúc nữa làm sao nói!"),t("br"),t("br"),n._v(" Đi ngưu bức con đường, để ngươi không đường có thể đi!"),t("br"),t("br"),n._v(" “Bức họa này, tuy rằng tác giả không có lưu lại tên gọi, nhưng ý cảnh cao xa, chỉ cần nhìn một chút, sẽ cảm thấy một cái bao la thảo nguyên phả vào mặt. Dã lộc nhàn nhã tự đắc ăn cỏ, bông hoa cạnh tranh thơm ngát... Ta đoán, vẽ tranh vị tông sư này, đang vẽ tranh thời điểm, tất nhiên đứng ở rộng rãi trên cỏ, cảm nhận được trước mắt tráng lệ cùng yên tĩnh, tâm tình thật tốt, mới múa bút vẩy mực, lưu lại bộ này bất hủ danh họa.” "),t("br"),t("br"),n._v(" “Nếu như thật muốn lấy cái tên, ta cảm thấy phải gọi... Múa bút thanh thiên đồ!” "),t("br"),t("br"),n._v(" Lục Tầm nói."),t("br"),t("br"),n._v(" “Múa bút thanh thiên đồ? Không sai, thật khí thế tên, viết đúng sự thật vị này thư họa sư trong lồng ngực, một luồng lăng nhiên khí tức, chọc tan bầu trời!” Điền Long tán dương."),t("br"),t("br"),n._v(" Thấy không?"),t("br"),t("br"),n._v(" Đây mới thực sự là thư họa cao thủ, vừa mở miệng liền nói ra bức họa này ý nhị, để người mơ màng vô hạn."),t("br"),t("br"),n._v(" “Nói xong tên, nói tiếp nội dung bên trong cùng bút pháp, rất rõ ràng, bức họa này dùng song câu bút pháp, trước phác hoạ ra vật tượng hình dáng, lại vẽ ra một cái vĩ mô ý cảnh, đem hoa cỏ cùng dã lộc bố trí tô điểm trong đó, cũng chỉ có loại này họa pháp, mới có thể biểu lộ ra như vậy khổng lồ cách cục, ẩn chứa mạnh mẽ như vậy ý cảnh!” "),t("br"),t("br"),n._v(" Lục Tầm hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v(" “Song câu bút pháp? Chẳng lẽ là sẽ không là Tả Hữu Du Long?” Trương Huyền không nhịn được nói."),t("br"),t("br"),n._v(" “Tả Hữu Du Long? Ngươi biết này từ là có ý gì sao?” "),t("br"),t("br"),n._v(" Lục Tầm còn chưa mở miệng, Điền Long nhịn không được cười lên: “Tả Hữu Du Long là ven đường thợ thủ công vì không có thời gian mới dùng thủ pháp, giống như in ấn, mỗi ngày tranh đồng nhất bộ tác phẩm, thời gian lâu, hay làm khéo tay, mới có thể hoàn thành. Ngươi nói ngũ cảnh tác phẩm hội họa, dùng loại thủ pháp này làm ra? Ha ha, ngươi hiểu thư họa sao?” "),t("br"),t("br"),n._v(" Không những Điền Long đầy vẻ khinh bỉ, liền ngay cả Điền lão nhìn Trương Huyền một chút, cũng không nhịn được lắc đầu."),t("br"),t("br"),n._v(" Tả Hữu Du Long vẽ ra ngũ cảnh? Rồi cùng dùng Thái Tổ Trường Quyền đánh ra Giáng long thập bát chưởng sức mạnh như thế, ngươi xác định là đến giám thưởng, không phải đến khôi hài?"),t("br"),t("br"),n._v(" “Ngươi thật sự dám nghĩ!” "),t("br"),t("br"),n._v(" Lục Tầm cười gằn, trong mắt xem thường tâm ý càng nồng, khóe miệng giương lên, không để ý cái này cái gì cũng không hiểu gia hỏa, tiếp tục nhìn về phía trước mắt thư họa: “Mới vừa nói chính là toàn thể kết cấu, bây giờ nói nói những chi tiết này phương diện, điểm ấy, vị tông sư này xử lý trông rất sống động. Xem những cái này hoa dại, có thể rõ ràng nhìn thấy mạch lạc; Đầu kia dã lộc, thậm chí có thể nhìn thấy thân thể lông tóc... Từ điểm này xem, bức họa này khẳng định không phải một lần mà thôi, coi như là Tông Sư, cũng vẽ chí ít hai ba ngày thời gian.” "),t("br"),t("br"),n._v(" “Chỉ có thời gian dài như vậy tinh điêu tỉ mỉ trác, mới có thể đem một bộ tranh chi tiết hoàn mỹ bày ra, ý nhị đầy đủ, không cho thưởng thức người, chọn gặp sự cố cùng tật xấu, không thể không nói, vị đại sư này tâm tính cùng sự chịu đựng, đều không phải người thường đi tới!” "),t("br"),t("br"),n._v(" Lục Tầm trong mắt lộ ra khâm phục tâm ý, vờn quanh tranh vẽ quay một vòng, nói tiếp: “Cuối cùng, bức họa này, ý cảnh xa xưa phong cách cổ xưa, tuy rằng bên trên không có để tác giả tên, nhưng đi qua ta nhiều năm xem tranh kinh nghiệm, cũng có thể đoán ra một, hai.” "),t("br"),t("br"),n._v(" “Ồ? Ngươi có thể đoán được? Là ai?” "),t("br"),t("br"),n._v(" Một bên Trương Huyền lần thứ hai không nhịn được nói, trong mắt quái lạ tâm ý càng nồng."),t("br"),t("br"),n._v(" Hai tay một lưng, Lục Tầm đầu vung lên, mang theo ở trên cao nhìn xuống ngạo khí cùng đối với thư họa mạnh mẽ tự tin: “Có thể có loại này lòng dạ cùng ý nhị thư họa đại sư, chung quanh mười quốc, trăm năm qua tổng cộng bất quá bảy vị, trong đó bốn vị, truyền lưu thế gian tác phẩm, rất ít đề cập đến loại này cảnh tượng, còn lại ba vị, từng có tương tự tác phẩm tiền lệ.” "),t("br"),t("br"),n._v(" “Ba người này, phân biệt là Bắc Vũ Vương quốc kim Mãn Đường tiền bối, Hàn Vũ Vương quốc Ngô Kiệt Siêu tiền bối cùng với Thần phong Vương quốc Vân Thiểu Khanh tiền bối.” "),t("br"),t("br"),n._v(" “Kim Mãn Đường tiền bối, từng ở mười tám trượng nguyên lưu lại kiệt tác, trong đó tranh ra cây thông, cứng cáp mạnh mẽ, có phần cổ nhân ý nhị, cùng con này dã lộc, thảo nguyên cảnh tượng không bàn mà trùng ý, nguyên là đáng giá nhất hoài nghi ứng cử viên. Bất quá, Mãn Đường tiền bối, tám mươi năm trước liền qua đời, nếu như thật lưu lại bức họa này, khẳng định sớm đã có người biết được, mà không đến mức hiện tại mới nhìn thấy, vì lẽ đó, không thể là hắn.” "),t("br"),t("br"),n._v(" “Ngô Kiệt Siêu tiền bối, am hiểu tranh động vật, có người nói từng vẽ một cái bạch nga, nằm rạp ở mặt đất, trêu đến một đầu thật nga đến đây, cho rằng đồng bạn tử vong, bi thiết hí dài, hậm hực mà chết, e sợ cũng chỉ có loại này tài nghệ, mới có thể tranh ra con này dã lộc.” "),t("br"),t("br"),n._v(" “Bất quá, hắn chỉ am hiểu động vật, không am hiểu hoa cỏ, có thể đem hai thứ này, đều tranh xuất sắc như thế, e sợ chúng ta thập đại Vương quốc phụ cận, cũng chỉ có Vân Thiểu Khanh tiền bối! Mấu chốt nhất chính là, ba vị này Tông Sư, chỉ có vị này còn sống trên đời.” "),t("br"),t("br"),n._v(" “Vì lẽ đó, ta suy đoán, bức họa này sáng tác, chính là lão nhân gia người lưu lại.” "),t("br"),t("br"),n._v(" Nói xong, Lục Tầm ngạo nghễ đứng thẳng, hơn người."),t("br"),t("br"),n._v(" “Tốt, tốt, phân tích rất tốt, sâu sắc vào bên trong, lập luận sắc sảo!” "),t("br"),t("br"),n._v(" Điền lão vỗ tay ca ngợi."),t("br"),t("br"),n._v(" Thấy không, cái gì gọi là chuyên nghiệp?"),t("br"),t("br"),n._v(" Đây mới là!"),t("br"),t("br"),n._v(" Không những đem tranh phân tích mạch lạc rõ ràng, thậm chí ngay cả tác giả là ai, đều đoán cái tám chín phần mười, không hổ là Lục Trầm đại sư con trai, đơn phần này kiến thức cùng hiểu biết chính xác, lần thi này nghiệm liền thắng chắc."),t("br"),t("br"),n._v(" “Được rồi, Trương lão sư đến lượt ngươi!” "),t("br"),t("br"),n._v(" Tán dương xong, Điền lão nhìn về phía cách đó không xa Trương Huyền."),t("br"),t("br"),n._v(" “Luân ta nói rồi? Ngươi xác định thật làm cho ta nói?” Trương Huyền nhìn sang."),t("br"),t("br"),n._v(" “Đương nhiên, làm sao, có phải là ta mới vừa mới đem ngươi muốn nói đều nói xong, nghĩ nói mình đã bí lời?” Lục Tầm trong mắt lộ ra trào phúng mùi vị."),t("br"),t("br"),n._v(" “Không phải... Ta kỳ thực trong lúc nhất thời nhớ không nổi cái gì giám thưởng ngôn ngữ... Nghĩ tới nghĩ lui, chỉ có bốn chữ!” Trương Huyền nói."),t("br"),t("br"),n._v(" “Bốn chữ?” Điền lão cùng Lục Tầm đồng thời nhìn sang."),t("br"),t("br"),n._v(" “Ừm!” "),t("br"),t("br"),n._v(" Trương Huyền vò đầu, có chút không tốt lắm ý tứ: “Bức họa này, nếu như không nhìn lầm... Là ta vẽ ra!” "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-212"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-214"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);