(window.webpackJsonp=window.webpackJsonp||[]).push([[1272],{1626:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2116-long-đat-cho-đen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2116-long-đat-cho-đen"}},[n._v("#")]),n._v(" Chương 2116: Lòng đất chợ đen")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Đa tạ vị trưởng lão này...” Thấy đồng ý hắn rời đi, Trương Huyền mỉm cười, mang theo trước mọi người đi mấy bước, vừa muốn rời đi đại điện, đột nhiên ngừng lại, quay đầu nhìn qua: “A, đúng, Dạ Minh Thành đã chủ trương công bằng, nơi này lại không cho phép gian lận, cái kia... Có người chào hàng bản thân tính toán ra dãy số, dùng cái này kiếm lời, từ đó rút thành, có tính hay không làm trái quy tắc?”"),t("br"),t("br"),n._v("“Thuộc về làm trái quy tắc!” Phó Viễn trưởng lão gật đầu."),t("br"),t("br"),n._v("Bọn cơ hội đều phạm pháp, huống chi loại này lừa gạt người hành động."),t("br"),t("br"),n._v("“Vị này Nghiêu Sơn tiền bối, ngay tại chào hàng bản thân suy tính ra dãy số, không tin, ngươi có thể tìm người hỏi một chút!” Mỉm cười, Trương Huyền chỉ hướng một bên Nghiêu Sơn, lần nữa ôm quyền: “Cáo từ!”"),t("br"),t("br"),n._v("Nói xong, mặc kệ đối phương thế nào, xoay người rời đi."),t("br"),t("br"),n._v("Cái tên này thua tiền, như vậy coi như thôi, bản thân cũng lười chú ý, vừa rồi cố ý tìm phiền toái, không cho rời đi... Hắn Trương Huyền, há lại là tùy ý để cho người ta bắt nạt?"),t("br"),t("br"),n._v("Còn, tra như thế nào ra hắn chào hàng dãy số, vô cùng đơn giản, mới vừa rồi cùng bản thân đánh cược thời điểm, mấy cái người mới bị dao động, khai ra hắn tới, không khó."),t("br"),t("br"),n._v("“Phó Viễn trưởng lão, ta...” Nằm mơ đều không có nghĩ đến, vị này như vậy thù dai, Nghiêu Sơn giật nảy mình, vội vàng muốn giải thích."),t("br"),t("br"),n._v("Tiếng nói xuống dốc, Phó Viễn trưởng lão hét lớn một tiếng: “Cầm xuống!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Một đám người vây lên, tóm lấy."),t("br"),t("br"),n._v("Không cần nghĩ, cái tên này khẳng định thảm rồi, trước đó chỉ là tổn thất một gian nhà vệ sinh, một gian nhà bếp, hiện tại đoán chừng phòng khách và nhà hàng cũng mất."),t("br"),t("br"),n._v("Không tiếp tục để ý bên trong xuất hiện biến cố, Trương Huyền đám người cưỡi lên ngựa thớt, rời đi U Hồn điện, cấp tốc hướng chỗ ở bay lượn mà đi, vòng qua mấy cái đường phố, xác định không có người theo dõi cùng chú ý, lúc này mới ngừng lại, bàn giao mọi người: “Các ngươi về trước đi tiến hành tu luyện, chuẩn bị nghênh đón ngày mai thần sơn hành trình, tranh thủ người người đều có thể ở trong đó đột phá đến Thần Linh cảnh giới!”"),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("Không tiếp tục để ý, Trương Huyền phóng ngựa thay đổi phương hướng, thời gian không dài, biến mất tại cuối con đường."),t("br"),t("br"),n._v("Cũng không nhiều lời, mọi người cấp tốc hướng về chạy như điên mà đi."),t("br"),t("br"),n._v("Trở thành Thần Linh sau, mới có thể không trở thành vướng víu, nhất định phải tăng thêm tốc độ tu luyện."),t("br"),t("br"),n._v("Rời đi mọi người sau, Trương Huyền một đường hướng về phía trước, rất nhanh, đi tới một chỗ không quá cao kiến trúc trước."),t("br"),t("br"),n._v("Cái này kiến trúc thoạt nhìn cùng địa phương khác không có khác nhau quá nhiều, không có bất kỳ cái gì chỗ đặc thù, giống như là cái dân cư, mười phần không đáng chú ý, hơn nữa cửa đóng chặt, bên ngoài cũng không có người nào ẩn hiện."),t("br"),t("br"),n._v("Đi tới trước mặt, ngón tay khép lại, tại cửa gỗ bên trên liên tục gõ sáu lần, bốn dài hai ngắn."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Cửa phòng mở ra, một cái người mù đi ra: “Mời!”"),t("br"),t("br"),n._v("Cũng không nhiều lời, Trương Huyền nhấc chân đi vào."),t("br"),t("br"),n._v("Gian phòng hơi có vẻ u ám, đi theo người mù sau lưng, đi tới bên trong tận cùng bên trong gian phòng."),t("br"),t("br"),t("br"),n._v("Ngừng lại, người mù xòe bàn tay ra."),t("br"),t("br"),n._v("Trương Huyền cổ tay khẽ đảo, đưa qua một cái lệnh bài."),t("br"),t("br"),n._v("Đối phương cầm ở trong tay, lục lọi hai cái, lúc này mới gật gật đầu, bàn tay đặt tại trên vách tường."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một tiếng kêu khẽ, tựa như có trận pháp kích hoạt, bức tường thời gian nháy mắt, biến thành trong suốt chi sắc, người mù tiến về phía trước một bước, đi vào, Trương Huyền theo sát phía sau."),t("br"),t("br"),n._v("Là cái hướng phía dưới lan ra cầu thang, tràn đầy đen kịt, thấy không rõ phía dưới đến cùng có cái gì, chỉ có thể nhìn thấy trên tảng đá khắc đầy đặc thù phù văn, để cho người ta khó mà phát giác chỗ này tồn tại."),t("br"),t("br"),n._v("Con mắt nửa khép nửa mở, Trương Huyền cũng không nói chuyện, rất đi mau đến cầu thang phần cuối, là cái không lớn thuyền nhỏ, trôi lơ lửng ở một cái mạch nước ngầm phía trên."),t("br"),t("br"),n._v("Ngồi lên thuyền, chạy tại đen kịt thông đạo dưới lòng đất, đưa tay không thấy được năm ngón, một đường tiến lên, cho dù Minh Lý Chi Nhãn, dường như cũng mất đi hiệu quả, phân biệt không ra đông nam tây bắc."),t("br"),t("br"),n._v("Đại khái nửa canh giờ, thuyền nhỏ tại một khối thoạt nhìn cứng rắn vô cùng nham thạch bên trên nhẹ nhàng đụng một cái, rơi vào trong đó, lắc lư hai cái, ngừng lại."),t("br"),t("br"),n._v("Đi theo người mù sau lưng, lại dọc theo đồng dạng một đạo cầu thang đi lên một hồi, lần nữa theo mặt tường xuất hiện, đã đi tới một cái rộng lớn Thương trong tràng, người bên trong tiếng huyên náo, không biết bao nhiêu."),t("br"),t("br"),n._v("“Chờ ở tại đây, ta giao dịch xong, liền ra tới!” Trương Huyền bàn giao một câu, nhấc chân hướng đám người đi tới."),t("br"),t("br"),n._v("Nơi này... Chính là Dạ Minh Thành lòng đất chợ đen!"),t("br"),t("br"),n._v("Có quy tắc tồn tại, tự nhiên là có hay không quy tắc địa phương."),t("br"),t("br"),n._v("Cái này chợ đen đúng là như thế."),t("br"),t("br"),n._v("Trước đó cái kia gọi một tấc bụi sơn tặc, thường xuyên đem giành được bảo vật, ở đây bán ra, trữ vật giới chỉ bên trong thư tịch, tỉ mỉ ký thuật thế nào tiến vào bên trong phương pháp, cho nên, nhìn một lần, liền triệt để rõ ràng."),t("br"),t("br"),n._v("Ở đây, có thể mua được muốn mua tất cả đồ vật, cũng có thể bán ra, ngươi muốn ra tay bảo bối, chỉ bất quá giá cả, không có như vậy bình dị gần gũi thôi."),t("br"),n._v("Chợ đen người quản lý, là cái thoạt nhìn mang theo yêu diễm nữ tử, chừng ba mươi tuổi, một thân nồng đậm mùi nước hoa, có chút gay mũi, trên cổ thêu lên một đóa đỏ thẫm hoa hồng, gai nhọn mang theo sắc bén."),t("br"),t("br"),n._v("“Vị tiểu ca này rất là lạ mặt...” Khuôn mặt tiến tới, yêu diễm nữ tử mị nhãn như tơ."),t("br"),t("br"),n._v("“Lạ mặt liền không buôn bán?” Trương Huyền mí mắt vừa nhấc."),t("br"),t("br"),n._v("“Dĩ nhiên không phải!” Yêu diễm nữ tử, vặn vẹo một cái rắn nước đồng dạng vòng eo, theo trong quầy đi ra, nhẹ nhàng cười một tiếng: “Không biết... Vị tiểu ca này, muốn làm gì dạng buôn bán? Chúng ta nơi này, nhưng mà cái gì đều có thể mua được... Bao quát ta!”"),t("br"),t("br"),n._v("Nói xong, cố ý tại Trương Huyền trên mặt thổi một hơi."),t("br"),t("br"),n._v("Không để ý tới nàng dụ dỗ, ngón tay gõ mặt bàn, Trương Huyền cười nhạt một tiếng: “Ta muốn【 Ma Dương thú máu】, không biết nơi này khả năng tìm tới?”"),t("br"),t("br"),n._v("Đã chiếm được thanh thú cổ cỏ, hiện tại cần tìm kiếm Ma Dương thú máu, mới có thể nghĩ biện pháp, để lực lượng linh hồn tăng lên."),t("br"),t("br"),n._v("“Ma Dương thú máu? Ma Dương thú, nhưng mà hạ phẩm Thiên Thần cảnh Thần thú, máu của nó, không có tốt như vậy làm...” Yêu diễm nữ tử nhíu nhíu mày, nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Hạ phẩm Thiên Thần cấp bậc Thần thú, phần lớn cũng đã biến hóa, nắm giữ cùng nhân loại đồng dạng thân thể, muốn nắm lấy rất khó, chớ nói chi là đạt được huyết dịch."),t("br"),t("br"),n._v("“Vậy được, ta đi tìm người khác!” Đứng dậy, Trương Huyền xoay người rời đi."),t("br"),t("br"),n._v("“Ha ha, không nên gấp gáp!” Vội vàng đi tới trước mặt, yêu diễm nữ tử ngăn lại đường đi: “Ta chỉ nói không có tốt như vậy làm, cũng không nói, tìm không thấy...”"),t("br"),t("br"),n._v("“Ta muốn một lít!” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Nhiều như vậy?” Yêu diễm nữ tử sửng sốt một chút, nhịn không được cau mày: “Thiên Thần cảnh Thần thú huyết dịch, giá trị bao nhiêu, nếu ngươi mua sắm, chắc hẳn đã hỏi thăm rõ ràng! Ta cũng liền không cùng ngươi nói nhảm nhiều, một lít huyết dịch, ba ngàn thần tệ!”"),t("br"),t("br"),n._v("“Ba ngàn?” Trương Huyền cau mày: “Cái giá tiền này có chút không đúng ah!”"),t("br"),t("br"),n._v("Theo một tấc bụi trong tay, được thần tệ, cộng lại tổng cộng cũng liền sáu trăm tả hữu, khoảng cách ba ngàn, còn kém không biết bao nhiêu."),t("br"),t("br"),n._v("Ma Dương thú máu, đắt như vậy?"),t("br"),t("br"),n._v("Tuy là không biết Thiên Thần cảnh Thần thú huyết dịch, đến cùng nhiều đáng giá, nhưng đối phương khẳng định báo cao, hơn nữa vượt xa quá giá thị trường."),t("br"),t("br"),n._v("Chợ đen mua sắm đồ vật, sẽ không lưu lại dấu vết, có thể miễn trừ không ít phiền phức, nguyên nhân chính là như vậy, hắn mới không có đi vào trong thành chính quy Thương đi hỏi thăm."),t("br"),t("br"),n._v("“Thiên Thần cảnh Thần thú huyết dịch, không phải dễ dàng như vậy được, ba ngàn thần tệ, đã không cao... Lại nói, ngươi cũng rõ ràng, giống ta dạng này nữ nhân, sinh hoạt tại đây a u ám địa phương, người không nhận ra... Lại không kiếm nhiều một chút, về sau thế nào gả ra ngoài?”"),t("br"),t("br"),n._v("Vây quanh Trương Huyền dạo qua một vòng, yêu diễm nữ tử “Cục cục!” Cười một tiếng: “Nếu không, vị tiểu ca này, đem ta cưới, cho ngươi tiện nghi chút?”"),t("br"),t("br"),n._v("“Sáu trăm thần tệ, cộng thêm một câu lời khuyên!”"),t("br"),t("br"),n._v("Không để ý tới đối phương, Trương Huyền đầu lâu nâng lên."),t("br"),t("br"),n._v("“Sáu trăm thần tệ? Ngươi tới đập phá quán?” Yêu diễm nữ tử sầm mặt lại, “Rầm!” Tiếng nói kết thúc, lập tức theo bốn phía thoát ra mấy vị trung niên nhân áo đen, mỗi một cái đều khí tức sôi trào, đạt đến trung phẩm Thần Linh tình trạng."),t("br"),t("br"),n._v("“Ngươi cả nghĩ quá rồi, ta sẽ không đập phá quán, cũng khinh thường tại đập ngươi danh dự!” Trương Huyền thản nhiên nói: “Bởi vì lòng đất âm hàn ướt lạnh, hơn nữa linh khí chưa tới, ngươi có phải hay không một mực tại dùng【 Khứ Thấp Đan】, giải quyết trong cơ thể khí ẩm, lấy duy trì bản thân thanh xuân mỹ lệ?”"),t("br"),t("br"),n._v("“Phải thì như thế nào?” Yêu diễm nữ tử cười nhạo."),t("br"),t("br"),n._v("Sinh hoạt dưới đất, cả ngày không gặp được mặt trời, dùng Khứ Thấp Đan nhiều người đi, không nhiều nàng một cái, cũng không ít nàng một cái."),t("br"),t("br"),n._v("“Đúng là được, người khác có thể dùng, ngươi nhưng dùng không thể, không có đoán sai, ngươi cái cổ tính cả ngực, mọc đầy màu đỏ mụn nước, một mực không cách nào khu trừ a!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Ngươi nghe ai nói?” Biến sắc, yêu diễm nữ tử lông mày nâng lên."),t("br"),t("br"),n._v("Đối phương nói không sai, trên người nàng quả thực mọc đầy mụn nước, hơn nữa nắm giữ mùi vị khác thường, lúc này mới mỗi ngày ở trên người phun lên nồng đậm nước hoa, càng đem trần trụi bên ngoài làn da, thêu lên đỏ thẫm hoa hồng, che chắn mụn nước màu sắc."),t("br"),t("br"),n._v("“Không cần nghe ai nói, một cái liền có thể nhìn ra!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Trương Huyền mang theo lãnh đạm: “Thân là nữ tử, tu luyện chính là âm thuộc tính pháp quyết【 Triều Thủy Hàn Lưu công】, bộ công pháp này, cần hấp thu thủy triều cùng luồng khí lạnh bên trong âm thuộc tính lực lượng, bản thân trong cơ thể khí ẩm liền nặng hơn, dùng Khứ Thấp Đan, đem khu trừ, bên ngoài là khu trừ lòng đất sinh hoạt tai hoạ ngầm, trên thực tế đem bản thân công lực tinh hoa bộ phận cũng đi rơi mất. Công pháp mất đi hiệu lực hơn phân nửa, lại thêm nơi này như vậy hoàn cảnh, xuất hiện mụn nước chỉ là đơn giản nhất phản ứng, thời gian lâu dài, cơ bắp đều sẽ thối rữa, thậm chí tu luyện sẽ càng ngày càng chậm chạp, cuối cùng... Đình trệ xuống, tẩu hỏa nhập ma mà chết!”"),t("br"),t("br"),n._v("“Ngươi hù ta? Tỷ tỷ, ta không phải là hù lớn!” Sửng sốt một chút, yêu diễm nữ tử híp mắt lại."),t("br"),t("br"),n._v("Cứ việc đối phương thuyết rất nhiều địa phương đều là đúng, bao quát công pháp của nàng, cùng với trong thân thể biến hóa, có thể... Nàng cũng không tin tưởng, chỉ phục dùng Khứ Thấp Đan, liền có thể để hắn xuất hiện loại tình huống này, mấu chốt nhất là, bản thân không phát giác gì."),t("br"),t("br"),n._v("“Ngươi đương nhiên không phải hù lớn!” Trương Huyền tiếp tục nói: “Đơn thuần Khứ Thấp Đan, quả thực không cách nào đối ngươi công pháp, sinh ra ảnh hưởng quá lớn, nhưng nếu như phối hợp thêm, cái này thị trường ngầm đặc thù che giấu trận pháp đây? Không có đoán sai, ngươi chỗ ở, hẳn là tại phía tây nam vị a!”"),t("br"),t("br"),n._v("Yêu diễm nữ tử cau mày."),t("br"),t("br"),n._v("Không để ý tới nét mặt của nàng, Trương Huyền tiếp tục nói: “Mặt trời mọc đông bắc, chìm tây nam. Mặt trời mọc, dương khí mới sinh, mặt trời lặn, âm môn mở ra. Phía tây nam, chính là âm dương luân phiên chỗ. Cái khác vị trí, vạn vật sinh trưởng, sinh cơ bừng bừng, không nhận bất kỳ ảnh hưởng gì, có thể âm môn chỗ, lại khác biệt!”"),t("br"),t("br"),n._v("“Đổi lại những công pháp khác ngược lại cũng thôi, ngươi tu luyện chính là âm thuộc tính công pháp, dưới tình huống bình thường, tu luyện hẳn là một ngày ngàn dặm, đáng tiếc, Khứ Thấp Đan có thể khu trừ khí ẩm, ẩn chứa trong đó dương tính, dùng lâu dài, âm dương hỗn loạn, sinh ra mụn nước, đã là đơn giản nhất chứng bệnh.”."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2129"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2131"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);