(window.webpackJsonp=window.webpackJsonp||[]).push([[1273],{1627:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2117-my-dung-đan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2117-my-dung-đan"}},[n._v("#")]),n._v(" Chương 2117: Mỹ Dung đan")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Cái này...”"),t("br"),t("br"),n._v("Yêu diễm nữ tử lại nói không ra nói tới."),t("br"),t("br"),n._v("Nếu như nói trước đó, cho rằng đối phương là ăn nói lung tung, nghe được cái này, chần chờ."),t("br"),t("br"),n._v("Nàng chỗ ở, quả thực là tại đây cái địa cung tây nam phương hướng, tới gần lòng đất dòng sông, vốn nghĩ đến, thích hợp tu luyện, lại càng dễ hấp thu dòng nước bên trong hơi ẩm, âm khí, để bản thân sử dụng, hiện tại xem ra, cũng không phải là đơn giản như vậy."),t("br"),t("br"),n._v("Chẳng lẽ thật cùng đối phương nói đồng dạng, là bởi vì nuốt đi ướt đan, hơn nữa ở tại tây nam phương hướng gây nên?"),t("br"),t("br"),n._v("“Không tin?” Trương Huyền lần nữa nhìn qua: “Các ngươi ẩn tàng toà này cung điện dưới đất trận pháp, là dựa vào dòng nước khởi động, mang theo âm thuộc tính a! Chỉ có loại trận pháp này, mới có thể che lại Thiên Thần cường giả thần thức thăm dò cùng dò xét... Phía tây nam, chính là trận pháp âm khí tới nguyên địa, nồng nặc nhất, quanh năm sinh hoạt trong đó, không ra vấn đề, đều khó có khả năng!”"),t("br"),t("br"),n._v("“Ta là mọc ra chút, không nên mọc đồ vật, nhưng... Ngươi nói chưa chắc là đúng, trừ phi...” Yêu diễm nữ tử vung vung tay, để bốn cái trung niên nhân lui xuống, lúc này mới lần nữa nhìn qua: “Ngươi có biện pháp, để cho ta trên người nhọt biến mất! Ta liền có thể dựa theo ngươi mới vừa nói giá cả, đem Ma Dương thú huyết, bán ra cho ngươi!”"),t("br"),t("br"),n._v("“Chuyện này có khó khăn gì!” Trương Huyền nhìn qua: “Cho ta mười bình Thần Nguyên đan!”"),t("br"),t("br"),n._v("“Thần Nguyên đan? Mười bình?” Yêu diễm nữ tử tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Thần giới linh khí suy yếu, Thần Nguyên đan liền lộ ra vô cùng trân quý, mười bình, đường đường lòng đất chợ đen, tự nhiên có thể nhẹ nhõm lấy ra, nhưng đối với nàng mà nói, cũng tuyệt không phải một con số nhỏ."),t("br"),t("br"),n._v("“Không sai, muốn giải quyết triệt để trên người ngươi vấn đề, liền muốn vứt bỏ một vài thứ, Thần Nguyên đan đừng nhìn chỉ là giúp người tăng cao tu vi dược vật, nhưng chỉ cần phối hợp tốt, đồng dạng sẽ có bất ngờ hiệu quả! Đương nhiên... Không nỡ lòng bỏ liền thôi, coi như ta trước đó lời nói chưa nói, ba ngàn thần tệ, mặc dù khó mà nhận được, dựa vào thủ đoạn của ta, muốn có được, hẳn là sẽ không quá khó.”"),t("br"),t("br"),n._v("Bàn tay vẫy một cái, Trương Huyền không nói thêm lời."),t("br"),t("br"),n._v("“Tốt, nếu như không giải quyết được trên người ta vấn đề, biết ngươi đang gạt ta, ngày hôm nay ngươi không cần rời đi!”"),t("br"),t("br"),n._v("Hừ một tiếng, yêu diễm nữ tử tay ngọc vẫy một cái: “Dạ Minh thành tuân thủ quy củ không giả, nhưng nơi này không có bất kỳ cái gì quy củ, giết ngươi, cũng không có người phát giác, lại không người tìm được!”"),t("br"),t("br"),n._v("Hai tay ôm ở trước ngực, Trương Huyền không thèm để ý đối phương uy hiếp."),t("br"),t("br"),n._v("Thời gian không dài, mười bình hạ phẩm Thần Nguyên đan được đưa đến trước mặt."),t("br"),t("br"),n._v("“Ta cần một cái tĩnh thất, đem những thuốc này, luyện chế thành có thể giải quyết thân thể ngươi tai họa ngầm đồ vật.” Trương Huyền nói."),t("br"),t("br"),n._v("Yêu diễm nữ tử lạnh lùng liếc mắt nhìn, không nói thêm lời, thời gian không dài, Trương Huyền liền bị mang tại một cái phòng trước mặt, đẩy cửa đi vào, tiện tay bố trí mấy cái ngăn cách thăm dò trận pháp."),t("br"),t("br"),n._v("Lúc này mới đem mười bình Thần Nguyên đan lần nữa lấy ra ngoài."),t("br"),t("br"),n._v("Tùy ý đổ ra một viên, ngón tay một điểm, một đạo Thiên đạo chân khí rót vào trong đó."),t("br"),t("br"),n._v("Vấn đề của đối phương, người khác giải quyết, rất khó làm được, đối với hắn mà nói, không tính là gì, một đạo Thiên đạo chân khí liền có thể giải quyết."),t("br"),t("br"),n._v("Còn những này Thần Nguyên đan, đương nhiên là vì chính mình muốn."),t("br"),t("br"),n._v("Muốn cấp tốc đột phá, loại này ẩn chứa tinh thuần linh khí đồ vật, ắt không thể thiếu, không thừa dịp hiện tại lừa gạt điểm, chẳng phải thua lỗ?"),t("br"),t("br"),n._v("Dù sao mở lòng đất chợ đen, khẳng định cũng làm không biết bao nhiêu chuyện thương thiên hại lý, theo trong tay nàng làm vài thứ, mảy may đều không có lòng áy náy."),t("br"),t("br"),n._v("Đem còn lại đan dược thả lại nhẫn, tại trong tĩnh thất đợi một phút trái phải, lúc này mới vận chuyển ngụy trang phù lục, một mặt trắng bệch ra khỏi phòng."),t("br"),t("br"),n._v("“Đem viên đan dược này ăn vào!”"),t("br"),t("br"),n._v("Thấy yêu diễm nữ tử canh giữ ở cổng, cũng không rời khỏi, Trương Huyền đem dược vật đưa tới."),t("br"),t("br"),n._v("“Mười bình Thần Nguyên đan... Liền luyện chế một viên?” Yêu diễm nữ tử ánh mắt lộ ra vẻ ngờ vực."),t("br"),t("br"),n._v("Một bình Thần Nguyên đan, chứa đựng mười cái, mười bình chính là một trăm viên..."),t("br"),t("br"),n._v("Chỉ lấy ra một viên cái này... Hơn nữa hình dáng không có biến hóa chút nào, hù ta chơi a?"),t("br"),t("br"),n._v("“Thử một chút có hiệu quả hay không liền biết...” Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Kiểm tra một chút, phát hiện đan dược không có độc, yêu diễm nữ tử lúc này mới nuốt xuống, một lát sau, ánh mắt sáng lên, vội vàng hướng trên cổ hoa hồng nhìn sang, đồng thời xé rách quần áo, tiếp tục hướng xuống nhìn lại."),t("br"),t("br"),n._v("Ánh mắt sáng lên, thân thể không ngừng run rẩy."),t("br"),t("br"),n._v("Đối phương nói không sai, thuốc này quả nhiên có hiệu quả, vừa nuốt vào không đến hai phút đồng hồ, quấy nhiễu nàng hơn hai năm mụn nước, vậy mà hoàn toàn biến mất, trước đó khó chịu trong người, cũng hoàn toàn khôi phục!"),t("br"),t("br"),n._v("Thuốc này... Không khỏi quá thần kỳ đi!"),t("br"),t("br"),n._v("“Ngươi...” Yêu diễm nữ tử lần nữa nhìn tới, không còn là trước đó nghi ngờ, mà là nồng đậm khiếp sợ."),t("br"),t("br"),n._v("Từ khi mụn nước sau khi xuất hiện, dùng không biết bao nhiêu dược vật, cũng mời rất nhiều y sư loại hình dò xét, cuối cùng đều không có biện pháp gì, lúc này mới bất đắc dĩ, tung nước hoa, thêu hình xăm che giấu..."),t("br"),t("br"),n._v("Nằm mộng cũng nghĩ không ra, đối phương chỉ nhìn một cái liền xác nhận mấu chốt, càng một viên đan dược liền triệt để chữa khỏi..."),t("br"),t("br"),n._v("Thật là đáng sợ đi!"),t("br"),t("br"),n._v("“Lão đại, da của ngươi... Thật giống so trước đó tốt hơn!”"),t("br"),t("br"),n._v("Một cái trung niên nhân áo đen đi tới, nói."),t("br"),t("br"),n._v("Yêu diễm nữ tử sững sờ, lấy ra một cái kính, vội vàng nhìn sang, vừa nhìn phía dưới, con mắt trợn tròn."),t("br"),t("br"),n._v("Trước đó nhận mụn nước ăn mòn, làn da đã có chút thô ráp, hết cách rồi, chỉ có thể lau bên trên thật dày phấn lót, nằm mơ đều không có nghĩ đến, vừa rồi viên đan dược này ăn vào, không chỉ mụn nước khu trừ, làn da cũng nhẵn bóng không ít, phảng phất trong nháy mắt trẻ bảy, tám tuổi!"),t("br"),t("br"),n._v("Thuốc này... Thần kỳ như vậy?"),t("br"),t("br"),n._v("“Ngươi thuốc này... Còn nữa không?” Cũng nhịn không được nữa, yêu diễm nữ tử nhìn qua."),t("br"),t("br"),n._v("Tuy là trên người nàng mụn nước đã biến mất, nhưng không xác định, có phải hay không còn có thể xuất hiện, ngộ nhỡ lại xuất hiện, không còn thuốc, nên làm cái gì?"),t("br"),t("br"),n._v("Lại nói, nữ nhân, xem mỹ lệ, so sinh mệnh đều quan trọng."),t("br"),n._v("Không nói trị liệu mụn nước, chỉ nói để cho người ta làn da nhẵn bóng, đầu này, liền có thể để vô số người, điên cuồng."),t("br"),t("br"),n._v("“Ta tiêu hao nửa cái mạng, lúc này mới luyện chế thành công một viên, nếu như còn muốn... Cần lại đến mười bình Thần Nguyên đan!” Trương Huyền nói."),t("br"),t("br"),n._v("Đối phương mụn nước, là âm dương không cân đối đưa đến, hắn Thiên đạo chân khí, có thể khơi thông trợ giúp trị liệu, hắn biết rõ... Có thể làn da thay đổi tốt hơn, hơn nữa dung mạo trẻ, chuyện gì xảy ra?"),t("br"),t("br"),n._v("Chẳng lẽ, Thiên đạo chân khí, biến thành “Thiên Nhược Hữu Tình” chân khí về sau, không chỉ sức chiến đấu tăng cường, công hiệu cũng biến thành càng thêm cường đại?"),t("br"),t("br"),n._v("“Phổ thông nữ hài, nhận tình cảm tưới nhuần, cũng sẽ trở nên mặt mày tỏa sáng, dung nhan càng thêm mỹ lệ, biết hay không biết... Ta Thiên Nhược Hữu Tình, tương đương với Thiên đạo chân khí gia nhập tình cảm, đối nữ tử dung mạo, nắm giữ cực lớn hiệu quả?”"),t("br"),t("br"),n._v("Một cái ý nghĩ xông ra."),t("br"),t("br"),n._v("Tình cảm có thể để cho nam nhân càng thêm có lực lượng, làm việc càng thêm có động lực, cũng có thể để nữ nhân, càng thêm mỹ lệ, cũng chính là cái gọi là tình cảm tưới nhuần..."),t("br"),t("br"),n._v("Lĩnh ngộ “Thiên Nhược Hữu Tình” công pháp về sau, Thiên đạo chân khí trở nên càng thêm tinh thuần, vốn cho rằng còn cùng trước đó chân khí đồng dạng, không nghĩ tới còn có mặt khác hiệu quả."),t("br"),t("br"),n._v("“Ngươi chờ một lát...” Yêu diễm nữ tử xoay người đi ra ngoài, thời gian không dài, lần nữa mang đến mười bình Thần Nguyên đan."),t("br"),t("br"),n._v("“Ta hiện tại liền đi luyện chế...” Trương Huyền xoay người đi trở về gian phòng."),t("br"),t("br"),n._v("Đồng dạng qua một phút mới đi ra khỏi đến, vẻ mặt càng trắng hơn, xem ra bất cứ lúc nào cũng sẽ ngã sấp xuống, chết ngay tại chỗ."),t("br"),t("br"),n._v("Nhận lấy đan dược, yêu diễm nữ tử nghiên cứu một lần, thủy chung không có phát hiện cùng phổ thông Thần Nguyên đan có gì khác biệt, lúc này mới xoay người dặn dò một tiếng."),t("br"),t("br"),n._v("Thời gian không dài, một cái trung niên nữ tử đi vào trước mặt."),t("br"),t("br"),n._v("Đồng dạng trung phẩm Thần Linh thực lực, thoạt nhìn khoảng bốn mươi tuổi, trên thực tế, không ít địa phương, đã có da đồi mồi, làn da cũng hiện ra màu xám tro."),t("br"),t("br"),n._v("Chỉ sợ số tuổi thật sự, không thua kém bảy, tám mươi tuổi."),t("br"),t("br"),n._v("Nhìn qua Một Tấc Bụi lưu lại thư tịch, Trương Huyền đối Thần Linh tuổi thọ cũng có nhất định lý giải."),t("br"),t("br"),n._v("Linh khí chưa suy yếu phía trước, bình thường Thần Linh, tuổi thọ có thể đạt tới một trăm năm mươi tuổi trở lên, mà bây giờ, tám mươi chính là đại nạn."),t("br"),t("br"),n._v("Còn Ngụy Thần, có thể sống đến sáu mươi, coi như thọ."),t("br"),t("br"),n._v("Muốn tuổi thọ có đại đột phá, cần xung kích Thiên Thần thành công."),t("br"),t("br"),n._v("Thiên Thần cường giả, tuổi thọ sẽ đạt tới một ngàn năm trái phải."),t("br"),t("br"),n._v("Còn Thần Vương, có một vạn tuổi tuổi thọ."),t("br"),t("br"),n._v("Phong hào Thần Vương, ít nhất có thể sống mười vạn tuổi trở lên..."),t("br"),t("br"),n._v("Đến Đế Quân cảnh giới..."),t("br"),t("br"),n._v("Chín đại Đế Quân, từ khi Thần giới có lịch sử đến nay, vẫn tồn tại, tại trong mắt mọi người, đều là bất tử bất diệt."),t("br"),t("br"),n._v("“Đem viên đan dược này ăn vào!”"),t("br"),t("br"),n._v("Dừng lại một chút, yêu diễm nữ tử nói."),t("br"),t("br"),n._v("Trung niên nữ tử cũng không nhiều lời, há miệng liền đem dược vật nuốt xuống."),t("br"),t("br"),n._v("Một lát sau, dung mạo quả nhiên có thay đổi, da đồi mồi biến mất, trước đó màu xám tro khuôn mặt, lần nữa trở nên mặt mày tỏa sáng, tựa như một cái trẻ mấy chục tuổi, chân chính biến thành hơn ba mươi tuổi hình dáng."),t("br"),t("br"),n._v("“Cái này...” Trung niên nữ tử nhìn mình trong kiếng, tràn đầy khó tin."),t("br"),t("br"),n._v("Thần giới, mặc dù vô số cao thủ, nhưng vẫn không có có thể làm đẹp dược vật, mặc dù có, cũng chỉ là nhẹ thay đổi dung mạo, không có khả năng có như thế biến hóa cực lớn."),t("br"),t("br"),n._v("Vừa rồi viên đan dược kia, quả thực cùng khởi tử hồi sinh không có gì khác biệt."),t("br"),t("br"),n._v("“Quả nhiên có hiệu quả... Ha ha, xem ra, về sau không cần mở lòng đất chợ đen, cũng có kiếm không xong tiền!”"),t("br"),t("br"),n._v("Lần nữa nhìn về phía Trương Huyền, yêu diễm nữ tử tràn đầy vẻ hưng phấn: “Không biết vị tiểu ca này, xưng hô như thế nào?”"),t("br"),t("br"),n._v("Trước đó chỉ là giao dịch, không biết tên cũng không sao, mọi người chẳng mấy chốc sẽ tách ra, cá quay về nước, quên đi chuyện trên bờ, bây giờ thì khác, hoàn toàn là cái túi tiền lớn."),t("br"),t("br"),n._v("“Trương Huyền!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Tại hạ Tề Linh Nhi, ngươi luyện chế loại thuốc này vật, đối làm đẹp có hiệu quả, không bằng... Ngươi ta kết hội bán ra, ngươi phụ trách luyện chế, ta phụ trách bán ra, lấy được lợi nhuận, năm năm phân chia làm sao!” Yêu diễm nữ tử Tề Linh Nhi nói."),t("br"),t("br"),n._v("“Năm năm phân chia? Có thể!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Đối phương phụ trách con đường cùng tiêu thụ, bản thân luyện chế đan dược cũng rất đơn giản, hoàn toàn có thể tiếp nhận."),t("br"),t("br"),n._v("Cùng lắm thì đến lúc đó để Tôn Cường tiếp nhận là được, dù sao hắn làm ăn mạnh hơn chính mình cỡ nào."),t("br"),t("br"),n._v("“Vậy là tốt rồi, ngươi nghĩ biện pháp giúp ta luyện chế mười cái, ta mấy ngày nay đem thứ này tại Dạ Minh thành khai hỏa bảng hiệu!” Mắt sáng lên, Tề Linh Nhi gật đầu."),t("br"),t("br"),n._v("Dựa vào một nữ nhân thân phận, có thể chống lên cái này chợ đen, nhất định có bản thân con đường cùng phương pháp."),t("br"),t("br"),n._v("“Ta cần đủ Thần Nguyên đan mới có thể luyện chế...” Mí mắt vừa nhấc, Trương Huyền nhìn lại."),t("br"),t("br"),n._v("“Tốt, chẳng qua... Thần Nguyên đan có thể coi là tại chi phí bên trong!” Dường như biết mục đích của hắn, Tề Linh Nhi Uyển nhi cười một tiếng: “Thần Nguyên đan một viên, đối ứng một viên thần tệ, mười bình chi phí chính là một trăm thần tệ!”"),t("br"),t("br"),n._v("“Có thể!” Trương Huyền gật đầu đáp ứng."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2130"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2132"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);