(window.webpackJsonp=window.webpackJsonp||[]).push([[1274],{1628:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-đoi-loi-ngan-ngui"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-đoi-loi-ngan-ngui"}},[n._v("#")]),n._v(" Chương: Đôi lời ngắn ngủi")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Cha hậu sự đã xong xuôi, mấy ngày nay chương mới."),t("br"),t("br"),n._v(" Thiên đạo thư viện  Hoành Tảo Thiên Nhai 2326 tự 2019. 06. 26 20:06"),t("br"),t("br"),n._v("Dừng càng hơn một tuần lễ."),t("br"),t("br"),n._v("2019 năm ngày 18 tháng 6 15 giờ 25 phút, cha phát sinh tai nạn ô-tô, cưỡi xe điện bị kéo đất xe đụng vào, xương sọ bị vỡ nát gãy xương, xương sườn gãy mất năm cái, xương hông cùng xương đùi đều có đứt gãy, nhận được tin tức chạy về trong nhà đã 1 9 ngày buổi chiều 1 4 giờ."),t("br"),t("br"),n._v("Trong bệnh viện nhìn thấy trên người hắn cắm hô hấp máy, trên đầu cũng cắm cái ống, toàn bộ mặt đều bị băng gạc bao lấy, toàn thân sưng vù, cũng lại không có chịu đựng, tại chỗ sụp đổ."),t("br"),t("br"),n._v("Ngày thứ hai liên hệ Tế Nam chuyên gia, nói có cơ hội, ngồi xe cứu thương đi qua, chuẩn bị làm giải phẫu, kết quả, trong cơ thể chất điện phân không thăng bằng, Natri, kali ion quá cao, quá thấp, một mực không có giải phẫu điều kiện. Trải qua một đêm cứu chữa, chẳng những không có chuyển biến tốt đẹp, ngược lại càng ngày càng nghiêm trọng, não nhồi máu cũng vượt qua hơn phân nửa, toàn thể cho điểm chỉ có 3, cũng chính là thấp nhất điểm."),t("br"),t("br"),n._v("Bác sĩ nói, không có hi vọng."),t("br"),t("br"),n._v("Mẹ nói, không thể ở bên ngoài, dựa theo quê hương phong tục, cho dù chết, cũng không thể lưu lạc tha hương, về nhà đi."),t("br"),t("br"),n._v("Ngày 21 buổi chiều 15 giờ về đến nhà, đình chỉ hô hấp."),t("br"),t("br"),n._v("Lúc này ta mới biết, nhân sinh ngắn ngủi."),t("br"),t("br"),n._v("Những ngày gần đây, một mực ngơ ngơ ngác ngác, cảm thấy đang nằm mơ, rất muốn lúc nào có thể tỉnh lại, phát hiện tất cả đều là giả, hắn vẫn tại trong phòng ngủ, vẫn như cũ gọi nhũ danh của ta, vẫn như cũ hỏi ta hiện tại trải qua như thế nào... Đáng tiếc, những này, đều thành “Nếu như”."),t("br"),t("br"),n._v("Phụ thân là cái lão sư, hơn ba mươi năm giáo dục, học trò vô số, chưa hề làm qua bất kỳ làm trái đạo nghĩa, làm trái lương tâm sự tình, có thể ông trời liền không thể để hắn lưu thêm một đoạn thời gian, đi nhanh như vậy, như vậy dứt khoát, liền một câu đều không có lưu lại, thậm chí một thanh âm đều không có phát ra, theo xảy ra tai nạn xe cộ đến qua đời, con mắt đều không có mở ra một lần."),t("br"),t("br"),n._v("Sáng tác hơn mười năm, dưới ngòi bút vô số sinh ly tử biệt, cũng không cảm thấy chật vật, ân cần trải qua trải qua trên người mình, cảm giác được là thống khổ như vậy, như vậy bất lực, giống như là toàn bộ thế giới, đều đem ta từ bỏ. Ta biết, từ nay về sau, cha dung mạo cùng thanh âm, chỉ có thể xuất hiện tại trong hồi ức, cũng không còn cách nào nhìn thấy, ta đã triệt để mất đi hắn, thành cái không có cha hài tử."),t("br"),t("br"),n._v("Ta sau khi tốt nghiệp đại học, công việc tại biển xanh, về sau thành gia lập nghiệp, lưu tại địa phương, không có về Sơn Đông quê quán, cùng cha mẹ thời gian chung đụng, mười mấy năm qua, cũng không nhiều."),t("br"),t("br"),n._v("Cũng không phải là không muốn bọn họ, mà là cảm thấy thời gian còn đủ, chỉ cần lại cho ta một đoạn thời gian, liền có thể có được chính mình sự nghiệp, có thể thành Thần, có thể bạch kim, có thể được sống cuộc sống tốt, đem bọn hắn đón về, hoặc là về quê nhà... Vì thế chiến đấu hơn mười năm, ngay tại sắp làm được những này, hảo hảo tận hiếu thời điểm, hắn đi."),t("br"),t("br"),n._v("Những ngày này một mực đang nghĩ, vì sao không để cho ta chút thời gian? Ta thật có thể, dù là lại cho ta một giờ, để cho ta hảo hảo phụng dưỡng một chút, cũng không tính phí công sinh ra đứa con trai này."),t("br"),t("br"),n._v("Có thể sự thật chính là như vậy đột nhiên..."),t("br"),t("br"),n._v("Những năm này, đi làm, viết sách, không thể ngừng có chương mới, cả người làm liên tục không nghỉ suốt ngày đêm, hầu như không có thời gian của mình, làm gì đều muốn tính toán tỉ mỉ, không dám lãng phí một giờ, liều mạng chính là vì có thể cho bọn họ cuộc sống tốt hơn, nhưng có loại điều kiện này, hắn lại không hưởng thụ được."),t("br"),t("br"),n._v("Nghe ca ca nói, tháng trước ta đi Thượng Hải, hắn vẫn phàn nàn, nếu đến nơi này, vì sao không trở về nhà một chuyến?"),t("br"),t("br"),n._v("Ta biết, hắn nhớ ta."),t("br"),t("br"),n._v("Ta không có về, cũng liền không thấy một lần cuối."),t("br"),t("br"),n._v("Một mực cho là có thời gian, hiện tại đã biết rõ, triệt để không còn."),t("br"),t("br"),n._v("Phụ thân là cái điển hình dân quê, cả đời tiết kiệm, sáu tuổi liền mất đi mẹ, hơn hai mươi tuế cha qua đời, một cái nhân sinh sống, cho đến cùng mẹ ta kết hôn. Sinh ra ba cái con trai, theo dân bạn giáo sư, một mực nhịn đến hiện tại, chưa hề qua qua một ngày ngày lành."),t("br"),t("br"),n._v("Năm 2017 con dâu có thai, để hắn cùng mẹ tới chăm sóc, ý là muốn cùng chúng ta chờ lâu một đoạn thời gian, nhưng ta vị trí độ cao so với mặt biển hơn ba ngàn mét, qua không đủ bốn tháng, gầy hơn 10 cân, đi bộ đi không được, tôn tử ra đời tám ngày, liền trở về."),t("br"),n._v(" Trước khi đi dọn dẹp phòng ở, mới phát hiện nhiều một bình nước khoáng đinh ốc, đinh ốc, nói là bình thường đi tản bộ nhặt, ngộ nhỡ có ích, cho dù vô dụng, sắt, cũng có thể bán lấy tiền. Đưa lên máy bay, kiểm an gây khó dễ, một kiểm tra, mới phát hiện trong túi xách để đứt góc cái càng, không có chuôi tua vít, vứt bỏ la bàn... Ta hỏi mang cái này làm cái gì, hắn nói đều là nhặt, trở về còn có thể dùng, ta cười nói, ngươi ý tứ, ta liền thứ này cũng mua không nổi? Mua cho ngươi là được. Hắn nói, còn có thể dùng, mua cái gì? Đừng lãng phí!"),t("br"),t("br"),n._v("Về đến nhà, gọi điện thoại cho ta, vô cùng vui vẻ, nói thủy chung nghĩ mãi mà không rõ, vì sao máy bay như vậy lớn khối sắt, có thể bay lên trời, hơn nữa bay cao như vậy. Có thể đi máy bay ở trong thôn, xem như rất lợi hại, người người đều hâm mộ, rất nhiều người còn chưa tin đây."),t("br"),t("br"),n._v("Cha ưa thích học tập kiến thức mới, ta khi còn bé, trong nhà sách, hầu như đều là 《 mười vạn cái vì sao 》, 《 mặt trăng bí ẩn 》 loại hình, dùng di động, so ta còn tinh thông, vẫn muốn lúc nào học cái máy vi tính, ta nói quay đầu giúp ngươi mua một cái, có liền biết, sẽ giúp ngươi mua cái tốt một chút điện thoại... Nhưng cái này “Quay lại”, cũng lại thực hiện không được nữa."),t("br"),t("br"),n._v("Biết làm thợ mộc, trong nhà không ít ghế, là hắn tự thân làm. Sẽ xây điện tử sản phẩm, ti vi trắng đen thời điểm, là làng xã chung quanh tám thôn nổi danh thợ điện, cơ bản mỗi Thiên Đô có người đưa đồ điện tới xây. Sửa xong, nhiều nhất thu cái linh kiện tiền, không lấy này kiếm sống, cũng không lấy này kiếm tiền, chỉ hạ thấp cái “Người tốt” thanh danh."),t("br"),t("br"),n._v("Một mực muốn cái lão niên nhà, ta nói tại huyện thành mua, nói ở không quen trong thành nhà, muốn ở nông thôn, ta nói vậy thì che, đầu năm nay đưa tiền, tại gia tộc đóng một bộ. Mấy tháng qua, mua tài liệu, tìm người, các loại phí tâm, mỗi lúc trời tối ở tại nơi này, nhìn một viên gạch một viên gạch xây xong, hút thuốc một mặt thỏa mãn. Đầu tháng rốt cục che kín, đồ dùng trong nhà mua tề, cao hứng nói, rốt cục có thuộc về mình ổ, đang chuẩn bị dời đi qua, người liền không có, mới tinh nhà, một ngày đều không có ở."),t("br"),t("br"),t("br"),n._v("Thích xem một ít tạp thư, đối các loại sự kiện thần bí, vô cùng hướng về, ta nói làm cái hộ chiếu a, dẫn ngươi đi kim tự tháp loại hình địa phương đi dạo, lĩnh hội thế giới kỳ quan. Hắn vô cùng vui vẻ, mỗi ngày kiên trì rèn luyện thân thể, nói về sau xuất ngoại thời điểm, không biết tụt lại phía sau, có thể đuổi theo chúng ta, mà bây giờ, hộ chiếu đồng dạng một lần vô dụng."),t("br"),t("br"),n._v("Không thích ghi nợ, cho dù là con trai, trên điện thoại di động ghi chép ta cho hắn mua xe vé, dẫn hắn ra ngoài tiêu xài mỗi một lần chi phí, vé xe lửa, ô tô vé, vé máy bay thời gian, giá cả tất cả đều ghi vào cùng một chỗ, ý định tiền tích lũy đủ rồi, đưa ta. Còn muốn lấy, những năm này lại tiết kiệm tiết kiệm, chờ nhà tiền tích lũy đủ rồi, cũng đưa ta..."),t("br"),t("br"),n._v("Ta không có ý định để hắn trả, nhưng bây giờ nghĩ, có thể trả tốt biết bao nhiêu, chí ít cho thấy còn sống."),t("br"),t("br"),n._v("Theo Tế Nam trên đường trở về, nhìn tính mạng hắn kiểm tra triệu chứng bệnh tật một chút xíu hạ thấp, cuối cùng xu hướng tại không, ta cảm giác bộ óc giống như là muốn nổ tung. Những năm này một mực tại sáng tạo thế giới, vẫn cảm thấy bản thân không gì không làm được, chuyện gì cũng khó khăn không được, mà hiện thực phía trước lại một chút tác dụng đều không có."),t("br"),t("br"),n._v("Ta tưởng tượng, nếu có điều trị chi khí, là hồn sư, có Thiên đạo chi khí, có thể triệu hoán Trương Huyền... Như vậy, liền có thể để hắn không đi, nhưng không làm được!"),t("br"),t("br"),n._v("Đã từng nghĩ, chỉ cần có thể kiếm càng nhiều tiền, thời gian liền sẽ dễ chịu, rất nhiều chuyện cũng có thể tránh khỏi, có thể trải qua rất hạnh phúc, bây giờ lại phát hiện, nhiều khi, tiền là vô dụng."),t("br"),t("br"),n._v("Từng nhỏ hẹp nghĩ, tại sao là ngươi, mà không phải người khác."),t("br"),t("br"),n._v("Suy nghĩ nhiều mời ở lại ngươi, nhưng thủy chung mời ở lại không được."),t("br"),t("br"),n._v("Suy nghĩ nhiều trở lại vài ngày trước, ngăn cản ngươi ra ngoài, liền sẽ không phát sinh loại này bi kịch, nhưng ta không phải tiểu thuyết nhân vật chính, thời gian không cách nào lui về phía sau, tiếc nuối không cách nào bù đắp."),t("br"),t("br"),n._v("Ta chỉ là cái phàm nhân."),t("br"),t("br"),n._v("Hoàn toàn không có tác dụng phàm nhân."),t("br"),t("br"),n._v("Cái gì đều không làm được phế vật."),t("br"),t("br"),n._v("Đối mặt rời đi, chỉ có thể trơ mắt nhìn, không có bất kỳ biện pháp nào."),t("br"),t("br"),n._v("Cảm xúc hỗn loạn, không biết lời nói."),t("br"),t("br"),n._v("Cha tang sự đã xong xuôi, nhập thổ vi an, hai ngày này xử lý một chút, tai nạn giao thông trách nhiệm sự tình, chương mới mấy ngày nay liền sẽ khôi phục."),t("br"),t("br"),n._v("Hoành Tảo Thiên Nhai"),t("br"),t("br"),n._v("2019 năm ngày 26 tháng 6"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2131"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2133"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);