(window.webpackJsonp=window.webpackJsonp||[]).push([[1278],{1632:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2121-lan-giap-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2121-lan-giap-thu"}},[n._v("#")]),n._v(" Chương 2121: Lân Giáp thú")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Hẳn là thắng không nổi...” Trương Huyền xấu hổ."),t("br"),t("br"),n._v("Nắm giữ “Thiên Nhược Hữu Tình” công pháp cùng kiếm chiêu, trung phẩm Thần Linh đỉnh phong, có thể nhẹ nhõm đánh bại, nhưng thượng phẩm Thần Linh, muốn vượt qua liền khó khăn!"),t("br"),t("br"),n._v("Huống chi, càng cường đại hơn thần thú!"),t("br"),t("br"),n._v("Đương nhiên, lần này tới, không phải chém giết đối phương, mà là thu hoạch một ít huyết dịch, chỉ cần gặp được, dựa vào thư viện ưu thế, cùng với ba tấc không nát miệng lưỡi, mê hoặc đối phương, thậm chí nghĩ biện pháp hàng phục, đều là có khả năng làm được!"),t("br"),t("br"),n._v("Hắn thuần thú, cũng không phải một mực dựa vào sức chiến đấu, dựa vào là... Vô thượng nhân cách mị lực!"),t("br"),t("br"),n._v("Mị lực không làm được, vậy chỉ dùng Thần Nguyên đan trao đổi."),t("br"),t("br"),n._v("Dù sao thứ này hiện tại bản thân có rất nhiều, trong sơn cốc không có linh khí, một ít huyết dịch mà thôi, đối phương hẳn là sẽ không từ chối."),t("br"),t("br"),n._v("“Thắng không nổi, cũng dám tới... Lá gan thật là lớn!” Thanh niên lắc đầu, ngay sau đó nhìn qua: “Mặt khác, ta còn có chút kỳ lạ... Ngươi luôn mồm muốn lấy được Lân Giáp thú huyết dịch, nó ở chỗ nào, lại là loại nào hình dáng, ngươi nhưng có biết?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ: “Chỉ cần tìm được lực phòng ngự mạnh mẽ thượng phẩm đỉnh phong Thần Linh cấp bậc thần thú, hẳn là...”"),t("br"),t("br"),n._v("Vị kia người phụ trách, chỉ nói đầu này thần thú, tại đây cái Lạc Vân hạp cốc, vị trí cụ thể, cũng không có nói tỉ mỉ, dáng người trạng thái cũng không có tỉ mỉ miêu tả, vốn muốn, đi vào sau đó, tuỳ ý tìm thần thú thuần phục, liền có thể thăm dò được, kết quả, cái gì đều không có gặp lên."),t("br"),t("br"),n._v("Tự nhiên không hiểu ra sao!"),t("br"),t("br"),n._v("Bất quá, hạp cốc chỉ có một con đường, chỉ cần dọc theo đi, không bỏ sót bất kỳ chỗ nào, liền khẳng định có thể tìm tới."),t("br"),t("br"),n._v("“Gặp được là được... Ngươi không cảm thấy quá trò đùa ư?”"),t("br"),t("br"),n._v("Cười nhạo một tiếng, thanh niên nghiêng dựa vào bên ngoài sơn động một cái nham thạch bên trên, một mặt nghiền ngẫm."),t("br"),t("br"),n._v("“Không có gì, ta cũng thuần qua không ít thú, biết bọn chúng bản tính, chỉ cần gặp được, khẳng định có phương pháp đem hắn thuyết phục!” Trương Huyền khoát tay áo, không nói thêm lời: “Được rồi, nhận được bằng hữu rượu ngon, thịt ngon, ta thật muốn cáo từ, bình minh trước đó, nhất định phải chạy trở về, không có quá nhiều thời gian lưu lại!”"),t("br"),t("br"),n._v("Nói xong, vòng qua đối phương, thẳng tắp hướng trong sơn cốc đi tới."),t("br"),t("br"),n._v("“Nhìn ngươi cái gì cũng đều không hiểu, nếu ngươi đã cứu ta, ngày hôm nay giúp ngươi tìm kiếm Lân Giáp thú!” Thanh niên đứng dậy."),t("br"),t("br"),n._v("“Cái này... Có chút quá phiền toái!” Trương Huyền sửng sốt, cuối cùng lắc đầu: “Tuy là bằng hữu tu vi cực cao, nhưng Lân Giáp thú thân là thần thú, hơn nữa phòng ngự vô địch, nhất định rất khó chống lại, vẫn là không làm phiền ngươi cũng rơi vào nguy hiểm!”"),t("br"),t("br"),n._v("“Không sao, nhàn rỗi cũng là nhàn rỗi, uống chung rượu, ngươi lại cứu ta, giúp ngươi cũng là nên! Lại nói, ngươi cái gì cũng không biết, riêng này dạng tìm, phải tìm đến lúc nào?”"),t("br"),t("br"),n._v("Đem chỉ còn lại nửa hồ lô rượu, đeo trên người, thanh niên hai bước đi vào trước mặt."),t("br"),t("br"),n._v("Đối phương tới gần, Trương Huyền lúc này mới phát hiện, trước mắt người thanh niên này, vậy mà chừng cao hơn hai mét, cao hơn hắn trọn một đầu, thân thể cường tráng, cho người ta cực mạnh lực áp bách."),t("br"),t("br"),n._v("“Nếu bằng hữu ý tốt, vậy ta liền chân thành ghi nhớ... Đương nhiên, ta cũng sẽ không phí công để bằng hữu hỗ trợ, đây là năm cái Thần Nguyên đan, liền xem như thù lao của ta!”"),t("br"),t("br"),n._v("Cong ngón tay búng một cái, Trương Huyền ném qua tới một cái bình ngọc."),t("br"),t("br"),n._v("  Hắn không muốn nợ người ân tình."),t("br"),t("br"),n._v("Đối phương khăng khăng hỗ trợ, giao thù lao là được."),t("br"),t("br"),n._v("Năm cái Thần Nguyên đan, coi như đối phương là thượng phẩm Thần Linh cấp bậc cao thủ, cũng coi là cực cao đánh đổi."),t("br"),t("br"),n._v("“Ồ?” Nhận lấy bình ngọc, thanh niên sửng sốt một chút, tiện tay mở ra, quả nhiên thấy bên trong nằm ngang năm cái Thần Nguyên đan, tiện tay nhặt lên một viên, ngửi một chút, xác nhận là chân chính Thần Nguyên đan, ánh mắt sáng lên: “Đa tạ!”"),t("br"),t("br"),n._v("Nói xong nhét vào một viên đặt ở trong miệng, cảm nhận được trong đó khuấy động linh khí, đem còn lại nhét vào túi, cười khanh khách tiến về phía trước một bước bước ra: “Đi theo ta, ta dẫn ngươi đi tìm Lân Giáp thú!”"),t("br"),t("br"),n._v("Trương Huyền cũng không nhiều lời, theo sát tại sau lưng, hướng trong sơn cốc đi tới."),t("br"),t("br"),n._v("Hạp cốc hình thành đặc thù khu vực, càng hướng phía trong đi, tiếng gió càng mãnh liệt, nghẹn ngào không ngừng bên tai."),t("br"),t("br"),n._v("Hai người một trước một sau, đi tiếp cận nửa canh giờ, thanh niên ngừng lại: “Phía trước chính là Lân Giáp thú sào huyệt...”"),t("br"),t("br"),n._v("Trương Huyền nhìn về phía trước, ngay sau đó nhìn thấy đen như mực trong hạp cốc, một cái to lớn sơn động, xuất hiện tại phía trước."),t("br"),t("br"),n._v("Cửa động chừng hơn mười người cao, bên trong đen thui thâm thúy, thấy không rõ lắm có cái gì."),t("br"),t("br"),n._v("“Đây là?” Trương Huyền mang theo kỳ lạ."),t("br"),t("br"),n._v("Thoạt nhìn cái này Lân Giáp thú, so với trong tưởng tượng còn muốn to lớn, bằng không thì cũng không có khả năng xây dựng như vậy rộng lớn sơn động."),t("br"),t("br"),n._v("“Ừm, muốn bắt lời nói, trực tiếp đi vào là được, ta đi theo phía sau ngươi, nếu quả thật cần hỗ trợ, nói một tiếng là đủ...” Thanh niên nói."),t("br"),t("br"),n._v("“Tốt!” Gật gật đầu, Trương Huyền không nói thêm lời, hít sâu một hơi, thân thể vụt qua, lặng yên không tiếng động hướng xa xa sơn động bay lượn mà đi."),t("br"),t("br"),n._v("Thanh niên theo sát mà lên."),t("br"),t("br"),n._v("Rất mau tới đến cửa động, Trương Huyền Minh Lý Chi Nhãn vận chuyển, nhìn sang."),t("br"),t("br"),n._v("Bên trong tràn đầy đen như mực, dán chặt lấy bức tường, hướng phía trong đi một hồi, sáng tỏ thông suốt, sơn động tận cùng bên trong, bó đuốc ánh sáng chiếu rọi tứ phương."),t("br"),t("br"),n._v("“Đó chính là...”"),t("br"),t("br"),n._v("Thanh niên truyền âm tới."),t("br"),t("br"),n._v("Trương Huyền ngẩng đầu, ngay sau đó nhìn thấy một cái to lớn thần thú, ghé vào trong động trên bệ đá ngủ, thân dài sáu, bảy mét, toàn thân bụi sâu kín lông tóc."),t("br"),t("br"),n._v("“Làm sao cảm giác... Như đầu sói?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Trước mắt đầu này thần thú, mang đến cho hắn một cảm giác, giống như là một đầu to lớn dã lang."),t("br"),t("br"),n._v("Không phải nói Lân Giáp thú, toàn thân photpho giáp phòng ngự vô địch ư?"),t("br"),t("br"),n._v("“Chính là nó, ta thường xuyên đến, chắc chắn sẽ không sai...” Thanh niên gật đầu."),t("br"),t("br"),n._v("“Vậy là tốt rồi...” Hít sâu một hơi, Trương Huyền lông mày giương lên, trường kiếm xuất hiện tại lòng bàn tay, nhẹ nhàng run lên, kiếm mang lập tức bắn đi ra."),t("br"),t("br"),n._v("Còn chưa tới đến đối phương trước mặt, đối diện Lân Giáp thú, giống như là cảm ứng được có người đánh lén, đột nhiên đứng dậy, thô to đuôi quét qua, toàn bộ sơn động lập tức cuồng phong gào thét."),t("br"),t("br"),n._v("“Quả nhiên thượng phẩm Thần Linh cảnh giới!”"),t("br"),n._v("Trương Huyền chợt lóe, tránh thoát công kích, vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Trước mắt đầu này đại gia hỏa, quả nhiên đạt đến thượng phẩm Thần Linh cảnh giới, nhất cử nhất động, đều mang nồng đậm áp lực, để hắn khó mà chống lại."),t("br"),t("br"),n._v("“Tâm Tự Song Ti Võng, Trung Hữu Thiên Thiên Kết!”"),t("br"),t("br"),n._v("Lông mày giương lên, Trương Huyền không chần chờ chút nào, liền đem bản thân mạnh nhất tuyệt chiêu phát huy ra."),t("br"),t("br"),n._v("Trong nháy mắt, kiếm khí khuấy động, một cái lưới đánh cá hình dáng vết kiếm, đối Lân Giáp thú liền lao đến."),t("br"),t("br"),n._v("“Thật là lợi hại kiếm pháp!”"),t("br"),t("br"),n._v("Sau lưng thanh niên sợ hãi thán phục."),t("br"),t("br"),n._v("Một mực cảm thấy đối phương có át chủ bài, nếu không không dám lẻ loi một mình liền đến tìm Lân Giáp thú, nhìn thấy kiếm pháp này mới biết, hắn cứ việc chỉ là hạ phẩm Thần Linh đỉnh phong, chân chính sức chiến đấu, so với một ít thượng phẩm Thần Linh, đều chỉ mạnh không yếu!"),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Bị kiếm mang lưới đánh cá bao phủ, Lân Giáp thú gào lên một tiếng, chỉ có mấy hơi thở, liền bị nó nhẹ nhõm tránh thoát."),t("br"),t("br"),n._v("Đồng thời càng thêm nổi giận lao đến, đối Trương Huyền một móng vuốt vung vẩy mà xuống."),t("br"),t("br"),n._v("Trước thời hạn liền biết chiêu số của nó đồng dạng, Trương Huyền lui về phía sau một bước, quay đầu nhìn về phía sau lưng thanh niên: “Còn xin hỗ trợ ra tay!”"),t("br"),t("br"),n._v("“Tốt!” Thanh niên dường như đã sớm chờ lấy câu nói này, tiến về phía trước một bước bước ra, một quyền đập mà tới."),t("br"),t("br"),n._v("Trương Huyền nhân cơ hội lần nữa thi triển kiếm pháp, đánh lén đối phương điểm yếu."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Nhất thời không kiểm tra, Lân Giáp thú bị thanh niên một quyền đánh trúng phần bụng, bay ngược lấy đâm vào trên tường, đập ra một cái lỗ to."),t("br"),t("br"),n._v("Trương Huyền vừa vặn tấn công, các loại thủ đoạn, toàn bộ thi triển đi ra."),t("br"),t("br"),n._v("Thanh niên cũng nghiêm túc, hai cái cánh tay, giống như là biến thành tám cái, tốc độ cực nhanh, hơn nữa, thân thể của hắn vô cùng cứng rắn, liên tục cứng rắn chịu Lân Giáp thú hai lần công kích, vậy mà đều mảy may thương thế đều không có."),t("br"),t("br"),n._v("Mười mấy hô hấp sau đó, Lân Giáp thú đã không địch lại, vẩy xuống một bãi máu tươi về sau, bỗng nhiên lao ra sơn động, thời gian nháy mắt liền biến mất trong đêm tối, cũng tìm không được nữa tung tích."),t("br"),t("br"),n._v("“Quá tốt rồi...”"),t("br"),t("br"),n._v("Thấy nó lưu lại huyết dịch, lần này không tính đến không, Trương Huyền thở phào nhẹ nhõm, tìm cái bình chứa lên."),t("br"),t("br"),n._v("“Đa tạ bằng hữu hỗ trợ...” Sắp xếp gọn đồ vật, nhìn về phía thanh niên trước mắt, Trương Huyền chắp tay."),t("br"),t("br"),n._v("“Không cần cám ơn, nếu là hỗ trợ, tự nhiên muốn giao đánh đổi, vừa mới Thần Nguyên đan, lại cho ta năm mươi viên! Chúng ta liền thanh toán xong...”"),t("br"),t("br"),n._v("Thanh niên bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Năm mươi viên?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Thần Nguyên đan giá cả cực cao, hơn nữa có tiền mà không mua được, vô cùng trân quý, vừa mở miệng năm mươi viên, không khỏi sư tử mở miệng đi!"),t("br"),t("br"),n._v("Vừa mới thoạt nhìn rất thẳng thắn, không nghĩ tới, đột nhiên đổi sắc mặt."),t("br"),t("br"),n._v("“Không sai, nếu như ta không ra tay, ngươi khẳng định sẽ bị giết, năm mươi viên Thần Nguyên đan mua mình một mạng, không đắt lắm đi...” Thanh niên nhìn qua."),t("br"),t("br"),n._v("“Ta chỉ là cái phổ thông tu luyện giả, sao có thể có nhiều như vậy đan dược, như vậy đi, ta đem trên người tất cả đan dược, hết thảy mười cái, toàn bộ cho ngươi...” Dừng lại một chút, Trương Huyền nói."),t("br"),t("br"),n._v("“Không có? Để cho ta tìm kiếm liền biết!” Bàn tay lớn vồ một cái, thanh niên thần lực từ ngón tay thả ra ngoài, đem bốn phía đường lui, toàn bộ phong tỏa."),t("br"),t("br"),n._v("“Ngươi... Muốn làm gì?” Cảm thụ thân thể bị giam cầm, Trương Huyền sắc mặt trắng nhợt, vội vàng quát tháo."),t("br"),t("br"),n._v("“Không làm gì, ngươi tới giết ta, ta đem ngươi giết chết, cướp đoạt trên người ngươi đồ vật... Không tính quá phận đi!”"),t("br"),t("br"),n._v("Thanh niên cười hắc hắc."),t("br"),t("br"),n._v("“Giết ngươi? Lời này bắt đầu nói từ đâu? Ta vừa mới thế nhưng là cứu tính mạng của ngươi...”"),t("br"),t("br"),n._v("“Cứu ta? Ha ha!”"),t("br"),t("br"),n._v("Thanh niên nói: “Ngươi lần này tới không phải là vì giết Lân Giáp thú ư?”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ta chính là... Lân Giáp thú!” Thanh niên hừ một tiếng, thân thể đột nhiên xuất hiện biến hóa, sau một khắc, một đầu tràn đầy photpho giáp to lớn thần thú xuất hiện tại phía trước."),t("br"),t("br"),n._v("“Ngươi là Lân Giáp thú?” Trương Huyền sững sờ, con mắt trợn tròn: “Ngươi đã biến hóa?”"),t("br"),t("br"),n._v("Thần thú có thể biến hóa, đi vào Thần giới liền biết, chỉ là không nghĩ tới, cùng nhân loại giống nhau như đúc, chính mình cũng không có phân biệt ra được."),t("br"),t("br"),n._v("“Không sai, ngươi muốn giết ta, ta đem ngươi giết... Rất công bằng!” Lân Giáp thú cười hắc hắc."),t("br"),t("br"),n._v("“Quả thực rất công bằng!” Trương Huyền gật gật đầu: “Chẳng qua... Ngươi cảm thấy cái này hơn nửa đêm nhìn thấy một thân một mình uống rượu, phòng ngự lại mạnh như vậy, ta thật một điểm đề phòng đều không có?”"),t("br"),t("br"),n._v("“Thực lực của ngươi cùng át chủ bài, ta đều thấy được, làm sao còn có những hậu thủ khác?” Lân Giáp thú sững sờ."),t("br"),t("br"),n._v("Mang đối phương tới đây, tìm vừa mới đầu kia thần thú, chính là muốn thử xem đến cùng có bao nhiêu cân, nhìn thấy liền dựa dẫm một bộ kiếm pháp, bản thân không có quá mạnh thực lực, cũng không có quá lợi hại pháp bảo, lúc này mới thở phào nhẹ nhõm, trực tiếp ra tay."),t("br"),t("br"),n._v("“Ta quả thực không cách nào vượt qua ngươi, nhưng... Thực sự xấu hổ, ngươi vừa mới ăn, không nên ăn đồ vật...”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, Trương Huyền bấm đốt ngón tay nhẹ nhàng bắn ra."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Đang muốn tấn công Lân Giáp thú, đột nhiên con mắt một chút trợn tròn, trong bụng kịch liệt đau đớn, trên đầu mồ hôi lạnh trong nháy mắt xông ra, thân thể khổng lồ, không ngừng run rẩy."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2135"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2137"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);