(window.webpackJsonp=window.webpackJsonp||[]).push([[1279],{1633:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2122-thuan-phuc-lan-giap-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2122-thuan-phuc-lan-giap-thu"}},[n._v("#")]),n._v(" Chương 2122: Thuần phục Lân Giáp thú")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Ngươi Thần Nguyên đan có độc?” Thân thể cứng ngắc, Lân Giáp thú toàn thân xiết chặt."),t("br"),t("br"),n._v("Từ khi cùng đối phương gặp mặt, chỉ ăn qua đối phương cấp cho Thần Nguyên đan một viên, lúc ấy cẩn thận kiểm tra, không có bất kỳ cái gì đặc thù, mới ăn vào, chẳng lẽ trong này ẩn giấu đồ vật?"),t("br"),t("br"),n._v("Chỉ là..."),t("br"),t("br"),n._v("Đến cùng cái gì độc, có thể để cho nó trong cõi u minh cảm nhận được giữa thiên địa áp bức, một điểm sức phản kháng đều không có?"),t("br"),t("br"),n._v("“Coi như ngươi thông minh!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Làm ngang dọc danh sư đại lục, Thượng Thương cường giả đỉnh cao, kinh nghiệm giang hồ, tuyệt đối được cho lão quái vật cấp bậc. Hơn nửa đêm toát ra một cái uống rượu thanh niên, thực lực lại cao như vậy, làm sao có thể, tùy ý tin tưởng?"),t("br"),t("br"),n._v("Cho nên, trước thời hạn làm chuẩn bị, cấp cho đối phương Thần Nguyên đan bên trong, ẩn giấu mấy đạo chân khí!"),t("br"),t("br"),n._v("Thật sự là hảo tâm, không có ác ý, những này chân khí, chính là trợ lực, có thể trợ giúp khơi thông kinh mạch, cải thiện làn da, thậm chí mỹ nhan... Có mặt khác để tâm, xấu hổ, những này đại bổ chân khí, sẽ thời gian nháy mắt thuế biến thành kịch độc chi khí, mang theo người Thiên đạo cảm giác áp bách, chui vào thân thể rất hết sức nhỏ chỗ, mạnh hơn người, đều không thể ngăn cản."),t("br"),t("br"),n._v("“Rất tốt... Bất quá, ngươi cũng đừng cao hứng quá sớm, ngươi vừa mới ăn thịt dê và rượu ngon bên trong, cũng có ta hạ độc, cùng lắm thì, mọi người trao đổi giải dược...” Lân Giáp thú nghiến răng."),t("br"),t("br"),n._v("“Trong rượu có độc?” Trương Huyền lắc đầu: “Ngươi cảm thấy lúc này nói loại lời này, ta sẽ tin tưởng?”"),t("br"),t("br"),n._v("Có độc không độc, thân là độc sư, vẫn là có thể tuỳ tiện phát giác, đối phương rõ ràng cố ý cố tình bày nghi trận, muốn cho bản thân cũng rối tung lên."),t("br"),t("br"),n._v("“Rượu ngon của ta, là không có độc tố, nhưng mà dùng đặc thù trái cây ủ chế mà thành, chỉ thích hợp ta Lân Giáp thú dùng, cái khác sinh mệnh dùng, trong thời gian ngắn không có quá lớn biến cố, nhưng vượt qua nửa ngày, liền sẽ bên ngoài thân mọc đầy lân phiến, toàn thân ngứa, sống không bằng chết...” Lân Giáp thú nói: “Chỉ cần ngươi cho ta giải dược, ta cũng dạy ngươi phương pháp giải quyết, hơn nữa sẽ để cho ngươi rời khỏi, sẽ không tìm phiền phức...”"),t("br"),t("br"),n._v("“Không cần!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Ngươi không tin?”"),t("br"),t("br"),n._v("“Không phải, lời của ngươi nói, ta tin tưởng, chẳng qua... May mắn ngươi nhắc nhở một chút, vừa mới đem ngươi hạ độc, giải!” Trương Huyền nói."),t("br"),t("br"),n._v("Lân Giáp thú sững sờ, tràn đầy khó tin: “Ngươi nói bậy...”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười: “Rượu ngon của ngươi, là dùng hạp cốc đặc thù  ủ chế mà thành, loại trái cây này, chỉ có nơi này có, hơn nữa là ngươi lân giáp duy trì mạnh mẽ, tốt nhất bổ sung chi vật, nguyên nhân chính là như vậy, ngươi mới không cách nào rời đi nơi này, bởi vì ngươi biết, mất đi Tử Chân quả... Nếu nói phòng ngự vô địch, sẽ biến thành chê cười!”"),t("br"),t("br"),n._v("“Ngươi...” Lân Giáp thú sững sờ."),t("br"),t("br"),n._v("Kẻ trước mắt này, là quái vật ư?"),t("br"),t("br"),n._v("Làm sao có thể liền loại này chuyện bí ẩn, đều biết?"),t("br"),t("br"),n._v("Dạ Minh thành thành chủ, tới thuần phục qua nó, cho ra điều kiện, vô cùng phong phú! Đổi lại trước kia, khẳng định cân nhắc cũng sẽ không cân nhắc, liền trực tiếp từ chối, nhưng bây giờ Thần giới linh khí suy yếu, một mực đợi ở chỗ này, sẽ chỉ thực lực càng ngày càng yếu."),t("br"),t("br"),n._v("Có thể... Nó vẫn như cũ không có đáp ứng, cũng bởi vì hạt giống này thật quả!"),t("br"),t("br"),n._v("Loại trái cây này, là nó phòng ngự trở nên cường đại nguyên nhân trọng yếu nhất, chỉ cần một mực dùng, sẽ trở nên mạnh hơn, mà một khi ngừng lại, dần dần suy yếu, cái gọi là ưu thế cũng liền không thấy."),t("br"),t("br"),n._v("Hơn nữa, thứ này, dùng máu của nó mới có thể bồi dưỡng, rời khỏi Lạc Vân sơn cốc liền không cách nào sống sót... Cho nên, nó cũng chỉ có thể thủ tại chỗ này, không cách nào rời khỏi."),t("br"),t("br"),n._v("Đây là bí mật lớn nhất, tuyệt không thể để cho người ta biết, bởi vậy... Bất luận người nào tới muốn thuần phục, đều một tiếng cự tuyệt, thậm chí liều đi tính mạng, cũng muốn đem hắn đuổi đi."),t("br"),t("br"),n._v("Đối phương... Mở miệng nói ra!"),t("br"),t("br"),n._v("Đến cùng làm thế nào biết?"),t("br"),t("br"),n._v("“Ngươi đem Tử Chân quả ủ chế thành rượu ngon, thứ nhất, có thể che giấu tai mắt người, để cho người ta phân biệt không ra, thứ hai, thường xuyên dùng, có thể duy trì thân thể ngươi phòng ngự cường độ... Bởi vì dùng dòng máu của ngươi bồi dưỡng, người bình thường, quả thực không cách nào dùng, một khi uống, là sẽ xuất hiện thân thể khó chịu tình huống, nhưng... Với ta mà nói, không tính là gì!” Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Trên thế giới độc nhất đồ vật, trên trời đạo chân chọc tức phía trước, cũng không tính là cái gì, huống chi một ít trái cây ủ thành rượu ngon."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Giải thích xong, Trương Huyền bàn tay lớn vẫy một cái, ngồi tại cách đó không xa vừa mới đầu kia thần thú ngủ vị trí, vẻ mặt lạnh nhạt nhìn qua: “Cho ngươi hai con đường lựa chọn, đệ nhất... Cho ta một trăm viên Tử Chân quả, cùng hai ngươi thăng máu tươi! Đương nhiên, ta cũng không hỏi không ngươi muốn, hai mươi viên Thần Nguyên đan! Yên tâm, lần này bên trong không độc.”"),t("br"),t("br"),n._v("“Thứ hai, độc phát tử vong! Ta hạ độc, có thể bảo đảm không người có thể giải, coi như ngươi tìm tới U Hồn thiên, tìm tới Càn Khôn Đế Quân, cũng khẳng định không có bất kỳ biện pháp nào!”"),t("br"),t("br"),n._v("“Ta giết ngươi...”"),t("br"),t("br"),n._v("Lân Giáp thú gào lên một tiếng, liền muốn đối trước mắt thanh niên công kích, phát hiện thân thể xiết chặt, đối phương ở trong cơ thể nó hạ độc, vậy mà một cái nháy mắt liền khóa lại kinh mạch, toàn thân thực lực, một chút cũng thả ra không đi ra."),t("br"),t("br"),n._v("“Ngươi đây là cái gì độc?” Đồng tử co rút lại, Lân Giáp thú run rẩy."),t("br"),t("br"),n._v("Không biết bao nhiêu cường giả muốn thuần phục nó, trong đó không thiếu hạ độc, bởi vậy, đối các loại độc cũng biết qua một ít, dựa vào mạnh mẽ thân thể cùng phòng ngự, có thể che đậy phần lớn độc dược, nhưng đối phương... Lại không hề có một chút năng lực phản kháng nào!"),t("br"),t("br"),n._v("“Ngươi không cần biết, hai con đường cho ngươi lựa chọn, lựa chọn đầu thứ hai, hạ độc chết ngươi sau đó, ta vẫn như cũ có thể thu thập huyết dịch cùng Tử Chân quả, thậm chí không cần tiêu phí một phân một hào!” Trương Huyền nói."),t("br"),t("br"),n._v("Biết hắn thực sự nói thật, một khi chết rồi, thứ gì đều là đối phương, Lân Giáp thú vẻ mặt một hồi âm một hồi quang: “Nhưng có con đường thứ ba?”"),t("br"),t("br"),n._v("“Có!” Hai tay chắp sau lưng, Trương Huyền đứng dậy: “Trở thành ta thú sủng, ta cần dùng huyết dịch lúc, tùy thời cung cấp, ta có thể để cho ngươi cách Lạc Vân sơn cốc đồng dạng có thể duy trì hiện tại lực phòng ngự, thậm chí... Mạnh hơn! Hơn nữa, còn có thể giúp ngươi, đột phá ràng buộc, đi đến Thiên Thần cảnh!”"),t("br"),t("br"),n._v("“Giúp ta rời đi nơi này, đột phá đến Thiên Thần cảnh?”"),t("br"),t("br"),n._v("Lân Giáp thú lắc đầu: “Điều đó không có khả năng...”"),t("br"),t("br"),n._v("Nó đã biến hóa, có thể đi vào thành trì sinh hoạt, hưởng thụ xa hoa truỵ lạc, xa hoa đồi truỵ, chắc chắn sẽ không từ chối, có thể... Hiện thực không cách nào hoàn thành! Cũng từng lẫn vào Dạ Minh thành, tìm kiếm qua không người tìm hiểu, đáng tiếc đều bất lực."),t("br"),n._v("Trước mắt cái này chừng hai mươi tuổi gia hỏa, có thể làm được?"),t("br"),t("br"),n._v("Hơn nữa giúp nó đột phá?"),t("br"),t("br"),n._v("Thiên Thần cảnh, dễ dàng như vậy đột phá, Dạ Minh thành cũng không có khả năng chỉ có thành chủ một người đi đến loại thực lực này!"),t("br"),t("br"),n._v("“Không có gì không thể nào!”"),t("br"),t("br"),n._v("Trương Huyền đầu lâu có chút nâng lên, ánh mắt mang theo thâm thúy: “Tử Chân quả, có thể để ngươi phòng ngự mạnh lên, nhưng chính ngươi cũng rõ ràng, tác dụng phụ không nhỏ, cùng ngươi mới vừa nói đồng dạng, toàn thân ngứa, sống không bằng chết! Nhất định phải không ngừng dùng Tử Chân quả, mới có thể giảm bớt, như là thuốc lá đồng dạng, không cách nào từ bỏ.”"),t("br"),t("br"),n._v("Nhìn qua thư tịch, biết Thần giới cũng có thuốc lá, cơ bản hút người, rất khó từ bỏ."),t("br"),t("br"),n._v("“Ngươi coi như biết, lại có gì loại phương pháp?” Lân Giáp thú nói."),t("br"),t("br"),n._v("Điểm ấy nó làm sao không biết, có thể thì có biện pháp gì?"),t("br"),t("br"),n._v("Nếu như có thể từ bỏ, đã sớm không ở nơi này đợi."),t("br"),t("br"),n._v("“Thiên đạo vạn vật, đều là hỗ trợ lẫn nhau, lẫn nhau cân bằng, rắn độc ẩn hiện chỗ, bảy bước bên trong tất có giải dược. Nói chính là cái này ý tứ. Tử Chân quả, có tác dụng phụ, tự nhiên cũng liền có giải quyết nó đồ vật tồn tại... Còn để ngươi đột phá Thiên Thần cảnh, chính là dựa dẫm thứ này, không có đoán sai, ngươi nhiều năm dùng Tử Chân quả, đã bị loại thuốc này vật lực lượng tê liệt, trong cơ thể kinh mạch, không giống trước đó như thế nhạy bén đi!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Ngươi làm sao...” Vừa định hỏi thăm, ngươi làm sao biết được, thấy mình bí mật thật giống ở trước mặt đối phương trong suốt đồng dạng, cuối cùng ngừng lại, Lân Giáp thú gật gật đầu: “Là!”"),t("br"),t("br"),n._v("Dùng lâu dài Tử Chân quả, trong cơ thể nó kinh mạch, quả thực không giống trước đó như vậy nhạy bén, chân khí lưu thông, cũng không dễ dàng như vậy khống chế."),t("br"),t("br"),n._v("Cái này cùng chuỳ sắt đồng dạng, nếu muốn cho nó có sức mạnh, liền không thể lại hy vọng xa vời đối phương nhạy bén."),t("br"),t("br"),n._v("Phòng ngự vô địch, đối chân khí nhỏ bé lực khống chế, tự nhiên muốn thoái hóa, trên thế giới vốn cũng không có mười phân vẹn mười đồ vật."),t("br"),t("br"),n._v("“Nếu để cho ngươi đối chân khí khống chế, lần nữa khôi phục nhạy bén, có phải hay không liền có thể đột phá đến Thiên Thần cảnh?”"),t("br"),t("br"),n._v("Trương Huyền cười nói."),t("br"),t("br"),n._v("“Đương nhiên!” Lân Giáp thú gật đầu."),t("br"),t("br"),n._v("Đối chân khí khống chế lần nữa nhạy bén, dựa vào nó tích lũy nhiều năm như vậy, xung kích cảnh giới kia, cũng không khó."),t("br"),t("br"),n._v("“Vậy thì rất đơn giản...” Trương Huyền lần nữa cong ngón tay búng một cái."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Đối phương trong cơ thể đạo kia, để hắn đau đớn không ngớt, không cách nào thi triển lực lượng chân khí, sau một khắc biến thành vật đại bổ, ở trong kinh mạch cấp tốc chảy xuôi."),t("br"),t("br"),n._v("Trước đó bởi vì phòng ngự mạnh lên, đã biến cứng nhắc kinh mạch, một cái nháy mắt, trở nên mềm mại lên, tắc nghẽn địa phương, cũng không trở ngại."),t("br"),t("br"),n._v("“Cái này...” Lân Giáp thú trợn mắt lên, tràn đầy khó tin."),t("br"),t("br"),n._v("Kịch độc tại đối phương một ý niệm, biến thành vật đại bổ... Hơn nữa hiệu quả mạnh mẽ như vậy, làm sao làm được?"),t("br"),t("br"),n._v("Kinh mạch biến mềm, trong cơ thể lực lượng rục rà rục rịch, thật giống bất cứ lúc nào cũng sẽ đột phá đến Thiên Thần cảnh, ngay tại nó muốn thừa thế xông lên, trực tiếp thành công thời điểm, đạo kia chân khí ngừng lại."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Kinh mạch toàn thân lần nữa cứng ngắc, bất cứ lúc nào cũng sẽ đột phá xúc động, lại như cùng phong ấn đồng dạng, lại không cách nào tiến lên."),t("br"),t("br"),n._v("Trong nháy mắt từ trên trời rơi xuống phàm trần, mạnh mẽ cảm giác mất mát, để nó sắp bùng nổ."),t("br"),t("br"),n._v("Trương Huyền cười một tiếng, nói: “Trong cơ thể ngươi cỗ lực lượng này, quá mức nhỏ yếu, chỉ có thể giúp ngươi duy trì một đoạn thời gian, nhưng liền đoạn thời gian này, ngươi hẳn là rõ ràng, ta mới vừa nói là thật, không có đánh lừa, lựa chọn ra sao, liền nhìn ngươi!”"),t("br"),t("br"),n._v("Thiên đạo chân khí có thể làm cho nó cứng rắn kinh mạch, trở nên mềm mại, nhưng cũng chỉ là duy trì một đoạn thời gian mà thôi, không có khả năng kiên trì quá lâu. Lại nói đối phương còn chưa làm ra lựa chọn, không có khả năng liền như thế giúp nó đột phá."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Lân Giáp thú trầm mặc xuống."),t("br"),t("br"),n._v("Nếu như nói trước đó còn có điều hoài nghi lời nói, đối phương lực lượng ở trong người chạy thời điểm, cũng đã xác định, trước mắt vị này, quả thực có thể giúp nó giải quyết Tử Chân quả lưu lại tai hoạ ngầm, thậm chí để nó đột phá!"),t("br"),t("br"),n._v("Đã như vậy, ba con đường, cái kia lựa chọn cái nào một cái... Ngu nữa cũng rõ ràng."),t("br"),t("br"),n._v("“Ta nguyện ý trở thành ngươi thú sủng, không rời không bỏ, đến chết không hối hận...”"),t("br"),t("br"),n._v("Cắn răng một cái, Lân Giáp thú bái phục xuống, tinh thần khẽ động, một giọt tinh huyết bay ra."),t("br"),t("br"),n._v("“Rất tốt!”"),t("br"),t("br"),n._v("Thở phào nhẹ nhõm, Trương Huyền mỉm cười, ngón tay một điểm, đem trong máu ẩn chứa linh hồn ấn ký thu vào mi tâm, thuận lợi đem hắn thuần phục."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2136"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2138"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);