(window.webpackJsonp=window.webpackJsonp||[]).push([[128],{481:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1091-truyen-the-đoc-đien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1091-truyen-the-đoc-đien"}},[n._v("#")]),n._v(" Chương 1091: Truyền thế độc điển")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Độc điện tổng bộ, tọa lạc tại một cái cực lớn bên trong dãy núi, không hề giống những phân bộ khác đồng dạng, có trận pháp bao phủ, mà là trực tiếp trần trụi ở bên ngoài, là một tòa bao trùm lên ngàn cây số cự đại thành thị."),t("br"),t("br"),n._v("Đủ loại vật tư, tự sản tự tiêu, như là một cái tiểu vương quốc."),t("br"),t("br"),n._v("“Sơn mạch này lan tràn mấy vạn cây số, nghe nói đều bị hạ kịch độc, không phải Độc điện độc sư đi vào, sẽ trực tiếp độc chết, bởi vậy, tổng bộ căn bản không cần ẩn tàng...”"),t("br"),t("br"),n._v("Nhìn trước mắt nguy nga hùng vĩ thành thị, Bạch Đình nhịn không được nói."),t("br"),t("br"),n._v("Độc điện tại bất luận cái gì địa phương, đều là người người e ngại tồn tại, vì an toàn, không thể không dùng trận pháp ẩn tàng, để những nghề nghiệp khác người khó mà tìm kiếm."),t("br"),t("br"),n._v("Nhưng tổng bộ khác biệt, trực tiếp xây dựng ở trong dãy núi, cái gì đều không có che lấp, có điều, mặc dù không có trận pháp, toàn bộ sơn mạch, nuôi dưỡng vô số độc trùng, độc thú, cho dù có người muốn tới đây tìm phiền toái, cũng sẽ trả giá cực lớn đánh đổi."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, cho dù rất nhiều người biết tổng bộ ở đây, cũng không ai dám dễ dàng động thủ, ngay cả Danh Sư đường đều muốn kiêng kị ba phần."),t("br"),t("br"),n._v("“Cũng không biết lúc nào, độc sư có thể không cần e ngại người thế tục ánh mắt, cùng những nghề nghiệp khác đồng dạng, đi lại tại ánh sáng bên trong...”"),t("br"),t("br"),n._v("Cười khổ lắc đầu, Hải Minh Kim cảm khái."),t("br"),t("br"),n._v("Tổng bộ cho dù không cần che lấp, cũng là giấu ở trong núi sâu, cùng những nghề nghiệp khác tại mỗi bên lớn phồn hoa nơi chốn xây dựng phân bộ tình huống, hoàn toàn khác biệt."),t("br"),t("br"),n._v("Độc sư, sớm đã tại người suy nghĩ bên trong lưu lại âm lãnh quỷ quyệt hình ảnh, cấp bậc càng cao càng làm người ta sợ hãi, loại này quan điểm, không phải dễ dàng liền có thể thay đổi."),t("br"),t("br"),n._v("“Ta cũng hi vọng có một ngày như vậy... Bất quá, cũng nhanh!” Quay đầu nhìn một cái sau lưng dùng chân khí kéo lên nữ hài, Bạch Đình mỉm cười."),t("br"),t("br"),n._v("Theo Tĩnh Viễn thành rời khỏi, liên tục phi hành tiếp cận hai tháng, cô gái này từ đầu đến cuối không có tỉnh táo dấu hiệu, hơn nữa, trong cơ thể toả ra khí độc càng ngày càng nồng đậm."),t("br"),t("br"),n._v("Nếu không phải nàng lúc thanh tỉnh, hóa thân Tôn Cường, cho mình hai người giải độc đan dược, chỉ sợ hiện tại liền xem như Thất Tinh đỉnh phong thực lực, hấp thu nhiều như vậy khí độc, độc phát thân vong."),t("br"),t("br"),n._v("Bị động toả ra khí tức, liền có thể để bọn hắn loại cấp bậc này độc sư đều không chịu nổi, Tiên Thiên độc thể, thật là đáng sợ."),t("br"),t("br"),n._v("Thể chất là Tiên Thiên, không có cách nào sửa đổi, cường đại hơn là, vị này khai phái tổ sư đối dùng độc lý giải, càng là nghe rợn cả người."),t("br"),t("br"),n._v("Hai người trên đường đi, một mực nghiên cứu tổ sư cho bọn hắn trong đầu truyền lại dùng độc kiến thức, càng nghiên cứu, càng cảm thấy đáng sợ, có thể nói hai tháng này lý giải, vượt qua bọn họ trước đó mấy chục năm học tập! Nhất là ban cho những cái kia thuốc giải độc hoàn, quả thực có thể nói là bách độc bất xâm giải độc Thánh dược để hình dung."),t("br"),t("br"),n._v("Không chỉ có thể đủ hóa giải trên người nàng toả ra khí độc, ngay cả sơn mạch này bên trong tất cả độc trùng độc vật, toàn bộ có thể khắc chế."),t("br"),t("br"),n._v("Trước mấy ngày Bạch Đình độc sư, không cẩn thận bị một cái con rắn độc cắn bị thương, vốn cho rằng không cứu, phục dụng viên kia giải độc đan dược về sau, thời gian không dài liền khôi phục như lúc ban đầu, một loại bình thường đan dược có thể hiểu bách độc, liền xem như tự mình sử dụng, đều cảm thấy không thể tưởng tượng nổi."),t("br"),t("br"),n._v("Nếu không phải những này giải độc đan dược, khả năng còn chưa đi đến nơi đây, liền không biết chết bao nhiêu lần."),t("br"),t("br"),n._v("“Chỉ cần tổ sư tỉnh táo lại, lần nữa khôi phục đỉnh phong thực lực, để những nghề nghiệp khác thừa nhận chúng ta cũng không phải việc khó, chỉ tiếc Độc điện cường giả thực sự quá nhiều, ai cũng không phục ai, tất cả mọi người mạnh ai nấy làm! Đến mức chia rẽ, lúc này mới không còn trước đó rầm rộ, tại cái khác nghề nghiệp chèn ép xuống, co đầu rút cổ một góc.”"),t("br"),t("br"),n._v("Nhớ tới những năm này, Độc điện trải qua, Hải Minh Kim cảm khái."),t("br"),t("br"),n._v("Thật ra thì Độc điện vừa bắt đầu, cũng không phải là như bây giờ, để những nghề nghiệp khác kiêng kị, mà là giống như những người khác, sinh hoạt tại ánh sáng bên trong, không cần trốn trốn tránh tránh."),t("br"),t("br"),n._v("Sở dĩ xuất hiện hiện tại loại cục diện này, là bởi vì tổ sư đi về cõi tiên về sau, chưa từng xuất hiện một vị chân chính siêu tuyệt thực lực độc sư."),t("br"),t("br"),n._v("Dùng độc cường giả, ai cũng không phục ai, có đôi khi tại không có quy củ dưới tình huống, còn lẫn nhau tàn sát, lại thêm chiêu thu đệ tử không có một cái nào thống nhất tiêu chuẩn, từ đó vàng thau lẫn lộn, lúc này mới dẫn đến thực lực tổng hợp ngày càng sa sút, biến thành hiện tại bộ này cục diện."),t("br"),t("br"),n._v("Nếu như có thể có người lần nữa nhất thống Độc điện, làm cho tất cả mọi người toàn bộ thần phục, có lẽ liền có thể thay đổi cục diện bây giờ, lần nữa đạt tới năm đó thịnh thế."),t("br"),t("br"),n._v("“Trước không nên cao hứng quá sớm, hiện tại Độc điện đã cùng trước kia không đồng dạng... Ta trước khi tới thông tri lão sư, cũng không nói tổ sư chuyện, dù vậy, lão sư vẫn là để ta cẩn thận một chút, lặng lẽ tới, không nên bị những người khác phát hiện. Hình như hiện tại, toàn bộ tổng bộ, đủ loại tranh đấu mười phần hỗn loạn, liền xem như hắn, cũng không có cách nào.” Bạch Đình độc sư nói."),t("br"),t("br"),n._v("“Ngươi lão sư? Liền là vị kia mệnh danh Vạn Độc Tôn Giả Lâm Giang Hải?” Hải Minh Kim tò mò."),t("br"),t("br"),n._v("Vị này Bạch Đình mặc dù cùng hắn là bạn, hơn nữa theo cùng một cái Độc điện đi ra, nhưng xưa nay chưa nói qua bản thân truyền thừa chuyện, hắn cũng là căn cứ dùng độc thủ pháp đoán được một chút."),t("br"),t("br"),n._v("“Không sai, lão sư ta chính là Lâm Giang Hải độc sư, từng tại bọn họ bên dưới học 30 năm, đáng tiếc bởi vì tư chất không đủ, không được đến chân truyền, nếu không, chỉ sợ hiện tại cũng đạt đến bát tinh cấp bậc.” Bạch Đình gật đầu."),t("br"),t("br"),n._v("Nói đến lão sư, hắn tràn đầy tôn kính."),t("br"),t("br"),n._v("Sư ân như biển."),t("br"),t("br"),n._v("Không có lão sư, liền không có hắn hôm nay, dù là cũng không phải là thân truyền, sư ân cũng không được xóa đi."),t("br"),t("br"),n._v("“Độc điện tổng bộ có hai vị hộ pháp, tứ đại Tôn giả, mười hai Độc Vương, bảy mươi hai cái nhất đẳng phân điện điện chủ, những cường giả này, kém cỏi nhất đều đạt đến bát tinh đỉnh phong. Vạn Độc Tôn Giả tại tứ đại Tôn giả bên trong, mặc dù không xếp hạng tới đệ nhất nhưng cũng không phải cuối cùng, hắn thân truyền đệ tử, thực lực kém cỏi nhất đều đạt đến bát tinh, có thể trở thành đệ tử của hắn, từng cái đều là nhân trung chi long, khiến cho người hâm mộ!”"),t("br"),t("br"),n._v("Nhớ tới Độc điện tổng bộ truyền thuyết, Hải Minh Kim nhịn không được nói."),t("br"),t("br"),n._v("Hắn mặc dù chỉ là Thất Tinh đỉnh phong cấp bậc độc sư, nhưng mà trong khoảng thời gian này vì tới tổng bộ, trước thời hạn nghiên cứu không ít sách vở, đối tổng bộ quyền lực phân chia, biết không ít."),t("br"),n._v("Độc điện tổng bộ, bởi vì chưa từng xuất hiện một cái dùng độc, có thể lực áp quần hùng, làm cho tất cả mọi người bội phục, điện chủ vị trí đã trống chỗ mấy ngàn năm, cơ bản đều từ tứ đại Tôn giả hiệp thương nghị sự, còn hai đại hộ pháp... Một mực dạo chơi bên ngoài, rất ít có thể nhìn thấy, liền xem như tổng bộ độc sư, đều chưa bao giờ thấy qua."),t("br"),t("br"),n._v("Cho đến trước mắt, bốn người này chính là độc sư cái nghề nghiệp này bên trong, quyền lực cùng địa vị cao nhất."),t("br"),t("br"),n._v("Bạch Đình có thể trở thành trong đó một vị đệ tử, cho dù không phải thân truyền, đi tới tổng bộ cũng sẽ thuận tiện không ít."),t("br"),t("br"),n._v("Nếu không nhờ vào hai người bọn họ Thất Tinh cấp bậc độc sư, muốn dễ dàng như vậy đi vào nơi này, còn có chút làm không được."),t("br"),t("br"),n._v("“Đi thôi, nhanh lên một chút vào thành, tổ sư thể nội độc khí có chút không khống chế nổi, không nhanh chóng áp chế lời nói, ta sợ sẽ xuất hiện nguy hiểm!”"),t("br"),t("br"),n._v("Cảm khái vài câu, không tiếp tục xoắn xuýt, Bạch Đình nói một tiếng, mang theo Ngụy Như Yên, đi đầu đi thẳng về phía trước."),t("br"),t("br"),n._v("Độc điện tổng bộ thành thị, cùng ngoại giới vương thành, Đế đô không có bất kỳ cái gì quá lớn khác nhau, không chỉ sinh sống độc sư, còn có một số người bình thường."),t("br"),t("br"),n._v("Tiến vào thành thị, đi lại tại trên đường phố rộng rãi, Bạch Đình cùng Hải Minh Kim liếc mắt nhìn nhau, đều tự có chút mê hoặc."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, như vậy rộng lớn đường phố chính, hẳn là phi thường náo nhiệt mới là, bây giờ lại nhìn thấy hai bên cửa hàng cửa sổ đóng chặt, không có bất kỳ ai, chẳng lẽ chuyện gì xảy ra?"),t("br"),t("br"),n._v("Không nên ah!"),t("br"),t("br"),n._v("Nếu như Danh Sư đường thật muốn động thủ, tất nhiên sẽ huyên náo toàn bộ đại lục xôn xao, chưa từng nghe qua loại này tin tức, cũng liền nói rõ, không có việc gì."),t("br"),t("br"),n._v("Có thể nổi danh sư đường, ai có thể để ừm lớn Độc điện, trong nháy mắt trở nên người đi nhà trống?"),t("br"),t("br"),n._v("“Tiếp tục hướng bên trong xem một chút đi!” Đi hơn nửa canh giờ, đều không có nhìn thấy bóng người, hai người có chút hoảng hốt, Hải Minh Kim nhịn không được nói."),t("br"),t("br"),n._v("“Ừm!” Nhẹ gật đầu, Bạch Đình đang nghĩ nói chuyện, liền nghe đến một thanh âm truyền tới."),t("br"),t("br"),n._v("“Phía trước thế nhưng là Bạch Đình sư đệ?”"),t("br"),t("br"),n._v("Hai người ngẩng đầu nhìn lại, lập tức nhìn thấy một người trung niên bay tới."),t("br"),t("br"),n._v("Cái này nhân thân vật liệu thấp bé, đại khái là 1m6 bộ dạng, hơn nữa còn có một chút còng lưng, bộ dáng mười phần không đáng chú ý, nhưng mà huy chương trước ngực lên, tám khỏa sao lấp lánh chói mắt, hiển lộ rõ ràng thân phận, để cho người ta không dám khinh thường."),t("br"),t("br"),n._v("“Nguyên Lăng sư huynh!”"),t("br"),t("br"),n._v("Thấy rõ bộ dáng, Bạch Đình độc sư không dám do dự, vội vàng ôm quyền."),t("br"),t("br"),n._v("“Nguyên Lăng? Chẳng lẽ liền là vị kia từng độc chết qua Thánh vực bát trọng Thánh thú bát tinh đỉnh phong độc sư?” Nghe được cái tên này, Hải Minh Kim giật nảy mình."),t("br"),t("br"),n._v("Muốn nói cái khác bát tinh độc sư, hắn khả năng cũng không rõ ràng, nhưng mà vị này danh khí thế nhưng là cực lớn."),t("br"),t("br"),n._v("Bảy mươi hai cái nhất đẳng phân điện điện chủ, cấp bậc đã sớm đạt đến bát tinh đỉnh phong, chế biến ra tới , vô sắc vô vị, có thể hóa thành mây khói, bốn phía phiêu tán, một khi hấp thu, cho dù Thánh vực bát trọng cường giả, đều khó mà địch nổi!"),t("br"),t("br"),n._v("Nghe nói vị này Nguyên Lăng điện chủ, từng bị một đầu Thánh vực bát trọng Thánh thú đuổi giết, cùng đường mạt lộ phía dưới, thả ra độc dược, cứ thế mà đem hắn độc chết."),t("br"),t("br"),n._v("Chính là bởi vì một trận chiến này, hắn tại toàn bộ liên minh đế quốc, đều có cực lớn danh khí, Thanh Nguyên phong hào vương quốc Độc điện, càng là không ai không biết, không người không hay."),t("br"),t("br"),n._v("Nhân vật như vậy, lại là Bạch Đình sư huynh, cùng hắn cùng một chỗ đồng môn học nghệ qua... Suy nghĩ một chút đều để người khiếp sợ."),t("br"),t("br"),n._v("“Không cần khách khí, lão sư nghe nói ngươi đã đạt đến Xuất Khiếu cảnh, muốn khảo hạch bát tinh độc sư, đặc biệt để cho ta tới tiếp ngươi!” Nguyên Lăng cười cười."),t("br"),t("br"),n._v("Bạch Đình lần này trở về, nghe theo Trương Huyền lời nói, không đem tổ sư sự tình nói ra, mà là nói muốn khảo hạch bát tinh độc sư, cũng không nổi bật, cũng có thể thuận lợi đi tới tổng bộ."),t("br"),t("br"),n._v("“Đa tạ sư huynh, đúng, sư huynh không phải trong ngực nay phân điện ư? Làm sao đến nơi này?”"),t("br"),t("br"),n._v("Bạch Đình kỳ quái."),t("br"),t("br"),n._v("Dưới tình huống bình thường, phân điện điện chủ, là không thể dễ dàng rời khỏi điện đường, vị này nhưng đến nơi này, còn tới đón hắn, để hắn thụ sủng nhược kinh."),t("br"),t("br"),n._v("“Ngươi không biết?” Nguyên Lăng sửng sốt một chút, lập tức kịp phản ứng: “Cũng đúng, ngươi rời đi nơi này đã nhiều năm như vậy, không biết rất bình thường! Là Hữu hộ pháp Thẩm Khuyết trở về, tìm được khai phái tổ sư tín vật, muốn kế vị trở thành điện chủ, tổ chức độc sư đại hội! Chúng ta bảy mươi hai điểm điện điện chủ, trong khoảng thời gian này đều chạy tới, thành thị người bên ngoài, cũng đều đi tham gia, cho nên, nơi này trống trải như dã, một bóng người đều không nhìn thấy!”"),t("br"),t("br"),n._v("“Hữu hộ pháp Thẩm Khuyết?”"),t("br"),t("br"),n._v("“Tìm được khai phái tổ sư di vật?”"),t("br"),t("br"),n._v("Hai người sững sờ, lập tức nhíu nhíu mày, Bạch Đình nhìn lại, nhịn không được hỏi: “Không biết là... Cái gì di vật?”"),t("br"),t("br"),n._v("“Khai phái tổ sư !” Nguyên Lăng nói."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1099"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1101"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);