(window.webpackJsonp=window.webpackJsonp||[]).push([[1282],{1641:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-205-coi-nhu-ta-thang-sao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-205-coi-nhu-ta-thang-sao"}},[n._v("#")]),n._v(" Chương 205: Coi như ta thắng sao?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Ngươi vẽ?” "),t("br"),t("br"),n._v(" Gian phòng yên lặng."),t("br"),t("br"),n._v(" Tiếp theo tiếng ồn ào vang lên."),t("br"),t("br"),n._v(" “Ngươi nói này ngũ cảnh tranh vẽ là ngươi vẽ? Trương Huyền, ngươi thật là cười chết ta, coi như giám thưởng không ra, chịu thua chính là, không cần như thế trang đi!” "),t("br"),t("br"),n._v(" “Mặt thật là lớn, trang bức chung quy phải có cái mức độ, ngươi vẽ, sao không nói, ngươi chính là Vân Thiếu Khanh Tông Sư?” "),t("br"),t("br"),n._v(" “Không biết trời cao đất rộng đồ vật, ngươi biết ngũ cảnh tác phẩm hội họa đại biểu ý nghĩa sao? Còn ngươi vẽ? Đùa gì thế!” "),t("br"),t("br"),n._v(" Điền Long, Lục Tầm, Vương Siêu tất cả đều cười lạnh thành tiếng, từng cái từng cái nhìn về phía Trương Huyền như là nhìn một cái kẻ ngu si."),t("br"),t("br"),n._v(" Ngũ cảnh tác phẩm hội họa, khái niệm gì?"),t("br"),t("br"),n._v(" Toàn bộ Thiên Huyền Vương quốc, đều không có một người có thể vẽ ra, liền ngay cả phụ thân của Lục Tầm, Đế sư Lục Trầm đại sư, đều không làm được, một cái học viện lão sư, không đủ hai mươi tuổi tiểu tử, nói hắn có thể làm ra, này không phải đùa giỡn là cái gì?"),t("br"),t("br"),n._v(" “Lưu Lăng, này chính là các ngươi coi trọng, dự định chiêu vì học đồ người? Không biết trời cao đất rộng, nói bốc nói phét, người như thế cũng xứng làm lão sư?” "),t("br"),t("br"),n._v(" Điền lão quăng một cái ống tay áo, sắc mặt âm trầm lại."),t("br"),t("br"),n._v(" Lưu sư đem bức họa này xem như lễ mừng thọ đưa tới, hắn cả ngày treo ở gian phòng, cẩn thận tìm tòi, kinh động như gặp thiên nhân, vẫn cảm thấy là một cái nào đó thư họa đại tông sư lưu lại Mặc Bảo, lòng sinh ngưỡng mộ, cái tên này lại nói là của hắn vẽ?"),t("br"),t("br"),n._v(" Quả thực chính là sỉ nhục danh họa."),t("br"),t("br"),n._v(" “Điền lão sư, bức họa này...” "),t("br"),t("br"),n._v(" Nghe đến lão sư chất vấn, Lưu Lăng đầy mặt băn khoăn: “... Thực sự là Trương Huyền lão sư vẽ!” "),t("br"),t("br"),n._v(" “Làm sao, ngươi cũng thừa nhận hắn ngông cuồng đi... Hả? Ngươi nói cái gì?” Điền lão không nghe rõ, lầm bầm lầu bầu một câu, đột nhiên phản ứng lại, suýt chút nữa ngất đi, môi run: “Hắn... Hắn vẽ?” "),t("br"),t("br"),n._v(" “Vâng, bức họa này... Là Trương lão sư mấy ngày trước tự tay chỗ vẽ...” "),t("br"),t("br"),n._v(" Lưu Lăng cười khổ nói."),t("br"),t("br"),n._v(" Lúc trước Hoàng Ngữ đem bộ này lấy tới, giao cho hắn làm quà tặng thời điểm, nói thật hắn cũng bối rối."),t("br"),t("br"),n._v(" Ngũ cảnh tác phẩm hội họa, coi như tại bắc võ nhị đẳng Vương quốc, đều là có thể gặp không thể cầu bảo vật, lại có thể xuất từ Hồng Thiên học viện, một cái không đủ hai mươi tuổi lão sư tay..."),t("br"),t("br"),n._v(" Coi như chính tai nghe thấy, đều cảm thấy trong đầu say xe, khó có thể tin tưởng được."),t("br"),t("br"),n._v(" “Chuyện này... Chuyện này...” "),t("br"),t("br"),n._v(" Điền lão con ngươi co rụt lại, nét mặt già nua một lần tăng thấu hồng, hận không thể có khe nứt chui vào."),t("br"),t("br"),n._v(" Lưu Lăng làm danh sư, không thể ăn nói bừa bãi, cũng liền nói rõ... Bức họa này, thực sự là vị này Trương lão sư vẽ."),t("br"),t("br"),n._v(" Bản thân luôn mồm luôn miệng sùng bái có gia, sinh thời muốn bái kiến, kết quả thật nhìn thấy, còn nghi vấn đối phương..."),t("br"),t("br"),n._v(" “Ta không tin... Hắn coi như theo từ bụng mẹ học tập thư họa, cũng không thể vẽ ra như thế lợi hại tác phẩm hội họa...” Nghe được Lưu sư thừa nhận, Lục Tầm cắn răng nhìn sang."),t("br"),t("br"),n._v(" Vừa đem vẽ tranh tác giả, khen ngợi đến bầu trời, lại là loại thủ pháp này, lại là loại này đầu bút lông... Lại là người đại sư này, lại là người tông sư kia, hận không thể quỳ liếm, kết quả... Là hắn vẫn muốn đỗi vị này, đánh có chết cũng không tin."),t("br"),t("br"),n._v(" “Ta cũng không tin, Lưu sư tuyệt đối đừng bị hắn lừa...” "),t("br"),t("br"),n._v(" Vương Siêu cũng một tiếng gào thét."),t("br"),t("br"),n._v(" Bất quá, hai người tiếng gào còn không có kết thúc, liền thấy Trương Huyền đã đi tới thư họa trước mặt, bàn tay cùng với nhẹ nhàng tiếp xúc."),t("br"),t("br"),n._v(" Ong!"),t("br"),t("br"),n._v(" Một trận thanh hót, bức tranh bên trên dã lộc lập tức giống sống giống như vậy, quay đầu vọt tới, một lát sau tiêu tan trên không trung."),t("br"),t("br"),n._v(" “Đây là... Bản nguyên hóa linh? Chỉ có thư họa sáng tác người mới có bản nguyên hóa linh?” "),t("br"),t("br"),n._v(" “Một bức thư họa, ẩn chứa sáng tác người tất cả tâm huyết, cùng hắn tinh khí thần có thể rất hoàn mỹ hô ứng, đạt đến ngũ cảnh tác phẩm hội họa, chỉ cần tác giả đụng vào, liền sẽ tự động hóa linh...” "),t("br"),t("br"),n._v(" “Chuyện này... Thực sự là hắn vẽ...” "),t("br"),t("br"),n._v(" Đạt đến Hóa Linh cảnh thư họa, muốn nhận biết ai vẽ hết sức dễ dàng, chỉ cần sáng tác người bàn tay tiếp xúc, bức tranh sẽ tự động bày ra hóa linh hiệu quả."),t("br"),t("br"),n._v(" Nhìn thấy dã lộc chạy trốn, bức tranh bên trên khí tức lưu chuyển, cùng cách đó không xa Trương Huyền lão sư kêu gọi kết nối với nhau... Kẻ ngu đến đâu cũng biết, bức họa này tác giả, khẳng định là hắn."),t("br"),t("br"),n._v(" “Chuyện này... Làm sao có khả năng?” "),t("br"),t("br"),n._v(" Lục Tầm một cái lảo đảo, ngã xuống đất, khóc không ra nước mắt."),t("br"),t("br"),n._v(" Vừa nãy hắn còn tự tin nói, bức họa này tác giả khẳng định là Vân Thiếu Khanh đại sư, dùng khẳng định là chữ viết nét bút pháp, kết quả... Hiện thực liền mạnh mẽ đánh một cái tát."),t("br"),t("br"),n._v(" Đại ca, nếu như sớm biết ngươi là thư họa Tông Sư, ta cùng so cái len sợi a!"),t("br"),t("br"),n._v(" Này không phải tìm gọt sao?"),t("br"),t("br"),n._v(" Một bên Vương Siêu, Điền Long cũng cảm thấy toàn thân co giật, sắp ngất đi."),t("br"),t("br"),n._v(" Đặc biệt là Điền Long, lúc này cuối cùng đã rõ ràng rồi vì sao Nguyên Ngữ đại sư đều đối với cái tên này như thế cung kính, thậm chí càng với hắn học tập..."),t("br"),t("br"),n._v(" Một cái có thể vẽ ra ngũ cảnh tác phẩm hội họa cấp bậc tông sư thư họa sư... Đáng giá hắn tôn kính như vậy."),t("br"),t("br"),n._v(" “Trương Huyền Tông Sư, là ta mắt vụng về, xin hãy tha lỗi...” "),t("br"),t("br"),n._v(" Điền lão cũng sắc mặt khó nhìn hợp mắt thân ôm quyền, trong nháy mắt phảng phất già rồi mười mấy tuổi."),t("br"),t("br"),n._v(" Cả đời giáo thư dục nhân, được hưởng rất lớn danh tiếng, kết quả tại đây ngã xuống bổ nhào."),t("br"),t("br"),n._v(" Để Lục Tầm cùng một cái thư họa Tông Sư tỷ thí thư họa?"),t("br"),t("br"),n._v(" Còn có so này càng kích thích sự tình sao?"),t("br"),t("br"),n._v(" Vốn là giúp hắn bái sư, kết quả... Tựu cái này dạng đem hắn hãm hại, vẫn là hố rất thảm loại này..."),t("br"),t("br"),n._v(" Khó trách Lưu Lăng bọn họ xé rách đầu cũng muốn thu hắn làm học đồ, không để ý danh sư thân phận, thì ra là như vậy."),t("br"),n._v("  Một vị có thể vẽ ra ngũ cảnh thư họa Tông Sư, một khi trở thành học đồ, liền lập tức nắm giữ sát hạch danh sư tư cách, thật muốn thành công, làm lão sư cũng sẽ vang vọng Vương quốc!"),t("br"),t("br"),n._v(" Không có trả lời Điền lão mà nói, Trương Huyền yên tĩnh đứng ở thư họa trước, nhìn trước mắt bức tranh: “Bức họa này là mấy ngày trước ta tại Lục Trầm đại sư phủ đệ vẽ, cũng không có đứng ở thảo nguyên, cũng không dùng chữ viết nét bút pháp, mà là dùng Tả Hữu Du Long thủ pháp, tổng cộng vẽ đi ra dùng bốn mươi bảy cái hô hấp, không có cái gọi là hai ba ngày.” "),t("br"),t("br"),n._v(" “Tả Hữu Du Long?” "),t("br"),t("br"),n._v(" “Bốn mươi bảy cái hô hấp?” "),t("br"),t("br"),n._v(" Lục Tầm khóe miệng lần thứ hai kéo."),t("br"),t("br"),n._v(" Vừa nãy hắn còn lời thề son sắt nói, làm ra bức họa này coi như là Tông Sư, cũng tiêu tốn chí ít hai ba ngày, hơn nữa còn là thân ở thảo nguyên, biểu lộ cảm xúc, kết quả nằm mơ đều không nghĩ tới, chính ở nhà hắn vẽ, còn chỉ dùng thời gian ngắn như vậy..."),t("br"),t("br"),n._v(" Càng khuếch đại chính là, dùng rìa đường thợ thủ công in ấn thư họa kỹ xảo làm ra ngũ cảnh danh họa..."),t("br"),t("br"),n._v(" Đây cũng quá nghịch thiên rồi đi!"),t("br"),t("br"),n._v(" Nghe đến mấy cái này, Điền Long càng là sắp khóc."),t("br"),t("br"),n._v(" Mới vừa mới đối phương nói cái kỹ xảo này thời điểm, còn bị hắn mắng chửi cho mất mặt, kết quả trong chớp mắt liền chứng thực... Là thật sự!"),t("br"),t("br"),n._v(" Tả Hữu Du Long loại này gấp tốc độ vẽ tranh thủ pháp, cũng có thể làm ra như thế ngưu bức vẽ?"),t("br"),t("br"),n._v(" Trương lão sư, ngươi đến cùng đạt đến cái gì trình độ?"),t("br"),t("br"),n._v(" Không để ý tới hai người thái độ, Trương Huyền ngón tay tại bức tranh bên trên bắn ra: “Không biết nơi này có thể có bút mực?” "),t("br"),t("br"),n._v(" “Có, có!” "),t("br"),t("br"),n._v(" Điền lão vẫy tay, Điền Cương lập tức sốt ruột chạy ra ngoài, thời gian không lâu, liền đem bút mực mang theo lại đây, bởi vì quá mức kích động, vào nhà thời điểm, suýt chút nữa ngã chổng vó."),t("br"),t("br"),n._v(" Chỉ cần không ngốc, đều biết vị này Trương lão sư, nhất định phải vì bộ này thư họa lưu lại tên."),t("br"),t("br"),n._v(" Một bức họa, lưu danh chữ cùng không lưu danh chữ, giá trị khác biệt rất lớn, thường thường sẽ nhảy một cái mấy chục lần."),t("br"),t("br"),n._v(" Đặc biệt là vị này Trương lão sư trẻ tuổi như vậy liền trở thành thư họa Tông Sư, một khi lại quá mấy năm, trở thành danh sư, vang vọng chung quanh Vương quốc, lưu lại tên tác phẩm hội họa, giá trị nhất định sẽ lần thứ hai tăng lên dữ dội."),t("br"),t("br"),n._v(" Đương nhiên, giá trị là tiếp theo, chủ yếu nhất chính là, có thể tận mắt nhìn thấy một vị thư họa Tông Sư nhấc lên bút viết chữ, cỡ nào vinh hạnh!"),t("br"),t("br"),n._v(" “Này tấm đồ không có múa bút vẩy mực khí thế, cũng không có Lục lão sư nói ý cảnh cao xa, múa bút thanh thiên đồ danh tự này, khẳng định là không chịu đựng nổi!” Cầm bút lên dính đầy mực viết, Trương Huyền đứng ở bức tranh trước, không chút do dự nào, bút lông về phía trước múa."),t("br"),t("br"),n._v(" Xoạt xoạt xoạt!"),t("br"),t("br"),n._v(" Hai chữ lớn lập tức xuất hiện bức tranh phía trên."),t("br"),t("br"),n._v(" “Dã lộc?” "),t("br"),t("br"),n._v(" Nhìn thấy hắn viết ra tên, mọi người đồng thời sững sờ."),t("br"),t("br"),n._v(" Vốn tưởng rằng sẽ sinh mệnh cái thật lợi hại tên,  không khỏi quá tùy ý đi."),t("br"),t("br"),n._v(" “Thẳng biểu đạt ngực ức, đây mới thực sự là thư họa Tông Sư cảnh giới.” "),t("br"),t("br"),n._v(" Trầm tĩnh chốc lát, Điền lão mở miệng."),t("br"),t("br"),n._v(" “Đúng đấy, này tấm đồ, chủ yếu nhất chính là dã lộc, cái khác đều là làm nền, cái gọi là khí chất, cái gọi là ý cảnh, đều là nó mang đến, kêu , nghe tới đơn giản, không có có khí thế, lại giống như đem bức họa này nội dung chủ yếu tất cả bày ra, để chỉnh bức họa lần thứ hai tăng lên một cái độ cao.” "),t("br"),t("br"),n._v(" Lưu Lăng cảm thán."),t("br"),t("br"),n._v(" Không hổ là Trương lão sư, tuổi còn trẻ liền trở thành thư họa Tông Sư tồn tại, tuy rằng chỉ là cái đơn giản tên, lại trực tiếp đem vẽ phẩm chất lần thứ hai tăng lên."),t("br"),t("br"),n._v(" “Không chỉ như vậy, nếu như bức họa này kêu múa bút thanh thiên đồ, nhìn thấy bức họa này người, chịu ảnh hưởng, sẽ không tự nhiên vì đó bên trong khí thế cảm hoá, lại không thể nào hiểu được cái khác ý nhị. Mà kêu , đơn giản là đơn giản, lại không tư duy hạn chế, mỗi một cái xem vẽ người, đều sẽ lòng sinh mơ màng, để đồ ý nhị càng đủ, ý cảnh càng càng bao la.” "),t("br"),t("br"),n._v(" Trang Hiền mở miệng."),t("br"),t("br"),n._v(" “Đúng đấy, thần lai chi bút (tác phẩm của thần), thần lai chi bút (tác phẩm của thần) a...” Trịnh Phi gật gù, đang muốn nói ra bản thân giới thiệu tóm tắt đột nhiên sững sờ, chỉ về phía trước: “Mau nhìn!” "),t("br"),t("br"),n._v(" Mọi người thấy đi, chỉ thấy trong bức tranh dã lộc lần thứ hai trốn ra, tràn đầy quyến luyến nhìn về phía Trương Huyền, cũng tại trên cánh tay của hắn sượt mấy lần, lúc này mới chậm rãi tiêu tan."),t("br"),t("br"),n._v(" “Đây là... Linh trí?” "),t("br"),t("br"),n._v(" “Linh trí cảnh tác phẩm hội họa? Không đúng, còn không có đạt đến, nếu như đạt đến, con này dã lộc, ở bên ngoài chờ đến thời gian khẳng định so này lâu dài.” "),t("br"),t("br"),n._v(" “Tuy rằng không phải, lại cũng gần như sắp đến rồi, gia tăng rồi tên, liền để bức họa này, tăng lên tiếp cận một cấp bậc, vô hạn tiếp cận lục cảnh...” "),t("br"),t("br"),n._v(" Nhìn thấy dã lộc dáng vẻ, mọi người tất cả đều chấn động, từng cái từng cái kích động sắc mặt đỏ lên."),t("br"),t("br"),n._v(" Thư họa cảnh giới, bốn vị trí đầu cảnh, phân biệt là lục thực, linh động, ý tồn, kinh hồng."),t("br"),t("br"),n._v(" Cảnh giới thứ năm vì hóa linh."),t("br"),t("br"),n._v(" Hóa linh bên trên còn có cảnh giới thứ sáu, cũng chính là bọn họ mới vừa nói linh trí."),t("br"),t("br"),n._v(" Có người nói đạt đến loại cảnh giới này tác phẩm hội họa, trong đó thư họa động vật, hoàn toàn có bản thân linh trí, thậm chí còn có thể hấp thu linh khí trong thời gian ngắn duy trì hình thái, theo thư họa bên trong đi ra, thần kỳ không gì sánh được."),t("br"),t("br"),n._v(" Vốn tưởng rằng truyền thuyết, nằm mơ đều không nghĩ tới, dĩ nhiên tận mắt đến."),t("br"),t("br"),n._v(" Trước mắt bộ này, trong đó dã lộc tuy rằng sẽ không hấp thu linh khí trên không trung nhiều duy trì một quãng thời gian, nhưng có thể đối với tác giả quyến luyến, nói rõ đã sắp có linh trí."),t("br"),t("br"),n._v(" Một khi đặt ở linh khí sung túc nơi, ôn dưỡng trăm năm, không hẳn liền không thể trở thành chân chính lục cảnh tác phẩm hội họa!"),t("br"),t("br"),n._v(" Một cái tên, liền để cả bức họa, mạnh mẽ vượt lên tiếp cận một cấp bậc, “Dã lộc” hai chữ, tuyệt đối một chữ vạn kim!"),t("br"),t("br"),n._v(" Tất cả mọi người giờ khắc này đều nhìn về thanh niên trước mắt, ánh mắt kịch liệt."),t("br"),t("br"),n._v(" Đặc biệt là Hoàng Ngữ, Bạch Tốn mấy người, kích động đều có chút run rẩy."),t("br"),t("br"),n._v(" Không để ý tới mọi người kích động, Trương Huyền đem bút lông để xuống, nhìn quanh một vòng."),t("br"),t("br"),n._v(" “Ta giám thưởng xong, cái này thử thách... Coi như ta thắng sao?” "),t("br"),t("br"),n._v(" (Ngày hôm nay bạo phát, cầu vé tháng, phiếu đề cử! Các vị, cho già nhai động lực đi, để già nhai lần thứ hai điên cuồng lên đến!!!!) "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-213"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-215"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);