(window.webpackJsonp=window.webpackJsonp||[]).push([[1283],{1637:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2125-my-dung-đan-dien-the-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2125-my-dung-đan-dien-the-ha"}},[n._v("#")]),n._v(" Chương 2125: Mỹ Dung đan diện thế (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Đúng vậy a, chính là Thần Nguyên đan!”"),t("br"),t("br"),n._v("“Nhà chúng ta vị kia, vẫn luôn là luyện chế thứ này, mỗi ngày đều có thể luyện chế mấy lô, chắc chắn sẽ không nhìn lầm!”"),t("br"),t("br"),n._v("“Thật sự cho rằng là cái gì Mỹ Dung đan, Thần Nguyên đan góp đủ số, là khi dễ chúng ta không biết hàng ư?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Kèm theo thanh âm này, những người khác cũng xác nhận xuống, từng cái tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Không phải nói có cái gì Mỹ Dung đan ư? Làm sao lấy ra, phổ thông Thần Nguyên đan tới góp đủ số?"),t("br"),t("br"),n._v("Dạ Thu Nhạn cũng nhíu nhíu mày."),t("br"),t("br"),n._v("Trước kia bất kể nói thế nào cũng là Thiên Thần cảnh cường giả, càng theo U Hồn thiên mà đến, nhãn lực rất tốt, đối phương trong hộp ngọc đồ vật, bất kể thế nào nhìn, cũng là bình thường nhất Thần Nguyên đan, không có bất kỳ cái gì chỗ đặc thù."),t("br"),t("br"),n._v("Vừa khen xong vị này Tề Linh Nhi năng lực không tệ, tổng sẽ không ở loại chuyện nhỏ nhặt này bên trên, phạm sai lầm lớn đi!"),t("br"),t("br"),n._v("“Theo ta được biết, linh hồn hoàng thành, đều không có người có thể luyện chế ra Mỹ Dung đan, Tề Linh Nhi tiểu thư, triệu tập chúng ta tới, cho chúng ta nhìn một cái Thần Nguyên đan, như vậy nói đùa, bất kể nói thế nào, đều muốn cho cái bàn giao đi!”"),t("br"),t("br"),n._v("Một vị nữ tu sĩ cũng đứng dậy, lông mày nâng lên."),t("br"),t("br"),n._v("Tu vi của nàng cũng đạt tới thượng phẩm Thần Linh cấp bậc, cứ việc khoảng cách đỉnh phong còn có chênh lệch rất lớn, nhưng ở Dạ Minh thành, cũng tuyệt đối tính cả đỉnh phong nhất một đợt."),t("br"),t("br"),n._v("Dù sao, Dạ Minh học viện viện trưởng, cũng chỉ có cấp bậc này mà thôi."),t("br"),t("br"),n._v("“Nhất định phải cho cái bàn giao, chúng ta thời gian tu luyện cũng rất quý giá...”"),t("br"),t("br"),n._v("Lập tức có người nghênh hợp."),t("br"),t("br"),n._v("“Mọi người bình tĩnh đừng nóng, chẳng lẽ các ngươi, cho rằng đây là phổ thông Thần Nguyên đan?”"),t("br"),t("br"),n._v("Dưới bàn tay ép, ổn định đám người rối loạn cảm xúc, Tề Linh Nhi nhẹ nhàng cười một tiếng: “Nếu thật là, ta cũng không cần thiết lấy tới bêu xấu! Người tới!”"),t("br"),t("br"),n._v("Vẫy vẫy tay."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Một cái thần thú bị mang theo tới."),t("br"),t("br"),n._v("Tất cả mọi người “Lộp bộp!” Một chút."),t("br"),t("br"),n._v("“Là thượng phẩm thần thú, dung nham cự mãng!”"),t("br"),t("br"),n._v("“Cái tên này, sinh hoạt tại dung nham bên trong, toàn thân nóng bỏng, có thể phun ra hỏa diễm, sức chiến đấu cực mạnh!”"),t("br"),t("br"),n._v("“Mang thứ này tới đây làm gì, một khi bị nó tổn thương, làn da hầu như không có cách nào chữa trị...”"),t("br"),t("br"),n._v("“Ta một bạn thân, liền bị cái tên này bỏng cánh tay, các loại dược vật dùng không biết bao nhiêu, cái này đều hai mươi năm, đều không có một tia chuyển biến tốt đẹp!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bị dắt đi vào đầu này thần thú, tại Dạ Minh thành danh khí rất lớn, am hiểu hỏa diễm, thực lực mạnh mẽ."),t("br"),t("br"),t("br"),n._v("“Đầu này thần thú năng lực chắc hẳn không cần ta nói nhiều, mọi người cũng đều rõ ràng... Không biết có người hay không đi ra, nguyện ý cùng nó chiến đấu một hồi?”"),t("br"),t("br"),n._v("Tề Linh Nhi cười cười."),t("br"),t("br"),n._v("Tất cả mọi người nhíu mày."),t("br"),t("br"),n._v("Cùng cấp bậc thần thú, sức chiến đấu so cùng cấp bậc tu luyện giả mạnh hơn rất nhiều, thượng phẩm Thần Linh cấp bậc, ở đây nữ tu sĩ, hầu như không có người nào có thể vượt qua đi!"),t("br"),t("br"),n._v("“Nếu như không có, ta dùng ta người, cũng không tính gian lận đi!” Tề Linh Nhi nói."),t("br"),t("br"),n._v("“Nhiều người nhìn như vậy, muốn gian lận, cũng không dễ dàng như vậy! Muốn làm gì liền nhanh lên đi, đừng chậm trễ mọi người chúng ta thời gian!” Trước đó nói chuyện nữ tử nói."),t("br"),t("br"),n._v("“Nếu Trần phu nhân nói như vậy, vậy ta liền không khách khí!”"),t("br"),t("br"),n._v("Tề Linh Nhi lần nữa vẫy vẫy tay."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Một cô gái đi tới, đi vào dung nham mãng xà trước mặt, hít sâu một hơi, đứng tại chỗ."),t("br"),t("br"),n._v("“Bắt đầu!”"),t("br"),t("br"),n._v("Tề Linh Nhi lông mày giương lên."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Cách đó không xa mãng xà, đột nhiên một tiếng nổi giận, to lớn miệng mở ra, một đoàn nóng bỏng hỏa diễm, lập tức phun ra ngoài, thời gian nháy mắt liền đem mới đi ra nữ tử bao phủ."),t("br"),t("br"),n._v("Một lát sau, hỏa diễm biến mất, nữ tử toàn thân máu tươi, một thân trọng thương nằm trên mặt đất, đặc biệt là gương mặt, vốn vô cùng mịn màng làn da, bị ngọn lửa thiêu đốt tràn đầy vết sẹo, không nói ra được dữ tợn."),t("br"),t("br"),n._v("“Tề Linh Nhi, ngươi muốn làm gì...” Dạ Thu Nhạn da đầu nổ tung."),t("br"),t("br"),n._v("Quả thực chính là xem mạng người như cỏ rác!"),t("br"),t("br"),n._v("Mặc kệ cô gái này, có phải hay không thủ hạ của nàng, giữa hai người lại có giao dịch gì, như vậy trắng trợn dùng thần thú phá hủy một người sinh mệnh, tuyệt đối là phạm vào Thần giới luật pháp, Dạ Minh thành quy củ!"),t("br"),t("br"),n._v("Không nói mặt khác, chỉ điều này, liền có thể bắt lại, nhận cực kỳ nghiêm trọng hình phạt."),t("br"),t("br"),n._v("“Mọi người không cần khẩn trương! Ta cũng không có hại người ý tứ, nếu để nàng, tiếp nhận dung nham mãng xà thiêu đốt, khẳng định có phương pháp, để hắn khôi phục như lúc ban đầu!”"),t("br"),t("br"),n._v("Thấy mọi người vẻ mặt tất cả đều thay đổi, Tề Linh Nhi lần nữa nhìn quanh: “Trần phu nhân, ngươi là đan dược thế gia, ngươi nói lữ, cũ theo gấu, chính là Dạ Minh thành nổi danh nhất luyện đan đại sư... Không biết, nhưng có phương pháp, đem hắn chữa khỏi?”"),t("br"),t("br"),n._v("Bị nàng thét lên Trần phu nhân, dừng lại một chút: “Dung nham mãng xà hỏa diễm quỷ dị, trong đó hỏa độc một khi xâm nhập thân thể, thượng phẩm Thần Linh, đều khó mà khu trừ, ta không có biện pháp tốt!”"),t("br"),t("br"),n._v("“Cái kia Lưu phu nhân đâu? Ngươi nói lữ Lưu Minh dương, là Dạ Minh thành bác sĩ giỏi nhất, chắc hẳn sẽ có phương pháp đi!” Tề Linh Nhi nói tiếp."),t("br"),t("br"),n._v("“Không có! Hỏa độc khó trừ, bó tay toàn tập!” Lưu phu nhân cũng lắc đầu."),t("br"),t("br"),n._v("“Nếu hai vị cũng không có cách nào, ta muốn hỏi hỏi ở đây chư vị, còn có những người khác, có biện pháp không? Mặt khác, những này hỏa độc, có phải thật vậy hay không? Chúng ta nhưng có giả mạo?” Tề Linh Nhi tiếp lấy cười nói."),t("br"),t("br"),n._v("“Chúng ta trị không được!” Đám người đồng thời lắc đầu."),t("br"),t("br"),n._v("Trần phu nhân, Lưu phu nhân đều không làm được, bọn họ tự nhiên càng không làm được."),t("br"),n._v(" Còn giả mạo... Càng không thể nào nói đến."),t("br"),t("br"),n._v("Dung nham mãng xà đang ở trước mắt, dâng trào hỏa diễm, như vậy sắc bén, tất cả mọi người cảm nhận được nóng bỏng, bất luận từ chỗ nào một chút nhìn, đều khó có khả năng giả mạo, cũng không cách nào giả mạo."),t("br"),t("br"),n._v("“Nếu mọi người cho là ta không có cách nào giả mạo, vậy ta liền chữa khỏi nàng, để mọi người nhìn một chút trong miệng các ngươi cái này viên Thần Nguyên đan uy lực...”"),t("br"),t("br"),n._v("Thấy tô đậm hỏa hầu đến, Tề Linh Nhi tay ngọc khẽ vẫy, cầm trong tay hộp ngọc hầu gái, liền đem nó bên trong đan dược lấy ra ngoài, để vào bị tổn thương nữ tử trong miệng."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Đan dược đi vào cổ họng, bị thương nữ tử không ngừng run rẩy, không đến ba cái hô hấp, trên mặt liền rơi tầng tiếp theo u hắc da chết, ngay sau đó trên người cũng không ngừng rơi."),t("br"),t("br"),n._v("Dùng nước sạch nhẹ nhàng bay sượt, lộ ra bên trong trắng tinh làn da."),t("br"),t("br"),n._v("Tất cả mọi người đều chấn động."),t("br"),t("br"),n._v("Trước mắt cái này vừa mới bị thiêu đốt không có hình người, thoạt nhìn bất cứ lúc nào cũng sẽ tử vong nữ tử, không riêng gì hoàn hảo không chút tổn hại, bị tổn thương địa phương vậy mà một chút dấu vết đều không có lưu lại, thậm chí, làn da so trước kia còn tốt hơn rất nhiều, tựa như thoát thai hoán cốt, giải phẫu thẩm mỹ đồng dạng."),t("br"),t("br"),n._v("Làm sao làm được?"),t("br"),t("br"),n._v("Chẳng lẽ cũng bởi vì ăn vừa mới viên kia Thần Nguyên đan?"),t("br"),t("br"),n._v("Hiệu quả trị liệu như vậy lớn?"),t("br"),t("br"),n._v("Trong lúc nhất thời lặng ngắt như tờ."),t("br"),t("br"),n._v("Tất cả mọi người bị khiếp sợ nói không ra lời."),t("br"),t("br"),n._v("Trước đó tiếng chất vấn âm lớn nhất mấy người, càng là giống như là bị cái gì ngăn chặn miệng, trên mặt nóng bỏng, một câu đều nói không đi ra."),t("br"),t("br"),n._v("“Đây chính là chúng ta luyện đan sư, luyện chế Thần Nguyên đan, không chỉ có thể làm đẹp, còn có thể chữa trị thân thể vết thương, để cho người ta dung nhan thường trú...”"),t("br"),t("br"),n._v("Thấy mọi người đã bị vừa mới hiệu quả của đan dược triệt để rung động, Tề Linh Nhi mỉm cười, nói: “Lợi hại như thế đan dược, chỉ cần 1000 thần tệ liền có thể mua sắm một viên! Không tin, có thể hiện trường kiểm tra, có hiệu quả, trả lại tiền. Lần đầu tiên bán ra, chỉ có hai mươi viên, hiện tại đã sử dụng một viên, nói cách khác... Còn thừa lại 19 viên, bỏ lỡ cơ hội, cũng không biết lúc nào mới có thể có cơ hội thứ hai!”"),t("br"),t("br"),n._v("“Một viên 1000 viên thần tệ? Đây là cướp bóc đi...”"),t("br"),t("br"),n._v("Tất cả mọi người khiếp sợ."),t("br"),t("br"),n._v("Đường đường “Một Tấc Bụi” sơn tặc, cướp bóc không biết bao nhiêu năm, cũng mới lưu lại mấy trăm thần tệ tích góp, Dạ Minh thành xa xôi một chút nhà, một bộ cũng liền mấy chục thần tệ mà thôi..."),t("br"),t("br"),n._v("Một cái đan dược liền muốn một ngàn..."),t("br"),t("br"),n._v("Giá cả có chút không hợp thói thường!"),t("br"),t("br"),n._v("“Một ngàn là đắt, nhưng... Trong tay nàng chỉ có 19 viên, một khi bán xong, lần sau chẳng biết lúc nào mới có mới...”"),t("br"),t("br"),n._v("“Tình cảnh vừa nãy, ta một mực nhìn lấy, không làm được giả... Nếu là thật, cái này dược hiệu có thể xưng kinh khủng... Thật có thể để cho ta thanh xuân mãi mãi lời nói, một ngàn thần tệ tuy nhiều, nhưng cũng không tính là gì!”"),t("br"),t("br"),n._v("“Này ngược lại là... Chúng ta bây giờ đã già, mặt cũng thất bại, không nghĩ biện pháp lời nói, trong nhà vị kia khẳng định sẽ bị phía ngoài hồ ly tinh câu đi, đến lúc đó, tốn nhiều tiền hơn nữa cũng vô ích...”"),t("br"),t("br"),n._v("“Hồ ly biến hóa nữ tử, trời sinh mị cốt, quả thực để cho người ta khó mà chống cự...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Một viên đá dấy lên ngàn cơn sóng, nghị luận ầm ĩ."),t("br"),t("br"),n._v("1000 thần tệ, giá cả quá đắt, coi như lần này có tư cách tới, cũng là Dạ Minh thành phú giả, tán tu bên trong cường giả, muốn một hơi lấy ra nhiều như vậy, cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("“Tiểu thư... Giá tiền là không phải định quá cao?”"),t("br"),t("br"),n._v("Nhìn thấy đám người từng cái tràn đầy chần chừ cùng nghi hoặc, Tề Linh Nhi sau lưng bà lão nhịn không được truyền âm tới."),t("br"),t("br"),n._v("Tiểu thư định cái giá tiền này, không cùng bất luận người nào thảo luận, nàng cũng là vừa mới biết."),t("br"),t("br"),n._v("“Không cao, ta muốn bán chính là xa xỉ phẩm, yên tâm đi... Chỉ cần có thể để các nàng trẻ tuổi, tốn nhiều tiền hơn nữa, cũng sẽ không cự tuyệt!”"),t("br"),t("br"),n._v("Tề Linh Nhi mỉm cười."),t("br"),t("br"),n._v("Đều là nữ nhân, biết tâm lý nữ nhân."),t("br"),t("br"),n._v("Mỹ lệ hay không, đối với các nàng tới nói, tuyệt đối là cái không thể vượt qua núi cao, chỉ cần có cơ hội biến đẹp, coi như tiêu phí lại lớn đánh đổi, cũng ở đây không tiếc."),t("br"),t("br"),n._v("“Tốt a!” Thấy tiểu thư như vậy vững vàng, bà lão không hỏi thêm nữa."),t("br"),t("br"),n._v("“Không có người mua?”"),t("br"),t("br"),n._v("Lại đợi một hồi, thấy mọi người chỉ là thảo luận, không có người nguyện ý bỏ tiền, Tề Linh Nhi cũng không vội vã, mà là lần nữa cười một tiếng, con mắt rơi vào cách đó không xa Dạ Thu Nhạn trên người."),t("br"),t("br"),n._v("“Gặp qua Dạ đặc sứ!”"),t("br"),t("br"),n._v("Đi vào trước mặt, Tề Linh Nhi ôm quyền."),t("br"),t("br"),n._v("Dạ Thu Nhạn gật gật đầu, cũng không nói nhiều."),t("br"),t("br"),n._v("“Nếu không có người nguyện ý bắt đầu, không bằng Dạ đặc sứ tới trước một viên làm sao?” Tề Linh Nhi cười nhạt một tiếng: “Đặc sứ yên tâm, ngươi bây giờ liền có thể dùng, không có hiệu quả, ta vài xu không lấy!”"),t("br"),t("br"),n._v("Dạ Thu Nhạn nhíu nhíu mày, thấy đối phương không có ý tứ gì khác, trầm tư trở xuống, gật gật đầu: “Cũng tốt!”"),t("br"),t("br"),n._v("Nói xong, nhận lấy trong tay đối phương hộp ngọc, cẩn thận kiểm tra một lần, xác định không có bất cứ vấn đề gì, lúc này mới đem đan dược nắm đi ra, nhẹ nhàng để vào trong miệng."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Đan dược vào miệng tức hóa, ngay sau đó một cỗ đặc thù lực lượng, tràn vào trong cơ thể, trên mặt tê dại một hồi cùng xốp giòn ngứa."),t("br"),t("br"),n._v("Tràn đầy nghi hoặc, đang không biết chuyện gì xảy ra, chỉ thấy trước mắt Tề Linh Nhi, giơ lên một viên gương đồng đặt ở phía trước."),t("br"),t("br"),n._v("Sửng sốt một chút, Dạ Thu Nhạn nhìn lại, mạng che mặt trần trụi bên ngoài làn da quả thực so vừa mới non mịn không biết bao nhiêu, bàn tay run rẩy, kéo xuống trên mặt lụa mỏng."),t("br"),t("br"),n._v("Đồng tử co rụt lại."),t("br"),t("br"),n._v("Quấy nhiễu nàng không biết bao nhiêu năm vết sẹo, đã biến mất không thấy hình bóng, không chỉ có như vậy... Dung mạo càng giống là một chút trẻ mười tuổi, thay đổi lớn, tưởng như hai người!"),t("br"),t("br"),n._v("“Cái này... Làm sao làm được?”"),t("br"),t("br"),n._v("Thân thể mềm mại run rẩy, Dạ Thu Nhạn tràn đầy khó tin!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2139"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2141"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);