(window.webpackJsonp=window.webpackJsonp||[]).push([[1284],{1638:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2126-ngo-phuong-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2126-ngo-phuong-thanh"}},[n._v("#")]),n._v(" Chương 2126: Ngô Phương Thanh")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Đặc sứ...” Theo sau lưng lão giả, hốc mắt đỏ lên."),t("br"),t("br"),n._v("Những năm này đi theo đối phương sau lưng, bởi vì dung mạo bị tổn thương, nhận oan ức, toàn bộ nhìn ở trong mắt, vốn cho rằng cả đời lại không cách nào khôi phục, không nghĩ tới đối phương một viên đan dược giải quyết."),t("br"),t("br"),n._v("U Hồn hoàng thành Thần Vương cường giả, cũng chưa chắc có thể làm được."),t("br"),t("br"),n._v("Bốn phía đám người, cũng từng cái không ngừng run rẩy."),t("br"),t("br"),n._v("Dạ Minh nội thành, rất nhiều người không biết vị này đặc sứ tồn tại, nhưng thân là nội thành phú giả, đỉnh phong một tầng nhân sĩ, vị này mỹ lệ Dạ Thu Nhạn, cho dù chưa thấy qua, cũng đều nghe nói qua."),t("br"),t("br"),n._v("U Hồn thiên hoàng thành cường giả, bởi vì bị thương rớt cấp, mới tới nơi này, trên mặt có một đạo vết đao, hầu như mỗi lần đều là mạng che mặt gặp người..."),t("br"),t("br"),n._v("Chỉ phục dùng một viên đan dược, không chỉ vết đao loại bỏ, cả người càng thoạt nhìn trẻ không biết bao nhiêu tuổi..."),t("br"),t("br"),n._v("Cái này đã không thể gọi là thần hiệu, mà là kỳ tích!"),t("br"),t("br"),n._v("“Thế nào? Không tin, có thể thử một chút, đồng dạng lời nói, không có hiệu quả, vài xu không lấy!” Tề Linh nhi cười một tiếng."),t("br"),t("br"),n._v("Có thể một thân một mình chưởng khống to lớn chợ đen, loại này buôn bán thao tác, đã sớm xe nhẹ đường quen, lúc này toàn bộ cục diện, đối với nàng mà nói, đã hoàn toàn tại trong khống chế."),t("br"),t("br"),n._v("“Ta đi thử một chút!” Trước đó tiếng chất vấn lớn nhất vị kia Trần phu nhân đi ra."),t("br"),t("br"),n._v("Nàng đến từ đan dược thế gia, đạo lữ, Trần Y Hùng, chính là Dạ Minh thành cường đại nhất luyện đan sư."),t("br"),t("br"),n._v("Trong đám người đi ra, Dạ Thu Yến thấy rõ dung mạo, là cái đại khái chừng năm mươi bà lão, tuy là bảo dưỡng không sai, nhưng đã cho thấy vẻ già nua, không cách nào ngăn chặn."),t("br"),t("br"),n._v("Nhận lấy đan dược, nuốt xuống, Trần phu nhân nhắm mắt lại, dường như muốn cảm thụ dược lực, tìm hiểu đan dược thành phần, còn không có cảm giác được cái gì, liền nghe đến bốn phía vang lên quyết liệt tiếng nghị luận."),t("br"),t("br"),n._v("Vội vàng mở mắt, ngay sau đó nhìn thấy phía trước trong gương nhiều hơn một cái thoạt nhìn chỉ có chừng ba mươi tuổi trung niên phụ nhân."),t("br"),t("br"),n._v("“Đây là... Ta?”"),t("br"),t("br"),n._v("Thân thể chấn động, Trần phu nhân không ngừng run rẩy."),t("br"),t("br"),n._v("Trong gương, chính là nàng hơn mười năm trước dung mạo, ăn một viên đan dược, làn da liền trở nên căng cứng, dung mạo khôi phục lúc trước... Cái này, đây quả thật là dược hiệu?"),t("br"),t("br"),n._v("Chân khí trong cơ thể vận chuyển một vòng, muốn nhìn một chút có phải hay không chướng nhãn pháp, đợi thời gian vừa tới, lần nữa khôi phục nguyên dạng, rất nhanh thi kiểm tra xong, thân thể là thực sự thay đổi!"),t("br"),t("br"),n._v("Hơn nữa thuốc này không chỉ có để nàng làn da trở nên càng thêm nhẵn bóng, thoạt nhìn trẻ lại không ít, càng quan trọng hơn là, trước đó trong cơ thể ám thương, cùng với dùng đan dược lưu lại đan độc, cũng triệt để dọn dẹp sạch sẽ, tu vi cứ việc không có tăng thêm, thực lực lại tăng lên không ít!"),t("br"),t("br"),n._v("“Cái này... Không chỉ có là Mỹ Dung đan, vậy mà đối tu vi còn có như vậy lớn trợ giúp...”"),t("br"),t("br"),n._v("Trần phu nhân kích động không kềm chế được."),t("br"),t("br"),n._v("Thân là đan dược thế gia người, biết dược vật công hiệu, có thể làm đẹp liền rất nghịch thiên, đối tu vi còn có trợ giúp... Cái này hai cái, một ngàn thần tệ, tuyệt đối kiếm lợi lớn!"),t("br"),t("br"),n._v("“Trần phu nhân, làm sao?”"),t("br"),t("br"),n._v("Thấy nàng bộ dáng này, tề Linh nhi lần nữa cười một tiếng."),t("br"),t("br"),n._v("“Cái này... Hiệu quả thoạt nhìn không sai, có điều, ta còn muốn cảm thụ một chút, có hay không tác dụng phụ, bình thường lợi hại đan dược, tác dụng phụ cũng đều rất mạnh...” Nhịn xuống hưng phấn, Trần phu nhân một mặt lãnh đạm."),t("br"),t("br"),n._v("“Đúng a...”"),t("br"),t("br"),n._v("Đám người gật đầu."),t("br"),t("br"),n._v("Thiên địa vạn vật, không có khả năng mười phân vẹn mười, nếu lợi hại như vậy hiệu quả trị liệu, sẽ có hay không có cái gì để cho người ta khó mà tiếp nhận thiếu hụt?"),t("br"),t("br"),n._v("Tề Linh nhi nhíu nhíu mày."),t("br"),t("br"),n._v("Trước mắt vị này vừa mới biểu lộ, rõ ràng đã xác nhận hiệu quả, làm sao không muốn thừa nhận?"),t("br"),t("br"),n._v("“Trần phu nhân, không biết có cái gì tác dụng phụ?”"),t("br"),t("br"),n._v("Trong đám người có người hô."),t("br"),t("br"),n._v("“Cái này, ta cũng không rõ lắm, cần hảo hảo nghiên cứu!” Dừng lại một chút, Trần phu nhân nói: “Như vậy đi, ngươi bây giờ trong tay hẳn là chỉ còn lại có 18 viên thuốc đi, một ngàn một viên, không bằng toàn bộ bán cho ta, cũng tốt trở về tìm vị kia cùng nhau nghiên cứu, nhìn một chút tác dụng phụ đến cùng có bao lớn, có thể cải tiến lời nói, không còn gì tốt hơn...”"),t("br"),t("br"),n._v("“Cải tiến?” Tề Linh nhi bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Còn tưởng rằng đối phương thật cảm thấy không đúng, náo loạn nửa ngày, là phát giác thứ này hiệu quả nghịch thiên, muốn nuốt một mình."),t("br"),t("br"),n._v("ngantruyen.com"),t("br"),n._v("“Ngươi muốn toàn bộ mua? Cái này không được, ta cũng muốn thử một chút!” Một bên Lưu phu nhân nhìn ra không đúng, có chút không vui, tiến về phía trước một bước."),t("br"),t("br"),n._v("“Ta cũng cần mua!”"),t("br"),t("br"),n._v("“Cho ta một viên!”"),t("br"),t("br"),n._v("“Cái này viên ta muốn...”"),t("br"),t("br"),n._v("“Không nên cùng ta cướp, ta ra 1100 thần tệ!”"),t("br"),t("br"),n._v("“Ta ra 1200!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Có thể trở thành Thần Linh, không có một cái ngốc, trong nháy mắt, tất cả mọi người rùm beng."),t("br"),t("br"),n._v("Hết thảy 18 viên, không nhanh chút ra tay, chờ sau đó lần không biết năm nào tháng nào."),t("br"),t("br"),n._v("Tranh đoạt bên dưới, rất nhanh 18 viên Mỹ Dung đan một bán mà không, mỗi một viên giá bán, vậy mà cao tới 1500 thần tệ, so dự trù càng nhiều."),t("br"),t("br"),n._v("Nhìn thấy một cái nho nhỏ đan dược, vậy mà bán đi như vậy giá trên trời, bà lão lần nữa nhìn về phía trước người tề Linh nhi, lộ ra nồng đậm khâm phục."),t("br"),t("br"),n._v("Không hổ là tiểu thư, thủ đoạn cao minh."),t("br"),t("br"),n._v("Không nói hạn chế số lượng, không quang minh chính đại bày ra hiệu quả trị liệu, chỉ bằng vào truyền miệng lời nói, đừng nói bán không được, khả năng 500 một viên đều không người hỏi thăm."),t("br"),t("br"),n._v("Nhưng bây giờ, ngắn ngủi một canh giờ không đến, đã lật còn nhiều gấp ba, quan trọng danh khí đánh ra ngoài, lại có thuốc, khẳng định cung không đủ cầu."),t("br"),t("br"),n._v("Đây là đầu óc buôn bán, những người khác không ai sánh nổi."),t("br"),t("br"),n._v("Trước đó chợ đen, không có tiếng tăm gì, tiêu thụ ngạch có hạn, chính là bởi vì tiểu thư tới, mới càng ngày càng tốt."),t("br"),t("br"),n._v("Không chỉ có bà lão khâm phục, một bên Dạ Thu Nhạn cũng tràn ngập tò mò."),t("br"),n._v(" Vốn cho rằng chỉ là dọa người mánh lới, không nghĩ tới thật có như vậy hiệu quả trị liệu."),t("br"),t("br"),n._v("Tề Linh nhi, từ nơi nào lấy được lợi hại như vậy linh đan diệu dược?"),t("br"),t("br"),n._v("Nàng tại hoàng thành cầu không biết bao nhiêu người, cũng không phát hiện tương tự, đối phương lại có thể tìm tới, tận mắt nhìn đến, đều có chút khó tin."),t("br"),t("br"),n._v("“Đi thăm dò một chút, nàng thuốc này lai lịch! Loại hiệu quả này dược vật, Tề Mãnh phong hào Thần Vương, cũng chưa chắc có thể luyện chế ra tới!” Dạ Thu Nhạn quay đầu phân phó nói."),t("br"),t("br"),n._v("“Vâng!” Sau lưng lão giả gật đầu lên tiếng, biến mất tại đám người."),t("br"),t("br"),n._v("Có thể trở thành đặc sứ, biết một thành lớn nhỏ tin tức, tự nhiên có bản thân con đường."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Phủ thành chủ."),t("br"),t("br"),n._v("Một người mặc khôi giáp trung niên nhân, đang nhìn trong tay hộp, lông mày thít chặt cùng một chỗ."),t("br"),t("br"),n._v("Thành chủ Ngô Phương Thanh!"),t("br"),t("br"),n._v("Trước đó Trương Huyền gặp phải nữ hài kia, Ngô Tiêu Tiêu cha."),t("br"),t("br"),n._v("“Thật có ngươi nói lớn như vậy công hiệu?” Ngô Phương Thanh ngẩng đầu nhìn về phía trước mắt một người trung niên."),t("br"),t("br"),n._v("“Vâng! Ti chức tận mắt nhìn thấy!” Trung niên nhân ôm quyền."),t("br"),t("br"),n._v("Tuy là thuốc này cuối cùng xào thành giá trên trời, hơn nữa số lượng có hạn, nhưng phủ thành chủ, thân là Dạ Minh thành tồn tại cường đại nhất, muốn có được một viên, vẫn là hết sức dễ dàng."),t("br"),t("br"),n._v("“Để Tiêu Tiêu tới!” Ngô Phương Thanh dường như vẫn là không quá tin tưởng, phân phó nói."),t("br"),t("br"),n._v("“Cha, ngươi tìm ta?”"),t("br"),t("br"),n._v("Thời gian không dài, Ngô Tiêu Tiêu đi vào trước mặt: “Ta đang tu luyện đây, không có trộm chơi...”"),t("br"),t("br"),n._v("“Không phải nói ngươi tu luyện chuyện, đem viên đan dược này nuốt vào!” Ngô Phương Thanh nói."),t("br"),t("br"),n._v("“Đan dược?” Ngô Tiêu Tiêu không biết cha gấp gáp như vậy đưa nàng tìm đến, chỉ là vì để hắn dùng một viên phổ thông Thần Nguyên đan, dừng lại một chút, vẫn là nhận lấy ngẩng đầu nuốt xuống."),t("br"),t("br"),n._v("Vừa nuốt xong, ngay sau đó nhìn thấy cha không hề bận tâm trên mặt lộ ra nồng đậm vẻ kinh ngạc, dường như trong mắt tràn đầy khó tin."),t("br"),t("br"),n._v("“Cha, làm sao vậy?”"),t("br"),t("br"),n._v("Cha thân là thành chủ, từ trước tới nay nói năng thận trọng, đột nhiên cái bộ dáng này, Ngô Tiêu Tiêu có chút khẩn trương, sợ đã làm sai điều gì."),t("br"),t("br"),n._v("“Tiểu thư, chính ngươi nhìn!”"),t("br"),t("br"),n._v("Ngô Phương Thanh không có giải đáp, vừa mới trung niên nhân đi vào trước mặt, đưa qua một chiếc gương."),t("br"),t("br"),n._v("Ngô Tiêu Tiêu nghi ngờ liếc mắt nhìn, thân thể mềm mại chấn động mạnh một cái, tràn đầy khó tin: “Đây là ta?”"),t("br"),t("br"),n._v("Trong gương, trên da còn có tàn nhang cùng tì vết, đã biến mất không thấy gì nữa, dung mạo tuy là không có quá lớn biến hóa, nhưng thoạt nhìn lại so vừa mới mỹ lệ gấp bội!"),t("br"),t("br"),n._v("Tựa như mở mỹ nhan đồng dạng, không chỉ dung nhan, khí chất tựa hồ cũng có biến hóa cực lớn."),t("br"),t("br"),n._v("Dùng một viên đan dược, biến hóa như vậy lớn..."),t("br"),t("br"),n._v("“Cha, ngươi vừa mới cho ta thuốc...” Lại nhịn không được, nhìn lại."),t("br"),t("br"),n._v("“Không có gì!” Ngô Phương Thanh đứng dậy, chắp tay sau lưng trong phòng xoay hai vòng, lần nữa nhìn tới: “Ngươi đem lúc trước vị gọi Trương Huyền người, lại nói cho ta nghe nghe!”"),t("br"),t("br"),n._v("“Trương Huyền?” Ngô Tiêu Tiêu nghi ngờ liếc mắt nhìn, vẫn gật đầu, đem trước sự tình nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Ta đã biết, đi xuống đi!”"),t("br"),t("br"),n._v("Đợi nàng nói xong, Ngô Phương Thanh khoát tay áo."),t("br"),t("br"),n._v("Chợ đen chuyện, thân là thành chủ, tự nhiên biết rõ, thậm chí trong đó, còn có hắn người, mai phục trong đó!"),t("br"),t("br"),n._v("Bởi vậy, vị này Trương Huyền bán ra Mỹ Dung đan, hắn là biết một ít tin tức."),t("br"),t("br"),n._v("Lại thêm trước đó mua sắm danh ngạch, mỗi lần đều là bên trong, đều không ngoại lệ, thân là thành chủ, không chú ý, cũng khó khăn!"),t("br"),t("br"),n._v("“Thành chủ, loại đan dược này giá trị không thể đánh giá!”"),t("br"),t("br"),n._v("Thấy tiểu thư đi ra, trung niên nhân mắt sáng lên."),t("br"),t("br"),n._v("Người khác không biết loại đan dược này giá trị cùng thị trường, hắn liếc mắt nhìn, liền hiểu được."),t("br"),t("br"),n._v("Tuyệt đối tiền đồ vô lượng."),t("br"),t("br"),n._v("Ngô Phương Thanh gật đầu: “Nếu như có thể theo cho mình dùng, Dạ Minh thành sẽ có to lớn phát triển, ta cũng có khả năng, đột phá cấp bậc cao hơn... Bất quá, vừa mới ngươi cũng nghe đến, vị này Trương Huyền, hẳn là có một vị cực kỳ cường đại lão sư, hơn nữa có thể là danh sư! Cái này đan dược, hay là hắn luyện chế.”"),t("br"),t("br"),n._v("Để con gái tới, thứ nhất thí nghiệm thuốc, thứ hai chính là hỏi thăm vị kia Trương Huyền lai lịch."),t("br"),t("br"),n._v("Cái tên này cứu mình con gái, bắt sống sơn tặc “Một Tấc Bụi”, thân là thành chủ, nếu như không tra được trải qua, cũng sẽ không cần lăn lộn."),t("br"),t("br"),n._v("“Mặc Vân chẳng qua trung phẩm Thần Linh, nhãn lực không đủ, nhìn không ra đối phương sâu cạn, có lẽ vị này Dương Huyền, chưa hẳn bao nhiêu lợi hại, không được... Thủ hạ đi dò xét một chút!” Trung niên nhân híp mắt lại."),t("br"),t("br"),n._v("“Cẩn thận một chút, mặt khác, cùng hắn thảo luận, có nguyện ý hay không bán ra phương thuốc, không nguyện ý... Còn muốn những biện pháp khác, đương nhiên, không nên để cho hắn biết, ta là người mua!” Ngô Phương Thanh dặn dò."),t("br"),t("br"),n._v("“Vâng!” Trung niên nhân gật gật đầu, lắc người một cái biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("Thành chủ, xác định quy củ, bao trùm tại quy củ phía trên, tự nhiên không phải người hiền lành."),t("br"),t("br"),n._v("Loại này lợi hại đan dược, cho dù là hắn, cũng nhịn không được động tâm."),t("br"),t("br"),n._v("Đương nhiên, điều kiện tiên quyết là, có thể dựa theo quy củ đi làm, không làm được, cũng chỉ có thể dùng những phương pháp khác."),t("br"),t("br"),n._v("“Có thể khơi thông trong cơ thể tu luyện chướng ngại, còn có thể làm đẹp... Đoán ra danh ngạch, một tia không sai... Hạ phẩm Thần Linh, lại có thể bắt sống thượng phẩm cấp bậc Một Tấc Bụi... Vị này Trương Huyền, không có đơn giản như vậy!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Ngô Phương Thanh mỉm cười, nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Có điều, không đơn giản lại như thế nào? Tại ta Dạ Minh thành, là rồng, cho ta cuộn lại, là hổ cho ta ổ lấy... Người nào tới đều không được! Cho dù là... Danh sư!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2140"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2142"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);