(window.webpackJsonp=window.webpackJsonp||[]).push([[1289],{1643:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2131-than-son"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2131-than-son"}},[n._v("#")]),n._v(" Chương 2131: Thần sơn")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Ngươi?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Tôn Cường cái tên này từ trước tới nay không thích tu luyện, có thể nắm giữ Ngụy Thần thực lực, cũng là vì đi theo phía sau hắn có thể đi vào Thần giới mới nỗ lực, để hắn cùng Thần giới đám thiên tài bọn họ giao đấu, trong lòng thủy chung không quá an tâm."),t("br"),t("br"),n._v("“Thực lực của bọn hắn, hẳn là không đáng tiểu thiếu gia cùng tiểu thư bọn họ động thủ, thực sự không được, chờ ta thua, lại để cho bọn họ lên!” Biết hắn băn khoăn, Tôn Cường nói."),t("br"),t("br"),n._v("“Tốt a!” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Ta cùng ngươi tỷ thí!”"),t("br"),t("br"),n._v("Thấy bên này toát ra cái hơn bốn mươi tuổi mập mạp, trung niên nhân sau lưng, một thiếu niên đi ra, hai mắt đen nhánh, mang theo thiếu niên đặc thù dũng mãnh cùng nhuệ khí."),t("br"),t("br"),n._v("“Điền Hoành, điểm đến là dừng là được, không nên đả thương người!” Trung niên nhân nói."),t("br"),t("br"),n._v("“Vâng!” Gọi Điền Hoành thanh niên đi vào Tôn Cường phía trước, ánh mắt lộ ra khinh thường: “Xin mời!”"),t("br"),t("br"),n._v("Hơn bốn mươi tuổi đều không có đột phá đến Thần Linh, đã bỏ qua tốt nhất đột phá thời gian, coi như nắm giữ Thần Linh chi khí, cũng chưa chắc có thể thành công."),t("br"),t("br"),n._v("“Một mình ngươi khiêu chiến ta?” Tôn Cường cũng không sốt ruột ra tay, mà là ngón tay lắc lắc, lộ ra vẻ đạm nhiên: “Vẫn là các ngươi cùng lên đi!”"),t("br"),t("br"),n._v("“Ta một người là đủ rồi!”"),t("br"),t("br"),n._v("Điền Hoành hừ lạnh, một quyền đánh ra."),t("br"),t("br"),n._v("Không khí bị áp súc, phát ra roi âm thanh."),t("br"),t("br"),n._v("Cứ việc chỉ có mười mấy tuổi, tu vi lại không yếu, lực lượng sức mạnh đều gây khó dễ cực kỳ chuẩn, uy lực thuần chất."),t("br"),t("br"),n._v("Lắc đầu, Tôn Cường tay phải vẫn như cũ vác tại sau lưng, tay trái nhẹ nhàng bao quát, bàn tay thời gian nháy mắt biến thành mấy cái, trước mắt một mảnh huyễn ảnh."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Còn không có phản ứng lại, Điền Hoành liền bị đánh trúng ngực, liên tục lùi lại ba bước, dưới chân mềm nhũn, ngồi dưới đất."),t("br"),t("br"),n._v("Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Tôn Cường mặc dù không cố gắng tu luyện, dù sao học tập Thiên đạo tinh giản công pháp và Thiên đạo tinh giản võ kỹ, lại thêm trải qua danh sư đại lục cùng Thượng Thương, nắm giữ kinh nghiệm chiến đấu phong phú, thiếu niên này cho dù thiên tư không sai, tới tỷ thí, vẫn là kém rất lớn một đoạn."),t("br"),t("br"),n._v("“Ta nói qua, ngươi không phải là đối thủ, cùng lên đi!”"),t("br"),t("br"),n._v("Một chiêu đánh bại Điền Hoành, Tôn Cường nhìn quanh một vòng, nhìn về phía còn lại rất nhiều thiếu niên, mang theo cao thủ tịch mịch biểu lộ."),t("br"),t("br"),n._v("“Tôn thúc, ngươi đã chiến đấu một hồi, vẫn là để ta tới đi!” Viên Đào đi ra."),t("br"),t("br"),n._v("“Các ngươi cũng đủ, cũng không nhìn một chút đây là địa phương nào, vẫn là ta tới... Đồng dạng là mọi người cùng nhau xông lên, thực sự không được, chư vị tiền bối muốn động thủ lời nói, cũng có thể đè thấp tu vi, yên tâm, ta một người tiếp...”"),t("br"),t("br"),n._v("Trường thương run lên, Trịnh Dương uy phong lẫm liệt."),t("br"),t("br"),n._v("“Dựa vào cái gì ngươi tới? Ta cũng đã lâu không có xuất thủ có được hay không...” Ngụy Như Yên mỉm cười."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trương Huyền che mặt."),t("br"),t("br"),n._v("Một bên Trương Giáp trợn mắt hốc mồm."),t("br"),t("br"),n._v("Chủ nhân không phải nói, hắn ưa thích khiêm tốn ư?"),t("br"),t("br"),n._v("Làm sao bản thân học sinh, hạ nhân không có một cái bớt lo?"),t("br"),t("br"),n._v("Này làm sao nhìn, đều cùng khiêm tốn không liên quan đến nhau tí nào ah..."),t("br"),t("br"),n._v("“Phách lối như vậy, ta muốn cùng ngươi so!”"),t("br"),t("br"),n._v("“Ta cùng cái kia áo xanh nữ hài so!”"),t("br"),t("br"),n._v("“Ta giáo huấn cái tên mập mạp kia, nói chuyện quá khinh người, quả thực không đem ta để vào mắt...”"),t("br"),t("br"),n._v("“Ta đem ngươi để vào mắt... Đúng, đại ca, xin hỏi ngươi vị nào...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trịnh Dương đám người động tác, triệt để đem bốn phía thiếu niên chọc giận, một hồi gào thét, nhao nhao vọt lên."),t("br"),t("br"),n._v("Mấy hơi thở sau đó, tất cả đều nằm trên mặt đất."),t("br"),t("br"),n._v("Những thiên tài này, cứ việc tại Dạ Minh thành Ngụy Thần bên trong sắp xếp bên trên thứ tự, nhưng ở Triệu Nhã đám người phía trước, cái gì cũng không tính, thậm chí liền Tôn Cường đều kém xa tít tắp."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trước đó còn chất vấn mấy cái trung niên nhân, nhìn nằm tại liền hợp lại đều không làm được rất nhiều hậu bối, từng cái con mắt choáng váng, suýt chút nữa không có hộc máu."),t("br"),t("br"),n._v("Còn tưởng rằng đối phương là dựa vào quan hệ, không nghĩ tới thực lực từng cái mạnh mẽ như vậy."),t("br"),t("br"),n._v("Sớm lợi hại như vậy... Tại sao phải mua danh ngạch, mà không đi tranh đoạt ah!"),t("br"),t("br"),n._v("Tranh đoạt lời nói... Mười vị trí đầu nơi nào còn có bọn họ chuyện gì."),t("br"),t("br"),n._v("Đối mặt một màn này, Ngô Phương Thanh tuy là không lên tiếng, nhưng trong lòng càng thêm khẳng định."),t("br"),t("br"),n._v("Bất luận từ chỗ nào một chút, cũng có thể nhìn ra, những người này thiên phú từng cái cũng rất cao, loại người này xuất hiện một cái, xem như gặp may mắn, một chút toát ra nhiều như vậy, muốn nói không có điểm vấn đề, đánh chết cũng không tin."),t("br"),t("br"),n._v("Bởi vậy, đối phương cho dù không phải Đế Quân huyết mạch, cũng nhất định sẽ không kém."),t("br"),t("br"),n._v("“Như vậy có thể hay không chứng minh, Trương Huyền thiếu gia học sinh, có tư cách ngồi ở chỗ này?”"),t("br"),t("br"),n._v("Mỉm cười, Ngô Phương Thanh nói."),t("br"),t("br"),n._v("“Có thể...” Đám người lại nói không ra lời nói tới."),t("br"),t("br"),n._v("Tuy thần sơn, bị truyền tống đến sườn núi, đạt được Thần Linh chi khí cơ hội sẽ lớn hơn một chút, nhưng cuối cùng vẫn muốn nhìn thực lực."),t("br"),t("br"),n._v("Thực lực càng mạnh, lấy được khả năng cũng liền càng lớn."),t("br"),t("br"),n._v("Trước mắt đám người này, mỗi một cái đều như vậy lợi hại, coi như bọn họ hậu bối muốn tranh, cũng không tranh nổi, còn không bằng làm thuận nước giong thuyền, nếu không, tại bên trong ngọn thần sơn động thủ, cũng không có cách nào ngăn cản."),t("br"),t("br"),n._v("“Nếu tất cả mọi người không có vấn đề, thời gian cũng không còn nhiều lắm, chuẩn bị bắt đầu đi!”"),t("br"),t("br"),n._v("Thấy mọi người lại không dị nghị, Ngô Phương Thanh không nói thêm lời, đứng dậy, thành chủ lệnh lập tức huyền phù tại phía trước, nhẹ nhàng điểm một cái, một đạo trắng tinh quang mang trong nháy mắt đem trọn cái quảng trường bao phủ ở bên trong."),t("br"),t("br"),n._v("Ngay sau đó, một cái cánh cửa khổng lồ, bất ngờ xuất hiện tại quảng trường chính giữa vị trí."),t("br"),n._v(" Mọi người đi tới trước mặt."),t("br"),t("br"),n._v("“Trương Huyền thiếu gia, để cho bọn họ tiên tiến đi!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Ngô Phương Thanh nói."),t("br"),t("br"),n._v("Trương Huyền nhìn lại, thấy cửa chỉ là cái truyền tống trận, cũng không nguy hiểm, lúc này mới thở phào nhẹ nhõm, chào hỏi Triệu Nhã đám người, đi vào trước mặt."),t("br"),t("br"),n._v("“Mỗi người năm cái đan dược, trong này ẩn chứa lực lượng của ta, có thể giúp các ngươi đột phá ràng buộc! Gặp được nguy hiểm cũng có thể có chỗ trợ giúp!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, đưa qua mấy bình Thần Nguyên đan."),t("br"),t("br"),n._v("Thứ này trong tay hắn đã không có quá lớn trợ giúp, còn không bằng ban cho đám người, một khi đạt được Thần Linh chi khí, thành công đột phá, có thứ này, cũng có thể xung kích cảnh giới cao hơn."),t("br"),t("br"),n._v("“Đa tạ lão sư (thiếu gia, Huyền nhi)!” Đám người đồng thời gật đầu."),t("br"),t("br"),n._v("Ngụy Thần tranh đoạt, cũng không có gì có thể giao phó, Trương Huyền không hề phí lời, đám người nối đuôi nhau mà vào."),t("br"),t("br"),n._v("Đi vào cửa về sau, lập tức biến mất không thấy gì nữa, sau một khắc đã xuất hiện tại một cái cao lớn trên dãy núi."),t("br"),t("br"),n._v("Trương Huyền bởi vì đã đột phá Thần Linh, lại không cách nào đi vào, chỉ có thể đợi ở bên ngoài chờ đợi."),t("br"),t("br"),n._v("Trên quảng trường Ngụy Thần thiếu niên, lần lượt tiến lên, chưa tới một canh giờ, một ngàn hai trăm người, liền toàn bộ đi vào, cửa lung lay vụt qua, chậm chạp biến mất."),t("br"),t("br"),n._v("“Truyền tống trận sau một ngày sẽ lần nữa mở ra... Chư vị không vội vã lời nói, phủ thành chủ đã chuẩn bị xong tiệc rượu, vừa vặn tại chỗ này chờ đợi!”"),t("br"),t("br"),n._v("Ngô Phương Thanh cười nói."),t("br"),t("br"),n._v("“Tốt!” Đám người đồng thời gật đầu."),t("br"),t("br"),n._v("Một ngày thời gian mà thôi, đối với tu luyện giả tới nói, không tính là gì."),t("br"),t("br"),n._v("Chẳng muốn trở về, Trương Huyền đi theo đám người sau lưng, đi vào phủ thành chủ."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thông qua cửa, thân thể vụt qua, Triệu Nhã lần nữa đứng vững thân hình, phát hiện đã đi tới một tòa cao vút trong mây trên dãy núi."),t("br"),t("br"),n._v("Cùng trước đó Linh Cơ sơn hoàn toàn khác biệt, nơi này linh khí sung túc, thảm thực vật tươi tốt, khắp nơi tràn đầy sinh cơ, mà không phải suy yếu."),t("br"),t("br"),n._v("“Trước tìm Thần Linh chi khí...”"),t("br"),t("br"),n._v("Biết tìm tới Thần Linh chi khí mới có thể đột phá, thân thể mềm mại vụt qua, thẳng tắp hướng đỉnh núi phương hướng bay lượn mà đi."),t("br"),t("br"),n._v("Thông qua quan sát, nàng đã phát hiện, càng hướng lên trên linh khí càng nồng đậm, Thần Linh chi khí nhất định cũng càng dễ dàng tìm kiếm."),t("br"),t("br"),n._v("Bay vút mấy trăm mét, nghe được trên không “Ô ô” vang lên tiếng gió, thân thể vụt qua, trốn ở một cây đại thụ sau lưng, hướng âm thanh vang lên phương hướng nhìn lại, một đạo mũi tên bắn tại cách đó không xa."),t("br"),t("br"),n._v("Ngay sau đó, một cái màu trắng tựa như thỏ đồng dạng đồ vật, nhận lấy kinh hãi, nhanh chóng vọt lên phía trước."),t("br"),t("br"),n._v("“Là cái này... Thần Linh chi khí?”"),t("br"),t("br"),n._v("Triệu Nhã ánh mắt sáng lên."),t("br"),t("br"),n._v("Tới thời điểm, nghe Tôn Cường chuyên môn bàn giao, Thần Linh chi khí, tại thần sơn vô cùng có khả năng huyễn hóa thành sinh mệnh, không tốt như vậy bắt, không nghĩ tới thoạt nhìn cùng thỏ tương đương."),t("br"),t("br"),n._v("“Nắm lấy nó...”"),t("br"),t("br"),n._v("Một tiếng la hét, hai bóng người vội vã đi vào trước mặt."),t("br"),t("br"),n._v("Trước đó mũi tên, chính là từ bọn họ bắn ra."),t("br"),t("br"),n._v("Sưu sưu sưu sưu!"),t("br"),t("br"),n._v("Lại liên tục mấy mũi tên, thỏ bộ dáng Thần Linh chi khí bị triệt để đánh trúng, nằm trên mặt đất vùng vẫy hai cái, biến thành một đạo màu vàng kim thể khí."),t("br"),t("br"),n._v("“Ta tới trước đi!” Bắn tên một vị, tràn đầy ngạc nhiên đem thể khí cầm ở lòng bàn tay, há miệng nuốt xuống."),t("br"),t("br"),n._v("Ngay sau đó, Triệu Nhã cũng cảm giác được đối phương lực lượng trong cơ thể, cấp tốc tăng thêm, càng ngày càng mạnh, xem ra, bất cứ lúc nào cũng sẽ đột phá Ngụy Thần ràng buộc, trở thành chân chính Thần Linh."),t("br"),t("br"),n._v("“Ta cũng muốn nhanh lên một chút...” Thấy săn bắt Thần Linh chi khí, cũng không phức tạp, Triệu Nhã không tại chần chừ, tiếp tục hướng trên núi bay lượn."),t("br"),t("br"),n._v("Trên đường đi, đồng dạng gặp không ít thỏ bộ dáng Thần Linh chi khí, chẳng qua đều có người săn bắt, cũng lười ra tay cướp đoạt."),t("br"),t("br"),n._v("Rất mau tới đến đỉnh núi."),t("br"),t("br"),n._v("Nơi này áp lực rõ ràng so phía dưới mạnh mẽ hơn không ít, có thể đi lên người, cũng thiếu, dạo qua một vòng, cũng không phát hiện những người khác, Triệu Nhã nhíu nhíu mày, căn cứ địa mặt để lại đầu mối, tìm kiếm Thần Linh chi khí lưu lại ấn ký."),t("br"),t("br"),n._v("Đi lên thời điểm, chuyên môn quan sát, biết thứ này làm sao tìm."),t("br"),t("br"),n._v("“Cái này dấu vết cùng trước đó có chút không đúng, lớn hơn một chút, chẳng qua... Không quan trọng!”"),t("br"),t("br"),n._v("Rất nhanh, để nàng tìm được một chỗ không giống địa phương."),t("br"),t("br"),n._v("Lực lượng toàn thân vận chuyển, thuần âm chi thể phát huy đến cực hạn, Triệu Nhã dựa theo dấu vết phương hướng, cấp tốc tiến lên."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Dấu vết cuối cùng, một đầu Bạch Hổ chui ra, tỏa ra khí tức kinh người."),t("br"),t("br"),n._v("“Cái này...” Triệu Nhã sững sờ."),t("br"),t("br"),n._v("Cái khác Thần Linh chi khí đều là thỏ, này làm sao toát ra như vậy một đầu đại gia hỏa?"),t("br"),t("br"),n._v("Bất quá, lúc này cũng không quản được nhiều như vậy, tay ngọc khẽ đảo, xông tới, Thiên đạo tinh giản võ kỹ tùy ý đi ra, thân thể mềm mại không ngừng lấp lóe."),t("br"),t("br"),n._v("Thân là Trương Huyền rất nhiều đệ tử đại sư tỷ, Triệu Nhã từ trước tới nay đối với mình nghiêm khắc yêu cầu, lại thêm thể chất đặc thù gia trì, cùng cấp bậc đồng môn đều không người có thể vượt qua, Trịnh Dương cũng không phải đối thủ, lúc này phát huy toàn bộ thực lực, tiếng gió rít gào, trong cơ thể lực lượng vây quanh tại bốn phía, tựa như gió lốc."),t("br"),t("br"),n._v("“Cái tên này thật lợi hại...”"),t("br"),t("br"),n._v("Chiến đấu một hồi, trên đầu toát ra mồ hôi lạnh."),t("br"),t("br"),n._v("Trước đó thỏ, nàng nhìn, chỉ cần đuổi theo, một ngón tay liền có thể bóp chết, bằng không thì cũng sẽ không bị mũi tên bắn giết, nhưng trước mắt này cái Bạch Hổ, so với nàng thực lực đều cường đại hơn không ít, cùng bình thường hạ phẩm Thần Linh so, không kém chút nào, thậm chí còn còn hơn!"),t("br"),t("br"),n._v("Nói cách khác, bây giờ tại cùng một cái chân chính Thần Linh, tại chiến đấu!"),t("br"),t("br"),n._v("Dưới tình huống bình thường, rất khó vượt qua, nhưng lúc này tên đã trên dây, lại không có biện pháp nào khác, răng cắn chặt, há miệng ra, đem lão sư ban cho đan dược nuốt xuống."),t("br"),t("br"),n._v("Dược vật vào miệng tan đi, ẩn chứa trong đó Thiên đạo chi khí, lập tức chạy toàn thân, chiến đấu sinh ra mệt mỏi, lập tức biến mất."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2145"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2147"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);