(window.webpackJsonp=window.webpackJsonp||[]).push([[129],{482:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1092-to-su-toi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1092-to-su-toi"}},[n._v("#")]),n._v(" Chương 1092: Tổ sư tới")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Truyền thế độc điển?”"),i("br"),i("br"),n._v("Bạch Đình, Hải Minh Kim đồng thời giật nảy mình."),i("br"),i("br"),n._v("Nghe nói thứ này là khai phái tổ sư tự tay viết, đem một đời nghiên cứu dùng độc bí pháp, ghi lại ở phía trên, là độc sư muốn lấy được nhất không có con đường thứ hai, chỉ là... Nương theo tổ sư đi về cõi tiên, đã sớm biến mất."),i("br"),i("br"),n._v("Tổ sư sau khi chết, lục tục ngo ngoe lại kế nhiệm không ít điện chủ, chỉ bất quá, đều bởi vì không được đến truyền thừa, khó mà phục chúng, đưa đến Độc điện càng thêm hỗn loạn."),i("br"),i("br"),n._v("Vốn cho rằng thứ này, lại khó tìm, không nghĩ tới Hữu hộ pháp Thẩm Khuyết tìm trở về!"),i("br"),i("br"),n._v("“Đúng vậy a, có thứ này, quả thực có thể danh chính ngôn thuận kế vị, không ai dám nói cái gì, chỉ là... Quên đi, không nói những thứ này, gặp được sư phụ, ngươi tự nhiên là sẽ minh bạch!” Còn muốn nói nhiều cái gì, Nguyên Lăng dừng lại một chút, nuốt trở vào, vẫy vẫy tay, đi đầu bay tới đằng trước."),i("br"),i("br"),n._v("Hai người theo sát phía sau."),i("br"),i("br"),n._v("Đều là vượt qua Thánh vực tứ trọng cường giả, tốc độ cực nhanh, thời gian không dài, liền đến đến tới gần trong thành thị ở giữa rộng lớn phủ đệ."),i("br"),i("br"),n._v("Nguyên Lăng phía trước dẫn đường, vừa đi vào lão sư năm đó giảng bài đại điện, liền nghe đến một hồi tiếng cãi vã vang lên."),i("br"),i("br"),n._v("“Hắn đây chính là cầm lông gà làm lệnh tiễn! Dựa vào cái gì muốn cho lão sư tới? Cùng lắm thì liều mạng với bọn hắn!”"),i("br"),i("br"),n._v("“Quả thực khinh người quá đáng, chúng ta không thể ngồi mà chờ chết!”"),i("br"),i("br"),n._v("“Hắn nói là truyền thế độc điển, liền là truyền thế độc điển? Dù ai cũng không cách nào xác nhận, ta nhớ được năm đó hắn cùng lão sư liền không hợp nhau, chỉ sợ cố ý nói như vậy, là muốn diệt trừ đối lập, thành công kế vị!”"),i("br"),i("br"),n._v("“Hữu hộ pháp bản thân liền bụng dạ hẹp hòi, lần này có như thế lớn dựa dẫm, khẳng định muốn ra tay...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Đi vào gian phòng, lập tức nhìn thấy mấy chục cái độc sư đứng tại trong đó, nguyên một đám lòng đầy căm phẫn."),i("br"),i("br"),n._v("Bạch Đình nhìn sang, chính là trước kia lúc tu luyện sư huynh, sư đệ, hầu như tất cả đều đạt đến bát tinh độc sư thực lực."),i("br"),i("br"),n._v("Trong đại sảnh ở giữa, ngồi một cái lão giả, một thân tố y, khí tức yên lặng như giếng, hít thở như có như không, giống như không tồn tại đồng dạng."),i("br"),i("br"),n._v("Hải Minh Kim dùng thần thức lặng lẽ dò xét thoáng cái, lập tức hoảng sợ."),i("br"),i("br"),n._v("Thần thức cảm ứng xuống, trước mắt không có vật gì, không hề phát hiện thứ gì."),i("br"),i("br"),n._v("“Thực lực thật là mạnh...”"),i("br"),i("br"),n._v("Con ngươi co rụt lại."),i("br"),i("br"),n._v("  Trước mắt vị này thực lực, đã vượt ra khỏi tưởng tượng ra, đạt đến một loại quỷ thần khó lường cảnh giới, hắn loại cấp bậc này thần thức, căn bản là không cách nào cảm thấy."),i("br"),i("br"),n._v("Đối mặt loại này cường giả, đối phương có thể không cần động thủ, chỉ cần một cái ý niệm, hắn liền sẽ hồn phi phách tán, chống lại không được."),i("br"),i("br"),n._v("Chớ nói chi là dùng độc!"),i("br"),i("br"),n._v("Đáng sợ!"),i("br"),i("br"),n._v("“Lão sư, Bạch Đình sư đệ đến rồi!”"),i("br"),i("br"),n._v("Không để ý tới mọi người cãi lộn, Nguyên Lăng tiến về phía trước một bước."),i("br"),i("br"),n._v("“Ừm!” Lão giả nhẹ gật đầu, nhìn lại: “Đi qua sự kiện kia về sau, ngươi chán nản, ta còn tưởng rằng, lại không cách nào đột phá, không nghĩ tới thành bát tinh độc sư, cũng không uổng công ta một phen chỉ bảo!”"),i("br"),i("br"),n._v("“Lão sư...”"),i("br"),i("br"),n._v("Hốc mắt một đỏ, Bạch Đình tiến về phía trước một bước quỳ gối trước mặt."),i("br"),i("br"),n._v("Rời khỏi tổng bộ thời gian lâu dài, một mực không mặt mũi nào tới, chân chính gặp được sư phụ, loại cảm tình này cũng lại kìm nén không được."),i("br"),i("br"),n._v("“Được rồi, đều là bát tinh độc sư, đừng làm những này tiểu nhi nữ trang!” Lão giả cười cười."),i("br"),i("br"),n._v("“Đúng!” Bạch Đình đứng dậy, nghi ngờ ngẩng đầu nhìn tới: “Lão sư, vừa rồi chư vị sư huynh đệ nói... Muốn lão sư ngài tới... Có ý tứ gì?”"),i("br"),i("br"),n._v("Mọi người như thế lòng đầy căm phẫn, khẳng định không phải chuyện gì tốt."),i("br"),i("br"),n._v("“Không phải cái đại sự gì, đã trở về, liền an tâm khảo hạch, khảo hạch xong, trở về đi! Độc điện đã không phải là trước kia cái kia yên lặng chi địa, sau này chỉ sợ khó tránh khỏi gió tanh mưa máu, có thể trốn bao xa trốn bao xa đi!” Lão giả khoát tay áo, than thở một tiếng."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Bạch Đình cau mày, không hiểu ra sao."),i("br"),i("br"),n._v("Lão sư là tứ đại Tôn giả một trong, mới mở miệng cứ như vậy chán nản, đến cùng xảy ra chuyện gì?"),i("br"),i("br"),n._v("Dựa theo tình huống bình thường, Thẩm Khuyết nhận được truyền thế độc điển, trở thành tân nhiệm điện chủ, không phải là đáng giá chúc mừng sự tình ư?"),i("br"),i("br"),n._v("“Thẩm hộ pháp nhận được truyền thế độc điển, muốn kế vị điện chủ sự tình, chắc hẳn ngươi hẳn phải biết đi!” Thấy hắn không hiểu, một cái bát tinh độc sư đi tới trước mặt."),i("br"),i("br"),n._v("“Đúng!” Bạch Đình nhẹ gật đầu."),i("br"),i("br"),n._v("Chuyện này mới vừa nghe Nguyên Lăng độc sư nói qua."),i("br"),i("br"),n._v("“Truyền thế độc điển, là khai phái tổ sư lưu lại, có mang kịch độc, muốn mở ra, nhất định phải lấy  phương thức mới có thể hoàn thành! Thẩm hộ pháp để lão sư, đi làm chuyện này...” Bát tinh độc sư cắn răng."),i("br"),i("br"),n._v("Bạch Đình con ngươi co rụt lại."),i("br"),i("br"),n._v("Độc sư rất nhiều bí tịch, đều ưa thích dùng kịch độc phong ấn, muốn phá vỡ, liền cần nắm giữ đối ứng giải độc bản lĩnh, nếu như không có, cũng chỉ có thể lấy mệnh tới lấp."),i("br"),i("br"),n._v("Nói cách khác, tìm người hấp thu phía trên kịch độc, một cái không đủ, hai cái, hai người không đủ ba cái... Nhân số nhiều, độc số lượng có hạn, luôn có thể bị hấp thu sạch sẽ."),i("br"),i("br"),n._v("Cũng chính là cái gọi là !"),i("br"),i("br"),n._v("Mạng người nuôi nấng, mười phần tàn khốc, cũng mười phần kỳ lạ, có thể nói, bỏ qua sinh mệnh tôn nghiêm. Nguyên nhân chính là như vậy, độc sư mới bị định nghĩa vì tà ma ngoại đạo, vì chính phái chức nghiệp chỗ khinh thường."),i("br"),i("br"),n._v("“Tại sao là lão sư...” Bạch Đình cắn răng."),i("br"),i("br"),n._v("Lão sư thế nhưng là tứ đại Tôn giả một trong, dựa vào cái gì để hắn đi làm loại sự tình này?"),i("br"),i("br"),n._v("“Năm đó lão sư cùng vị này Thẩm hộ pháp từng có khúc mắc, mà truyền thế độc điển bên trên kịch độc thực sự quá mức mạnh mẽ, người bình thường liền tới gần đều làm không được, muốn loại bỏ độc tính, chỉ có cửu tinh cấp bậc độc sư... Nguyên nhân chính là như vậy, hắn cố ý cầm lão sư khai đao, xác lập uy phong, còn không có kế vị, liền muốn để Độc điện tất cả mọi người, biết sự lợi hại của hắn!”"),i("br"),n._v(" Vị này độc sư cắn răng."),i("br"),i("br"),n._v("Mạng người nuôi nấng mặc dù có thể loại bỏ độc tính, cũng phải nhìn thực lực, thực lực không đủ, cho dù xông đi lên một vạn cái, hai vạn cái, đều không có bất kỳ chỗ dùng nào."),i("br"),i("br"),n._v("Truyền thế Độc điện chính là khai phái tổ sư lưu lại, phía trên kịch độc, chỉ có đạt tới cửu tinh cấp bậc độc sư mới có thể phá giải, đổi lại những người khác, bên trên nhiều hơn nữa đều không dùng."),i("br"),i("br"),n._v("“Đã hắn công báo tư thù, lão sư hoàn toàn có thể không cần đáp ứng!” Bạch Đình vội vàng mở miệng, tràn đầy cuống cuồng."),i("br"),i("br"),n._v("“Là có thể không cần đáp ứng, nhưng hắn... Lấy đại nghĩa danh nghĩa tiến hành áp chế!”"),i("br"),i("br"),n._v("“Đại nghĩa?”"),i("br"),i("br"),n._v("“Ừm, hắn hướng tất cả độc sư hứa hẹn, chỉ cần mở ra truyền thế độc điển, chọn đem bên trong dễ dàng học tập nội dung, truyền thụ đi ra, để Độc điện thực lực tổng hợp bạo tăng... Coi đây là áp chế, trực tiếp đem lão sư đưa vào tuyệt lộ! Hiện tại, toàn bộ tổng bộ độc sư, đều đi tới nơi này, trên danh nghĩa là muốn quan sát kế vị đại điển, trên thực tế có không ít người đang giám thị, sợ lão sư chạy trốn...”"),i("br"),i("br"),n._v("Vị này bát tinh độc sư, con mắt thấu đỏ."),i("br"),i("br"),n._v("Hỗn loạn biết trung thần, lâu ngày mới rõ lòng người."),i("br"),i("br"),n._v("Bình thường cùng lão sư rất tốt, thậm chí không ngừng nịnh bợ độc sư bọn họ, hiện tại nguyên một đám có thể trốn bao xa trốn bao xa, chỉ còn lại có bọn họ những học sinh này, thủ tại chỗ này."),i("br"),i("br"),n._v("Bạch Đình nắm đấm xiết chặt."),i("br"),i("br"),n._v("Quả thực."),i("br"),i("br"),n._v("Lấy điều kiện này, dụ dỗ cái khác độc sư, lão sư liền thành người hy sinh, không đi hi sinh lời nói, ngược lại sẽ trở thành mục tiêu công kích."),i("br"),i("br"),n._v("“Được rồi, chuyện này ta đã có quyết định, chờ sau khi ta chết, các ngươi cũng đừng tới nơi này, có thể trốn bao xa trốn bao xa đi, miễn cho bị đứt đoạn truyền thừa...” Đánh gãy vị này độc sư lời nói, ở giữa lão giả khoát tay áo."),i("br"),i("br"),n._v("Hắn còn sống, không ai dám thế nào, một khi chết rồi, những đệ tử này, tám chín phần mười đều sẽ bị đồ sát sạch sẽ."),i("br"),i("br"),n._v("Độc điện không giống Danh Sư đường như vậy chú ý quy củ, tất cả dựa vào thực lực, không thực lực, chỉ có bị giết phần."),i("br"),i("br"),n._v("“Lão sư...”"),i("br"),i("br"),n._v("Nghe nói như thế, mọi người một hồi thần thương, Nguyên Lăng cũng hốc mắt ửng hồng, đang không biết nói cái gì cho phải, liền nghe đến Bạch Đình thanh âm vang lên."),i("br"),i("br"),n._v("“Thật ra thì... Lão sư, ngươi không cần hi sinh, Thẩm hộ pháp, muốn làm điện chủ... Cũng không dễ dàng như vậy!”"),i("br"),i("br"),n._v("“Ừm?”"),i("br"),i("br"),n._v("Nghe nói như thế, mọi người đồng loạt quay đầu lại, ngay cả lão giả cũng không nhịn được mày nhăn lại."),i("br"),i("br"),n._v("“Bạch sư đệ cớ gì nói ra lời ấy?” Nguyên Lăng hỏi."),i("br"),i("br"),n._v("“Cũng không có gì, đổi lại trước kia, vị này Thẩm hộ pháp, tìm tới truyền thế độc điển, quả thực có thể trở thành điện chủ, không ai dám nói cái gì, nhưng bây giờ... Chỉ sợ không làm được!”"),i("br"),i("br"),n._v("Cười nhạt một tiếng, Bạch Đình lộ ra vẻ tự tin."),i("br"),i("br"),n._v("“Nắm giữ truyền thế độc điển đều không thành được điện chủ? Cái này... Làm sao có thể?” Nguyên Lăng sững sờ ngay tại chỗ."),i("br"),i("br"),n._v("Những người khác cũng đều tràn đầy mê hoặc, khó tin."),i("br"),i("br"),n._v("Truyền thế độc điển là cái gì? Khai phái tổ sư vật lưu lại, cùng Khổng sư Xuân Thu đại điển tương đồng, thuộc về chí cao vô thượng bảo vật, chỉ cần tìm được, chẳng khác nào nắm giữ đại nghĩa, có thể được đến tất cả độc sư công nhận, đừng nói trở thành điện chủ, cho dù để cho người ta tự sát, đều không có phương pháp."),i("br"),i("br"),n._v("Bọn họ lão sư, hiện tại liền chịu đến loại này bức bách."),i("br"),i("br"),n._v("Người trước mắt này, lại còn nói, cái này đều không thành được điện chủ... Vậy làm sao mới có tư cách?"),i("br"),i("br"),n._v("“Đúng vậy a, đến cùng chuyện gì xảy ra? Truyền thế độc điển thế nhưng là khai phái tổ sư lưu lại vô thượng bảo vật, nhận được thứ này, chẳng khác nào nhận được quyền lên tiếng...”"),i("br"),i("br"),n._v("Lời mới vừa nói vị kia bát tinh độc sư nhịn không được lắc đầu: “Muốn cho hắn không đảm đương nổi điện chủ, trừ phi mời đến khai phái tổ sư!”"),i("br"),i("br"),n._v("“Lục sư huynh nói không sai, ta đích xác mời tới... Khai phái tổ sư!” Bạch Đình nhẹ nhàng cười một tiếng."),i("br"),i("br"),n._v("“Khai phái tổ sư?”"),i("br"),i("br"),n._v("“Bạch sư đệ, ngươi hẳn là nói đùa sao, khai phái tổ sư sớm đã đi về cõi tiên vài vạn năm, đi nơi nào tìm?”"),i("br"),i("br"),n._v("“Cái này một chút cũng không tốt cười...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Mọi người đầu tiên là sững sờ, lập tức tất cả đều lắc đầu."),i("br"),i("br"),n._v("Nếu như nói tìm được tổ sư bảo vật gì, bọn họ tin tưởng, có thể tìm được tổ sư... Đây không phải nằm mơ sao?"),i("br"),i("br"),n._v("“Quả thực là tổ sư, nàng chỉ còn lại có một đạo tàn hồn, bám vào đến cô gái này trên thân...” Bạch Đình về phía sau một ngón tay."),i("br"),i("br"),n._v("Mọi người lúc này mới phát hiện, hắn dùng chân khí bao lấy nữ hài, nhẹ nhàng thả xuống, triệt khai chân khí, Ngụy Như Yên thể nội độc khí lập tức tán dật ra, để mọi người kìm lòng không được lui về phía sau mấy bước."),i("br"),i("br"),n._v("“Đây là... Tiên Thiên độc thể?”"),i("br"),i("br"),n._v("Mọi người còn không có kịp phản ứng, ở giữa lão giả Lâm Giang Hải, biến sắc, tràn đầy khó tin."),i("br"),i("br"),n._v("Tiên Thiên độc, là tu luyện độc sư vô thượng bảo thể, từ khi tổ sư rời khỏi về sau, toàn bộ Độc điện vài vạn năm tới cũng không có xuất hiện qua như nhau, nằm mơ đều không có nghĩ đến, xuất hiện ở đây."),i("br"),i("br"),n._v("“Đúng, không riêng gì Tiên Thiên độc thể, vẫn là độc hồn thể!” Bạch Đình gật đầu."),i("br"),i("br"),n._v("“Cái này...” Lâm Giang Hải thân thể run lên: “Đến cùng chuyện gì xảy ra? Cô gái này làm sao ngủ mê không tỉnh?”"),i("br"),i("br"),n._v("“Là như vậy...”"),i("br"),i("br"),n._v("Bạch Đình đem làm sao gặp được “Khai phái tổ sư”, lại như thế nào đáp ứng đối phương, đem nữ hài mang tới chuyện, nói rõ chi tiết một lần."),i("br"),i("br"),n._v("“Ngươi nói... Cô gái này là khai phái tổ sư linh hồn phụ thể? Hiện tại chỉ là không chịu nổi độc hồn thể mang tới áp lực, lâm vào hôn mê?”"),i("br"),i("br"),n._v("Lâm Giang Hải thân thể cứng đờ, khó mà tin được."),i("br"),i("br"),n._v("“Không sai, không tin, ngươi có thể nhìn nàng một cái cho chúng ta viên đan dược này!”"),i("br"),i("br"),n._v("Cổ tay khẽ đảo, Bạch Đình đem một cái bình ngọc lấy ra ngoài."),i("br"),i("br"),n._v("Nhẹ nhàng cười một tiếng, Bạch Đình cổ tay khẽ đảo, đem “Tổ sư” cho một cái giải độc đan đưa tới."),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1100"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1102"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);