(window.webpackJsonp=window.webpackJsonp||[]).push([[1291],{1646:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2133-chu-thien-chan-đong-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2133-chu-thien-chan-đong-ha"}},[n._v("#")]),n._v(" Chương 2133: Chư thiên chấn động (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Kiếm Thần thiên, toàn bộ Thần giới, loại trừ Tự Tại thiên, thần bí nhất Kiếm Lư."),t("br"),t("br"),n._v("Mặt khác Đế Quân cung điện, tráng lệ, to lớn hùng vĩ, mà ở trong đó, chỉ là cái phổ thông nhà tranh, thoạt nhìn cũng không thu hút."),t("br"),t("br"),n._v("“Đế Quân...”"),t("br"),t("br"),n._v("Một người trung niên đi vào trước mặt, muốn nói cái gì, lời nói còn không có lối ra, liền bị đánh gãy: “Ta đã biết, thần sơn có am hiểu kiếm pháp thiên tài xuất hiện!”"),t("br"),t("br"),n._v("“Vâng...” Trung niên nhân liền vội vàng gật đầu."),t("br"),t("br"),n._v("“Kiếm pháp của hắn ta xem, là rất kinh diễm, thậm chí có khác với chúng ta, chẳng qua... Không phải ta Kiếm Lư muốn chờ người!”"),t("br"),t("br"),n._v("Trong túp lều âm thanh thản nhiên nói."),t("br"),t("br"),n._v("“Thế nhưng là... Phong Vương chuông gõ...” Trung niên nhân sửng sốt một chút, vội vàng nói: “Chúng ta không nhanh chút, bị địa phương khác chiêu đi, lần tiếp theo linh khí thuỷ triều, chúng ta Kiếm Lư, lấy được danh ngạch càng ít, đất phong cũng sẽ nhận tước đoạt...”"),t("br"),t("br"),n._v("“Danh ngạch ít liền ít đi, đất phong tước đoạt cũng không có gì, chúng ta muốn là một loại độc lập thuộc về mình kiếm ý cùng lĩnh ngộ, cũng không phải là cứng nhắc học tập!”"),t("br"),t("br"),n._v("Trong túp lều âm thanh, nói: “Học cho dù tốt cũng chỉ là bắt chước, linh khí thuỷ triều chuyện, không cần lo lắng, không có gì bất ngờ xảy ra, người của chúng ta, đã xuất hiện, ta sẽ tự mình đi tìm hắn!”"),t("br"),t("br"),n._v("“Vâng...”"),t("br"),t("br"),n._v("Thấy Đế Quân có tính toán của mình, trung niên nhân không nói thêm lời, khom người lui xuống."),t("br"),t("br"),n._v("“Ai!”"),t("br"),t("br"),n._v("Trung niên nhân rời khỏi, trong túp lều vang lên thở dài một tiếng, giống như là tại cảm xúc, lại tràn đầy sự khó hiểu cùng nghi vấn: “Ngươi làm như vậy, đến cùng là có đúng hay không?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mây mù vây quanh trên núi cao, một cái vóc người cao to lão giả, nhìn về phía trước mắt ngồi rất nhiều đệ tử."),t("br"),t("br"),n._v("Huyền diệu ngôn ngữ, từ trong miệng chảy xuôi mà ra, nghe vào trong tai mọi người, tựa như tiên nhạc, tu vi cũng tại đây chút trong lời nói, không ngừng tiến bộ."),t("br"),t("br"),n._v("“Được rồi, ngày hôm nay liền đến nơi này!”"),t("br"),t("br"),n._v("Đột nhiên, lão giả ngừng lại, nhìn về phía một cái phương hướng."),t("br"),t("br"),n._v("“Lão sư, chuyện gì xảy ra?” Một người trung niên nhìn ra không đúng, ôm quyền khom người, đi đủ lễ tiết, hỏi."),t("br"),t("br"),n._v("Tình huống bình thường, lão sư đều sẽ truyền thụ xong một cái kiến thức điểm, mới kết thúc chương trình học, hiện tại mới nói một nửa liền dừng lại, rõ ràng có chuyện phát sinh."),t("br"),t("br"),n._v("“Tự Tại thiên vị kia, đã đã đặt xong thời gian, tháng sau mười lăm cùng ta quyết đấu!” Lão giả mỉm cười, hình như nói là người khác, mà không phải chính hắn."),t("br"),t("br"),n._v("Mảy may không có vì quyết đấu, mà cảm thấy áp lực."),t("br"),t("br"),n._v("“Tự Tại thiên, vì Cửu Thiên đứng đầu, quản lý vạn giới... Lão sư khiêu chiến vị kia Đế Quân, ta sợ...” Trung niên nhân trong mắt tràn đầy lo lắng."),t("br"),t("br"),n._v("Tuy là đối lão sư thực lực, vô cùng tự tin, có thể Tự Tại thiên dù sao cũng là Thần giới ngày đầu tiên, trong đó Đế Quân, cũng mệnh danh Thần giới đệ nhất nhân... Cùng loại này cường giả luận võ, thực sự có chút yên lòng không dưới."),t("br"),t("br"),n._v("Mấu chốt nhất là, hai người khoảng ở dưới sinh tử đấu, cùng trước đó điểm đến là dừng, hoàn toàn khác biệt."),t("br"),t("br"),n._v("“Không sao cả!” Lão giả trên mặt vẫn như cũ mang theo vui vẻ, vuốt râu: “Nếu là quyết đấu, tự nhiên có thắng có bại, không có người sẽ một mực thắng, cũng không có người sẽ một mực bại! Tận lực liền tốt.”"),t("br"),t("br"),n._v("“Nhưng... Đối phương vì cùng ngươi chiến đấu, chuyên môn hạ giới, đi tìm Xuân Thu đại điển, mục đích đúng là vì đối lão sư giải càng nhiều, biết thực lực của ngươi... Ta sợ, hiện tại đáp ứng, đã biết lão sư sơ hở, cùng cấp bậc cao thủ, một khi không may xuất hiện, nguy hiểm cực lớn...” Trung niên nhân ánh mắt lộ ra vẻ lo lắng."),t("br"),t("br"),n._v("Lão sư khiêu chiến Tự Tại thiên vị kia, vừa mới bắt đầu đối phương một mực không có đáp ứng, về sau vị kia đi một chuyến hạ giới, trở về liền xác định ngày, rõ ràng đã có chuẩn bị."),t("br"),t("br"),n._v("Cao thủ đối chiêu, chỉ kém chút xíu, ngộ nhỡ lão sư thiếu hụt bị phát giác, từ đó nhằm vào công kích, liền rất nguy hiểm."),t("br"),t("br"),n._v("“Tìm tới thiếu hụt lại như thế nào? Muốn đánh bại ta, không có dễ dàng như vậy!” Đầu lâu nâng lên, lão giả lộ ra nồng đậm tự tin."),t("br"),t("br"),n._v("Cùng nhau đi tới, khiêu chiến nhiều như vậy Đại Đế thành công, thực lực của hắn, sớm đã đi đến Thần giới đỉnh phong nhất."),t("br"),t("br"),n._v("“Lão sư thực lực, chúng ta tự nhiên tin tưởng và nghe theo, chỉ là học sinh không hiểu, vì sao phải cứ cùng đối phương giao đấu, mà vì sao, lập xuống sống chết ước định...”"),t("br"),t("br"),n._v("Trung niên nhân có chút không rõ ràng cho lắm."),t("br"),t("br"),n._v("Lão sư nhận được tám Đế công nhận, Thiên Túng Đại Đế danh tiếng, đã danh xứng với thực, Thần giới người người công nhận, vì sao còn phải cứ cùng vị này Tự Tại thiên Đế Quân tỷ thí?"),t("br"),t("br"),n._v("Quan trọng lập xuống vẫn là sống chết khoảng?"),t("br"),t("br"),n._v("“Đây là định số, chúng ta không cách nào tránh đi, các ngươi không hiểu...” Lão giả lắc đầu: “Được rồi, tháng sau tỷ thí, nếu như ta không may vẫn lạc, các ngươi liền yên tĩnh đợi, đừng có động tác khác, chờ đợi truyền nhân của ta xuất hiện, nghe hắn dặn dò!”"),t("br"),t("br"),n._v("“Truyền nhân? Lão sư truyền nhân?” Trung niên nhân kỳ lạ."),t("br"),n._v(" Bọn họ rất sớm trước kia, liền theo lão sư, tung hoành thiên địa, không biết đã bao nhiêu năm, chưa từng nghe nói qua, cái gì truyền nhân ah!"),t("br"),t("br"),n._v("“Không sai, hắn nắm giữ giống như ta năng lực, đồng dạng theo danh sư đại lục mà tới!” Lão giả mỉm cười."),t("br"),t("br"),n._v("“Cái kia... Không biết lão sư vị này truyền nhân, tên gọi là gì? Chúng ta làm sao xác nhận là hắn?” Trung niên nhân liền vội vàng hỏi."),t("br"),t("br"),n._v("“Không cần xác nhận, nhìn thấy nếu liền có thể nhận ra!”"),t("br"),t("br"),n._v("Cũng không giải thích, lão giả trong ánh mắt mang theo thâm thúy: “Còn tên... Ngược lại là có thể nói cho các ngươi biết, hắn gọi... Trương Huyền!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tự Tại thiên, một cái không biết tên gian phòng bên trong."),t("br"),t("br"),n._v("Nồng đậm hơi nước, tràn ngập gian phòng, hơi nóng vây quanh, để cho người ta thấy không rõ lắm cảnh tượng trước mắt."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Dòng nước lay động tiếng vang, một cái hoàn mỹ không một tì vết thân thể mềm mại, từ bên trong đứng lên."),t("br"),t("br"),n._v("Mái tóc đen nhánh, vô cùng mịn màng da thịt, tuyệt mỹ dung nhan..."),t("br"),t("br"),n._v("Tay ngọc khẽ vẫy, một cái lụa mỏng liền bao phủ ở trên người, đem uyển chuyển dáng người, toàn bộ che giấu."),t("br"),t("br"),n._v("“Tiểu thư!”"),t("br"),t("br"),n._v("Một cái lục y nữ tử đi vào trước mặt, đưa tới một cái lược."),t("br"),t("br"),n._v("Tuyệt mỹ nữ tử đem mái tóc chậm rãi chải mở, cả người nhất cử nhất động, như là một bộ bày ra bức tranh."),t("br"),t("br"),n._v("“Tin tức truyền ra ngoài?”"),t("br"),t("br"),n._v("Tuyệt mỹ nữ tử thản nhiên nói."),t("br"),t("br"),n._v("“Vâng!” Lục y nữ tử gật đầu."),t("br"),t("br"),n._v("“Nhưng có trả lời?”"),t("br"),t("br"),n._v("“Tạm thời không có!” Lục y nữ tử lắc đầu, suy nghĩ một chút, nói: “Có điều, tiểu thư để chúng ta tìm hiểu chuyện, chúng ta ngược lại là tìm hiểu đến!”"),t("br"),t("br"),n._v("“Ồ?” Ánh mắt sáng lên, tuyệt mỹ nữ tử vội vàng nhìn tới."),t("br"),t("br"),n._v("“Dạ Minh thành, xuất hiện một vị gọi là Trương Huyền người trẻ tuổi, hắn rất nhiều đệ tử, thiên tư đều rất tốt, thần sơn tìm kiếm Thần Linh chi khí quá trình bên trong, dẫn động không ít phong Vương chuông!”"),t("br"),t("br"),n._v("Lục y nữ tử vội nói."),t("br"),t("br"),n._v("“Xem ra là hắn...” Mỉm cười, tuyệt mỹ nữ tử vẻ mặt có chút hạnh phúc."),t("br"),t("br"),n._v("“Tiểu thư, có muốn hay không nhìn hắn?” Thấy trước mắt vị này bộ dáng này, làm sao không biết nghĩ cái gì, lục y nữ tử vội vàng nói."),t("br"),t("br"),n._v("“Không cần, những ngày gần đây, ta muốn chuẩn bị chiến đấu, bất kỳ người nào không thể thấy, đặc biệt là hắn, nếu không, ta sợ sẽ mất đi chiến đấu lòng tin, từ đó dừng lại!” Tuyệt mỹ nữ tử lắc đầu, không biết nghĩ cái gì, ánh mắt phức tạp."),t("br"),t("br"),n._v("“Nếu như tiểu thư dao động... Vậy thì không chiến chính là, vị kia Thiên Túng Đại Đế, thực lực cực mạnh, tám vị đại Đế đều thua, ta sợ tiểu thư...” Lục y nữ tử tràn đầy lo lắng."),t("br"),t("br"),n._v("Tiểu thư thực lực, cứ việc quan lại thiên hạ, nhưng đối phương cũng không yếu."),t("br"),t("br"),n._v("Giống như sao băng chiếu sáng không trung, ngắn ngủi hơn bốn mươi năm công phu, liền vang vọng toàn bộ Thần giới, không ai không biết không người không hiểu, khiêu chiến tám Đế, mặc dù không có người biết chiến đấu chi tiết, nhưng thấy người sau thừa nhận thân phận, liền biết, nhất định tám trận chiến tám thắng!"),t("br"),t("br"),n._v("Đối với toàn bộ Thần giới tới nói, vị này đều là nhất thời không hai."),t("br"),t("br"),n._v("Phong mang mạnh như vậy, tránh đi chính là, thậm chí, coi như giao đấu, thắng thua cũng rất bình thường, vì sao nhất định muốn lập xuống sống chết quyết đấu, nhất định phải phân cái một mất một còn?"),t("br"),t("br"),n._v("“Không có cách nào tránh khỏi, ta nhất định phải giết hắn, không có thứ hai con đường có thể chọn... Đây là sứ mệnh của ta, cũng là không thể trốn tránh trách nhiệm!” Tuyệt mỹ nữ tử lắc đầu."),t("br"),t("br"),n._v("“Thế nhưng là...” Lục y nữ tử tràn đầy sốt ruột."),t("br"),t("br"),n._v("“Không có thế nhưng là, không cần nói, chuyện này đã làm ra quyết định, nói nhiều hơn nữa cũng vô dụng!” Tuyệt mỹ nữ tử khoát tay áo: “Được rồi, ta muốn bế quan tu luyện...”"),t("br"),t("br"),n._v("Nói xong, thân thể vụt qua, biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("“Tiểu thư...” Không nghĩ tới tiểu thư như vậy cố chấp, lục y nữ tử tuy là đầy mặt không cam lòng, cuối cùng vẫn gật gật đầu, không nói thêm lời."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2147"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2149"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);