(window.webpackJsonp=window.webpackJsonp||[]).push([[1296],{1651:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2137-da-huy-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2137-da-huy-thanh"}},[n._v("#")]),n._v(" Chương 2137: Dạ Huy thành")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Tề Linh Nhi gật đầu, nói: “Đúng, có thể tiến hành không gian truyền tống, cùng Đế Quân cường giả thuấn di tương đương, trong nháy mắt có thể nhảy vọt khoảng cách ngàn vạn dặm, không nhận không gian địa phương bên trên gò bó. Bất quá, thứ này, là thành chủ cùng quyền quý mới có tư cách ngồi, người bình thường không cách nào sử dụng.”"),t("br"),t("br"),n._v("“Thành chủ mới có thể sử dụng?”"),t("br"),t("br"),n._v("Trương Huyền nghi hoặc."),t("br"),t("br"),n._v("Thật sự là nếu như vậy, tính hạn chế liền lớn hơn quá nhiều, khó trách không ít người đều không biết."),t("br"),t("br"),n._v("“Vâng! Thực ra cũng không phải là đặc quyền, mà là vì bảo vệ bị truyền tống người! Thần giới chính là chư thiên vạn giới đứng đầu, không gian củng cố, phong hào Thần Vương cường giả, muốn phá vỡ, đều không có dễ dàng như vậy, nguyên nhân chính là như vậy, truyền tống trận một khi sử dụng, tất nhiên sẽ dẫn tới cực lớn không gian bão táp, thực lực không đủ, rất dễ dàng bị khuấy thành tro tàn!”"),t("br"),t("br"),n._v("Thấy hắn không hiểu, Tề Linh Nhi giải thích nói: “Trở thành thành chủ, nắm giữ thành chủ lệnh, tương đương với ẩn chứa một thành người nguyện lực, bão táp coi như nguy hiểm, cũng không cách nào tổn thương.”"),t("br"),t("br"),n._v("“Nguyện lực?”"),t("br"),t("br"),n._v("Tề Linh Nhi nói: “Người bên trong thành tuân thủ thành chủ quy định, chẳng khác nào đối thành chủ vô điều kiện tin tưởng và nghe theo, một khi hắn xảy ra chuyện, toàn bộ thành liền sẽ lần nữa rơi vào hỗn loạn, dân chúng lầm than, đây đều là nguyện lực! Gia nhập vào thành chủ lệnh phía trong, một cách tự nhiên liền để hắn có cực mạnh phòng ngự tác dụng! Tân tấn vị kia Thiên Túng Đại Đế, vì sao nhất định muốn nhận được mặt khác Cửu Đế công nhận? Cũng là bởi vì cái này. Cửu Đế công nhận, chứng minh Cửu Đế quản hạt phạm vi bên trong tất cả sinh mệnh cũng đều công nhận, như vậy trải qua, cũng liền có thể ngưng tụ nhiều hơn nữa nguyện lực...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Nhìn tới ở tại thần giới, nguyện lực vô cùng quan trọng."),t("br"),t("br"),n._v("Thiên Túng Đại Đế, hắn thấy, hẳn là Khổng sư, dựa theo bản thân đối vị này vạn thế chi sư hiểu rõ, học thức thiên hạ, không màng danh lợi... Nhưng bây giờ lại vì tranh đoạt Đại Đế tên tuổi, liên tiếp khiêu chiến Cửu Đế, trước đó một mực nghi hoặc, có chút không hiểu, hiện tại xem ra, nguyên nhân chủ yếu nhất, khả năng ở đây."),t("br"),t("br"),n._v("Nhận được thừa nhận, cũng chính là cái gọi là “Chính thống”, mới có thể có đến tin tưởng và nghe theo, nhận được nguyện lực."),t("br"),t("br"),n._v("“Nói như vậy lên, ta muốn đi tới hoàng thành, hoặc là ở tại thần giới không chướng ngại chút nào đi nhận chức địa phương nào, nhất định phải đạt được thành chủ thân phận?”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Tề Linh Nhi mỉm cười."),t("br"),t("br"),n._v("“Cái này đơn giản, ta cùng tiểu giáp cùng đi, đem Ngô Phương Thanh giết... Ngươi chính là thành chủ!” Tôn Cường nói."),t("br"),t("br"),n._v("Trên đường trở về, hắn cùng Trương Giáp thân quen, biết thực lực của người này."),t("br"),t("br"),n._v("Ngô Phương Thanh cứ việc nắm giữ hạ phẩm Thiên Thần khí, muốn vượt qua hắn, cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đối phương lại không làm gì sai, vì chức thành chủ... Giết người, không phải hắn nên đi làm."),t("br"),t("br"),n._v("“Thực ra không cần giết người, Thần giới thoạt nhìn củng cố, trên thực tế khắp nơi đều có tranh đấu, không ít thành, đều không có thành chủ!” Tề Linh Nhi nói: “Không nói mặt khác, ta trên thực tế cũng là một vị thành chủ, cứ việc quản lý địa phương, so Dạ Minh thành kém xa tít tắp, có thể thành chủ lệnh dùng để truyền tống, lại đủ!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Không cần cướp đoạt, tự nhiên tốt nhất."),t("br"),t("br"),n._v("Nhìn tới, Thần giới tuy là có quy củ, cũng không ít người lợi dụng sơ hở."),t("br"),t("br"),n._v("Chỉ cần có thể sử dụng truyền tống trận, tiết kiệm thời gian, chơi vị thành chủ làm một chút, cũng không sao."),t("br"),t("br"),n._v("Cùng nhau đi tới, đưa gia chủ, đưa đường chủ, đưa tông chủ thì thôi đi, thêm một cái thành chủ thân phận, không tính là gì."),t("br"),t("br"),n._v("Nhịn không được hỏi: “Chung quanh đây nhưng có thành, không có thành chủ? Có thể đi qua cạnh tranh?”"),t("br"),t("br"),n._v("“Cách nơi này một ngày lộ trình Dạ Huy thành, hiện nay còn không có thành chủ, vì thế tranh đoạt nửa năm, thủy chung không có người có thể phục chúng, cũng không có người thành công!”"),t("br"),t("br"),n._v("Tề Linh Nhi bù trừ lẫn nhau tức vô cùng linh thông, cười nói: “Có điều, cái này thành so Dạ Minh thành còn muốn lớn hơn một vòng, tuy là một thành, vẫn còn quản lý ba quận chỗ, tu vi cao nhất, đã đạt đến trung phẩm Thiên Thần cảnh, cho dù loại này cường giả, đều không có thành công, độ khó không nhỏ!”"),t("br"),t("br"),n._v("“Ba quận? Những này quận chúa, có hay không lệnh bài, có thể tiến hành truyền tống?” Trương Huyền hỏi."),t("br"),t("br"),n._v("“Ta là căn cứ Trương thiếu tình huống hiện tại mới nói, cái thành phố này, tự nhiên có!” Tề Linh Nhi gật đầu."),t("br"),t("br"),n._v("Nàng biết trước mắt vị này cướp đoạt thành chủ mục đích là vì truyền tống trận, mà sử dụng vật này, mang theo trước mắt bên cạnh ba người này khả năng hành tương đối lớn, cho nên mới nói, cái này Dạ Huy thành."),t("br"),t("br"),n._v("Nếu không, tuỳ ý tiến cử cái tiểu thành thị là được, không cần thiết nói, so Dạ Minh thành còn cường đại hơn."),t("br"),t("br"),n._v("“Vậy là tốt rồi, nếu như không có chuyện gì khác, hiện tại liền lên đường đi...” Trương Huyền nói."),t("br"),t("br"),n._v("“Làm phiền Trương thiếu chờ đợi phút chốc, ta bây giờ đi về chuẩn bị, sau hai canh giờ xuất phát!” Tề Linh Nhi gật đầu."),t("br"),t("br"),n._v("Trương Huyền không nói thêm lời, yên tĩnh đợi chờ."),t("br"),t("br"),n._v("Tề Linh Nhi làm việc rất nhanh, nói là hai canh giờ, sau một canh giờ rưỡi, liền trở lại sân nhỏ, hơn nữa mang đến mấy con to lớn phi hành thần thú."),t("br"),t("br"),n._v("Đám người bước lên lưng thú, thẳng tắp hướng đối phương trong miệng nói tới Dạ Huy thành bay đi."),t("br"),t("br"),n._v("“Đi?”"),t("br"),t("br"),n._v("Nghe được vị này Trương Huyền đã rời khỏi, Ngô Phương Thanh khắp khuôn mặt là thất lạc, còn muốn lấy, loại nhân vật này xuất hiện tại Dạ Minh thành, có cơ hội hảo hảo nịnh bợ một chút, bản thân cũng tốt có cơ hội đột phá càng cao tu vi, không nghĩ tới nhanh như vậy liền rời đi"),t("br"),t("br"),n._v("“Ngô thành chủ, đây là Trương thiếu để cho ta đưa tới, nói là báo lại lễ vật của ngươi cùng trợ giúp!” Ưng Phí đi vào trước mặt."),t("br"),t("br"),n._v("Ngô Phương Thanh nghi ngờ nhận lấy trong tay đối phương ngọc phù, nhẹ nhàng điểm một cái, một đạo thần lực tuôn đi vào, một lát sau, con mắt đột nhiên trợn tròn: “Cái này, cái này...”"),t("br"),n._v("Ngọc phù phía trong ghi lại, đều là hắn trên việc tu luyện sai lầm cùng sơ hở, thậm chí sửa lại phương pháp, chỉ cần dựa theo cái này tu luyện, nhất định có thể giải quyết trên việc tu luyện tất cả tai hoạ ngầm, xung kích trung phẩm Thiên Thần, cũng chưa biết chừng!"),t("br"),t("br"),n._v("“Hắn làm sao mà biết được?”"),t("br"),t("br"),n._v("Tràn đầy khó tin."),t("br"),t("br"),n._v("Tính toán đâu ra đấy cùng đối phương quen biết cũng liền một ngày công phu, không nói gì qua... Công pháp bên trên thiếu hụt cùng sơ hở, rất nhiều chính hắn đều sơ sót, đối phương làm sao liếc mắt nhìn ra, hơn nữa tìm ra mấu chốt?"),t("br"),t("br"),n._v("“Đế Quân huyết mạch người, quả nhiên... Không đơn giản!”"),t("br"),t("br"),n._v("Qua không biết bao lâu, lúc này mới cảm xúc một tiếng."),t("br"),t("br"),n._v("Thực sự không nghĩ ra, cũng chỉ có thể quy kết tại Đế Quân huyết mạch trên thân, nếu không, thật không biết giải thích như thế nào loại năng lực này..."),t("br"),t("br"),n._v("Trương Huyền xoay người rời đi, không có lưu lại mảy may dấu vết, cũng không có dẫn tới gợn sóng quá lớn."),t("br"),t("br"),n._v("Dù sao đi vào Dạ Minh thành về sau, một mực rất biết điều, biết hắn người như vậy, cũng không nhiều."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Một đường phi hành, sau một ngày, quả nhiên thấy một cái thành phố khổng lồ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("So Dạ Minh thành bao la gấp ba bốn lần, vô số cao vút kiến trúc đứng sừng sững, nhân số càng nhiều, nhiều vô kể."),t("br"),t("br"),n._v("Theo phi hành thần thú bên trên rơi trên mặt đất, đám người đi vào thành trấn, Trương Huyền nhìn thấy trước mắt trước mắt chen chúc dòng người, như cá diếc sang sông, nhịn không được lắc đầu."),t("br"),t("br"),n._v("“Thần giới gặp phải biến cố, linh khí tán loạn, ngoài thành có thể chỗ ở ít, hầu như tất cả mọi người di chuyển đến nội thành, cho nên nơi này vô cùng chen chúc, có thể ở đây, mua sắm một bộ ba, bốn mươi mét vuông nhà, liền xem như phú hào, có thể để cho vô số nữ hài dũng cảm quên mình...”"),t("br"),t("br"),n._v("Nhìn ra ý nghĩ của hắn, Tề Linh Nhi giải thích."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Loại tình huống này cùng kiếp trước didu, Ma đô có chút giống nhau, tất cả mọi người muốn chen vào, đừng nói phòng ốc, một cái hộ khẩu, cũng có thể làm cho vô số nữ hài kẻ trước ngã xuống, kẻ sau tiến lên."),t("br"),t("br"),n._v("Thần giới bị dị biến, muốn an ổn sống sót, thậm chí tu luyện, chỉ có thể tiến vào thành thị, công dân đầy là mối họa."),t("br"),t("br"),n._v("“Đi đâu cạnh tranh thành chủ?”"),t("br"),t("br"),n._v("Không để ý tới người đông nghìn nghịt chuyện, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Cái này Dạ Huy thành sở dĩ đến bây giờ đều không có xác định được thành chủ nhân tuyển, cũng không phải là không có cao thủ, mà là... Lẫn nhau giằng co, không cách nào xác định được!”"),t("br"),t("br"),n._v("Tề Linh Nhi nói: “Lẫn nhau giằng co người hết thảy ba cái, theo thứ tự là am hiểu đao pháp con em thế gia, Lâm Tạ. Am hiểu kiếm pháp tán tu, Ngọ Dương. Cùng với một cái gọi Mính Lai Hương nữ tử! Trước hai cái đều là trung phẩm Thần Linh cảnh giới, chiến lực không tầm thường, cuối cùng cái kia cùng Ngô Phương Thanh đồng dạng, chỉ là một vị hạ phẩm Thần Linh, có điều, chớ xem thường nàng cấp bậc thấp, nghe nói, nàng mới là trong ba người đáng sợ nhất cái kia!”"),t("br"),t("br"),n._v("“Mính Lai Hương...” Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Có thể cạnh tranh đứng đầu một thành, hơn nữa để như vậy lớn một cái thành thị, lâu như vậy đều không có xác định được, tự nhiên không thể khinh thường."),t("br"),t("br"),n._v("“Muốn trở thành thành chủ, thực ra cũng không có phức tạp như vậy, đơn độc khiêu chiến ba người, vượt qua đối phương, để cho bọn họ thật lòng khâm phục công nhận ngươi, chức thành chủ cũng liền tới tay!”"),t("br"),t("br"),n._v("Tề Linh Nhi nói: “Cái kia... Trương thiếu, chúng ta chọn trước chiến cái nào?”"),t("br"),t("br"),n._v("Đổi lại trước kia, U Hồn thiên tuyển chọn thành chủ, cần U Hồn điện trước thời hạn chấp nhận, các loại thủ tục xuống, không có mấy tháng công phu, không có khả năng hoàn thành."),t("br"),t("br"),n._v("Nhưng bây giờ linh khí tán loạn, Thần giới người người cảm thấy bất an, tình cảnh hoàn toàn khác biệt."),t("br"),t("br"),n._v("Đứng đầu một thành, nhất định phải nắm giữ thực lực tuyệt đối, mới có thể bảo vệ tất cả mọi người an toàn, cho nên, chỉ cần có thể đem mọi người trấn phục, liền có tư cách làm thành chủ, liền có thể nhận được U Hồn thiên công nhận."),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền nói: “Cái này... Nơi nào có trung phẩm Thần Nguyên đan? Trước đi tìm đan dược, lại đi khiêu chiến bọn họ!”"),t("br"),t("br"),n._v("“Trung phẩm Thần Nguyên đan?”"),t("br"),t("br"),n._v("Không nghĩ tới trước mắt vị này muốn thứ này, Tề Linh Nhi sửng sốt một chút, suy nghĩ một chút, nói: “Thương hội nên liền có, có điều, cơ bản đều là có tiền mà không mua được, đúng, ta biết một chỗ khẳng định có, hơn nữa số lượng không ít, chỉ là đối phương không tốt như vậy nói chuyện, ta sợ cũng không dễ bán...”"),t("br"),t("br"),n._v("“Ồ?” Trương Huyền nhìn qua: “Nói nghe một chút!”"),t("br"),t("br"),n._v("“Đúng Dạ Huy thành một cái tán tu, tuổi chừng hơn sáu mươi tuổi, tu vi cũng không cao, chỉ là thượng phẩm Thần Linh, am hiểu luyện đan. Luyện chế đan dược, từ trước tới nay bản thân dùng, chưa từng bán ra. Yêu cờ thành si, mỗi ngày đều tìm người đánh cờ, cờ thuật rất cao, từng tuyên bố, ai có thể tại cờ thuật bên trên vượt qua hắn một ván, liền dâng tặng một viên trung phẩm Thần Nguyên đan, đáng tiếc... Nhiều năm như vậy, chưa hề có một người thành công!”"),t("br"),t("br"),n._v("Tề Linh Nhi nói."),t("br"),t("br"),n._v("Trương Huyền cau mày: “Tu vi không cao lời nói, chẳng lẽ không có người cướp đoạt?”"),t("br"),t("br"),n._v("Thượng phẩm Thần Linh, có được trung phẩm Thần Nguyên đan bản thân liền rất kỳ quái, lại còn dám nói cho những người khác, chẳng lẽ không sợ có người tới cướp?"),t("br"),t("br"),n._v("Đổi lại những thành thị khác, nắm giữ thành chủ quy củ, không có người mạo phạm, nhưng nơi này thành chủ không phải không xác lập ư?"),t("br"),t("br"),n._v("“Tu vi của hắn là không cao, nhưng hắn đệ đệ rất lợi hại, nghe nói là hoàng thành một vị Thần Vương cường giả, trên người hắn nắm giữ đối phương lưu lại bảo vệ tính mạng ngọc phù, trước đó hai vị hạ phẩm Thiên Thần đến tìm phiền phức, tất cả đều bị toả ra khí tức chém giết... Thời gian lâu dài, cũng liền lại không người dám tới!”"),t("br"),t("br"),n._v("Tề Linh Nhi giải thích."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2151"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2153"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);