(window.webpackJsonp=window.webpackJsonp||[]).push([[1298],{1653:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2139-đanh-co"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2139-đanh-co"}},[n._v("#")]),n._v(" Chương 2139: Đánh cờ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Đương nhiên!” Gật gật đầu, Trương Huyền ngồi xuống, tay trái kẹp lên quân cờ, tay phải vươn ra: “Mời!”"),t("br"),t("br"),n._v("Tam Tu đạo nhân không nói thêm lời, đem quân cờ hạ xuống."),t("br"),t("br"),n._v("Khẽ mỉm cười, Trương Huyền trong thư viện, xuất hiện một quyển sách."),t("br"),t("br"),n._v("Phía trên rậm rạp chằng chịt viết đầy nội dung, mỗi một cái cờ đường vị trí, đều ghi chú rõ thiếu hụt loại hình."),t("br"),t("br"),n._v("“Đệ nhất đường đệ nhất con vị trí, thiếu hụt: Thứ hai đường, con thứ hai vị trí, thiếu hụt:”"),t("br"),t("br"),n._v("Trên bàn cờ mỗi một cái vị trí thiếu hụt, có có nhiều ít, chỉ có một chỗ, không có biểu hiện nội dung."),t("br"),t("br"),n._v("“Quả nhiên có thể...”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, Trương Huyền khóe miệng nâng lên."),t("br"),t("br"),n._v("Biết quy tắc về sau, đem những kiến thức này, cùng trước mắt bàn cờ dung hợp, quả nhiên tìm ra cờ giữa đường thiếu hụt, chỉ cần dựa theo trong đó hoàn mỹ vị trí tới bên dưới, khẳng định sẽ xuất hiện kết quả không tệ."),t("br"),t("br"),n._v("“Việc này... Rất vững vàng!”"),t("br"),t("br"),n._v("Thấy hắn ra tay, Trương Giáp cùng Tề Linh Nhi đồng thời nhìn qua, nhìn nhau, từng người thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Biết đối phương vừa rõ ràng quy tắc, đối với chiến thắng đã không có gì hy vọng xa vời, chỉ hy vọng, đừng thua quá mất mặt."),t("br"),t("br"),n._v("Cứ việc hai bên đều chỉ hạ thấp một cái con, nhưng tất cả đều là chính quy hạ cờ quy củ, có kỳ phổ có thể tìm ra, không có xuất hiện ngoài nghề hồ hạ khó khăn cục diện."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Trước mấy bước, mới bắt đầu bố cục, không cần quá nhiều suy nghĩ, kinh nghiệm liền có thể bù đắp, Tam Tu đạo nhân hạ rất nhanh, Trương Huyền theo sát ở phía sau, hầu như không có bất kỳ cái gì suy tư, đối phương hạ càng nhanh, hắn cũng càng nhanh."),t("br"),t("br"),n._v("Không đến năm phút đồng hồ, trên bàn cờ cũng đã từng người rơi xuống hơn ba mươi bước."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy rõ phía trên cục diện, Trương Giáp cùng Tề Linh Nhi có chút choáng váng."),t("br"),t("br"),n._v("Tuy là Trương Huyền hạ, thoạt nhìn rất đơn giản, không có quá nhiều kinh nghiệm cùng phương pháp, lại mỗi một bước đều giống như tính toán được rồi bình thường, đúng quy đúng củ, đem đối phương quân cờ, một mực phong tỏa ở bên trong, muốn tránh thoát, đều rất khó."),t("br"),t("br"),n._v("“Làm sao lại không có sơ hở?”"),t("br"),t("br"),n._v("Đám người kỳ lạ, Tam Tu đạo nhân thì tràn đầy khó tin."),t("br"),t("br"),n._v("Hạ bước số không nhiều, nhưng là hắn đánh cờ đến nay, uất ức nhất một lần."),t("br"),t("br"),n._v("Đối phương mỗi một bước đều giống như tính toán được rồi đồng dạng, đem hắn tất cả ý đồ triệt để tiêu diệt, nếu như đem hắn ví von thành một cái mang binh tướng lĩnh, phe mình hành quân bố cục, bị đối phương xem thấu đồng dạng, vô luận như thế nào đi, đều trước thời hạn bị chặn đường, bị đuổi theo, bị bao vây... Không cho nửa điểm cơ hội!"),t("br"),t("br"),n._v("Trên đầu mồ hôi ứa ra."),t("br"),t("br"),n._v("Liên tục đổi tám loại lợi hại đánh cờ phương pháp, mười hai bộ kỳ phổ, dưới tình huống bình thường, sẽ để cho đối thủ mê loạn, tìm mơ hồ mục đích cùng phương hướng... Nhưng bên này vừa bên dưới xong, đối phương đồng dạng không có suy nghĩ, lập tức liền đem quân cờ hạ xuống."),t("br"),t("br"),n._v("Vừa mới bắt đầu cảm giác không đến hắn nước cờ này tác dụng, ba bước sau đó, phát hiện ảo diệu, tựa như một cái xương cá cắm ở yết hầu, muốn ói cũng nhả không ra, nghẹn hô hấp đều có chút khó khăn."),t("br"),t("br"),n._v("“Không có khả năng...”"),t("br"),t("br"),n._v("Ngón tay run rẩy."),t("br"),t("br"),n._v("Hắn đối với mình kỳ thuật, có cực lớn tự tin, Dạ Huy thành không có đối thủ, cho dù hoàng thành có thể vượt qua, cũng có thể đếm được trên đầu ngón tay!"),t("br"),t("br"),n._v("Những cái kia có thể gọi ra họ tên kỳ thuật đại sư, cho dù có thể chiến thắng, cũng ít nhất tại trăm bước sau đó, trước mắt vị này, mới rơi xuống chừng ba mươi bước, liền đem bản thân bức thành như vậy, nghĩ mãi không ra, quả thực không thể tưởng tượng, không dám tưởng tượng."),t("br"),t("br"),n._v("Mấu chốt nhất là, đối phương giống như là không có chút nào sơ hở bình thường, hạ mỗi một bước, đều đem đường lui của mình đóng chặt hoàn toàn, căn bản không cho bất cứ cơ hội nào."),t("br"),t("br"),n._v("Xoa xoa mồ hôi trên đầu, lại rơi xuống mấy bước."),t("br"),t("br"),n._v("Đối phương vẫn không có suy nghĩ, tiện tay mà xuống."),t("br"),t("br"),n._v("Tam Tu đạo nhân đánh cờ tốc độ càng ngày càng chậm."),t("br"),t("br"),n._v("“Nơi này không được... Đó cũng không được...”"),t("br"),t("br"),n._v("Trong tay cầm quân cờ, không biết nên để ở nơi đâu, đã có chút do dự."),t("br"),t("br"),n._v("“Thiếu gia kỳ thuật...”"),t("br"),t("br"),n._v("Trương Giáp lúc này cũng nhìn ra không được bình thường, lần nữa nhìn về phía một bên Tề Linh Nhi, con mắt trợn tròn."),t("br"),t("br"),n._v("Còn tưởng rằng thiếu gia vừa học được quy tắc, khẳng định kỳ thuật không cao minh, tận mắt thấy mới biết, ý nghĩ này là cỡ nào ngây thơ."),t("br"),t("br"),n._v("Mỗi một bước, đều giống như tính toán không biết bao nhiêu lần bình thường, không có chút nào sơ hở cùng sơ hở, trước mắt vị lão giả này kỳ thuật cứ việc rất cao minh, vẫn không có một chút biện pháp, từng bước túng quẫn."),t("br"),t("br"),n._v("“Thật sự là vừa học được?”"),t("br"),t("br"),n._v("Tề Linh Nhi cũng đôi mắt thanh tú không ngừng nháy, một bộ khó có thể tin biểu lộ."),t("br"),t("br"),n._v("Trương Giáp là cái thần thú, đối kỳ thuật cho dù có chút ít giải, cũng không có khả năng hiểu được quá nhiều, nàng thế nhưng là theo đại gia tộc đi ra thế hệ sau, mặc dù không có chuyên môn nghiên cứu qua kỳ thuật, lại biết không ít."),t("br"),t("br"),n._v("Tam Tu đạo nhân kỳ thuật, cho dù tại Tề gia, cũng coi như được đỉnh phong tồn tại, có thể đối mặt trước mắt vị này, một bước đều không có chiếm tiện nghi không nói, còn bị đè lên đánh..."),t("br"),t("br"),n._v("Hình như một vị phong hào Thần Vương đối chiến hạ phẩm Thần Linh, người sau không có chút nào sức đối kháng..."),t("br"),t("br"),n._v("Thế thì còn đánh như thế nào?"),t("br"),t("br"),n._v("Không phải mình vừa dạy quy tắc ư?"),t("br"),t("br"),n._v("Lần đầu tiên chơi, cứ như vậy lợi hại?"),t("br"),t("br"),n._v("Bên này khiếp sợ còn không có kết thúc, bên kia Tam Tu đạo nhân trong tay quân cờ cũng lại không có buông ra, tiện tay ném xuống, một mặt cười khổ: “Ta thua...”"),t("br"),t("br"),n._v("Không nhận thua không được, đối phương đem hắn tất cả con đường đi tới đầy đủ phong kín, lại không lưu lại một tia một hào sơ hở, tiếp tục nữa, chỉ chờ tại tự rước lấy nhục."),t("br"),t("br"),n._v("“Đa tạ! Thực ra ta cũng vừa học được, rất nhiều quy củ đều không có hiểu rõ.”"),t("br"),t("br"),n._v("Trương Huyền ôm quyền."),t("br"),t("br"),n._v("Thực ra đến bây giờ, hắn đều không có xem hiểu, đến cùng làm sao hạ, chỉ là dựa theo trong thư viện hoàn mỹ trình tự, từng bước một đem quân cờ buông ra."),t("br"),t("br"),n._v("Tiếp đó... Đối phương liền nhận thua!"),t("br"),t("br"),n._v("Suy nghĩ một chút đều cảm thấy xấu hổ."),t("br"),t("br"),n._v("“Đã nhường? Không có hiểu rõ...” Tam Tu đạo nhân còn tưởng rằng hắn khiêm tốn, khuôn mặt đỏ lên: “Tiểu huynh đệ nói như vậy, thật sự là chiết sát ta. Thực ra xuống đến bước thứ mười, ta liền biết bản thân phải thua, phía sau chẳng qua vùng vẫy giãy chết mà thôi! Thua thảm như vậy... Đã nhường nếu như vậy, thì không cần nói! Như ngươi loại này kỳ thuật, đều nói quy tắc không có hiểu rõ, ta đây tính toán là cái gì?”"),t("br"),n._v(" Chênh lệch một quân hai con, nói đã nhường, thì cũng thôi đi, cũng có thể tiếp nhận, hiện tại... Không tại một cái cấp bậc, Thần Vương chiến thắng hạ phẩm Thần Linh, nói đã nhường... Khiêm tốn cũng phải có cái mức độ."),t("br"),t("br"),n._v("Đại ca, chứa có hơi quá..."),t("br"),t("br"),n._v("“Tiền bối khách khí...” Trương Huyền xấu hổ."),t("br"),t("br"),n._v("“Được rồi, thua ta nhận, có cái gì yêu cầu, cứ việc nói đi!” Tam Tu đạo nhân bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Ta muốn hướng tiền bối mua sắm một ít trung phẩm Thần Nguyên đan, mong rằng tiền bối có thể bán ra!” Trương Huyền nói."),t("br"),t("br"),n._v("“Chỉ mua sắm trung phẩm Thần Nguyên đan?” Tam Tu đạo nhân sững sờ."),t("br"),t("br"),n._v("Đối phương chuyên môn chạy tới, cùng hắn đánh cờ, lại thắng được huy hoàng như vậy, còn tưởng rằng, có cái gì khó lấy làm được yêu cầu, không nghĩ tới đơn giản như vậy!"),t("br"),t("br"),n._v("Trung phẩm Thần Nguyên đan, tại Dạ Huy thành tuy có giá cả không thành phố, lại là rất dễ dàng tìm tới, một ít phú thương, đại gia tộc, đều có tồn kho... Một cái đường đường kỳ đạo đại tông sư, liền vì chút chuyện nhỏ này?"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Mua sắm cũng không cần, ta chỗ này có ba mươi viên trung phẩm Thần Nguyên đan, trực tiếp đưa tặng cho ngươi, không cần tốn tiền...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, Tam Tu đạo nhân đưa tới ba cái bình ngọc."),t("br"),t("br"),n._v("“Đưa tặng? Cái này làm sao được...” Trương Huyền vội vàng xua tay."),t("br"),t("br"),n._v("Trung phẩm Thần Nguyên đan giá cả, hắn chuyên môn hỏi thăm, gấp trăm lần ở dưới phẩm, hơn nữa hầu như mua không được, nhiều như vậy trực tiếp đưa tặng, thực sự không dám nhận nhận."),t("br"),t("br"),n._v("“Đều là bản thân luyện chế, giá cả không có trong tưởng tượng cao như vậy, nếu như... Cảm thấy ngượng ngùng lời nói, có thể lại theo ta chơi hai bàn!” Tam Tu đạo nhân nói."),t("br"),t("br"),n._v("“Vui lòng tiếp, chỉ bất quá... Tiền bối không phải nói, một ngày chỉ chơi ba bàn ư?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Đó là không có gặp được đối thủ, gặp được đối thủ, ba mươi bàn cũng không sao...” Hơi đỏ mặt, Tam Tu đạo nhân vội nói."),t("br"),t("br"),n._v("Trương Huyền khẽ mỉm cười."),t("br"),t("br"),n._v("Đối phương trực tiếp tặng cho nhiều như vậy đan dược, không thể báo đáp, cùng đánh vài ván cờ, không tính là gì."),t("br"),t("br"),n._v("Dù sao đều theo chiếu trong thư viện trình tự bên dưới, không uổng phí cái gì đầu óc."),t("br"),t("br"),n._v("Lần nữa bày bàn, một cái con tiếp lấy một cái con hạ xuống."),t("br"),t("br"),n._v("Sau năm phút, Tam Tu đạo nhân vẻ mặt lần nữa đỏ lên: “Một ván nữa...”"),t("br"),t("br"),n._v("Lại sau bốn phút: “Lại đến!”"),t("br"),t("br"),n._v("Lại sau ba phút: “Lại đến...”"),t("br"),t("br"),n._v("Lại một phút đồng hồ sau: “Tốt a, ta nhận thua...”"),t("br"),t("br"),n._v("Liên tục mấy lần, Tam Tu đạo nhân bắt đầu nghi ngờ nhân sinh."),t("br"),t("br"),n._v("Còn tưởng rằng, đối phương chỉ là vận khí tốt, mới bên dưới ra trước đó cờ đường, làm sao đều không có nghĩ đến, bất luận hắn làm sao bắt đầu, dùng loại phương pháp nào, đều cùng đâm vào trên tường đồng dạng, không có chút nào phản kháng chỗ trống."),t("br"),t("br"),n._v("Một lần thua, có thể nói bản thân tài đánh cờ không cao, nhiều lần thua, đủ để chứng minh, cùng đối phương chênh lệch thực sự quá lớn, không thể so sánh nổi."),t("br"),t("br"),n._v("“Như ngươi loại này kỳ thuật, thiên hạ có thể vượt qua không nhiều, hoàn toàn có thể tham gia thi đấu, kiếm lấy ban thưởng hoặc là tài nguyên tu luyện...”"),t("br"),t("br"),n._v("Biết tiếp tục nữa, sẽ chỉ càng ngày càng mất mặt, Tam Tu đạo nhân không tại rầu rĩ, nhìn về phía trước mắt người thanh niên này, trong mắt tràn đầy mong đợi."),t("br"),t("br"),n._v("Bất luận loại nghề nghiệp nào, chỉ cần đi đến đỉnh phong, đều sẽ có ích lợi rất lớn, kỳ thuật cũng giống như vậy."),t("br"),t("br"),n._v("“Không cần!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đối với đánh cờ, hắn là thường dân, chỉ là mượn thư viện chiến thắng mà thôi, không đáng giá nhắc tới."),t("br"),t("br"),n._v("“Vậy thì thật là đáng tiếc...” Thấy hắn quả thực không muốn đến trên con đường này tiếp tục phát triển, Tam Tu đạo nhân tràn đầy tiếc nuối lắc đầu."),t("br"),t("br"),n._v("Cùng đối phương cáo từ, lần nữa đi trở về khu phố, Tề Linh Nhi nhìn lại: “Có muốn hay không tìm một chỗ an tĩnh, trước đem trung phẩm Thần Nguyên đan luyện hóa lại nói?”"),t("br"),t("br"),n._v("Đối phương giờ phút này tìm Thần Nguyên đan mục đích, nàng cũng có thể nhìn ra, hẳn là muốn đột phá tu vi."),t("br"),t("br"),n._v("Đã như vậy, khẳng định cần tìm yên tĩnh vị trí bế quan."),t("br"),t("br"),n._v("“Không cần!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Tề Linh Nhi nghi hoặc."),t("br"),t("br"),n._v("Có ý tứ gì?"),t("br"),t("br"),n._v("Chẳng lẽ tìm những đan dược này mục đích, không phải là vì đột phá, mà là có mặt khác dùng?"),t("br"),t("br"),n._v("Ngay tại kỳ lạ, chỉ thấy thiếu niên vừa đi đường, một bên đem vừa lấy được ba mươi viên thuốc theo trong bình ngọc đổ ra, từng mai từng mai hướng trong mồm ném đi, tựa như đang ăn đường đậu."),t("br"),t("br"),n._v("Lông mày nhảy dựng."),t("br"),t("br"),n._v("Trung phẩm Thần Nguyên đan, Thiên Thần cảnh cường giả đều có hiệu quả, phổ thông thượng phẩm Thần Linh, nuốt một viên, đều cần tiêu hóa ít nhất một tháng, mới có thể hoàn toàn luyện hóa năng lượng trong đó, cái tên này, trực tiếp xem như đường đậu ăn..."),t("br"),t("br"),n._v("Xác định sẽ không bạo thể mà chết?"),t("br"),t("br"),n._v("Ngay tại kỳ lạ, chỉ thấy trên người đối phương khí thế cấp tốc tăng thêm."),t("br"),t("br"),n._v("Thượng phẩm Thần Linh trung kỳ!"),t("br"),t("br"),n._v("Thượng phẩm Thần Linh hậu kỳ!"),t("br"),t("br"),n._v("Thượng phẩm Thần Linh đỉnh phong!"),t("br"),t("br"),n._v("Không đến hai cái hô hấp, đã đột phá ba cái tiểu cấp bậc, khoảng cách Thiên Thần cảnh, chỉ có kém một bước."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Tề Linh Nhi run rẩy, cả người đều muốn điên rồi."),t("br"),t("br"),n._v("Thân là con em đại gia tộc, thiên tài gặp qua không ít, cường giả cũng không biết bao nhiêu mà đếm..."),t("br"),t("br"),n._v("Loại này tuỳ ý nuốt đan dược, nhẹ nhõm tấn cấp phương pháp... Còn là lần đầu tiên thấy!"),t("br"),t("br"),n._v("Đế Quân huyết mạch... Thật đáng sợ như thế?"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2153"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2155"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);