(window.webpackJsonp=window.webpackJsonp||[]).push([[130],{483:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1093-to-su-tha-mang-thuong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1093-to-su-tha-mang-thuong"}},[n._v("#")]),n._v(" Chương 1093: Tổ sư tha mạng (thượng)")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Trên đường đi sử dụng mấy lần, giờ phút này, trong bình ngọc chỉ còn lại có một viên viên thuốc."),i("br"),i("br"),n._v("“Cái này... Là cố bản bồi nguyên đan dược?” Nhìn một cái, Lâm Giang Hải cau mày."),i("br"),i("br"),n._v("Thân là độc sư, đối dược vật dược tính vô cùng hiểu rõ, trước mắt cái này đan dược, vừa nhìn liền biết, chỉ là cố bản bồi nguyên bình thường dược vật, không có bất kỳ cái gì chỗ đặc thù."),i("br"),i("br"),n._v("“Ta vừa mới bắt đầu cũng tưởng là chỉ là cố bản bồi nguyên bình thường dược vật, nhưng mà đi qua tổ sư tay luyện chế lại một lần, đã là giải độc Thánh dược, có thể giải vạn độc!” Bạch Đình gật đầu."),i("br"),i("br"),n._v("Lâm Giang Hải lông mày nhíu một cái."),i("br"),i("br"),n._v("Hắn mệnh danh Vạn Độc Tôn Giả, đủ thấy dùng độc mạnh, chủng loại nhiều, khiến cho mắt người hoa hỗn loạn, nghẹn họng nhìn trân trối."),i("br"),i("br"),n._v("Trước mắt bình thường cố bản bồi nguyên đan dược, mệnh danh giải vạn độc... Chẳng phải liền là khắc tinh của hắn?"),i("br"),i("br"),n._v("Thần thức hướng trong dược vật lan tràn, trong chớp mắt liền nhìn cái kết quả, càng xem càng cảm thấy kỳ quái."),i("br"),i("br"),n._v("Lấy ánh mắt của hắn các loại đối dược vật lý giải, cái này đan dược không có bất kỳ cái gì đặc thù, cùng trên thị trường bán những cái kia, không sai chút nào... Đừng nói hắn, liền xem như Thánh vực ngũ trọng người, ăn một bữa bên trên ba cân, cũng không quá lớn hiệu quả... Mệnh danh giải vạn độc, có phần quá nói ngoa đi?"),i("br"),i("br"),n._v("Hắn là cửu tinh độc sư, toàn bộ đại lục, đỉnh phong nhất dùng độc Tôn giả, cũng nhìn không ra đặc thù, nếu không phải Bạch Đình lời thề son sắt, khẳng định đã sớm ném tới cống ngầm bên trong đi."),i("br"),i("br"),n._v("“Thuốc này thật sự có giải độc hiệu quả?” Nhịn không được nhìn lại."),i("br"),i("br"),n._v("“Lão sư nếu như không tin, ta có thể làm tràng cho ngươi thí nghiệm...” Bạch Đình nhẹ nhàng cười một tiếng, xoay đầu lại: “Nguyên Lăng sư huynh, không biết ngươi  có thể tùy thân mang theo?”"),i("br"),i("br"),n._v("“Tại...” Nguyên Lăng gật đầu, lòng bàn tay nhiều ra một cái bình ngọc."),i("br"),i("br"),n._v("Làm hắn nghiên cứu ra tới lợi hại nhất độc dược, tự nhiên vẫn luôn tùy thân mang theo, chuẩn bị bất cứ tình huống nào."),i("br"),i("br"),n._v("“Hải độc sư, khả năng còn muốn làm phiền ngươi một việc...” Nhận lấy bình ngọc, Bạch Đình gọi."),i("br"),i("br"),n._v("“Bạch độc sư không cần khách khí như thế, có chuyện xảy ra cứ nói đừng ngại!”"),i("br"),i("br"),n._v("Hải Minh Kim đi tới."),i("br"),i("br"),n._v("“Thật ra thì cũng không có gì, làm phiền ngươi đem cái này Yên Vân tán ăn...” Bạch Đình cổ tay rung lên, đem độc dược ném tới."),i("br"),i("br"),n._v("“A?” Hải Minh Kim khóe miệng giật một cái."),i("br"),i("br"),n._v("Còn tưởng rằng là chuyện gì, không nghĩ tới Bạch độc sư là tính toán để hắn lấy thân thử độc!"),i("br"),i("br"),n._v("Kiểm nghiệm thuốc giải độc biện pháp hữu hiệu nhất, không phải nghiên cứu thành phần, mà là thử độc, như vậy trực tiếp nhất, cũng hữu hiệu nhất."),i("br"),i("br"),n._v("Chỉ là... Ngươi tìm thử đan thú loại hình đồ vật là được, làm gì tìm ta..."),i("br"),i("br"),n._v("“Tốt a!”"),i("br"),i("br"),n._v("Không chần chờ bao lâu, cắn răng một cái nhẹ gật đầu."),i("br"),i("br"),n._v("Mặc dù có chút không tình nguyện, vừa nghĩ tới tổ sư đan dược thần kỳ, còn tràn đầy tự tin mở ra nắp bình liền đem bên trong Yên Vân tán ăn một miếng."),i("br"),i("br"),n._v("Phốc!"),i("br"),i("br"),n._v("Mới vừa uống thuốc xong vật, vẻ mặt lập tức trở nên đen kịt, cả người hắn nhịn không được ho ra một ngụm máu tươi."),i("br"),i("br"),n._v("Nhìn thấy hắn một màn này, mọi người ở đây tất cả đều mày nhăn lại."),i("br"),i("br"),n._v("Mọi người thấp nhất đều là bát tinh cấp bậc độc sư, nhãn lực siêu quần, tự nhiên đó có thể thấy được trước mắt vị này là thật trúng độc, mà không phải ngụy trang."),i("br"),i("br"),n._v("“Cho!”"),i("br"),i("br"),n._v("Biết Yên Vân tán uy lực kinh người, Bạch Đình không dám do dự, đem trong tay giải độc đan đưa tới."),i("br"),i("br"),n._v("Hải Minh Kim vội vàng nuốt vào."),i("br"),i("br"),n._v("Vù vù!"),i("br"),i("br"),n._v("Mới vừa dùng không lâu, sắc mặt ngăm đen, lập tức biến hóa tới, chỉ bất quá, dường như còn không có hoàn toàn giải độc, một nửa đen, một nửa trắng, như là một cái mặt hoa."),i("br"),i("br"),n._v("“Khụ khụ, Bạch độc sư, còn có đan dược không, lại cho ta ăn một viên, ta vừa rồi hình như Yên Vân tán ăn nhiều, còn không có hoàn toàn giải xong...” Hô hấp đều đặn xuống, độc tính tạm thời ổn định, Hải Minh Kim mang theo lúng túng nói."),i("br"),i("br"),n._v("Những này cố bản bồi nguyên đan dược, cái đầu có hạn, dung nạp Thiên Đạo chân khí cũng có hạn, cứ việc có thể giải độc, có thể Yên Vân tán cấp bậc thực sự quá cao, một cái dược vật, căn bản không đủ để hoàn toàn giải hết, chỉ giải chừng phân nửa."),i("br"),i("br"),n._v("“Ta đây là một viên cuối cùng...” Bạch Đình khóe miệng giật một cái."),i("br"),i("br"),n._v("“A? Ta vừa rồi tại trên đường thời điểm, ăn xong rồi...” Hải Minh Kim thân thể nhoáng một cái, suýt chút nữa không khóc lên."),i("br"),i("br"),n._v("Chỉ là thí nghiệm đan dược cho mọi người nhìn, kết quả làm cho bộ dáng này, có muốn hay không như thế?"),i("br"),i("br"),n._v("Ta đây là trêu ai ghẹo ai?"),i("br"),i("br"),n._v("“Cái này...” Bạch Đình cũng lông mày nhảy loạn."),i("br"),i("br"),n._v("Vốn nghĩ, cầm tổ sư giải độc Thánh dược, biểu hiện ra cho mọi người nhìn, ai biết... Đan dược chưa đủ!"),i("br"),i("br"),n._v("“Không cần thử nữa, cái này đích xác là giải độc Thánh dược!”"),i("br"),i("br"),n._v("Hai người đang tại bất đắc dĩ, Lâm Giang Hải khoát tay áo: “Nguyên Lăng, giúp hắn đem độc giải!”"),i("br"),i("br"),n._v("“Vâng!” Nguyên Lăng đi tới, bàn tay tại Hải Minh Kim trên thân nhẹ nhàng phất một cái, người sau lập tức cảm thấy toàn thân nhẹ nhõm, mặt hoa giống như là tẩy trang, khôi phục như lúc ban đầu."),i("br"),i("br"),n._v("“Có thể đem cố bản bồi nguyên đan dược, luyện chế thành giải độc Thánh dược, liền Yên Vân tán đều có thể giải... Thủ đoạn này, quả thực nghe rợn cả người...”"),i("br"),i("br"),n._v("Không tiếp tục để ý Hải Minh Kim, Lâm Giang Hải đứng dậy, mấy bước đi tới Ngụy Như Yên trước mặt, tràn đầy khó tin."),i("br"),i("br"),n._v("Muốn nói trước đó hắn còn có điều hoài nghi, gần như là trăm phần trăm xác định."),i("br"),i("br"),n._v("Tiên Thiên độc thể, Tiên Thiên độc hồn thể không nói, chỉ nói vừa rồi cái kia đan dược, hắn thần thức đảo qua, không phát hiện bất luận cái gì đặc thù, kết quả nhưng có thực sự giải độc hiệu quả... Hơn nữa liền Yên Vân tán đều có thể giải... Vậy thì có chút kinh khủng."),i("br"),i("br"),n._v("Nguyên Lăng nghiên cứu ra cái độc dược này, hắn chuyên môn nhìn qua, Thánh vực bát trọng cường giả đều có thể nhẹ nhõm độc chết... Một cái nho nhỏ đan dược liền có thể giải hết, không có bất kỳ cái gì di chứng, loại thủ đoạn này, quả thực tài năng như thần, để cho người ta nhìn mà than thở."),i("br"),i("br"),n._v("Liền xem như hắn, đều khó mà lý giải."),i("br"),i("br"),n._v("Chỉ sợ cũng chỉ có chân chính khai phái tổ sư, mới có như vậy quỷ thần khó lường thủ đoạn."),i("br"),i("br"),n._v("“Lão sư, ngươi tin tưởng?”"),i("br"),n._v("Bạch Đình ánh mắt sáng lên."),i("br"),i("br"),n._v("“Ừm, loại trừ tổ sư, ta thực sự nghĩ không ra, người nào có thể có như thế năng lực...” Lâm Giang Hải nhẹ gật đầu, nhẹ nhàng cười một tiếng: “Đi, đi tham gia kế vị đại điển, Thẩm Khuyết muốn giết ta, ta ngược lại muốn xem xem, ai có thể cười đến cuối cùng!”"),i("br"),i("br"),n._v("Mắt sáng lên, Lâm Giang Hải híp mắt lại."),i("br"),i("br"),n._v("Có thể tại Độc điện tổng bộ sống sót, hơn nữa đứng tại đỉnh phong nhất, hắn tự nhiên cũng không phải cái gì người hiền lành."),i("br"),i("br"),n._v("“Tốt!”"),i("br"),i("br"),n._v("Thấy lão sư lần nữa lòng tin mười phần, mọi người tất cả đều nắm đấm xiết chặt, theo ở phía sau đi ra ngoài."),i("br"),i("br"),n._v("Kế vị đại điển ngay tại khoảng cách phủ đệ không xa trên quảng trường tổ chức, toàn bộ tổng bộ, không biết bao nhiêu người đều đi tới nơi này, bên trong ba vòng, bên ngoài ba vòng, lít nha lít nhít, một cái không nhìn thấy phần cuối."),i("br"),i("br"),n._v("Ở giữa nhất trên đài cao, một quyển sách lơ lửng ở trên không, tỏa sáng chói lọi, phía dưới một người mặc trường bào màu đen độc sư, đứng tại phía dưới, tản mát ra khí tức kinh người."),i("br"),i("br"),n._v("“Lâm tôn giả, ngươi rốt cuộc đã đến...”"),i("br"),i("br"),n._v("Thấy mọi người bay tới, áo bào đen độc sư nhẹ nhàng cười một tiếng, thanh âm vang vọng toàn bộ quảng trường: “Đã ngươi đồng ý mạng người nuôi nấng, vậy thì bắt đầu đi, mở ra truyền thế độc điển, ta đem bên trong nội dung truyền thụ cho mọi người lại kế vị, như vậy cũng có thể danh chính ngôn thuận!”"),i("br"),i("br"),n._v("“Lâm tôn giả, thân là tứ đại Tôn giả một trong, hưởng thụ Độc điện nhiều năm như vậy cung phụng, cũng nên làm ra hi sinh!”"),i("br"),i("br"),n._v("“Hi sinh một cái, tác thành nhiều như vậy độc sư, để chúng ta Độc điện càng thêm cường đại, đây là chuyện tốt... Yên tâm đi, ngươi sẽ ghi tên sử sách!”"),i("br"),i("br"),n._v("Trên đài cao, lại có hai người hô lên."),i("br"),i("br"),n._v("Chính là tứ đại Tôn giả bên trong hai người khác."),i("br"),i("br"),n._v("“Ha ha, Thẩm Khuyết, Vương tôn giả, Hà tôn giả, ta hiện tại tới, không phải tới nuôi dưỡng truyền thế độc điển, mà là... Muốn lần nữa thảo luận điện chủ nhân tuyển!”"),i("br"),i("br"),n._v("Không để ý tới mấy người lời nói, Lâm Giang Hải cười lạnh, thân thể nhoáng một cái, rơi xuống trên đài cao."),i("br"),i("br"),n._v("“Lần nữa thảo luận nhân tuyển? Lâm tôn giả, ngươi hẳn là hồ đồ rồi? Thẩm hộ pháp, nhận được truyền thế độc điển, danh chính ngôn thuận, làm sao... Ngươi chẳng lẽ còn có thể lại tìm một bản truyền thế độc điển tới?”"),i("br"),i("br"),n._v("Vương tôn giả cười lạnh một tiếng."),i("br"),i("br"),n._v("“Truyền thế độc điển, là Độc điện trọng yếu nhất bảo vật một trong, Thẩm hộ pháp tìm đến, đã chiếm cứ chí cao vô thượng vị trí, làm sao, ngươi là không phục Thẩm hộ pháp, còn là không phục khai phái tổ sư?” Hà tôn giả cũng khẽ nói."),i("br"),i("br"),n._v("Hai người này sớm đã cùng Thẩm hộ pháp đứng ở mặt trận thống nhất, trong lời nói tràn đầy châm chọc cùng khinh thường."),i("br"),i("br"),n._v("“Các ngươi không cần lên mặt nghĩa tới dọa ta, đã ta cảm giác tới đây, lần nữa thảo luận điện chủ nhân tuyển, tự nhiên chuẩn bị kỹ càng!”"),i("br"),i("br"),n._v("Lâm Giang Hải hất lên ống tay áo: “Ta mặc dù không tìm được truyền thế độc điển, nhưng... Ta tìm được khai phái tổ sư!”"),i("br"),i("br"),n._v("Nói đến đây, ngẩng đầu nhìn về phía trên không: “Cho mời tổ sư!”"),i("br"),i("br"),n._v("Soạt!"),i("br"),i("br"),n._v("Nguyên Lăng, Bạch Đình đám người lập tức đem Ngụy Như Yên mang theo tới, nhẹ nhàng đặt ở trên đài."),i("br"),i("br"),n._v("“Khai phái tổ sư?”"),i("br"),i("br"),n._v("“Có ý tứ gì?”"),i("br"),i("br"),n._v("Dưới đài một hồi xôn xao, tất cả độc sư, đều hai mặt nhìn nhau, không biết đến cùng xảy ra chuyện gì."),i("br"),i("br"),n._v("“Tổ sư?”"),i("br"),i("br"),n._v("Thẩm Khuyết cùng cái khác hai vị Tôn giả, cũng đều nhíu nhíu mày."),i("br"),i("br"),n._v("“Không sai, vị này chính là chúng ta khai phái tổ sư, hồn phách của nàng sống đến nay, lần nữa phụ thể tại một cái Tiên Thiên độc thể nữ hài trên thân, bất cứ lúc nào cũng sẽ sống lại, lần nữa thống lĩnh chúng ta, đi đến huy hoàng!”"),i("br"),i("br"),n._v("Lâm Giang Hải vung tay lên, chỉ hướng Ngụy Như Yên."),i("br"),i("br"),n._v("“Nàng là khai phái tổ sư?”"),i("br"),i("br"),n._v("“Nói đùa cái gì? Tổ sư mấy vạn năm trước cũng đã về cõi tiên, tại sao sẽ là như vậy một cái mười mấy tuổi nữ hài?”"),i("br"),i("br"),n._v("Mọi người đầu tiên là sững sờ, lập tức tất cả đều xôn xao."),i("br"),i("br"),n._v("Thẩm Khuyết cũng cười nhạo lên tiếng: “Lâm tôn giả, ta nhìn ngươi là sợ hãi là độc điện làm cống hiến, đã mất lý trí đi? Tùy tiện tìm một người liền là khai phái tổ sư... Làm sao không tìm người, nói hắn là Khổng sư?”"),i("br"),i("br"),n._v("“Đúng vậy a!”"),i("br"),i("br"),n._v("Mọi người dưới đài cũng tất cả đều cười lắc đầu."),i("br"),i("br"),n._v("Vị này Vạn Độc Tôn Giả bình thường đều khẩu đáng tin cậy, làm sao lần này như vậy lỗ mãng?"),i("br"),i("br"),n._v("Như vậy một cái hôn mê bất tỉnh gia hỏa, liền nói là khai phái tổ sư, nói đùa cái gì!"),i("br"),i("br"),n._v("“Làm càn!” Lâm Giang Hải nhướng mày: “Thẩm Khuyết, ngươi thân là cửu tinh độc sư, sẽ không liền Tiên Thiên độc thể đều nhận không ra a?”"),i("br"),i("br"),n._v("“Tiên Thiên độc thể?”"),i("br"),i("br"),n._v("“Cô bé này là Tiên Thiên độc thể?”"),i("br"),i("br"),n._v("Một hồi xôn xao, Thẩm Khuyết cũng là sững sờ, vội vàng nhìn sang, quả nhiên để hắn nhìn ra không đúng, trong ánh mắt hàn mang lóe lên."),i("br"),i("br"),n._v("“Tiên Thiên độc thể, là một loại thể chất đặc thù, vài vạn năm đến, cũng chỉ có tại khai phái tổ sư trên thân mới xuất hiện qua, chúng ta ở đây chư vị, đều không có gặp qua, ngươi tùy tiện tìm một cái nữ hài tới nói, liền nói là loại thể chất này... Nhưng có chứng cứ? Hơn nữa, liền xem như loại thể chất này, cùng khai phái tổ sư có quan hệ gì?”"),i("br"),i("br"),n._v("Hất lên ống tay áo, Thẩm Khuyết cười lạnh: “Vì không cho ta làm điện chủ, để ngươi quyền lợi không bị ảnh hưởng, cái này da hổ kéo tới có chút quá lớn đi!”"),i("br"),i("br"),n._v("“Xé da hổ?”"),i("br"),i("br"),n._v("“Thật là có khả năng!”"),i("br"),i("br"),n._v("“Tiên Thiên độc thể, ai cũng chưa thấy qua, tùy tiện tìm người liền nói đúng vậy, nào có trùng hợp như vậy...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Quả nhiên, Thẩm Khuyết lời nói vừa kết thúc, dưới đài lập tức huyên náo lên."),i("br"),i("br"),n._v("Trước đó, mọi người còn có chút kỳ quái, một câu xuống, đã tràn đầy hoài nghi."),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1101"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1103"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);