(window.webpackJsonp=window.webpackJsonp||[]).push([[1301],{1657:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2142-thanh-chu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2142-thanh-chu"}},[n._v("#")]),n._v(" Chương 2142: Thành chủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Mặt khác thành cũng là như vậy? Cũng có thành chủ bia, đời trước thành chủ, có thể đem ý niệm gia trì ở phía trên?”"),t("br"),t("br"),n._v("Trương Huyền nhìn về phía một bên Tề Linh Nhi."),t("br"),t("br"),n._v("Nếu nàng cũng là một vị thành chủ, khẳng định trải qua loại sự tình này, có lẽ có thể biết một ít."),t("br"),t("br"),n._v("“Thành chủ bia, mỗi cái thành đều có, dùng để xác định quy củ, chẳng qua... Hầu như không có người có thể đem ý niệm gia trì ở phía trên, này bằng với cho bia đá khải linh, để nó biến thành pháp bảo! Về sau, toàn bộ thành hội tụ nguyện lực, đều sẽ khỏe mạnh bia đá, mà đối với hắn tác dụng không lớn.”"),t("br"),t("br"),n._v("Tề Linh Nhi giải thích."),t("br"),t("br"),n._v("Nàng cũng rất kỳ quái."),t("br"),t("br"),n._v("Loại sự tình này, coi như tại hoàng thành, đều không có làm sao nghe qua."),t("br"),t("br"),n._v("Thành chủ bia trấn áp một thành, quy định đồ vật, tương đương với pháp luật, đại biểu công chính cùng vô tư, đem bản thân ý niệm lưu tại phía trên, về sau toàn bộ thành sinh ra nguyện lực, đều đem tẩm bổ đạo ý niệm này, thật có thể bảo đảm nhận khống chế của mình?"),t("br"),t("br"),n._v("Đừng nói cùng nhau ý niệm, coi như phân thân, rời khỏi bản thể thời gian lâu dài, đồng dạng không cách nào duy trì bản tâm."),t("br"),t("br"),n._v("Như là Thượng Thương vị kia Khổng sư!"),t("br"),t("br"),n._v("“Dạ Vũ Thiên Thần nhân phẩm làm sao?”"),t("br"),t("br"),n._v("Không có tiếp tục luyện hóa thành chủ bia, Trương Huyền nhìn về phía cách đó không xa Mính Lai Hương."),t("br"),t("br"),n._v("Dạ Vũ Thiên Thần, chính là đời trước thành chủ, linh khí thuỷ triều tử vong vị kia."),t("br"),t("br"),n._v("“Ngay thẳng, công bằng, người người ca tụng.” Mính Lai Hương cười khổ nói: “Nguyên nhân chính là ai cũng không làm được điểm ấy, cho nên bia đá mới khó mà luyện hóa, cũng không cách nào làm cho tất cả mọi người tin tưởng và nghe theo, Dạ Huy thành mới nhiều năm như vậy, cũng không có xuất hiện chân chính thành chủ...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Khó trách."),t("br"),t("br"),n._v("Trước đó liền suy nghĩ, thành chủ nếu có thể mượn truyền tống trận, cũng không ít cường giả, đều nguyện ý làm mới là, vì sao tiếp cận mười năm, như vậy lớn thành thị đều không người thống trị, nhìn tới cũng không phải là tưởng tượng dễ dàng như vậy."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Mính Lai Hương cứ việc tỷ thí thua bởi chính mình, lại có vẻ cũng không thèm để ý, bởi vì nàng biết, muốn trở thành thành chủ, trước hết qua bia đá con đường này, nếu không, nhiều nhất giống như nàng, trở thành người dự bị."),t("br"),t("br"),n._v("“Không thể phá hủy, không thể xoá bỏ, vậy thì cùng vũ khí đồng dạng luyện hóa đi!”"),t("br"),t("br"),n._v("Bình thường có được chính mình linh tính vũ khí, người khác muốn luyện hóa, cũng cần thời gian mấy năm ở chung, mới có thể từng chút một cảm hóa, mà Trương Huyền không cần."),t("br"),t("br"),n._v("Thân thể vụt qua, lần nữa tới đến bia đá trước mặt, liên tục điểm vài cái, miệng khẽ động, truyền âm qua."),t("br"),t("br"),n._v("Vù!"),t("br"),t("br"),n._v("Bia đá lắc lư một cái, cũng không thần phục."),t("br"),t("br"),n._v("Luyện hóa vũ khí, chỉ cần tìm được thiếu hụt, lấy hủy diệt linh tính làm đại giá, đối phương khẳng định thần phục, trước mắt cái này bia đá, hiển nhiên so phổ thông vũ khí càng thêm kiên cường."),t("br"),t("br"),n._v("Bởi vì nó biết, trước mắt vị này tuyệt đối không dám phá hủy."),t("br"),t("br"),n._v("“Không đồng ý cũng không sao, ngươi đem ý niệm lưu tại trong tấm bia đá, hẳn là đoán được bản thân sẽ gặp được nguy hiểm, hy vọng có thể hấp thụ nhiều một ít nội thành nguyện lực, sớm chút phục sinh!”"),t("br"),t("br"),n._v("Trương Huyền khẽ mỉm cười, tiếp tục truyền âm nói: “Ý tưởng thật là tốt, nhưng nội thành không có cái mới thành chủ xuất hiện, quy tắc phá hoại hầu như không còn, nguyện lực cũng liền dần dần giảm bớt, đã tiếp cận mười năm, ngươi thành công không? Thành công, liền sẽ không một mực đợi tại trong tấm bia đá, khẳng định là không cách nào thành công, tương lai cũng một mực sẽ không thành công!”"),t("br"),t("br"),n._v("“Ta đối thành chủ không có gì ham muốn, chỉ muốn mượn thành chủ lệnh bài sử dụng, dùng để tiến hành không gian truyền tống, nếu như ta làm thành chủ, có thể tiếp tục tiếp tục sử dụng ngươi quyết định quy củ, ý niệm của ngươi thậm chí có thể đồng dạng lưu tại trong đó. Có tân thành chủ, quy củ lần nữa bắt đầu sử dụng, mới có thể hấp thu nhiều hơn nữa nguyện lực, lần nữa khôi phục!”"),t("br"),t("br"),n._v("Trương Huyền thanh âm bên trong mang theo sư ngôn thiên bẩm lực lượng, trong tấm bia đá ý niệm tựa hồ nghe đến hắn lời nói, trước đó kịch liệt phản kháng cảm xúc chậm chạp ổn định lại."),t("br"),t("br"),n._v("Chức thành chủ, Trương Huyền cũng không thèm để ý."),t("br"),t("br"),n._v("Coi như về sau cùng Khổng sư đồng dạng, cần nguyện lực, cũng tuyệt không phải một thành trên đất có thể thỏa mãn."),t("br"),t("br"),n._v("Cho nên, đối phương muốn, cho hắn chính là, quy củ không thay đổi tốt, bởi vì chính mình cũng không rảnh phản ứng."),t("br"),t("br"),n._v("“Mau chóng làm quyết định đi, ta không có nhiều như vậy kiên nhẫn! Thành chủ có làm hay không ta không có vấn đề, cùng lắm thì, đem bia đá phá hủy.” Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("Đối quyền thế không có quá lớn ham muốn, cũng không quan trọng đối phương uy hiếp."),t("br"),t("br"),n._v("“Cho ngươi thời gian mười hơi thở cân nhắc!”"),t("br"),t("br"),n._v("Nói xong những này, Trương Huyền không tại nhiều ngôn, mà là lui về sau hai bước, hai tay chắp sau lưng."),t("br"),t("br"),n._v("“Thế nào? Có phải hay không không cách nào luyện hóa?”"),t("br"),t("br"),n._v("Mính Lai Hương cười cười, nói."),t("br"),t("br"),n._v("Nhìn thấy đối phương như vậy, nàng cũng coi như an ủi."),t("br"),t("br"),n._v("Tuy là thực lực ngươi so với ta mạnh hơn, thiên phú cao hơn ta, nhưng ở luyện hóa trên tấm bia đá, cũng là đại ca không cười nhị ca, không kém bao nhiêu..."),t("br"),t("br"),n._v("Đang muốn nhìn đối phương ăn quả đắng hình dáng, ngay sau đó nhìn thấy thanh niên trước mắt, vẻ mặt lạnh nhạt đứng tại chỗ, thanh âm nhàn nhạt từ trong miệng phát ra."),t("br"),t("br"),n._v("“Mười, chín, tám, bảy...”"),t("br"),t("br"),n._v("“Sao?”"),t("br"),t("br"),n._v("Mính Lai Hương sững sờ."),t("br"),t("br"),n._v("Ta hỏi ngươi có phải hay không cũng không cách nào luyện hóa, đếm ngược làm cái gì?"),t("br"),t("br"),n._v("“... Ba, hai, một!”"),t("br"),t("br"),n._v("Ngay tại nghi hoặc, Trương Huyền liền đem con số đếm xong, nhìn về phía bia đá: “Làm quyết định đi!”"),t("br"),t("br"),n._v("Nói xong, một đạo kiếm khí tại đầu ngón tay tung hoành, bất cứ lúc nào cũng sẽ bắn ra."),t("br"),t("br"),n._v("Cảm nhận được hắn trong kiếm ý kiên quyết, dường như thật không đáp ứng, thực có can đảm ra tay, phát ra một tiếng vang lên, toàn bộ bia đá bất ngờ đung đưa."),t("br"),t("br"),n._v("“Cái này còn tạm được!”"),t("br"),n._v(" Trương Huyền khẽ mỉm cười, lần nữa đem ẩn chứa ý niệm máu tươi nhỏ đi tới, sau một khắc liền bị hấp thu đi vào, triệt để luyện hóa."),t("br"),t("br"),n._v("“Cái này...” Mính Lai Hương ngây người."),t("br"),t("br"),n._v("Trước đó nàng dùng không biết bao nhiêu loại phương pháp, đều không có thành công, trước mắt vị này chỉ đếm ngược mười cái đếm, liền luyện hóa... Có muốn hay không đơn giản như vậy?"),t("br"),t("br"),n._v("“Ta đã luyện hóa bia đá, có hay không có thể làm thành chủ?”"),t("br"),t("br"),n._v("Cảm nhận được ý niệm của mình, đồng dạng xuất hiện tại trên tấm bia đá, Trương Huyền cười nhạt một tiếng."),t("br"),t("br"),n._v("Tuy là lòng tràn đầy không cam lòng, Mính Lai Hương cuối cùng gật gật đầu."),t("br"),t("br"),n._v("Đánh không lại đối phương, luyện hóa bia đá, cũng xa xa không kịp, không thừa nhận cũng không được."),t("br"),t("br"),n._v("“Đây là một viên Mỹ Dung đan, đưa cho ngươi, mặc dù không thể thay đổi khung xương kết cấu, để ngươi trở nên càng giống nữ tử, vẫn là rất dễ dàng!” Trương Huyền cong ngón tay búng một cái, một viên hạ phẩm Thần Nguyên đan bay đi: “Lâm Tạ, Ngọ Dương bên kia giúp ta giải quyết, sau hai canh giờ, ta muốn nội thành tất cả mọi người biết, ta là tân nhiệm thành chủ, vẫn như cũ tuân thủ đời trước thành chủ tuân thủ quy củ, không làm thay đổi!”"),t("br"),t("br"),n._v("Tuy là luyện hóa bia đá, những người khác cũng sẽ đồng ý hắn làm thành chủ, nhưng vì để tránh cho phiền phức, vẫn là để vị này Mính Lai Hương ra mặt cho thỏa đáng."),t("br"),t("br"),n._v("Có thể tranh đoạt lâu như vậy, mà không bị đào thải, khẳng định có thế lực của mình cùng phương pháp."),t("br"),t("br"),n._v("Lợi dụng được, so với chính mình tự mình đến, dễ dàng rất nhiều."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Chần chờ một chút, Mính Lai Hương gật đầu."),t("br"),t("br"),n._v("Đối phương chính mình đồng dạng có thể đem sự tình giải quyết, để nàng hỗ trợ chỉ là vì tránh khỏi phiền phức mà thôi, đã như vậy, còn không bằng đáp ứng."),t("br"),t("br"),n._v("Dù sao loại này Mỹ Dung đan, có tiền mà không mua được."),t("br"),t("br"),n._v("Đáp ứng đối phương, há miệng đem đan dược nuốt xuống."),t("br"),t("br"),n._v("Ngay sau đó lấy ra một tấm gương đồng nhìn sang, con mắt phóng ra quang mang."),t("br"),t("br"),n._v("Quả nhiên cùng trong truyền thuyết đồng dạng, sau khi dùng thuốc, hiệu quả nhanh chóng, dung mạo cùng làn da, rất nhanh liền hữu chất bay vọt cùng thay đổi cứ việc xương cốt cùng dáng người vẫn như cũ giống như nam nhân, dung mạo lại trở nên so trước đó vũ mị nhiều."),t("br"),t("br"),n._v("Để Trương Huyền đám người trước vào ở phủ thành chủ, Mính Lai Hương ngồi phi hành thần thú, cấp tốc bay ra ngoài."),t("br"),t("br"),n._v("Không hổ là trước đó trở thành thành chủ ý kiến lớn nhất người dự bị, không đến hai canh giờ, tin tức liền triệt để truyền ra, tất cả mọi người biết một vị gọi Trương Huyền người, kế thừa chức thành chủ, cũng xác lập quy tắc."),t("br"),t("br"),n._v("Tin tức truyền ra, không đến sập tối, U Hồn hoàng thành liền truyền đến chấp nhận sách cùng đại biểu thân phận thành chủ lệnh bài."),t("br"),t("br"),n._v("“Thành công...” Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Có thứ này, liền có thể mượn nội thành truyền tống trận, cấp tốc chạy tới U Hồn hoàng thành."),t("br"),t("br"),n._v("“Đây là Dạ Huy thành ba quận lệnh bài!” Thấy hắn nhanh như vậy trở thành thành chủ, Tề Linh Nhi tràn đầy kinh ngạc, nhưng cũng không lời nào để nói."),t("br"),t("br"),n._v("Nàng lúc trước vì đứng đầu một thành, suýt chút nữa không có đem mệnh đều chôn vùi, đối phương từ khi đi vào, đến trở thành thành chủ, liền nửa ngày đều vô dụng... Giữa người và người, thật đúng là không thể so được."),t("br"),t("br"),n._v("“Đem thứ này luyện hóa!”"),t("br"),t("br"),n._v("Đem quận chúa lệnh bài đưa cho Tôn Cường cùng cha mẹ ba người, Trương Huyền lần nữa nhìn tới: “Tiểu hoàng kê cùng Trương Giáp đều là ta thú sủng, cùng ta đi cùng một người, hẳn là có thể tiếp nhận truyền tống đi!”"),t("br"),t("br"),n._v("“Có thể!” Tề Linh Nhi gật đầu."),t("br"),t("br"),n._v("Thú sủng bất luận danh sư đại lục, Thượng Thương vẫn là Thần giới, bởi vì có linh hồn khế ước, cơ bản đều tương đương với cùng là một người."),t("br"),t("br"),n._v("Cho nên, bọn chúng có hay không thành chủ lệnh bài, cũng có thể hưởng thụ truyền tống trận chỗ tốt."),t("br"),t("br"),n._v("Trương Huyền thở phào nhẹ nhõm, bàn giao nói: “Các ngươi tranh thủ ngày hôm nay quân lệnh bài luyện hóa, sáng mai, chúng ta xuất phát đi U Hồn hoàng thành!”"),t("br"),t("br"),n._v("Vừa trở thành thành chủ, rất nhiều chuyện cần an bài, làm cho tất cả mọi người đều biết bản thân vị thành chủ này tồn tại, nếu không, không còn tồn tại cảm, nguyện lực mất đi hiệu quả, liền không thể sử dụng truyền tống trận."),t("br"),t("br"),n._v("Huống chi, Tôn Cường mấy người cũng không có đem quận chúa lệnh bài luyện hóa."),t("br"),t("br"),n._v("Rời khỏi phủ thành chủ, Trương Huyền đi vào phía trước quảng trường, thân thể vụt qua, đi vào thành chủ bia phía trên, khoanh chân ngồi xuống."),t("br"),t("br"),n._v("“Nếu trở thành Dạ Huy thành thành chủ, tự nhiên hi vọng nội thành cao thủ càng ngày càng nhiều, sau hai canh giờ, ta ở đây giảng bài, chỉ điểm các vị trong tu luyện nghi hoặc!”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, Trương Huyền âm thanh vang vọng bốn phía."),t("br"),t("br"),n._v("Mặc dù không biết nguyện lực đến cùng có ích lợi gì, nhưng có thể để cho người khác ghi nhớ, khiến người khác cảm thấy ân huệ, liền có thể liên tục không ngừng đạt được."),t("br"),t("br"),n._v("Mà đối với hắn tới nói, giảng bài, tuyệt đối là mau lẹ nhất, thuận tiện nhất phương pháp."),t("br"),t("br"),n._v("Cùng Khổng sư xây dựng Danh Sư đường đồng dạng, truyền đạo học nghề giải thích nghi hoặc, đồng thời truyền thụ tư tưởng của mình, mới có thể truyền thừa trăm đời mà không suy."),t("br"),t("br"),n._v("Như danh sư đại lục, Khổng sư danh tiếng, vạn cổ bất diệt."),t("br"),t("br"),n._v("Thành chủ tự thân giảng bài, tin tức như thủy triều lan truyền, không đến hai canh giờ, phủ thành chủ phía trước trên quảng trường, liền hội tụ không dưới trăm vạn tu luyện giả."),t("br"),t("br"),n._v("Nhìn thấy tới nhiều như vậy, Trương Huyền khẽ mỉm cười, sư ngôn thiên bẩm âm thanh vang vọng toàn bộ quảng trường."),t("br"),t("br"),n._v("“Tu luyện, ở chỗ tâm, ở chỗ Thần, ở chỗ hồn phách...”"),t("br"),t("br"),n._v("Âm thanh như thủy triều, như gió xuân, ấm áp nhân tâm."),t("br"),t("br"),n._v("Đám người nghe như si như say."),t("br"),t("br"),n._v("Trước đó, còn cảm thấy Mính Lai Hương tại sao lại đột nhiên khuất phục một cái chừng hai mươi tuổi thanh niên, thậm chí cảnh cáo nhóm người mình cũng không cần tranh chấp, Ngọ Dương tự thân tới nghe một tiết khóa, triệt để bái phục."),t("br"),t("br"),n._v("Khó trách có thể trở thành thành chủ, không nói mặt khác, chỉ nói đối tu luyện hiểu, liền so với bọn hắn thấy qua bất kỳ một cao thủ nào đều cao minh hơn."),t("br"),t("br"),n._v("Từ nông tới sâu, nội dung sâu sắc, lời lẽ dễ hiểu, có thể đoán được, Dạ Huy thành nghe giảng bài nhiều người như vậy tu vi, tất nhiên sẽ có biến hóa nghiêng trời lệch đất, toàn bộ thành thị về sau nhất định có kinh người phát triển!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2156"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2158"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);