(window.webpackJsonp=window.webpackJsonp||[]).push([[1302],{1656:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2143-lam-ta"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2143-lam-ta"}},[n._v("#")]),n._v(" Chương 2143: Lâm Tạ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Dạ Huy thành, tam đại gia tộc, Lâm, Điền, Triệu."),t("br"),t("br"),n._v("Lâm gia bởi vì xuất hiện Lâm Tạ, ghi tên thứ nhất, gia chủ, cứ việc không bằng vị này, tu vi cũng đạt tới hạ phẩm Thần Linh đỉnh phong, một đôi tử mẫu kiếm uy lực vô tận, để cho người ta kiêng kị."),t("br"),t("br"),n._v("Còn Triệu gia, thực lực yếu nhất, bất luận nhân tài dự trữ vẫn là gia chủ, đều kém xa trước hai cái, có thể ngang hàng tam đại gia tộc, là bởi vì, khống chế toàn bộ thành thị thương hội, tiền bạc vô số."),t("br"),t("br"),n._v("Lâm gia phủ đệ."),t("br"),t("br"),n._v("“Lâm Tạ, thực lực ngươi mạnh như vậy, so về Mính Lai Hương không hề yếu, tại sao phải nghe nàng lời nói, từ bỏ chức thành chủ? Ngươi không phải một người, sau lưng còn có toàn cả gia tộc!”"),t("br"),t("br"),n._v("Nhìn trước mắt trung niên nhân, Lâm gia chủ chỉ tiếc mài sắt không nên kim."),t("br"),t("br"),n._v("Lâm Tạ là Lâm gia nổi danh nhất thiên tài, cũng là tu vi người mạnh nhất, vốn cho rằng có thể cầm xuống chức thành chủ, để gia tộc càng ngày càng huy hoàng, nằm mơ không nghĩ tới, nửa đường giết ra cái Trình Giảo Kim, vị trí này bị một cái không biết tên tiểu gia hỏa cướp đi."),t("br"),t("br"),n._v("Bị cướp đi, vậy thì nghĩ biện pháp đoạt lại, trước mắt vị này, không những không có đi khiêu chiến, còn trực tiếp thừa nhận đối phương... Để hắn càng nghĩ càng thấy đến tức giận cùng buồn bực."),t("br"),t("br"),n._v("Lâm gia duy nhất một lần, cơ hội một bước lên trời, liền bị như vậy chôn vùi."),t("br"),t("br"),n._v("“Mính Lai Hương không phải là đối thủ, ta cũng không phải, hơn nữa hắn còn luyện hóa thành chủ bia, cùng hắn tự rước lấy nhục, bị mất mặt, còn không bằng sảng khoái thừa nhận!” Lâm Tạ lắc đầu, vẻ mặt lãnh đạm: “Được rồi, ta muốn tu luyện, không có việc gì, không được tìm ta!”"),t("br"),t("br"),n._v("Nói xong, thân thể xoay một cái, đi về phòng, còn chưa đi xa, chỉ thấy quản gia vội vã đi vào, ôm quyền khom người: “Gia chủ, Lâm Tạ thiếu gia!”"),t("br"),t("br"),n._v("Lâm gia chủ đứng chắp tay."),t("br"),t("br"),n._v("“Vị kia tân nhiệm thành chủ, báo tin nội thành, công khai giảng bài! Hiện tại giảng bài đã bắt đầu, không ít người đi nghe, gia tộc bọn ta có muốn hay không cũng phái người tới?” Quản gia nói."),t("br"),t("br"),n._v("“Phái người? Cái tên này, vừa mới cướp đi thuộc về Lâm gia chúng ta chức thành chủ, không có đi qua làm loạn là tốt lắm rồi, nghe giảng bài, nằm mơ! Làm sao, còn muốn để chúng ta người của Lâm gia, làm học sinh của hắn hay sao?” Lâm gia chủ bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Cái này...” Quản gia chần chờ một chút, nói: “Nghe giảng bài người báo lại, vị này tân nhiệm thành chủ, giảng bài rất tốt, để không ít người mao nhét mở ra, được lợi rất nhiều...”"),t("br"),t("br"),n._v("“Được lợi rất nhiều? Mê hoặc, thu mua nhân tâm trò xiếc mà thôi!” Lâm gia chủ bàn tay lớn vẫy một cái: “Được rồi, cảnh cáo tộc nhân, ai cũng không cho phép nghe giảng bài, không chấp nhận ân huệ của hắn! Như làm trái đọc, từ bỏ tộc nhân hộ tịch, vĩnh viễn không cho phép bước vào gia tộc nửa bước!”"),t("br"),t("br"),n._v("“Vâng...” Gặp gia chủ làm ra quyết định, đành phải đem lời muốn nói nuốt tại cổ họng, quản gia bất đắc dĩ đi ra ngoài, truyền mệnh lệnh."),t("br"),t("br"),n._v("“Lâm Tạ, ngươi nghe được đi, cái tên này vừa biến thành thành chủ, liền thi triển ơn huệ nhỏ, chờ hắn triệt để củng cố, toàn bộ Dạ Huy thành còn không triệt để rơi vào trong khống chế? Ngươi không phải muốn tu luyện ư? Hiện tại ta để bát đại trưởng lão cùng một chỗ tới, mượn gia tộc thần bí nhất trận pháp, giúp ngươi tăng cao tu vi. Nhất định phải đem chức thành chủ đoạt tới!” Lâm gia chủ nghiến răng."),t("br"),t("br"),n._v("“Cái này... Làm sao có thể! Mượn trận pháp tăng lên, sẽ để cho tám vị trưởng lão nhận tổn thương rất nặng, thậm chí tuổi thọ đều muốn bị tổn thương...”"),t("br"),t("br"),n._v("Lâm Tạ sững sờ."),t("br"),t("br"),n._v("Lâm gia, có một loại bí pháp, có thể đem người khác lực lượng tụ lại, để người được lợi, trong thời gian ngắn, tu vi tăng lên một phần, bởi vì tăng lên không tính quá lợi hại, lại tổn hại cực lớn, hầu như không chút sử dụng qua."),t("br"),t("br"),n._v("Nằm mộng cũng nghĩ không ra, gia chủ hiện tại muốn dùng."),t("br"),t("br"),n._v("“Không cần nói, chỉ cần ngươi thành thành chủ, lớn hơn nữa tổn thất, đều có thể bù đắp tới, ngươi bây giờ chỉ cần làm một chuyện, hảo hảo điều chỉnh trạng thái, chuẩn bị tiếp nhận bát đại trưởng lão lực lượng!”"),t("br"),t("br"),n._v("Lâm gia chủ bàn tay lớn vẫy một cái, ngắt lời hắn, truyền xuống mệnh lệnh."),t("br"),t("br"),n._v("Rất nhanh, hạ phẩm Thiên Thần cảnh trưởng lão xuất hiện tại gian phòng, bày trận, truyền tống lực lượng, chơi đùa bốn canh giờ."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trong cơ thể một hồi sôi trào, Lâm Tạ chỉ cảm thấy khí tức cả người, như rồng giống như giao, có cực lớn bay vọt."),t("br"),t("br"),n._v("Vội vàng đứng dậy, trong đan điền, bùng nổ giống như âm thanh vang lên, trước đó một mực cầm cố lại tu vi, thời gian nháy mắt có đột phá."),t("br"),t("br"),n._v("Nửa bước thượng phẩm Thiên Thần!"),t("br"),t("br"),n._v("Tuy là không có triệt để đi đến thượng phẩm Thiên Thần cảnh, lại chỉ còn lại có nửa bước, bất luận sức chiến đấu vẫn là ý thức, đều có tăng lên cực lớn cùng bay vọt."),t("br"),t("br"),n._v("“Có loại thực lực này, lo gì chức thành chủ không chiếm được...” Xiết chặt nắm đấm, thở ra một hơi, Lâm Tạ con mắt sáng lên."),t("br"),t("br"),n._v("Trước đó, hắn cùng Mính Lai Hương chiến đấu, thắng bại năm năm số lượng, mà giờ khắc này, là dùng bí pháp, hấp thu tám vị trưởng lão lực lượng, sức chiến đấu so trước đó cao đâu chỉ gấp đôi!"),t("br"),t("br"),n._v("Lại tranh đoạt thành chủ lời nói, tuyệt đối có thể một lần thành công!"),t("br"),t("br"),n._v("“Đa tạ chư vị...”"),t("br"),t("br"),n._v("Nhìn về phía ngồi dưới đất, đã sinh cơ không sai biệt lắm hao hết tám vị trưởng lão, Lâm Tạ gật gật đầu, tràn đầy biết ơn."),t("br"),t("br"),n._v("Bí pháp thi triển, tương đương với dùng tám người tu vi, giúp mình tăng lên từng chút một..."),t("br"),t("br"),n._v("Thoáng cái mất đi tám vị cao thủ, đổi lại trước đó giằng co thời điểm, ai cũng không nỡ lòng bỏ, mà bây giờ, thế cuộc phát sinh thay đổi, lại không ra tay, đối phương triệt để chưởng khống dân tâm, còn muốn giành lấy liền khó khăn!"),t("br"),t("br"),n._v("Chỉ cần hắn thành thành chủ, tất cả tổn thất đều sẽ nhận được bù đắp!"),t("br"),t("br"),n._v("“Không cần phải nói nhiều như vậy, đi đem chức thành chủ cướp về, chúng ta cho dù chết, cũng đáng...” Một vị trắng như tuyết sợi râu trưởng lão, uể oải nói."),t("br"),t("br"),n._v("“Đi thôi, chức thành chủ, nhất định là Lâm gia chúng ta...” Lại một vị trưởng lão nói."),t("br"),t("br"),n._v("“Được...” Lâm Tạ gật gật đầu, tích súc lực lượng, vừa định đi ra ngoài, chỉ thấy trước đó quản gia lần nữa vội vã đi vào."),t("br"),t("br"),n._v("“Bẩm báo gia chủ, Lâm Tạ thiếu gia... Tán tu Ngọ Dương, Điền gia gia chủ Điền Phi, Triệu gia gia chủ Triệu mạnh, mang theo một đám hậu bối, đến đây thăm hỏi!”"),t("br"),t("br"),n._v("“Thăm hỏi? Lúc này tới thăm hỏi cái gì?”"),t("br"),t("br"),n._v("Gia chủ cùng Lâm Tạ liếc mắt nhìn nhau, từng người ánh mắt lộ ra nghi hoặc."),t("br"),t("br"),n._v("Bình thường tam đại gia tộc tuy có kết giao, lại từng người cảnh giác, cái này hơn nửa đêm chạy tới làm gì?"),t("br"),t("br"),n._v("“Cho mời!”"),t("br"),t("br"),n._v("Tuy là kỳ lạ, vẫn là vẫy vẫy tay."),t("br"),t("br"),n._v("Rất nhanh, mọi người đi tới gian phòng."),t("br"),t("br"),n._v("“Gặp qua Lâm gia chủ, Lâm Tạ thiếu gia!” Đám người ôm quyền."),t("br"),t("br"),n._v("“Không biết chư vị, đêm khuya tới ta Lâm gia, vì chuyện gì?”"),t("br"),t("br"),n._v("Lâm gia chủ nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Chúng ta vừa nghe xong thành chủ giảng bài, không thấy người của Lâm gia đi qua nghe giảng bài, cố ý tới hỏi một chút!” Điền Phi gia chủ cười cười."),t("br"),n._v("“Nghe giảng bài? Ta Lâm gia, đường đường Dạ Huy thành đệ nhất gia tộc, cao thủ như thế nào không có, đi nghe một cái miệng còn hôi sữa tiểu tử khóa?” Lâm gia chủ sầm mặt lại: “Nói đùa cái gì!”"),t("br"),t("br"),n._v("“Lâm gia chủ, chúng ta tới, là cảm thấy các ngươi gia tộc không có người nghe giảng bài, cố ý đem ghi chép ngọc tinh ghi chép lại nội dung chia sẻ các ngươi, không muốn nghe liền thôi, làm gì làm nhục ta sư?” Điền Phi gia chủ sầm mặt lại."),t("br"),t("br"),n._v("Nghe hai canh giờ khóa, ở sâu trong nội tâm, đã đem trên vách đá vị kia vững vàng thận trọng thanh niên, trở thành lão sư của mình."),t("br"),t("br"),n._v("“Ngươi sư? Ha ha, coi như hắn là tân thành chủ, cũng không cần như vậy nịnh bợ đi! Không có gì bất ngờ xảy ra, hắn vị trí này lập tức liền muốn mất đi, trở thành U Hồn thiên trong lịch sử, đoản mệnh nhất thành chủ...”"),t("br"),t("br"),n._v("Lâm gia chủ cười lạnh."),t("br"),t("br"),n._v("“Lập tức mất đi? Ngươi muốn đoạt quyền?” Sửng sốt một chút, Điền Phi gia chủ ngay sau đó nhìn về phía Lâm Tạ, lập tức bừng tỉnh hiểu ra: “Nguyên lai tu vi vừa mới đột phá, khó trách nói như vậy...”"),t("br"),t("br"),n._v("“Nếu Lâm Tạ huynh đột phá, đi đến nửa bước thượng phẩm Thiên Thần tình trạng, không bằng chúng ta lại tỷ thí một trận làm sao?”"),t("br"),t("br"),n._v("Một bên một mực không lên tiếng Ngọ Dương, đánh gãy Điền Phi gia chủ, tiến về phía trước một bước."),t("br"),t("br"),n._v("Hai người cạnh tranh thành chủ hơn nửa năm, giao thủ qua không dưới năm lần, đối tu vi của mình biết rất nhiều."),t("br"),t("br"),n._v("“Vui lòng tiếp!” Lâm Tạ khẽ mỉm cười."),t("br"),t("br"),n._v("Vừa đột phá, muốn luyện tập, lập uy, đối phương liền xông lại, không còn gì tốt hơn."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thân ảnh vụt qua, hắn vọt tới, năm ngón tay mở ra, một quyền đánh tới."),t("br"),t("br"),n._v("Lập uy, tự nhiên muốn đánh ra uy phong cùng khí thế."),t("br"),t("br"),n._v("Vừa ra tay liền thi triển ra lực lượng cường đại nhất, không có lưu mảy may hậu chiêu."),t("br"),t("br"),n._v("Đùng!"),t("br"),t("br"),n._v("Người còn chưa tới đến đối phương trước mặt, đột nhiên cảm thấy trên mặt tê rần, toàn thân không tự chủ được dạo qua một vòng."),t("br"),t("br"),n._v("Vội vàng nhìn lại, chỉ thấy trước đó cùng hắn đấu khó khăn chia lìa Ngọ Dương, một bàn tay quất vào trên mặt của mình, như là dạy dỗ vãn bối."),t("br"),t("br"),n._v("“Ngươi...” Đồng tử co rút lại, Lâm Tạ nghiến răng lần nữa vọt tới."),t("br"),t("br"),n._v("Ba ba ba!"),t("br"),t("br"),n._v("Liên tục ba bàn tay, cả người tại chỗ xoay tầm vài vòng, thân thể mềm nhũn, ngồi ngay đó, khóe miệng máu tươi tràn ra, cả người đều bối rối."),t("br"),t("br"),n._v("“Thực lực của ngươi...”"),t("br"),t("br"),n._v("Thực lực của đối phương, cũng chính là trung phẩm Thần Linh, còn không có đột phá, nhưng vì sao... Sức chiến đấu mạnh như vậy?"),t("br"),t("br"),n._v("Bản thân đường đường nửa bước thượng phẩm Thần Linh, bị tuỳ tiện tát bạt tai, liền trở tay lực lượng đều không có?"),t("br"),t("br"),n._v("“Lâm Tạ, ta trước đó một mực lấy ngươi làm đối thủ, hiện tại xem ra, ngươi không xứng!” Ở trên cao nhìn xuống, Ngọ Dương nhàn nhạt nhìn qua: “Còn có, về sau còn dám nói thành chủ nửa câu nói xấu, cẩn thận ta lấy tính mạng ngươi!”"),t("br"),t("br"),n._v("Nói xong, xoay người rời khỏi."),t("br"),t("br"),n._v("“Cái này...” Lâm Tạ sắp điên rồi."),t("br"),t("br"),n._v("Vừa mới đột phá, vốn cho rằng tung hoành Dạ Huy thành, lại không người có thể ngăn cản, nằm mộng cũng nghĩ không ra, bị cái này cùng hắn chiến đấu qua không biết bao nhiêu lần gia hỏa, mấy bàn tay rút trở về..."),t("br"),t("br"),n._v("Làm sao lại đột nhiên trở nên mạnh như vậy?"),t("br"),t("br"),n._v("Hôm qua bọn họ vẫn còn so sánh thử qua võ công ah!"),t("br"),t("br"),n._v("“Đạo bất đồng bất tương vi mưu, chúng ta cũng cáo từ, Lâm gia chủ, nhớ, ta nếu nghe được ngươi nhục mạ thành chủ, nhất định giết ngươi Lâm thị cả nhà!” Điền Phi gia chủ cũng đứng dậy."),t("br"),t("br"),n._v("“Không vội đi...” Gặp vị này chỉ có hạ phẩm Thiên Thần đỉnh phong Điền gia chủ cũng lớn lối như thế, Lâm Tạ suýt chút nữa nổ tung, đột nhiên đứng dậy, tiến về phía trước một bước, nắm đấm đập xuống mà xuống."),t("br"),t("br"),n._v("Đánh không lại Ngọ Dương, đối phó cái này trước đó hoàn toàn không phải đối thủ gia hỏa, vẫn là rất nhẹ nhàng."),t("br"),t("br"),n._v("Ba ba ba!"),t("br"),t("br"),n._v("Đồng dạng, nắm đấm xuống dốc tại trên người đối phương, lần nữa bị rút mấy cái bạt tai, té ngã trên đất."),t("br"),t("br"),n._v("“...” Lâm Tạ triệt để bối rối."),t("br"),t("br"),n._v("Hắn nhưng là vừa đột phá ah..."),t("br"),t("br"),n._v("Làm sao liền một cái hạ phẩm Thiên Thần đều có thể đánh người?"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Ngay tại buồn bực, chỉ thấy Triệu gia gia chủ Triệu mạnh, đi vào trước mặt: “Dám nói ta sư nói xấu, tự tìm cái chết!”"),t("br"),t("br"),n._v("Đạp một cước."),t("br"),t("br"),n._v("Vừa định né tránh, Lâm Tạ lúc này mới phát hiện, tất cả đường lui đều bị phong kín."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Khuôn mặt đỏ lên, bị một chân đạp trúng đầu, bay lùi ra xa hơn mười thước."),t("br"),t("br"),n._v("“Ta cũng không phải đối thủ...”"),t("br"),t("br"),n._v("Không nghĩ tới, liền cái này tu vi yếu nhất, bình thường nhìn cũng không nhìn liếc mắt Triệu mạnh, đều có thể một chân đem hắn đạp bay, bị đá hắn không hề có lực hoàn thủ, Lâm Tạ triệt để điên rồi."),t("br"),t("br"),n._v("Hắn không phải tiêu phí vô số đại giới tiến bộ ư?"),t("br"),t("br"),n._v("Làm sao cảm giác... Những người khác tiến bộ càng nhanh?"),t("br"),t("br"),n._v("Ai có thể nói cho ta biết, đến cùng xảy ra chuyện gì..."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2157"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2159"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);