(window.webpackJsonp=window.webpackJsonp||[]).push([[1306],{1660:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2146-su-nao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2146-su-nao"}},[n._v("#")]),n._v(" Chương 2146: Sư náo")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Cái này tiết khóa, ta nói thoáng cái, làm sao đột phá Thiên Thần. Đối với tu hành giả tới nói, nếu như nói Thần Linh là bước lên tiến lên con đường đạo thứ nhất ngưỡng cửa lời nói, Thiên Thần chính là cái thứ hai khó mà đột phá cảnh giới, một khi thành công, một bước lên trời, tại một ít tiểu thành thị, đều có thể làm lên thành chủ! Thậm chí hoàng thành, cũng có thể đạt được vệ đội đội trưởng chức vụ!”"),t("br"),t("br"),n._v("“Mọi người đều biết, thăng cấp cấp bậc này, cần hai cái phương pháp, thứ nhất, tinh thuần huyết mạch! Loại người này cho dù không sao tu luyện, đồng dạng có thể đột phá. Hình như Vân Ly thiên những Long tộc kia cường giả, hạch tâm huyết mạch, trưởng thành liền có thể đi đến Thiên Thần cảnh, để rất nhiều nhân loại tu luyện giả, theo không kịp! Còn có phong hào Thần Vương đời thứ nhất hậu bối, trở thành Thiên Thần, ván đã đóng thuyền. Đời thứ hai hậu bối, chín mươi phần trăm, đời thứ ba tám mươi phần trăm... Cứ thế mà suy ra, kèm theo sinh sôi càng nhiều, hậu bối huyết mạch càng mỏng manh, trở thành Thiên Thần tỷ lệ cũng sẽ giống như người bình thường từ từ nhỏ dần.”"),t("br"),t("br"),n._v("“Huyết mạch, vừa ra đời liền đã chú định, vô lực thay đổi, đối với người bình thường tới nói, chỉ có thể sử dụng biện pháp thứ hai thăng cấp! Cũng chính là tu luyện. Thông qua tu luyện trở thành Thiên Thần, độ khó lớn, không cần nhiều lời, chư vị chắc hẳn cũng đều rõ ràng, không chỉ có muốn có thích hợp bản thân công pháp, càng phải có kì ngộ cùng kiên cường quyết tâm, lòng tin! Thượng phẩm Thần Linh, có thể thành công đột phá, tỷ lệ không cao hơn một phần trăm!”"),t("br"),t("br"),n._v("“Bởi vậy, mỗi một cái Thiên Thần, đều không có dễ dàng như vậy... Ta hiện tại liền cho mọi người nói thoáng cái, một ít phổ biến đột phá phương pháp cùng thủ đoạn...”"),t("br"),t("br"),n._v("Phạm Triết lão sư bắt đầu giảng giải."),t("br"),t("br"),n._v("Nghe một hồi, Trương Huyền nhịn không được gật đầu."),t("br"),t("br"),n._v("Người trước mắt này chương trình học, mặc dù có chút học cứu, buồn tẻ không thú vị, để cho người ta không có quá nhiều hứng thú, nhưng truyền thụ kiến thức điểm, vô cùng tinh chuẩn, không có bất kỳ cái gì chỗ sơ suất, đối tu luyện giả, đặc biệt là lân cận đột phá người, có rất lớn dẫn dắt cùng tác dụng."),t("br"),t("br"),n._v("Học tập một hồi, cảm giác được lợi rất nhiều, nhìn trời Thần cảnh giới, biết càng thêm triệt để cùng tỉ mỉ."),t("br"),t("br"),n._v("Biết tiếp tục nghe tiếp, lại không ý nghĩa, đang định rời khỏi phòng học, tiếp tục tìm kiếm thư tịch, cửa phòng đóng chặt đột nhiên “Kẹt kẹt!” Một tiếng mở ra, hai bóng người đi vào."),t("br"),t("br"),n._v("Đi đầu một người trung niên, dáng người cao to, người mặc màu bạc khôi giáp, nhất cử nhất động ở giữa, tỏa ra thiết huyết cùng dữ tợn mùi vị."),t("br"),t("br"),n._v("Vừa nhìn liền biết, trải qua sa trường, giết qua người!"),t("br"),t("br"),n._v("Phía sau hắn một cái thiếu niên gầy yếu, vẻ mặt trắng bệch, khí tức nhỏ yếu, chịu nội thương rất nặng, tuy là tính mạng không ngại, nhưng nhìn lúc này bộ dáng, về sau muốn đột phá, cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("“Là quân đoàn ngân giáp tướng lĩnh!”"),t("br"),t("br"),n._v("“Tu vi thấp nhất đều muốn đi đến trung phẩm Thần Linh đi!”"),t("br"),t("br"),n._v("“Có thể tại quân đội đạt được ngân giáp, riêng là trung phẩm Thần Linh còn chưa đủ, còn muốn có cực mạnh thực lực cùng công lao...”"),t("br"),t("br"),n._v("“Loại người này tới đây làm gì?”"),t("br"),t("br"),n._v("“Là con của hắn, bởi vì hắn cùng vợ đều nhận được tổn thương, con trai thiên phú quá kém, chưa đi đến vào chính quy học viện, ngay sau đó đặt ở Phạm Triết lão sư môn hạ tu hành...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bốn phía nghị luận lên."),t("br"),t("br"),n._v("“Ngân giáp tướng lĩnh?”"),t("br"),t("br"),n._v("Trương Huyền nhìn lại, quả nhiên cảm thấy vị này trong cơ thể ẩn chứa lực lượng kinh người, so về Mính Lai Hương bọn người mạnh hơn rất nhiều."),t("br"),t("br"),n._v("“Ngươi chính là Phạm Triết?”"),t("br"),t("br"),n._v("Đánh gãy giảng bài, ngân giáp tướng lĩnh đi vào trong đại sảnh ở giữa, sắc mặt tái xanh, từng đoàn từng đoàn lửa giận trong mắt không ngừng thiêu đốt."),t("br"),t("br"),n._v("đọc ngantruyen.com/"),t("br"),n._v("“Lão phu chính là...” Phạm Triết lão sư gật đầu."),t("br"),t("br"),n._v("“Dung sư hại người, ngày hôm nay ta sẽ dạy thoáng cái ngươi cái này dạy hư học sinh gia hỏa!”"),t("br"),t("br"),n._v("Hét lớn một tiếng, ngân giáp tướng lĩnh thân thể nhảy lên, nắm đấm gào thét lên hướng lão giả trước mắt vọt tới."),t("br"),t("br"),n._v("Ba ba ba!"),t("br"),t("br"),n._v("Còn tại trên không, khí tức liền bị đè nát, cả phòng khí lưu tán loạn."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Phạm Triết lão sư sững sờ, vội vàng lui về phía sau."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Bàn giáo viên tại quyền phong bên dưới, chia năm xẻ bảy, hóa thành bột mịn, Phạm Triết lão sư cánh tay nâng lên, vừa định phản kích, nhớ tới Danh Sư đường quy định, cắn răng, đưa tay lại để xuống."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Ngực bị đánh trúng, sống lưng đâm vào trên vách tường, đập ra một cái lỗ to, Phạm Triết lão sư hơi đỏ mặt, một ngụm máu tươi phun tới."),t("br"),t("br"),n._v("Bàn về thực lực, hắn cùng đối phương không kém bao nhiêu, thậm chí thân là chinh chiến tướng lĩnh, trước mắt vị này sát khí càng nặng một ít, toàn lực ngăn cản cũng chưa chắc có thể chống lại, huống chi cứng rắn bị đánh một cái."),t("br"),t("br"),n._v("“Dừng tay!”"),t("br"),t("br"),n._v("“Có lời gì hảo hảo nói...”"),t("br"),t("br"),n._v("“Ngươi muốn làm gì, như vậy làm trái quy định...”"),t("br"),t("br"),n._v("Những người khác cũng phản ứng lại, lập tức có mấy cái phụ huynh lao đến, đem vị này ngân giáp tướng lĩnh ngăn lại."),t("br"),t("br"),n._v("Tướng lĩnh gầm thét: “Làm gì? Để cho ta đánh chết cái này ngu ngốc lão sư...”"),t("br"),t("br"),n._v("“Vị này phụ huynh, đến cùng chuyện gì, coi như ngươi muốn đánh chết ta, cũng muốn để cho ta biết, xảy ra chuyện gì đi!”"),t("br"),t("br"),n._v("Giẫy giụa đứng dậy, điều tức hai cái, khôi phục một chút khí lực, Phạm Triết lão sư nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Hắn có thể khẳng định, trước mắt vị này, trước kia chưa bao giờ thấy qua."),t("br"),t("br"),n._v("“Con của ta Trác Nham thế nhưng là học sinh của ngươi?”"),t("br"),t("br"),n._v("Gặp hắn không biết, càng thêm tức giận, ngân giáp tướng lĩnh một chỉ cổng bị nội thương thiếu niên."),t("br"),t("br"),n._v("Phạm Triết lão sư gật đầu."),t("br"),t("br"),n._v("“Ta tin tưởng ngươi, mới đưa con trai thả ngươi nơi này học tập, mà ngươi đây? Lên lớp không cố gắng nói, sau khi tan học, để hài tử đi ngươi trường luyện thi! Hiểu ngươi muốn kiếm tiền, không có gì có thể nói. Có thể ngươi hảo hảo giáo ah, ngươi cái dung sư, truyền thụ sai lầm phương pháp tu luyện, để hắn tẩu hỏa nhập ma! Còn có mặt mũi nói cho cùng chuyện gì?”"),t("br"),t("br"),n._v("Ngân giáp tướng lĩnh sắp nổ tung."),t("br"),t("br"),n._v("“Tẩu hỏa nhập ma?”"),t("br"),n._v("Phạm Triết lão sư sững sờ, hướng trước cửa thiếu niên nhìn lại, quả nhiên thấy nội thương của hắn có chút không đúng, cùng đi qua nhập ma giống nhau: “Ta truyền thụ cho hắn, đều là công pháp cơ bản, không có sai lầm chút nào, tại sao có thể như vậy?”"),t("br"),t("br"),n._v("Nhịn đau đau, đi vào trước mặt, ngón tay dựng tới."),t("br"),t("br"),n._v("Trương Huyền cũng không ra tay, cũng không nói chuyện, đồng dạng nhìn lại."),t("br"),t("br"),n._v("Danh sư đại lục, hắn tiền thân, đã từng truyền thụ kiến thức sai lầm, để cho người ta tẩu hỏa nhập ma, nhưng ở đó, lão sư địa vị cực cao, phụ huynh coi như phẫn nộ, cũng không dám vọt tới trong trường học động thủ."),t("br"),t("br"),n._v("Mà ở đây, trực tiếp đi vào Danh Sư đường, đem lão sư đánh..."),t("br"),t("br"),n._v("Địa vị, quả nhiên không tưởng tượng bên trong cao như vậy."),t("br"),t("br"),n._v("Cũng có thể là bởi vì Khổng sư mới đi đến Thần giới mấy chục năm nguyên nhân, cùng danh sư đại lục đồng dạng, truyền thừa mấy vạn năm, liền sẽ nuôi thành một chủng tập quán, nhận tôn trọng."),t("br"),t("br"),n._v("Bất quá, nếu như đối phương nói là sự thật, trước mắt vị này Phạm Triết, lên lớp không hảo hảo giáo, chờ lấy học bổ túc khóa truyền thụ kiến thức, tốt dùng cái này đạt được danh khí cùng tiền bạc, bản thân phẩm chất liền có chuyện, không xứng làm lão sư!"),t("br"),t("br"),n._v("Sư, lớn hơn công vô tư, lấy truyền đạo học nghề làm nhiệm vụ của mình, mà không phải vì tiền tiền, dao động bản tâm."),t("br"),t("br"),n._v("Ngón tay đáp lên đối phương mạch đập bên trên, nhìn một hồi, Phạm Triết lão sư lông mày nhảy dựng: “Quả nhiên là tẩu hỏa nhập ma...”"),t("br"),t("br"),n._v("Người học sinh này tình huống, vừa nhìn liền có thể biết, quả thực là tu luyện công pháp xảy ra sai sót."),t("br"),t("br"),n._v("“Thừa nhận liền tốt... Ngày hôm nay ta giết ngươi, vì dân trừ hại!”"),t("br"),t("br"),n._v("Gặp hắn nói như vậy, tướng quân giáp bạc cái kia còn an chịu được, lại là một quyền đập mà tới."),t("br"),t("br"),n._v("“Bớt giận, bớt giận!”"),t("br"),t("br"),n._v("Trước đó khuyên can người đem hắn kéo."),t("br"),t("br"),n._v("Đây là U Hồn hoàng thành, chưởng khống giả chính là Càn Khôn Ma Quân, không cho phép tuỳ ý giết người, chỉ cần làm trái, đừng nói hắn chỉ là cái trung phẩm Thiên Thần tướng quân giáp bạc, liền xem như Thần Vương, đều có thể trong khoảnh khắc biến thành tro bụi."),t("br"),t("br"),n._v("“Trác Nham phụ huynh ngươi trước đừng tức giận, ta truyền thụ cho kiến thức, khẳng định không có sai lầm, về phần hắn tại sao lại tẩu hỏa nhập ma, có thể là trên việc tu luyện xuất hiện sai lầm, hảo hảo hỏi một chút, đợi tìm tới nguyên nhân, giải quyết xong trên người tai hoạ ngầm lại nói!”"),t("br"),t("br"),n._v("Khoát tay áo, Phạm Triết lão sư nhìn về phía thiếu niên ở trước mắt: “Đem ngươi tẩu hỏa nhập ma tình cảnh, nói rõ chi tiết một lần...”"),t("br"),t("br"),n._v("“Hôm qua lên lão sư trường luyện thi, ta về đến trong nhà, liền theo lão sư nói tu luyện, tiếp đó... Cứ như vậy!” Thân thể thiếu niên run rẩy, nói."),t("br"),t("br"),n._v("“Dựa theo ta nói tu luyện? Đem ngươi quá trình tu luyện cẩn thận nói rõ!” Phạm Triết lão sư nói."),t("br"),t("br"),n._v("“Chính là như ngươi nói vậy, thần lực, quán thông trong cơ thể thủ thiếu dương tam tiêu kinh, dọc theo Trùng Dương huyệt chạy... Cuối cùng dung hợp đan điền!” Thiếu niên nói một phen pháp quyết tu luyện."),t("br"),t("br"),n._v("Nghe phút chốc, Trương Huyền lần nữa cau mày."),t("br"),t("br"),n._v("Đối phương nói là hạ phẩm Thần Linh xung kích trung phẩm Thần Linh pháp quyết tu luyện, sớm đã tạo thành Thiên đạo pháp quyết, biết vô cùng tỉ mỉ."),t("br"),t("br"),n._v("Dựa theo phương pháp, cứ việc khoảng cách Thiên đạo pháp quyết chênh lệch không biết bao lớn, đối với phổ thông tu luyện giả tới nói, không có quá nhiều sai lầm, thuộc về vô cùng hoàn mỹ tu hành phương thức."),t("br"),t("br"),n._v("Dưới tình huống bình thường, chỉ cần thần lực trong cơ thể tích lũy đủ, đột phá không khó lắm... Làm sao lại tẩu hỏa nhập ma, hơn nữa nhìn bộ dáng, thương thế nặng như vậy?"),t("br"),t("br"),n._v("“Không có sai lầm ah...”"),t("br"),t("br"),n._v("Vốn cho rằng đối phương có phải hay không nhớ lầm phương pháp tu luyện, tu luyện ra sai lầm, không nghĩ tới hoàn toàn chính xác, Phạm Triết lão sư cũng cau mày, không biết làm sao."),t("br"),t("br"),n._v("Giảng dạy học sinh tẩu hỏa nhập ma, đối với lão sư tới nói, là rất lớn tội danh, cho dù hắn là danh sư, cũng đảm đương không nổi."),t("br"),t("br"),n._v("“Không có sai lầm, con của ta vì sao bộ dáng như thế? Rõ ràng chính là dung sư...”"),t("br"),t("br"),n._v("Ngân giáp tướng lĩnh càng thêm tức giận."),t("br"),t("br"),n._v("“Đừng nóng vội, nhìn một chút có thể hay không giúp hắn trị liệu...”"),t("br"),t("br"),n._v("Thần lực tại thiếu niên trong cơ thể du tẩu một vòng, Phạm Triết lão sư rơi vào suy nghĩ."),t("br"),t("br"),n._v("Đối phương quả thực là tẩu hỏa nhập ma, hơn nữa nhìn trong cơ thể thương thế bộ dáng, cùng hắn nói phương pháp tu hành, không có bất kỳ cái gì khác biệt... Nói cách khác, hắn giáo không sai, đối phương luyện cũng không sai, nhưng là như vậy, tẩu hỏa nhập ma!"),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Da đầu run lên, Phạm Triết lão sư không biết như thế nào cho phải."),t("br"),t("br"),n._v("Trở thành danh sư không dưới mười lăm năm, giảng dạy học sinh, không có một vạn, cũng ít nhất bảy, tám ngàn, còn là lần đầu tiên gặp được loại tình huống này."),t("br"),t("br"),n._v("“Nhanh đi đem Dư Phong y sư mời đến!”"),t("br"),t("br"),n._v("Suy tư một hồi, tìm không thấy đáp án, Phạm Triết lão sư phân phó một tiếng."),t("br"),t("br"),n._v("Vừa mới tiến tới một vị lão sư, vội vã rời khỏi."),t("br"),t("br"),n._v("“Dư Phong y sư tại toàn bộ hoàng thành đều khá là nổi danh, xem như rất lợi hại y sư, hắn tới hẳn là có thể giải quyết!”"),t("br"),t("br"),n._v("“Chỉ cần có thể tìm tới nguyên do, giải quyết liền dễ dàng...”"),t("br"),t("br"),n._v("“Trước đem thương thế chữa khỏi, còn đến cùng vì sao tẩu hỏa nhập ma, thương thế được rồi, có thời gian lý luận!”"),t("br"),t("br"),n._v("“Đúng vậy a, có điều, lão sư chức trách là giảng bài trồng người, vị này Phạm Triết lão sư, lên lớp không cố gắng nói, tan học lái trường luyện thi, ta cũng là sớm có nghe nói!”"),t("br"),t("br"),n._v("“Nếu không phải vì hài tử, khẳng định cũng đánh người, còn không phải sợ đắc tội, cho chúng ta hài tử gây khó dễ, không nói mặt khác, chỉ cần an bài cái dựa vào sau chỗ ngồi? Cả một đời liền xong rồi!”"),t("br"),t("br"),n._v("“Nghe nói hắn trên lớp học, chỗ ngồi đều là mua, khá cao vị trí, phụ huynh đều là đưa tiền...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Những nhà khác dài nghị luận ầm ĩ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2160"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2162"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);