(window.webpackJsonp=window.webpackJsonp||[]).push([[1308],{1663:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2148-đot-pha-thien-than-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2148-đot-pha-thien-than-thuong"}},[n._v("#")]),n._v(" Chương 2148: Đột phá Thiên Thần! (Thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Bình thường hắn đi theo quân đội, bên ngoài chinh chiến, mấy ngày nay vừa vặn nghỉ phép, mới có thể đúng lúc xuất thủ cứu giúp, không đến mức ủ thành hậu quả lớn hơn, nếu không, biết tin tức, khẳng định đã chậm."),t("br"),t("br"),n._v("Nếu thực như thế, cái kia có bao nhiêu tự trách mình?"),t("br"),t("br"),n._v("Bởi vậy, mới không có khống chế lại, vọt thẳng tới."),t("br"),t("br"),n._v("Trương Huyền nói tiếp: “Hắn từ nhỏ đã bị quán thâu, muốn cùng ngươi đồng dạng, trở thành Thiên Thần cảnh cường giả, thế nhưng bản thân thiên phú không cao, một mực thừa nhận đủ loại áp lực. Lại thêm các loại trường luyện thi, chưa từng hạ xuống, vì tu luyện, chỉ sợ một ngày tự do cùng thuộc về mình thời gian đều không có đi!”"),t("br"),t("br"),n._v("Trác Nham im lặng không lên tiếng."),t("br"),t("br"),n._v("Tướng quân giáp bạc nói: “Lúc còn trẻ, không cố gắng, đến ta cái tuổi này, lại cố gắng thì có ích lợi gì? Ta cũng là vì hắn ý định! Huống chi, không đột phá Thiên Thần, phổ thông Thần Linh tuổi thọ, hơn trăm cũng khó khăn, mà tuổi thọ của ta là một ngàn tuổi, chẳng lẽ, để cho ta người đầu bạc tiễn người đầu xanh?”"),t("br"),t("br"),n._v("“Ngươi tất cả đều là tốt, nhưng... Tu luyện cũng không phải là cố gắng liền tốt, còn muốn chú ý lao động nhàn hạ kết hợp! Dựa vào là ngộ, dựa vào là tâm cảnh! Tâm không tại, nói cho dù tốt lại như thế nào?” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Phụ huynh mong con hơn người tâm tình, hắn có thể lý giải, có thể hi vọng hắn thành rồng, liền có thể thành long ư?"),t("br"),t("br"),n._v("Thật muốn người người như rồng, đâu còn có chia cao thấp?"),t("br"),t("br"),n._v("Trương Huyền tiếp tục nói: “Hắn nghe theo lời của ngươi, một mực cố gắng, chưa kịp củng cố tốt tu vi, liền muốn đột phá, áp lực lớn, suy nhược tinh thần, tâm cảnh tự nhiên không cách nào làm được tinh thuần như một! Còn Phạm Triết lão sư, không nhìn lầm, học thêm thời điểm, có phải hay không nói với hắn cái gì?”"),t("br"),t("br"),n._v("Phạm Triết lão sư sững sờ."),t("br"),t("br"),n._v("“Hắn một mực bên trên ngươi trường luyện thi, lại lâu như vậy đều không có đột phá trung phẩm Thần Linh, hẳn là rơi xuống tối hậu thư, để hắn đêm nay nhất định phải đột phá đi!”"),t("br"),t("br"),n._v("Trương Huyền nói: “Vốn tâm cảnh liền yếu ớt, đây coi như là đè chết lạc đà cuối cùng một cái rơm rạ, lại kiên trì không được, cuối cùng dẫn đến sinh ra tâm ma, từ đó xuất hiện loại sự tình này!”"),t("br"),t("br"),n._v("“Vị này Trương huynh đệ nói có đúng không?” Dừng lại một chút, tướng quân giáp bạc nhìn lại."),t("br"),t("br"),n._v("Hi vọng con thành rồng, nhưng nhi tử nếu là thật có chuyện bất trắc, mong đợi tất cả, còn có cái gì dùng?"),t("br"),t("br"),n._v("“Vâng...”"),t("br"),t("br"),n._v("Trác Nham gật đầu, ánh mắt bứt rứt nhìn về phía cha, sợ nhận quát tháo."),t("br"),t("br"),n._v("Làm tẩu hỏa nhập ma tu luyện giả, tại sao lại như vậy, nguyên nhân hắn biết rất rõ ràng, cùng trước mắt vị này nói giống nhau như đúc, không kém chút nào."),t("br"),t("br"),n._v("“Ta về sau không bức ngươi, có thể tu luyện thành ra sao, liền ra sao!”"),t("br"),t("br"),n._v("Gặp hắn thừa nhận, tướng quân giáp bạc than thở."),t("br"),t("br"),n._v("Không có cái kia mệnh lời nói, cưỡng cầu không được."),t("br"),t("br"),n._v("Vốn còn muốn oán hận Phạm Triết lão sư, nghe được đối phương cũng giống như mình, chỉ bức bách tu luyện, không có lỗi gì lầm, muốn nói lời, lại nói không đi ra."),t("br"),t("br"),n._v("“Trương huynh đệ, ngươi đã cứu ta con trai, cấp cho hắn tân sinh, ta Trác Phong từ hôm nay trở đi, nợ ngươi một cái hứa hẹn, chỉ cần một câu, dù là muốn mạng của ta, đều tuyệt không một chút nhíu mày!”"),t("br"),t("br"),n._v("Tướng quân giáp bạc Trác Phong nói."),t("br"),t("br"),n._v("“Tướng quân khách khí... Tiện tay mà làm, cũng không hy vọng xa vời báo đáp, hơn nữa, ta ra tay, là không muốn để cho danh sư cái nghề nghiệp này, bị trường luyện thi, cùng với các loại lấy lợi nhuận làm mục đích giáo dục cơ quan, hỏng tên tuổi! Bị tất cả phụ huynh hiểu lầm.” Trương Huyền cười nhạt một tiếng."),t("br"),t("br"),n._v("Khổng sư sáng lập chức nghiệp, là hắn xuyên qua đến nay, một mực kiên trì kiêu ngạo, bị người nhục nhã, mặt mũi làm sao có ánh sáng?"),t("br"),t("br"),n._v("“Chẳng lẽ... Trương Huyền tiểu hữu, cũng là một vị danh sư?”"),t("br"),t("br"),n._v("Phạm Triết lão sư hỏi."),t("br"),t("br"),n._v("Những người khác cũng tràn ngập tò mò."),t("br"),t("br"),n._v("Người trước mắt này chưa từng nghe qua, lại có thể nhẹ nhõm giải quyết tẩu hỏa nhập ma, y thuật mạnh mẽ, so Dư Phong đều cường đại hơn không ít, chẳng lẽ thật sự là một vị lợi hại danh sư đi!"),t("br"),t("br"),n._v("Phổ thông danh sư, không tính là gì, nhưng Thiên Túng Đại Đế thân truyền đệ tử, mỗi một cái đều nắm giữ nhiều loại chức nghiệp, bất kỳ cái gì sự tình, đều vô cùng tinh thông, trước mắt vị này, tuổi tác tuy là thoạt nhìn không giống, thế nhưng có thể hay không... Tới liên quan đến?"),t("br"),t("br"),n._v("“Xem như thế đi!” Trương Huyền cũng không phủ nhận."),t("br"),t("br"),n._v("Tại danh sư đại lục, là đỉnh phong nhất danh sư, cùng Khổng sư đồng dạng, được khen là vạn thế chi sư, có thể đây là Thần giới, danh sư làm sao thi đậu, lại cần gì yêu cầu, hoàn toàn không biết."),t("br"),t("br"),n._v("“Quá tốt rồi...” Phạm Triết lão sư thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Hắn xuất hiện chỗ sơ suất, trước mắt vị này hỗ trợ giải quyết, cùng chức nghiệp lời nói, chí ít sẽ không bởi vậy ảnh hưởng Danh Sư đường."),t("br"),t("br"),n._v("“Phạm sư phụ, ta có thể hay không cho ngươi cái đề nghị?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Trương sư mời nói!” Phạm Triết lão sư vội vàng ôm quyền."),t("br"),t("br"),n._v("Tuy là thoạt nhìn tu vi của đối phương kém xa hắn, nhưng bất luận kiến thức vẫn là thủ đoạn, đều cao minh không ít, càng hỗ trợ hóa giải xấu hổ, nói cái gì, tự nhiên muốn nghiêm túc nghe theo."),t("br"),t("br"),n._v("Trương Huyền nói: “Tu luyện giả, đều có trì trệ, đối với hài tử, loại tình huống này càng lớn, mở trường luyện thi, để hắn càng nhiều thời gian đi học tập, là đúng, nhưng... Cứng nhắc dạy học, sẽ chỉ làm hắn sinh ra chán ghét chi tâm! Cuối cùng tâm cảnh bị tổn thương, cùng Trác Nham đồng dạng, không được hiệu quả gì!”"),t("br"),t("br"),n._v("Phạm Triết lão sư lặng im: “Nhưng có phương pháp bổ cứu?”"),t("br"),t("br"),n._v("Trường luyện thi đối có chút hài tử tới nói, hiệu quả rõ rệt, nhưng đối với phần lớn người mà nói, cũng không có quá lớn trợ giúp."),t("br"),t("br"),n._v("Không khác, tâm mệt mỏi."),t("br"),t("br"),n._v("Trương Huyền nói: “Thân là lão sư, hẳn là tùy theo tài năng tới đâu mà dạy, nhằm vào mỗi cái học sinh khác biệt cùng đặc điểm, có mục đích tính dạy học, bồi dưỡng hắn ưu tú học tập quen thuộc... Quen thuộc một khi nuôi thành, tâm cảnh tự nhiên cũng đã thành!”"),t("br"),t("br"),n._v("“Tùy theo tài năng tới đâu mà dạy... Bồi dưỡng học tập quen thuộc?” Phạm Triết lão sư gật đầu: “Nào có dễ dàng như vậy!”"),t("br"),t("br"),n._v("Nói dễ, làm sao mà khó khăn."),t("br"),t("br"),n._v("“Rất đơn giản, mỗi cái học sinh tính cách khác biệt, tu vi khác biệt, bày ra kết quả cũng liền không giống, liền giống như Trác Nham, hắn từ nhỏ đến lớn, áp lực quá lớn, vẫn cảm thấy bản thân không cách nào vượt qua cha, từ đó mất đi lòng tin, chỉ cần cho thêm cùng cổ vũ, một cách tự nhiên liền có thể thành công!”"),t("br"),t("br"),n._v("Trương Huyền khẽ mỉm cười, nhìn về phía thiếu niên ở trước mắt: “Lời nói mới rồi, ngươi cũng nghe đến, vừa mới ta đã kiểm tra, thiên phú của ngươi, cũng không phải là không tốt, trái lại vượt xa rất nhiều người! Chỉ là, không có lợi dụng được mà thôi!”"),t("br"),n._v(" “Ta thiên phú rất tốt?” Trác Nham nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Từ nhỏ đến lớn, hắn đều là trong miệng người khác đỡ không nổi tường bùn nhão, phụ thân anh hùng nhi gấu chó điển hình ví dụ, liền phổ thông học viện, đều đem hắn cự tuyệt ở ngoài cửa..."),t("br"),t("br"),n._v("Ở đâu tốt?"),t("br"),t("br"),n._v("“Không cần hoài nghi, ngươi sở dĩ không cách nào đột phá, là bởi vì, trong lòng sợ hãi, cho mình áp lực quá lớn... Nghe lời của ta, mở rộng nội tâm, dựa theo Phạm sư phụ dạy ngươi phương pháp, xung kích một lần...”"),t("br"),t("br"),n._v("Trương Huyền thanh âm bên trong mang theo sư ngôn thiên bẩm giọng nói, trên không tựa như có quy tắc đang chảy, để hắn ngoan ngoãn tuân thủ, không thể làm trái."),t("br"),t("br"),n._v("Vốn Trác Nham trong mắt tràn đầy mê man cùng không hiểu, sư ngôn thiên bẩm trùng kích vào, dần dần biến thành kiên định, cắn răng một cái ngồi trên mặt đất, hít sâu một hơi, trong cơ thể lực lượng sôi trào mà lên."),t("br"),t("br"),n._v("Cô cô cô! Rầm rầm rầm!"),t("br"),t("br"),n._v("Không đến mười phút đồng hồ, khí tức nổ vang, giam giữ lại không biết bao lâu hạ phẩm Thần Linh cảnh giới, ầm ầm phá vỡ, thuận lợi đạt đến trung phẩm Thần Linh cảnh giới."),t("br"),t("br"),n._v("Trên thực tế, những người khác nhìn không sai, Trác Nham thiên phú quả thực rất yếu, hạ phẩm Thần Linh đỉnh phong, đã là tiêu phí vô số đại giới đổi lấy kết quả."),t("br"),t("br"),n._v("Không có gì bất ngờ xảy ra, trung phẩm Thần Linh, cả một đời đều không thể đột phá, nhưng vừa mới Trương Huyền hóa giải hắn tẩu hỏa nhập ma, Thiên đạo chân khí chạy toàn thân, ràng buộc đã triệt để loại bỏ."),t("br"),t("br"),n._v("Nói cách khác, hiện tại thiên phú, cho dù không đuổi kịp một ít thiên tài, cũng không hề yếu."),t("br"),t("br"),n._v("Chỉ cần có tự tin, đột phá, chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("“Trương sư đại ân, tại hạ vô cùng cảm kích...” Tướng quân giáp bạc Trác Phong tràn đầy kích động."),t("br"),t("br"),n._v("Con trai tình huống, hắn biết rất rõ ràng, không nghĩ tới trước mắt vị này chẳng những giải quyết tẩu hỏa nhập ma, thậm chí còn mấy câu liền trợ giúp đột phá..."),t("br"),t("br"),n._v("Ân tình, quá lớn."),t("br"),t("br"),n._v("“Tiện tay mà thôi mà thôi!” Trương Huyền cười nhạt một tiếng, không nói thêm lời."),t("br"),t("br"),n._v("Phân chia ra phân thân về sau, quả nhiên vẫn là khiêm tốn nhiều!"),t("br"),t("br"),n._v("Vừa nghĩ tới đó, liền tràn đầy vui mừng."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Một lát sau, mở mắt ra, cảm nhận được trong cơ thể gào thét lực lượng, Trác Nham hốc mắt ửng hồng, rầu rĩ thoáng cái, cũng không đứng dậy, trực tiếp quỳ mọp xuống đất: “Còn xin Trương lão sư, thu ta vì học sinh!”"),t("br"),t("br"),n._v("Tuy là một mực đi theo Phạm Triết lão sư, nhưng mấy năm qua, tu vi gia tăng cũng không rõ ràng, trước mắt vị này, mới thấy một hồi, liền có đột phá này, nói rõ thực lực."),t("br"),t("br"),n._v("Có thể bái làm sư, về sau tuyệt đối thành tựu vô tận."),t("br"),t("br"),n._v("“Đúng, bái Trương sư vi sư!” Trác Phong liền vội vàng gật đầu."),t("br"),t("br"),n._v("Trước kia vẫn cảm thấy đứa bé này không hiệu nghiệm, không nghĩ tới trong chuyện này, phản ứng đến nhanh, so với chính mình thông minh nhiều."),t("br"),t("br"),n._v("Gọi cũng theo trước “Trương huynh đệ”, biến thành “Trương sư”."),t("br"),t("br"),n._v("“Ta có thể thu ngươi làm giảng bài học sinh!” Trương Huyền nói."),t("br"),t("br"),n._v("Trước mắt vị này tư chất, cơ sở cũng không có đánh tốt, thân truyền khả năng không lớn, có điều, giảng bài ngược lại là có thể, cũng không ngại có lần này duyên phận."),t("br"),t("br"),n._v("“Học sinh gặp qua lão sư!” Trác Nham quỳ gối."),t("br"),t("br"),n._v("Thần giới cũng có giảng bài, thân truyền khác nhau."),t("br"),t("br"),n._v("Có thể thu vì giảng bài, đã coi như là cực lớn mặt mũi."),t("br"),t("br"),n._v("“Nếu gọi lão sư ta, hiện tại truyền thụ cho ngươi một bộ thích hợp ngươi pháp quyết, nghiêm túc tu luyện, đột phá Thiên Thần, cũng chưa biết chừng!” Cười nhạt một tiếng, Trương Huyền truyền âm qua."),t("br"),t("br"),n._v("Thiên Thần cảnh trở xuống công pháp, sớm đã hết sức quen thuộc, vừa mới thư viện dò xét đối phương, trong lòng cũng đã có ý tưởng cùng lối suy nghĩ."),t("br"),t("br"),n._v("Rất nhanh, một bộ mới tinh pháp quyết truyền thụ xong xuôi."),t("br"),t("br"),n._v("Trác Nham nghe xong, trong mắt tràn đầy vẻ khiếp sợ, lần nữa quỳ gối."),t("br"),t("br"),n._v("Tu vi của hắn mặc dù không cao, xuất thân gia đình lại không sai, gặp qua không biết bao nhiêu lợi hại công pháp, nhưng muốn nói kiện tinh diệu, tất cả đều kém xa tít tắp trước mắt vị này vừa mới truyền thụ cho."),t("br"),t("br"),n._v("Chỉ cần nghiêm túc tu luyện, có thể đoán được bản thân, không lâu liền có thể đột phá trung phẩm Thần Linh, xung kích càng cao!"),t("br"),t("br"),n._v("Trân quý như thế công pháp, tuyệt đối là vô giá!"),t("br"),t("br"),n._v("Trực tiếp tặng cho..."),t("br"),t("br"),n._v("Đại công vô tư, đây mới thật sự là lão sư!"),t("br"),t("br"),n._v("Một mực đến nay, hắn đều là trong mắt người khác người thất bại, không có người coi trọng qua, còn lần đầu tiên nhận coi trọng như vậy, cảm kích hầu như nói không ra lời."),t("br"),t("br"),n._v("Vù!"),t("br"),t("br"),n._v("Đem thiếu niên đỡ dậy, Trương Huyền cảm thấy trong đầu một hồi lay động, một bản thư tịch màu vàng xuất hiện."),t("br"),t("br"),n._v("Ánh mắt sáng lên."),t("br"),t("br"),n._v("Vừa cảm thấy không có kim sắc thư tịch, liền xuất hiện một bản, như vậy cũng coi như có tự vệ vốn liếng."),t("br"),t("br"),n._v("Quả nhiên là thật tâm thật ý biết ơn, mới có thể xuất hiện."),t("br"),t("br"),n._v("Không chỉ có như vậy, thấy được Thần giới Danh Sư đường, thấy được trường luyện thi, cùng với khác người phương thức giáo dục, để hắn đối giáo dục cũng có hiểu mới, đối “Sư” cùng với thầy trò tình, lĩnh ngộ càng thêm triệt để."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2162"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2164"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);