(window.webpackJsonp=window.webpackJsonp||[]).push([[1310],{1666:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2150-bat-tu-thien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2150-bat-tu-thien"}},[n._v("#")]),n._v(" Chương 2150: Bất Tử thiên")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Một canh giờ sau, Trương Huyền đi ra tàng thư khố."),t("br"),t("br"),n._v("Không hổ là hoàng thành Danh Sư đường, cứ việc thoạt nhìn không đáng chú ý, chức nghiệp cũng kém xa danh sư đại lục phong cảnh, ngược lại giống như một cái hai tay trong huấn luyện tâm, có thể bên trong tàng thư lại không phải giả."),t("br"),t("br"),n._v("Hạ phẩm Thần Linh đến thượng phẩm Thiên Thần phạm vi bên trong tu luyện công pháp, võ kỹ đều có, hơn nữa số lượng rất nhiều, toàn bộ nhìn xem đến, đã một cách tự nhiên sinh thành Thiên đạo công pháp."),t("br"),t("br"),n._v("Chỉ tiếc không có Thần Vương cường giả pháp quyết."),t("br"),t("br"),n._v("Suy nghĩ một chút cũng có thể rõ ràng, Thần Vương cường giả, mỗi một vị đều có địa vị cực cao, Thần giới được cho đỉnh tiêm, hơn nữa cùng huyết mạch có rất lớn quan hệ... Tu luyện công pháp, tự nhiên truyền thừa tại gia tộc hoặc là thế lực lớn, người ngoài muốn có được gần như không có khả năng!"),t("br"),t("br"),n._v("“Đột phá Thần Vương còn sớm, đoán chừng phải mấy ngày công phu, không đi nghĩ, trước triệt để củng cố tu vi, nghĩ biện pháp xung kích thượng phẩm Thiên Thần đỉnh phong rồi nói sau...”"),t("br"),t("br"),n._v("Biết tu luyện muốn từng bước một làm gì chắc đó, không thể sốt ruột, Trương Huyền không còn rầu rĩ."),t("br"),t("br"),n._v("Mặc dù bây giờ tu luyện rất chậm, nhưng hắn một mực tại cố gắng, chỉ cần có ý, nhất định có thể càng đi càng xa, không tự ti, không mê man!"),t("br"),t("br"),n._v("“Trước chơi chút trung phẩm Thần Nguyên đan... Tốt nhất... Thượng phẩm!”"),t("br"),t("br"),n._v("Mặt khác đều là giả, tăng cao tu vi mới trọng yếu nhất."),t("br"),t("br"),n._v("Đột phá Thiên Thần cảnh, trung phẩm Thần Nguyên đan hao hết, không chút củng cố, chỉ bằng vào hấp thu trên không linh khí tấn cấp lời nói, không biết cần bao lâu thời gian, đối với hắn mà nói, thực sự quá chậm."),t("br"),t("br"),n._v("Hơn nữa, trung phẩm đối với hắn mà nói, hiệu quả đã không có trước đó tốt như vậy, trở thành Thiên Thần về sau, dược lực dần dần suy yếu, không bao lâu, có lẽ cùng hạ phẩm đồng dạng, lại không cách nào sử dụng."),t("br"),t("br"),n._v("Tốt nhất là thượng phẩm, cũng không biết thứ này, ở đâu có thể trải qua đến."),t("br"),t("br"),n._v("“Cách đó không xa Minh Viên thương hội, liền có bán ra trung phẩm Thần Nguyên đan, còn thượng phẩm, tại hoàng thành là hàng cấm, muốn mua không dễ dàng như vậy...” Phạm Triết lão sư biết gì nói nấy."),t("br"),t("br"),n._v("U Hồn hoàng thành, tại cửu thiên bên trong, đều được cho rất thành phố khổng lồ một trong, giao dịch năng lực, tự nhiên không phải Dạ Minh thành loại địa phương nhỏ này có thể so sánh được."),t("br"),t("br"),n._v("Người sau không cách nào mua sắm trung phẩm Thần Nguyên đan, ở đây, rất dễ dàng mua được, số lượng rất nhiều."),t("br"),t("br"),n._v("Trung phẩm Thần Nguyên đan đối Thiên Thần cường giả hữu hiệu, hoàng thành Thiên Thần cường giả, nhiều vô số kể, loại đan dược này không cho phép bán ra lời nói, rất dễ dàng dẫn tới bắn ngược."),t("br"),t("br"),n._v("Thượng phẩm Thần Nguyên đan muốn có được, liền không dễ dàng như vậy."),t("br"),t("br"),n._v("Bất quá, hiện tại trung phẩm cực hạn còn không có đi qua, thử trước một chút lại nói."),t("br"),t("br"),n._v("Khéo léo từ chối Phạm Triết lão sư lưu lại ăn cơm ngữ, Trương Huyền cùng Tôn Cường, hướng cách đó không xa thương hội đi tới, trong tay có hơn bốn vạn thần tệ, hẳn là có thể mua sắm không ít."),t("br"),t("br"),n._v("Rất nhanh, Tôn Cường cười khanh khách đi tới, đưa tới trữ vật giới chỉ, liếc mắt nhìn, tiếp cận bốn trăm viên."),t("br"),t("br"),n._v("Tình huống bình thường, một trăm thần tệ mới có thể mua sắm một viên trung phẩm cấp bậc Thần Nguyên đan, bởi vì người sau số lượng không hề giống hạ phẩm Thần Nguyên đan như vậy đầy đủ, hơn nữa bọn họ mua sắm số lượng lớn, giá cả sẽ giả cao một chút."),t("br"),t("br"),n._v("“Thiếu gia, có cần hay không thuê tĩnh thất, ngươi tốt dùng đan dược, yên tĩnh tu luyện?”"),t("br"),t("br"),n._v("Đem dược vật đưa qua, Tôn Cường hỏi."),t("br"),t("br"),n._v("“Tĩnh thất?” Trương Huyền lắc đầu: “Không cần, tiền còn thừa không có mấy, vẫn là tiết kiệm tiết kiệm đi!”"),t("br"),t("br"),n._v("“Cái kia... Chúng ta quay về chỗ ở?” Tôn Cường nghi hoặc."),t("br"),t("br"),n._v("Thiếu gia tu luyện nghiêm túc như vậy, vốn cho rằng cầm tới đan dược liền sẽ không kịp chờ đợi bắt đầu, vậy mà nghĩ đến tiết kiệm tiền... Trở về tuy là cũng có thể tu luyện, dù sao muốn chậm trễ không ít thời gian."),t("br"),t("br"),n._v("“Dĩ nhiên không phải, ngươi chờ một lát...” Khẽ mỉm cười, Trương Huyền lấy ra vừa mua được trung phẩm Thần Nguyên đan, một bình một bình hướng trong miệng đổ vào."),t("br"),t("br"),n._v("Thời gian nháy mắt, liền đổ hơn mười bình, hơn một trăm viên."),t("br"),t("br"),n._v("Tôn Cường ngay tại nghi hoặc, ngay sau đó liền thấy thiếu gia vừa mới đột phá tu vi, mắt thường có thể thấy được tăng thêm, hạ phẩm Thiên Thần sơ kỳ, hạ phẩm Thiên Thần trung kỳ..."),t("br"),t("br"),n._v("Không đến hai cái hô hấp, liền đạt đến hạ phẩm Thiên Thần đỉnh phong."),t("br"),t("br"),n._v("Lại nuốt một cái bình đan dược, Trương Huyền ngừng lại, bất đắc dĩ lắc đầu: “Nhìn tới so ta tưởng tượng bên trong nhanh hơn...”"),t("br"),t("br"),n._v("Còn tưởng rằng, trung phẩm Thần Nguyên đan, có thể giúp hắn ít nhất đột phá đến thượng phẩm Thiên Thần hoặc là trung phẩm Thiên Thần, hiện tại xem ra, cả nghĩ quá rồi."),t("br"),t("br"),n._v("Mới vừa đem tu vi tích tụ hạ phẩm Thiên Thần đỉnh phong, liền ngừng lại, lại không còn hiệu quả gì."),t("br"),t("br"),n._v("Cũng khó trách, tu luyện Thiên đạo công pháp thời điểm, trong cơ thể lực lượng liền tinh thuần, phổ thông linh thạch, Tiên Nguyên đan hiệu quả không lớn, hiện tại tu luyện vượt qua Thiên đạo Thiên Nhược Hữu Tình pháp quyết, đối với linh khí độ tinh thuần cùng số lượng dự trữ, tự nhiên có càng cao yêu cầu!"),t("br"),t("br"),n._v("Rõ ràng, phổ thông trung phẩm Thần Nguyên đan đã triệt để mất đi hiệu quả."),t("br"),t("br"),n._v("Có lẽ, chỉ có Thần Vương cường giả mới có thể dùng thượng phẩm Thần Nguyên đan, mới có thể tốt hơn kích động, để hắn đi vào cảnh giới cao hơn."),t("br"),t("br"),n._v("“Chẳng qua... Cùng hạ phẩm Thần Linh đồng dạng, muốn xung kích tu vi bên trên độ cao, linh hồn cùng thân thể không thể hạ xuống...”"),t("br"),t("br"),n._v("Thiên Thần cảnh, có thể chưởng khống một phần thiên địa lực lượng, thân thể cùng linh hồn nhất định cũng muốn đồng bộ tăng cường."),t("br"),t("br"),n._v("Nếu không, lúc chiến đấu thiên địa phản hồi áp lực, liền sẽ không chịu nổi, tại chỗ sụp đổ."),t("br"),t("br"),n._v("Chỉ là..."),t("br"),t("br"),n._v("Trước đó Danh Sư đường thư tịch, ghi chép rất nhiều tăng cao tu vi công pháp và võ kỹ, lại chỉ có không có linh hồn cùng thân thể."),t("br"),t("br"),n._v("Muốn tấn cấp, cũng chỉ có thể muốn biện pháp khác."),t("br"),t("br"),n._v("Tu luyện thêm vào những này suy nghĩ, tổng cộng không cao hơn ba cái hô hấp, khôi phục lại, giương mắt nhìn về phía trước, ngay sau đó nhìn thấy Tôn Cường con mắt trợn tròn, một bộ khó tin bộ dạng."),t("br"),t("br"),n._v("“Thiếu gia... Ngươi đã tu luyện xong?”"),t("br"),t("br"),n._v("Thiếu gia tu luyện là rất nhanh, tốc độ là để cho người ta hâm mộ và đố kị, có thể... Làm sao đến Thần giới về sau, nhanh hơn?"),t("br"),n._v("Trước kia còn cần bế quan, dù là thời gian không dài, một, hai canh giờ vẫn phải có, hiện tại, hoàn toàn không cần, cảm giác tốc độ đột phá, bị giới hạn ăn đan dược tốc độ, chỉ cần uống thuốc có thể nhanh một chút... Đột phá hoàn toàn theo kịp!"),t("br"),t("br"),n._v("Chẳng lẽ, Thần giới cùng hạ giới khác biệt?"),t("br"),t("br"),n._v("Cần cấp tốc nuốt thuốc, mới có thể càng nhanh tấn cấp?"),t("br"),t("br"),n._v("Tay vịn cái cằm, Tôn Cường trong lòng sinh ra một cái kế hoạch to gan."),t("br"),t("br"),n._v("Trương Huyền cũng không giải thích, hỏi: “Ta tu luyện công phu, ngươi có hay không hỏi thăm, nào có thăng cấp thân thể công pháp hoặc là bảo vật?”"),t("br"),t("br"),n._v("“...” Lông mày nhảy dựng, Tôn Cường muốn giết người."),t("br"),t("br"),n._v("Ngươi tu luyện tổng cộng dùng hai, ba cái hô hấp, để cho ta hỏi thăm cái gì? Cho người khác nghe được, thật đúng là cho rằng, ta không làm việc giống như..."),t("br"),t("br"),n._v("“Ta hiện tại liền đi...” Một mặt buồn bực, lời vừa ra miệng, đột nhiên nhớ tới cái gì: “Đúng rồi thiếu gia, cái kia Trác Phong, một mực theo ở phía sau, nói muốn báo đáp thiếu gia, thân là U Hồn hoàng thành tướng quân, trung phẩm Thiên Thần cường giả, muốn nói rèn luyện thân thể lời nói, nên biết tương đối nhiều!”"),t("br"),t("br"),n._v("Trác Phong, chính là vị kia tướng quân giáp bạc, địa vị kính trọng, khẳng định so với hắn một ngoại nhân, ra ngoài tìm hiểu tin tức đáng tin."),t("br"),t("br"),n._v("Dù sao, thiếu gia thực lực, đã đột phá đến Thiên Thần cảnh giới, loại thực lực này phương thức tu luyện, có thể tìm hiểu con đường khẳng định càng ngày càng ít."),t("br"),t("br"),n._v("“Để hắn vào đi!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Thời gian không dài, Trác Phong đi vào trước mặt."),t("br"),t("br"),n._v("“Trương sư!”"),t("br"),t("br"),n._v("Trước mắt vị này, chẳng những cứu con của hắn, càng làm cho hắn đột phá, cấp cho hi vọng, ân tình lớn, không cách nào báo đáp, làm sao có thể đi thẳng một mạch."),t("br"),t("br"),n._v("“Ừm, ta muốn hỏi thăm ngươi một việc...” Trương Huyền nói."),t("br"),t("br"),n._v("“Trương sư mời nói!”"),t("br"),t("br"),n._v("Trương Huyền nói: “Ta muốn luyện thể, cùng rèn luyện linh hồn, không biết ngươi cũng đã biết có cái gì phương pháp hoặc là phương pháp ư? Nếu có, hơn nữa đáng tin, coi như ngươi ta ân tình thanh toán xong, ai cũng không nợ người nào!”"),t("br"),t("br"),n._v("“Cái này...” Không nghĩ tới đối phương yêu cầu đơn giản như vậy, Trác Phong chần chờ một chút, nói: “Thần giới luyện thể phương pháp, đủ loại, ta biết một ít, có điều, đều là một ít cho Thần Linh tu luyện, còn Thiên Thần cảnh giới... Quả thực biết một cái, chỉ là... Đối phương chưa hẳn đồng ý!”"),t("br"),t("br"),n._v("“Ồ?”"),t("br"),t("br"),n._v("“Luyện thể chỗ tốt nhất, là Bất Tử Dung Nham trì! Nơi này, không chỉ có bất diệt dung nham, còn có để cho người ta có thể nhanh chóng khôi phục linh khí. Là rèn luyện thân thể, địa phương thích hợp nhất, chỉ bất quá... Bất Tử Dung Nham trì bốn mươi năm trước đột nhiên dập tắt. Bất Tử Đế Tôn lão nhân gia ông ta, không có ra tay chữa trị, một mực liền ở lại tại hiện tại!”"),t("br"),t("br"),n._v("Trác Phong nói."),t("br"),t("br"),n._v("“Bất Tử Đế Tôn?” Trương Huyền trong lòng khẽ động: “Cửu Đế một trong?”"),t("br"),t("br"),n._v("Thượng Thương Hãm Không chi thành, từng gặp vị này Bất Tử Đế Tôn bia mộ, lúc ấy không hiểu rõ Thần cấp đẳng cấp cùng phân chia, còn tưởng rằng chỉ là cái phổ thông Thần Linh, không có để ở trong lòng."),t("br"),t("br"),n._v("Giờ phút này, từ đối phương trong miệng nói ra cái tên này, trong lòng nhịn không được hỏi!"),t("br"),t("br"),n._v("Cửu Thiên Cửu Đế, Thần giới người mạnh nhất, không người có thể địch nổi, chết rồi... Cho dù tận mắt thấy bia mộ, cũng không dám tin tưởng."),t("br"),t("br"),n._v("“Đúng vậy a!” Trác Phong gật đầu: “Có điều, làm người khiêm tốn, đã mấy chục năm cũng không có xuất hiện qua, cũng không nghe thấy qua động tĩnh!”"),t("br"),t("br"),n._v("“Mấy chục năm?” Trương Huyền cau mày: “Thiên Túng Đại Đế, không phải cùng hắn giao đấu qua ư?”"),t("br"),t("br"),n._v("“Là giao đấu qua, nhưng mà chiến đấu ở nơi nào, cụ thể chiến không có chiến đấu, đều không người biết được... Ta nghe được một tin tức là... Thiên Túng Đại Đế đi qua khiêu chiến, Bất Tử Đế Tôn thấy đều không có gặp, liền nhận thua... Được rồi, dính dáng Đế Quân, chúng ta vẫn là không nói chuyện cho thỏa đáng!” Trác Phong hạ giọng truyền âm tới."),t("br"),t("br"),n._v("Nếu không phải trước mắt vị này là cứu hắn hài tử ân nhân, khẳng định cũng sẽ không nói nhiều như vậy."),t("br"),t("br"),n._v("Những tin tức này, tại ngoại giới không chút lưu truyền, nhưng hắn trung phẩm Thiên Thần, thân cư cao vị, một ít tin tức vẫn là đã nghe qua."),t("br"),t("br"),n._v("“Thấy đều không có thấy...” Trương Huyền cứng đờ."),t("br"),t("br"),n._v("Vốn đang cảm thấy, Hãm Không chi thành nhìn thấy Bất Tử Đế Tôn bia mộ, có thể là giả, nghe được cái này, trong lòng không khỏi tin tưởng hơn phân nửa."),t("br"),t("br"),n._v("Đế Quân cũng sẽ chết?"),t("br"),t("br"),n._v("Hơn nữa còn là... Mệnh danh bất tử Đế Quân?"),t("br"),t("br"),n._v("Mấy chục năm trước, Thần giới linh khí biến mất, chỉ để lại thành thị, Bất Tử Đế Tôn biến mất không thấy gì nữa..."),t("br"),t("br"),n._v("Đến cùng xảy ra chuyện gì?"),t("br"),t("br"),n._v("“Ngươi nói vị này Bất Tử Đế Tôn, không biết... Chưởng khống Cửu Thiên cái nào?” Nhịn không được nhìn qua."),t("br"),t("br"),n._v("Cửu Thiên hắn nghe người khác tỉ mỉ giới thiệu qua, cũng nhìn qua liên quan thư tịch, biết mấy vị Đại Đế, không biết vị này đến cùng là phương nào thế lực."),t("br"),t("br"),n._v("“Bất Tử Đế Tôn, chưởng khống chính là Linh Nguyên thiên, cũng gọi Bất Tử thiên. Cùng U Hồn thiên sát bên, ở tại thần giới chính nam phương! Trong đó ngũ giang tam trạch, phạm vi không biết bao lớn. Ta từng có may mắn đi qua Linh Nguyên hoàng thành một lần, đáng tiếc chỉ ở chủ thành bên ngoài đi lòng vòng, nghe nói chủ thành phía trong hết sức phồn hoa, nhưng tình huống cụ thể, cũng không hiểu rõ tình hình, dường như mấy chục năm trước, bị Bất Tử Đế Tôn mang đi...” Trác Phong nói."),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Linh Nguyên thiên trước hắn liền biết, không nghĩ tới vậy mà chính là vị kia bất tử Đế Quân chưởng khống thế lực, nói cách khác cái gọi là Bất Tử thiên."),t("br"),t("br"),n._v("Sớm biết gọi Bất Tử thiên, khẳng định đã sớm biết, cùng Bất Tử Đế Tôn có liên quan rồi."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2164"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2166"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);