(window.webpackJsonp=window.webpackJsonp||[]).push([[1315],{1670:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-208-muc-viet"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-208-muc-viet"}},[n._v("#")]),n._v(" Chương 208: Mực viết")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v(" Hồng Thiên học viện giờ khắc này đã sôi trào khắp chốn."),i("br"),i("br"),n._v(" Tân sinh thi đấu sắp bắt đầu."),i("br"),i("br"),n._v(" Một vạn tên nhập học tân sinh, đi qua nửa tháng học tập, đến kiểm nghiệm thành quả thời điểm."),i("br"),i("br"),n._v(" “Các ngươi nói lần này thi đấu, ai có thể lấy được số một?” "),i("br"),i("br"),n._v(" “Ta cảm thấy vẫn là trước đệ nhất cái kia gia hỏa, nhập học liền đạt đến võ giả đệ nhị trọng, hiện tại khẳng định càng mạnh mẽ hơn.” "),i("br"),i("br"),n._v(" “Tân sinh thi đấu, lại không dựa cả vào thực lực, lại nói, hắn nhập học trước lợi hại, không có nghĩa là hiện tại lợi hại. Ta nghe nói Lục lão sư môn hạ cái kia mấy cái tham gia sư giả bình trắc tân sinh, cũng đều đột phá võ giả nhị trọng, hơn nữa còn đạt được của hắn chân truyền, có thể thắng hay không qua bọn họ đều còn khó nói!” "),i("br"),i("br"),n._v(" “Hắn thật giống cũng là Lục lão sư học sinh đi, nhìn dáng dấp người thứ nhất coi như không phải cái tên này, cũng muốn tại Lục lão sư môn hạ xuất hiện.” "),i("br"),i("br"),n._v(" “Cái kia là tự nhiên, nhập môn sát hạch xếp hạng thứ 500, có ít nhất 300 người đến Lục lão sư môn hạ, coi như muốn thứ hai, cũng phải có đối thủ a!” "),i("br"),i("br"),n._v(" “Trương Huyền lão sư cùng Lục lão sư còn có sư giả bình trắc, các ngươi nói ai sẽ thắng?” "),i("br"),i("br"),n._v(" “Thắng? Ngươi choáng váng, đây còn phải nói? Trương Huyền lão sư có tư cách gì cùng Lục lão sư cạnh tranh? Coi như là phòng giáo dục cố ý làm khó dễ, để hắn giáo viên sát hạch không chính xác, đảm nhiệm dạy học hơn một năm nay, có vẻ như cũng không có dạy dỗ cái gì thành tích đi!” "),i("br"),i("br"),n._v(" “Chuyện này... Này ngược lại là!” "),i("br"),i("br"),n._v(" “Cái kia không phải, nói thật cho ngươi biết, ta nghe nói, là cái tên này muốn nổi danh, cố ý khiêu khích Lục lão sư, đem người dạy tẩu hỏa nhập ma, lại có thể còn không thấy ngại đưa ra sư giả bình trắc... Thật không biết sao mặt lại dầy như thế!” "),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v(" “Ta làm sao nghe được tin tức không phải như vậy? Ta nghe nói vị này Trương Huyền lão sư, là người cao thượng, đã sớm đạt đến Ích Huyệt cảnh, vì chăm sóc học viện mặt mũi, vẫn ẩn nhẫn không nói!” "),i("br"),i("br"),n._v(" “Ta cũng nghe nói, cái kia tẩu hỏa nhập ma học sinh, có người nói là hắn cố ý, mục đích là vì để cho hắn đột phá trời sinh Tỏa mạch ràng buộc.” "),i("br"),i("br"),n._v(" “Cái tên này kêu Triệu Nham Phong, ta đã từng thấy mấy mặt, Trương lão sư không những giúp hắn giải trừ loại này trời sinh bình phong, còn trực tiếp để cho đột phá, hiện tại đã là võ giả nhị trọng cao thủ, cảm niệm hắn ân, muốn một lần nữa bái vào Trương lão sư môn hạ, lúc này mới chọc giận Lục lão sư, đưa ra sư giả bình trắc.” "),i("br"),i("br"),n._v(" “Là như vậy? Thật hay giả?” "),i("br"),i("br"),n._v(' "Đương nhiên là thật sự, ta tin tức này,'),i("br"),i("br"),n._v(' Thế nhưng một vị bằng hữu quen thuộc nói, chính xác trăm phần trăm."'),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v(" Khắp nơi đều là nghị luận âm thanh, có nói Trương Huyền không biết tự lượng sức mình, cũng có nói hắn là bị phá nghênh chiến, đúng là bất đắc dĩ."),i("br"),i("br"),n._v(" Phòng giáo dục sự tình, đi qua Diêu Hàn tận lực truyền bá, rốt cục tất cả bạo phát, học viện người hầu như đều biết."),i("br"),i("br"),n._v(" Bất quá, phần lớn học sinh lựa chọn không tin."),i("br"),i("br"),n._v(" Chung quy, Lục Tầm tại học viện tiếng tăm thực sự quá to lớn, Trương Huyền tuy rằng rửa sạch giáo viên sát hạch là không điểm oan khuất, trong thời gian ngắn muốn ở đây sao nhiều học viên trong lòng dựng nên cao to hình tượng, vẫn là rất khó."),i("br"),i("br"),n._v(" Đối với những cái này, Trương Huyền cũng không để ý."),i("br"),i("br"),n._v(" Dù sao cũng ngày mai sư giả bình trắc, hết thảy đều sẽ có kết quả, cùng với ở đây băn khoăn, còn không bằng nhanh lên một chút giúp mấy học sinh tăng cao thực lực."),i("br"),i("br"),n._v(" Rất nhanh đi tới bản thân phòng học."),i("br"),i("br"),n._v(" “Trương lão sư!” "),i("br"),i("br"),n._v(" Triệu Nhã bọn người tại, mỗi người tinh khí no đủ, triển lộ ra không yếu khí tức."),i("br"),i("br"),n._v(" “Không sai!” "),i("br"),i("br"),n._v(" Nhìn thấy mấy người trạng thái, Trương Huyền thoả mãn gật gù."),i("br"),i("br"),n._v(" Quả nhiên không có uổng phí hắn một phen khổ tâm."),i("br"),i("br"),n._v(" Bất luận Triệu Nhã, Trịnh Dương, Vương Dĩnh, Lưu Dương vẫn là Viên Đào, năm người thực lực đều có chất bay vọt, cùng nửa tháng trước, như là biến thành người khác giống như."),i("br"),i("br"),n._v(" Không chỉ có như vậy, liền ngay cả mấy cái dự thính sinh, Vương Đào, Vương nham, Triệu Nham Phong mấy người, cũng đều có tiến bộ nhảy vọt."),i("br"),i("br"),n._v(" Hắn giảng bài tuy rằng không nhiều, lại mỗi một chữ đều nhắm thẳng vào tu luyện hạch tâm, tại bọn họ xuống nghe giảng bài, đối với tu luyện lĩnh ngộ cũng càng ngày càng nhiều, tiến bộ liền đã biến thành chuyện đương nhiên."),i("br"),i("br"),n._v(" “Cái kia là cái gì?” "),i("br"),i("br"),n._v(" Khích lệ một câu, đang muốn đơn độc giảng bài, để bọn họ tiến thêm một bước nữa, đột nhiên, Trương Huyền con mắt rơi ở phòng học bên trong góc."),i("br"),i("br"),n._v(" Mấy cái chậu bày một bên, bên trong hoàn toàn đen sì, không biết là món đồ gì."),i("br"),i("br"),n._v(" Hắn rõ ràng nhớ kỹ, trước trong phòng học không có vật này."),i("br"),i("br"),n._v(" “Trương lão sư, là mực viết.” Vương Dĩnh hơi đỏ mặt, nhỏ giọng nói."),i("br"),i("br"),n._v(" “Mực viết?” Trương Huyền đầu óc mơ hồ: “Trong phòng học xếp đặt vật này làm gì?” "),i("br"),i("br"),n._v(" Hắn dạy chính là tu vi, không phải văn hóa khóa, cần vẩy mực vẽ tranh, cũng không cần vẽ thư pháp, chơi những thứ đồ này làm gì?"),i("br"),i("br"),n._v(" “Cái này...” "),i("br"),i("br"),n._v(" Nghe hắn vừa hỏi, mấy học sinh tất cả đều đầy mặt lúng túng, mỗi một người đều không nói lời nào."),i("br"),i("br"),n._v(" “Làm sao? Chẳng lẽ có cái gì thuyết pháp? Viên Đào, ngươi nói!” "),i("br"),i("br"),n._v(" Thấy mọi người thái độ quái lạ, Trương Huyền lông mày giương lên."),i("br"),i("br"),n._v(" “Ta...” "),i("br"),i("br"),n._v(" Không nghĩ tới Trương lão sư điểm danh hỏi hắn, Viên Đào sắc mặt như khổ qua, băn khoăn chốc lát: “Là như vậy, Triệu Nhã tiểu thư thuần âm thể chất mở ra, trở nên càng xinh đẹp hơn, không ít học viên đều muốn cùng nàng đi lại...” "),i("br"),i("br"),n._v(" “Đi lại?” "),i("br"),i("br"),n._v(" Trương Huyền lúc này mới chú ý, thuần âm thể chất mở ra phía sau, Triệu Nhã quả nhiên thoát thai hoán cốt, bất luận dung mạo vẫn là khí chất, đều có biến hóa long trời lở đất, không nữa giống trước cái kia thô bạo Đại tiểu thư, mà là một cái mười phần đại mỹ nữ."),i("br"),i("br"),n._v(" Loại này dung mạo, so Thẩm Bích Như đều lợi hại hơn mấy phần, cũng khó trách có những học sinh khác ngưỡng mộ, lặng lẽ lại đây."),i("br"),i("br"),n._v(" “Có người đến tìm nàng... Chơi mực viết làm gì?” Trương Huyền càng thêm kỳ quái."),i("br"),i("br"),n._v(" Coi như Triệu Nhã biến đẹp đẽ, có những nam sinh khác lại đây quấy rầy, đánh đuổi chính là, tin tưởng trong học viện, bọn họ còn không dám làm bừa."),i("br"),n._v("  Lại nói, này mấy học sinh, thực lực tăng mạnh, có người dám qua tới quấy rối, cũng sớm bị đánh đi rồi đi."),i("br"),i("br"),n._v(" Trịnh Dương, Lưu Dương, Viên Đào này ba cái, nhìn chút liền không phải cái gì kẻ tầm thường, người khác đục khoét nền tảng đào đến nhà đến rồi, còn có thể ngoan ngoãn cùng bọn họ giảng đạo lý?"),i("br"),i("br"),n._v(" “Cái này... Học sinh chúng ta cơ bản đều đánh đuổi, bất quá...” Viên Đào vò đầu, lúng túng nở nụ cười một tiếng: “Bất quá, hai ngày nay mỗi ngày đều có ba cái hèn mọn lão đầu, quanh quẩn một chỗ ở xung quanh, nhìn bọn họ động tác cùng cử động, chúng ta khẳng định đánh không lại, cho nên mới chuẩn bị chút mực viết, dự định giội bọn họ một thân! Nghe nói mực viết chuyên phá dâm tục, bọn họ già mà không đứng đắn, dính lên vật này, khẳng định thật không tiện tiếp tục tiếp tục chờ đợi...” "),i("br"),i("br"),n._v(" “Hèn mọn lão đầu?” Trương Huyền trượng nhị hòa thượng không hiểu rõ nổi: “Các ngươi xác định không phải học viện lão sư?” "),i("br"),i("br"),n._v(" “Không phải, học viện lão sư ta cơ bản đều biết, tuyệt đối không có này mấy cái, nhìn chút cái kia hèn mọn dáng dấp, liền không có lòng tốt!” Lần này nói chuyện chính là Trịnh Dương: “Ta hai ngày nay vẫn thấy bọn họ lặng lẽ trốn ở một bên, đối với Triệu Nhã, Vương Dĩnh chỉ chỉ chỏ chỏ, thỉnh thoảng còn gật đầu cười cười, nhìn chút liền không phải người tốt lành gì.” "),i("br"),i("br"),n._v(" “Đối với Triệu Nhã mấy người chỉ chỉ chỏ chỏ? Học viện khi nào tới đây loại vô sỉ gia hỏa?” "),i("br"),i("br"),n._v(" Thấy mấy học sinh lời thề son sắt, không giống nói láo, Trương Huyền cau mày."),i("br"),i("br"),n._v(" Lão đầu, đối với mười sáu, mười bảy tuổi bé gái chỉ chỉ chỏ chỏ... Xác thực đủ hèn mọn."),i("br"),i("br"),n._v(" “Như vậy đi!” "),i("br"),i("br"),n._v(" Lại nghe mấy người nói rồi vài câu, cũng cảm thấy này mấy cái lão đầu không có ý tốt, Trương Huyền gật gù: “Các ngươi cẩn thận một ít, Triệu Nhã, Vương Dĩnh, mấy ngày nay không muốn đơn độc đi ra ngoài, nếu như gặp lại này ba cái lão đầu, có thể dùng mực viết giội!” "),i("br"),i("br"),n._v(" Nếu là lão đầu, khẳng định là có thân phận, công nhiên theo dõi nhìn trộm bé gái, liền rất mất mặt, vạn lần nữa bị vẩy mực, có lẽ, cũng sẽ biết mất mặt, tự động rút đi."),i("br"),i("br"),n._v(" Đương nhiên, hai ngày nay hắn phần lớn thời gian đều sẽ chờ ở trường học, mấy tên này còn dám lại đây, không ngại ra tay dạy dỗ một trận."),i("br"),i("br"),n._v(" “Trương lão sư vạn tuế!” "),i("br"),i("br"),n._v(" “Ta liền nói Trương lão sư thâm minh đại nghĩa, sẽ không trách tội chúng ta...” "),i("br"),i("br"),n._v(" Nghe hắn đồng ý, mấy học sinh đồng thời hô to."),i("br"),i("br"),n._v(" “Ân, được rồi, đừng nói nhảm, Viên Đào, ngươi trước lại đây, ta nhìn ngươi một chút gần nhất tu vi tiến bộ như thế nào.” "),i("br"),i("br"),n._v(" Ngày mai sẽ phải sư giả bình trắc, ngày hôm nay nhất định phải đối với học sinh có cái thống nhất hiểu rõ mới được, đồng thời, đơn độc chỉ điểm một chút, có lẽ còn có thể để cho lại có một ít tiến bộ."),i("br"),i("br"),n._v(" Dù cho tiến bộ rất nhỏ, làm không cẩn thận ở trong chiến đấu liền có thể làm cho phần thắng tăng cường không ít."),i("br"),i("br"),n._v(" Đi vào phòng học nhỏ, đóng cửa lại, Trương Huyền khoát tay chặn lại: “Đánh một bộ võ kỹ.” "),i("br"),i("br"),n._v(" “Vâng!” Viên Đào gật gù, một quyền đánh ra ngoài, tiếng gió rít gào, khí thế kinh người."),i("br"),i("br"),n._v(" Tu vi của hắn tuy rằng không có quá tiến bộ lớn, vẫn như cũ là võ giả nhất trọng Tụ Tức cảnh trung kỳ, có thể sức mạnh lại vô cùng mạnh mẽ, một quyền xuất hiện, không trung xuất hiện âm bạo."),i("br"),i("br"),n._v(" “Lực lượng này...” "),i("br"),i("br"),n._v(" Trương Huyền tặc lưỡi."),i("br"),i("br"),n._v(" Có thể một quyền đánh ra âm bạo, nói rõ sức mạnh của hắn cùng phòng ngự đều đạt đến rất tài nghệ cao, một quyền xuống, đừng nói tướng cùng cấp bậc người tu luyện, coi như võ giả tam trọng Chân Khí cảnh cường giả có thể hay không chịu đựng, đều còn khó nói."),i("br"),i("br"),n._v(" “Không hổ là Long Tê huyết mạch, dù cho chỉ kích hoạt mười phần trăm, cũng vô cùng khủng bố!” "),i("br"),i("br"),n._v(" Thể chất đặc thù có thể nổi danh như vậy, quả nhiên không tầm thường."),i("br"),i("br"),n._v(" Viên Đào giờ khắc này tu vi vẫn như cũ là năm cái học viên bên trong kém cỏi nhất, nhưng lực công kích cùng sức phòng ngự, đều tuyệt đối mạnh lớn đến đáng sợ."),i("br"),i("br"),n._v(" “Tu vi của ngươi vẫn là quá chênh lệch, ngươi trong quá trình tu luyện, hội tụ linh khí thời điểm, quá chú trọng đối với linh khí hấp thu mà không thể cùng bản thân tình huống phối hợp, ta hiện tại nói một cái khẩu quyết, ngươi nhớ kỹ, chỉ cần cố gắng tu luyện, ngày hôm nay xung kích Tụ Tức cảnh hậu kỳ, đều không phải việc khó.” "),i("br"),i("br"),n._v(" Nhìn một hồi, Trương Huyền nói."),i("br"),i("br"),n._v(" Đối phương đánh quyền, trong đầu đã hình thành mới thư tịch, đem hắn bên trong nội dung xem xong, dĩ nhiên biết vấn đề của hắn cùng thiếu hụt."),i("br"),i("br"),n._v(" “Vâng!” Viên Đào liền vội vàng gật đầu, chuyên tâm đọc thuộc lòng, thời gian không lâu, liền đem khẩu quyết nhớ kỹ."),i("br"),i("br"),n._v(" “Được rồi, đem Triệu Nhã kêu đến.” "),i("br"),i("br"),n._v(" Bàn giao xong Viên Đào, Trương Huyền vung vung tay."),i("br"),i("br"),n._v(" Thời gian không lâu Triệu Nhã đi tới."),i("br"),i("br"),n._v(" Không thể không nói, thuần âm thể chất quả nhiên đáng sợ, một khi kích hoạt, lập tức tỏa ra làm người thán phục khí chất, làm cho người ta một loại tiên nữ trên chín tầng trời rơi xuống phàm trần cảm giác."),i("br"),i("br"),n._v(" Triệu Nhã vốn là mỹ nữ, giờ khắc này càng càng mỹ lệ, nhất cử nhất động, đều tự mang phong độ."),i("br"),i("br"),n._v(" “Cái này dung mạo khí chất, e sợ cùng nam bạn học gặp gỡ, chỉ cần đối với hắn nở nụ cười, sẽ chủ động chịu thua.” "),i("br"),i("br"),n._v(" Trời cao quả nhiên là không công bằng, Triệu Nhã dáng vẻ ấy, tham gia tỷ thí, đối diện nếu như là cái nam sinh, làm không cẩn thận ra tay cũng không dám."),i("br"),i("br"),n._v(" “Đánh một quyền nhìn!” "),i("br"),i("br"),n._v(" “Vâng!” Triệu Nhã gật đầu, thanh tú quyền xiết chặt, một quyền đánh ra."),i("br"),i("br"),n._v(" Ào ào ào!"),i("br"),i("br"),n._v(" Trong phòng tràn đầy tiếng gió thổi."),i("br"),i("br"),n._v(" “Thực lực của ngươi...” "),i("br"),i("br"),n._v(" Đầu óc hình thành thư tịch, Trương Huyền mở ra nhìn chút, con mắt nhất thời trợn tròn, tràn đầy không thể tin được."),i("br"),i("br"),n._v(" Chuyện này... Làm sao có khả năng?"),i("br"),i("br"),n._v(" “Các ngươi làm gì?” "),i("br"),i("br"),n._v(" “Làm càn!” "),i("br"),i("br"),n._v(" Rào rào! Ầm ầm!"),i("br"),i("br"),n._v(" Đang muốn nói chuyện, đột nhiên bên ngoài phòng học một tiếng nổ vang, tiếp theo kịch liệt tiếng gầm gừ vang lên, chen lẫn gầm lên giận dữ."),i("br"),i("br"),n._v(" “Xảy ra chuyện gì?” "),i("br"),i("br"),n._v(" Hơi nhướng mày, Trương Huyền lại không để ý tới nhiều lời, nhấc chân đi ra ngoài."),i("br"),i("br"),n._v(" (Chưa xong còn tiếp.) "),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-216"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-218"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);