(window.webpackJsonp=window.webpackJsonp||[]).push([[1320],{1674:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2159-muon-lam-đong-tac-lon"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2159-muon-lam-đong-tac-lon"}},[n._v("#")]),n._v(" Chương 2159: Muốn làm động tác lớn")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Quan trọng làm cái gì việc lớn?"),t("br"),t("br"),n._v("Có thể để cho tất cả mọi người biết, hơn nữa cảm niệm bản thân tốt?"),t("br"),t("br"),n._v("Nơi này là U Hồn thiên hoàng thành, cường giả vô số, tuy là thân thể tiến bộ rất lớn, còn không tự đại cho rằng, đã vô địch thiên hạ."),t("br"),t("br"),n._v("Không nói mặt khác, trước đó nhìn thấy vị kia Tề Dược, khả năng cũng không phải là đối thủ."),t("br"),t("br"),n._v("Chân khí đột phá, cùng thân thể đột phá, là hai khái niệm."),t("br"),t("br"),n._v("Người sau nhiều nhất phòng ngự tăng cường, cận thân vô địch, một khi khoảng cách xa, liền nguy hiểm."),t("br"),t("br"),n._v("Cái trước, rời xa vài trăm mét, thậm chí hơn ngàn mét tiến công, bản thân chỉ sợ một chút năng lực phản kháng đều không có."),t("br"),t("br"),n._v("Đặc biệt là mượn thiên địa lực lượng, chỉ có thể bị động ăn đòn."),t("br"),t("br"),n._v("Cho nên, muốn có được những người khác công nhận, hội tụ nguyện lực, không chỉ có muốn trang bức, còn muốn làm vừa đúng, không thể quá tao, không thể dẫn phát quá nhiều người chú ý, nếu không, làm không cẩn thận bị người ghen ghét, liền bị người chết luôn."),t("br"),t("br"),n._v("“Như thế nào mới có thể để nhiều hơn nữa người ghi nhớ, đạt được nhiều hơn nữa nguyện lực?” Nghĩ đến cái này, nhịn không được hỏi."),t("br"),t("br"),n._v("Hắn mới đến Thần giới, vừa rõ ràng đạo lý này, phía trước khẳng định có rất nhiều người biết được, cũng đi làm, hỏi thăm thoáng cái, khẳng định so với chính mình suy nghĩ nhanh hơn nhiều."),t("br"),t("br"),n._v("“Muốn cấp tốc để cho người ta ghi nhớ biết ơn, hơn nữa sinh ra nguyện lực, trực tiếp khiêu chiến Đế Quân, liền giống như Thiên Túng Đại Đế đồng dạng, một khi thành công, toàn bộ Thần giới đều có thể náo động, nguyện lực tự nhiên lấy mãi không hết, dùng mãi không cạn!”"),t("br"),t("br"),n._v("Trác Phong cười khổ nói."),t("br"),t("br"),n._v("“Cái này chuyện cười không mở ra được!” Trương Huyền nói."),t("br"),t("br"),n._v("Thật muốn làm như vậy, đoán chừng mới nói xong lời nói, liền bị đối phương giết."),t("br"),t("br"),n._v("Đế Quân tuy là chưa thấy qua, phong hào Thần Vương thế nhưng là thật thấy qua, loại này cường giả, một ngón tay liền có thể đem bản thân nghiền chết, chớ nói chi là, càng cường đại hơn Cửu Đế."),t("br"),t("br"),n._v("“Đúng vậy a!” Trác Phong thần sắc cứng lại."),t("br"),t("br"),n._v("Trêu chọc như thế, quả thực có chút lỗ mãng."),t("br"),t("br"),n._v("Xem ra hôm nay con trai khôi phục, hơn nữa tu vi đột phá, thật cao hứng, trở nên có chút nhẹ nhàng."),t("br"),t("br"),n._v("“Bình thường muốn đạt được nguyện lực con đường, là trở thành một cái ngành nghề tài năng xuất chúng. Tỷ như, luyện khí! Chỉ cần ngươi luyện chế vũ khí mạnh mẽ mà dùng tốt, chỉ cần tu luyện giả, đều lấy nắm giữ ngươi luyện chế vũ khí làm vinh, nguyện lực tự nhiên sẽ càng ngày càng nhiều. Còn có, chế thuốc, ngươi thuốc nếu như có thể rất tốt chữa bệnh, hơn nữa giá cả tiện nghi, biết ơn tự nhiên tránh không được...”"),t("br"),t("br"),n._v("Trác Phong nói tiếp."),t("br"),t("br"),n._v("“Chế thuốc?” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Đúng a, hắn luyện khí không được, hơn nữa tốc độ sẽ không rất nhanh, có thể chế thuốc... Hoàn toàn không cần ah!"),t("br"),t("br"),n._v("Chỉ cần tìm đến hạ phẩm Thần Nguyên đan, hướng bên trong truyền vào chân khí, liền có thể lập tức biến thành Mỹ Dung đan, chẳng những có thể để cho người ta làn da biến tốt, càng có thể chữa trị trong cơ thể ám thương, khơi thông kinh mạch, để cho người ta cấp tốc tiến bộ!"),t("br"),t("br"),n._v("Chỉ là..."),t("br"),t("br"),n._v("Mỹ Dung đan giá cả, thực sự quá cao, không phải người bình thường có thể gánh vác lên."),t("br"),t("br"),n._v("Mà muốn bán tiện nghi, tương đương với đập trước đó vừa mới xây dựng thị trường, khẳng định sẽ dẫn tới càng lớn bắn ngược."),t("br"),t("br"),n._v("“U Hồn hoàng thành nhưng có tiện nghi gì viên thuốc loại hình đồ vật, hơn nữa không hạn mua, có thể tuỳ ý mua?” Trong lòng hơi động, Trương Huyền hỏi."),t("br"),t("br"),n._v("Hắn hiện tại muốn không nhất định là Thần Nguyên đan, chỉ cần là đan dược, có thể dung nạp Thiên đạo chân khí không bị người phát hiện là được, trước đó còn chơi qua rượu thuốc đây."),t("br"),t("br"),n._v("Suy nghĩ một chút, Trác Phong nói: “Hoàng thành bán tốt nhất là có thể để người ta khôi phục thần lực, Thần Nguyên hoàn! Thần Nguyên đan phiên bản đơn giản hóa, bởi vì dùng quá nhiều người, giá cả rất thấp. Một viên thần tệ có thể mua mua mười cái tả hữu, cơ bản muốn mua bao nhiêu có thể mua bao nhiêu, không hạn số lượng. Mỗi cái tu luyện giả, bình thường đều sẽ chuẩn bị hơn vài chục trên trăm viên, hiệu quả kém xa Thần Nguyên đan, nhưng cũng có thể để cho người ta tại thời điểm nguy hiểm khôi phục nhanh chóng khí lực!”"),t("br"),t("br"),n._v("“Ừm, là hắn!” Trương Huyền gật đầu, trong lòng đã có kế hoạch."),t("br"),t("br"),n._v("Tiện nghi, chi phí thấp, mới có thể đối mặt đại chúng, đối mặt người bình thường."),t("br"),t("br"),n._v("Chỉ đi cấp cao, nhiều nhất kiếm tiền, muốn đạt được biết ơn, khẳng định là không làm được."),t("br"),t("br"),n._v("Cho nên, Mỹ Dung đan, vẫn như cũ đi làm, chinh phục cấp cao, chinh phục nữ nhân, Thần Nguyên hoàn lời nói, liền nghĩ biện pháp chinh phục người bình thường, làm cho tất cả mọi người điên cuồng!"),t("br"),t("br"),n._v("Bất quá, Thiên đạo chân khí tỉ lệ cũng phải đem nắm tốt."),t("br"),t("br"),n._v("Mỹ Dung đan phía trong, có thể để vào một đạo Thiên đạo chân khí, Thần Nguyên hoàn phía trong, một phần trăm đạo chân khí là được, muốn là có hiệu quả, không cần thiết rõ ràng như vậy."),t("br"),t("br"),n._v("Bằng không, cấp cao đồ vật liền không có cách nào bán."),t("br"),t("br"),n._v("Hơn nữa, thăng đấu ân, mễ đấu cừu."),t("br"),t("br"),n._v("Giá rẻ, bán quá hiệu suất cao quả thuốc, không những không thể lôi kéo nhân tâm, ngược lại sẽ hoàn toàn ngược lại, dẫn tới đại động thanh trừ."),t("br"),t("br"),n._v("Bất cứ chuyện gì đều muốn nắm chắc một cái độ, nắm giữ tốt, mới có thể cân nhắc thiên hạ,"),t("br"),t("br"),n._v("“Đa tạ!” Khẽ mỉm cười, Trương Huyền nhìn về phía trước mắt trung niên nhân."),t("br"),t("br"),n._v("Lần này may mắn mà có hướng hắn hỏi thăm, nếu không, làm một cái người từ ngoài đến, căn bản không biết những thứ này."),t("br"),t("br"),n._v("“Vẫn là câu nói kia, Trương sư như có dặn dò, mở miệng chính là, ta nhất định dốc hết toàn lực trợ giúp, sẽ không lùi bước!” Trác Phong nói."),t("br"),n._v(" “Thật là có sự tình làm phiền ngươi thoáng cái, ta phải lượng lớn thu mua Thần Nguyên hoàn, số lượng càng nhiều càng tốt, nếu như Trác Tướng quân có rảnh rỗi, liền giúp ta một tay!” Trương Huyền nói."),t("br"),t("br"),n._v("“Cái này đơn giản, hàng năm quân doanh đều muốn thu mua, ta có thể tìm tới những này bán ra thương nghiệp, hơn nữa cho ta giá cả, tuyệt đối rẻ nhất!” Trác Phong cười nói."),t("br"),t("br"),n._v("“Vậy thì tốt nhất rồi...” Trương Huyền gật gật đầu: “Ngươi bây giờ liền đi thu thập, càng nhiều càng tốt, mặc kệ bao nhiêu, ta trong bảy ngày đều muốn, đến lúc đó, một tay giao tiền, một tay giao hàng!”"),t("br"),t("br"),n._v("“Tốt, chuyện này, giao cho ta, ngươi yên tâm!” Mặc dù không biết trước mắt vị này muốn làm gì, nhưng nếu đã nói như vậy, đương nhiên sẽ không nói nhiều, xoay người đi ra ngoài, sẽ không biến mất ở trước mắt."),t("br"),t("br"),n._v("Hắn vừa rời đi, Trương Huyền tìm tới Tề Linh Nhi cùng Tôn Cường: “Hiện tại dặn dò hai người các ngươi sự kiện!”"),t("br"),t("br"),n._v("“Trương thiếu (thiếu gia) mời nói!” Hai người đồng thời khom người."),t("br"),t("br"),n._v("Trương Huyền nói: “Thứ nhất, tìm đến hạ phẩm Thần Nguyên đan, số lượng không thua kém một vạn viên! Ta muốn đem hắn toàn bộ luyện chế thành Mỹ Dung đan, tiếp đó tiêu thụ ra đi, tận khả năng đem thời gian áp súc tại trong bảy ngày!”"),t("br"),t("br"),n._v("“Chuyện này, ta đi làm, một vạn viên Thần Nguyên đan, tại Dạ Minh thành không có khả năng hoàn thành, tại hoàng thành lời nói, nửa ngày liền có thể mang tới!”"),t("br"),t("br"),n._v("Tề Linh Nhi gật gật đầu: “Còn bán ra, gia chủ nói đều muốn, có thể một vạn viên thực sự quá nhiều, đoán chừng Tề gia cũng bắt không được, nhưng không sao, hiện tại Tề gia cho ta quyền lợi, chỉ cần nghiêm túc đi làm, bảy ngày thời gian, một vạn viên tiêu thụ không được, vượt qua một nửa số lượng, vấn đề không lớn!”"),t("br"),t("br"),n._v("Tại Dạ Minh thành loại địa phương nhỏ này, đều có thể hô phong hoán vũ, giờ phút này, đi vào hoàng thành, lại lấy được lão tổ công nhận, nắm giữ quyền lợi cùng giao thiệp, một vạn viên Thần Nguyên đan, một vạn viên Mỹ Dung đan, thoạt nhìn là cái khó khăn nhiệm vụ, hoạt động tốt lắm lời nói, sẽ không quá khó."),t("br"),t("br"),n._v("Chỉ cần thời gian không quá căng liền tốt."),t("br"),t("br"),n._v("“Ừm, còn có, nghĩ biện pháp thu mua bình ngọc, muốn đẹp mắt, hơn nữa giá cả thấp, có thể chứa đựng dược vật, không toả ra linh khí, đồng dạng số lượng càng nhiều càng tốt... Chuyện này, đồng dạng trong bảy ngày làm tốt. Mặt khác, chiếu theo các ngươi Tề gia quan hệ, triệu tập trong hoàng thành nhân vật nổi tiếng cùng người bình thường, ít nhất mười vạn, ta cần một cái lớn sân bãi, bảy ngày sau có thể dùng.”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Muốn trong thời gian ngắn đem dược vật phát triển ra ngoài, dược hiệu bùng nổ, hình thành danh tiếng, chỉ có một con đường có thể đi, đó chính là dùng phương pháp nhanh nhất tuyên truyền!"),t("br"),t("br"),n._v("Mà đem người triệu tập lại, cùng trước đó Tề Linh Nhi tại Dạ Minh thành làm như thế, hiện trường đi làm, làm cho tất cả mọi người tận mắt thấy, hiệu quả đương nhiên là lớn nhất!"),t("br"),t("br"),n._v("“Chuyện này ta lập tức đi làm!” Tề Linh Nhi gật đầu."),t("br"),t("br"),n._v("Đổi lại trước đó, nàng khẳng định không làm được, hiện tại có được gia chủ cùng lão tổ công nhận, có thể sử dụng tài nguyên sẽ càng ngày càng nhiều, trong bảy ngày làm được điểm ấy, cũng không khó!"),t("br"),t("br"),n._v("Thấy nàng đồng ý, Trương Huyền nhìn về phía Tôn Cường: “Chuyện thứ hai, ngươi tới làm a, trong thành thuê bên trên một trăm nhà cửa hàng, không nên quá lớn, mỗi một gian mười cái mét vuông, có thể ở người là được, tất cả đều sửa chữa, sửa thành thống nhất bề ngoài, tên gọi... Trương Huyền chế dược!”"),t("br"),t("br"),n._v("“Trương Huyền chế dược?” Tôn Cường thể diện một quất."),t("br"),t("br"),n._v("Tên này cũng quá tầm thường đi! Không có chút nào cao đại thượng."),t("br"),t("br"),n._v("“Muốn lưu truyền rộng rãi, làm cho tất cả mọi người ghi nhớ, đây là phương pháp đơn giản nhất, trực tiếp quan danh, chỉ cần lưu truyền ra đến, tự nhiên đều sẽ biết tên của ta!” Gặp hắn biểu lộ, biết nghĩ cái gì, Trương Huyền giải thích nói."),t("br"),t("br"),n._v("Nếu đối mặt chính là cấp thấp người sử dụng, tự nhiên càng đơn giản càng tốt, làm quá phức tạp đi, ngược lại để cho người ta không nhớ được."),t("br"),t("br"),n._v("Trương Huyền chế dược, thoạt nhìn tầm thường, trên thực tế chỉ cần trải rộng ra, nhất định có thể dẫn tới cực lớn náo động, đến lúc đó, danh khí cũng có, nguyện lực cũng có thể thu thập."),t("br"),t("br"),n._v("Càng quan trọng hơn là, một khi danh khí truyền đi, Lạc Nhược Hi hoặc là Lạc Thất Thất biết, có lẽ sẽ trước thời hạn tới tìm kiếm, so với chính mình chẳng có mục đích còn mạnh hơn nhiều."),t("br"),t("br"),n._v("“Những cửa hàng này, trang trí thêm vào tìm kiếm, đều muốn trong mười ngày hoàn thành, có thể làm được hay không?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Cái này...” Tôn Cường mập mạp cái cổ co rụt lại, cắn răng: “Tốt!”"),t("br"),t("br"),n._v("Một trăm cửa hàng, trong mười ngày hoàn thành, tương đương với mỗi ngày mười nhà, độ khó thực sự quá lớn, chẳng qua... Chỉ cần không sợ dùng tiền, tìm đúng môi giới, hoàn thành không khó lắm."),t("br"),t("br"),n._v("“Rất tốt, nắm chặt đi làm a!”"),t("br"),t("br"),n._v("Chẳng muốn nói thêm nữa, Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Vâng!” Tề Linh Nhi cùng Tôn Cường đồng thời đi ra ngoài, chỉ chốc lát biến mất ở trước mắt."),t("br"),t("br"),n._v("“Nắm giữ danh khí, chẳng khác nào nắm giữ vô số nguyện lực... Hiện tại ta muốn làm chính là... Nghĩ biện pháp tại U Hồn trong ao lưu lại linh hồn của mình ấn ký! Càn Khôn Đế Quân U Hồn trì, quá mức cao cấp, ta khẳng định không cần đến, cũng không thể đi dùng, nhưng U Hồn hoàng thành U Hồn trì ngược lại là có thể đi nhìn một chút...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền đứng dậy."),t("br"),t("br"),n._v("An bài đám người đi làm việc, hắn tự nhiên cũng sẽ không nhàn rỗi."),t("br"),t("br"),n._v("Việc cấp bách là tại U Hồn trì lưu lại ấn ký, nếu không, coi như danh khí đi ra, cũng không có khả năng tẩm bổ linh hồn của mình, để hồn lực trở nên mạnh mẽ."),t("br"),t("br"),n._v("Hoàng thành U Hồn trì tại U Hồn điện phía trong, điểm ấy tại Danh Sư đường đọc sách thời điểm, liền biết, vị trí cụ thể cũng làm rõ ràng, hiện tại vừa vặn có thời gian, vậy thì đi qua nhìn một chút."),t("br"),t("br"),n._v("Đi ra sân nhỏ, thẳng tắp hướng U Hồn điện phương hướng đi tới."),t("br"),t("br"),n._v("Sau hai canh giờ, một tòa cực kỳ rộng lớn cung điện xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“U Hồn điện trọng địa, người ngoài không được tự tiện xông vào!”"),t("br"),t("br"),n._v("Vừa tới đến trước mặt, hai cái hộ vệ liền ngăn tại phía trước."),t("br"),t("br"),n._v("“Ta muốn đi một chuyến U Hồn trì, không biết có thể?” Trương Huyền khẽ mỉm cười."),t("br"),t("br"),n._v("“Đi vào U Hồn trì? Nhưng có hẹn trước? Đi thăm viếng người nào?” Một cái hộ vệ mày nhăn lại, nhìn lại."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2173"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2175"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);