(window.webpackJsonp=window.webpackJsonp||[]).push([[1321],{1675:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2160-u-hon-tri"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2160-u-hon-tri"}},[n._v("#")]),n._v(" Chương 2160: U Hồn trì")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Cũng không phải là thăm viếng người nào, mà là muốn lưu ý niệm, sưu tập nguyện lực!” Trương Huyền nói."),t("br"),t("br"),n._v("Hộ vệ tràn đầy nghi hoặc: “Nguyện lực cũng không phải là người nào đều có thể tiếp nhận, hơn nữa, không có công lao loại hình, cũng không có tư cách! Bất quá, ngươi thật muốn đi làm lời nói, U Hồn điện sẽ không cự tuyệt, cần giao nộp một vạn thần tệ!”"),t("br"),t("br"),n._v("“Một vạn?” Trương Huyền gật gật đầu: “Tốt!”"),t("br"),t("br"),n._v("Trước đó Tề gia chủ cho tiền, Tề Linh Nhi một phần không có lưu quay lại, đối với những người khác tới nói, một vạn thần tệ, vô cùng có khả năng táng gia bại sản, đối với hắn mà nói, vẫn là có thể tuỳ tiện lấy ra."),t("br"),t("br"),n._v("Lấy ra thẻ, đem tiền tìm tới, tại đối phương dẫn dắt bên dưới, đi vào đại điện."),t("br"),t("br"),n._v("U Hồn trì, tại cung điện chỗ sâu nhất, cùng Nguyệt Thanh trì lực lượng hoàn toàn khác biệt, đứng tại trong đó, để cho người ta linh hồn dịu dàng, dường như có không biết tên lực lượng tự nơi xa mà đến, không ngừng tẩm bổ."),t("br"),t("br"),n._v("Bất quá, muốn hấp thu cỗ lực lượng này, nhưng lại giống như là diều bị đứt dây đồng dạng, không thể nào nắm lên."),t("br"),t("br"),n._v("“Đây là U Hồn trì, nếu như muốn lưu lại ấn ký lời nói, đem máu tươi lưu tại trên tấm bia đá là được!” Hộ vệ một chỉ."),t("br"),t("br"),n._v("Trương Huyền nhìn lại, chỉ thấy U Hồn bên cạnh ao bên trên đứng sừng sững lấy một cái không lớn bệ đá, phía trên điêu khắc có đặc thù hoa văn, tựa như tế tự tế đàn."),t("br"),t("br"),n._v("“Cùng Dị Linh tộc tế đàn có chút giống nhau... Nhưng lại hoàn toàn khác biệt!”"),t("br"),t("br"),n._v("Không biết Linh Thần là ai, nhưng chuyên môn hỏi thăm qua, Thần giới người nào am hiểu tế tự, lấy được kết quả, là U Hồn thiên cường giả. Vốn cho rằng cùng Linh Thần có một loại nào đó liên quan, nhìn thấy trước mắt hoa văn, Trương Huyền nhịn không được lắc đầu."),t("br"),t("br"),n._v("Mặc dù có chút giống nhau, nhưng ở cấu tạo cùng nội dung bên trên, hoàn toàn khác biệt, không thuộc về cùng một loại hệ thống."),t("br"),t("br"),n._v("Xem ra, nơi này cùng Linh Thần, cũng không quan hệ."),t("br"),t("br"),n._v("Đang muốn đi tới bệ đá trước mặt, đem máu tươi rót vào trong đó, lưu lại ấn ký, chỉ thấy trước mắt ao nước một hồi sôi trào, liên tiếp hư ảnh đung đưa."),t("br"),t("br"),n._v("Những này hư ảnh, cái đầu có lớn có nhỏ, cao chừng một trượng lớn nhỏ, thấp, không cao hơn ngón cái, liên tiếp rậm rạp chằng chịt, chừng mấy ngàn vị nhiều, kèm theo ao nước sôi trào, mà không ngừng nhảy lên."),t("br"),t("br"),n._v("Trên không trước đó cảm nhận được cái loại này đặc thù lực lượng tùy ý xuống, như là ánh mặt trời chiếu sáng, cao hư ảnh, trở nên càng thêm ngưng thực, nhỏ không ngừng lay động, nến đồng dạng, sáng tối chập chờn."),t("br"),t("br"),n._v("Ba!"),t("br"),t("br"),n._v("Một cái lớn chừng ngón cái hư ảnh phá vỡ, biến mất ở trước mắt."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?” Trương Huyền nghi hoặc."),t("br"),t("br"),n._v("“Những cái kia lớn hư ảnh, là bởi vì tưởng niệm nhiều người, nguyện lực nhiều, mới hình thành, nhỏ, khả năng chỉ có người nhà bằng hữu, kèm theo thời gian trôi qua, những người này vẫn lạc, không có người nhớ ra, một cách tự nhiên liền dập tắt!”"),t("br"),t("br"),n._v("Hộ vệ nhìn qua, lắc đầu: “Thực ra, hàng năm không ít người tiêu phí lớn đánh đổi ở đây lưu lại ấn ký, hi vọng về sau chết rồi, có thể một lần nữa phục sinh, đáng tiếc là... Người chết như đèn diệt, thật muốn không còn, lại có mấy người có thể ghi nhớ? Không phải thân nhất cốt nhục, nguyện lực rất ít sinh ra. Cho nên, trừ phi đặc biệt có tiền, nếu không, coi như lưu lại ấn ký, cũng đồng dạng không cách nào phục sinh. Nếu không Thần giới người người không chết, còn không đã sớm kín người hết chỗ?”"),t("br"),t("br"),n._v("Trương Huyền rất tán thành."),t("br"),t("br"),n._v("Ân tình, biết ơn, đều là ngắn ngủi, ai có thể ghi nhớ bao lâu?"),t("br"),t("br"),n._v("Như tiền thế rất nhiều hệ thống tác giả đồng dạng, mấy ngày không chương mới, người đọc liền sẽ đem ngươi quên."),t("br"),t("br"),n._v("Không nhớ được, nguyện lực càng ngày càng ít, duy trì đều rất khó làm được, chớ nói chi là ngưng tụ thành hình..."),t("br"),t("br"),n._v("Cho nên, cái gọi là phục sinh, cũng chỉ là một loại trong lòng an ủi mà thôi, toàn bộ Thần giới, không biết bao nhiêu năm rồi, có thể thành công, có thể đếm được trên đầu ngón tay."),t("br"),t("br"),n._v("“Cái kia cao lớn nhất hư ảnh, là ai? Xem ra nhanh ngưng tụ thành hình đi!”"),t("br"),t("br"),n._v("Trong lòng cảm xúc, nhìn về phía trong nước hồ cao nhất cái kia."),t("br"),t("br"),n._v("Lớn chừng ngón cái, không chịu nổi, sớm muộn đều sẽ tiêu tán, nhưng này cái cao to, vượt qua hơn trượng, khẳng định có thể ngưng tụ thành hình, thật không biết là vị nào tiên liệt, có thể có được như vậy tế tự cùng tôn sùng, hội tụ ra loại này hình dạng."),t("br"),t("br"),n._v("“Cụ thể là ai, ta còn thực sự không biết, đại khái hơn bốn mươi năm trước, đột nhiên xuất hiện, thấy không rõ hình dáng, cũng không có đối ứng Thần Linh, cho nên, cho dù là chúng ta cũng không rõ ràng...”"),t("br"),t("br"),n._v("Hộ vệ lắc đầu."),t("br"),t("br"),n._v("“Không biết là người nào, cũng có thể hội tụ nguyện lực?” Trương Huyền sửng sốt."),t("br"),t("br"),n._v("  “Nguyện lực là căn cứ tưởng niệm hắn người, mà xuất hiện, chỉ cần trong lòng có hắn, liền sẽ tồn tại, người ngoài không biết, cũng không kỳ lạ! Dù sao, không ít linh hồn hội tụ hơn ngàn năm... Cũng không phải là tất cả mọi người có thể nhớ!” Hộ vệ nói."),t("br"),t("br"),n._v("Trương Huyền gật đầu, đi vào bệ đá trước mặt, cắn nát đầu ngón tay, một giọt máu tươi bay đi."),t("br"),t("br"),n._v("Vù!"),t("br"),t("br"),n._v("Huyết dịch cùng bệ đá vừa tiếp xúc, lập tức tạo nên sóng năng lượng văn, dọc theo trên bệ đá hoa văn, cấp tốc lan ra, ngay sau đó, Trương Huyền liền cảm thấy mình linh hồn hình như cùng thứ gì nối liền cùng nhau, trong nước bất ngờ toát ra một cái so ngón cái còn muốn nhỏ hư ảnh."),t("br"),t("br"),n._v("“Đây là ấn ký của ta?” Trương Huyền dở khóc dở cười."),t("br"),t("br"),n._v("“Vừa mới hội tụ, liền có như vậy lớn, xem như không tệ...” Hộ vệ liếc mắt nhìn, nói."),t("br"),t("br"),n._v("Người bình thường vừa lưu lại ấn ký, tại U Hồn trì là không cách nào hình thành nguyện lực, trước mắt vị này, vậy mà trực tiếp nắm giữ lớn chừng ngón cái, chứng minh đã có không ít người nghĩ đến hắn."),t("br"),t("br"),n._v("“Chẳng lẽ, vừa mới hội tụ còn có nhỏ hơn?” Trương Huyền hỏi."),t("br"),t("br"),n._v("“Phần lớn đều so cái này còn muốn nhỏ, không ít mắt thường đều không thể thấy được, ta đang làm nhiệm vụ hơn hai mươi năm trong lúc đó, bái kiến lớn nhất chính là là mười năm trước chết đi vị kia Bạch Ích Thần Vương! Tại linh khí thuỷ triều bên trong bị giết, rất nhiều thuộc hạ, đem hắn lưu lại đồ vật, bỏ vào nước trì, lập tức xuất hiện một cái lớn chừng bàn tay hư ảnh!”"),t("br"),t("br"),n._v("Hộ vệ nói: “Khiếp sợ không ít người.”"),t("br"),t("br"),n._v("“Vị này Bạch Ích Thần Vương rất được kính yêu?” Trương Huyền nói."),t("br"),t("br"),n._v("“Đó cũng không phải, xem như Thần Vương, khẳng định có không ít mạnh mẽ thuộc hạ, không nói mặt khác, chỉ những người này, liền có thể sinh ra rất lớn nguyện lực!” Hộ vệ nói."),t("br"),t("br"),n._v("“Chẳng lẽ... Nguyện lực sinh ra, cùng tưởng niệm hắn người, thực lực cường đại hay không cũng có quan hệ?”"),t("br"),n._v("“Tự nhiên có quan hệ, thực lực càng mạnh tuổi thọ càng dài, tưởng niệm có thể thời gian duy trì, tự nhiên cũng liền càng dài, sinh ra nguyện lực, khẳng định cũng liền càng lớn. Tình huống bình thường Thần Linh, Thiên Thần, Thần Vương... Sinh ra nguyện lực, là gấp trăm lần tăng lên!”"),t("br"),t("br"),n._v("Hộ vệ cười cười."),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Tuổi thọ không giống, thực lực khác biệt, sinh ra nguyện lực lại thế nào khả năng đồng dạng?"),t("br"),t("br"),n._v("Ngưu tầm ngưu mã tầm mã, nhân dĩ quần phân, tu vi càng cao người, càng dễ dàng phục sinh, cũng không phải là riêng là danh khí lớn, nhớ nhiều người, càng quan trọng hơn là, tới có quan hệ, đều là cao thủ, bọn họ một người tưởng niệm, liền chống đỡ lên kẻ yếu trên trăm, hơn ngàn... Hiệu suất tự nhiên hoàn toàn khác biệt."),t("br"),t("br"),n._v("“Ngươi cũng xem là không tệ, lớn chừng ngón cái, chứng minh có thể ghi nhớ ngươi, sát nhập sinh nguyện lực, không chỉ có Thần Linh, còn có Thiên Thần cường giả!” Hộ vệ cười cười."),t("br"),t("br"),n._v("Lớn chừng ngón cái, thoạt nhìn không tính là gì, nhưng tưởng niệm vị này người, nhất định xuất hiện Thiên Thần cường giả, nếu không, không có khả năng có cường đại như vậy nguyện lực."),t("br"),t("br"),n._v("“Thiên Thần?” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Tề Linh Nhi, Trương Giáp đều là Thiên Thần, một trung tâm vì chính mình làm việc, một cái khác là bản thân thú sủng, khẳng định đều có thể sinh ra nguyện lực."),t("br"),t("br"),n._v("Cảm nhận được U Hồn trong ao lực lượng cùng mình linh hồn đã nối liền cùng một chỗ, quả thực có thể để cho người sau chậm chạp tiến bộ, Trương Huyền thở phào nhẹ nhõm, lúc này mới ôm quyền rời khỏi."),t("br"),t("br"),n._v("Đi ra đại điện, một cái hộ vệ đi vào, nhìn về phía vị thứ nhất, tò mò hỏi: “Vừa tới vị này, thế nào?”"),t("br"),t("br"),n._v("“Lớn chừng ngón cái mà thôi, đoán chừng không dùng đến nửa năm, liền sẽ tự động tiêu tán!” Cái thứ nhất hộ vệ hừ một tiếng."),t("br"),t("br"),n._v("“Vừa đến có thể kiên trì nửa năm, coi là không tệ...” Vị thứ hai nói."),t("br"),t("br"),n._v("“Là không sai, nhưng người còn chưa có chết, liền muốn phục sinh... Một chút cố gắng, lang bạt tinh thần đều không có, ta đều xem thường, lại thế nào khả năng có người tưởng niệm, khâm phục? Hiện tại là có nguyện lực, đoán chừng là một ít người thân không biết, một khi biết tin tức, khẳng định sẽ lập tức dập tắt!” Vị thứ nhất hộ vệ nói."),t("br"),t("br"),n._v("“Này ngược lại là...” Cái thứ hai hộ vệ thâm dĩ vi nhiên gật đầu."),t("br"),t("br"),n._v("Thần giới cường giả vi tôn, tôn trọng lực lượng, thực lực mạnh mãi mãi cũng lại nhận những người khác khâm phục."),t("br"),t("br"),n._v("Nếu như ngươi là cụ già chi niên ngược lại cũng thôi, chừng hai mươi tuổi thanh niên, còn có rất nhiều tuổi thọ có thể sống, liền sợ chết đem linh hồn ấn ký lưu lại, vừa nhìn liền biết là đồ hèn nhát, làm sao đạt được người khác thành tâm tin tưởng và nghe theo?"),t("br"),t("br"),n._v("Coi như hiện tại có thể sinh ra hư ảnh, không bao lâu cũng sẽ dập tắt!"),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Cảm xúc một tiếng, cái thứ nhất hộ vệ, không nói thêm lời, vừa định rời khỏi, chỉ thấy trước mắt ao nước, đột nhiên đung đưa."),t("br"),t("br"),n._v("Dừng bước, hai người nhìn sang."),t("br"),t("br"),n._v("Chỉ thấy người thanh niên kia lưu lại hư ảnh, không ngừng lay động, kích thước không lớn, lại chấn động ao nước cuồn cuộn không ngớt."),t("br"),t("br"),n._v("“Đây là...”"),t("br"),t("br"),n._v("Nghi ngờ phút chốc, cái thứ nhất hộ vệ giống như là nhớ ra cái gì đó, đồng tử co rụt lại: “Có càng nhiều người đối với hắn tưởng niệm, vọt tới nhiều hơn nữa nguyện lực?”"),t("br"),t("br"),n._v("Xuất hiện loại tình huống này, chỉ có một khả năng, đó chính là, tưởng niệm đối phương nguyện lực, càng ngày cũng nhiều."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Lời còn chưa dứt, lớn chừng ngón cái hư ảnh, chấn động mạnh một cái, biến thành lớn chừng bàn tay, hơn nữa hư ảnh càng thêm ngưng thực, lực lượng thuần chất."),t("br"),t("br"),n._v("“Tuy là tưởng niệm rất nhiều người, nhưng phần lớn đều là Thần Linh cường giả, hiệu quả không lớn...”"),t("br"),t("br"),n._v("Cứ việc không rõ vì sao những này nguyện lực, tại sao lại lâu như vậy mới đi đến, nhưng căn cứ độ tinh thuần cùng nhiều ít đến xem, tưởng niệm người của đối phương, phần lớn đều là Thần Linh cảnh giới, hơn nữa còn đều là ở tại tầng thấp nhất Thần Linh, Thiên Thần đều rất ít, hiệu quả tuy có tiến bộ, nhưng cũng cực kỳ bé nhỏ, hơn nữa thời gian duy trì không lớn, rất nhanh liền có thể tiêu tán."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Đồng dạng lời còn chưa dứt, bàn tay lớn nhỏ hư ảnh, lần nữa điên cuồng bạo tăng, mắt thường có thể thấy được tăng thêm."),t("br"),t("br"),n._v("“Đây là... Thần Vương cường giả tưởng niệm?”"),t("br"),t("br"),n._v("Đồng tử co rụt lại, cái thứ nhất hộ vệ đần độn tại chỗ."),t("br"),t("br"),n._v("Đối phương chỉ là cái hạ phẩm Thiên Thần, làm sao lại nhận được Thần Vương cường giả tưởng niệm?"),t("br"),t("br"),n._v("Hô hô!"),t("br"),t("br"),n._v("Hư ảnh lần nữa tăng thêm."),t("br"),t("br"),n._v("“Lại có hai cái Thần Vương?” Cái thứ hai hộ vệ cũng bối rối."),t("br"),t("br"),n._v("“Ba cái Thần Vương đồng thời đối với hắn một người sinh ra nguyện lực, người này, thật chỉ là hạ phẩm Thiên Thần?” Cái thứ nhất hộ vệ răng cắn chặt không ngừng run rẩy."),t("br"),t("br"),n._v("Vừa nói có tiền đốt, ấn ký này nửa năm không đến khẳng định sẽ biến mất, nằm mơ đều không có suy nghĩ một chút đến, trong chớp mắt, liền có vô số Thần Linh, cùng với ba cái Thần Vương liền đối với hắn sinh ra tưởng niệm."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Hư ảnh lần nữa bạo tăng, trở nên cùng nhân vật thật lớn nhỏ."),t("br"),t("br"),n._v("Hai cái hộ vệ nhìn nhau, thân thể không ngừng run rẩy, giống như là gặp được khó tin chuyện, không biết qua bao lâu, lẩm bẩm âm thanh, theo hầu kết bên trong, chậm chạp phun ra."),t("br"),t("br"),n._v("“Đây là... Phong hào Thần Vương nguyện lực?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2174"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2176"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);