(window.webpackJsonp=window.webpackJsonp||[]).push([[1327],{1682:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2165-chi-can-998"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2165-chi-can-998"}},[n._v("#")]),n._v(" Chương 2165: Chỉ cần 998")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Là Thần giới thứ nhất luyện đan đại tông sư, Phù Giang Thần!”"),t("br"),t("br"),n._v("“Hắn mặc dù là Thần giới danh khí lớn nhất luyện đan sư, vẫn còn không gọi được thứ nhất đi! Nghe nói Thiên Túng Đại Đế thuật luyện đan, có một không hai cổ kim, không ai sánh nổi.”"),t("br"),t("br"),n._v("“Đế Quân cao cao tại thượng, sẽ không cùng người bình thường so, loại trừ bọn họ, vị này tuyệt đối được cho, Thần giới đứng đầu nhất, cho dù không xếp hạng tới thứ nhất, năm vị trí đầu khẳng định cũng có thể sắp xếp lên! Dù sao, chân chính cao nhân, cũng không thích xuất đầu lộ diện...”"),t("br"),t("br"),n._v("“Này ngược lại là, chỉ là... Hắn không phải tại Linh Lung thiên ư? Làm sao đến nơi này?”"),t("br"),t("br"),n._v("Nhận ra Phù Giang Thần thân phận, toàn bộ quảng trường xôn xao."),t("br"),t("br"),n._v("Chỉ Thần giới nổi danh nhất luyện đan sư cái thân phận này, liền có thể để vô số người khâm phục, huống chi còn là Thần Vương cảnh giới cường giả!"),t("br"),t("br"),n._v("Thấy mọi người từng cái tràn đầy tôn sùng, Trương Huyền cho dù không biết trước mắt vị này, cũng biết nhất định không đơn giản, lúc này gật gật đầu: “Tốt!”"),t("br"),t("br"),n._v("Cũng không nhiều lời, Phù Giang Thần vẫy bàn tay lớn một cái, sau lưng một vị thanh niên tung người nhảy lên, rơi vào đài cao."),t("br"),t("br"),n._v("“Đây là đồ tôn của ta, Hàn Cừ! Thượng phẩm Thần Linh đỉnh phong, bởi vì vừa đột phá không lâu, đột phá tỷ lệ, chỉ có ba mươi phần trăm.”"),t("br"),t("br"),n._v("Phù Giang Thần thản nhiên nói."),t("br"),t("br"),n._v("Nếu đối phương đem thử đan cơ hội để lại cho bản thân, tự nhiên muốn giải thích rõ ràng, nếu không, nói hắn tới thông đồng, danh dự của mình, cũng sẽ nhận tổn thương rất nặng."),t("br"),t("br"),n._v("Tiếng nói kết thúc, trên đài Hàn Cừ lập tức bày ra trong cơ thể tu vi, quả nhiên là thượng phẩm Thần Linh đỉnh phong, hơn nữa cũng không tính quá thâm hậu, khoảng cách đột phá, hẳn là còn có rất lớn một khoảng cách."),t("br"),t("br"),n._v("“Phù đan sư danh khắp thiên hạ, không có khả năng giả bộ chứng nhận, chúng ta tin tưởng!”"),t("br"),t("br"),n._v("“Ta cũng tin tưởng...”"),t("br"),t("br"),n._v("“Phù đan sư nhân phẩm, ta sớm có nghe nói...”"),t("br"),t("br"),n._v("Phía dưới lập tức có người hô lên, rất nhanh như như thủy triều, bao phủ toàn bộ quảng trường."),t("br"),t("br"),n._v("Ở đây nhiều người như vậy, người nào đi tới đều sẽ gặp phải nghi ngờ, thậm chí Tề gia phái ra cao thủ, cũng biết để cho người ta cảm thấy không đúng, nhưng trước mắt vị này, lại không người sinh ra loại ý nghĩ này."),t("br"),t("br"),n._v("Không chỉ bởi vì hắn là Thần Vương cường giả, càng quan trọng hơn là, đối phương nổi danh mấy ngàn năm, uy tín khắp thiên hạ, chưa từng nghe qua bất kỳ việc xấu."),t("br"),t("br"),n._v("Hắn phái ra đồ tôn, tự nhiên cũng liền không tồn tại bất kỳ vấn đề gì."),t("br"),t("br"),n._v("“Nếu mọi người tin tưởng, vậy thì bắt đầu đi!” Trương Huyền nói."),t("br"),t("br"),n._v("Vốn còn nghĩ, có thể phải tốn thêm rất lắm lời lưỡi, mới có thể để cho đám người tin tưởng, không nghĩ tới vị này Phù đan sư xuất hiện, trực tiếp hóa giải loại này xấu hổ."),t("br"),t("br"),n._v("Cong ngón tay búng một cái, trong hộp đan dược, hướng thanh niên Hàn Cừ bay đi."),t("br"),t("br"),n._v("Đưa tay nhận lấy, người sau ngẩng đầu đem đan dược nuốt xuống."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Mạnh mẽ linh khí, tràn vào đan điền, thanh niên không dám chần chừ, lập tức khoanh chân ngồi xuống, hóa giải trong đó dược lực."),t("br"),t("br"),n._v("Phía dưới đám người, tất cả đều ngừng thở, không dám nói lời nào, sợ sẽ đánh quấy rầy hắn tu luyện."),t("br"),t("br"),n._v("Kéo dài thời gian không dài, vẫn chưa tới ba phút, đám người liền rõ ràng cảm nhận được, vị này Hàn Cừ lực lượng trong cơ thể, thủy triều đồng dạng hội tụ vào một chỗ, phát ra tựa như thanh âm như sấm, càng ngày càng vang dội, càng ngày càng cao ngang..."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Một tiếng bùng nổ, khí tức cường đại, bay thẳng Thiên Đình, áp chế ở đỉnh đầu ràng buộc, lập tức mở ra."),t("br"),t("br"),n._v("“Cái này... Đột phá?”"),t("br"),t("br"),n._v("“Vẫn chưa tới năm phút đồng hồ...”"),t("br"),t("br"),n._v("Đám người tất cả đều nuốt nước bọt."),t("br"),t("br"),n._v("Thần Vương, Thần giới 99.9999% tu luyện giả cũng không dám suy nghĩ, Thiên Thần cảnh xem như trong cả đời, lớn nhất cửa ải khó."),t("br"),t("br"),n._v("Không ít hơn phẩm Thần Linh cố gắng cả đời, đều khoảng cách đột phá không biết bao xa, trước mắt vị này ngược lại tốt... Không đến năm phút đồng hồ liền thành công!"),t("br"),t("br"),n._v("Thuốc này thật nghịch thiên như vậy?"),t("br"),t("br"),n._v("Đồng tử co rụt lại, Phù Giang Thần hiển nhiên cũng đầy là khó tin."),t("br"),t("br"),n._v("Có thể tăng tiến Thần Linh tấn cấp Thiên Thần tỷ lệ Phá Chướng đan, hắn cũng biết luyện chế, nhưng lớn nhất công hiệu, không cao hơn hai mươi phần trăm, hơn nữa, nhiều nhất chỉ có thể dùng một viên!"),t("br"),t("br"),n._v("Dựa theo hắn bình thường phỏng đoán, đối phương dược vật coi như có thể tăng thêm năm mươi phần trăm tỷ lệ, chiếu theo cái này đồ tôn năng lực, muốn thành công, cũng cần ít nhất mười mấy cái chân khí đại chu thiên, nói cách khác ít nhất bảy, tám canh giờ tu luyện mới có thể làm đến!"),t("br"),t("br"),n._v("Năm phút đồng hồ liền hoàn thành..."),t("br"),t("br"),n._v("Cái này Phá Chướng đan, so với trong tưởng tượng còn muốn đáng sợ, còn cường đại hơn!"),t("br"),t("br"),n._v("Nhịn không được nhìn về phía đồ tôn: “Cảm giác làm sao?”"),t("br"),t("br"),n._v("Giờ phút này Hàn Cừ vừa mới vững chắc trong cơ thể đột phá mà tàn phá bừa bãi chân khí, đứng dậy hai mắt thả ra tinh quang: “So... Trong tưởng tượng càng thêm lợi hại, để cho ta thành công đột phá tỷ lệ, tăng lên ít nhất sáu mươi phần trăm trở lên!”"),t("br"),t("br"),n._v("Không dùng đan dược, hắn xung kích tỷ lệ thành công rất nhỏ, nói là ba mươi phần trăm, thực ra đã là cất nhắc, giờ phút này... Nhẹ nhõm đột phá, không có một chút ràng buộc, dược vật này công hiệu, đối với hắn mà nói, đâu chỉ năm mươi phần trăm... Sáu mươi phần trăm, bảy mươi, thậm chí hoàn toàn đều có!"),t("br"),t("br"),n._v("“Thật có mạnh như vậy công hiệu?” Phù Giang Thần cũng không dám tin tưởng."),t("br"),t("br"),n._v("“Đồ tôn không dám có nửa câu nói dối, như có nói láo, tình nguyện đời này kiếp này, lại không cách nào luyện đan, ruột xuyên bụng nát mà chết...” Hàn Cừ một mặt nghiêm mặt."),t("br"),t("br"),n._v("Nghe được hắn phát xuống loại độc này thề, trước mọi người còn có nghi ngờ, cũng lại nói không ra lời."),t("br"),t("br"),n._v("“Đã có vị này Hàn Cừ tiểu hữu ở trước mặt chứng thực, Phá Chướng đan công hiệu, chắc hẳn chư vị cũng đều thấy được...”"),t("br"),t("br"),n._v("Thấy mọi người nói không ra lời, Trương Huyền biết đã tạo thành mãnh liệt rung động, lúc này mở miệng: “Hiện tại ta liền tuyên bố, loại đan dược này giá cả! Cùng với số lượng.”"),t("br"),t("br"),n._v("Đám người đồng loạt nhìn qua."),t("br"),t("br"),n._v("Dược vật mạnh mẽ hay không, là để cho người ta khiếp sợ, nhưng giá cả mới là căn bản nhất."),t("br"),n._v("Trương Huyền nói: “Có thể loại bỏ trong tu luyện ràng buộc, viên đan dược này giá cả, tự nhiên muốn sánh bằng cho đan quý hơn. Mỹ Dung đan giá bán lẻ vì 1500 thần tệ, cái này đan dược vừa vặn là hắn 10 lần, cũng chính là... 15000 thần tệ!”"),t("br"),t("br"),n._v("“15000 thần tệ?”"),t("br"),t("br"),n._v("“Đây là đoạt đi...”"),t("br"),t("br"),n._v("“Quá đắt, có ít người tiêu phí cả đời tích góp, cũng mua không được...”"),t("br"),t("br"),n._v("“Tuy là đắt, nhưng nếu thật là vừa mới cái loại này công hiệu, đáng giá...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Phía dưới tất cả mọi người lộ ra phức tạp biểu lộ."),t("br"),t("br"),n._v("Một vạn năm ngàn thần tệ, tại U Hồn hoàng thành ngũ hoàn phía trong, đều có thể mua nhà... Chỉ mua một viên thuốc..."),t("br"),t("br"),n._v("Bất quá, thượng phẩm Thần Linh cùng hạ phẩm Thiên Thần, cách nhau tuy chỉ có một đường, lại cách xa nhau thiên địa, viên đan dược này thật có thể để hắn đột phá, tiền không phải vấn đề."),t("br"),t("br"),n._v("Bởi vì... Tuổi thọ cùng địa vị, hoàn toàn khác biệt, tiền tài cũng liền không còn ý nghĩa."),t("br"),t("br"),n._v("“Có phải hay không cảm thấy quá đắt?” Nhìn thấy đám người biểu lộ, Trương Huyền khẽ mỉm cười: “Ta cũng cảm thấy quá đắt, ngày hôm nay ngày đầu tiên bán ra, cái giá tiền này lời nói, khẳng định sẽ để cho không ít người chùn bước, bởi vậy... Không được một vạn năm, không được một vạn hai, thậm chí liền một vạn cũng không cần! Chỉ cần 998, chỉ cần 998! Chỉ cần tiêu phí 9,980 thần tệ, liền có thể mua sắm một viên, không đến một vạn giá cả, không mua được chịu thiệt, cũng không mua được mắc lừa, mua là đột phá, mua là an tâm, mua là thực lực, mua là hoàn toàn khác biệt địa vị...”"),t("br"),t("br"),n._v("“9980?”"),t("br"),t("br"),n._v("“Không đến một vạn...”"),t("br"),t("br"),n._v("“Quá tốt rồi, thoáng cái ít đi một phần ba!”"),t("br"),t("br"),n._v("Tất cả mọi người con mắt trong nháy mắt hừng hực."),t("br"),t("br"),n._v("9,980, đối với một viên đan dược tới nói, cứ việc vẫn như cũ rất đắt, có thể cùng trước đó một vạn năm so sánh kém quá nhiều, liền một vạn cũng chưa tới, liền có một lần đột phá Thiên Thần cơ hội, sao lại không làm?"),t("br"),t("br"),n._v("Chỉ cần có tiền, thằng ngốc mới không mua!"),t("br"),t("br"),n._v("“Ta muốn...”"),t("br"),t("br"),n._v("“Trương đan sư, cho ta tới bên trên hai cái!”"),t("br"),t("br"),n._v("“Ta muốn bốn cái...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Quần tình kích động."),t("br"),t("br"),n._v("“Mọi người bình tĩnh đừng nóng...”"),t("br"),t("br"),n._v("Thấy mục đích đi đến, kiếp trước TV mua sắm quả nhiên có tẩy não tác dụng, Trương Huyền thở phào nhẹ nhõm, nói tiếp: “Không phải ta không muốn bán, mà là loại đan dược này luyện chế thực sự quá phức tạp đi, nhận hạn chế tài liệu cùng tinh lực, có thể bán ra không nhiều, không có khả năng làm cho tất cả mọi người đều thỏa mãn! Ngày hôm nay, chỉ có một trăm viên bán ra, bán hết mới thôi, lần sau có hay không, hoặc là lúc nào đem bán, cũng không rõ ràng!”"),t("br"),t("br"),n._v("Hunger marketing!"),t("br"),t("br"),n._v("Coi như làm ra loại này Phá Chướng đan, đối với hắn mà nói, không tính tốn công, nhưng cũng không có khả năng một hơi lấy ra hết, nếu không, cũng coi như không hơn trân quý, không thể để cho càng nhiều người nhớ, sinh ra liên tục không ngừng nguyện lực."),t("br"),t("br"),n._v("“Chỉ có một trăm viên, mau trở về để người nhà chuẩn bị tiền... Làm sao chuẩn bị? Còn không mau đem nhà bán! Chỉ cần ta đột phá Thiên Thần, nhà về sau còn không phải muốn bao nhiêu, có bao nhiêu?”"),t("br"),t("br"),n._v("“Nhà không đủ mua? Ngươi nhìn ta đáng giá không...”"),t("br"),t("br"),n._v("“Coi như đập nồi bán sắt, cũng cần mua lên một viên...”"),t("br"),t("br"),n._v("“Đều chớ giành với ta, người nào cướp, ta giết người nào...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tất cả mọi người điên rồi, từng cái nghiến răng, kích động không kềm chế được."),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, qua nửa ngày, Phù Giang Thần mới đưa miệng khép lại."),t("br"),t("br"),n._v("Hắn là Thần giới nổi danh nhất luyện đan sư, luyện chế ra tới đan dược, vạn kim khó cầu, không ít người trước thời hạn dùng tiền, chỉ vì có thể có được bản thân tự tay luyện chế dược vật..."),t("br"),t("br"),n._v("Có thể cùng trước mắt cái này so, vẫn là kém chút."),t("br"),t("br"),n._v("Hiện tại đan dược còn không có đem bán, cũng đã cướp sắp đánh nhau!"),t("br"),t("br"),n._v("“Chư vị yên tĩnh!”"),t("br"),t("br"),n._v("Thấy đại thế đã xác định được, Trương Huyền gật đầu, nói: “Làm sao mua sắm, các vị không cần phải gấp, đợi cái này buổi trình diễn thời trang kết thúc, đi tới Tề Linh Nhi ở đâu báo danh là được, trước báo danh một trăm vị, đem có tư cách mua sắm đan dược! Mặt khác, Mỹ Dung đan, ngày hôm nay cũng hạ giá, đồng dạng không cần 1500, chỉ cần 998, liền có thể mua sắm, số lượng vì ba ngàn viên, mua trước trước được, bỏ lỡ ngày hôm nay, đem khôi phục giá gốc!”"),t("br"),t("br"),n._v("Phía dưới lần nữa kích động."),t("br"),t("br"),n._v("Trước đó không có mua bên trên Mỹ Dung đan, tất cả đều hưng phấn mà vẻ mặt đỏ lên."),t("br"),t("br"),n._v("“Phá Chướng đan 9980 thần tệ, Mỹ Dung đan 998 thần tệ, ta biết hai loại thuốc giá cả, không ít Thần Linh bằng hữu, coi như cố gắng cả đời, cũng mua không nổi, có điều, không cần lo lắng, cái này dính dáng, ta hôm nay muốn nói cuối cùng một loại đan dược.”"),t("br"),t("br"),n._v("Trương Huyền vẫy vẫy tay, Tề Linh Nhi lấy ra một cái bình ngọc, từ bên trong đổ ra một viên mấy cái viên thuốc, rải rác tại lòng bàn tay, thoạt nhìn vô cùng mộc mạc."),t("br"),t("br"),n._v("“Đây không phải là... Tu luyện dùng Thần Nguyên hoàn ư?”"),t("br"),t("br"),n._v("“Một viên thần tệ có thể mua sắm mười cái, tiện nghi một nhóm...”"),t("br"),t("br"),n._v("“Thoạt nhìn đồng dạng, khẳng định không phải...”"),t("br"),t("br"),n._v("Đám người lần nữa đem ánh mắt tập trung tới."),t("br"),t("br"),n._v("Trương Huyền nói: “Cái này gọi ! Thoạt nhìn cùng Thần Nguyên hoàn giống nhau, nhưng lại có hoàn toàn khác biệt công hiệu, không chỉ có thể giúp người khôi phục trong cơ thể tiêu hao lực lượng, còn có thể hội tụ tinh thần để cho người ta thần thanh khí minh, tu luyện càng thêm nhanh chóng! Bất quá, chỉ có thể nhằm vào Thần Linh cùng với Thần Linh trở xuống tu luyện giả, giá cả... 9. 98 một viên, còn chưa đủ 10 viên thần tệ!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2179"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2181"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);