(window.webpackJsonp=window.webpackJsonp||[]).push([[133],{485:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1096-hua-hoi-pho"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1096-hua-hoi-pho"}},[n._v("#")]),n._v(" Chương 1096: Hứa hội phó")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Trung phẩm Thánh khí?” Thằng nhóc giật nảy mình."),i("br"),i("br"),n._v("Thánh khí, bình thường là cường giả Thánh vực mới có thể có, đạt tới trung phẩm, càng là mười phần hiếm thấy, cho dù Thanh Nguyên đế quốc Hoàng đế Thương Mang kiếm, cũng chỉ là loại này cấp bậc, chỉ cần có một cái xuất thế, đều sẽ dẫn tới vô số cường giả tranh đoạt."),i("br"),i("br"),n._v("Vốn hắn thấy đối phương tuổi trẻ, có tối đa nhất cái tuyệt phẩm Linh khí là tốt lắm rồi, nằm mơ đều không có nghĩ đến, nắm giữ loại pháp bảo cấp bậc này."),i("br"),i("br"),n._v("“Vị thiếu gia này... Là danh sư a?” Nhịn không được nhìn lại."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền gật đầu, có chút kỳ quái: “Ngươi làm sao biết được?”"),i("br"),i("br"),n._v("Hắn cũng không xuyên danh sư đặc thù trang phục, cũng không toả ra liên quan tới danh sư bất kỳ khí tức gì, đối phương làm sao biết?"),i("br"),i("br"),n._v("“Nếu như không phải danh sư, nắm giữ loại cấp bậc này Thánh khí, khẳng định sớm đã có người ra tay tranh đoạt...” Thằng nhóc cười khổ."),i("br"),i("br"),n._v("Trung phẩm Thánh khí, toàn bộ Thanh Nguyên đế quốc đều là bảo vật siêu đẳng, một khi rò rỉ tin tức, rất nhiều người tất nhiên muốn có được, há lại một cái trẻ tuổi như vậy tu luyện giả, có thể thủ hộ được?"),i("br"),i("br"),n._v("Trừ phi... Là danh sư!"),i("br"),i("br"),n._v("Có danh sư đường bảo hộ, vẫn chưa có người nào dám loạn đánh danh sư chủ ý."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền nhẹ gật đầu, hiểu được."),i("br"),i("br"),n._v("Chuyện này, hắn cũng hiểu."),i("br"),i("br"),n._v("Nếu không phải danh sư, dọc theo con đường này ngấp nghé hắn tốc độ tu luyện cùng công pháp không ít người, phiền phức đã sớm theo nhau mà đến, sao có thể dễ dàng như vậy liền đến đến nơi đây."),i("br"),i("br"),n._v("Danh sư không riêng gì một loại trách nhiệm, càng là một cái cực lớn ô dù."),i("br"),i("br"),n._v("“Trung phẩm Thánh khí, cần thất tinh Khải Linh sư, mới có thể đánh thức trong đó linh trí, toàn bộ công hội đạt tới loại cấp bậc này, tổng cộng chỉ có ba vị, theo thứ tự là hội trưởng cùng hai vị phó hội trưởng, bọn họ vị trí tương đối vắng vẻ... Ta mang ngươi tiến đến xin...”"),i("br"),i("br"),n._v("Biết đối phương là danh sư, thằng nhóc không tại vấn đề này tiếp tục dây dưa nói."),i("br"),i("br"),n._v("Trương Huyền đáp một tiếng, có chút kỳ quái: “Khải Linh sư cũng cùng những nghề nghiệp khác đồng dạng, chia làm rất nhiều đẳng cấp?”"),i("br"),i("br"),n._v("“Cũng chia đẳng cấp... Chẳng qua thấp nhất là tứ tinh!” Thằng nhóc giải thích nói."),i("br"),i("br"),n._v("“Thấp nhất tứ tinh?” Trương Huyền không hiểu."),i("br"),i("br"),n._v("“Những nghề nghiệp khác trên cơ bản đều là theo nhất tinh đến cửu tinh, mà Khải Linh sư không giống, học đồ kết thúc, chỉ cần khảo hạch liền là tứ tinh...”"),i("br"),i("br"),n._v("Thằng nhóc nói: “Thật ra thì đây là vì phối hợp những nghề nghiệp khác mới có xưng hô, trên thực tế, bọn họ chân chính cấp bậc là sơ cấp, trung cấp cùng cao cấp! Sơ cấp đối ứng tứ tinh, ngũ tinh, trung cấp đối ứng lục tinh, thất tinh, cao cấp đối ứng bát tinh, cửu tinh! Nhưng phân chia như vậy lời nói, liền cùng những nghề nghiệp khác cấp bậc chia lìa, ngược lại không tốt.”"),i("br"),i("br"),n._v("“Ah!” Suy tư một chút, Trương Huyền hiểu được."),i("br"),i("br"),n._v("Liên lụy đến linh tính, linh hồn, đã nói lên tu vi ít nhất phải đạt tới Hóa Phàm ngũ trọng Hợp Linh cảnh, tu vi như thế, đã đạt đến tứ tinh đỉnh phong, nửa bước ngũ tinh danh sư cấp bậc."),i("br"),i("br"),n._v("Nếu như lại từ nhất tinh bắt đầu sắp xếp lời nói, cùng những nghề nghiệp khác liền hoàn toàn không có cách nào móc nối, tự nhiên muốn theo tứ tinh bắt đầu."),i("br"),i("br"),n._v("Trên đời này đủ loại chức nghiệp không biết bao nhiêu, thiên hình vạn trạng quy định cũng nhiều vô số kể."),i("br"),i("br"),n._v("Thật giống như trước đó nghe nói qua Thiên Cơ sư, thôi diễn thiên cơ, xác định họa phúc, cần đối với thiên địa đại đạo, sớm tối họa phúc có chút lý giải mới được, tu vi thấp nhất đều muốn đạt tới Thánh vực."),i("br"),i("br"),n._v("Ngay từ đầu cấp bậc tự nhiên cao hơn, thậm chí trực tiếp liền là thất tinh."),i("br"),i("br"),n._v("Đối phương trong miệng thất tinh Khải Linh sư, trên thực tế chính là cái gọi là trung cấp Khải Linh sư, tu vi chỉ sợ thấp nhất, đều muốn đạt đến Thánh vực nhị trọng."),i("br"),i("br"),n._v("Đi theo thằng nhóc sau lưng, đi vào đại điện."),i("br"),i("br"),n._v("Vừa đi vừa tiến hành hỏi thăm, rất nhanh, đối cái này công hội cũng hiểu chút đỉnh."),i("br"),i("br"),n._v("Công hội hội trưởng cùng phó hội trưởng, đều là đã đạt tới Nguyên Thần cảnh cường giả, bản thân tu vi tại toàn bộ Thanh Nguyên Đế đô, đều được cho đỉnh tiêm."),i("br"),i("br"),n._v("Đại điện rộng lớn, nhìn một cái, không ít tu luyện giả, xếp hàng chờ đợi Khải Linh sư hỗ trợ rèn luyện hồn phách, tăng lên thực lực của mình."),i("br"),i("br"),n._v("“Ta đến bây giờ đều bước vào không được Hợp Linh cảnh, từ đầu đến cuối cảm giác không thấy linh hồn, mong rằng có thể có được chỉ điểm...”"),i("br"),i("br"),n._v("Một cái Trọc Thanh cảnh tu luyện giả đi tới tưởng là thanh niên trước mặt."),i("br"),i("br"),n._v("“Ngồi xuống đi!”"),i("br"),i("br"),n._v("Thanh niên nhàn nhạt nhẹ gật đầu, chỉ về phía trước."),i("br"),i("br"),n._v("Trọc Thanh cảnh tu luyện giả vội vàng ngồi tại cách đó không xa trên sân khấu."),i("br"),i("br"),n._v("Đợi hắn vào chỗ, thanh niên lông mày nâng lên, bàn tay trên mặt đất nhấn một cái, một đạo quang mang lập tức lan tràn đi qua, hình thành một cái vòng sáng đem hắn bao phủ."),i("br"),i("br"),n._v("“Thể xác tinh thần thả lỏng...”"),i("br"),i("br"),n._v("Nhàn nhạt lời nói, chậm rãi vang lên, thanh niên tựa như mang theo một loại đặc thù ma lực cùng mê hoặc."),i("br"),i("br"),n._v("Trọc Thanh cảnh tu luyện giả kìm lòng không được nhắm mắt lại, giống như là chìm vào đặc thù nào đó ý cảnh."),i("br"),i("br"),n._v("“Đây là... Kích thích linh hồn? Để hắn quan sát được hồn phách lực lượng, thuận tiện đột phá? Lợi hại...”"),i("br"),i("br"),n._v("Đem một màn này nhìn ở trong mắt, Trương Huyền ánh mắt sáng lên."),i("br"),i("br"),n._v("Trước đó nghe được giới thiệu, còn tưởng rằng cái gọi là Khải Linh sư, chỉ là một loại kích thích linh hồn thủ đoạn đặc thù, hiện tại xem ra, cũng không phải là đơn giản như vậy."),i("br"),i("br"),n._v("Cái nghề nghiệp này cùng vu hồn sư có chút tương tự, người sau, là thông qua thủ đoạn đặc thù tu luyện bản thân, trước người, thì là thông qua kỹ xảo tăng cường người khác."),i("br"),i("br"),n._v("Lấy vị này Trọc Thanh cảnh tu luyện giả làm thí dụ, lúc trước hắn cảm ngộ không đến hồn phách, cũng liền không cách nào tiến vào Hợp Linh cảnh, nhưng bị trước mắt vị này kích thích, linh hồn trở nên mạnh mẽ, liền có thể một cách tự nhiên vận dụng trong đó lực lượng, khoảng cách đột phá cũng không xa."),i("br"),i("br"),n._v("Ầm ầm!"),i("br"),i("br"),n._v("Đang tại suy tư, quả nhiên thấy trước mắt vị này Trọc Thanh cảnh cường giả, hét dài một tiếng, trên thân khí tức nhanh chóng gia tăng, đã thăm dò linh hồn, đột phá Hóa Phàm tứ trọng."),i("br"),n._v("“Khó trách, người người đều muốn đi cao đẳng đế quốc, gặp gỡ quả nhiên cao hơn nhiều...”"),i("br"),i("br"),n._v("Nhìn thấy vị này theo Khải Linh sư bắt đầu khải linh đến đột phá, liền vài phút công phu đều không có, Trương Huyền nhịn không được cảm khái."),i("br"),i("br"),n._v("Vạn Quốc liên minh những địa phương này, Hợp Linh cảnh là lớn nhất khảm, vô số người cố gắng cả đời, đều khó mà thành công, mà ở đây, nhưng đơn giản như vậy, cái nghề nghiệp này, quả nhiên không tầm thường."),i("br"),i("br"),n._v("Bất quá, cứ việc tất cả mọi người biết, cao đẳng đế quốc kỳ ngộ nhiều, cũng không phải bất luận kẻ nào đều có thể đi tới."),i("br"),i("br"),n._v("Không nói trước theo Vạn Quốc liên minh tới đây khoảng cách, một đường không biết có bao nhiêu nguy hiểm, chỉ nói lần này khải linh giá cả cũng không phải ai có thể tiếp nhận lên... Trọn vẹn một cái linh thạch thượng phẩm!"),i("br"),i("br"),n._v("Giá cả cỡ này, cho dù vương quốc liên minh minh chủ phủ, đều không bỏ ra nổi tới."),i("br"),i("br"),n._v("Lại thêm đủ loại điều kiện hạn chế... Thật giống như kiếp trước địch bái, người người đều biết nơi này kiếm tiền dễ dàng, nhưng... Vô dụng, không đạt tới cấp bậc nhất định, tiền nhiều hơn nữa, cũng không kiếm được."),i("br"),i("br"),n._v("“Được rồi, hôm nay danh ngạch đến, ta muốn nghỉ ngơi, muốn tới tìm ta, mười ngày sau lại đến đi!”"),i("br"),i("br"),n._v("Trước mắt vị này Trọc Thanh cảnh tu luyện giả đột phá, thanh niên vẻ mặt cũng biến thành trắng bệch, tựa như hư thoát."),i("br"),i("br"),n._v("“Khải Linh sư khải linh một lần, đối với mình linh hồn tiêu hao rất nhiều, một lần nhiều nhất ba người, ít nhất phải nghỉ ngơi mười ngày!” Sợ trước mắt vị thiếu gia này không hiểu, thằng nhóc giải thích nói."),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Hắn là vu hồn sư, đối hồn phách giải rất nhiều, tự nhiên cũng có thể nhìn ra khải linh độ khó, nghỉ ngơi mười ngày, đã coi như là khôi phục rất nhanh."),i("br"),i("br"),n._v("Tiếp tục tiến lên, nhìn thấy có Khải Linh sư giúp tu luyện giả rèn luyện linh hồn, cũng có tại giúp vũ khí khải linh, vì có thể có được khải linh danh ngạch, rất nhiều người thậm chí giá cao mua sắm xếp hàng trình tự."),i("br"),i("br"),n._v("“Quả nhiên kiếm tiền...”"),i("br"),i("br"),n._v("Nhìn một hồi, Trương Huyền nhịn không được cảm khái."),i("br"),i("br"),n._v("Cái nghề nghiệp này quả thực đủ kiếm tiền."),i("br"),i("br"),n._v("Linh hồn cùng thân thể, chân khí tu luyện khác biệt, thân thể, chân khí thấy được sờ được, tu luyện càng thêm dễ dàng, hồn phách không giống, đơn độc tu luyện khá là phiền toái, nhưng dựa vào Khải Linh sư, có thể làm cho hồn phách nhanh chóng trở nên mạnh mẽ, so đơn độc tu luyện phải nhanh hơn gấp bội, còn nữa, lợi hại vũ khí, để trong đó linh tính thần phục bản thân, cần tiêu tốn không biết bao lâu, có ít người thật nhiều năm đều làm không được, cùng hắn phiền phức, không bằng trực tiếp gạt bỏ, sau đó mượn nhờ Khải Linh sư tay, lần nữa đổi một cái nghe lời."),i("br"),i("br"),n._v("Chỉ hai thứ này, cũng đủ để cho cái nghề nghiệp này nóng nảy tới cực điểm."),i("br"),i("br"),n._v("“Phía trước liền là Hứa hội phó khải linh địa phương, nàng khải linh một lần, giá cả cực cao, nhất là đạt tới trung phẩm cấp bậc Thánh khí...”"),i("br"),i("br"),n._v("Vòng qua đám người, đi tới một cái rộng lớn bên ngoài phòng, thằng nhóc ngừng lại."),i("br"),i("br"),n._v("“Cần bao nhiêu linh thạch?” Trương Huyền biết chắc không rẻ, không nói nhảm quá nhiều, trực tiếp hỏi."),i("br"),i("br"),n._v("“100 linh thạch thượng phẩm!” Thằng nhóc nói."),i("br"),i("br"),n._v("“Không quý!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("Chỉ cần có thể cứu sống Kim Nguyên đỉnh, đừng nói một trăm linh thạch thượng phẩm, cho dù một vạn, hắn cũng sẽ cam tâm tình nguyện lấy ra."),i("br"),i("br"),n._v("Soạt!"),i("br"),i("br"),n._v("Cổ tay khẽ đảo, lấy ra một trăm linh thạch thượng phẩm đưa tới."),i("br"),i("br"),n._v("“Ngươi ở đây sau đó...”"),i("br"),i("br"),n._v("Thằng nhóc đi vào gian phòng, thời gian không dài, lần nữa đi tới trước mặt, trên mặt vui mừng: “Thiếu gia, số ngươi cũng may, Hứa hội phó hôm nay vừa vặn còn thừa lại cái cuối cùng danh ngạch, chúng ta đi vào đi!”"),i("br"),i("br"),n._v("Trương Huyền theo sau lưng, đi vào."),i("br"),i("br"),n._v("Gian phòng chừng hơn trăm mét vuông lớn nhỏ, ở giữa nhất một cái mỹ phụ ngồi ở trong đó, thoạt nhìn hơn ba mươi tuổi bộ dáng, mặc dù không có thi triển lực lượng, nhưng nhất cử nhất động kèm theo khí tức, cùng trước đó Ngô sư đồng dạng, đã đạt đến Thánh vực tứ trọng Nguyên Thần cảnh."),i("br"),i("br"),n._v("“Hứa hội phó, đây chính là tới muốn cho trung phẩm Thánh khí khôi phục linh tính vị danh sư kia!” Thằng nhóc giới thiệu."),i("br"),i("br"),n._v("“Ừm!” Hứa hội phó nhẹ gật đầu, nhìn Trương Huyền một cái, mang theo khó hiểu: “Ngươi có trung phẩm cấp bậc Thánh khí?”"),i("br"),i("br"),n._v("Trước mắt vị này, chỉ là cái Thánh vực nhất trọng đỉnh phong, liền xem như danh sư, cũng không tư cách nắm giữ cường đại như thế vũ khí đi."),i("br"),i("br"),n._v("“Đúng!” Trương Huyền cổ tay khẽ đảo, đem Kim Nguyên đỉnh để dưới đất."),i("br"),i("br"),n._v("“Ta kiện binh khí này, ý thức rơi vào trạng thái ngủ say, mong rằng Hứa hội phó có thể hỗ trợ đánh thức...”"),i("br"),i("br"),n._v("“Ngủ say?”"),i("br"),i("br"),n._v("Hứa hội phó đứng dậy đi tới Kim Nguyên đỉnh trước mặt, nhìn kỹ một vòng, mày nhíu lại thành viên, suy tư một lát, nhìn lại."),i("br"),i("br"),n._v("“Ý niệm ngủ say... Ngươi là tính toán lần nữa sinh ra một cái mới ý niệm, thay thế đối phương, vẫn là để ngủ say thức tỉnh?”"),i("br"),i("br"),n._v("Để ngủ say vũ khí, khôi phục linh tính, có hai loại phương pháp, thứ nhất, lần nữa sinh ra một cái mới ý niệm. Thứ hai, đem trong ngủ mê đánh thức."),i("br"),i("br"),n._v("“Đương nhiên là đánh thức!” Trương Huyền vội nói."),i("br"),i("br"),n._v("Sinh ra mới ý niệm chẳng khác nào đem Kim Nguyên đỉnh gạt bỏ, đây là hắn sẽ không đi làm."),i("br"),i("br"),n._v("“Cái này có chút khó khăn...” Hứa hội phó lắc đầu, đang nghĩ tiếp tục nói chuyện, liền nghe bên ngoài vang lên tiếng đập cửa, ngay sau đó lại một cái thằng nhóc đi đến."),i("br"),i("br"),n._v("“Hứa hội trưởng, Đạo Khấu tiền bối, muốn để ngươi giúp hắn đệ Tử Khải Linh Thai anh, xung kích Nguyên Thần cảnh!”"),i("br"),i("br"),n._v("“Đạo Khấu?”"),i("br"),i("br"),n._v("Hứa hội phó lắc đầu: “Ngươi nói cho hắn biết, ta hôm nay không có danh ngạch...”"),i("br"),i("br"),n._v("Lời còn chưa dứt, một cái lão giả đẩy cửa đi đến, phía sau hắn đi theo một người trung niên."),i("br"),i("br"),n._v("“Không danh ngạch? Ta nghe nói, ngươi hôm nay mới khải linh hai người, không phải còn có cái cuối cùng ư?”"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1104"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1106"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);