(window.webpackJsonp=window.webpackJsonp||[]).push([[1332],{1687:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2170-y-đao-ty-thi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2170-y-đao-ty-thi"}},[n._v("#")]),n._v(" Chương 2170: Y đạo tỷ thí")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Y Sư công hội khoảng cách chỗ ở cực xa, hai người ngồi thần thú phi hành hơn nửa canh giờ mới đi đến trước mặt, cùng trước đó Danh Sư đường đồng dạng, kiến trúc cũng không hùng vĩ, cũng không huy hoàng, cho người ta một loại phòng khám bệnh tư nhân hình dáng."),t("br"),t("br"),n._v("Tiến vào bên trong, chỉ thấy đại điện phía trong chất đầy người, cả đám đều ăn mặc thống nhất trường bào, vẻ mặt vội vàng."),t("br"),t("br"),n._v("“Uông Thanh y sư, Nhạc Tuyền y sư, Thẩm Tử Mộc y sư...”"),t("br"),t("br"),n._v("Hướng người trong đại điện nhìn lại, Tề Linh Nhi rất nhanh nhận ra một số người đến, tràn đầy kỳ lạ."),t("br"),t("br"),n._v("“Những người này rất nổi danh?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Xem như toàn bộ U Hồn hoàng thành lợi hại nhất y sư! Bình thường, đều có từng người sự tình, cho dù cùng ở tại hoàng thành, cũng là cả đời không qua lại với nhau, làm sao đều hội tụ ở chỗ này?” Tề Linh Nhi không hiểu."),t("br"),t("br"),n._v("Uông Thanh y sư, là bọn họ Tề gia ngự dụng y sư, chuyên môn vì Tề gia cao tầng chữa bệnh; Nhạc Tuyền là Bắc Đường gia tộc ngự dụng y sư, còn Thẩm Tử Mộc, xem như đứng ở Thân Đồ gia tộc trận doanh."),t("br"),t("br"),n._v("Tam đại gia tộc bên ngoài bình an vô sự, sau lưng, phân cao thấp không ít, bình thường coi như Y Sư công hội tổ chức toàn viên đại hội, cũng đều là tránh được nên tránh, không thể nào gặp mặt, lúc này lại xuất hiện cùng một chỗ, không kinh ngạc đó là giả."),t("br"),t("br"),n._v("“Tìm người hỏi một chút chẳng phải sẽ biết!”"),t("br"),t("br"),n._v("Trương Huyền khẽ mỉm cười, tìm tới một vị y sư, tiện tay đưa ra mười cái thần tệ: “Đây là... Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Là một vị người trẻ tuổi, chừng ba mươi tuổi hình dáng, thấy trước mắt vị này như thế hào phóng, không có gì giấu diếm, nói: “Là !”"),t("br"),t("br"),n._v("“Y đạo tỷ thí?” Trương Huyền nghi hoặc."),t("br"),t("br"),n._v("Có tỷ thí luyện đan, tỷ thí luyện khí, y đạo cũng có thể tỷ thí?"),t("br"),t("br"),n._v("Luyện đan, người nào thời gian ngắn, dùng thuốc ít, luyện chế đan dược thành phẩm cao, người nào liền có thể chiến thắng; Luyện khí người nào luyện chế vũ khí mạnh hơn một bậc, người nào thì càng cao minh... Y đạo làm sao so?"),t("br"),t("br"),n._v("Cũng không thể, tại trên người bệnh nhân thí nghiệm thuốc đi!"),t("br"),t("br"),n._v("“Ngươi là người ngoài không biết rất bình thường, y đạo tỷ thí là y sư ở giữa giao lưu kỹ nghệ trực tiếp nhất phương pháp, trải qua chẩn bệnh, trị liệu mấy người mấy cái quá trình, để phán đoán một người y thuật, có hay không càng cao minh hơn...” Trẻ tuổi y sư giải thích nói."),t("br"),t("br"),n._v("Những nghề nghiệp khác có thể phân biệt ra được mạnh yếu, y sư tự nhiên cũng có thể."),t("br"),t("br"),n._v("Đơn thuần chờ ngươi trị không được chứng bệnh, ta lại ra tay, phải chờ tới năm nào tháng nào?"),t("br"),t("br"),n._v("Cho nên, rất nhiều y sư vì cấp tốc nổi danh, đều sẽ tham gia loại này y đạo tỷ thí, có thể trổ hết tài năng, ai cũng sẽ thừa nhận y thuật của ngươi cao minh."),t("br"),t("br"),n._v("“Chẩn bệnh, trị liệu... Muốn có bệnh nhân a, chẳng lẽ, loại này tỷ thí, còn cần sớm chuẩn bị một ít, thân có có, nghi vấn khó xử lý tạp chứng người bệnh? Hơn nữa muốn cùng nhau chờ lấy?” Trương Huyền kỳ lạ."),t("br"),t("br"),n._v("Thân là y sư, đối phương nói những cái kia hắn có thể rõ ràng, nhưng không có bệnh nhân, mạnh hơn y sư, cũng không có khả năng làm không bột đố gột nên hồ đi!"),t("br"),t("br"),n._v("Mà một khi chuẩn bị người bệnh, dùng để tranh tài... Liền sẽ dính dáng nhân đạo vấn đề, xa không tưởng tượng đơn giản như vậy."),t("br"),t("br"),n._v("“Này cũng không cần, y đạo tỷ thí có chuyên môn hệ thống, có chút là cần đặc thù bệnh nhân, nhưng cũng có chút là không cần, dù sao, không có khả năng có nhiều như vậy bệnh nhân, cũng không có khả năng để bệnh nhân chờ lấy tranh tài thời gian...”"),t("br"),t("br"),n._v("Thanh niên y sư nói."),t("br"),t("br"),n._v("Cứu người như cứu hỏa, chờ đến tranh tài tuyển thủ đều đến đông đủ, lại liên hợp cùng một chỗ cấp cứu, các loại dược vật thử một lần... Không chết cũng đoán chừng giết chết!"),t("br"),t("br"),n._v("Y đạo tỷ thí, khẳng định cùng bình thường tỷ thí hoàn toàn khác biệt."),t("br"),t("br"),n._v("“Cái kia...” Trương Huyền đang muốn hỏi thăm, cụ thể làm sao thao tác, chỉ thấy trước mắt rất nhiều y sư, đồng thời xôn xao, ngay sau đó nhìn thấy một người trung niên sải bước đi vào trước gian phòng mặt trên đài cao, vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("“Là Y Sư công hội hội trưởng Tôn Tuyền...”"),t("br"),t("br"),n._v("Nhận ra được, Tề Linh Nhi truyền âm nói."),t("br"),t("br"),n._v("Trương Huyền không còn tiếp tục hỏi thăm, mà là nhìn sang."),t("br"),t("br"),n._v("Vị hội trưởng này thoạt nhìn khoảng bốn mươi tuổi hình dáng, khí tức trầm ổn cũng giống như mình, cũng đạt tới thượng phẩm Thiên Thần đỉnh phong."),t("br"),t("br"),n._v("“Chư vị, Vân Ly thiên công khai khiêu chiến ta U Hồn thiên, yêu cầu tỷ thí y thuật, hơn nữa rơi xuống giấy sinh tử, một khi thua, ta U Hồn thiên Y Sư công hội y sư, phải thừa nhận bọn họ càng thêm cường đại, thậm chí càng phái ra đứng đầu nhất mười vị y sư, đi Vân Ly thiên miễn phí cống hiến sức lực một trăm năm... Loại sự tình này, ta nhẫn chẳng được, chư vị đâu?”"),t("br"),t("br"),n._v("Nhìn quanh một vòng, trung niên hội trưởng Tôn Tuyền thanh âm bên trong mang theo phẫn nộ."),t("br"),t("br"),n._v("“Chúng ta khẳng định nhẫn chẳng được!”"),t("br"),t("br"),n._v("“Vân Ly thiên cũng liền dựa dẫm huyết mạch mà thôi, biết cái gì y thuật? Như thế nào cùng chúng ta so?”"),t("br"),t("br"),n._v("“Miễn phí cống hiến sức lực một trăm năm, thật muốn như vậy, ta U Hồn thiên y sư sẽ vĩnh viễn không ngẩng đầu được lên...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Một hồi xôn xao, bên trong căn phòng đám người tất cả đều vẻ mặt khó coi."),t("br"),t("br"),n._v("“Nếu tất cả mọi người nhịn không được, vậy thì chiến! Vì U Hồn thiên, chúng ta không thể thua.”"),t("br"),t("br"),n._v("Thấy mọi người tất cả đều lên cơn giận dữ, sĩ khí xúc động phẫn nộ lên, Tôn Tuyền gật gật đầu: “Hiện tại ta tuyên bố, tham gia tỷ thí danh sách, hết thảy năm cái, chọn đều là U Hồn thiên nổi danh nhất nhìn y sư, nếu như chư vị có dị nghị, cũng có thể đưa ra, có điều, muốn vượt qua chọn lựa người tới mới được!”"),t("br"),t("br"),n._v("Đám người đồng thời gật đầu."),t("br"),t("br"),n._v("Văn vô đệ nhất, võ vô đệ nhị."),t("br"),t("br"),n._v("Y thuật chữ Nhật đồng dạng, rất khó phân tích ra ai là thứ nhất, ai là thứ hai, nhưng cùng một cái vòng tròn bên trong, người nào mạnh hơn một bậc, tất cả mọi người vẫn là có thể có chút khái niệm, biết một ít."),t("br"),t("br"),n._v("“Nhận được chư vị ưu ái, thêm vì Y Sư công hội hội trưởng, lần này đối phương khiêu chiến, nếu như ta không ra mặt nghênh chiến, tất nhiên sẽ nhận người khác chỉ trích, cho nên, ta tính một trong số đó, không có người có chuyện đi!”"),t("br"),n._v(" Tôn Tuyền nói."),t("br"),t("br"),n._v("“Hội trưởng tự thân bán ra, chúng ta tự không vấn đề...” Đám người đồng thời gật đầu."),t("br"),t("br"),n._v("“Uông Thanh, Nhạc Tuyền, Thẩm Tử Mộc ba vị y sư, có thể cống hiến tam đại gia tộc, liền phong hào Thần Vương có chứng bệnh đều có thể trị liệu, y thuật cao, không thể nghi ngờ, ba người bọn họ có thể xuất chiến!” Tôn Tuyền tiếp tục nói."),t("br"),t("br"),n._v("Đám người lần nữa gật đầu."),t("br"),t("br"),n._v("Ba người này có thể trở thành tam đại gia tộc ngự dụng y sư, hơn nữa rất được coi trọng, đủ để chứng minh năng lực."),t("br"),t("br"),n._v("Có tư cách đại biểu U Hồn thiên xuất chiến."),t("br"),t("br"),n._v("“Còn vị cuối cùng, ta muốn để chư vị đề cử một cái, nhìn một chút có hay không người thích hợp!”"),t("br"),t("br"),n._v("Thời gian nháy mắt xác định bốn cái, còn thừa lại cái cuối cùng danh ngạch, Tôn Tuyền tiếp tục nói."),t("br"),t("br"),n._v("“Thôi tuyển lời nói, ta đề cử Trần Siêu y sư! Tám năm trước,  xuất hiện, một mình hắn ngăn cơn sóng dữ, cứu vớt chẳng được mấy ngàn cường giả, công lao rất cao, y thuật cao minh!”"),t("br"),t("br"),n._v("“Ta đề cử Triệu Xung y sư, những năm này chiến công của hắn chư vị đều có thể nhìn thấy, các loại nghi vấn khó xử lý tạp chứng, đều có thể tuỳ tiện trị liệu, mười năm gần đây đến, dưới tay người còn sống, chẳng được mấy trăm, người người gọi là thần y... Cùng Vân Ly thiên giao chiến, tuyệt đối có tư cách!”"),t("br"),t("br"),n._v("“Ta cảm thấy tiền hải y sư cực kỳ có tư chất, tuy là danh khí không vang dội, lại là thật làm chuyện thật người, chưa từng phù phiếm...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Một cái nháy mắt, phía dưới liền liệt kê ra bảy, tám vị người ứng cử."),t("br"),t("br"),n._v("Mỗi một cái tại U Hồn hoàng thành đều tên tuổi lẫy lừng, chiếm cứ một phương, nhận vô số người ngưỡng mộ."),t("br"),t("br"),n._v("Đám người thảo luận, trong thời gian ngắn, không quyết định được, Trương Huyền thì quay đầu nhìn về phía cô bé trước mắt mở miệng hỏi: “Trở thành năm người này một trong, có thể hay không có sử dụng đại na di truyền tống trận tư cách?”"),t("br"),t("br"),n._v("Đặc biệt tới, chính là nghĩ biện pháp khảo hạch đại na di tư chất, cục diện trước mắt, đoán chừng cũng không có người có tinh lực khảo hạch, đã như vậy, không bằng tranh đoạt danh ngạch nhìn một chút."),t("br"),t("br"),n._v("“Quá sức... Loại này sử dụng đại na di truyền tống trận tư cách, Uông Thanh, Nhạc Tuyền, Thẩm Tử Mộc y sư bọn người không có, toàn bộ U Hồn thiên, chỉ có Tôn Tuyền hội trưởng có, nếu quả thật muốn lấy được... Chỉ sợ không chỉ có muốn cầm tới năm cái danh ngạch một trong, còn muốn vượt qua Vân Ly thiên người, để lần này y đạo tỷ thí chiến thắng...”"),t("br"),t("br"),n._v("Dừng lại một chút, Tề Linh Nhi nói."),t("br"),t("br"),n._v("Đại na di truyền tống trận sử dụng tư cách, điều yêu cầu thứ nhất chính là Thần Vương... Đủ thấy độ cao!"),t("br"),t("br"),n._v("Một cái thành thị đơn nhất chức nghiệp năm vị trí đầu, liền có thể thành công, cũng liền không có trong truyền thuyết khó khăn như vậy."),t("br"),t("br"),n._v("“Như vậy khó khăn?” Trương Huyền sửng sốt một chút, trong lòng không khỏi tò mò: “Sử dụng loại này đại truyền tống trận khó như vậy, chẳng phải biểu thị, Cửu Thiên hầu như không thể nào liên hệ?”"),t("br"),t("br"),n._v("Một cái chức nghiệp năm vị trí đầu, đều không thể sử dụng, chư thiên hết thảy bao nhiêu cái chức nghiệp?"),t("br"),t("br"),n._v("Như vậy tính toán, U Hồn thiên không cách nào đi vào mặt khác trời, mặt khác trời cũng khẳng định không có cách nào đi vào, buôn bán còn thế nào làm? Làm sao giao lưu?"),t("br"),t("br"),n._v("Như vậy trải qua Thần giới chẳng phải tương đương từng người phát triển, tựa như một đầm nước đọng?"),t("br"),t("br"),n._v("“Làm sao có thể không liên hệ!” Cười khổ một tiếng, Tề Linh Nhi lắc đầu: “Ngươi cần có là sử dụng đại na di truyền tống trận pháp danh ngạch... Mà không phải đi vào mặt khác thiên danh ngạch! Ngồi phi hành thần thú lời nói, tùy tiện đi vào, không có người quản... Không ít thương khách, không quan tâm thời gian, đều là ngồi phi hành thần thú đi tới!”"),t("br"),t("br"),n._v("“Cái này...” Trương Huyền vỗ trán một cái, cười khổ một tiếng."),t("br"),t("br"),n._v("Để tâm vào chuyện vụn vặt."),t("br"),t("br"),n._v("Đối phương nói không sai, đại na di trận pháp, một cái liền đem người truyền tống đến đối phương hoàng thành, sào huyệt, lúc này mới sẽ có hạn chế, người bình thường ngồi phi hành thần thú, chậm rãi từ từ, tuỳ ý một cái khoảng cách gần thành thị đều cần mười ngày nửa tháng, tự nhiên không quan trọng."),t("br"),t("br"),n._v("Phổ thông Thần Linh, có thể sẽ cảm thấy mười ngày nửa tháng rất dài, nhưng đi đến Thiên Thần cảnh, tuổi thọ nhẹ nhõm vượt qua một ngàn tuổi, bất kỳ cái gì một cái tu luyện, cũng phải có mấy tháng, thậm chí mấy năm, cái gọi là thời gian, cũng liền không còn ý nghĩa."),t("br"),t("br"),n._v("“Nếu mọi người không cách nào phân tích đi ra, liền dùng phương pháp đơn giản nhất, đem mấy vị chọn lựa y sư, tụ lại, tiến hành y sư khảo hạch, có thể đạt được đệ nhất, đương nhiên là y thuật cao minh nhất, sẽ đại biểu U Hồn thiên cùng Vân Ly thiên chiến đấu!”"),t("br"),t("br"),n._v("Bên này giao lưu, trên đài Tôn Tuyền cũng làm ra quyết định, nói."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Dưới đài đồng thời gật đầu."),t("br"),t("br"),n._v("Mỗi người đều có ý trong mắt lợi hại y sư, chỉ bằng vào tuyển cử lời nói, khẳng định không chọn được, cũng tìm không ra chân chính y thuật cao minh, cùng hắn như vậy, còn không bằng trực tiếp tới trong đó bộ y đạo tỷ thí!"),t("br"),t("br"),n._v("Ai có thể trổ hết tài năng, người nào liền cường đại nhất, không thể nghi ngờ."),t("br"),t("br"),n._v("“Cái kia xin tuyển chọn chư vị, đi vào đài cao...” Thấy mọi người đồng ý, Tôn Tuyền nói."),t("br"),t("br"),n._v("Rất nhanh, mấy vị bị đám người chọn lựa tới y sư đi tới, hết thảy tám vị, đều là U Hồn hoàng thành, mười năm này, nhất tên tuổi lẫy lừng y sư, mỗi một cái đều có để cho người ta ngưỡng mộ công lao."),t("br"),t("br"),n._v("“Còn có nguyện ý khiêu chiến ư? Chỉ cần có thể vượt qua mấy người bọn hắn, đồng dạng có cơ hội đại biểu U Hồn hoàng thành xuất chiến!”"),t("br"),t("br"),n._v("Tôn Tuyền lần nữa nhìn về phía đám người, nói."),t("br"),t("br"),n._v("“Trương thiếu, ngươi có muốn hay không tham gia?”"),t("br"),t("br"),n._v("Tề Linh Nhi nhìn lại."),t("br"),t("br"),n._v("Trước mắt vị này y thuật cao thấp, nàng không có chút nào biết, nhưng thân có Đế Quân huyết mạch, nàng cũng không dám nói nhiều, chỉ có thể hỏi thăm một cái, nhìn một chút thái độ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2184"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2186"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);