(window.webpackJsonp=window.webpackJsonp||[]).push([[1338],{1693:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2175-truong-huyen-y-đao-ty-thi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2175-truong-huyen-y-đao-ty-thi"}},[n._v("#")]),n._v(" Chương 2175: Trương Huyền y đạo tỷ thí")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Liêu hội trưởng, lần trước linh khí thuỷ triều đến, chịu chút ám thương, bởi vì chính mình là y sư, mười năm qua, các loại dược vật ăn không biết bao nhiêu, cũng nghiên cứu qua không biết bao nhiêu thư tịch, từ đầu đến cuối không có hiệu quả gì!”"),t("br"),t("br"),n._v("Thấy bọn họ choáng váng, một bên trung niên y sư giới thiệu: “Gần nhất hai năm, ám thương khuếch tán ra đến, càng ngày càng lợi hại, vốn cho rằng, đến đại nạn, lại không cách nào kiên trì... Ngay tại mấy ngày trước đây, có người mua Mỹ Dung đan, đưa cho hắn. Vốn cũng không cảm thấy cái gì, ai biết sau khi phục dụng, trong cơ thể lan tràn ám thương, vậy mà dần dần khôi phục! Lại cầu người mua một ít, mười cái đi xuống, không chỉ có thương thế hoàn hảo không chút tổn hại, tuổi thọ dường như cũng tăng lên không ít... Cho nên, nói ngươi có ân cứu mạng, cũng không quá đáng!”"),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Mỹ Dung đan nguyên lý, là ẩn chứa hắn Thiên đạo chân khí, chữa trị ám thương, dễ như trở bàn tay, thương thế được rồi, sinh cơ tăng cường, tuổi thọ một cách tự nhiên nhận được kéo dài."),t("br"),t("br"),n._v("“Ta nghiên cứu không biết bao lâu, không chữa khỏi tổn thương, bị ngươi đan dược dễ như trở bàn tay hóa giải, y thuật của ngươi, chẳng phải vượt xa quá ta?” Liêu Tân nói."),t("br"),t("br"),n._v("“Cái này... Ngươi dạng này hiểu, ngược lại cũng không sao cả!” Muốn khiêm tốn một cái, suy nghĩ một chút, thật đúng là đúng là như thế, Trương Huyền đành phải cố hết sức gật gật đầu."),t("br"),t("br"),n._v("Bình thường bệnh, Thiên đạo chân khí đều có thể trị liệu, trị liệu không tốt, cũng gần như chỉ có thể chờ đợi chết rồi."),t("br"),t("br"),n._v("Thừa nhận... Y thuật không bằng người khác, như vậy trái lương tâm lời nói, thật đúng là khó mà mở miệng, suy nghĩ một chút liền xấu hổ!"),t("br"),t("br"),n._v("Ưu tú, chính là như vậy không có cách nào..."),t("br"),t("br"),n._v("Suy nghĩ một chút đều có chút đau đầu."),t("br"),t("br"),n._v("“Vậy thì tốt, ta hiện tại liền đưa tin trở về, giúp ngươi tiến hành có thể ngồi đại na di truyền tống trận lệnh phù!” Liêu Tân nói."),t("br"),t("br"),n._v("Lần này tới khiêu chiến U Hồn hoàng thành Y Sư công hội, dựa theo tình huống bình thường, hắn cũng không cần tự thân tới, sở dĩ chạy tới, chính là muốn gặp một cái vị này Trương Huyền Trương đan sư, tự thân nói một tiếng cảm ơn."),t("br"),t("br"),n._v("Nếu đối phương có chỗ yêu cầu, đáp ứng là được, cũng coi như báo đáp ân tình."),t("br"),t("br"),n._v("Ngay tại Liêu Tân lấy ra đặc thù đưa tin ngọc phù, ý định đưa tin thời điểm, lại một cái lão giả từ trong phòng đi ra, vội vàng xua tay: “Hội trưởng, không thể...”"),t("br"),t("br"),n._v("“Vương hội phó, làm sao không thể?” Liêu Tân cau mày."),t("br"),t("br"),n._v("Người tới, chính là Vân Ly hoàng thành Y Sư công hội phó hội trưởng, Vương Phi."),t("br"),t("br"),n._v("“Luyện đan sư luyện đan, y thuật là y thuật, không thể nói làm một!” Vương Phi vội nói."),t("br"),t("br"),n._v("Tuy là hắn cũng thừa nhận trước mắt vị thanh niên này luyện chế Mỹ Dung đan thiên hạ vô song, có thể... Luyện đan chính là luyện đan, mạnh hơn cũng thay đổi không thành y thuật."),t("br"),t("br"),n._v("[ truyen cua"),t("br"),n._v("tui | Net ]  Y thuật là đối chứng hạ độc, trị liệu ổ bệnh, mà luyện đan, là thông qua phương pháp đặc thù đem dược vật dung hợp lại cùng nhau, mục đích chỉ vì tăng lên dược hiệu, cả hai hoàn toàn khác biệt, không thể so sánh nổi."),t("br"),t("br"),n._v("Ngày hôm nay hỗ trợ xử lý cái này lệnh phù, chẳng khác nào y thuật không bằng trước mắt vị này, một khi người sau không may xuất hiện, Liêu Tân hội trưởng một thế anh danh, chỉ sợ đều sẽ trôi theo nước chảy."),t("br"),t("br"),n._v("“Hội trưởng cân nhắc!”"),t("br"),t("br"),n._v("Ngay sau đó lại có người đi ra."),t("br"),t("br"),n._v("Vân Ly thiên năm vị y sư, giờ phút này đã đến tề."),t("br"),t("br"),n._v("“Hắn đối ta có ân cứu mạng, ta vừa vặn cũng ý định về hưu, cùng lắm thì từ nay về sau, sẽ không lại cho người trị liệu... Được rồi, không cần nhiều lời, ta tâm đã quyết!”"),t("br"),t("br"),n._v("Liêu Tân xua tay."),t("br"),t("br"),n._v("Tuy là hắn thừa nhận vị này Trương Huyền đan dược, quả thực vô song, nhưng ở sâu trong nội tâm, cũng cùng đám người đồng dạng, cho là hắn luyện đan lợi hại, y thuật chưa hẳn so ra mà vượt bản thân."),t("br"),t("br"),n._v("Sở dĩ đáp ứng, là vì báo đáp ân cứu mạng."),t("br"),t("br"),n._v("Đối với tính mạng đến, danh dự bị tổn thương, không tính là gì."),t("br"),t("br"),n._v("“Như vậy đi, không phải có cái gì y đạo tỷ thí ư? Tới đều tới, tỷ thí một chút cũng không sao!”"),t("br"),t("br"),n._v("Rõ ràng đối phương lo lắng ý tứ, Trương Huyền cũng không tức giận."),t("br"),t("br"),n._v("Hắn trẻ tuổi như vậy, tới Thần giới thời gian cũng không dài, gặp phải nghi ngờ cũng rất bình thường."),t("br"),t("br"),n._v("“Ngươi muốn y đạo tỷ thí?” Liêu Tân cau mày."),t("br"),t("br"),n._v("“Dù sao đều tới!” Trương Huyền vẻ mặt lãnh đạm."),t("br"),t("br"),n._v("“Cái này...” Thấy hắn thái độ kiên quyết, Liêu Tân gật gật đầu: “Y đạo tỷ thí, chia làm nhìn, nghe, hỏi, cắt bốn bước, mỗi một cái, đều muốn viết phán đoán của mình, tiếp đó chẩn đoán được chứng bệnh, viết xuống phương thuốc, cuối cùng nhìn một chút có hay không có thể đúng bệnh hốt thuốc!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trước đó còn nghi hoặc, y đạo tỷ thí chuyện gì xảy ra, hiện tại xem ra, cùng bình thường xem bệnh đồng dạng, chỉ là..."),t("br"),t("br"),n._v("“Bệnh nhân làm sao bây giờ? Chẳng lẽ, không nghĩ biện pháp cứu chữa, mà là để cho bọn họ tiếp nhận đau đớn, chờ lấy tỷ thí?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Như vậy, không phải lương y như từ mẫu, mà là đem bệnh nhân, xem như bản thân nổi danh thẻ đánh bạc."),t("br"),t("br"),n._v("Hoàn toàn vi phạm với y sư hẳn là có chức nghiệp cùng phẩm hạnh."),t("br"),t("br"),n._v("“Dĩ nhiên không phải...”"),t("br"),t("br"),n._v("Liêu Tân mang theo kỳ quái nhìn qua: “Ngươi... Không biết y đạo tỷ thí?”"),t("br"),t("br"),n._v("Trước mắt vị này, đưa ra y đạo tỷ thí, vậy mà một khiếu không hiểu?"),t("br"),t("br"),n._v("Vốn còn nghĩ, thừa nhận bại bởi đối phương, là trả nhân tình, đối phương coi như y thuật so ra kém bản thân, có thể luyện chế ra Mỹ Dung đan, hẳn là cũng sẽ không quá kém, hiện tại xem ra... Căn bản không phải chuyện này."),t("br"),t("br"),n._v("Liền y đạo tỷ thí cũng không biết, chỉ sợ y thuật... Không phải so với chính mình hơi thấp, mà là... Căn bản không biết ah!"),t("br"),t("br"),n._v("“Y đạo tỷ thí, là Cửu Thiên y thuật cường giả, liên hợp cùng một chỗ, làm một loại dùng cho y thuật bình xét thủ đoạn, chủ yếu dựa vào, là y đạo thạch!”"),t("br"),t("br"),n._v("Thấy hắn quả thật không biết, tràn đầy bất đắc dĩ lắc đầu, Liêu Tân giải thích nói."),t("br"),n._v("Nhìn tới Vương hội phó lo lắng chuyện là thật."),t("br"),t("br"),n._v("Trước mắt vị này y thuật, cũng không dạng gì, nếu không, nắm giữ cao minh y thuật người, người nào không biết y đạo tỷ thí?"),t("br"),t("br"),n._v("“Y đạo thạch?”"),t("br"),t("br"),n._v("“Là một loại đặc thù bảo vật, có thể ghi chép bệnh nhân thân thể chân thực số liệu, hơn nữa sáng tạo ra hư ảnh, để cho y sư dò xét cùng hỏi thăm. Chỉ cần ngươi có thể đưa ra chính xác nhất phương thuốc, trên người hắn ‘Chứng bệnh’ cũng có thể bởi vậy khôi phục!”"),t("br"),t("br"),n._v("Liêu Tân nói, một viên tảng đá xuất hiện tại phía trước, ở phía trên nhẹ nhàng điểm một cái."),t("br"),t("br"),n._v("Kêu khẽ âm thanh bên trong, mười người ảnh bất ngờ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Là đặc thù trận pháp hình thành hư ảnh."),t("br"),t("br"),n._v("Mỗi một cái, đều ốm yếu, hình như trên người lây nhiễm một loại chứng bệnh, vô cùng nghiêm trọng, không kịp chữa trị, vô cùng có khả năng tại chỗ tử vong."),t("br"),t("br"),n._v("“Mười người này trên người bệnh, là Cửu Thiên phạm vi bên trong, mới nhất phát hiện hơn nữa chữa trị nghi vấn khó xử lý tạp chứng, cũng có mấy trăm năm trước, vài ngàn năm trước bệnh thể, thông qua thủ đoạn đặc thù sao chép lại đến, cùng chân chính bệnh nhân giống nhau như đúc, có thể dùng nhìn, nghe, hỏi, cắt, bốn loại phương thức xác định nguyên nhân bệnh, nghĩ biện pháp chế biến ra dược vật.”"),t("br"),t("br"),n._v("Liêu Tân giải thích đôi câu, dường như nhìn ra Trương Huyền lo lắng, nói: “Yên tâm, những bệnh này chứng, bao gồm thượng cổ cùng hiện tại, thậm chí còn có chút chứng bệnh là giao hòa, không có khả năng có người có thể toàn bộ ghi nhớ! Hơn nữa, mỗi một lần truyền vào lực lượng, xuất hiện người đều cùng trước đó hoàn toàn khác biệt, đọc thuộc lòng câu trả lời khả năng, sẽ không phát sinh.”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Chữa bệnh không phải cõng đề thi, thoạt nhìn đồng dạng chứng bệnh, căn cứ người thể chất khác biệt, giới tính khác biệt, chế thuốc thời điểm, cũng là có phân chia đối đãi."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Trương Huyền lo lắng, có người trước thời hạn nhớ kỹ những bệnh nhân này đặc thù cùng với phương pháp trị liệu, trực tiếp máy móc, hiện tại xem ra, cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("Thần giới cường giả, cứ việc trí nhớ mạnh mẽ, có thể đếm được ngàn năm, trên vạn năm tới chứng bệnh không biết bao nhiêu, lại thêm lịch sử tư liệu thiếu hụt, muốn hoàn chỉnh điều tra, cũng sẽ nhớ ở, khẳng định không dễ dàng như vậy, thậm chí có thể nói... Tuyệt đối không thể!"),t("br"),t("br"),n._v("Liêu Tân nói: “Nếu biết quy tắc, vậy thì bắt đầu đi! Quy tắc chính là cho những này hư ảnh xem bệnh, mỗi khi trải qua một bước, liền đem bản thân đoán viết xuống đến, vọng văn vấn thiết kết thúc, đem phương thuốc viết xong, đưa tới trong tay đối phương, một khi phương thuốc cùng trước đó chữa khỏi bệnh chứng phương án tương đồng, hư ảnh chứng bệnh liền sẽ hoàn hảo không chút tổn hại, xuất hiện sai lầm, cũng biết xuất hiện muốn đối ứng kết quả!”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, bàn tay liên lụy y đạo thạch, nhẹ nhàng đụng một cái, phía trên mười cái hư ảnh quả nhiên toàn bộ đổi, trở nên cùng trước đó hoàn toàn khác biệt."),t("br"),t("br"),n._v("“Bắt đầu!”"),t("br"),t("br"),n._v("Kèm theo lời nói, hai vị y sư đưa tới giấy bút, Liêu Tân hai bước đi vào hư ảnh trước mặt, nhìn kỹ quá khứ."),t("br"),t("br"),n._v("Làm bác sĩ, “Nhìn” là bước đầu tiên, thông qua quan sát bệnh nhân, Thần, sắc, hình, trạng thái, lưỡi voi mấy người tiến hành có mục đích quan sát, lấy đo biết trong cơ thể biến đổi bệnh lý tình huống."),t("br"),t("br"),n._v("Thấy hắn hướng đi trong mười người một bên, Trương Huyền hướng đi mặt khác một bên."),t("br"),t("br"),n._v("Rất mau đem đám người quét xong, trong lòng đã suy đoán ra được tám chín phần mười."),t("br"),t("br"),n._v("Đi vào chính giữa vị trí, một tiếng thở nhẹ: “Thiếu hụt!”"),t("br"),t("br"),n._v("Hô hô hô hô!"),t("br"),t("br"),n._v("Trong đầu nhiều ra mười bản thư tịch."),t("br"),t("br"),n._v("Tuy là chỉ bằng vào bước đầu tiên, đã suy đoán ra mười người nguyên nhân bệnh cùng chứng bệnh, nhưng vẫn là không yên lòng, dùng thư viện thử một chút, nhìn một chút có phải hay không cùng mình suy đoán đồng dạng."),t("br"),t("br"),n._v("Dính dáng đến mau chóng đi Linh Nguyên thiên, tìm kiếm Lạc Nhược Hi, cũng không muốn tiếp tục trễ nải nữa."),t("br"),t("br"),n._v("Trước mắt xuất hiện mặc dù là hư ảnh, nhưng dính dáng chứng bệnh, thư viện quả nhiên đồng dạng đưa ra bệnh nhân xuất hiện thiếu hụt, rất mau nhìn một lần, cùng mình suy đoán tám chín phần mười, lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Thần giới y thuật, cứ việc nhìn không nhiều, nhưng ở danh sư đại lục, là cường đại nhất y đạo cao thủ, với thân thể người cùng với mỗi một loại dược liệu dược hiệu, nghiên cứu đến cốt tủy. Lúc này chỉ cần rõ ràng, Thần Linh cùng người bình thường chênh lệch, cùng với cùng danh sư đại lục các loại dược liệu dược lực khác biệt, liền có thể triệt để đem hai loại kiến thức dung hội quán thông."),t("br"),t("br"),n._v("Trong đầu thôi diễn một phen, ngồi tại nguyên chỗ, đem tính toán sau kết quả, viết đi ra, mười cái giấy rất nhanh viết xong."),t("br"),t("br"),n._v("“Được rồi, ta hoàn thành!”"),t("br"),t("br"),n._v("Trương Huyền khẽ mỉm cười."),t("br"),t("br"),n._v("“Hoàn thành...?”"),t("br"),t("br"),n._v("“Lúc này mới qua bao lâu? Mười phút đồng hồ? Tám phút? Hơn nữa, cơ bản đều tại viết?”"),t("br"),t("br"),n._v("“Ngắn như vậy đem mười người chứng bệnh toàn bộ xác định, hơn nữa viết ra phương thuốc? Không phải là nói đùa sao?”"),t("br"),t("br"),n._v("Đám người tất cả đều sững sờ."),t("br"),t("br"),n._v("Xem bệnh, là cứu người, tuy là cũng không có thời gian, cũng không đến mười phút đồng hồ, mười vị y đạo trong lịch sử đều có thể phải kể đến nghi vấn khó xử lý tạp chứng, không chỉ xác định được, liền phương thuốc đều viết ra..."),t("br"),t("br"),n._v("Xác định không phải đang nói đùa?"),t("br"),t("br"),n._v("Đám người hai mặt nhìn nhau, lần nữa nhìn về phía Liêu Tân hội trưởng, chỉ thấy hắn xoa lông mày, đứng tại cái thứ nhất bệnh nhân phía trước, vừa hỏi thăm xong, ngay tại bắt mạch."),t("br"),t("br"),n._v("Lại qua phút chốc, lúc này mới lấy ra giấy, đem bản thân xác định được chứng bệnh cùng với phương thuốc viết đi ra."),t("br"),t("br"),n._v("Làm xong những này, thở phào nhẹ nhõm, quay đầu nhìn thấy Trương Huyền vậy mà dừng lại, lông mày nhíu: “Đừng chậm trễ thời gian, đồng dạng giải quyết bệnh chứng dưới tình huống, thời gian nhanh, là sẽ chiến thắng!”"),t("br"),t("br"),n._v("Vừa mới chỉ lo nghiên cứu bệnh nhân, không để ý người thanh niên này động tác."),t("br"),t("br"),n._v("Nói xong, đang định tiếp tục xem cái thứ hai bệnh nhân, chỉ thấy những người khác, tất cả đều một mặt im lặng nhìn qua."),t("br"),t("br"),n._v("“Thế nào?” Nhịn không được, hỏi."),t("br"),t("br"),n._v("“Hội trưởng... Hắn đã xác định xong chứng bệnh, hơn nữa đem phương thuốc đều viết xong, ngươi vẫn là động tác... Nhanh lên một chút đi!” Vương Phi mặt mũi lúng túng nói."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2189"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2191"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);