(window.webpackJsonp=window.webpackJsonp||[]).push([[134],{487:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1097-ta-ghet-nhat-co-nguoi-cung-ta-trang-buc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1097-ta-ghet-nhat-co-nguoi-cung-ta-trang-buc"}},[n._v("#")]),n._v(" Chương 1097: Ta ghét nhất có người cùng ta trang bức")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Lão giả hơn sáu mươi tuổi bộ dáng, râu tóc hoa râm, hai hàng lông mày giống như tiễn, mang theo nồng đậm sát phạt khí tức, vừa nhìn liền biết kinh nghiệm sa trường, một thân tu vi, dâng trào muốn ra, đã đạt đến Thánh vực tứ trọng Nguyên Thần cảnh sơ kỳ."),t("br"),t("br"),n._v("“Là Tề Bắc song hùng một trong Lạc Địa Hùng Đạo Khấu!”"),t("br"),t("br"),n._v("Thấy rõ bộ dáng, một bên thằng nhóc sợ tới mức sắc mặt trắng nhợt, vội vàng truyền âm tới: “Vị này Lạc Địa Hùng, tại Tề Bắc là đặc biệt có tên tồn tại, có thể nói một phương bá chủ, thực lực mạnh mẽ không nói, mấu chốt nhất là, làm bất cứ chuyện gì đều không coi trọng quy củ, bá đạo kiêu ngạo, tùy ý làm bậy...”"),t("br"),t("br"),n._v("Tề Bắc là sạch nguyên phong hào đế quốc một chỗ địa danh, chiếm diện tích phạm vi cực lớn, thậm chí so Hồng Viễn nhất đẳng đế quốc toàn bộ lãnh thổ diện tích còn lớn hơn, có thể ở đây trở thành một phương bá chủ, đủ thấy đáng sợ."),t("br"),t("br"),n._v("“Không coi trọng quy củ lại như thế nào? Hứa hội phó đã đón lấy linh thạch của ta, chẳng lẽ còn có thể nuốt lời?” Trương Huyền lắc đầu, cũng không thèm để ý."),t("br"),t("br"),n._v("Bất kể chuyện gì, đều có cái tới trước tới sau, đây là quy củ, đối phương liền xem như Nguyên Thần cảnh cường giả, tại Danh Sư đường vị trí, cũng không có khả năng bỏ qua những thứ này."),t("br"),t("br"),n._v("“Ta không phải ý tứ này, ta nghe qua, trước kia hắn từng có đoạt người khác khải linh ghi chép, ngộ nhỡ tranh chấp, tốt nhất cẩn thận một chút, người này mang thù, có thù tất báo...”"),t("br"),t("br"),n._v("Thằng nhóc tiếng nói chưa rơi, chỉ thấy một bên Hứa hội phó lắc đầu: “Ta hôm nay quả thực chỉ khải linh hai người, nhưng vị công tử này, đã trước thời hạn đi tới, hơn nữa nộp khải linh phí tổn. Cho nên, các ngươi chỉ có thể về sau đẩy!”"),t("br"),t("br"),n._v("Thân là Khải Linh sư công hội phó hội trưởng, tự nhiên cũng muốn tuân thủ quy củ."),t("br"),t("br"),n._v("Huống chi trước mắt vị này còn là một vị danh sư."),t("br"),t("br"),n._v("“So với chúng ta tới trước? Cái này đơn giản!”"),t("br"),t("br"),n._v("Đạo Khấu cười cười, mấy bước đi tới Trương Huyền trước mặt, trong giọng nói mang theo áp bức cùng không cho phản bác lực lượng: “Vị tiểu hữu này, đưa ngươi danh ngạch nhường cho ta, vừa rồi ngươi hẳn là bỏ ra một trăm linh thạch thượng phẩm đi, hiện tại cho ngươi hai trăm, xem như đền bù!”"),t("br"),t("br"),n._v("Nói, cổ tay khẽ đảo, trước mắt nhiều ra một đống linh thạch thượng phẩm, đúng lúc là 200 viên nhiều."),t("br"),t("br"),n._v("Vừa ra tay liền tăng lên gấp đôi, xem như cực cao giá tiền."),t("br"),t("br"),n._v("Nói hết, ngẩng đầu lên, hai tay chắp sau lưng, Đạo Khấu đang cảm thấy đối phương sẽ cầm lấy linh thạch, xoay người rời đi, dù sao, một cái Thánh vực nhất trọng, không thể là vì một cái danh ngạch, đắc tội bản thân, lập tức nhìn thấy thanh niên hình như không nghe thấy hắn, nhìn về phía trước mắt Hứa hội phó: “Ngươi nói để nó khôi phục có chút khó khăn, không biết có gì khó khăn, có cần hay không ta hỗ trợ...”"),t("br"),t("br"),n._v("“Lão sư ta nói chuyện cùng ngươi, không nghe thấy ư?”"),t("br"),t("br"),n._v("Thấy cái tên này, bỏ qua lão sư, trung niên nhân trên mặt không vui, tiến lên một bước, lông mày nâng lên, hét lớn một tiếng."),t("br"),t("br"),n._v("“Nếu là cảm thấy ta đối khải linh, không hiểu nhiều, có thể hiện tại đi học...” Giống như không nghe thấy hắn, Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“Học?” Hứa hội phó lắc đầu: “Khải Linh sư mặc dù không phải đặc thù chức nghiệp, nhưng cũng không phải một lần mà thôi, hiện tại học khẳng định là không còn kịp rồi, để ngủ say ý thức thanh tỉnh, dính dáng linh tính đặc thù, mười phần phiền phức, ta cũng không dám cam đoan có thể thành công!”"),t("br"),t("br"),n._v("“Cái này...” Trương Huyền cau mày: “Cái kia... Không thành công, có hay không có cái gì nguy hại?”"),t("br"),t("br"),n._v("Không thành công cũng không có gì, liền sợ không thành công, ngược lại làm hại trước đó linh tính biến mất, vậy liền được không bù mất."),t("br"),t("br"),n._v("“Nhiều nhất không thành công mà thôi, đối vũ khí vốn linh tính, không có nguy hiểm gì...” Hứa hội phó cười cười."),t("br"),t("br"),n._v("“Vậy làm phiền Hứa hội phó thử một chút, đại ân chắc chắn báo đáp!” Trương Huyền khom người."),t("br"),t("br"),n._v("Chỉ cần có thể cứu tỉnh Kim Nguyên đỉnh, trả giá lại lớn đánh đổi, cũng đáng giá."),t("br"),t("br"),n._v("“Không cần khách khí, có thể thành công hay không, ta cũng không nói được, chỉ có thể nói toàn lực ra tay!” Hứa hội phó gật đầu."),t("br"),t("br"),n._v("Thân là danh sư, lại tiện tay lấy ra quý giá như vậy vũ khí, thân phận làm sao có thể đơn giản? Trong giọng nói của nàng không có chút nào thờ ơ."),t("br"),t("br"),n._v("Bất quá, nàng không chậm trễ, nghe được Đạo Khấu cùng người trung niên kia trong tai, cũng có chút không nhìn hương vị."),t("br"),t("br"),n._v("“Tiểu tử, nói chuyện cùng ngươi, chẳng lẽ không nghe thấy?”"),t("br"),t("br"),n._v("Không dám đối mỹ phụ nổi giận, đi tới Trương Huyền trước mặt, trung niên nhân trong ánh mắt mang theo trần trụi sát ý."),t("br"),t("br"),n._v("Bỏ qua hắn ngược lại cũng thôi, bỏ qua lão sư, quả thực tội không thể tha!"),t("br"),t("br"),n._v("Hắn nắm giữ Thánh vực tam trọng đỉnh phong thực lực, đứng tại trước mặt, mặc dù còn chưa ra tay, nhưng áp lực cường đại liền cho người ta một loại nghiền ép cảm giác."),t("br"),t("br"),n._v("“Người trẻ tuổi, thái độ rất nhiều, khiêm tốn một chút, ta có thể sẽ trả lời ngươi một lần!”"),t("br"),t("br"),n._v("Bỏ qua lực lượng của đối phương, Trương Huyền mí mắt vừa nhấc."),t("br"),t("br"),n._v("“Tự tìm cái chết!”"),t("br"),t("br"),n._v("Trung niên nhân da mặt run rẩy, suýt chút nữa nổ."),t("br"),t("br"),n._v("Một cái hai mươi tuổi tiểu gia hỏa, xưng hô hắn tuổi trẻ người, một bộ trưởng bối dạy bảo vãn bối hơi thở... Quả thực để hắn phát điên."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Lật bàn tay một cái, liền muốn đập xuống tới."),t("br"),t("br"),n._v("Hứa hội phó sầm mặt lại, đang nghĩ ra tay ngăn cản, chỉ thấy cách đó không xa thanh niên, mí mắt vừa nhấc, thanh âm đột nhiên vang lên."),t("br"),t("br"),n._v("“Cút!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Thanh âm như là tiếng sấm, tại nguyên chỗ nổ tung."),t("br"),t("br"),n._v("Lộc cộc lộc cộc lộc cộc lộc cộc!"),t("br"),t("br"),n._v("Giống như là bị trọng kích, trung niên nhân công kích còn chưa rơi vào trên người đối phương liền sắc mặt trắng nhợt, liên tục lui về sau bảy, tám bước, một ngụm máu tươi phun tới!"),t("br"),t("br"),n._v("Hắn mặc dù là Thánh vực tam trọng đỉnh phong, nhưng Trương Huyền phong Thánh thành công, sức chiến đấu có thể nói Nguyên Thần cảnh phía dưới vô địch, đối phó loại này liền danh sư đều không phải là, thậm chí đều không cần ra tay, một tiếng quát tháo, liền có thể để tâm thần đại loạn, đạo tâm mất phòng."),t("br"),t("br"),n._v("“Nghiêm Kỳ...”"),t("br"),t("br"),n._v("Thấy mình học sinh, bị một tiếng quát tháo, liền bị trọng thương, Đạo Khấu sầm mặt lại, tiến về phía trước một bước, một đạo chân khí rót vào thuộc hạ trong cơ thể, giúp hắn ngăn chặn thương thế, lúc này mới ngẩng đầu nhìn về phía cách đó không xa thanh niên, ánh mắt băng hàn: “Kinh hồng sư, ma âm sư? Vị bằng hữu này, vừa ra tay cứ như vậy hung ác, có phải hay không có chút quá đáng?”"),t("br"),t("br"),n._v("Lấy nhãn lực của hắn, lập tức nhìn ra đối phương vừa rồi vận dụng cái chiêu số gì."),t("br"),n._v("Chỉ là nói một cách đơn giản một chữ, trong đó nhưng ẩn chứa ma âm sư cùng kinh hồng sư năng lực, khó lòng phòng bị."),t("br"),t("br"),n._v("“Nếu như ta không đánh trả, chỉ sợ hắn công kích đã rơi vào trên người ta đi!” Mặc kệ trả lời hắn, Trương Huyền nói."),t("br"),t("br"),n._v("“Học trò ta là có chút lỗ mãng, có điều, còn sẽ không tùy tiện ra tay!”"),t("br"),t("br"),n._v("Đạo Khấu cười lạnh liên tục: “Làm tổn thương ta học sinh, làm lão sư, vì hắn đòi lại công đạo, cũng không tính không tuân theo quy định, như vậy... Ta không lấy lớn lấn nhỏ, nhường ngươi ba chiêu, trong vòng ba chiêu, có thể đụng tới góc áo của ta, không nói hai lời, trực tiếp rời khỏi. Không đụng tới, vậy liền thật không tiện, đem danh ngạch cho ta, cút ngay ra ngoài, nếu không, đừng trách ta không khách khí!”"),t("br"),t("br"),n._v("Hắn mệnh danh Tề Bắc chúa tể, lúc nào nếm qua lớn như thế thua thiệt!"),t("br"),t("br"),n._v("“Ba chiêu?” Trương Huyền lông mày giương lên: “Tốt, ngươi ra tay đi!”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Đạo Khấu kìm nén tràn đầy nội thương: “Ta nói chính là nhường ngươi ba chiêu!”"),t("br"),t("br"),n._v("Trương Huyền một mặt ngạc nhiên: “Ngươi xác định?”"),t("br"),t("br"),n._v("“Xác định!”"),t("br"),t("br"),n._v("Đạo Khấu tràn đầy tự tin."),t("br"),t("br"),n._v("Một tiếng quát tháo, để học sinh của hắn lui về phía sau, bị trọng thương, mặc dù thoạt nhìn đáng sợ, trên thực tế cũng nhìn ra, đối phương am hiểu là linh hồn phương diện công kích."),t("br"),t("br"),n._v("Am hiểu linh hồn, thân thể chân khí tất nhiên yếu, đây là người người đều biết định lý, huống chi còn trẻ như vậy, có thể mạnh bao nhiêu?"),t("br"),t("br"),n._v("Bởi vậy, cố ý đưa ra ba chiêu đụng phải hắn góc áo tỷ thí!"),t("br"),t("br"),n._v("Nếu như là tại Tề Bắc, tất cả mọi người sẽ biết, đây là tại cố ý bắt nạt người, hắn am hiểu nhất liền là tốc độ, tại am hiểu lĩnh vực cùng một cái chỉ có Thánh vực nhất trọng tiểu gia hỏa tỷ thí... Có thể nói là giết gà dùng đao mổ trâu, lấy lớn hiếp nhỏ."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Không để ý tới tự tin của hắn, Trương Huyền nhẹ gật đầu, lời còn chưa dứt, bóng người đột nhiên lóe lên, đã xuất hiện tại Đạo Khấu trước mặt, tốc độ nhanh như là sấm sét."),t("br"),t("br"),n._v("Mặc dù ghét bỏ  thiếu hụt quá nhiều, không đi tu luyện, nhưng cái này hơn một tháng qua, cũng không nhàn rỗi, để Thiên Đạo thân pháp cùng chính xác bộ phận tiến hành dung hợp, làm cho tốc độ lần nữa tiến bộ một đoạn dài, lúc này vận chuyển lên đến, cho dù không đuổi kịp thuấn di, nhưng cự ly ngắn phía trong, cũng mười phần kinh khủng."),t("br"),t("br"),n._v("Theo bắt đầu hành động, đến xuất hiện ở trước mặt đối phương, liền một phần mười cái hô hấp đều không dùng đến."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Người xuất hiện tại trước mặt, nắm đấm cũng lập tức duỗi ra."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới còn không có chuẩn bị kỹ càng, đối phương liền lao đến, hơn nữa nhanh như vậy, Đạo Khấu sợ tới mức sắc mặt trắng nhợt, đang nghĩ đánh trả, liền phát hiện thanh niên nắm đấm so thân ảnh nhanh hơn, giống như đột phá thời gian hạn chế."),t("br"),t("br"),n._v("Ngực tê rần, một tiếng vù vù cả người liền trực tiếp bay ngược ra ngoài."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Trực tiếp đâm vào trên vách tường, bại liệt xuống, như là một bãi bùn nhão, mắt tối sầm lại, tại chỗ hôn mê bất tỉnh."),t("br"),t("br"),n._v("“Tề Bắc song hùng một trong Lạc Địa Hùng Đạo Khấu, am hiểu tốc độ,  thân pháp thi triển đi ra, giống như quỷ mị, tìm không thấy chân thân, tốt nhất đừng đáp ứng...”"),t("br"),t("br"),n._v("Hai bên giao thủ nói đến phức tạp, trên thực tế liền một cái hô hấp cũng chưa tới, cùng Trương Huyền đi vào thằng nhóc, nghe được vị thiếu gia này thế mà đáp ứng đối phương, tràn đầy gấp gáp truyền âm, nói còn chưa dứt lời, liền thấy nằm dưới đất Đạo Khấu, cả người câm tại nguyên chỗ, cảm thấy sắp điên rồi."),t("br"),t("br"),n._v("Trước mắt vị này so với hắn cũng lớn hơn không được bao nhiêu, vốn cho rằng thực lực bình thường, nằm mơ đều không có nghĩ đến, một tiếng quát tháo để một vị Thai Anh cảnh trọng thương, một quyền giải quyết một vị Nguyên Thần cảnh..."),t("br"),t("br"),n._v("Lạc Địa Hùng Đạo Khấu, bởi vì  bộ này thân pháp mà nổi danh, cho dù Nguyên Thần cảnh cường giả đỉnh phong, muốn đuổi theo cũng khó khăn, kết quả... Ở trước mặt đối phương thân pháp cũng không kịp thi triển, liền bị đánh ngất xỉu..."),t("br"),t("br"),n._v("Thằng nhóc nuốt nước bọt, thân thể cứng ngắc."),t("br"),t("br"),n._v("May mắn hắn bởi vì thực lực yếu, không đi suy nghĩ nhiều trung phẩm Thánh khí sự tình, nhìn tới thật muốn suy nghĩ... Đoán chừng hiện tại chết cũng đã là hắn."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Trung niên nhân lúc này cũng theo đần độn bên trong kịp phản ứng, vội vàng tiến lên cứu người."),t("br"),t("br"),n._v("Nhìn một cái, đã hôn mê Đạo Khấu, Trương Huyền nhếch miệng."),t("br"),t("br"),n._v("Ba chiêu..."),t("br"),t("br"),n._v("Một chiêu liền đem ngươi giây, liền chút thực lực ấy, giả trang cái gì lão sói vẫy đuôi?"),t("br"),t("br"),n._v("Làm cho cùng chuyện thật giống như..."),t("br"),t("br"),n._v("Liền chán ghét loại này trang bức không hàm lượng..."),t("br"),t("br"),n._v("Mất mặt ah! Mất mặt!"),t("br"),t("br"),n._v("Nói thật, lấy thực lực của hắn bây giờ, chỉ cần đối phương câu thông nguyên thần, thi triển ra toàn lực, vẫn là chiến thắng chẳng qua."),t("br"),t("br"),n._v("Nhưng để ba chiêu... Liền đợi đến xui xẻo!"),t("br"),t("br"),n._v("Chiêu số của hắn, há lại tùy tiện để?"),t("br"),t("br"),n._v("Đừng nói một cái Nguyên Thần cảnh sơ kỳ, lúc trước Xuất Khiếu cảnh sơ kỳ Thiên Diệp Vương, cũng không dám nói để hắn, kết quả, vẫn như cũ bị tươi sống giết chết!"),t("br"),t("br"),n._v("Mặc dù lực chiến đấu của hắn, Nguyên Thần cảnh trở xuống, nhưng thủ đoạn quá nhiều, hại chết Nguyên Thần cảnh cường giả, mười phần nhẹ nhõm."),t("br"),t("br"),n._v("Than thở một tiếng, Trương Huyền nhịn không được lắc đầu, nhìn về phía phương xa, ánh mắt phiêu hốt, Cô Độc Cầu Bại."),t("br"),t("br"),n._v("“Ta ghét nhất người khác cùng ta trang bức, ngươi còn nhất định muốn đi lên, ta cũng rất bất đắc dĩ ah...”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1105"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1107"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);