(window.webpackJsonp=window.webpackJsonp||[]).push([[1340],{1695:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2177-can-nhac-kiem-phap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2177-can-nhac-kiem-phap"}},[n._v("#")]),n._v(" Chương 2177: Cân nhắc kiếm pháp")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Vốn nghĩ, đối phương y thuật, khẳng định kém xa bản thân, giờ phút này mới hiểu được, y thuật của mình cùng đối phương so, quả thực chính là đom đóm cùng hạo nhật chênh lệch."),t("br"),t("br"),n._v("Hoàn toàn không phải một cái khái niệm."),t("br"),t("br"),n._v("“Nếu nhận thua, ta có phải hay không có thể có được đại na di truyền tống trận sử dụng tư cách?”"),t("br"),t("br"),n._v("Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Đương nhiên, có điều, đừng có gấp, ta thừa nhận còn không được, còn cần đem trong chuyện này báo, tiếp đó, mới có thể cấp cho ngươi trao tặng, chậm nhất xế chiều hôm nay, lệnh phù hẳn là có thể đến trên tay ngươi!”"),t("br"),t("br"),n._v("Liêu Tân giải thích nói."),t("br"),t("br"),n._v("Tuy là trước mắt vị này, thông qua được một phương thế lực Thần Vương tiềm lực bảng ba mươi vị trí đầu khảo hạch, y thuật bên trên cũng vượt qua bản thân, nhưng nhất định phải đem tin tức báo lên tới chư thiên, lập ra xong lệnh bài, mới có thể đi vào đi truyền tống, nếu không, vẫn như cũ không được."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Đều chậm trễ đã lâu như vậy, cũng không quan tâm cái này nửa ngày, Trương Huyền gật gật đầu, không nói thêm lời, cùng Tề Linh Nhi xoay người rời khỏi."),t("br"),t("br"),n._v("Bọn họ đi rồi, gian phòng nhất thời an tĩnh lại."),t("br"),t("br"),n._v("“Ngao Hoa, hắn thật... Dùng thân thể đem ngươi đánh bại?” Liêu Tân nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Vâng!” Ngao Hoa gật đầu."),t("br"),t("br"),n._v("“Thân thể vượt qua ngươi, luyện đan để Phù Giang Thần đều phục sát đất, y thuật càng là ảo diệu vô song, ta đều xa xa không kịp... Thiên hạ thật đúng là tàng long ngọa hổ!”"),t("br"),t("br"),n._v("Liêu Tân than thở."),t("br"),t("br"),n._v("Vốn cho rằng lần này tới U Hồn thiên có thể đại khai sát giới, hành hạ đối phương tơi bời hoa lá, từ đó xác lập bất diệt uy danh, nằm mơ cũng không ngờ tới, đối phương Y Sư công hội người, cũng không thấy, cũng đã thảm bại rối tinh rối mù."),t("br"),t("br"),n._v("Chính mình cũng không phải là đối thủ, những người khác càng không cần nghĩ, thua sẽ chỉ càng nhiều."),t("br"),t("br"),n._v("“Trở về đi!” Bàn tay lớn vác tại sau lưng, Liêu Tân có chút buồn bã."),t("br"),t("br"),n._v("“Cùng U Hồn hoàng thành Y Sư công hội tỷ thí, không thể so sánh?” Vương Phi sửng sốt."),t("br"),t("br"),n._v("“Chỉ cần vị này Trương y sư, là U Hồn thiên người, coi như chúng ta thắng đối phương, cũng không vẻ vang, cùng hắn như thế, còn không bằng hào phóng một ít!”"),t("br"),t("br"),n._v("Liêu Tân nói."),t("br"),t("br"),n._v("Lần này tới U Hồn thiên mục đích rất đơn giản, khai hỏa Vân Ly thiên Y Sư công hội tên tuổi, kết quả... Cường trung tự hữu cường trung thủ, coi như đối phương Y Sư công hội không có so với chính mình đám người mạnh lại như thế nào?"),t("br"),t("br"),n._v("Chỉ cần vị này Trương Huyền tại, bọn họ vẫn như cũ là thua!"),t("br"),t("br"),n._v("Cùng hắn mất mặt như vậy, còn không bằng như vậy coi như thôi."),t("br"),t("br"),n._v("“Được...”"),t("br"),t("br"),n._v("Rõ ràng đạo lý này, đám người đồng thời đồng ý."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("U Hồn thiên Y Sư công hội."),t("br"),t("br"),n._v("“Chúc mừng Triệu Xung y sư!”"),t("br"),t("br"),n._v("Trải qua mấy vòng y sư tỷ thí, cuối cùng xác định năm người danh sách cái cuối cùng, Triệu Xung y sư!"),t("br"),t("br"),n._v("Cứ việc trước đó danh khí không hiện, y thuật lại là thực sự mạnh mẽ, một phen thao tác, làm cho tất cả mọi người đều bội phục không thôi."),t("br"),t("br"),n._v("“Triệu Xung y sư vốn là lợi hại, tự nhiên không phải cái kia luyện đan có thể so sánh!”"),t("br"),t("br"),n._v("“Đúng vậy a, vị này Trương Huyền, luyện đan lợi hại, ta thừa nhận, y thuật nha... Ha ha!”"),t("br"),t("br"),n._v("“Khác nghề như cách núi, may mắn không có để hắn so, nếu không, mất mặt khẳng định mất quá trớn...”"),t("br"),t("br"),n._v("Nhìn thấy Triệu Xung biểu hiện, đám người nhớ tới trước đó vị kia không biết trời cao đất rộng Trương đan sư, tất cả đều nở nụ cười."),t("br"),t("br"),n._v("Luyện đan lợi hại có ích lợi gì? Y sư so cũng không phải luyện đan!"),t("br"),t("br"),n._v("May mắn không có để cái tên này bên trên, nếu không, chẳng phải truyện cười bọn họ Y Sư công hội, cái gì đều phái đi ra tỷ thí?"),t("br"),t("br"),n._v("Ngay tại cảm xúc, đám người chỉ thấy một người trung niên vội vã vọt vào."),t("br"),t("br"),n._v("Tôn Tuyền hội trưởng nhíu nhíu mày: “Thế nào?”"),t("br"),t("br"),n._v("Trung niên nhân vội nói: “Vân Ly thiên y sư, toàn bộ đi...”"),t("br"),t("br"),n._v("“Đi? Có ý tứ gì? Bọn họ không thể so sánh?” Tôn Tuyền hội trưởng ngẩn ngơ."),t("br"),t("br"),n._v("Đối phương sát khí hừng hực mà đến, làm sao còn chưa bắt đầu tỷ thí, liền lui về sau?"),t("br"),t("br"),n._v("“Bọn họ nói...” Trung niên nhân nuốt ngụm nước bọt: “Tài nghệ không bằng người, lưu lại sẽ chỉ tăng thêm trò cười, cùng hắn bản thân chịu nhục, còn không bằng trước thời hạn rời khỏi...”"),t("br"),t("br"),n._v("“Tài nghệ không bằng người, có ý tứ gì?”"),t("br"),t("br"),n._v("Tất cả mọi người bối rối."),t("br"),t("br"),n._v("Đây không phải là còn không có so ư?"),t("br"),t("br"),n._v("Làm sao, liền biến thành tài nghệ không bằng người?"),t("br"),t("br"),n._v("“Vừa mới, chúng ta U Hồn thiên một vị y sư, đến bọn họ chỗ ở, đem Liêu Tân hội trưởng nhẹ nhõm đánh bại, cũng để còn lại tất cả mọi người đối hắn y thuật khuất phục... Cho nên, bọn họ mới chủ động nhận thua!”"),t("br"),t("br"),n._v("Trung niên nhân đã hỏi thăm xong rồi tin tức, biết không ít."),t("br"),t("br"),n._v("“Có người khiêu chiến, hơn nữa thắng Liêu Tân hội trưởng?”"),t("br"),t("br"),n._v("“Liêu Tân hội trưởng y thuật, trong vòng chín ngày, đều được cho khá cao đi!”"),t("br"),t("br"),n._v("“Đúng vậy a, y thuật có một không hai thiên hạ, dù sao ta kém xa tít tắp...”"),t("br"),t("br"),n._v("“Lại bị một tên y sư nhẹ nhõm đánh bại...”"),t("br"),t("br"),n._v("Một hồi xôn xao, tất cả mọi người hai mặt nhìn nhau, một lát sau, một cái y sư nhịn không được mở miệng: “Trong hoàng thành, đăng kí y sư, toàn bộ ở đây, không có một cái rời đi, là ai... Vượt qua hắn?”"),t("br"),t("br"),n._v("“Ta vừa mới chuyên môn hỏi thăm tin tức, hình như vị y sư này gọi...”"),t("br"),t("br"),n._v("Suy tư phút chốc, trung niên nhân vỗ trán một cái: “Gọi... Trương Huyền!”"),t("br"),t("br"),n._v("“Trương Huyền?”"),t("br"),t("br"),n._v("“Vị kia trước đó bị chúng ta đuổi đi... Luyện đan sư?”"),t("br"),t("br"),n._v("“Chúng ta nói hắn không có tư cách trở thành y sư, kết quả... Hắn y đạo tỷ thí vượt qua Liêu Tân hội trưởng?”"),t("br"),n._v(" “Cái này không phải là thật sao...”"),t("br"),t("br"),n._v("Tất cả mọi người đồng thời bối rối, từng cái khuôn mặt, giống như là bị người quất mấy bạt tai đồng dạng."),t("br"),t("br"),n._v("Vừa nói xong một cái luyện đan, còn muốn khảo hạch y sư, nằm mơ đều không có nghĩ đến, người ta y thuật, không chỉ có vượt xa quá đám người, càng đem Liêu Tân hội trưởng đều đánh bại dễ dàng!"),t("br"),t("br"),n._v("Tôn Tuyền khóe miệng co giật, hận không thể có một cái lỗ trực tiếp chui vào."),t("br"),t("br"),n._v("Hắn không coi trọng “Luyện đan sư”, lại có loại y thuật này... Rõ ràng là cơ hội tốt có thể học tập, liền như thế bỏ qua..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không quan tâm U Hồn hoàng thành Y Sư công hội buồn bực cùng khó chịu, lúc này Trương Huyền nhìn trên bàn một cái không lớn lệnh bài, con mắt sáng lên: “Đây là đại na di truyền tống trận lệnh phù?”"),t("br"),t("br"),n._v("Không đến buổi chiều, liền có người đem thứ này đưa tới."),t("br"),t("br"),n._v("Hài nhi lớn chừng bàn tay, phía trên điêu khắc đặc thù phù văn, càng có to lớn ý niệm chìm đắm trong đó, để cho người ta nhìn thời gian lâu dài, đều lòng sinh sợ hãi."),t("br"),t("br"),n._v("“Cái này phù văn, là Đế Quân cường giả tự thân điêu khắc, có thể chống cự đại na di trong truyền tống trận không gian bão táp...”"),t("br"),t("br"),n._v("Tề Linh Nhi ánh mắt lộ ra hâm mộ, giải thích một câu."),t("br"),t("br"),n._v("Cho dù thân là Tề gia hạch tâm, lại nắm trong tay cực lớn quyền thế, loại cấp bậc này lệnh bài, cũng không phải nàng có tư cách thu hoạch."),t("br"),t("br"),n._v("Nắm giữ thứ này, đi vào mặt khác chư thiên, chẳng qua mấy hơi thở mà thôi, chỉ cần tiền bạc đủ, hoàn toàn có thể tại cửu thiên bên trong, tùy ý ngao du."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Những phù văn này liếc mắt nhìn liền biết, không đơn giản, có lẽ cùng Đế Quân liên quan đến."),t("br"),t("br"),n._v("“Được rồi, ngươi tiếp tục an bài bán ra Phá Chướng đan cùng Mỹ Dung đan chuyện, tận lực mở rộng ảnh hưởng, để Tôn Cường chăm sóc cha mẹ ta, chỉ cần là đột phá tu vi, cần bất kỳ tài nguyên, cũng không cần keo kiệt!”"),t("br"),t("br"),n._v("Trương Huyền bàn giao."),t("br"),t("br"),n._v("Đi Linh Nguyên thiên đi tìm Lạc Nhược Hi, vô cùng có khả năng gặp được nguy hiểm, cho nên, cha mẹ đám người liền không mang, trước ở lại chỗ này cố gắng tu luyện."),t("br"),t("br"),n._v("Trải qua Kiếm Lư cường giả chỉ điểm, hai người bọn họ đối kiếm thuật hiểu, cũng càng ngày càng mạnh, mượn cái này, tu vi cũng là một ngày ngàn dặm, lại thêm Trương Huyền tài nguyên vô tận cung cấp, Phá Chướng đan, Mỹ Dung đan, đường đậu đồng dạng tuỳ ý ăn, hai người đã trùng kích vào phẩm Thiên Thần cảnh giới thành công!"),t("br"),t("br"),n._v("Đợi một thời gian, xung kích càng cao, cũng không phải không có khả năng."),t("br"),t("br"),n._v("An bài xong tất cả, Trương Huyền không tại quá nhiều chần chừ, xác định được rồi đại na di truyền tống trận vị trí, ngồi phi hành thần thú, tự mình bay đi."),t("br"),t("br"),n._v("Chỉ cần Tề gia người, vẫn như cũ hiểu lầm bản thân là “Đế Quân”, liền khẳng định sẽ dốc hết toàn lực bảo vệ, cha mẹ, Tôn Cường đám người liền sẽ không có bất kỳ nguy hiểm."),t("br"),t("br"),n._v("Ngược lại đi theo bản thân, nguy hiểm càng lớn!"),t("br"),t("br"),n._v("Mỹ Dung đan, Phá Chướng đan công hiệu cùng giá cả, khẳng định đã sớm đưa tới không biết bao nhiêu người hâm mộ, không rời đi U Hồn hoàng thành còn tốt, một khi rời khỏi, đoán chừng sẽ có vô số ánh mắt nhìn qua."),t("br"),t("br"),n._v("Cho nên, thoạt nhìn là thông qua đại na di truyền tống trận, trực tiếp đi vào Linh Nguyên thiên, nhưng trên đường đi, nhất định cũng không tưởng tượng bên trong dễ dàng như vậy."),t("br"),t("br"),n._v("Ngồi tại lưng thú bên trên, hai mắt nhắm nghiền, điều chỉnh trạng thái."),t("br"),t("br"),n._v("Đại na di trận pháp có thể na di chư thiên cường giả, cho dù mỗi cái người sử dụng, đều cần khảo hạch, cũng liền nguy hiểm nặng nề, cho nên, trận pháp này, cũng không tại trong hoàng thành, mà là thiết lập ở khoảng cách hoàng thành hơn một trăm dặm bên ngoài trên một ngọn núi phương."),t("br"),t("br"),n._v("Từ trọng binh canh gác."),t("br"),t("br"),n._v("Nói cách khác, muốn đi vào cái truyền tống trận này, trước hết cách Khai Hoàng thành, một đoạn này khoảng cách, liền vô cùng có khả năng trở thành người khác phục kích chỗ."),t("br"),t("br"),n._v("Chưởng khống Phá Chướng đan cùng Mỹ Dung đan, thì tương đương với nắm trong tay một cái to lớn túi tiền, không có khả năng không làm cho người ta điên cuồng."),t("br"),t("br"),n._v("“Khoảng cách rời khỏi thành trấn phạm vi, còn có một phút thời gian, vừa vặn nghiên cứu một chút, Thiên Thần cảnh kiếm pháp!”"),t("br"),t("br"),n._v("Rơi vào suy nghĩ."),t("br"),t("br"),n._v("Đột phá Thiên Thần thời gian cũng không dài, vẫn luôn vì hỏi thăm Lạc Nhược Hi tin tức, tăng cao tu vi mà cố gắng, không có thời gian cảm ngộ kiếm pháp, lúc này nhàn rỗi, vừa vặn có thể thử một chút."),t("br"),t("br"),n._v("Lĩnh ngộ loại này vượt qua Thiên đạo kiếm pháp, nhất định phải thân ở Thần giới bên trong, nói cách khác trong thư viện Xuân Thu đại điển, cho dù thời gian tăng tốc gấp một vạn lần, cũng không thể đối cảm ngộ có nửa phần tăng thêm."),t("br"),t("br"),n._v("“Thiên Nhược Hữu Tình công pháp, tầng thứ nhất, chủ tớ tình nghĩa, kiếm pháp, Tâm Tự Song Ti Võng, Trung Hữu Thiên Thiên Kết!”"),t("br"),t("br"),n._v("“Tầng thứ hai, tình nghĩa huynh đệ. Di Di Huynh Đệ Tình, Vỉ Vỉ Phó Ngự Lực. Kiếm pháp, Khởi Vi Sá Du Quan, Can Tràng Hiếu Tư Kích...”"),t("br"),t("br"),n._v("“Tầng thứ ba, thầy trò tình nghĩa. Lạc Hồng Bất Thị Vô Tình Vật, Hóa Tác Xuân Nê Canh Hộ Hoa!”"),t("br"),t("br"),n._v("Từ khi lĩnh ngộ ra tự mình tu luyện con đường về sau, từng tầng từng tầng cảnh giới pháp quyết tu luyện, cùng với kiếm pháp trong đầu, không ngừng khuấy động quanh quẩn một chỗ."),t("br"),t("br"),n._v("Tựa như một cái bức tranh, chậm chạp mở ra, phía trên ghi chú, một bộ mênh mông tình cảm sử."),t("br"),t("br"),n._v("Có để cho người ta kích động, có để cho người ta hạnh phúc, có để cho người ta kinh hoàng, có để cho người ta đau lòng..."),t("br"),t("br"),n._v("Ngũ vị tạp trần, đủ loại tình cảm, khích lệ người không ngừng tu luyện, không ngừng tiến lên."),t("br"),t("br"),n._v("Ưa thích, yêu quý, là tình cảm."),t("br"),t("br"),n._v("Chán ghét, chán ghét, là tình cảm."),t("br"),t("br"),n._v("Vui vẻ, đau khổ, là tình cảm."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Chỉ cần là sinh mệnh, người người đều không thể trốn Ly Tình cảm giác con đường này, mà không có sinh mệnh lời nói, Thiên đạo tồn tại ý nghĩa cũng liền không còn."),t("br"),t("br"),n._v("Cho nên, thiên nhược hữu tình thiên diệc lão!"),t("br"),t("br"),n._v("Thiên đạo có thể sẽ bị phá hủy, mà tình cảm, chỉ cần tồn tại sinh mệnh, liền nhất định tồn tại."),t("br"),t("br"),n._v("Thầy trò tình nghĩa, là người thân, cũng là người thừa kế..."),t("br"),t("br"),n._v("Để cho người ta khuấy động lòng dạ, không cách nào từ chối."),t("br"),t("br"),n._v("“Ta hiểu...”"),t("br"),t("br"),n._v("Trong lòng hơi động, một đạo hiểu ra xuất hiện tại trong óc, đang muốn đem kiếm pháp thôi diễn đi ra, liền cảm thấy toàn thân lông tơ bỗng nhiên nổ lên, bàn chân bỗng nhiên một bước."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Dưới chân phi hành thần thú đột nhiên nổ tung, mất đi tọa kỵ, cả người lập tức từ không trung rơi xuống dưới mà xuống."),t("br"),t("br"),n._v("Hoành Tảo Thiên Nhai nói"),t("br"),t("br"),n._v("Năm nay năm bản mệnh, một mực các loại không thuận. Cha sau khi qua đời, ở nhà thủ năm bảy, con dâu ở nhà một mình đột nhiên té xỉu, đánh 120 đi bệnh viện. Không có cách nào vội vã mua vé máy bay chạy trở về, đợi mấy ngày lại chạy về quê quán. Cha sự tình xử lý xong, hiện tại theo quê quán trở về, mang con dâu đến lan châu xem bệnh, chân chính cảm nhận được xem bệnh khó, quá phiền toái. Trước thời hạn đăng ký chừng mấy ngày, xếp hàng mấy giờ nhìn thấy bác sĩ, đối phương nói, chụp ảnh con, tiếp đó mở một đống lớn phiến tử. Tiếp đó chụp ảnh con các loại xếp hàng, chờ kết quả các loại xếp hàng, cầm tới kết quả, lại muốn lần nữa đăng ký, trước kia bác sĩ chẳng phải xem bệnh... Dù sao tháng này đều đang chơi đùa, chương mới sẽ thả chậm, mọi người thứ lỗi. Mặt khác, không có gì bất ngờ xảy ra, quyển sách tháng sau hoặc là tháng sau nữa liền trọn bộ rồi."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2191"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2193"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);