(window.webpackJsonp=window.webpackJsonp||[]).push([[1347],{1702:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2184-cho-phuong-cuu-ca-chua-benh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2184-cho-phuong-cuu-ca-chua-benh"}},[n._v("#")]),n._v(" Chương 2184: Cho Phượng Cửu Ca chữa bệnh")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Trải qua danh sư đại lục, Thượng Thương cùng Thần giới ba cái thế giới, Trương Huyền biết trong đó chênh lệch."),t("br"),t("br"),n._v("Linh khí càng lúc càng nồng nặc, đặc biệt là đến Thần giới, không chỉ là tốc độ thời gian trôi qua có biến hóa, càng quan trọng hơn là, linh khí trong đó so danh sư đại lục, dày đặc không biết gấp bao nhiêu lần, căn bản không tại một cái ngang nhau cấp."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, người sau sinh mệnh, mới có thể vừa ra đời, đạt đến Ngụy Thần cảnh, hơi tu luyện thành tựu Thần Minh."),t("br"),t("br"),n._v("Dị Linh tộc nhân, sát lục chi khí nồng đậm, cho người ta một loại nóng nảy cảm giác, nhưng hấp thu Thượng Thương thủy ngân linh khí, liền có thể nhận được giảm bớt, trở nên cùng bình thường sinh mệnh đồng dạng, đến Thần giới, càng là như vậy."),t("br"),t("br"),n._v("Lưu Dương thân là Dị Linh chi Hoàng, đi vào Thần giới sau đó, bất kể như thế nào nghịch hướng vận chuyển Thiên đạo tinh giản công pháp, đều cùng người bình thường đồng dạng, không có bất kỳ cái gì đặc thù."),t("br"),t("br"),n._v("Cũng liền chứng minh, sát lục chi khí tai hoạ ngầm, đã triệt để loại bỏ."),t("br"),t("br"),n._v("Vì sao trước mắt người thanh niên này trong cơ thể còn có?"),t("br"),t("br"),n._v("Tràn đầy nghi ngờ hướng về phía trước phủ đệ nhìn lại, trước cửa người đến người đi, còn có hộ vệ đứng sừng sững, nhìn một hồi, có thể xác định, những người này, cùng vị này Phượng Cửu Ca huyết mạch tương đồng, nhưng đồng dạng không có sát lục chi khí."),t("br"),t("br"),n._v("“Linh Thần là bộ tộc Phượng Hoàng lão tổ, Bất Tử Thiên Phượng, cũng là Dị Linh tộc Thần Minh... Hắn hậu bối, nắm giữ sát lục chi khí, cũng coi như bình thường...”"),t("br"),t("br"),n._v("Trong lòng phỏng đoán."),t("br"),t("br"),n._v("Dị Linh tộc nhân, tôn xưng Bất Tử Đế Quân vì Linh Thần, như vậy Bất Tử Đế Quân hậu bối, cũng cùng Dị Linh tộc nhân đồng dạng, nắm giữ sát lục chi khí, cũng không tính để cho người ta kinh ngạc."),t("br"),t("br"),n._v("Chỉ là..."),t("br"),t("br"),n._v("Vì sao chỉ có vị này trên người có, những người khác trên người hay không?"),t("br"),t("br"),n._v("Nhìn một hồi, Minh Lý Chi Nhãn dò xét không ra, Trương Huyền chần chờ một chút, bưng rượu lên hồ, sải bước hướng đối phương bàn đi tới."),t("br"),t("br"),n._v("Ngồi tại đối diện, cũng không nói chuyện, đồng dạng uống rượu."),t("br"),t("br"),n._v("Phượng Cửu Ca nhíu nhíu mày, lộ ra một tia không vui: “Nếu như là Phượng Triêu cùng Phượng Tường phái tới, tiếp tục chê cười ta, còn xin rời khỏi! Ta hiện tại là biến thành phế vật, nhưng tương tự không phải là cái gì người cũng có thể đùa cợt!”"),t("br"),t("br"),n._v("“Ta nghe nói trong tộc đại bỉ, chuyên môn chạy tới, trong lúc vô tình nghe được ngươi chuyện! Không có ác ý, chỉ là cùng ngươi uống chút rượu mà thôi...”"),t("br"),t("br"),n._v("Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Ngươi cũng là Hỏa Phượng nhất tộc người?”"),t("br"),t("br"),n._v("Phượng Cửu Ca nghi hoặc."),t("br"),t("br"),n._v("“Xem như thế đi!” Cũng không giải thích, Trương Huyền tò mò nhìn qua: “Ta gặp ngươi cơ sở rất tốt, thân thể cũng không có gì lợi hại thương thế, vì sao tu vi sẽ dần dần hạ thấp?”"),t("br"),t("br"),n._v("“Hừ!” Vốn là còn chút cảm tình, nghe được Trương Huyền nói như vậy, là đang cố ý đào nỗi đau của hắn, Phượng Cửu Ca sầm mặt lại."),t("br"),t("br"),n._v("Tuy là tu vi suy yếu, nhãn lực vẫn phải có."),t("br"),t("br"),n._v("Trước mắt vị này tu vi, so với hắn thời kỳ toàn thịnh, đều cường đại hơn, không phải như vậy, tuyệt đối đã sớm trở mặt."),t("br"),t("br"),n._v("“Ngươi không cần nghi ngờ, tại hạ không chỉ có là Hỏa Phượng nhất tộc tộc nhân, còn là một vị cao minh y sư, xem ngươi tình huống kỳ lạ, lúc này mới không chịu được hỏi thăm, có lẽ có thể giúp ngươi một chút!”"),t("br"),t("br"),n._v("Nhìn ra địch ý của hắn, Trương Huyền hơi hơi một chút, đem bản thân đại na di truyền tống trận lệnh phù đưa tới."),t("br"),t("br"),n._v("Cái này mặc dù là truyền tống đồ vật, nhưng cũng ghi chú chủ nhân, một cái nào đó phương diện đạt được thành tựu."),t("br"),t("br"),n._v("Phượng Cửu Ca đồng tử co rụt lại, lần nữa nhìn tới, không còn trước đó căm thù, mà là lộ ra chút kích động."),t("br"),t("br"),n._v("Đại na di truyền tống trận sử dụng quyền hạn, có bao nhiêu khó, thân là gia tộc tử đệ, hắn biết rất rõ ràng, lúc trước lợi hại nhất thời điểm, cũng không chiếm được, trước mắt vị này, dựa vào y thuật có thể đạt được..."),t("br"),t("br"),n._v("Không cần nghĩ, y thuật cao, coi như tại cửu thiên phạm vi bên trong, đều tuyệt đối có thể sắp xếp bên trên khá cao."),t("br"),t("br"),n._v("“Ta cũng không biết chuyện gì xảy ra!” Đặt chén rượu xuống, Phượng Cửu Ca lắc đầu: “Từ khi lần kia thí luyện sau đó, thân thể liền giống như biến thành cái phễu đồng dạng, bất luận hấp thu bao nhiêu linh khí, đều không thể dự trữ, tu vi cũng duy trì không được, không ngừng tiêu tán... Ngắn ngủi hai năm ở giữa, liền biến thành cái dạng này!”"),t("br"),t("br"),n._v("“Không cách nào dự trữ?”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày: “Có thể hay không đánh một quyền cho ta xem một chút?”"),t("br"),t("br"),n._v("“Làm sao không có thể!” Biết người trước mắt này, là cái lợi hại y sư, Phượng Cửu Ca không dám giấu dốt, nắm đấm xiết chặt, bỗng nhiên hướng về phía trước nứt ra."),t("br"),n._v("Cự long ra biển, diều hâu ra tổ, cho người ta một loại cực mạnh nghiền ép, chẳng qua... Trước nắm đấm vào một nửa, liền ngừng lại, hình như lực lượng kém, bình thường võ kỹ không thi triển ra được đồng dạng."),t("br"),t("br"),n._v("Sắc mặt trắng nhợt, Phượng Cửu Ca rên khẽ một tiếng."),t("br"),t("br"),n._v("t r u"),t("br"),n._v("y e n c u a t u i . Ｖ n  Loại vũ kỹ này, lúc trước hắn, tuỳ ý sử dụng ra, một hơi vung vẩy mấy trăm chiêu, cũng sẽ không cảm thấy lực lượng không đủ, mà bây giờ, một quyền đều không có hoàn thành, liền dừng lại, buồn bực trong lòng, sắp bùng nổ."),t("br"),t("br"),n._v("Hai năm trước hắn... Quả thực đã không tồn tại nữa."),t("br"),t("br"),n._v("Trong lòng coi như dù không cam lòng đến đâu, cũng không có cách nào."),t("br"),t("br"),n._v("“Xấu hổ, ta lại chuẩn bị một chút...” Than thở một tiếng, nhìn về phía thanh niên trước mắt."),t("br"),t("br"),n._v("“Không cần...” Trương Huyền đánh gãy hắn, tinh thần tập trung ở trong thư viện."),t("br"),t("br"),n._v("Thư tịch đã xuất hiện, ghi chép cặn kẽ thân thể đối phương tình huống."),t("br"),t("br"),n._v("“Căn cứ phía trên thiếu hụt nhìn, không có bất kỳ cái gì một chỗ, có thể ảnh hưởng tu vi của hắn...”"),t("br"),t("br"),n._v("Trương Huyền lông mày khóa chặt."),t("br"),t("br"),n._v("Thư tịch bên trên ghi chép vị này Phượng Cửu Ca trên người thiếu hụt rất nhiều, nhưng không có một chỗ, liên quan tới tu vi rớt xuống, nói cách khác, thư viện cũng không phát giác, hắn không thể tu luyện, hơn nữa tu vi rớt xuống nguyên nhân!"),t("br"),t("br"),n._v("Cái này kì quái."),t("br"),t("br"),n._v("Đi vào Thần giới về sau, thư viện tấn cấp, phong hào Thần Vương thiếu hụt đều có thể nhìn thấu, làm sao đối phương, ngược lại không thể nhận ra cảm giác?"),t("br"),t("br"),n._v("“Có thể hay không để ý, ta giúp ngươi dò xét mạch đập?”"),t("br"),t("br"),n._v("Thư viện điều tra không ra, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Đa tạ, dù sao đều quen thuộc, không giải quyết được cũng không có gì...”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, Phượng Cửu Ca vốn mang theo hi vọng ánh mắt, lần nữa ảm đạm xuống, cười khổ lắc đầu."),t("br"),t("br"),n._v("Bị thương tu vi lui về phía sau sau đó, hắn đi tìm không biết bao nhiêu y sư, Bất Tử thiên nổi danh nhất đều nhìn qua, đáng tiếc, đều không có tìm tới nguyên nhân."),t("br"),t("br"),n._v("Trước mắt vị này tuy là y thuật dũng mãnh, chỉ sợ cũng cùng trước đó đồng dạng, không có biến hoá quá lớn."),t("br"),t("br"),n._v("“Nhìn một chút mà thôi, sẽ không tổn thất cái gì!” Trương Huyền nói."),t("br"),t("br"),n._v("“Này ngược lại là... Ta như bây giờ, cũng không có gì có thể mất đi...” Phượng Cửu Ca lắc đầu."),t("br"),t("br"),n._v("Gia tộc tử đệ thân phận đều bị tước đoạt, từ nay về sau, một người cô đơn, lại không có gì có thể mất đi, đối phương loại thực lực này, thật muốn hại người, một cái ý niệm trong đầu là đủ rồi, không cần thiết khách khí như thế."),t("br"),t("br"),n._v("Nói xong đưa qua cổ tay."),t("br"),t("br"),n._v("Trương Huyền dựng đi tới, tinh thần khẽ động, một đạo Thiên đạo chi khí, lặng yên không tiếng động chui vào, đè lên huyết mạch, ở trong người chảy xuôi."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Ngón tay vừa mới liên lụy đối phương cổ tay, Trương Huyền lập tức cảm thấy đi vào trong cơ thể Thiên đạo chân khí, dọc theo kinh mạch du tẩu nói nào đó một chỗ, giống như là phát hiện cái gì, đột nhiên ngừng lại, đang muốn nhìn một chút chuyện gì xảy ra."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Chân khí tiêu tán mở ra, biến mất không thấy hình bóng, hình như chưa hề xuất hiện qua đồng dạng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Đồng tử co rụt lại, Trương Huyền biến sắc."),t("br"),t("br"),n._v("Thiên đạo chân khí từ trước tới nay mọi việc đều thuận lợi, đổi thành Thiên Nhược Hữu Tình công pháp, uy lực càng hơn, hiện tại đi vào đối phương trong cơ thể, vậy mà trực tiếp biến mất, hình như bị cái gì triệt tiêu đồng dạng, đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2198"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2200"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);