(window.webpackJsonp=window.webpackJsonp||[]).push([[1349],{1703:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-211-bai-su-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-211-bai-su-ha"}},[n._v("#")]),n._v(" Chương 211: Bái sư (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Muốn sát hạch nhị tinh, ngoại trừ có hai loại phụ trợ nghề nghiệp bên ngoài, tu vi ít nhất còn cần đạt đến Tông Sư cảnh giới, chúng ta hiện tại tuổi cùng trạng thái tu luyện, bái phỏng qua không ít người, đều không thể thành công, hy vọng duy nhất chính là Dương sư!” "),t("br"),t("br"),n._v(" Trang Hiền cũng gật đầu."),t("br"),t("br"),n._v(" Trở thành Tông sư, tuổi thọ tăng cường, đây là bất luận người nào đều không thể từ chối mê hoặc, bọn họ vây ở nửa bước Tông sư không biết bao lâu, cũng từng chuyên môn bái phỏng qua nhị tinh Danh sư, đáng tiếc, đều không có cách nào đột phá."),t("br"),t("br"),n._v(" Mấy ngày trước đây nhìn thấy Dương sư liền lập tức sẽ chết Thẩm Hồng đều thành công cứu sống, đạt đến Tông Sư cảnh giới, cũng không ngồi yên được nữa."),t("br"),t("br"),n._v(" Thẩm Hồng đều được, bọn họ khẳng định càng làm!"),t("br"),t("br"),n._v(" Bất quá, tiền đề là... Dương sư chịu dạy."),t("br"),t("br"),n._v(" “Bất kể như thế nào, lại đi thử xem, có lẽ liền có thể thành công.” "),t("br"),t("br"),n._v(" Trịnh Phi nói."),t("br"),t("br"),n._v(" Đối phương giúp hắn đột phá đến nửa bước Tông sư, hắn thì có ý bái sư, chỉ là không có đáp ứng, không có quá nhiều cưỡng cầu."),t("br"),t("br"),n._v(" Hiện tại liền Lưu sư, Trang sư cũng động tâm, tự nhiên không cam lòng lạc hậu."),t("br"),t("br"),n._v(" “Tốt, đi xem xem đi!” "),t("br"),t("br"),n._v(" Nghĩ đến điểm này, tam đại Danh sư không do dự nữa, nhanh chân hướng phủ đệ phương hướng đi đến."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" “Lục huynh, dược mua được, ngươi có muốn hay không suy nghĩ thêm một chút, lẽ nào thật sự muốn dùng?” "),t("br"),t("br"),n._v(" Lục Tầm lớp học, Vương Siêu nhìn trước mắt bạn tốt, tràn đầy lo lắng."),t("br"),t("br"),n._v(" Những đan dược này, tuy rằng có thể khiến người ta trong thời gian ngắn sức mạnh tăng lên dữ dội, nhưng đối với thân thể thương tổn cũng là rất lớn."),t("br"),t("br"),n._v(" “Yên tâm đi, ta tự có chừng mực.” Tiếp nhận đan dược, Lục Tầm chần chờ một chút, vẫn là dặn dò một tiếng."),t("br"),t("br"),n._v(" Chỉ chốc lát, mấy học sinh liền đi tới, chính là sư giả bình trắc trong danh sách mấy vị."),t("br"),t("br"),n._v(" Chu Hồng, Mạc Hiểu, Đỗ Lỗi, Bạch Siêu, Khổng Kiệt!"),t("br"),t("br"),n._v(" “Lão sư!” "),t("br"),t("br"),n._v(" Mấy người đứng tại chỗ, khí tức hùng hậu, ngăn ngắn nửa tháng tu luyện, bọn họ cùng Trịnh Dương mấy người như thế, cũng tất cả đều đạt đến võ giả nhị trọng Đan Điền cảnh."),t("br"),t("br"),n._v(" Tuy nói võ giả nhất trọng Tụ Tức cảnh, không có quá nhiều đường tắt có thể đi, nhưng này mấy cái cùng Vương Dĩnh mấy người không giống, tại bái sư trước, tu vi bên trên cơ bản đều đạt đến nhất trọng đỉnh phong, chỉ kém một cái đột phá."),t("br"),t("br"),n._v(" Coi như Khổng Kiệt hơi hơi hơi yếu một chút, đủ loại tài nguyên trút xuống hạ, cũng thuận lợi thăng cấp."),t("br"),t("br"),n._v(" “Này có một viên đan dược, ăn vào phía sau, có thể mở rộng đan điền, để cho các ngươi đêm nay liền có thể đột phá, đạt đến võ giả tam trọng Chân Khí cảnh! Như vậy, sư giả bình trắc sẽ không có sơ hở nào, khẳng định thắng lợi.” "),t("br"),t("br"),n._v(" Giơ lên trong tay đan dược, Lục Tầm nhìn quanh một vòng, cũng không có ẩn giấu, đem thiếu hụt cũng nói ra."),t("br"),t("br"),n._v(" “Bất quá, trên đời không có đến không cơm trưa, có thể khiến người ta nhanh chóng đột phá, cũng có nhất định mầm họa, vậy thì là đạt đến Chân Khí cảnh phía sau, tốc độ tu luyện giảm bớt, chỉ có trước 70%.” "),t("br"),t("br"),n._v(" “Loại này mầm họa, chỉ cần ta thành Danh sư, sẽ giúp các ngươi giải quyết, còn có thể thu làm thân truyền, chỉ điểm tu luyện về sau.” "),t("br"),t("br"),n._v(" “Đương nhiên... Làm sao lựa chọn, dựa vào chính các ngươi, ta sẽ không ép buộc, thật muốn thua, cũng chỉ có thể biểu thị, chúng ta thầy trò duyên phận đã hết!” "),t("br"),t("br"),n._v(" Nói xong đem đan dược thả xuống, nhìn quanh một vòng."),t("br"),t("br"),n._v(" Hắn tuy rằng làm ra quyết định, thậm chí đã đem đan dược mua được, làm sao lựa chọn, vẫn là giao cho học sinh bản thân, ép buộc dùng, coi như hắn rất muốn đạt được thắng lợi, cũng là làm không được."),t("br"),t("br"),n._v(" Lão sư có lão sư điểm mấu chốt."),t("br"),t("br"),n._v(" Lão sư có lão sư tôn nghiêm."),t("br"),t("br"),n._v(" Dạy khóa nhiều năm như vậy, trả không làm được vì thắng lợi không chừa thủ đoạn nào."),t("br"),t("br"),n._v(" “Ta tin tưởng Lục lão sư.” Chần chờ không bao lâu, Chu Hồng bước lên trước."),t("br"),t("br"),n._v(" Bình thường tu luyện, xung kích đến Chân Khí cảnh, không có hai, thời gian ba năm rất khó làm được, viên thuốc này có thể khiến người ta một buổi tối liền đạt đến, coi như sau đó tu luyện chịu ảnh hưởng thì lại làm sao?"),t("br"),t("br"),n._v(" Tu luyện ai có thể bảo đảm thuận buồm xuôi gió?"),t("br"),t("br"),n._v(" Hơn nữa, một khi trở thành thân truyền học viên, điểm ấy vấn đề liền không tính là gì."),t("br"),t("br"),n._v(" Đồng dạng là học sinh, giảng bài và thân truyền, vẫn có khác biệt rất lớn."),t("br"),t("br"),n._v(" Giảng bài, mấy trăm người đồng thời lên lớp, không cách nào châm đối với tu vi của chính mình làm ra chỉ điểm, cơ bản đều là tự mình tìm tòi tiến lên; Thật giống như bát tô món ăn, tương tự đồ gia vị, mùi vị lại chỉ có thể vào họng."),t("br"),t("br"),n._v(" Thân truyền không giống, giống như nồi nhỏ nấu nướng, lão sư sẽ thời khắc chỉ điểm, đơn độc chỉ đạo, bảo đảm tu luyện không nghiêng lệch, coi như bởi vì đan dược vấn đề, không có hiện tại tiến bộ nhanh, có loại này chỉ điểm, tiền đồ cũng sẽ càng thêm huy hoàng."),t("br"),t("br"),n._v(" Nghĩ tới đây, cái thứ nhất biểu lộ rõ ràng thái độ, tiếp nhận đan dược liền nuốt xuống."),t("br"),t("br"),n._v(" Thuốc vào miệng, lập tức hòa tan ra, hình thành một luồng đặc thù sức mạnh ở trong người xung kích."),t("br"),t("br"),n._v(" Thấy hắn nhanh như vậy làm ra quyết định, Lục Tầm thoả mãn gật gù, bàn tay kề sát ở đối phương phía sau lưng, chân khí chầm chậm đưa vào, dẫn dắt nguồn sức mạnh này."),t("br"),t("br"),n._v(" Không biết qua bao lâu, một tiếng nổ vang, Chu Hồng đột nhiên đứng lên, một ngụm trọc khí phun ra, khí tức như cầu vồng."),t("br"),t("br"),n._v(" Võ giả tam trọng, Chân Khí cảnh!"),t("br"),t("br"),n._v(" Quả nhiên cùng trước nói như thế, dùng viên thuốc này, trực tiếp để tu vi gia tăng rồi ròng rã một cấp bậc."),t("br"),t("br"),n._v(" “Thật là lợi hại... Ta phải thử một chút!” "),t("br"),t("br"),n._v(" Thấy hắn thành công, Mạc Hiểu, Đỗ Lỗi, Bạch Siêu, Khổng Kiệt mấy người cũng không nhịn được nữa, nhao nhao về phía trước."),t("br"),t("br"),n._v(" Chu Hồng nghĩ thông suốt, mấy người khác cũng hiểu được."),t("br"),t("br"),n._v(" Nếu như Lục lão sư thật thành Danh sư, làm thân truyền, bọn họ nhất định nhất phi trùng thiên, đây là một loại lựa chọn, không có cái gì có thể băn khoăn."),t("br"),t("br"),n._v(" Thời gian không lâu, mấy người cũng tất cả đều nhao nhao đột phá."),t("br"),t("br"),n._v(" “Tốt, tốt!” "),t("br"),t("br"),n._v(" Nhìn năm vị học viên đều đạt đến võ giả tam trọng Chân Khí cảnh, Lục Tầm con mắt tỏa ánh sáng."),t("br"),t("br"),n._v(" Đừng nói là tân sinh, coi như tại học viện hai ba năm học sinh cũ bên trong, thực lực như vậy cũng có thể khá cao."),t("br"),t("br"),n._v(" “Ngày mai sẽ là ta Lục Tầm biểu diễn thời khắc, đến thời điểm, ta sẽ khiếp sợ toàn bộ học viện, làm cho tất cả mọi người sau lưng nghị luận âm thanh, tất cả đều ngậm miệng!” "),t("br"),t("br"),n._v(" Nắm đấm xiết chặt, Lục Tầm ban ngày bị đánh tự tin, lần thứ hai khôi phục như cũ, hào khí ngất trời."),t("br"),t("br"),n._v(" Thư họa Tông sư, tinh thông trà đạo tính là gì?"),t("br"),n._v("  Giảng bài bên trên, ta Lục Tầm... Không kém gì bất luận người nào!"),t("br"),t("br"),n._v(" Ba vị Danh sư, ta cũng hướng các ngươi chứng minh, ta mới là học đồ lựa chọn tốt nhất!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" “Lão gia, ba vị Danh sư đã chờ đợi hơn nửa ngày rồi.” "),t("br"),t("br"),n._v(" Mới vừa trở lại phủ đệ, Tôn Cường liền tiến lên đón."),t("br"),t("br"),n._v(" “Bọn họ đến rồi?” "),t("br"),t("br"),n._v(" Còn muốn một lúc nữa có muốn hay không phái người tìm bọn họ lại đây, không nghĩ tới ba tên này so với mình còn muốn tích cực, Trương Huyền nhẹ nhàng nở nụ cười."),t("br"),t("br"),n._v(" Đi vào phòng tiếp khách, quả nhiên thấy ba vị Danh sư ngồi ở trong đó, thấy hắn đi tới, tất cả đều hưng phấn con mắt tỏa ánh sáng."),t("br"),t("br"),n._v(" “Lưu Lăng (Trang Hiền, Trịnh Phi) gặp qua Dương sư!” "),t("br"),t("br"),n._v(" Ba người gấp vội vàng khom người."),t("br"),t("br"),n._v(" “Không biết ba vị muộn như vậy tìm Dương mỗ chuyện gì?” "),t("br"),t("br"),n._v(" Trương Huyền ngồi xuống, để người rót chén trà, chậm rãi uống một hớp, lúc này mới vẻ mặt hờ hững nhìn sang."),t("br"),t("br"),n._v(" Đi tới một chuyến Điền phủ, trở về một chuyến học viện, giờ khắc này sáng sớm đã đại đen, thời gian không còn sớm."),t("br"),t("br"),n._v(" “Dương sư, ba người chúng ta thành khẩn bái ngươi làm thầy, mong rằng có thể thu xuống!” "),t("br"),t("br"),n._v(" Lưu Lăng mấy người vội vàng đứng lên, hai tay ôm quyền, khom người đến cùng, căng thẳng nhìn sang."),t("br"),t("br"),n._v(" Nghe đạo có trước sau, thuật nghiệp có chuyên nghiên cứu, bọn họ tuổi tuy rằng so vị này Dương sư đại rất nhiều, nhưng đối với phương rõ ràng so với bọn họ lợi hại hơn nhiều, bái sư, không thể chỉ xem tuổi."),t("br"),t("br"),n._v(" “Muốn bái ta làm thầy?” "),t("br"),t("br"),n._v(" Nâng chung trà lên, nhẹ lau bên trên trôi nổi lá trà, Trương Huyền khuôn mặt không có một chút biến hoá nào."),t("br"),t("br"),n._v(" Có như thế những ngày qua ngụy trang kinh nghiệm, đã không phải trước loại này rất dễ dàng bị người nhìn ra kẽ hở đến lông đầu tiểu tử, nhất cử nhất động, đều tự mang phong độ cùng uy nghiêm."),t("br"),t("br"),n._v(" Gian phòng nặng nề xuống."),t("br"),t("br"),n._v(" “Vâng, ba người chúng ta tuy rằng bất tài, nhưng có thể phụng dưỡng xung quanh, vì lão sư phân ưu giải nạn.” "),t("br"),t("br"),n._v(" Lưu Lăng mấy người hô hấp dồn dập."),t("br"),t("br"),n._v(" Mấy lần trước lại đây, tuy rằng cũng đều biểu thị muốn bái sư ý đồ, lại không nói thẳng, lần này nói thẳng ra, một khi bị từ chối, liền lại không có đường lùi."),t("br"),t("br"),n._v(" “Nếu như ta không nhìn lầm, muốn bái ta làm thầy, là bởi vì tu luyện đạt đến bình cảnh, không cách nào đột phá, mà một khi trở thành học sinh của ta, có lẽ liền có thể phá tan cuối cùng này cửa ải, tiến thêm một bước nữa đi.” "),t("br"),t("br"),n._v(" Cũng không ngẩng đầu lên, Trương Huyền âm thanh không có một chút nào sóng lớn, trầm tĩnh giống như hàn băng."),t("br"),t("br"),n._v(" “Ây... Là!” "),t("br"),t("br"),n._v(" Bị vạch trần mục đích, Lưu Lăng đám người sắc mặt đồng thời một đỏ."),t("br"),t("br"),n._v(" “Vẫn tính thành thực!” "),t("br"),t("br"),n._v(" Thấy bọn họ không có phủ nhận, Trương Huyền gật đầu."),t("br"),t("br"),n._v(" “Mong rằng Dương sư tác thành!” "),t("br"),t("br"),n._v(" Lưu Lăng mấy người cắn răng một cái."),t("br"),t("br"),n._v(" Trương Huyền không hề trả lời, chậm rãi để chén trà xuống, đứng dậy, đi tới trong phòng khách ở giữa."),t("br"),t("br"),n._v(" Ngay ở ba vị Danh sư, không biết hắn đến cùng muốn làm gì thời điểm, bình thản âm thanh tiếng vang lên."),t("br"),t("br"),n._v(" “Ta Dương Huyền du lịch các nước, luôn luôn yêu thích yên tĩnh, theo không biểu lộ thân phận, chắc hẳn, các ngươi theo những vương quốc khác lại đây, cũng chưa từng nghe qua ta danh tự này đi!” "),t("br"),t("br"),n._v(" “Vâng!” "),t("br"),t("br"),n._v(" Bọn họ trước nghe được Dương Huyền danh tự này, từng chuyên môn đã điều tra, xung quanh rất nhiều Vương quốc, xác thực không có người này, vốn tưởng rằng dùng giả danh, bây giờ nhìn lại, là Dương sư điệu thấp, không muốn nhiều lời."),t("br"),t("br"),n._v(" “Nếu như ta không muốn để người ta biết, không ai biết ta là Danh sư, coi như ngươi đến Danh Sư đường đi thăm dò, cũng khẳng định không tra được.” "),t("br"),t("br"),n._v(" Trương Huyền tiếp theo mở miệng: “Nhưng ta tại Thiên Huyền Vương quốc, lại biểu lộ rõ ràng thân phận, thậm chí ra tay làm cho người ta chỉ điểm, các ngươi biết tại sao không?” "),t("br"),t("br"),n._v(" Hắn nói như vậy, là sớm trước cho đối phương đánh dự phòng châm, “Dương Huyền” vốn là giả, đi Danh Sư đường tra, đương nhiên không tra được."),t("br"),t("br"),n._v(" Bất quá, có cái này làm nền, lại không tra được, không những không cảm thấy là giả, trả sẽ cho rằng là ngưỡng mộ núi cao, sùng bái không ngớt."),t("br"),t("br"),n._v(" Liền Danh Sư đường đều không tra được... Nên lợi hại bao nhiêu?"),t("br"),t("br"),n._v(" “Chúng ta ngu muội!” "),t("br"),t("br"),n._v(" Lưu Lăng mấy người nhìn sang."),t("br"),t("br"),n._v(" Kỳ thực liên quan với điểm ấy, bọn họ cũng kỳ quái."),t("br"),t("br"),n._v(" Như thế lợi hại Danh sư, nếu như thật không muốn dính dáng tới thế tục, ai có thể biết?"),t("br"),t("br"),n._v(" Có thể một mực huyên náo toàn bộ Thiên Huyền Vương quốc người người đều biết."),t("br"),t("br"),n._v(" “Bởi vì ta du lịch nơi đây, vừa vặn gặp phải một vị không sai vãn bối, không nhịn được tâm ưa thích, thu làm thân truyền. Bất quá... Ta ở đây cũng chờ không được quá lâu, sợ hắn chịu đến oan ức, lúc này mới có ý định triển lộ thân phận, cho hắn một cái tên tuổi.” "),t("br"),t("br"),n._v(" Hai tay chắp ở sau lưng, Trương Huyền thẳng tắp đứng trong đại sảnh ở giữa, cao to giống như một tòa núi cao."),t("br"),t("br"),n._v(" “Không sai vãn bối? Thu làm thân truyền?” "),t("br"),t("br"),n._v(" Lưu Lăng mấy người liếc mắt nhìn nhau, đột nhiên nghĩ tới điều gì, con ngươi đồng thời co rút lại: “Lẽ nào, lẽ nào... Dương sư học sinh thân truyền là... Hồng Thiên học viện Trương Huyền lão sư?” "),t("br"),t("br"),n._v(" Nếu như đúng là như vậy liền nói xuôi được."),t("br"),t("br"),n._v(" Một cái hẻo lánh Vương quốc đê cấp lão sư, có thể kể ra như thế lợi hại lý luận, vẫn là thư họa Tông sư, tinh thông trà đạo, mấu chốt nhất... Bản thân ba vị Danh sư mời chào, lại không không muốn đáp ứng."),t("br"),t("br"),n._v(" E sợ cũng chỉ có là Dương sư học sinh, mới có như thế năng lực."),t("br"),t("br"),n._v(" Thấy đối phương phản ứng so với mình ngẫm lại phải nhanh, tỉnh lại phí miệng lưỡi, Trương Huyền thoả mãn gật gật đầu."),t("br"),t("br"),n._v(" “Ân, Trương Huyền là của ta học sinh thân truyền, nếu như các ngươi cũng muốn bái vào môn hạ của ta... Hắn chính là các ngươi...” "),t("br"),t("br"),n._v(" “Đại sư huynh!” "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-219"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-221"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);