(window.webpackJsonp=window.webpackJsonp||[]).push([[135],{488:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1098-khao-hach-khai-linh-su"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1098-khao-hach-khai-linh-su"}},[n._v("#")]),n._v(" Chương 1098: Khảo hạch Khải Linh sư")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1098: Khảo hạch Khải Linh sư"),i("br"),i("br"),n._v("Nói thật, hắn không đi tìm Ngô sư, không đi tìm Hàn hội trưởng, không đi tìm Chiến sư đường, đã rất biết điều, cái tên này còn nhất định muốn đem mặt đưa qua tới chờ lấy đánh..."),i("br"),i("br"),n._v("Thật đúng là để cho người ta buồn cười đây!"),i("br"),i("br"),n._v("Quên đi, đại nhân không chấp tiểu nhân, ta Trương Huyền liền là như thế có đức độ..."),i("br"),i("br"),n._v("Không tiếp tục để ý cái tên này, lần nữa nhìn về phía cách đó không xa Hứa hội phó."),i("br"),i("br"),n._v("Thấy hắn nhìn tới, mỹ phụ lông mày nhảy một cái."),i("br"),i("br"),n._v("Sở dĩ trong lời nói nghiêng về trước mắt vị này, vừa đến, là bởi vì đối phương là danh sư, thứ hai, có cái tới trước tới sau... Vốn nghĩ, Đạo Khấu thật muốn ra tay, liền lên tiếng quát tháo, nằm mơ đều không có nghĩ đến, lời nói còn không có lối ra, chiến đấu cũng đã kết thúc..."),i("br"),i("br"),n._v("Hơn nữa còn là như vậy nghiền ép, không có chút hồi hộp nào!"),i("br"),i("br"),n._v("Thánh vực nhất trọng đỉnh phong... Một quyền đánh tứ trọng cường giả hôn mê..."),i("br"),i("br"),n._v("Cái này quái thai đến cùng từ nơi nào xuất hiện?"),i("br"),i("br"),n._v("“Chẳng lẽ là... Chiến sư đường cường giả?”"),i("br"),i("br"),n._v("Chiến sư đường người từ trước tới nay bế quan tu luyện, không nguyện ý đi ra, cực ít cùng người tiếp xúc, mặc dù cùng tồn tại Thanh Nguyên thành, nhưng cũng chưa thấy qua mấy vị."),i("br"),i("br"),n._v("Bởi vậy liền xem như nàng, cũng nhận biết rất ít, trước mắt vị này, Thánh vực nhất trọng liền nhẹ nhõm đánh bại Nguyên Thần cảnh cường giả, chỉ sợ chính là một thành viên trong đó, hơn nữa địa vị không thấp."),i("br"),i("br"),n._v("Khó trách có thể tiện tay lấy ra như vậy cấp bậc Thánh khí."),i("br"),i("br"),n._v("Trước đó còn tưởng rằng, đối phương là dựa vào Danh Sư đường bảo hộ mới có thể bình yên vô ưu, hiện tại xem ra, chỉ bằng vào chính hắn thực lực, cũng không ai dám tùy tiện quấy rầy."),i("br"),i("br"),n._v("“Vị công tử này, vẫn là cẩn thận một chút cho thỏa đáng, Tề Bắc song hùng sở dĩ danh khí lớn, Lạc Địa Hùng Đạo Khấu không tính là gì, chủ yếu là ca ca hắn, Kình Thiên Hùng Tàng Nhu! Nghe nói đã đạt đến Nguyên Thần cảnh đỉnh phong, hơn nữa thủ đoạn phong phú, khó có thể đối phó...”"),i("br"),i("br"),n._v("Mặc dù biết đối phương không đơn giản, suy nghĩ một chút, vẫn là truyền âm nhắc nhở."),i("br"),i("br"),n._v("Chỉ bằng vào một cái Nguyên Thần cảnh sơ kỳ, tự nhiên không có khả năng nhận được Tề Bắc chúa tể tên tuổi, trong hai người, lợi hại chính là vị này ca ca, Kình Thiên Hùng, Tàng Nhu!"),i("br"),i("br"),n._v("Một thân tu vi sâu không lường được, liền xem như nàng đụng tới, cũng chỉ có thể đi trốn."),i("br"),i("br"),n._v("Dù sao, Khải Linh sư chỉ là giúp đỡ chức nghiệp, đối với chiến đấu tới nói, cũng không am hiểu."),i("br"),i("br"),n._v("“Đa tạ hội trưởng nhắc nhở!” Biết đối phương hảo ý, Trương Huyền mỉm cười, cũng không thèm để ý, hỏi tiếp: “Ngươi nhìn... Lúc nào thuận tiện, có thể giúp ta đem binh khí linh trí đánh thức?”"),i("br"),i("br"),n._v("“Hiện tại liền bắt đầu đi!”"),i("br"),i("br"),n._v("Không có quá nhiều dừng lại, Hứa hội phó nhẹ gật đầu."),i("br"),i("br"),n._v("Dọc theo Kim Nguyên đỉnh chuyển hai vòng, bàn tay mãnh liệt tại một chỗ nhẹ nhàng nhấn một cái."),i("br"),i("br"),n._v("Ông!"),i("br"),i("br"),n._v("Một đạo quang mang lập tức đem lô đỉnh bao phủ."),i("br"),i("br"),n._v("Trương Huyền tràn đầy khẩn trương nhìn qua."),i("br"),i("br"),n._v("Qua đại khái hơn mười phút, bàn tay thu lại, Hứa hội phó liên tục lui về phía sau mấy bước, vẻ mặt trắng bệch: “Nhìn tới ta tưởng tượng vẫn còn có chút đơn giản... Ngươi cái này lô đỉnh, nhìn không sai, hẳn là bị lôi điện công kích, mới tiến vào ngủ mê mệt, lấy ta thực lực, chỉ sợ còn không cách nào đánh thức!”"),i("br"),i("br"),n._v("“Lôi điện đối linh tính có cực lớn biến mất tác dụng, điểm ấy chắc hẳn ngươi cũng rõ ràng, nếu là nguyên nhân khác rơi vào trạng thái ngủ say, ta dùng nhiều phí chút lực lượng, cũng có thể làm được, nhưng cái này...” Hứa hội phó lắc đầu."),i("br"),i("br"),n._v("“Cái kia... Hội trưởng có biết, ai có thể đem hắn đánh thức?”"),i("br"),i("br"),n._v("Trương Huyền nhịn không được hỏi."),i("br"),i("br"),n._v("“Lôi điện không chỉ có xâm nhập linh tính, càng tại lô đỉnh bên trong mang theo sót lại, muốn kêu gọi linh tính, liền muốn vượt qua phiến khu vực này... Thật muốn làm được, chỉ sợ cần Xuất Khiếu cảnh cường giả mới có thể hoàn thành, Xuất Khiếu cảnh nguyên thần đi qua lôi điện rèn luyện, không e ngại lôi đình chi uy, còn ai... Thanh Nguyên đế quốc Khải Linh sư công hội, không có người có thể làm được!”"),i("br"),i("br"),n._v("Suy nghĩ một chút, Hứa hội phó nói."),i("br"),i("br"),n._v("Thanh Nguyên đế quốc Khải Linh sư công hội, mạnh nhất hội trưởng, cũng liền mới vừa đạt tới Nguyên Thần cảnh đỉnh phong, khoảng cách xuất khiếu còn có rất lớn một đoạn, đồng dạng không cách nào hoàn thành."),i("br"),i("br"),n._v("“Ngươi nói... Lô đỉnh bên trong có lôi đình sót lại... Tu vi không đạt tới xuất khiếu, nhưng linh hồn không e ngại lôi đình, có phải hay không cũng có thể đem hắn đánh thức?”"),i("br"),i("br"),n._v("Nghe ra trong lời nói của nàng điều kiện, Trương Huyền mày nhăn lại."),i("br"),i("br"),n._v("“Hẳn là có thể, đương nhiên, điều kiện tiên quyết là nhất định phải đối Khải Linh sư lý giải đạt tới thất tinh cấp bậc! Chỉ là... Nào có linh hồn không sợ lôi điện?” Hứa hội phó cười khổ."),i("br"),i("br"),n._v("Nguyên thần, linh hồn giấu tại trong cơ thể, thuần âm tính, nhất e ngại liền là lôi điện, cho đến trước mắt, nàng còn chưa từng nghe qua ai không đạt tới Xuất Khiếu cảnh, nguyên thần, linh hồn liền có thể không sợ lôi đình."),i("br"),i("br"),n._v("“Đối Khải Linh sư lý giải đến thất tinh cấp bậc?” Dừng lại một chút, Trương Huyền ngẩng đầu: “Hứa hội trưởng, không biết muốn quan sát các ngươi công hội sách, cần gì điều kiện?”"),i("br"),i("br"),n._v("Đã trước mắt vị này, không cách nào đánh thức Kim Nguyên đỉnh, ngược lại là có thể bản thân thử một chút."),i("br"),i("br"),n._v("Đối với lôi đình, người khác e ngại, hắn đã trải qua lôi đình chi hải cùng phong thánh kiếp, linh hồn mượt mà như ý, bình thường lôi điện, đã không có bất cứ uy hiếp gì."),i("br"),i("br"),n._v("“Đọc sách?”"),i("br"),i("br"),n._v("Không nghĩ tới hắn lại đột nhiên đưa ra vấn đề này, Hứa hội phó tràn đầy không hiểu, chần chờ một chút, vẫn là nói ra: “Chúng ta công hội sách, không giống những nghề nghiệp khác đồng dạng, nắm giữ đẳng cấp hạn chế, cấp bậc không đến, không cho phép đọc qua. Mà là giao nộp nhất định phí tổn, đều có thể nhìn, thậm chí sao chép, chỉ là không thể mang đi nguyên kiện thôi.”"),i("br"),i("br"),n._v("Trương Huyền gật đầu: “Ta nghĩ đem đạt tới thất tinh cấp bậc sách toàn bộ nhìn một lần, không biết cần giao nộp bao nhiêu!”"),i("br"),i("br"),n._v("Chỉ có thực sự trở thành đặc thù chức nghiệp, nắm giữ hoàn mỹ truyền thừa, mới có thể ngang nhau cấp hạn chế tương đối nghiêm, bình thường chức nghiệp, là không nhiều như vậy yêu cầu."),i("br"),i("br"),n._v("Có thậm chí sẽ cố ý buông ra điều kiện, dẫn tới càng nhiều người học tập, để hắn càng thêm lớn mạnh."),i("br"),i("br"),n._v("“Nhìn thấy thất tinh cấp bậc sách, cần giao nộp năm ngàn linh thạch thượng phẩm...” Tính toán một chút, Hứa hội phó nói."),i("br"),i("br"),n._v("“Cho!”"),i("br"),i("br"),n._v("Bàn tay run lên, trước mắt linh khí lấp lánh, năm ngàn viên linh thạch thượng phẩm rơi trên mặt đất."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Hứa hội phó sững sờ tại nguyên chỗ."),i("br"),i("br"),n._v("Năm ngàn linh thạch thượng phẩm, liền xem như nàng, cũng không có khả năng dễ dàng như thế lấy ra, đối phương sảng khoái như vậy, chỉ vì đọc sách... Cũng quá tài đại khí thô đi!"),i("br"),i("br"),n._v("“Còn muốn làm phiền Hứa hội trưởng mang ta tới!”"),i("br"),n._v("Hai ngàn linh thạch thượng phẩm đối với hiện tại Trương Huyền tới nói, đã không tính là gì, có thể bởi vậy học tập một cái chức nghiệp, thậm chí đem Kim Nguyên đỉnh cứu tỉnh, giá trị tuyệt đối."),i("br"),i("br"),n._v("“Tốt!”"),i("br"),i("br"),n._v("Thấy hắn vẻ mặt thành thật, không giống như là nói đùa, Hứa hội phó đem linh thạch thu hồi: “Đi theo ta!”"),i("br"),i("br"),n._v("Đem Kim Nguyên đỉnh thu vào nhẫn, Trương Huyền theo sát trên đó."),i("br"),i("br"),n._v("Ra khỏi phòng, dọc theo hành lang đi một hồi, đi tới một cái gian phòng cực lớn."),i("br"),i("br"),n._v("Chính là Khải Linh sư Tàng Thư khố."),i("br"),i("br"),n._v("“Ở đây, có thể đọc sách, cũng có thể tu luyện, nếu có lĩnh ngộ, trong phòng cũng có đối ứng khôi lỗi, nhường ngươi tiến hành khải linh, có thể mở ra đối ứng linh trí, cũng liền cho thấy, đạt đến đối ứng đẳng cấp...”"),i("br"),i("br"),n._v("Đứng ở trước cửa, Hứa hội phó đưa tới một cái lệnh bài, giới thiệu nói."),i("br"),i("br"),n._v("Khải Linh sư, tại Thanh Nguyên đế quốc mặc dù nổi tiếng, lại không phải đặc thù chức nghiệp, không có quy củ nhiều như vậy, cho dù khảo hạch, cũng đều mười phần đơn giản."),i("br"),i("br"),n._v("“Đa tạ!”"),i("br"),i("br"),n._v("Nhận lấy lệnh bài, Trương Huyền nhẹ gật đầu."),i("br"),i("br"),n._v("“Đi vào đi, mỗi ngày sang đây xem sách, muốn học khải linh nghề nghiệp, cũng không phải số ít, chỉ là có thể có thành tựu rất ít thôi!”"),i("br"),i("br"),n._v("Hứa hội phó khoát tay áo."),i("br"),i("br"),n._v("Khải Linh sư chức nghiệp như vậy kiếm tiền, mỗi ngày đều có thể dẫn tới không ít người đến đây học tập, chỉ bất quá, muốn trở thành chân chính Khải Linh sư, muốn linh hồn mạnh mẽ, tinh tế... Đây là một loại thiên phú, không phải là cái gì người nhìn một chút sách liền có thể học được."),i("br"),i("br"),n._v("Không nói thêm lời, Trương Huyền đẩy cửa đi vào."),i("br"),i("br"),n._v("Tàng Thư khố không lớn, chỉ có mấy chục sắp xếp giá sách, thoạt nhìn mấy trăm ngàn quyển sách thôi."),i("br"),i("br"),n._v("Trong phòng, quả nhiên có không ít người đang tại đọc qua đọc sách, có lẽ cùng hắn đồng dạng, cũng là muốn học tập Khải Linh sư nghề nghiệp."),i("br"),i("br"),n._v("Vì không làm cho người khác chú ý, đi tới giá sách trước mặt, Trương Huyền tiện tay rút ra một bản, giả bộ như nghiêm túc đọc bộ dáng, ánh mắt lại hướng toàn bộ giá sách nhìn sang."),i("br"),i("br"),n._v("Rầm rầm!"),i("br"),i("br"),n._v("Ánh mắt đảo qua, vô số sách, đi vào thức hải."),i("br"),i("br"),n._v("Không đến mười phút đồng hồ, mấy chục sắp xếp giá sách bên trong sách liền thu sạch vào thư viện."),i("br"),i("br"),n._v("“Chính xác!”"),i("br"),i("br"),n._v("Hơi chuyển động ý nghĩ một chút, một quyển sách xuất hiện ở trước mắt, nhẹ nhàng mở ra, nhìn sang."),i("br"),i("br"),n._v("Chính là tứ tinh Khải Linh sư Thiên Đạo tu luyện công pháp."),i("br"),i("br"),n._v("Rất mau nhìn một lần, Trương Huyền đối cái nghề nghiệp này cũng có khắc sâu hơn hiểu rõ."),i("br"),i("br"),n._v("Cái gọi là khải linh, thật ra thì liền là mượn nhờ linh hồn của mình lực lượng, trợ giúp vũ khí hoặc là tu luyện giả, thoải mái linh tính cùng linh hồn."),i("br"),i("br"),n._v("Muốn làm như vậy, đầu tiên muốn đem linh hồn của mình, trở nên không có thuộc tính, như vậy mới có thể không làm cho đối phương thân thể bài xích, thành công kích hoạt linh tính hoặc là lớn mạnh."),i("br"),i("br"),n._v("Đối với người khác mà nói, cửa thứ nhất này liền hầu như không cách nào làm được, nhưng Trương Huyền khác biệt."),i("br"),i("br"),n._v("Thiên Đạo công pháp tu luyện ra được Vu hồn, bản thân liền không có bất luận cái gì thuộc tính, nếu không, cũng không có khả năng ngụy trang thành bất luận kẻ nào, đều không bị phát hiện."),i("br"),i("br"),n._v("Đem sách nhìn xong, lực lượng linh hồn ở trong người vận chuyển một lần, liền đem tứ tinh cấp bậc Khải Linh sư phương pháp tu luyện học tập xong tất, liền nửa canh giờ đều không dùng đến."),i("br"),i("br"),n._v("“Thử một chút có hiệu quả hay không...”"),i("br"),i("br"),n._v("Đi tới gian phòng một bên, Trương Huyền quả nhiên thấy góc tường đứng vững một cái kỳ quái khôi lỗi, mọc ra tám cái cánh tay cùng bốn cái đầu, nhìn không ra đẳng cấp, cũng không biết tài liệu gì luyện chế."),i("br"),i("br"),n._v("Lười nhác vận dụng Minh Lý Chi Nhãn cùng Thiên Đạo thư viện, tinh thần khẽ động, câu thông linh hồn, một cỗ ý niệm trực tiếp lan tràn đi lên, đồng thời bàn tay tại mặt đất nhẹ nhàng nhấn một cái."),i("br"),i("br"),n._v("Ông!"),i("br"),i("br"),n._v("Một đạo quang mang đem khôi lỗi hoàn toàn bao phủ."),i("br"),i("br"),n._v("Thời gian nháy mắt, ý niệm của hắn tựa như đi vào một cái đen kịt thế giới, chỉ có thể đem hắn thắp sáng."),i("br"),i("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),i("br"),i("br"),n._v("Mấy hơi thở qua đi, trước mắt khôi lỗi liền bị kích hoạt lên linh tính, bắt đầu chuyển động, tám cánh tay cánh tay loạn vũ cùng bốn cái đầu loạn chuyển."),i("br"),i("br"),n._v("“Nhìn tới tứ tinh Khải Linh sư rất đơn giản...”"),i("br"),i("br"),n._v("Mở mắt ra, Trương Huyền cười cười."),i("br"),i("br"),n._v("Còn tưởng rằng cái nghề nghiệp này có bao nhiêu khó, dễ dàng như thế liền đem linh tính kích hoạt, nói rõ không tính phức tạp."),i("br"),i("br"),n._v("Làm xong những này, nhấc chân hướng tầng thứ hai đi đến."),i("br"),i("br"),n._v("Tầng thứ hai sách vẫn như cũ có vài chục sắp xếp, mấy trăm ngàn sách, bắt chước làm theo, rất mau nhìn xong, tạo thành ngũ tinh cấp bậc Thiên Đạo khải linh công pháp."),i("br"),i("br"),n._v("Học tập xong đi tới góc tường, đem đồng dạng một cái khôi lỗi khải linh thành công, lần nữa đi lên đi."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Sau hai canh giờ, đi tới tầng thứ tư."),i("br"),i("br"),n._v("Nơi này trưng bày đều là thất tinh cấp bậc khải linh sách, thu nhập trong óc, đem Thiên Đạo công pháp học tập một lần, lần nữa đi tới chứa đựng khôi lỗi địa phương, nhìn một cái, lông mày nhịn không được nhíu một cái."),i("br"),i("br"),n._v("“Tầng này không có khôi lỗi? Cái này cột là cái gì?”"),i("br"),i("br"),n._v("Tầng này góc tường, cũng không dùng tới khải linh khôi lỗi, chỉ có một cái to hơn một người cột, từ trên xuống dưới xuyên qua gian phòng, không biết tài liệu gì, mười phần nặng nề."),i("br"),i("br"),n._v("“Đoán chừng là kiện vũ khí, chỉ cần đem hắn khải linh, liền coi như thất tinh Khải Linh sư!”"),i("br"),i("br"),n._v("Trong lòng âm thầm phỏng đoán, đi tới trước mặt."),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1106"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1108"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);