(window.webpackJsonp=window.webpackJsonp||[]).push([[1354],{1709:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2189-cuu-phuong-đai-tran"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2189-cuu-phuong-đai-tran"}},[n._v("#")]),n._v(" Chương 2189: Cửu Phượng đại trận")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Đối phương trong cơ thể đạo kia dòng khí màu xám, chỉ thôn phệ chân khí lực lượng, cũng sẽ không thay đổi một người huyết mạch, sở dĩ trước đó đo lường không đi ra, có thể là bởi vì bị thứ này, trấn áp."),t("br"),t("br"),n._v("Liền giống như ở trên thương, danh sư đại lục, cảm giác không ra Trục Tinh cung, Dị Linh tộc nhân, nắm giữ thần linh huyết mạch đồng dạng."),t("br"),t("br"),n._v("Lúc này, khí lưu bị loại bỏ sạch sẽ, huyết mạch lực lượng hẳn là có thể khôi phục, thậm chí trải qua Thiên đạo chân khí sắp xếp sau đó, vô cùng có khả năng trở nên càng thêm tinh thuần!"),t("br"),t("br"),n._v("“Tốt a...” Phượng Cửu Ca gật đầu."),t("br"),t("br"),n._v("Tuy là đối huyết mạch đo lường biến mất, còn có thể hay không xuất hiện, ôm lấy thái độ hoài nghi, nhưng hắn đối trước mắt vị lão sư này, hoàn toàn tin tưởng và nghe theo."),t("br"),t("br"),n._v("Không tiếp tục để ý bị thương Phượng Tường, Phượng Triêu, hai người đi vào Hỏa Phượng nhất tộc, vừa tiến vào đại viện, một đám người liền xông tới."),t("br"),t("br"),n._v("Nhìn tới Phượng Cửu Ca khôi phục thực lực, đem người đả thương chuyện, đã truyền đến trong tộc, tất cả mọi người biết."),t("br"),t("br"),n._v("Chẳng qua mười mấy cái hô hấp, liền để nhiều người như vậy xông lại, không hổ là Linh Nguyên thiên tam đại gia tộc đứng đầu, tin tức truyền lại thật là nhanh."),t("br"),t("br"),n._v("“Cửu Ca, ngươi đã bị trục xuất gia tộc, lại vẫn dám xông vào đi vào, đả thương trong tộc hạch tâm đệ tử, làm sao, muốn tạo phản phải không?”"),t("br"),t("br"),n._v("Dẫn đầu một thanh niên, lông mi nâng lên, khí thế hùng hổ."),t("br"),t("br"),n._v("Bị trục xuất gia tộc, liền cùng gia tộc không quan hệ, lần nữa xông tới, tương đương với tại khiêu chiến trong tộc uy quyền."),t("br"),t("br"),n._v("“Phượng Giang, ta cảm thấy trước đó huyết mạch kiểm tra, cũng không chuẩn xác, muốn về tới lần nữa kiểm tra!” Phượng Cửu Ca nhìn qua: “Chỉ cần thông qua kiểm tra, vẫn là hạch tâm đệ tử, vẫn là tộc nhân, tộc nhân ở giữa tranh tài, luận bàn võ nghệ... Không tính là tạo phản đi!”"),t("br"),t("br"),n._v("Phượng Giang cau mày."),t("br"),t("br"),n._v("Cẩn thận hướng thanh niên trước mắt nhìn lại."),t("br"),t("br"),n._v("Ngắn ngủi một, hai canh giờ không thấy, khí tức trên thân, rõ ràng khác biệt, cho người ta một loại dày nặng cảm giác, vậy mà giống như hắn, cũng đạt tới thượng phẩm Thiên Thần đỉnh phong!"),t("br"),t("br"),n._v("Trọng yếu nhất chính là, trên người sinh ra phong thái cùng tự tin, cho người ta một loại hai năm trước cảm giác."),t("br"),t("br"),n._v("Hai năm trước, trước mắt vị này, tung hoành trong tộc, nắm giữ Thần Vương phong thái, cỡ nào khí thế, cho dù là hắn nhìn thấy, đều muốn cúi đầu hành lễ, không dám vượt quá..."),t("br"),t("br"),n._v("Hai năm phế vật, đột nhiên xoay người, giống như trước đó hình dáng, để hắn khó mà tiếp nhận."),t("br"),t("br"),n._v("“Lần nữa kiểm tra?” Híp mắt lại, Phượng Giang hừ một tiếng: “Ngươi cho rằng, gia tộc khảo nghiệm là trò đùa, muốn đo liền đo?”"),t("br"),t("br"),n._v("Trong tộc kiểm tra huyết mạch, cần tiêu hao nhất định tài nguyên, bình thường đệ tử, cả đời chỉ có một lần khảo nghiệm cơ hội, Phượng Cửu Ca cũng là đặc thù, mới bị khảo nghiệm hai lần, còn muốn tiếp tục..."),t("br"),t("br"),n._v("Thật đúng là cho rằng, vẫn là hai năm trước ah, muốn làm sao tới liền làm sao tới?"),t("br"),t("br"),n._v("“Ta biết kiểm tra không phải trò đùa, cần tiêu phí nhất định đánh đổi, đánh đổi ta ra!” Phượng Cửu Ca nói."),t("br"),t("br"),n._v("Những năm này cứ việc tu vi một mực suy yếu, vừa vặn vì Hỏa Phượng nhất tộc, đã từng Thần Vương phong thái nhân tuyển, vẫn là có nhất định tích góp, cứ việc không nhiều, dùng để kiểm tra một lần huyết mạch, thừa sức."),t("br"),t("br"),n._v("“Ngươi ra?” Hừ một tiếng, Phượng Giang đột nhiên nghĩ đến cái gì, mắt sáng lên, lộ ra nghiền ngẫm vui vẻ: “Như vậy đi, bất kể nói thế nào, ngươi đã từng đều là trong tộc hạch tâm đệ tử, muốn đo cũng được, cho ngươi một cơ hội, xông ! Có thể vượt qua, có thể lần nữa quay về trong tộc, không cách nào thông qua... Vậy ta cũng không có biện pháp!”"),t("br"),t("br"),n._v("“Không sai, xông trận ah!”"),t("br"),t("br"),n._v("“Ngươi không phải khôi phục thực lực sao? Vậy thì xông, có thể thành công, chúng ta liền công nhận ngươi, xông không qua, chỉ có thể trách bản thân, lực lượng không đủ...”"),t("br"),t("br"),n._v("Bốn phía đám người, tất cả đều nở nụ cười, từng cái tràn đầy cười trên nỗi đau của người khác."),t("br"),t("br"),n._v("“Cửu Phượng đại trận...” Biến sắc, Phượng Cửu Ca híp mắt lại."),t("br"),t("br"),n._v("“Thế nào, không dám xông vào? Không dám, vậy thì thừa nhận ở đây làm loạn! Người tới, đem hắn cầm, giải đến tra tấn đường, xem hắn đến cùng cái mục đích gì...” Phượng Giang vung tay lên."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Lập tức từ trong đám người toát ra bốn vị thanh niên, mỉm cười đi tới."),t("br"),t("br"),n._v("“Đi thôi, chúng ta ‘Đã từng’ Cửu Ca thiếu gia!”"),t("br"),n._v("“Khôi phục thực lực, không có huyết mạch, cũng chỉ là tán tu mà thôi, thế nào, thật đúng là cho rằng mất đi gia tộc che chở, còn có thể hưởng thụ trước đó đãi ngộ, nhất phi trùng thiên?”"),t("br"),t("br"),n._v("Lúc trước Phượng Cửu Ca thiếu niên đắc chí, tùy ý tung hoành, trong tộc không ít người ăn hắn đau khổ, cho nên, nhân duyên không tính quá tốt."),t("br"),t("br"),n._v("“Cửu Phượng đại trận là cái gì?”"),t("br"),t("br"),n._v("Thấy mọi người thái độ cùng Phượng Cửu Ca hình dáng, Trương Huyền nhíu nhíu mày, truyền âm tới."),t("br"),t("br"),n._v("“Bẩm báo lão sư, Cửu Phượng đại trận, là Hỏa Phượng nhất tộc bị trục xuất tông chủ đệ tử, duy nhất một lần đạt được tộc nhân công nhận phương pháp, từ trong tộc chín vị cao thủ tạo thành trận pháp! Chỉ có đem bọn hắn toàn bộ đánh bại, mới coi như thông qua...”"),t("br"),t("br"),n._v("Phượng Cửu Ca vẻ mặt khó coi: “Ta xem qua trong tộc gần ba ngàn năm lịch sử, hết thảy ba trăm mười bảy vị bị trục xuất gia tộc thế hệ sau trở về vượt quan, cuối cùng... Chỉ có ba người thông qua, những người khác... Hầu như đều chết hết!”"),t("br"),t("br"),n._v("“Chết?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Hơn ba trăm người chỉ thông qua ba cái, một phần trăm xác suất..."),t("br"),t("br"),n._v("Tuy bị trục xuất gia tộc, có thể trước cũng là tộc nhân, trở về vượt quan, không đến mức tàn nhẫn như vậy đi!"),t("br"),t("br"),n._v("“Bị gia tộc trục xuất, bình thường đều là phạm vào không thể tha thứ hành vi phạm tội, coi như có thể trở về, cũng cùng những người khác không hợp nhau, cho nên... Muốn trở về, cũng không nhận chào đón! Phụ trách khảo hạch người, mới có thể ra tay độc ác, không có chút nào nhường...”"),t("br"),t("br"),n._v("“Đã như vậy nguy hiểm, vì sao còn có người nhất định muốn trở về, muốn xông khó như vậy trận pháp?”"),t("br"),t("br"),n._v("Không phải Phượng tộc người làm sao?"),t("br"),t("br"),n._v("Ở bên ngoài không phải rất tốt sao?"),t("br"),t("br"),n._v("Tựa như bản thân, không có quay về Trương gia thời điểm, đồng dạng lăn lộn tự do tự tại."),t("br"),t("br"),n._v("“Lão sư có chỗ không biết, những người này, sở dĩ xông Cửu Phượng đại trận, là bởi vì... Bất luận thành công hay là thất bại, sau khi chết, cũng có thể táng tại mộ tổ, đời sau cũng có cơ hội trở về gia tộc! Mà không xông, hoặc là lùi bước, liền vĩnh viễn cùng Hỏa Phượng nhất tộc, tách ra!”"),t("br"),t("br"),n._v("Phượng Cửu Ca lắc đầu."),t("br"),t("br"),n._v("Trương Huyền hiểu được."),t("br"),t("br"),n._v("Lá rụng về cội, ai cũng không muốn cuối cùng sau khi chết, đều không thể trở lại trong tộc, huống chi còn dính dáng hậu bối."),t("br"),t("br"),n._v("“Cho nên, trở về vượt quan người, cơ bản đều là tuổi thọ nhanh đến đại nạn, cái gọi là vượt quan, cũng chính là cam tâm tình nguyện chịu chết... Bởi vậy, Cửu Phượng đại trận, cũng được xưng chi vì ! Bị trục xuất gia tộc người, trở về gia tộc, đi vào từ đường duy nhất thông đạo...”"),t("br"),t("br"),n._v("Phượng Cửu Ca sắc mặc nhìn không tốt: “Hắn để cho ta xông, rõ ràng là muốn giết ta!”"),t("br"),t("br"),n._v("Hắn hiện tại mới hơn hai mươi tuổi, khoảng cách đại nạn không biết bao lâu, lúc này xông vào, rõ ràng không thích hợp."),t("br"),t("br"),n._v("“Thủ quan người, tu vi đều cao bao nhiêu?”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("Cửu Phượng đại trận, chín người trấn thủ, trận pháp rất quan trọng, càng quan trọng hơn là, chín người này thực lực làm sao."),t("br"),t("br"),n._v("“Tất cả đều cùng vượt quan giả thực lực tương đồng, cũng chính là... Chín đối một, tương đồng huyết mạch bên dưới, lại phối hợp trận pháp... Làm sao có thể chiến thắng?”"),t("br"),t("br"),n._v("Phượng Cửu Ca nói."),t("br"),t("br"),n._v("“Tương đồng thực lực? Chín đối một?” Trương Huyền cười."),t("br"),t("br"),n._v("Còn tưởng rằng nhiều khó khăn, làm sao nghe tới, không tính là gì đâu?"),t("br"),t("br"),n._v("Không nói mặt khác, liền nói lúc trước Trịnh Dương khảo hạch Thánh tử, độ khó liền lớn hơn so với cái này không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2203"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2205"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);