(window.webpackJsonp=window.webpackJsonp||[]).push([[136],{489:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1099-cong-hoi-song"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1099-cong-hoi-song"}},[n._v("#")]),n._v(" Chương 1099: Công hội sống")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Mấy tầng trước, đều là bốn đầu tám tay khôi lỗi, nơi này mặc dù không có, nhưng có cái trụ đen tử, cũng hẳn là tương đồng công hiệu."),i("br"),i("br"),n._v("“Thử một chút thì biết...”"),i("br"),i("br"),n._v("Ngồi tại nguyên chỗ, bàn tay trên mặt đất nhấn một cái, một vòng ánh sáng lập tức đem cột bao phủ, cùng trước đó khải linh đồng dạng, Trương Huyền ý niệm lập tức giống như là lâm vào một vùng tăm tối bên trong."),i("br"),i("br"),n._v("“Thắp sáng!”"),i("br"),i("br"),n._v("Biết thắp sáng bóng tối, tương đương với đối vật phẩm tiến hành khải linh, trong đầu vận chuyển Thiên Đạo khải linh pháp quyết, ngón tay hướng về phía trước điểm tới."),i("br"),i("br"),n._v("Ông!"),i("br"),i("br"),n._v("Một đạo quang mang chiếu rọi mà lên, khu trừ bóng tối, chỉ bất quá ánh sáng mười phần yếu ớt, như là trong gió ngọn nến, run run rẩy rẩy, bất cứ lúc nào cũng sẽ dập tắt."),i("br"),i("br"),n._v("“Ừm?”"),i("br"),i("br"),n._v("Nhướng mày."),i("br"),i("br"),n._v("Trước đó khôi lỗi, chỉ cần vận chuyển Thiên Đạo khải linh pháp quyết, liền có thể đem tất cả địa phương chiếu sáng, mà bây giờ, chỉ chiếu sáng trước mắt, hơn nữa rất dễ dàng biến mất, cũng liền nói rõ... Kiện vật phẩm này linh tính, so với trong tưởng tượng còn muốn khó mà mở ra."),i("br"),i("br"),n._v("“Không hổ là thất tinh cấp bậc, quả nhiên độ khó lớn thêm không ít...”"),i("br"),i("br"),n._v("Biết loại tình huống này, Trương Huyền không những không cảm thấy chán nản, ngược lại mắt sáng rực lên."),i("br"),i("br"),n._v("Trước mặt mấy cái khảo hạch, một điểm kỹ thuật hàm lượng đều không có, cái này mới coi như giống có chuyện như vậy."),i("br"),i("br"),n._v("“Ta ngược lại muốn xem xem, thất tinh khảo hạch có bao nhiêu khó... Hôm nay không đưa ngươi thắp sáng không được!”"),i("br"),i("br"),n._v("Có khiêu chiến, Trương Huyền lập tức hứng thú tràn đầy."),i("br"),i("br"),n._v("Cái khác Khải Linh sư, một ngày ba lần, sau đó muốn nghỉ ngơi rất lâu mới có thể tiếp tục, hắn không giống, Thiên Đạo khải linh hầu như không có cái gì tác dụng phụ, lại thêm hồn lực vượt xa cùng cấp bậc, một ngày cho dù tới số lượng mười lần, cũng không biết mệt mỏi."),i("br"),i("br"),n._v("Ngón tay lần nữa hướng về phía trước điểm tới."),i("br"),i("br"),n._v("Lại một đạo ánh sáng nhen lửa, thoạt nhìn vẫn như cũ yếu ớt, nhưng đem bóng tối đuổi càng xa hơn chút."),i("br"),i("br"),n._v("“Dung hợp!”"),i("br"),i("br"),n._v("Linh hồn nhanh chóng vận chuyển, đem hai cái ngọn lửa tập trung ở cùng một chỗ, trước đó mỗi người thiêu đốt hỏa diễm, chậm chạp dung hợp."),i("br"),i("br"),n._v("Cái này hai nơi ngọn lửa, đều là hắn nhen lửa linh tính, ẩn chứa linh hồn của hắn ý niệm, dung hợp được không khó."),i("br"),i("br"),n._v("Đương nhiên, đây cũng là đối với hắn mà nói, Thiên Đạo khải linh nhen lửa ngọn lửa, có thể thuộc tính hoàn toàn tương đồng, bình thường Khải Linh sư liền không làm được."),i("br"),i("br"),n._v("Thuộc tính khác biệt, liền không thể dung hợp, nếu không chẳng khác nào một kiện vật phẩm bên trong xuất hiện hai cái ý niệm, cùng lúc trước Thánh vực Bồ Đề thụ đồng dạng, một khi tranh cướp lẫn nhau, làm không cẩn thận còn có thể đem trọn cái vũ khí cũng hoàn toàn hủy hoại."),i("br"),i("br"),n._v("Vù vù!"),i("br"),i("br"),n._v("Dung hợp lại cùng nhau, ngọn lửa tăng cường rất nhiều, chiếu rọi bốn phương, cứ việc còn không thể đem bóng tối toàn bộ khu trừ, nhưng cũng không giống trước đó như thế, bất cứ lúc nào cũng sẽ diệt vong."),i("br"),i("br"),n._v("“Quả nhiên có thể...”"),i("br"),i("br"),n._v("Linh tính dung hợp, cũng là hắn tâm huyết dâng trào đột nhiên nghĩ đến, không nghĩ tới quả nhiên có thể, lúc này nhẹ nhàng cười một tiếng, lần nữa hướng về phía trước điểm tới."),i("br"),i("br"),n._v("Hô hô!"),i("br"),i("br"),n._v("Lại bị điểm đốt hai đạo hỏa diễm, tiếp tục cùng trước mắt cái này tiến hành dung hợp."),i("br"),i("br"),n._v("Bốn đóa hỏa diễm dung hợp, ngọn lửa biến thành bó đuốc, hùng hồn mạnh mẽ, ánh sáng hướng bốn phía phóng xạ, chiếu rọi ít nhất hơn trăm mét phạm vi."),i("br"),i("br"),n._v("“Thật là lớn không gian...”"),i("br"),i("br"),n._v("Mượn nhờ ánh sáng, Trương Huyền lúc này cũng thấy rõ tình huống chung quanh."),i("br"),i("br"),n._v("Trước đó khôi lỗi, ý thức cảm giác được phạm vi, cũng liền vài mét, mười mấy mét, chỉ cần thắp sáng linh tính, liền có thể toàn bộ bao phủ, mà trước mắt cái này, chiếu rọi hơn trăm mét, thế mà vẫn như cũ không nhìn thấy phần cuối... Binh khí này thật là quá lớn đi!"),i("br"),i("br"),n._v("Vũ khí càng lớn, cần càng cường đại linh tính chống đỡ, thật giống như Kim Nguyên đỉnh, tất nhiên phải so Băng Vũ kiếm phải cường đại đồng dạng... Nhen lửa bốn lần linh tính, thế mà cũng không tìm tới biên giới, đủ để chứng minh cái này cột, kinh khủng tới cực điểm."),i("br"),i("br"),n._v("“Không nghĩ tới một cái thất tinh khảo hạch khó như vậy, ta không tin, còn thắp sáng không được...”"),i("br"),i("br"),n._v("Thấy bó đuốc cũng chiếu rọi không đến phần cuối, Trương Huyền sinh ra lóng háo thắng."),i("br"),i("br"),n._v("Hắn tu luyện Thiên Đạo khải linh công pháp, nếu là cũng không thông qua khảo hạch, chẳng phải thẹn với trong đầu thư viện?"),i("br"),i("br"),n._v("“Tiếp tục...”"),i("br"),i("br"),n._v("Ngón tay lần nữa điểm nhẹ, linh hồn vận chuyển tăng tốc, trước mắt lần nữa nhiều hơn mấy đạo hỏa diễm, liên tục tiêu hao lực lượng, vẻ mặt cũng hơi hơi trở nên trắng."),i("br"),i("br"),n._v("Mặc dù hắn hồn lực mạnh mẽ, khải linh số lần phải so người bình thường hơn rất nhiều, nhưng mà liên tục thi triển, đối hồn phách cũng có tổn thương rất nặng."),i("br"),i("br"),n._v("Hô hô hô!"),i("br"),i("br"),n._v("Tiếp tục đem nhen lửa linh tính dung hợp lại cùng nhau, bó đuốc biến thành lửa trại, chiếu rọi đến chỗ xa hơn, có điều, vẫn như cũ không tìm được phần cuối."),i("br"),i("br"),n._v("Chiếu không tới phần cuối, chẳng khác nào không thành công."),i("br"),i("br"),n._v("“Liều mạng...”"),i("br"),i("br"),n._v("Hàm răng cắn chặt, một ngụm tinh huyết phun ra, Trương Huyền ý niệm liều mạng kiên trì."),i("br"),i("br"),n._v("Liên tục dung hợp ba mươi hai đồ hỏa diễm về sau, trước mắt lửa trại, biến thành hùng hồn biển lửa, lại hình như là một cái cực nóng mặt trời, chiếu rọi bốn phía."),i("br"),i("br"),n._v("Ầm ầm!"),i("br"),i("br"),n._v("Đen kịt không gian một hồi lay động, rốt cục để hắn thấy rõ ràng toàn bộ tình huống."),i("br"),i("br"),n._v("Cái này vũ khí dung nạp ý thức địa phương, thế mà chừng vài dặm nhiều, khó trách linh tính khó mà mở ra."),i("br"),i("br"),n._v("Rốt cục thành công, Trương Huyền cũng cảm thấy thân thể như là hư thoát, đồng thời tràn đầy tò mò."),i("br"),n._v("Hắn tu luyện Thiên Đạo khải linh công pháp, đối Khải Linh sư lý giải, đã đạt đến thất tinh đỉnh phong, thậm chí bát tinh sơ kỳ, có thể cam đoan, Hứa hội phó đám người, khải linh tốc độ tuyệt đối kém xa tít tắp..."),i("br"),i("br"),n._v("Dù vậy, cũng suýt chút nữa mệt chết, mới lấy thành công, đây rốt cuộc là cái gì khảo hạch?"),i("br"),i("br"),n._v("Có phần quá khó khăn đi!"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Khải Linh sư công hội một cái rộng lớn gian phòng bên trong, ba cái nữ tử ngồi đối diện nhau, chính là công hội địa vị cao nhất Hứa hội phó cùng nguyễn hội trưởng, Trần hội phó."),i("br"),i("br"),n._v("“Hứa Ảnh, đến cùng chuyện gì xảy ra? Tề Bắc song hùng cũng không phải dễ trêu, Lạc Địa Hùng Đạo Khấu mặc dù thực lực bình thường, nhưng Kình Thiên Hùng Tàng Nhu lại không phải chúng ta có thể đối phó, một khi đến tìm phiền phức, chúng ta Khải Linh sư công hội, chỉ sợ lại muốn nợ Danh Sư đường nhân tình...”"),i("br"),i("br"),n._v("Nguyễn hội trưởng nhìn lại."),i("br"),i("br"),n._v("Đạo Khấu tới giúp học sinh khải linh, xung kích Nguyên Thần cảnh, kết quả cái gì đều không có làm, liền bị đánh nửa chết nửa sống... Nàng vừa đến liền nghe nói, lúc này mới vội vã đem Hứa hội phó cùng Trần hội phó đều gọi tới."),i("br"),i("br"),n._v("Tề Bắc song hùng, nếu như là cái phân rõ phải trái người, không có gì có thể e ngại, nhưng hai người này, có tiếng xấu, hiện tại lại ăn như thế thiệt thòi lớn, ngộ nhỡ gây ra cái gì sự tình, coi bọn nàng thực lực, căn bản áp chế không nổi."),i("br"),i("br"),n._v("Đến lúc đó chỉ có thể xin giúp đỡ Danh Sư đường."),i("br"),i("br"),n._v("“Là như vậy...” Hứa Ảnh phó hội trưởng đem Trương Huyền sự tình nói rõ chi tiết một lần."),i("br"),i("br"),n._v("“Ngươi nói... Cái tên này có thể là Chiến sư?” Đôi mi thanh tú nhăn lại, nguyễn hội trưởng nhẹ gật đầu: “Nếu như là như vậy liền tốt, Tề Bắc song hùng cho dù lại muốn kiêu ngạo, khẳng định cũng không dám cùng Chiến sư đường đối nghịch!”"),i("br"),i("br"),n._v("Không nói trước Chiến sư đường là Danh Sư đường một cái chi nhánh, liền nói trong đó vô số chiến lực kinh người võ giả, liền là bất kỳ một cái nào chức nghiệp cũng không dám đắc tội."),i("br"),i("br"),n._v("“Ngộ nhỡ Kình Thiên Hùng Tàng Nhu đến tìm phiền phức, ngươi liền trực tiếp đem trách nhiệm toàn bộ đẩy ngã tên kia trên thân, không cần cho chúng ta Khải Linh sư công hội gây phiền toái, chúng ta chỉ là phụ trợ chức nghiệp, chống lại chẳng qua những cường giả này...”"),i("br"),i("br"),n._v("Nguyễn hội trưởng dặn dò."),i("br"),i("br"),n._v("Khải Linh sư đại bộ phận đều là nữ tử, bởi vì các nàng tâm tư tinh tế, tu luyện, lại càng dễ lĩnh ngộ tinh túy."),i("br"),i("br"),n._v("Bản thân liền là nữ tử, lại tu luyện phụ trợ chức nghiệp, tự nhiên không thể nào là những này hung thần ác bá đối thủ."),i("br"),i("br"),n._v("Nói thật, Khải Linh sư nếu không phải dựa vào Danh Sư đường quyết định quy củ, sớm đã bị những nghề nghiệp khác bắt đi, sao có thể giống như bây giờ nở mày nở mặt."),i("br"),i("br"),n._v("Nguyên nhân chính là như vậy, các nàng cái nghề nghiệp này chỉ xây dựng ở có danh sư đường địa phương, địa phương khác, căn bản không đi, miễn cho gặp được không được dự đoán phiền phức."),i("br"),i("br"),n._v("“Được...” Hứa Ảnh phó hội trưởng nhẹ gật đầu, đột nhiên nhớ tới một việc: “Đúng rồi, người thanh niên kia còn chưa đi, nói muốn học tập thoáng cái khải linh chi thuật, bản thân đi Tàng Thư khố đọc sách đi!”"),i("br"),i("br"),n._v("“Đọc sách? Nam tử cũng muốn học tập khải linh?” Nguyễn hội trưởng lắc đầu."),i("br"),i("br"),n._v("Không phải nàng xem thường nam tử, mà là căn bản cũng không phải là bọn họ có thể làm được."),i("br"),i("br"),n._v("“Đến cùng phải hay không học tập, ta đã phái người đi xem, hẳn là lập tức liền sẽ trở về...” Hứa Ảnh phó hội trưởng cũng cùng nàng ý nghĩ đồng dạng, cũng không xem trọng Trương Huyền, có điều, hiện tại cũng qua hai ba canh giờ, còn chưa có đi ra, không khỏi có lòng hiếu kỳ."),i("br"),i("br"),n._v("Kẹt kẹt!"),i("br"),i("br"),n._v("Vừa dứt lời, một nữ tử liền vội vã đi đến."),i("br"),i("br"),n._v("Chính là Hứa Ảnh Phó hội trưởng học sinh, một vị lục tinh Khải Linh sư."),i("br"),i("br"),n._v("“Thế nào, tiểu tử kia có phải thật vậy hay không đang đọc sách?” Thấy nàng trở về, Hứa Ảnh phó hội trưởng cười cười."),i("br"),i("br"),n._v("“Bẩm báo nguyễn hội trưởng, Trần hội phó, lão sư... Hắn không chỉ đang đọc sách... Còn khảo hạch Khải Linh sư...”"),i("br"),i("br"),n._v("Vị nữ tử này, nhớ tới vừa rồi nhìn thấy sự tình, liền mặt mũi khó tin."),i("br"),i("br"),n._v("“Khảo hạch Khải Linh sư?” Hứa Ảnh phó hội trưởng cau mày: “Hắn hình như đối khải linh chức nghiệp một chữ cũng không biết a? Đọc sách không đến ba canh giờ liền muốn khảo hạch tứ tinh Khải Linh sư?”"),i("br"),i("br"),n._v("Nàng cùng đối phương tiếp xúc qua, đối khải linh chức nghiệp hoàn toàn không hiểu, cứ như vậy còn muốn khảo hạch... Nói đùa cái gì!"),i("br"),i("br"),n._v("“Hắn... Không chỉ khảo hạch, còn đem góc tường khôi lỗi linh tính kích hoạt... Khôi lỗi chạy loạn khắp nơi, đã bị chúng ta mấy cái tỷ muội chế trụ...” Nữ tử vội vàng trả lời."),i("br"),i("br"),n._v("“Chạy loạn... Không đúng!”"),i("br"),i("br"),n._v("Lần này đến phiên nguyễn hội trưởng kì quái, nhịn không được nhìn qua: “Chỉ cần kích hoạt bốn đầu khôi lỗi hai cái cánh tay một cái đầu, cho dù thông qua khảo hạch, bởi vì có cái khác đầu hạn chế, cho dù kích hoạt, cũng không cách nào hành động... Làm sao sẽ chạy loạn?”"),i("br"),i("br"),n._v("“Hắn... Đem khôi lỗi bốn cái đầu cùng tám đầu cánh tay... Linh tính tất cả đều kích hoạt lên...” Bờ môi phát run, nữ tử dường như nói đến đây chính mình cũng không tin."),i("br"),i("br"),n._v("“Tất cả đều kích hoạt?” Ba đại hội trưởng đồng thời đứng dậy, trong mắt tràn đầy khó tin."),i("br"),i("br"),n._v("Muốn kích hoạt một cái linh tính, khống chế bốn cái đầu... Có thể nói quả thực là không thể nào sự tình, liền xem như các nàng cũng làm không được, một cái cái gì cũng không biết gia hỏa, chỉ nhìn một hồi sách, liền làm được?"),i("br"),i("br"),n._v("“Ngươi nói... Hắn đem đầu kia tứ tinh khôi lỗi, kích hoạt lên?”"),i("br"),i("br"),n._v("Hứa Ảnh phó hội trưởng cũng lại kìm nén không được."),i("br"),i("br"),n._v("Nếu thật là như vậy, người thanh niên này, làm không cẩn thận thật đúng là Khải Linh sư thiên tài."),i("br"),i("br"),n._v("“Không chỉ là... Tứ tinh, ngũ tinh, lục tinh cũng đều kích hoạt lên, hiện tại đang tại thất tinh trong phòng, đối với góc tường cột, không biết làm gì...”"),i("br"),i("br"),n._v("Nữ tử nói."),i("br"),i("br"),n._v("“Ngũ tinh, lục tinh cũng đều kích hoạt?”"),i("br"),i("br"),n._v("Ba đại hội trưởng tất cả đều thân thể nhoáng một cái."),i("br"),i("br"),n._v("Một lát sau, nguyễn hội trưởng nhíu nhíu mày: “Thất tinh khôi lỗi hỏng, một mực không có tới sửa chữa, cái kia cột là chống đỡ chúng ta công hội kiến trúc, không có linh tính, không có khả năng kích hoạt...”"),i("br"),i("br"),n._v("Ầm ầm!"),i("br"),i("br"),n._v("Lời còn chưa dứt, mọi người liền cảm thấy công hội một hồi kịch liệt lay động, giống như là ngồi ở một cái bị mặt nước xao động trên thuyền nhỏ."),i("br"),i("br"),n._v("“Không xong, hội trưởng... Chúng ta công hội... Sống!”"),i("br"),i("br"),n._v("Ngay sau đó, một trưởng lão vọt vào, vẻ mặt trắng bệch."),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1107"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1109"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);