(window.webpackJsonp=window.webpackJsonp||[]).push([[1365],{1720:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2199-can-đot-pha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2199-can-đot-pha"}},[n._v("#")]),n._v(" Chương 2199: Cần đột phá")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Thần giới đệ nhất luyện đan sư khống hỏa cùng đối dược tính nắm chắc quả nhiên đạt đến đỉnh phong nhất, Trương Huyền chỉ cần nói ra chính xác thời gian cùng trình tự, Phù Giang Thần đều có thể hoàn mỹ hoàn thành, không sai mảy may."),t("br"),t("br"),n._v("Rất nhanh, hơn một trăm loại dược liệu toàn bộ đi vào đan lô, hoàn mỹ dung hợp lại cùng nhau, hình thành một cái lớn chừng quả đấm giọt thuốc."),t("br"),t("br"),n._v("Vừa mới bắt đầu, nhìn thấy bình thường khó mà dung hợp dược liệu, đồng dạng tiếp đồng dạng hoàn mỹ dung hợp, Phù Giang Thần còn cảm thấy khó tin, về sau, khiếp sợ đã mất cảm giác."),t("br"),t("br"),n._v("Dường như chỉ cần dựa theo đối phương nói tới phương thức luyện chế, lại khó kết hợp dược vật, đều có thể dung hợp lại cùng nhau, không sai mảy may."),t("br"),t("br"),n._v("Vừa mới bắt đầu còn có chút nghi hoặc, tại sao lại như vậy, đến cuối cùng, mới hiểu được, trước mắt vị này, đối các loại dược liệu hiểu, đã đạt đến hòa vào cốt tủy tình trạng."),t("br"),t("br"),n._v("Lấy trước đó để hắn không hiểu xà thanh long tiên làm thí dụ, thứ này bình thường độc tính vô cùng mãnh liệt, nhưng một cái hô hấp phía trong đem nó nhiệt độ, thiêu đốt đến một ngàn ba trăm độ, độc tính sẽ ngắn ngủi biến mất nửa cái hô hấp, lúc này, chỉ cần đem nó cùng nặng mộc hoa dung hợp, độc tính không những sẽ không tái phát, còn có thể đem kích động người toàn thân tế bào lực lượng, bảo lưu lại tới."),t("br"),t("br"),n._v("Bách diệp tử hương hoa cũng là như vậy, có thể đóng băng chân khí, nhưng cùng đặc biệt dược vật, tại đặc biệt nhiệt độ bên dưới dung hợp, không những đối với chân khí không có ảnh hưởng, còn có thể vững chắc kinh mạch, tăng tốc chân khí lưu chuyển tốc độ!"),t("br"),t("br"),n._v("Mỗi một loại dược liệu thuộc tính, đều bị trước mắt người thanh niên này, hoàn mỹ phát huy ra, không lãng phí mảy may."),t("br"),t("br"),n._v("Thuật luyện đan của hắn đã rất cao minh, nhưng đến lúc này mới hiểu được, đan dược còn có thể như vậy luyện... Tựa như trong lòng mở ra một cái thiên địa mới, lại khó đóng lại."),t("br"),t("br"),n._v("“Không đúng...”"),t("br"),t("br"),n._v("Nhìn đan lô bên trong dung dịch, không có xuất hiện một lần trình tự sai lầm, Phù Giang Thần vừa định buông lỏng một hơi, đột nhiên nghĩ đến cái gì, vội vàng nhìn qua: “Hiện tại đã luyện chế một trăm linh bảy loại dược vật, vì sao còn không có lấy ra chủ dược tài?”"),t("br"),t("br"),n._v("Vừa mới một phen luyện chế, căn bản không kịp ngẫm nghĩ nữa, hiện tại dừng lại, nhất thời phát hiện không đúng."),t("br"),t("br"),n._v("Hắn nhìn qua đan phương, hết thảy một trăm không trăm tám loại dược liệu, lúc này đều luyện chế một trăm linh bảy loại, vì sao chủ dược tài còn không có xuất hiện?"),t("br"),t("br"),n._v("Cái gọi là chủ dược tài, chính là toàn bộ đan dược lấy làm hạch tâm dược vật, như là kiến trúc bên trong cốt thép, trụ cột, có hắn tồn tại, mới có thể tạo dựng dàn khung, hoàn thành cuối cùng thành phẩm."),t("br"),t("br"),n._v("Bình thường đan dược, đều là đem chủ dược tài tại phía trước nhất để vào, từ trong đó hòa, dung hợp mặt khác dược liệu, giờ phút này, đều luyện chế một trăm linh bảy trồng, tất cả đều là phối liệu..."),t("br"),t("br"),n._v("Đan dược này ngươi xác định là thật muốn luyện thành?"),t("br"),t("br"),n._v("Cùng xào rau đồng dạng, lập tức ra nồi, đều là tỏi, gừng, hành tây, hoa tiêu, mười ba hương loại hình gia vị, món chính đều không có nhìn thấy... Xác định không có ở nhìn chuyện cười"),t("br"),t("br"),n._v("“Đừng có gấp, tiếp tục làm nóng!”"),t("br"),t("br"),n._v("Nhìn chằm chằm trước mắt đan lô, Trương Huyền nói."),t("br"),t("br"),n._v("“Tốt!” Tuy là nghi hoặc, nhưng đối phương có lệnh, Phù Giang Thần không dám chần chừ, lực lượng trong cơ thể điên cuồng tràn vào đan lô, hỏa diễm lập tức bạo tăng."),t("br"),t("br"),n._v("Đan lô nhiệt độ nhanh chóng tăng thêm."),t("br"),t("br"),n._v("“Đủ rồi ư?”"),t("br"),t("br"),n._v("Gặp vừa mới luyện chế giọt thuốc, bắt đầu sôi trào, bất cứ lúc nào cũng sẽ nổ tung, Phù Giang Thần lông mày nhảy lên, vội vàng hỏi."),t("br"),t("br"),n._v("Dược dịch đã dung hợp tốt, tiếp tục làm nóng dẫn đến nổ tung lời nói, trước mặt công phu đều sẽ uổng phí."),t("br"),t("br"),n._v("“Không đủ, tiếp tục!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Cắn răng, Phù Giang Thần trong cơ thể lực lượng điên cuồng xông ra, lô đỉnh bên dưới hỏa diễm nhiệt độ tiếp tục tăng vọt."),t("br"),t("br"),n._v("“Nhiệt độ đã đi đến ta đủ khả năng mức cực hạn...”"),t("br"),t("br"),n._v("Ngắn ngủi năm cái hô hấp, vị này Thần giới đệ nhất luyện đan tông sư, trên đầu toát ra mồ hôi."),t("br"),t("br"),n._v("Lấy hắn tu vi hiện tại cùng lực lượng, lửa trong lò ngọn lửa nhiệt độ, đã đi đến cực hạn, lại cao hơn lời nói, chân khí kém không nói, còn có thể không nắm được, cuối cùng dẫn đến nổ bếp."),t("br"),n._v("“Nhiệt độ còn kém chút...” Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Thần Vương thảo, liền di động không gian đều có thể sống sót, lông tóc không tổn hao gì, đối nhiệt độ yêu cầu cực cao, trước mắt vị này thúc giục hỏa diễm, nhiệt độ là không thấp, nhưng vẫn như cũ không có đi đến có thể đem hắn hòa tan tình trạng."),t("br"),t("br"),n._v("“Nhưng ta đã dùng hết toàn lực...” Phù Giang Thần sắc mặt trắng nhợt: “Hơn nữa, trong cơ thể ta lực lượng, chỉ có thể duy trì ba mươi hô hấp, liền sẽ triệt để tiêu hao sạch sẽ...”"),t("br"),t("br"),n._v("Hắn tuy là mạnh mẽ, nhưng loại này toàn lực phát ra, đối chân khí cùng tinh thần đều có cực lớn tiêu hao, không có gì bất ngờ xảy ra, ba mươi hô hấp là cực hạn."),t("br"),t("br"),n._v("Một khi trong cơ thể lực lượng tiêu hao hầu như không còn, trước đó tất cả cố gắng, đều sẽ thất bại trong gang tấc."),t("br"),t("br"),n._v("Trương Huyền nắm đấm xiết chặt."),t("br"),t("br"),n._v("Làm xong tất cả kế hoạch, làm sao đều không có nghĩ đến, vị này Phù Giang Thần chân khí số lượng dự trữ yếu như vậy..."),t("br"),t("br"),n._v("Hơn nữa, nhiệt độ không đạt tới lời nói, coi như lại kiên trì ba mươi hô hấp cũng vô dụng ah!"),t("br"),t("br"),n._v("“Không có biện pháp nào khác...” Ánh mắt ngưng lại, Trương Huyền nhìn qua: “Ngươi bây giờ tu vi, là Thần Vương hậu kỳ, lập tức đột phá đến đỉnh phong... Lượng chân khí tuy là không có tăng nhiều, nhưng độ tinh thuần sẽ tăng thêm không ít, thúc giục hỏa diễm, nhiệt độ mới có thể càng cao, từ đó đi đến yêu cầu!”"),t("br"),t("br"),n._v("Phù Giang Thần tu vi chỉ là Thần Vương hậu kỳ, khoảng cách đỉnh phong còn cách một đoạn, nhưng bây giờ muốn không dẫn đến thất bại, chỉ có một cái phương pháp, để hắn cấp tốc đột phá!"),t("br"),t("br"),n._v("Chỉ cần đi đến Thần Vương đỉnh phong, chân khí càng thêm tinh thuần, hỏa diễm nhiệt độ tự nhiên là càng thêm nóng bỏng, đến lúc đó, liền có thể hòa tan Thần Vương thảo, đem đan dược luyện chế thành công!"),t("br"),t("br"),n._v("Nếu không, trước đó tất cả cố gắng, đều là uổng phí."),t("br"),t("br"),n._v("“Đột phá?” Nghe được hắn, Phù Giang Thần run run một chút: “Đây không phải là đùa giỡn thời điểm...”"),t("br"),t("br"),n._v("Tu vi của hắn cao bao nhiêu, bản thân hết sức rõ ràng, nếu có thể đột phá, đã sớm đột phá, cũng không đến mức chờ tới bây giờ."),t("br"),t("br"),n._v("Huống chi, giờ phút này ngay tại luyện đan, lực lượng duy trì đến cực hạn, sự tình khác cái gì đều không để ý tới... Để cho ta đột phá?"),t("br"),t("br"),n._v("Đùa giỡn đi!"),t("br"),t("br"),n._v("“Không có nói đùa, không muốn luyện đan thất bại, đây là con đường duy nhất...” Hừ một tiếng, Trương Huyền chẳng muốn phí lời, tiếp tục nói: “Ngươi sở dĩ vây ở Thần Vương hậu kỳ, không cách nào đột phá, là bởi vì, dùng đan dược quá nhiều, trong cơ thể đan độc không cách nào triệt để loại bỏ...”"),t("br"),t("br"),n._v("Là thuốc ba phần độc, cho dù tốt đan dược, ăn được nhiều, vẫn như cũ sẽ ở trong cơ thể tồn lưu đủ loại độc tính, trong thời gian ngắn, không có cái gì, nhưng kèm theo tu vi càng ngày càng cao, lực lượng càng ngày càng tinh thuần, dù là một chút, liền lộ ra cực kỳ trọng yếu."),t("br"),t("br"),n._v("Liền giống như vị này Phù Giang Thần, thân là Thần giới nổi danh nhất luyện đan đại tông sư một trong, dùng đan dược cơ hội đột phá, so người khác nhiều không biết bao nhiêu, có thể nói, hoàn toàn là dùng đan dược xung kích đến bây giờ cảnh giới!"),t("br"),t("br"),n._v("Dùng dược vật nhiều như vậy, đan độc một cách tự nhiên tích tụ ở trong người, không cách nào loại bỏ."),t("br"),t("br"),n._v("Cho dù dùng tất cả phương pháp, cũng không thể làm được."),t("br"),t("br"),n._v("“Hiện tại, ta giúp ngươi khu trừ đan độc, ngươi cái gì cũng không cần nghĩ, trực tiếp trùng kích cảnh giới liền tốt!”"),t("br"),t("br"),n._v("Biết đối phương chuẩn xác triệu chứng, Trương Huyền cổ tay khẽ đảo, đầu ngón tay bất ngờ xuất hiện mấy chục viên châm bạc."),t("br"),t("br"),n._v("Vù!"),t("br"),t("br"),n._v("Bí mật mang theo tiếng gió thẳng tắp hướng Phù Giang Thần toàn thân huyệt đạo bắn tới."),t("br"),t("br"),n._v("Lông mày nhảy dựng, vị này Thần giới nổi danh luyện đan tông sư, vừa định chống cự, đột nhiên cắn răng, hai mắt nhắm lại: “Liều mạng!”"),t("br"),t("br"),n._v("Lại không chống lại, mặc cho đối phương cắm vào thân thể!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2213"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2215"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);