(window.webpackJsonp=window.webpackJsonp||[]).push([[1367],{1722:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2201-bach-diep-thanh-hong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2201-bach-diep-thanh-hong"}},[n._v("#")]),n._v(" Chương 2201: Bách Diệp Thanh Hồng")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Khó trách, hắn vẫn cảm thấy ít nhất có thể luyện chế ra hơn mười viên thuốc, cuối cùng lại chỉ xuất hiện bốn cái, nguyên lai còn lại dược lực, đều tập trung vào cái này viên Đan Vương trên người!"),t("br"),t("br"),n._v("Không chỉ có như vậy, cái này Đan Vương, linh tính mạnh hơn, nắm giữ có thể so với trí tuệ của nhân loại."),t("br"),t("br"),n._v("Vừa mới những đan dược khác lao ra thời điểm, cái tên này trốn ở lô đỉnh phía trong, làm cho không người nào có thể phát hiện, thừa dịp Trương Huyền bắt được những dược vật khác tâm cảnh buông lỏng trong nháy mắt, chạy ra ngoài!"),t("br"),t("br"),n._v("Thời cơ, kiên nhẫn, đều hiện ra bất phàm."),t("br"),t("br"),n._v("Mà một khi thành công, tương đương với lần này luyện đan, tuy là hoàn thành, lại mất đi vật trân quý nhất."),t("br"),t("br"),n._v("“Trốn được ư?”"),t("br"),t("br"),n._v("Không nghĩ tới bị một viên đan dược trêu đùa, Trương Huyền bàn chân tại lơ lửng một bước, chân khí trong cơ thể trong nháy mắt bắn ra."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Không khí khuấy động ra gợn sóng, cả người thẳng tắp hướng đan dược biến mất phương hướng vọt tới."),t("br"),t("br"),n._v("Hồng Trần Đạp Thiên bộ bị hắn vận chuyển tới cực hạn, đan dược tốc độ cực nhanh, nhưng hắn tốc độ càng nhanh!"),t("br"),t("br"),n._v("“Thượng phẩm Thiên Thần đỉnh phong... Có thể bay được?”"),t("br"),t("br"),n._v("Phù Giang Thần trợn mắt lên."),t("br"),t("br"),n._v("Thần Vương mới có thể phi hành, đây là Thần giới không thể sửa đổi định luật, trước mắt vị này, một kiếm đem sức chiến đấu có thể so với Thần Vương đan dược lấy đi không nói, lại còn bay lên, tốc độ so với bản thân toàn lực đều không chậm..."),t("br"),t("br"),n._v("Làm sao làm được?"),t("br"),t("br"),n._v("Trong lúc nhất thời, Phù Giang Thần nghi ngờ có hay không nhìn lầm."),t("br"),t("br"),n._v("Chạy trốn Đan Vương cũng không nghĩ tới, vị này thượng phẩm Thiên Thần, nếu không sẽ bay, còn bay nhanh như vậy, gặp chẳng mấy chốc sẽ bị đuổi kịp, lại không chạy trốn, chợt xoay người, nhẹ nhàng khẽ động, huyễn hóa ra một vị sắc mặt dữ tợn thanh niên, một chưởng đập tới!"),t("br"),t("br"),n._v("Biến động bất ngờ, lực lượng tung hoành."),t("br"),t("br"),n._v("Viên đan dược này chưởng khống lực lượng, vậy mà đạt đến Thần Vương cảnh, so với Phù Giang Thần, đều không hề yếu!"),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Vẻ mặt khó coi, Trương Huyền ngừng lại, tránh thoát công kích, cùng thời khắc đó, trường kiếm trong tay nghênh đón tiếp lấy."),t("br"),t("br"),n._v("Tuy là trong cơ thể Thiên đạo chân khí, bị dòng khí màu xám tiêu hao sạch sẽ, nhưng thông qua vừa mới phi hành, hắn phát hiện, sức chiến đấu chẳng những không có mảy may hạ thấp, ngược lại càng thêm cường đại."),t("br"),t("br"),n._v("Chỉ là chân khí số lượng dự trữ quá ít, không thể giống như kiểu trước đây, tùy ý chiến đấu, mà không cần lo lắng hao hết."),t("br"),t("br"),n._v("Cho nên..."),t("br"),t("br"),n._v("Nhất định phải, tốc chiến tốc thắng!"),t("br"),t("br"),n._v("Không riêng gì chân khí trong cơ thể vấn đề, càng quan trọng hơn là, cái này viên Đan Vương chạy trốn, gây ra động tĩnh thực sự quá lớn, ngắn ngủi mấy hơi thở, đã có mấy chục đạo thần thức dò xét qua đến, không nhanh chút giải quyết, rất dễ dàng xuất hiện biến cố."),t("br"),t("br"),n._v("Xuân Tàm Đáo Tử Ti Phương Tẫn, Chá Cự Thành Hôi Lệ Thủy Kiền!"),t("br"),t("br"),n._v("Thiên Thần cảnh giới lĩnh ngộ kiếm chiêu kiếm ý, ầm ầm mà ra, một đạo chỉ, theo nhỏ bé một chút, bắn đi ra, giống như là sáp bó đuốc đang thiêu đốt giọt cuối cùng hỏa diễm, lại giống là xuân tằm, trước khi chết phun ra cuối cùng một đạo tơ tằm, mang theo vô tận ảo diệu, trong tích tắc, đi vào Đan Vương biến hóa thanh niên trước mặt, đem nó trói chặt chẽ vững vàng."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Đan Vương sửng sốt, tràn đầy khó tin."),t("br"),t("br"),n._v("Thần Vương cùng Thiên Thần, chênh lệch tựa như hai loại sinh mệnh, không thể bên trong mà tính, vốn cho rằng chỉ cần mình quay đầu, dùng hết toàn lực, nhất định có thể nhẹ nhõm đem nó chém giết, nằm mơ cũng không ngờ tới, một chiêu đều không có thi triển xong, liền bị phong bế."),t("br"),t("br"),n._v("Thượng phẩm Thiên Thần... Đều mạnh mẽ như vậy sao?"),t("br"),t("br"),n._v("“Thu!”"),t("br"),t("br"),n._v("Biết mình chân khí không kiên trì được bao lâu, vừa toàn lực phi hành, thi triển ra hai chiêu kiếm pháp, tiêu hao không ít, chẳng muốn phí lời, kiếm pháp hình thành dây thừng, bỗng nhiên nắm chặt."),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Một tiếng kêu thảm, Đan Vương biến thành thanh niên, thân thể thu nhỏ, lần nữa biến thành một viên mượt mà đan dược, tự chủ sinh ra linh tính biến mất."),t("br"),t("br"),n._v("Đem nó bắt được, Trương Huyền lúc này mới thở phào nhẹ nhõm, trường kiếm run lên, kiếm khí bao phủ đan dược thẳng tắp bay tới, vừa định đưa tay bắt được, thân thể đột nhiên cảm thấy cứng đờ, bay tới Đan Vương dừng ở trên không, ngay cả trong cơ thể hắn chân khí, đều có chút không nghe sai khiến, động đậy không được nữa."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Đồng tử co rụt lại, ngay sau đó nhìn thấy một cái cô gái xinh đẹp, đạp trên không khí từ đằng xa từng bước một đi tới."),t("br"),t("br"),n._v("Thon thon tay ngọc, nhẹ nhàng bóp một cái, bị kiếm khí bao phủ Đan Vương liền bị nàng nắm tại đầu ngón tay."),t("br"),t("br"),n._v("“Vị bằng hữu này, viên đan dược này, chính là ta  tiêu phí vô số đại giới luyện chế dược vật, vừa mới chạy đi, cảm ơn giúp ta bắt được, vô cùng cảm kích!”"),t("br"),t("br"),n._v("Nhìn lại, nữ tử khẽ mỉm cười."),t("br"),t("br"),n._v("“Thanh Thảo Hiên?” Tinh thần khẽ động, chân khí trong cơ thể theo huyệt đạo nổ tung, cầm cố lại lực lượng của mình, triệt để tiêu tán, Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("Đan Vương, còn dính dáng có thể làm cho người đột phá Thần Vương, sức hấp dẫn thực sự quá lớn, vừa mới vẫn lo lắng, sẽ có người chạy tới tiệt hồ, không nghĩ tới, thật đúng là xuất hiện."),t("br"),t("br"),n._v("Cướp người khác đồ vật, lại còn nói như vậy hùng hồn... Không hổ là Thần giới, không có quy tắc, tất cả đều lấy nắm đấm nói chuyện."),t("br"),t("br"),n._v("“Không sai!” Nữ tử lông mày nâng lên, cổ tay rung lên, một viên ngọc phù xuất hiện tại lòng bàn tay: “Đây là ta Thanh Thảo Hiên tín phù, ngươi giúp ta bắt được đan dược, không thể để cho ngươi giúp không bận bịu, dựa vào thứ này, có thể đi ta Thanh Thảo Hiên, tùy ý lựa chọn sử dụng một bộ tu luyện công pháp, xem như thù lao!”"),t("br"),t("br"),n._v("Ngọc phù nhẹ nhàng vụt qua, chậm chạp bay tới."),t("br"),t("br"),n._v("“Cường thủ hào đoạt ư?” Trường kiếm trong tay nhẹ nhàng vạch một cái, ngọc phù lập tức vỡ thành hai mảnh, Trương Huyền nhìn qua, híp mắt lại: “Muốn cướp ta đồ vật, ngươi cũng đã biết kết quả?”"),t("br"),t("br"),n._v("Đối phương cầm cố lại bản thân đoạt bản thân trói lại đan dược, ngược lại cũng thôi, vậy mà nói thẳng là bản thân, quả thực không biết xấu hổ đến cực hạn."),t("br"),t("br"),n._v("“Cướp ngươi đồ vật? Viên đan dược này, chính là ta tiêu phí vô số đại giới luyện chế mà thành, lấy một cái nho nhỏ thượng phẩm đỉnh phong Thiên Thần, có thể luyện chế đi ra? Có năng lực luyện chế đi ra?” Nữ tử cười lạnh, vung một cái ống tay áo: “Truyện cười!”"),t("br"),t("br"),n._v("“Ta tự nhiên không cách nào luyện chế ra đến, viên đan dược này, chính là Phù Giang Thần đại sư luyện chế mà thành, ta cung cấp dược vật cùng thủ pháp luyện chế, vừa mới luyện thành bị nó chạy trốn, lúc này mới đuổi tới... Ngươi nói là ngươi luyện chế, có biết viên đan dược này, có gì loại dược liệu luyện chế mà thành, lại muốn trải qua những cái kia trình tự?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Phù Giang Thần?” Nữ tử cười nhạo: “Nhìn tới ngươi cũng biết là hắn, dược vật này, là ta ủy thác hắn luyện chế, hơn nữa cấp cho dược liệu, làm sao chỉ chớp mắt biến thành ngươi? Lời không thể nói lung tung... Như vậy đi, ta cũng không bắt nạt ngươi, hiện tại liền để hắn tới, chứng minh một chút, viên đan dược này đến cùng là ai!”"),t("br"),t("br"),n._v("Nữ tử nói xong, cúi đầu nhìn về phía một cái phương hướng: “Phù Giang Thần, còn không mau một chút tới!”"),t("br"),t("br"),n._v("Âm thanh như là một đạo chỉ, cấp tốc truyền xuống tiếp."),t("br"),t("br"),n._v("Trương Huyền không lên tiếng, híp mắt lại."),t("br"),t("br"),n._v("Bốn phía mặc dù không có những người khác, nhưng thần thức chừng mấy chục đạo, nữ nhân này vừa mở miệng liền nói là bản thân, mục đích chỉ có một cái, chiếm cứ đại nghĩa, tốt đem cái này viên Đan Vương, chiếm làm của riêng!"),t("br"),t("br"),n._v("Không phải, truyền đi danh khí không dễ nghe!"),t("br"),t("br"),n._v("Chỉ từ điểm ấy, cũng có thể thấy được, trước mắt vị này, nhất định tại Linh Lung hoàng thành, địa vị không thấp."),t("br"),t("br"),n._v("“Cướp ta đan dược, như vậy hùng hồn, mặt đều không đỏ... Không biết ngươi xưng hô như thế nào?” Hừ một tiếng, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Biết Thanh Thảo Hiên không biết ta?”"),t("br"),t("br"),n._v("Cũng không tức giận, nữ tử sửng sốt một chút, nhàn nhạt nhìn qua, cười một tiếng: “Bản tọa, Bách Diệp Thanh Hồng!”"),t("br"),t("br"),n._v("“Bách Diệp Thanh Hồng?”"),t("br"),t("br"),n._v("Trương Huyền sầm mặt lại: “Phong hào Thần Vương?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2215"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2217"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);