(window.webpackJsonp=window.webpackJsonp||[]).push([[1375],{1730:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2208-sup-đo-linh-lung-tien-tu-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2208-sup-đo-linh-lung-tien-tu-thuong"}},[n._v("#")]),n._v(" Chương 2208: Sụp đổ Linh Lung tiên tử (Thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Sụp đổ Linh Lung tiên tử (bên trên)"),t("br"),t("br"),n._v("Chương trước trở về mục lục chương sau trở về trang sách"),t("br"),t("br"),n._v("“Làm phong hào Thần Vương?”"),t("br"),t("br"),n._v("Hai nữ hài do dự."),t("br"),t("br"),n._v("Đi đến Thần Vương đỉnh phong sau, chỉ cần có thể đạt được Đế Quân tán thưởng, bị phong phong hào, lực lượng tự nhiên sẽ tiến thêm một bước, trở thành Đế Quân phía dưới nhân vật cường hãn nhất."),t("br"),t("br"),n._v("Không chiếm được phong hào, nhiều nhất so với bình thường Thần Vương lớn mạnh một chút thôi."),t("br"),t("br"),n._v("“Không sai!” Linh Lung tiên tử gật đầu."),t("br"),t("br"),n._v("Bách Diệp Thanh Hồng bị giết sau, nàng tính toán đem cái này phong hào Thần Vương vị trí, để lại cho Lạc Thất Thất, ai biết cô gái này, việc nghĩa chẳng từ nan cùng cái kia Trương Huyền rời đi."),t("br"),t("br"),n._v("Đã nàng không nguyện ý làm, trước mắt hai vị này chỉ cần thành công, Linh Lung thiên tuy là tổn thất một vị phong hào, nhưng lại nhiều ra hai vị, ba đại phong hào, so với cái khác tám thiên, cũng không hề yếu."),t("br"),t("br"),n._v("“Làm sao, không nguyện ý?”"),t("br"),t("br"),n._v("Thấy yêu cầu làm phong hào Thần Vương, hai người kia đều do dự, Linh Lung tiên tử nhíu nhíu mày."),t("br"),t("br"),n._v("“Có thể để cho thực lực tăng tiến, đương nhiên là tốt nhất, chỉ là...” Đứng ở phía trước hơi có vẻ dịu dàng ít nói nữ hài dừng lại một chút, mở miệng nói: “Trở thành ngươi phong hào Thần Vương sau, có phải hay không nhất định phải nghe theo mệnh lệnh của ngươi, nếu để cho chúng ta đối phó thân nhất, cũng không thể làm trái?”"),t("br"),t("br"),n._v("“Phong hào Thần Vương, chỉ là thu hoạch được Cửu Thiên bên trong một bộ phận khu vực công nhận, thu hoạch được vô số dân chúng nguyện lực, từ đó có thể tốt hơn điều động thiên địa lực lượng, cũng không phải là nhất định phải nghe theo mệnh lệnh của ta!”"),t("br"),t("br"),n._v("Còn tưởng rằng đối phương lo lắng cái gì, Linh Lung tiên tử cười cười: “Nếu như nhất định muốn nghe theo mệnh lệnh của ta, còn gọi cái gì phong hào Thần Vương, không bằng gọi khôi lỗi coi như!”"),t("br"),t("br"),n._v("Hai nữ hài bừng tỉnh hiểu ra, lúc này mới gật gật đầu, ánh mắt kiên định: “Nếu như là như vậy, chúng ta nguyện ý làm Linh Lung thiên phong hào Thần Vương!”"),t("br"),t("br"),n._v("“Vậy là tốt rồi...”"),t("br"),t("br"),n._v("Thở phào nhẹ nhõm, Linh Lung tiên tử cổ tay rung lên, một cỗ thật lớn quang mang đem hai người bao phủ, cùng thời khắc đó, Linh Lung thiên phía trong vô số sinh mệnh, đồng thời nghe được một cái vang vọng tại trong óc âm thanh."),t("br"),t("br"),n._v("“Bách Diệp Thanh Hồng hành vi không ngay thẳng, đã tước phong hào. Vương Dĩnh, Ngụy Như Yên, tiếp nhận vị trí, vì tân nhiệm phong hào Thần Vương.”"),t("br"),t("br"),n._v("“Tham kiến tân nhiệm phong hào Thần Vương!”"),t("br"),t("br"),n._v("Linh Lung thiên vô số sinh mệnh, trong đầu đồng thời xuất hiện hai cái xinh đẹp hình dáng, nhìn thấy hình ảnh, mọi người đồng thời quỳ rạp xuống đất."),t("br"),t("br"),n._v("Phong hào Thần Vương tuổi thọ, vượt qua là mười vạn năm, một khi xuất hiện, liền đại biểu một cái to lớn gia tộc sinh ra."),t("br"),t("br"),n._v("Trước đó, toàn bộ Linh Lung thiên chỉ có hai vị phong hào, vốn cho rằng Bách Diệp Thanh Hồng bị giết, chỉ còn lại có một vị, lại khó cùng cái khác tám thiên chống lại, không nghĩ tới Linh Lung tiên tử, lập tức đã sắc phong hai vị."),t("br"),t("br"),n._v("Đương nhiên, không phải tất cả Thần Vương đỉnh phong, đều có thể được sắc phong."),t("br"),t("br"),n._v("Có thể thu hoạch được xưng hào, đã nói rõ thực lực."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Mọi người bái phục, hai nữ hài đồng thời cảm thấy một đạo vĩ đại ý niệm, gia trì ở trên người, tựa như thu được thiên địa công nhận, có thể điều động càng nhiều lực lượng."),t("br"),t("br"),n._v("“Thiên địa ý niệm?”"),t("br"),t("br"),n._v("Hai người giật mình."),t("br"),t("br"),n._v("“Đạt được Đế Quân sắc phong, trở thành phong hào Thần Vương, chân khí, tu vi, cũng không biết có bất kỳ biến hóa, nhưng linh hồn đạt được thiên địa ý niệm công nhận, chỉ cần đem cỗ này ý niệm gia trì ở trên người, sức chiến đấu liền sẽ so trước kia, mạnh mẽ gấp mười lần, gấp trăm lần!”"),t("br"),t("br"),n._v("Thấy các nàng không hiểu, Linh Lung tiên tử cười giải thích."),t("br"),t("br"),n._v("Phong hào Thần Vương sở dĩ mạnh mẽ, cũng không phải là tu vi càng cao thâm hơn, chân khí càng thêm tinh thuần, mà là... Phong hào lực lượng!"),t("br"),t("br"),n._v("Loại lực lượng này, là thiên địa ý niệm công nhận, chỉ cần đem loại này công nhận, vận dụng đi ra, Thần Vương căn bản là không có cách chống lại."),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("Cảm nhận được cỗ lực lượng này mang tới mạnh mẽ, hai người đồng thời con mắt sáng lên."),t("br"),n._v("Có loại lực lượng này, cũng hiểu được, trừ phi bị giết, nếu không, lực lượng là không cách nào bị tước đoạt."),t("br"),t("br"),n._v("Nói cách khác, tuy là loại lực lượng này là Đế Quân sắc phong được, nhưng cho dù Đế Quân muốn tước đoạt, cũng làm không được."),t("br"),t("br"),n._v("“Đa tạ tiên tử...”"),t("br"),t("br"),n._v("Hai người lần nữa quỳ gối."),t("br"),t("br"),n._v("Linh Lung tiên tử thoả mãn gật đầu, một mặt từ ái nhìn qua: “Còn không gọi... Sư phụ ư?”"),t("br"),t("br"),n._v("Hai người này là nàng theo thần sơn mang tới, mặc dù không có bái sư, nhưng cũng có sư đồ tình nghĩa, không phải nàng tự thân chỉ điểm, lại sử dụng thời không kính lực lượng, cho dù thiên tư trác tuyệt, cũng không có khả năng ngắn như vậy thời gian bên trong, liền đi qua phong hào Thần Vương khảo hạch, hơn nữa thu hoạch được sắc phong."),t("br"),t("br"),n._v("Bởi vậy, mặc dù chưa hề thu qua đệ tử, nhưng đối hai người này yêu thích thắng được tất cả, hy vọng có thể trở thành lão sư."),t("br"),t("br"),n._v("“Còn xin tiên tử thứ lỗi... Tỷ muội chúng ta trước khi tới cũng đã nói, đã có lão sư...”"),t("br"),t("br"),n._v("Dịu dàng ít nói nữ hài vội vàng ôm quyền nói."),t("br"),t("br"),n._v("“Ta biết các ngươi có lão sư... Nhưng hắn làm sao có thể so với ta? Chẳng qua cho các ngươi nhập môn, cho các ngươi đột phá đến Thần Linh thôi!”"),t("br"),t("br"),n._v(" Nhíu nhíu mày, Linh Lung tiên tử nói."),t("br"),t("br"),n._v("Hai người này có lão sư, tại thần sơn thời điểm, liền biết, lúc đó muốn dẫn các nàng đi, cũng không nguyện ý, nói muốn cho lão sư lưu tin tức."),t("br"),t("br"),n._v("Lúc đó còn muốn lấy, các nàng tôn sư trọng đạo, phẩm chất rất tốt, càng rót đầy hơn ý..."),t("br"),t("br"),n._v("Vốn cho rằng, chỉ là chỉ điểm để hắn đột phá Thần Linh thầy giáo vỡ lòng mà thôi, kèm theo thời gian trôi qua, khẳng định sẽ quên đi, không nghĩ tới, cái này đều đã lâu như vậy, còn chỉ nhận người kia, không nhận bản thân..."),t("br"),t("br"),n._v("Nàng nhưng mà Đế Quân!"),t("br"),t("br"),n._v("Chỉ cần mở miệng, thiên hạ không biết bao nhiêu cường giả, nguyện ý bái tại môn hạ, trở thành đệ tử..."),t("br"),t("br"),n._v("Đều là hắn bỏ ra nhiều như vậy, thậm chí sách Phong Thần vương, vẫn như cũ không chịu bái sư, chỉ nhận đối phương..."),t("br"),t("br"),n._v("Tên kia, đến cùng chỗ nào tốt?"),t("br"),t("br"),n._v("Nói đến thiên, cũng chỉ là cái phổ thông Thần Linh, cấp cho chỉ điểm một chút thôi, cần thiết hay không?"),t("br"),t("br"),n._v("“Còn xin tiên tử chớ nên làm nhục ta sư, nếu không, ân tình lưỡng nan dứt bỏ phía dưới, chúng ta chỉ có đem tu vi trả lại cho ngươi!” Khuôn mặt lạnh lùng nữ tử, lông mày giương lên."),t("br"),t("br"),n._v("“Đã bái sư, cả đời chỉ có một sư, kiếp này cũng không biết thay đổi, mong rằng... Đế Quân thứ lỗi!” Dịu dàng ít nói nữ tử cũng nói."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Thấy hai người kiên trì, mảy may đều không lay được, Linh Lung tiên tử đầy mặt bất đắc dĩ, mang theo tiếc hận, nói “Các ngươi có biết linh khí thuỷ triều?”"),t("br"),t("br"),n._v("“Nghe nói qua một ít!” Dịu dàng ít nói nữ hài gật đầu."),t("br"),t("br"),n._v("Thần giới đều tại điên truyền, các nàng tuy là một mực bế quan tu luyện, nhưng cũng biết một ít."),t("br"),t("br"),n._v("Nếu như không phải là bởi vì cái này, cho dù thiên phú không tồi, cũng không có khả năng đạt được Đế Quân coi trọng như vậy, ban tặng vô số tài nguyên, trong thời gian ngắn đem tu vi tăng lên tới như vậy cấp bậc."),t("br"),t("br"),n._v("“Linh khí thuỷ triều, mười năm một lần, Cửu Thiên Cửu Đế, cũng vì đó điên cuồng, phong hào Thần Vương, Thần Vương đều vô cùng có khả năng vẫn lạc...”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Linh Lung tiên tử nhàn nhạt nhìn qua: “Các ngươi tuy là thành phong hào Thần Vương, nhưng vẫn như cũ không phải mạnh nhất, lần này thần sơn, ta tuy là tìm tới các ngươi, nhưng cái khác Cửu Đế, cũng đều tìm được phù hợp truyền nhân của mình! Hơn nữa tại bọn họ bồi dưỡng bên dưới, khẳng định mạnh hơn! Nếu như không bái ta làm thầy, học không đến cao thâm hơn võ kỹ, một khi cùng bọn hắn chiến đấu, vô cùng có khả năng xuất hiện nguy hiểm, thân tử đạo vẫn!”"),t("br"),t("br"),n._v("“Không nói cái khác, Liệt Dương thiên Chước Dương Đại Đế mang đi vị kia gọi Trịnh Dương, các ngươi có thể từng nghe nói qua? Vài ngày trước đã đi đến phong hào Thần Vương cảnh giới, thực lực so với một ít uy tín lâu năm phong hào Thần Vương đều không hề yếu, nếu để cho hắn xuất chiến, các ngươi không học tập ta truyền thụ cho võ kỹ, rất khó thắng được...”"),t("br"),t("br"),n._v("“Chúng ta nghe nói qua...” Dịu dàng ít nói nữ hài gật đầu nhìn qua: “Đó là ta Tứ sư đệ!”."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2222"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2224"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);