(window.webpackJsonp=window.webpackJsonp||[]).push([[1380],{1736:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-2213-kiem-tra-phong-hao-than-vuong-tu-cach"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2213-kiem-tra-phong-hao-than-vuong-tu-cach"}},[n._v("#")]),n._v(" Chương 2213: Kiểm tra phong hào Thần Vương tư cách")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Trương Huyền lắc đầu: “Ta từ nhỏ đã tự do tản mạn quen rồi, mong rằng gia chủ thứ lỗi, thực sự không cách nào chịu trách nhiệm loại này trách nhiệm...”"),g("br"),g("br"),n._v("Tới Linh Nguyên thiên chính là vì tiến Linh Thần cung, làm cái gì gia chủ, khẳng định không nguyện ý."),g("br"),g("br"),n._v("Không nghĩ tới hắn sẽ từ chối, gia chủ sửng sốt một chút, đang muốn tiếp tục hỏi thăm, chỉ thấy thanh niên trước mắt, nghi ngờ nhìn qua: “Ngươi mới vừa nói, thông qua phong hào Thần Vương kiểm tra... Phong hào Thần Vương cần kiểm tra?”"),g("br"),g("br"),n._v("Còn tưởng rằng, phong hào Thần Vương, là bởi vì công lao cao, được sắc phong, mới thực lực kinh người, nếu có khảo nghiệm, rõ ràng không đúng."),g("br"),g("br"),n._v("“Cái gọi là phong hào, là Đế Quân tự thân sắc phong, chưởng khống một phương địa phương lực lượng, tiếp nhận một phương nguyện lực. Những này nguyện lực, cũng không phải là đơn thuần tưởng niệm, dị thường hỗn tạp, muốn triệt để chưởng khống, linh hồn phải biến đổi đến mức vô cùng kiên trì cùng mạnh mẽ, nói cách khác, thấp nhất đều cần linh hồn đi đến Thần Vương đỉnh phong cường giả, tiếp đó mới là tu vi bên trên...”"),g("br"),g("br"),n._v("Gia chủ giải thích."),g("br"),g("br"),n._v("Chưởng khống một phương địa phương, người bình thường sẽ có rất nhiều, mỗi người ý tưởng khác biệt, nguyện lực bên trong tự nhiên cũng xen lẫn một cái nhân tình cảm giác, muốn tốt hơn lợi dụng, nhất định phải loại bỏ các loại tình cảm gánh nặng."),g("br"),g("br"),n._v("Bản thân linh hồn không tinh khiết, không cường đại lời nói, dễ dàng rơi vào hỗn loạn."),g("br"),g("br"),n._v("Cho nên, nghĩ đến phong hào, hồn lực tất mạnh."),g("br"),g("br"),n._v("Trước mắt vị này, bản thân tu vi mới thượng phẩm Thiên Thần đỉnh phong, linh hồn nhưng đã đạt đến Thần Vương đỉnh phong, chỉ bằng vào điểm ấy, về sau đột phá đến Thần Vương cảnh, xung kích phong hào Thần Vương, ít nhất nắm giữ năm mươi phần trăm trở lên xác suất!"),g("br"),g("br"),n._v("Bọn họ Hỏa Phượng nhất tộc, hưng thịnh đến bây giờ, cũng bởi vì có một vị phong hào lão tổ, lại xuất hiện một vị lời nói, không cần nghĩ, Linh Nguyên thiên thứ nhất, thậm chí Thần giới đệ nhất gia tộc, đều thực chí danh quy!"),g("br"),g("br"),n._v("“Nhận được ưu ái...” Trương Huyền lắc đầu."),g("br"),g("br"),n._v("Thấy hắn thái độ kiên quyết, gia chủ biết khuyên can vô dụng, đành phải lùi lại mà cầu việc khác: “Nếu không như vậy, Linh Thần ngoài cung, có một đầu tượng đá Phượng Hoàng, vừa đến khống chế cung nội đại trận, thứ hai chuyên môn kiểm tra Thần Vương, không bằng... Ngươi thử một chút?”"),g("br"),g("br"),n._v("Linh khí thuỷ triều sắp tới, phương nào thế lực, không muốn để cho phía bên mình phong hào Thần Vương nhiều một ít, trước mắt vị này, linh hồn như vậy nghịch thiên, cứ việc còn không có đi đến Thần Vương cảnh giới, chỉ cần có thể kiên trì một đoạn thời gian, gia tộc bọn họ, liền có thể thuyết phục mặt khác hai đại gia tộc, cung cấp vô số tài nguyên, để hắn đột phá!"),g("br"),g("br"),n._v("Một khi thành công, Linh Nguyên thiên xuất hiện một vị siêu cấp cường giả không nói, chủ yếu nhất là Hỏa Phượng nhất tộc, càng thêm cường đại."),g("br"),g("br"),n._v("Trương Huyền chần chừ."),g("br"),g("br"),n._v("“Ngươi mới vừa rồi cùng Phượng Hàn Thu đánh cược, nhận được danh ngạch, vừa vặn muốn đi Linh Thần cung, kiểm tra một chút!” Gia chủ nói."),g("br"),g("br"),n._v("“Tốt a...” Trương Huyền im lặng."),g("br"),g("br"),n._v("Xem ra chính mình cùng Phượng Hàn Thu tranh đấu, đối phương đều thấy được, một mực không có khuyên can, cũng là vì cho Hỏa Phượng nhất tộc tranh thủ danh ngạch."),g("br"),g("br"),n._v("Bất quá, kiểm tra một chút, cũng không sao, dù sao đều muốn đi Linh Thần cung."),g("br"),g("br"),n._v("Linh Thần cung lơ lửng tại Linh Nguyên thành phía trên, đi theo gia chủ sau lưng phi hành một hồi, đi tới trước mặt."),g("br"),g("br"),n._v("Lúc này, cung điện bên ngoài, đã đứng hai người khác."),g("br"),g("br"),n._v("Mặt khác hai vị gia tộc gia chủ."),g("br"),g("br"),n._v("“Phượng Nguyên Trì, ngươi nói hắn... Linh hồn đã đi đến Thần Vương đỉnh phong?”"),g("br"),g("br"),n._v("Mới đi đến trước mặt, một vị một thân băng hàn gia chủ, liền đến đến trước mặt, trong ánh mắt mang theo không vui."),g("br"),g("br"),n._v("Băng Phượng nhất tộc tộc trưởng, Phượng Hàn Tuyết."),g("br"),g("br"),n._v("Vốn phải là gia tộc bọn họ Phượng Hàn Thu, đi vào Linh Thần cung cảm ngộ, bây giờ bị người thay thế, hơn nữa đối với vừa mới bên dưới nhiều ra một vị Thần Vương cùng một vị linh hồn Thần Vương đỉnh phong... Đổi lại người nào, đều sẽ mất hứng."),g("br"),g("br"),n._v("“Không sai!” Hỏa Phượng nhất tộc gia chủ, Phượng Nguyên Trì gật đầu."),g("br"),g("br"),n._v("Phượng Hàn Tuyết ngẩng đầu nhìn về phía Trương Huyền, quả nhiên cảm thấy hắn linh hồn sâu không lường được, cho dù là hắn, đều không thể nhìn thấu, tràn đầy buồn bực, nhưng cũng không tiện phát tác: “Bắt đầu kiểm tra a, trước thời hạn đã nói, phong hào Thần Vương kiểm tra mười phần nguy hiểm, hơi không cẩn thận, linh hồn bị nghiền ép, bị thương không nhẹ.”"),g("br"),g("br"),n._v("“Ừm!” Phượng Nguyên Trì gật đầu nhìn về phía Trương Huyền, lặng lẽ truyền âm: “Ngươi mục đích lần này, chỉ là vì kiểm tra, nắm giữ xung kích phong hào Thần Vương tiềm chất, tốt xin tốt hơn tài nguyên, giúp ngươi đột phá. Một khi không kiên trì nổi, liền lập tức lui về, tuyệt đối không nên cứng rắn chống đỡ...”"),g("br"),g("br"),n._v("Trương Huyền gật đầu, nghi ngờ nói: “Làm sao kiểm tra?”"),g("br"),g("br"),n._v("“Phía trước cái kia Phượng Hoàng tượng đá, đằng sau là bao phủ Linh Nguyên thiên tinh đồ, mượn lực lượng linh hồn, thông qua tượng đá nhen lửa tinh đồ, có thể thắp sáng một viên, đã nói lên nắm giữ tiềm lực, thắp sáng trăm viên, chứng minh chính thức có được trở thành phong hào Thần Vương tư chất...”"),g("br"),g("br"),n._v("Phượng Nguyên Trì chỉ về phía trước."),g("br"),n._v(" Trương Huyền nhìn lại, quả nhiên thấy Linh Thần ngoài cung, một cái to lớn Phượng Hoàng tượng đá, đứng sừng sững ở tại chỗ, đằng sau thì là một cái càng thêm to lớn tinh đồ."),g("br"),g("br"),n._v("Từ xa nhìn lại, tựa như một đầu to lớn Phượng Hoàng, bay lượn ở trên trời, thâm thúy, xa xăm."),g("br"),g("br"),n._v("Tinh đồ bên trên, vô số đầy sao lập loè, chẳng được mấy vạn."),g("br"),g("br"),n._v("Thắp sáng trăm viên, liền có trở thành phong hào Thần Vương tư cách... Có phải hay không quá đơn giản chút?"),g("br"),g("br"),n._v("“Đừng nghĩ lấy đơn giản, cái này tượng đá, ẩn chứa Đế Quân lưu lại ý niệm, linh hồn lan ra, sẽ gặp phải áp chế, muốn thông qua muôn vàn khó khăn không nói, còn lại lực lượng, còn muốn nhen lửa tinh đồ, càng là khó mà hoàn thành. Ta cùng bọn hắn hai vị gia chủ, đã từng thử qua, cuối cùng... Liền một ngôi sao đều không có thắp sáng!”"),g("br"),g("br"),n._v("Dường như nhìn ra ý nghĩ của hắn, Phượng Nguyên Trì xấu hổ cười một tiếng."),g("br"),g("br"),n._v("“Ah!” Trương Huyền gật đầu."),g("br"),g("br"),n._v("  Cũng đúng, nếu như phong hào Thần Vương, dễ dàng như vậy thông qua khảo hạch, cũng không đến mức toàn bộ Linh Nguyên thiên, chỉ có ba vị."),g("br"),g("br"),n._v("Tam đại gia chủ thảo luận tốt, lại không có gì có thể ngăn trở, Trương Huyền rất mau tới đến tượng đá trước mặt, cánh tay nâng lên, đưa tới."),g("br"),g("br"),n._v("Lòng bàn tay cùng tượng vừa tiếp xúc, quả nhiên cảm thấy một cỗ dày đặc cảm giác áp bách lan ra mà tới."),g("br"),g("br"),n._v("Lông mày giương lên, vừa mới thăng cấp linh hồn lực, lập tức lan ra."),g("br"),g("br"),n._v("Ầm ầm!"),g("br"),g("br"),n._v("Trong óc một hồi nổ vang, tựa như một đầu to lớn Phượng Hoàng, bất ngờ xuất hiện tại phía trước, Trương Huyền mạnh hồn lực, ở trước mặt đối phương cát bụi đồng dạng nhỏ yếu."),g("br"),g("br"),n._v("“Đột phá đầu này Phượng Hoàng?”"),g("br"),g("br"),n._v("Trương Huyền lông mày nhảy dựng."),g("br"),g("br"),n._v("Quả nhiên không tưởng tượng đơn giản như vậy."),g("br"),g("br"),n._v("Uy áp thực sự quá lớn, cho dù linh hồn vừa mới đột phá, muốn qua cũng không dễ dàng như vậy, thậm chí... Cũng có thể nói, là không thể nào hoàn thành nhiệm vụ."),g("br"),g("br"),n._v("“Thử một chút đi!”"),g("br"),g("br"),n._v("Dù sao đều đã tới, coi như khó mà hoàn thành, cũng muốn thử một lần."),g("br"),g("br"),n._v("Thần Vương phía trên là phong hào, muốn cùng Khổng sư đồng dạng, trở thành Đế Quân, cửa này nhất định phải qua, đã như vậy, cũng không có gì có thể trốn tránh."),g("br"),g("br"),n._v("Linh hồn ngưng tụ thành sợi, hướng trước mắt Phượng Hoàng lan ra quá khứ."),g("br"),g("br"),n._v("“Ừm? Không đúng...”"),g("br"),g("br"),n._v("Lan tràn một hồi, lập tức phát giác không đúng."),g("br"),g("br"),n._v("Vốn cho rằng cảm giác áp bách mạnh như vậy, càng hướng về phía trước càng khó đi lại, thậm chí có thể nói không cách nào phía trước tiến, ai biết... Càng hướng Phượng Hoàng tới gần, càng có một loại cảm giác quen thuộc, liền giống như đối phương khí tức, cùng mình vô cùng gần gũi đồng dạng."),g("br"),g("br"),n._v("Rất nhanh thông qua Phượng Hoàng áp bức, đi tới tinh đồ trước mặt."),g("br"),g("br"),n._v("Hơi chuyển động ý nghĩ một chút, hướng bên trong một cái nhấn tới."),g("br"),g("br"),n._v("Hô!"),g("br"),g("br"),n._v("Giống như là chạm đến công tắc, tất cả ngôi sao, trong tích tắc, tranh nhau chen lấn, toả ra ánh sáng chói lọi."),g("br"),g("br"),n._v("“Đều sáng lên... Đơn giản như vậy sao?”"),g("br"),g("br"),n._v("Trương Huyền ngây người."),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-2227"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-2229"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);