(window.webpackJsonp=window.webpackJsonp||[]).push([[1393],{1750:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-215-ta-thua"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-215-ta-thua"}},[n._v("#")]),n._v(" Chương 215: Ta thua")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chủ tịch vị trí lão sư Chuyên Tọa."),t("br"),t("br"),n._v("Vương Siêu ngồi ở trong đó, sống lưng thẳng tắp, thần thái ngạo nghễ, khác nào một cây muốn đâm thủng bầu trời trường thương."),t("br"),t("br"),n._v("“Xem ra cái này Trương Huyền là phá quán tử phá quăng ngã, người khác giao đấu, đều là vừa ra tay lên mạnh nhất, đánh thắng tốt cho người phía sau tăng cường tự tin, hắn lại tốt, vừa lên đến liền phái ra như thế cái thứ đồ hư, này không phải chịu thua là làm gì?”"),t("br"),t("br"),n._v("Một bên Hồng Hạo trưởng lão vuốt râu, đầy mặt hưng phấn."),t("br"),t("br"),n._v("Của hắn Hồng Thiên lâu, bị Trương Huyền nói ra giở trò sau đó, chuyện làm ăn thảm đạm, khoảng cách đóng cửa cũng không kém là bao nhiêu, trong lòng sớm hận không thể nhổ cái tên này da."),t("br"),t("br"),n._v("Muốn không phải sợ ra tay bị học viện tra được, sau đó chuyện làm ăn lại không làm, khẳng định đã sớm động thủ."),t("br"),t("br"),n._v("“Võ giả nhất trọng hậu kỳ tiểu tử, đối kháng võ giả tam trọng... Hoàn toàn không khả năng so sánh!”"),t("br"),t("br"),n._v("Vương Siêu đầu vung lên, mang theo chỉ điểm giang sơn hào khí: “Lại nói, Khổng Kiệt từ nhỏ tu luyện thiết chưởng, lực công kích siêu cường, thuộc về điên cuồng công kích hình, sẽ rơi xuống võ đài, suy yếu tại chỗ!”"),t("br"),t("br"),n._v("“Tụ Tức cảnh hậu kỳ đại khái nắm giữ 90 kg sức mạnh, mà Chân Khí cảnh sơ kỳ, có 300 kg, cách biệt nhiều gấp ba, hoàn toàn đi vắng cùng một cấp bậc, đừng nói mười cái hô hấp, ta xem ba cái hô hấp có thể hay không kiên trì đều rất khó nói!”"),t("br"),t("br"),n._v("Hồng Hạo trưởng lão cười nói."),t("br"),t("br"),n._v("“Không sai, nếu như Khổng Kiệt vừa bắt đầu liền ra tay mà nói, cái tên này cũng chính là một quyền bị miễu hàng!” Vương Siêu gật đầu: “Xem ra, trận đầu không cái gì hồi hộp!”"),t("br"),t("br"),n._v("“Đúng đấy, đừng nói trận đầu, ta cảm thấy năm trận đều không hồi hộp, lần này Lục lão sư khẳng định thắng lợi, để vị này Trương Huyền danh tiếng quét rác.”"),t("br"),t("br"),n._v("Hồng Hạo trưởng lão hừ lạnh."),t("br"),t("br"),n._v("Làm hại hắn chuyện làm ăn không làm được, đã sớm muốn làm cho đối phương thân bại danh liệt."),t("br"),t("br"),n._v("Trước liền chuyên môn đi tìm Lục Tầm, nếu không là hắn đổ thêm dầu vào lửa, cũng không thể có ngày hôm nay sư giả bình trắc."),t("br"),t("br"),n._v("Chỉ cần cái này Trương Huyền thất bại, hắn có rất nhiều loại phương pháp, để cho hối hận tại Hồng Thiên lâu chỗ làm tất cả."),t("br"),t("br"),n._v("“Bắt đầu rồi!”"),t("br"),t("br"),n._v("Không biết hắn nghĩ tới những cái này, đáp một tiếng, Vương Siêu gật gù."),t("br"),t("br"),n._v("Đối với cái này Khổng Kiệt, hắn vẫn rất có tự tin."),t("br"),t("br"),n._v("Ngày hôm qua giúp Lục Tầm dằn vặt một buổi tối, biết này năm cái tân sinh, tuổi không lớn lắm, thực lực chân chính nhưng có nhiều đáng sợ."),t("br"),t("br"),n._v("Một đám cao nhất Đan Điền cảnh gia hỏa, cũng muốn thắng lợi?"),t("br"),t("br"),n._v("Này không phải nằm mơ sao?"),t("br"),t("br"),n._v("Trong lòng cao hứng, nhìn về phía ngay phía trước tỷ thí đài, liền thấy hai cái tân sinh mặt đối mặt đi tới."),t("br"),t("br"),n._v("“Khổng học trưởng, mong rằng hạ thủ lưu tình...”"),t("br"),t("br"),n._v("Sau một khắc, Viên Đào cầu xin âm thanh âm vang lên, nghe nói như thế, hai người suýt chút nữa nước bọt phun ra, cười ngất đi. 【 】"),t("br"),t("br"),n._v("Viên Đào thanh âm không lớn, nhưng bọn họ đều là cao thủ, tai thính mắt tinh, nghe rõ ràng."),t("br"),t("br"),n._v("“Còn không ra tay liền sợ, còn tỷ thí cái rắm!”"),t("br"),t("br"),n._v("“Không đánh mà thắng chi binh, Lục lão sư học sinh quả nhiên lợi hại...”"),t("br"),t("br"),n._v("Hồng Hạo trưởng lão hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Thấy không, dạy học sinh cảnh giới tối cao, không phải học sinh chiến đấu thật lợi hại, mà là vừa ra trận, đối phương học sinh liền doạ co quắp..."),t("br"),t("br"),n._v("Đây mới là cường giả!"),t("br"),t("br"),n._v("Đây mới thực sự là minh tinh giáo sư!"),t("br"),t("br"),n._v("Một mình ngươi đê cấp giáo sư, còn kém mười vạn tám ngàn dặm đây!"),t("br"),t("br"),n._v("“Nhìn dáng dấp, cái tên này sẽ chủ động chịu thua... A?”"),t("br"),t("br"),n._v("Nghe được hai người đối thoại, Vương Siêu cũng gật gù, đang muốn tiếp tục cúi xuống nói, đột nhiên cả người như là bị người nắm cái cổ, âm thanh kẹt ở yết hầu, sắp kìm nén chết."),t("br"),t("br"),n._v("“Làm sao?”"),t("br"),t("br"),n._v("Đến thăm đắc ý, Hồng Hạo trưởng lão không khán đài lên cảnh tượng, giờ khắc này nghe hắn âm thanh quái dị, vội vàng ngẩng đầu, nhìn chút bên dưới, cũng thiếu chút nữa một thanh lão huyết phun ra."),t("br"),t("br"),n._v("Chỉ thấy bọn họ cho rằng phải thua không thể nghi ngờ Viên Đào, đang đứng tại tỷ thí trên đài, vỗ vỗ y phục trên người, một bộ anh hùng vô địch dáng dấp, mà vị kia lực công kích mạnh mẽ Khổng Kiệt, chẳng biết lúc nào đã nằm tại dưới lôi đài, con mắt trở nên trắng, dĩ nhiên ngất đi."),t("br"),t("br"),n._v("“Đây là... Xảy ra chuyện gì?”"),t("br"),t("br"),n._v("Song phương tỷ thí, chỉ cần có một phương đi xuống lôi đài, liền thay bề ngoài thua, vừa nãy không còn rất tốt sao?"),t("br"),t("br"),n._v("Làm sao một cái nháy mắt, cái này Chân Khí cảnh tân tú, vốn là dự định diễu võ dương oai, giương ra hùng phong gia hỏa, liền bị đánh mắt trợn trắng?"),t("br"),t("br"),n._v("Xác thực là ba cái hô hấp không tới liền kết thúc thi đấu, có thể... Làm sao sẽ là kết quả này?"),t("br"),t("br"),n._v("Hồng Hạo trưởng lão đều cảm thấy sắp điên rồi."),t("br"),t("br"),n._v("“Thằng ngu này...”"),t("br"),t("br"),n._v("Đầy mặt đờ ra, Vương Siêu gầm lên giận dữ, hận không thể quất chết cái này Khổng Kiệt."),t("br"),t("br"),n._v("Lực công kích của ngươi mạnh như vậy, thực lực lợi hại như vậy, với hắn phí nói cái gì... Trực tiếp ra tay không là tốt rồi, lần này ngược lại tốt, còn chưa kịp triển lộ thiết chưởng, liền bị đánh cha đẻ cũng không nhận ra..."),t("br"),t("br"),n._v("“Ngươi... Đánh lén, chơi xấu!” Đối diện Chu Hồng gào thét."),t("br"),t("br"),n._v("Có như vậy tỷ thí sao?"),t("br"),t("br"),n._v("Phía trước một khắc còn nói được lắm tốt muốn chịu thua, sau một khắc liền một quyền đánh tới, cũng quá không cao thủ phong độ chứ?"),t("br"),t("br"),n._v("Bất quá, vừa nghĩ tới lúc trước đi truyền tin đánh thành dáng dấp kia, khóe miệng không nhịn được kéo."),t("br"),t("br"),n._v("Có vẻ như... Vị này Trương lão sư môn hạ học sinh... Không có một cái có cao thủ phong độ..."),t("br"),t("br"),n._v("“Đánh lén? Tỷ thí cũng bắt đầu, bất luận người nào cũng có thể công kích, làm sao có thể gọi đánh lén đây? Nếu như hắn muốn động thủ với ta, ta cũng không ngại!”"),t("br"),t("br"),n._v("Không thèm để ý đối phương chít chít nghiêng, Viên Đào quăng một cái ống tay áo, mang theo cao thủ khí chất, trong mắt tràn đầy khinh bỉ: “Muốn trách thì trách chính hắn, liền một quyền đều không chịu nổi, quá yếu!”"),t("br"),t("br"),n._v("“Quá yếu?”"),t("br"),t("br"),n._v("“Loại này đánh lén, coi như mạnh hơn cũng không chống đỡ được đi...”"),t("br"),t("br"),n._v("“Đem đánh lén nói như vậy quang minh chính đại, tại sao có thể làm sao không biết xấu hổ?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được hắn lẽ thẳng khí hùng lời nói, hết thảy người xem cuộc chiến khóe miệng co giật."),t("br"),t("br"),n._v("Người khác đánh lén, coi như thắng, cũng sẽ cảm thấy hổ thẹn, cái tên này ngược lại tốt, như vậy lẽ thẳng khí hùng, khí thôn sơn hà..."),t("br"),t("br"),n._v("Ngươi muội a!"),t("br"),t("br"),n._v("Mặt đây?"),t("br"),t("br"),n._v("“Lão sư, coi như ta thắng lợi sao?”"),t("br"),t("br"),n._v("Hai tay chắp ở sau lưng, Viên Đào nhìn về phía trọng tài lão sư."),t("br"),t("br"),n._v("“Cái này... Thắng lợi!”"),t("br"),t("br"),n._v("Vị lão sư này quay đầu nhìn một chút, phát giác vị này Khổng Kiệt bị một quyền đánh trúng đầu, trong thời gian ngắn chắc là vẫn chưa tỉnh lại, đành phải không nói gì gật đầu."),t("br"),t("br"),n._v("Chủ trì tân sinh thi đấu thật nhiều năm, còn lần thứ nhất gặp phải tình huống như thế."),t("br"),t("br"),n._v("Muốn nói đánh lén đi, tỷ thí đã bắt đầu rồi, là chính ngươi không chú ý, bị người đánh bại cũng oán không được người khác."),t("br"),t("br"),n._v("Không nói đánh lén đi, rất rõ ràng không phải bình thường chiến đấu..."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Nhìn thấy vốn là chắc chắn thắng đệ một học sinh, vào sân liền bị đánh xuống lôi đài, Lục Tầm suýt chút nữa tươi sống tức chết."),t("br"),n._v(" Tiêu tốn ròng rã ba trăm vạn, mua tăng cao tu vi đan dược; Nhịn một đêm, tiêu hao vô số chân khí, trợ giúp đột phá đến Chân Khí cảnh..."),t("br"),t("br"),n._v("Như vậy hợp lại trứng?"),t("br"),t("br"),n._v("Ngươi xấu tốt đánh tới một quyền, thực lực chân chính không bằng đối phương, ta cũng nhận, nửa chiêu cũng không đánh, liền bị người rút ra đi..."),t("br"),t("br"),n._v("Lục Tầm bưng trái tim, hai mắt biến thành màu đen."),t("br"),t("br"),n._v("Nếu không là định lực tốt, e sợ hiện tại cũng đã đánh."),t("br"),t("br"),n._v("“Lục lão sư, trận đầu thất bại, làm sao bây giờ?”"),t("br"),t("br"),n._v("Chu Hồng không nhịn được hỏi."),t("br"),t("br"),n._v("“Mạc Hiểu, ngươi đến.”"),t("br"),t("br"),n._v("Cố nén nghẹn trong lòng, Lục Tầm bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Vâng!” Mạc Hiểu gật gù, cầm trong tay trường thương, đi tới."),t("br"),t("br"),n._v("“Trịnh Dương, ngươi đi đi!”"),t("br"),t("br"),n._v("Nhìn thấy đối phương phái ra hắn, Trương Huyền bắt chuyện một tiếng."),t("br"),t("br"),n._v("Trịnh Dương gật gù, cũng cầm trong tay trường thương, đi lên."),t("br"),t("br"),n._v("Hai cái bạn tốt, lần thứ hai đối diện đứng thẳng."),t("br"),t("br"),n._v("Không để ý tới xuất chiến Trịnh Dương, liếc mắt nhìn đi tới Viên Đào, Trương Huyền thoả mãn gật gù."),t("br"),t("br"),n._v("Ngược lại không là hắn tán thành đánh lén, mà là Viên Đào chỉ là Tụ Tức cảnh hậu kỳ, đối phương nhưng là Chân Khí cảnh, cách biệt một cái đại cấp bậc còn nhiều."),t("br"),t("br"),n._v("Coi như ngươi phòng ngự mạnh hơn, lực công kích cũng không kém, đánh tới trên người đối phương mới được a, đánh không được, đều không kéo."),t("br"),t("br"),n._v("Viên Đào rất rõ ràng biết rõ bản thân mình thiếu sót, tốc độ theo không kịp đối phương, cố ý yếu thế, lại đánh lén ra tay... Nói thật, như vậy thất bại, cũng không trách được người khác."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Là Mạc Hiểu, lần này nhất định có thể thắng lợi!”"),t("br"),t("br"),n._v("Thật vất vả áp chế lại buồn bực trong lòng, nhìn thấy Mạc Hiểu đi lên đài, Vương Siêu ánh mắt sáng lên."),t("br"),t("br"),n._v("“Vị này chính là...”"),t("br"),t("br"),n._v("Một bên Hồng Hạo trưởng lão thấy hắn như thế xác định, kỳ quái nhìn sang."),t("br"),t("br"),n._v("“Mạc Hiểu am hiểu thương pháp, từng là học sinh của ta!” Vương Siêu gật đầu: “Hắn không những thực lực đạt đến Chân Khí cảnh, đối với thương pháp lý giải, cũng rất lợi hại. Mấu chốt nhất chính là, ta đem Vương gia thương thức thứ ba, thức thứ bảy cùng thức thứ mười tám ba chiêu truyền thụ cho hắn!”"),t("br"),t("br"),n._v("“Hàn Mang Sạ Khởi, Tịch Dương Dư Huy, Quy Điểu Nhập Lâm?” Hồng Hạo trưởng lão ánh mắt sáng lên, có chút còn không thể tin được: “Ba chiêu này tuy rằng không phải Vương gia thương bên trong sức mạnh mạnh nhất, lại đều công phòng nhất thể, uy lực vô tận, ngươi dĩ nhiên đem như thế quý giá thương pháp truyền thụ?”"),t("br"),t("br"),n._v("Vương gia thương vì Thiên Huyền Vương quốc thương pháp cao nhất bí bản lĩnh, chỉ cần học thương, không ai không muốn học tập, mà Vương Sùng, Vương Siêu hai cha con, cũng theo không truyền ra ngoài."),t("br"),t("br"),n._v("Chính vì như thế, bộ này thương pháp tại càng quý giá, để vô số người vì đó điên cuồng."),t("br"),t("br"),n._v("Bình thường có thể học được một chiêu, liền cơ bản cùng cấp bậc vô địch rồi, hắn dĩ nhiên một hơi truyền ba chiêu, hơn nữa còn đều là uy lực lớn như vậy?"),t("br"),t("br"),n._v("“Đúng đấy, vì lẽ đó, Mạc Hiểu tất nhiên thắng lợi!”"),t("br"),t("br"),n._v("Vương Siêu trên mặt tự tin không chỉ triệt để khôi phục, thậm chí trở nên càng thêm nồng nặc, tựa hồ vừa nãy bất lợi, đã hoàn toàn không ảnh hưởng tới."),t("br"),t("br"),n._v("Cũng khó trách."),t("br"),t("br"),n._v("Khổng Kiệt không phải học sinh của hắn, hắn chỉ biết là không kém, tính cách loại hình, hợp lại không rõ ràng."),t("br"),t("br"),n._v("Mạc Hiểu không giống."),t("br"),t("br"),n._v("Với hắn đã học, tính cách cứng cỏi không chịu thua, Trịnh Dương muốn đánh lén, cũng là không thể thành công."),t("br"),t("br"),n._v("Lại thêm lên lại được của hắn thân truyền, thương pháp Thông Huyền, muốn thua cũng khó khăn!"),t("br"),t("br"),n._v("Nhất mấu chốt nhất chính là, lúc trước Trịnh Dương cũng từng nghĩ bái ông ta làm thầy, từng gặp hắn ra tay, thương pháp mềm mại, không có quyết chí tiến lên khí chất, bởi vậy, bị cự tuyệt ở ngoài cửa."),t("br"),t("br"),n._v("Người như thế, ngăn ngắn nửa cái đến tháng, coi như như thế nào đi nữa tiến bộ, cũng khẳng định mạnh quá mức chạy đi đâu, không phải là đối thủ của Mạc Hiểu."),t("br"),t("br"),n._v("Côn tháng năm đao cả đời thương."),t("br"),t("br"),n._v("Thương pháp nếu như trong thời gian ngắn có thể tiến bộ, bọn họ Vương gia cũng sẽ không được gọi là Thiên Huyền Vương quốc đệ nhất thương đạo thế gia."),t("br"),t("br"),n._v("Lục Tầm rất hiển nhiên cũng biết điểm ấy, mới phái Mạc Hiểu lên, hi vọng hắn có thể đẹp đẽ thắng lợi, hòa nhau cục diện."),t("br"),t("br"),n._v("“Mạc Hiểu, chúng ta lại gặp mặt!”"),t("br"),t("br"),n._v("Tỷ thí trên đài, Trịnh Dương trường thương ưỡn một cái, thẳng tắp thẳng đứng lên, trong nháy mắt cùng cả người hắn nối liền một đường thẳng."),t("br"),t("br"),n._v("“Đúng đấy, lại gặp mặt!”"),t("br"),t("br"),n._v("Mạc Hiểu gật gù, cũng đem trường thương giơ lên."),t("br"),t("br"),n._v("Hai cái từ nhỏ đến lớn bạn tốt, không nghĩ tới lại lần gặp gỡ, là tại tỷ thí trên đài, hơn nữa còn là trường hợp này."),t("br"),t("br"),n._v("“Động thủ đi!”"),t("br"),t("br"),n._v("Biết bạn tốt khả năng rất khó ra tay, Trịnh Dương đương mở miệng trước."),t("br"),t("br"),n._v("“Trước không vội, để ta nóng người!”"),t("br"),t("br"),n._v("Mạc Hiểu lắc đầu một cái, lùi lại mấy bước, chân khí trong cơ thể khuấy động, theo Vương Siêu nơi nào học tập đến ba chiêu thương pháp nước chảy mây trôi triển khai ra."),t("br"),t("br"),n._v("Ô u Ô u Ô u Ô u!"),t("br"),t("br"),n._v("Thương kính bắn ra bốn phía, khác nào cơn lốc, thổi đến mức người tóc dựng lên."),t("br"),t("br"),n._v("“Cái tên này làm gì? Ba chiêu thương pháp trực tiếp triển khai ra, không phải là nói cho đối phương biết hắn biết Vương gia thương? Một khi sớm có phòng bị, còn đánh như thế nào?”"),t("br"),t("br"),n._v("Nhìn thấy Mạc Hiểu không trực tiếp động thủ, ngược lại triển khai thương pháp làm nóng người, Hồng Hạo trưởng lão, đầy mặt nghi hoặc."),t("br"),t("br"),n._v("Cùng người chiến đấu, tốt nhất không biết thủ đoạn của chính mình, còn không chiến đấu, liền đem lá bài tẩy bạo lộ ra, này không phải chờ bị người phòng bị sao?"),t("br"),t("br"),n._v("“Ha ha, yên tâm đi!” Vương Siêu cười cợt, một mặt kiêu ngạo cùng tự hào: “Vương gia thương là không có cách nào phá giải, những năm này chiêu số đã sớm lưu truyền đi, không ít người đều mô phỏng theo qua, nhưng ai có thể chân chính phá giải? Mạc Hiểu làm được rất đúng, cố ý làm cho đối phương nhìn thấy, cho nội tâm hắn xuất hiện áp lực, sau đó sẽ một lần triển khai, phát dương ta Vương gia thương uy danh!”"),t("br"),t("br"),n._v("“Chuyện này... Trái lại!” Hồng Hạo trưởng lão gật đầu."),t("br"),t("br"),n._v("Vương gia thương xác thực truyền lưu lâu, đáng tiếc không có chân khí vận chuyển phương pháp, mô phỏng theo lại giống như cũng vô dụng, quan trọng nhất chính là... Căn bản phá giải không được."),t("br"),t("br"),n._v("Nếu như thật có thể phá giải, Vương Sùng cũng sẽ không có lớn như vậy uy danh."),t("br"),t("br"),n._v("Tỷ thí trên sân, Mạc Hiểu đã làm nóng người xong xuôi, hoàn mỹ bày ra ba chiêu Vương gia thương."),t("br"),t("br"),n._v("“Được rồi, phỏng chừng muốn bày ra Vương gia thương!”"),t("br"),t("br"),n._v("Vương Siêu lần thứ hai nở nụ cười, đang muốn ca ngợi vài câu, tận mắt đến Trịnh Dương bị đánh cho hoa rơi nước chảy, liền thấy Mạc Hiểu trường thương trong tay “Leng keng!” Một tiếng rơi trên mặt đất, cả người thở dài một tiếng."),t("br"),t("br"),n._v("“Ngươi cái kia một thương, coi như ta đạt đến Chân Khí cảnh cũng phá giải không được, ta thua!”"),t("br"),t("br"),n._v("“Dát?”"),t("br"),t("br"),n._v("Vương Siêu thân thể loáng một cái."),t("br"),t("br"),n._v("Không trả không đánh sao? Ngươi nhận thức cái len sợi thua?"),t("br"),t("br"),n._v("Coi như muốn thua, cũng là đối phương chịu thua có được hay không?"),t("br"),t("br"),n._v("Giời ạ, đây rốt cuộc làm sao?"),t("br"),t("br"),n._v("Đối diện Lục Tầm cũng một cái lảo đảo, liền muốn khóc."),t("br"),t("br"),n._v("(Chưa xong còn tiếp.)."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-223"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-225"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);